INSERT INTO myy_cat3 VALUES (6, '　赫勒一15', 36, '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　赫勒一15<br>\r\n      　　[异名] 曹您赫勒一15、章哲壮阿、十五味狼舌<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 雄黄(制)4.5g 石膏4.5g 草乌芽 3g 西红花1.5g 甘松3g 牛黄3.9g 麝香1.5g 狼舌 15g 石菖蒲 24g \r\n      草乌(制)12g 诃子 15g 青蒿 9g 青蛙肉 12g 丁香 9g 广木香9g <br>\r\n      　　功能与主治] 杀粘，消肿。用于舌根红肿、溃烂，舌巴木，结喉，龈部红肿。<br>\r\n      　　[附注] 本方性凉，为专治舌疾常用良方。方中以专治舌患药狼舌为主；石菖蒲以止腐、杀粘，为辅；以杀粘、止痛药草乌、草乌芽、麝香和利咽药青蒿、丁香及青蛙肉，共为佐使；配合诃子以调理体素，并合用三臣药(牛黄、红花、石膏)以增强清热解毒作用，故对各种舌病均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', '2003-06-07', '0000-00-00', NULL);
