INSERT INTO myy_cat4 VALUES (9, '疾病分类', 4, '', '2003-06-12', '0000-00-00', NULL);
INSERT INTO myy_text VALUES (1, 2, 2, 'df', '<table width="100%" border="0" cellspacing="0" cellpadding="0" align="center">\r\n  <tr align="left" valign="top"> \r\n    <td width="140" rowspan="5" align="center" background="../images/bg2.gif"> \r\n      <table width="90%" border="1" cellspacing="0" cellpadding="0" bordercolordark="#ffffff" bordercolorlight="#666666" height="100%" class="text">\r\n        <tr> \r\n          <td align="left" valign="top"><br>\r\n            <script language="javascript" src="left.js">\r\n</script>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n    <td class="text" height="20" align="center">病 因 </td>\r\n  </tr>\r\n  <tr align="left" valign="top"> \r\n    <td class="text" height="68">&nbsp;&nbsp;&nbsp;&nbsp;发病是由于人体赖以健康存在的物质基础――三根七素和血、黄水，因某种原因失去了相互依赖，相互促进，相互影响和相辅相成的平衡关系而导致病变，即转化为疾病的本质进而侵袭脏腑、器官、组织等。其中三根的作用比七素更为重要，由于三根是赖以七素而存在。所以，三根之任何一根发生反常，首先出现侵犯七素之现象，这里七素之一的血液和黄水二者失常的三根所侵袭的可能性更大。由此可见，三根七素既是构成人体进行生命活动的能量与物质基础，同时也是产生一切疾病的根本因素。因此，对于人体的生理功能和病理机制，均以此三者的生成变化为理论根据和说理工具。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;此外，血液受损时，易引起热性病变，并助长希拉之热，抑制巴达干之寒；黄水受损则与血、希拉相合而引起热性黄水病变，与巴达干或赫依相合则引起寒性黄水病变。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;还有肉眼看得见的和看不见的致病虫类亦可引起各种虫病。因而，近代蒙医将发病之根源归纳为赫依、希拉、巴达干、血、黄水、虫六种，并由此引起的疾病称为六基症。<br>\r\n    </td>\r\n  </tr>\r\n  <tr align="left" valign="top"> \r\n    <td class="text" height="20" align="center">致病因素 </td>\r\n  </tr>\r\n  <tr align="left" valign="top">\r\n    <td class="text"> 一、饮食因素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;人们在日常生活中，由饮食之味、质、性能、时间、量的过大、不足和误用所致的影响称为饮食因素。如摄取与三根的特性及血、黄水之性质相适的味、质、性能的饮食过多时，即成为导致三根之特性偏盛而发生疾病的因素；反之，过多地摄取与其相反性质的味、质和性能的饮食时，则将抑制三根之特性使之衰减，成为引起疾病的因素。如果饮食没有规律，没有定量，则导致营养缺乏；或者进食不习惯的和有毒的食物，则亦损害三根、血和黄水而引起疾病；食用不洁之食物，则可成为发生各种虫病的因素。<br>\r\n      <img src="pic/060.gif" width="200" height="274" align="right" border="1"><br>\r\n      二、起居因素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在人的日常生活中，由身体、言语和思考活动的性质、程度、时间和场所的过度，不足和失误所致的影响称为起居因素。包括衣服、居处、环境、体力劳动、脑力劳动、五官司职及房事等。又叫身语意。如过多地持续地劳作适合于三根特性及血、黄水性质的业务或者长时间呆在类似性质的环境，则促使三根及血、黄水之特性，成为引起疾病的因素。反之，与其相反性质的业务过量则将抑制其特性，使之衰减而成为引起疾病的因素。不善于调节五官之职能，长期居住不适的居处和环境，衣服不按气候之变化而更换，强阻性欲或房劳过度，则亦损害三根和血，黄水导致各种病变。 \r\n      <br>\r\n      <br>\r\n      三、气候之因素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自然界气候之反常变化所致的影响称为气候因素。基于研究疾病之需要，将一年之气候分为炎热时节、寒冷时节和雨水时节三个时节，又把一昼夜的气候分为黎明及下午之凉时(两头)、中午及午夜之热时(两中）上午及黄昏之温时(两尾)六时。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;此外，按各地气候的不同各有分法。在我区，将一年的气候分为春暖时节，夏热时节，秋凉时节，冬寒时节，每三个月为一季。然而，在北回归线附近地区则自上冬至秋季为上，以每两月为一季，依次分配为上冬、下冬、春季、暑季、夏季、秋季六个季节。如果上述季节气候不按各自的规律保持正常而发生反常现象，如在夏季气候应热的时候反而凉，冬寒季节反而温暖，雨水季节反而干旱等；在温热季节过于炎热时导致火元素旺盛而引起热性病变；寒冷季节过于寒冷则导致水元素旺盛而引起寒性病变。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自然界的气候，无时不在对人体发生影响，如气候之剧变，易使三根七素之正常功能失调，因而诱发各种类型的疾病。在人们日常生活中突然发生的事故，由于奇异遭遇、感染疾病、误诊、投药谬误等所致的影响，称为突发因素。如突然跌倒、机械损伤、大量失血、吐呕泻泄、被虫兽咬伤、由于恐怖，精神上受到强烈刺激，为粘虫所感染以及在治疗中使用催吐剂、泻药、穿刺针灸、火针等的谬误。上述各种因素之性质如与三根特性和血、黄水的性质相合则使之过剩，相反则即抑制及减弱等特性和功能而导致失常。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n  <tr align="left" valign="top"> \r\n    <td class="text"> <a href="bl-fl.htm">疾病分类</a>&gt;&gt;&gt;&gt;<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-05-28');
INSERT INTO myy_text VALUES (2, 3, 1, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0" align="center" height="427">\r\n  <tr align="left" valign="top"> \r\n    <td width="140" rowspan="3" align="center" background="../images/bg2.gif"> \r\n      <table width="90%" border="1" cellspacing="0" cellpadding="0" bordercolordark="#ffffff" bordercolorlight="#666666" height="100%" class="text">\r\n        <tr> \r\n          <td align="left" valign="top"><br>\r\n            <script language="javascript" src="left.js">\r\n</script>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n    <td class="text" height="20" align="center" valign="middle">剂 型 </td>\r\n  </tr>\r\n  <tr align="left" valign="top"> \r\n    <td class="text" height="174">&nbsp;&nbsp;&nbsp;&nbsp; <br>\r\n      &nbsp;&nbsp;&nbsp; 一、汤剂<img src="pic/057.jpg" width="220" height="156" align="right" border="1"><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将药物按处方配齐后，研制成粗粉，然后加水、奶或其它适宜液体，煎煮或浸泡后服用，称为汤剂。汤剂吸收快、奏效速，宜于急性病及新病的治疗，但煎煮手续繁琐、对重疾、痼疾、药效有不胜之短。汤剂可用于内服和外用，内服时除单用外亦可作其它剂型的药引子；外用时熏洗、熨、滴耳、鼻、眼、漱口等。汤剂分为浓缩汤和浸泡汤两类。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、散剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将药物按处方配齐，研制成细末、混匀，称为散剂。散剂吸收比汤剂较缓，但作用持久，适宜于慢性病、痼疾、综合症，且制作简便、便于服用、药效稳定、不易变质，还可根据病情灵活应用，如两种散剂合用或与其它剂型合用等优点，是临床中最广泛使用的一种剂型。方剂有内服和外用两种。内服，一般用开水送服，亦可视病情选用汤剂，白酒、奶、肉汤等送服。外用，均研成细末撒于患处或与油类、醋、蛋清、白酒等调和敷患处，亦可作点眼、喷喉使用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、丸剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将药物研成细末，用蜜、水或面糊、米糊等制成颗粒型，称为丸剂。丸剂吸收缓慢、药力持久、而且体积小，服用、携带、贮存都方便，是一种常用剂型，一般多用于慢性疾病及祛除痼疾之根。某些毒性药、峻烈药，为了使药缓缓溶解吸收，降低对人体的毒性、刺激亦可作丸剂用；贵重、芳香药物，如麝香、牛黄等，宜作丸剂用，以免其性味、功效散失、变质。另外，具有异臭味药物，如阿魏等，也应作丸剂，以减少其味，便于服用，临床常用的丸剂有糊丸、水丸、蜜丸等三种。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.糊丸――将药物细粉加米糊、面糊等制成丸剂。糊丸分解时间比水九、蜜丸缓慢，服后在体内徐徐吸收，既可延长药效，又能减少对胃肠的刺激及对人体的毒性，故毒性药、峻烈药物制成糊丸使用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.水丸――将药物细粉用冷开水湿润、粘合，制成的丸剂。水丸比糊丸和蜜丸易于分解、吸收快，是一种比较常用的丸剂。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.蜜丸――将药物细粉用炼制过的蜂蜜制成丸剂。蜜丸性质柔润，作用缓和，味甜、滋补，故补益片剂一般可制成蜜丸使用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;丸剂中蜜丸较大，一般每丸制成6g重；糊丸、水丸较小，一般药物每丸制成0.2g重，（黄豆粒大小丸)，毒性药物每丸制成0.025一0.05g \r\n      (绿豆粒大小丸)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医用丸剂除蜜丸外，糊丸、水丸均用适当的药物细粉，在表面粉饰，称挂衣。挂衣是为了增强药物的稳定性，保持药效、消除某些药物的臭味，使外观光洁、防止变质、虫蛀。挂衣常用药物有金粉、银粉、朱砂、银朱等。治疗神经系统疾患、心脏病、惊厥症，需用金粉挂衣；治疗主脉病、脑髓损伤、筋骨创伤及镇静安神，需用银朱或朱砂挂衣。<br>border="1">&nbsp;&nbsp;&nbsp;&nbsp;四、搅和剂<br>\r\n      将药物研制成细末，加入适量蜂蜜或红塘、白糖与酥油搅和成半固体剂，称为搅和剂。揽和剂主要用于祛除脉络、关节、皮肤之痼疾，其次因本制剂营养丰富，具强壮滋补之功，故宜于年老体衰，久病体虚者的调养治理。搅和剂分为清热，祛寒两大类型。使用搅和剂一般清晨空服为好。此剂易变质，故现用现配制，不宜久贮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;五、灰剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;以矿物药或动物药为主，其它药为辅。将诸药置耐火容器中密闭煅烧成灰，研粉末状，称为灰剂。灰剂多用于寒症、痞症、消化不良、胃火不足、浮肿、水肿、脓肠病，尤其对巴达干病、痼疾等症，疗效显著。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;六、油剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将药材精选，按处方配齐后，水中煎煮，取汁加鲜牛奶、酥油，再煎煮浓缩至无水分后，加入已配研的药物、蜂蜜、红糖、白糖等，搅和成粘稠状制剂，称为油剂。油剂具有滋补壮阳、润僵之功，一般用于老年补养、久病体虚者的调养治理。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;七、膏剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将药物用水煎熬浓缩而成的制剂，称为膏剂。膏剂一般用于创伤、疮疡、疖肿、热症等。膏剂有内服和外用两种。内服膏剂可单用或做药引子、亦可配方用；外用膏剂，可直接涂于患处，亦可用水稀释局部药浴或点眼，滴耳。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;八、酒剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;制法有两种、一种将药材用水煎熬或浸泡后，取汁加酒曲发酵酿制而成。一种将药材直接浸泡于白酒或黄酒内而所得的澄清浸出液。酒剂具有温中祛寒，开胃，强筋健骨，补肾，活血，调经之功。用于胃寒，筋骨损伤，风湿痹证，肾虚体弱，月经不调等症。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n  <tr align="left" valign="top"> \r\n    <td class="text" align="center" height="13"> <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '0000-00-00');
INSERT INTO myy_text VALUES (3, 4, 9, '脉病', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 黑脉或白脉发生病变，称为脉病。在临床上黑脉发病称黑脉病或血脉病；白脉发病称痹病或白脉病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;黑脉属阳，五元中属火，是正常血和希拉存在之处，也是病变血和希拉窜行之道。故发生病变后血与希拉自然要亢盛。发病则不但影响肝、心和命脉，同时也易连累其所连接或行走部位之脏腑及四肢等处，引起病变。发病黑脉出现怒张、肿胀、麻木、发硬、发酸、描搦、抽缩等症状，偶而发生脉疮、脉痞。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;白脉属阴，五元中属水，是正常巴达干存在之处，正常赫依运行之道，同时也是病变巴达干和赫依窜行之路。故发生病变后，巴达干与赫依自然要亢盛。发病则影响其所连接或行走部分之器官，如脑、脊髓和四肢等处，出现半身或手足麻木，浮肿、搐搦、跛行、僵直、萎缩、神志不清、小便不利甚至昏厥等症状。</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (4, 3, 8, '总第26期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>总第26期<br>\r\n      <br>\r\n      蒙医学对再障的认识及辨证治疗<br>\r\n      蒙医对治疗类风湿性关节炎的探讨<br>\r\n      蒙药降糖1号胶囊治疗糖尿病31例临床观察<br>\r\n      藏药安神丸治疗神经官能症30例疗效观察<br>\r\n      蒙药治疗萎缩性胃炎的研究<br>\r\n      维吾尔药斯波皮苏农治疗牙周病<br>\r\n      藏医药治疗小儿乙型肝炎80例疗效观察<br>\r\n      蒙药治疗偏头痛60例<br>\r\n      蒙药广枣蝎蜈丸治疗冠心病心绞痛30例临床观察<br>\r\n      蒙医药治疗椎一基底动脉痉挛疗效观察<br>\r\n      蒙医药浴疗法治疗血栓闭塞性脉管炎<br>\r\n      土家族药物炮制特点<br>\r\n      达斡尔族的保健植物昆米乐<br>\r\n      关于荜茇的蒙医中医药用分析<br>\r\n      壮医药线点灸对脾虚大鼠脑―肠轴生长抑素的影响<br>\r\n      金诃前列宝胶囊对小鼠免疫功能的影响<br>\r\n      土家药八棱麻治疗生膝过敏实验报告<br>\r\n      正交试验法研究森登―4汤煎煮工艺<br>\r\n      蒙药宝日苏日劳糖浆制备工艺研究<br>\r\n      蒙药蓝盆花中黄酮的提取工艺研究<br>\r\n      蒙药串铃草的生药学研究<br>\r\n      薄层扫描法测定蒙药六味安消散中异土木香内酯的含量<br>\r\n      薄层扫描法测定蒙药额力根―7中胆酸含量<br>\r\n      蒙药远志金属元素含量测定<br>\r\n      比色法测定嘎顺淖高颗粒剂中黄酮类化合物<br>\r\n      谈谈藏医学与中医学的基本异同关系<br>\r\n      浅谈蒙医整体观思想<br>\r\n      血液流变学参数昼夜变化与蒙医三根<br>\r\n      关于研究开发蒙医药的思考<br>\r\n      关于发展蒙药事业的几点思考<br>\r\n      藏医药的起源与发展<br>\r\n      蒙藏维医药治疗类风湿性关节炎近况<br>\r\n      蒙药嘎木朱尔治疗皮肤病现状综述<br>\r\n      维药斯亚旦注射液过敏反应及处理<br>\r\n      沙棘树枝及皮制剂治疗高脂血症54例临床观察<br>\r\n      壮医药线点灸治疗带状疱疹38例<br>\r\n      蒙西医结合对截肢残端综合症康复治疗<br>\r\n      藏药七味铁屑散治疗缺铁性贫血<br>\r\n      中蒙医结合治疗外伤性前房积血16例</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (5, 2, 62, '治疗方法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 一、营养之法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;营养之法系调理热能，增强体质，同时相对地减弱赫依功能而补益巴达干或希拉的一种治法。此法主要适用于老年、体弱、思劳过度、营养不良、长期食用性轻而粗糙食物和生活于寒冷而风大的环境中者、赫依性体质者和孕妇等，从疾病的本质和根本原因角度考虑，对赫依增盛而巴达干相对减少并与希拉相搏而引起的寒症均可采取营养之法。如失血过多，长期睡眠不足，肺痨疾，妇女赫依病，赫依性激荡症，赫依性癫狂，赫依性肾寒症，空虚热等。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;所用的药物和饮食：主要以甘、酸、涩、苦味和重、腻、柔、温性者为宜。例如；绵羊、牛、马之瘦肉及肉汤、飞禽肉、鸡蛋、小麦面、炒米、谷子米、骨汤、黄油、牛奶、奶酪、酸马奶新鲜蔬菜和 \r\n      水果、白糖、红糖、植物油、补酒等富有营养和热能的食物。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物和处治：以平息药物为主，辅以攻泻药物，并可选择黄油涂敷，按摩法和蒙古炙，温和导剂，热罨，针刺、火针，色布苏疗法、皮疗等外治法中的一至二种配合使用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在治疗过程中，为使患者增强体力，改善赫依、血之运行，为其居住安静温暖的环境中，充分休息，心情舒畅，适当参加强身锻炼。同时注意如果营养之法过量，则易使疾病转化为精华不消症或由巴达干过盛而引起其它诸症。因此，必须随时掌握病情的变化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、削弱之法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;削弱之法系调节热能，相对地滋补赫依，削减巴达干希拉的一种治法。此法主要适用于青壮年、身体强壮者，长期食用性锐而油腻食物和在干热环境中生活者、希拉型体质者等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;从疾病的本质和根本原因角度考虑对血、希拉增盛而巴达干相对减少并与赫依相搏而引起的热症，均可采取削弱之法。如精华或糟粕不消之症、瘟疫、热症、血、希拉之热邪炽盛、热性黄水病、巴达干、希拉增盛症、脑病、心脏病、脾病、内痈、痛风、痹病、妇血症、胃呆、呕吐、热泻、尿频等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;所用之药物和饮食：主要以甘、苦、涩味和凉、钝、稀、粗糙而性轻者为宜。例如：狍子肉、山羊肉、兔肉、牛肉、白米、牛和羊之奶、奶酪、酸奶、酸马奶、白糖、冰糖、新鲜蔬菜、水果及白开水等饮食。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物和外疗除用各该病之主治为药物外，以攻泻药物为主，适当配合平息药物，并可选择放血、针炙、催吐剂、泻剂、发汗疗法之一、二种，也可根据病情适当施以皮疗、脏疗、罨法、泥疗、砂疗、水疗等疗法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在治疗过程中，使患者作强身锻炼，尽量避免不良的精神刺激，防止过度的情志激动，保持心胸开朗和乐观愉快。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在给药和外治过程中，注意其限度。否则，削弱疗法过量，则易使疾病转化为赫依盛而引起其它诸症。团此，也应随时掌握病情的变化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、手技之法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;手技之法系用手法技巧治疗器官移位，肢体挫伤和断裂等的一种方法。其适应症为脑震荡，脏腑震动移位、骨折、关节脱位、胎儿移位、白脉运行受阻、关节积水等。此法共有三种，其一，以震治震，主要脑震荡、肾、胃受震移位、胎震等。以适应于各该部位损伤之手法进行复位。其二，利用患肢本身之力整复法，主要对颈椎关节和肢体关节脱位时，选择患部附近的适当部位，在病人毫无注意的情况下、以急速拨伸、揉推等手法给以刺激，以利用其惊动力量来达到整复的目的。其三，正骨法，系对筋伤骨折，脱臼等采取的一种手法治疗。正骨法与按摩疗法相配合，则其效更为显著。</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (6, 3, 3, '三根', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n      赫依<br><br>\r\n      <br><br>\r\n          从阴阳学说角度解释，它有两面性，从五元学说角度看乃属于气。赫依在人体正常生理活动中具有维持生命活动推进血液运行，司理呼吸，分解食物，输送精华与糟粕，增强体力，使五官功能正常及意识清楚，接宗传代，支配肢体活动功能反射等作用。且为希拉、巴达干二根之能以保持相对平衡状态的调节者，同时也是人体维持健康和延年益寿的引导者。在人体情志上主要表现为欲望。这种情志上的表现，还反转过来对人体起积极的作用。如若赫依本身失去平衡状态时，就导致病变。<br>\r\n          赫依之特性：赫依具有以轻、涩为主的凉、微、坚、动等六种特性。轻即轻扬在上之意，如人的行动灵活，心情急躁，对周围动静反应迅速等表现，均为轻的特征。它赖以轻性物质而滋长，为重性物质所涩则与腻、柔相反，指不滑润而言。如人的皮肤粗糙和心情执拗、急躁等表现，均为涩的特征。它赖以涩性物质而滋长，为油性、柔软物质所克制。动则与固相反，指飘忽不安定而言。如睡眠不实、健忘、好动和性情不安定等表现，均为动之特征。它赖以飘浮性物质而溺长，为安定性物质所克制。凉即与温相反。如赫依体质的人不能忍受寒冷即为凉之特征。它赖以凉性物质而滋长，为温性物质所克制。微则指赫依能通过任何细小之孔道而言，赫依在人体中无孔不入，无处不到走窜周身的表现，均为微的特征。它赖以枯干性物质而滋长，而为油腻性物质所克制。坚则指坚硬而言。如人之表皮结实而耐于磨擦，又如肠胃健康而耐于泻药等表现，均为坚之特征。它的滋长与受克制情况，与微相同。<br> \r\n      <br><br>\r\n      希拉<br><br>\r\n      <br><br>\r\n          希拉从阴阳学说角度解释，它属阳性，从五元学说角度看乃属于火。希拉为人体正常生理活动的热能，具有产生热量和调节体温，促进消化，引起食欲，开胃进食，使人容光焕发，有雄心，主谋略，促使营养七精华之成熟等功能。它以巴达干为自己存在的前提，与其保持相对平衡状态。在人体情态上的主要表现为聪明、高傲和怒气。这种情态上的表现还反转过来对人体起积极的作用。如若希拉本身失去平衡状态时，就导致病变。 \r\n      <br>\r\n      <br><br>\r\n          希拉之特性：希拉具有以热、锐为主的腻、轻、臭、泻、湿等七种特性。热，与寒相对而言，如人之易于饥渴，耐寒等表现，均为热的特征。热赖以热性物质而滋长，为寒性物质所克制；锐，与钝相对而言，头脑聪明，性情高傲等表现，均为锐的特征。锐赖以锐性物质而滋长，为钝性物质所克制；腻则指性温而油性较少而言，人之皮肤油光而柔软，均为腻的特征。腻赖以温性、油腻物质而滋长，为凉性物质所克制轻，希拉之轻，与赫依之轻不同，而指火元之旺盛而言，希拉虽位于身体中部，但其热力易于上升，如人的性情顿时激昂，立刻复原的表现，均为轻的特征。轻赖以温性油腻物质而滋长，为温柔物质所克制；臭则指由热产生的臭味而言。如排泄物之臭味，即臭之特征。臭赖以温而湿性物质而滋长，为液体物质所克制；泻指湿、锐之作用而言，腹部柔软，易于泻泄，即为泻的特征。泻赖以腻、锐性物质而滋长，为干性物质所克制；湿则指起湿润作用而言，由于热性而多汗易泻，乃湿之特征。湿赖以油腻液体物质而滋长，为干性物质所克制。希拉之七种特性中的热、锐、腻三者由希拉之实质所形成。<br><br>\r\n      <br><br>\r\n      巴达干<br><br>\r\n      <br><br>\r\n          从阴阳学说角度解释，它属阴性，从五元学说角度看乃属于水与土。在人体正常生理活动中，巴达干有滋生和调节体液，使身体、舌、思维活动稳定，辅助消化，滋养正精，增强意志，产生睡眠，滋润皮肤，延年益寿，产生耐力，坚固骨节等功能。并以希拉为自己存在的前提，与其保持相对平衡状态。在人体情志上的主要表现为记忆强，神志清醒，处事耐心。这种情志上的表现还反转过来对人体起积极的作用。如若巴达干本身失去干衡状态，就导致病变。<br><br>\r\n          巴达干之特性：巴达干具有以重、寒为主的腻、钝、软、固、粘等七种特性，重与轻相对而言，身重而行动缓慢，语言思维迟钝，多眠等表现，均为重的特性。重赖以重性物质而滋长，为轻性物质所克制。寒则与热相对而言，热量低弱为寒的特征。寒赖以寒性物质而滋长，为热性物质所克制。腻则指与粗涩而憔悴相对而言，身体皮肤白嫩而丰满，均为腻的特征。腻赖以油性物质而滋长，为憔悴物质所克制。钝与锐想对而言，身体动作和思维活动迟缓为钝的特征。钝赖以钝性物质而滋长，为锐性物质所克制。软与涩相对而言，人之皮肤柔软，性情温和等，均为软之特征。软赖以软性物质而滋长，为涩性物质克制。固则与动相对而言，人之品行正派而稳重，记忆力强，有耐心，性格开朗等表现，均为固之特征。固赖以固、重性物质而滋长，为动、轻性物质所克制。粘，即指粘液而言，巴达干为粘液性物质，所以具有粘性。粘赖以软性物质而滋长，为干性物质所克制。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '0000-00-00');
INSERT INTO myy_text VALUES (7, 3, 8, '总第25期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>总第25期<br>\r\n      <br>\r\n      土家族医药研究回顾<br>\r\n      回族医学的针灸治疗经验初探<br>\r\n      蒙医药对妇科肿瘤的认识<br>\r\n      藏药晶珠肝泰舒治疗慢性乙肝33例疗效观察<br>\r\n      蒙药治疗痛经的临床疗效观察<br>\r\n      壮医药线点灸治疗疖肿361例疗效观察<br>\r\n      维医药治疗86例肾结石临床报告<br>\r\n      蒙医辨证施治类风湿性关节炎临床总结<br>\r\n      土家族民间医药治疗骨病骨伤经验<br>\r\n      傣药雅景波内服治疗高血压<br>\r\n      侗医药补肾益肝汤治疗乙型肝炎 <br>\r\n      中蒙药内外结合治疗亚急性甲状腺炎56例<br>\r\n      额日和穆八味散临床应用体会<br>\r\n      瑶族“庞桶药浴”及浴波植物<br>\r\n      蒙医中医关于皮肤炭疽的认识与比较<br>\r\n      “三木赛”并非“三焦”<br>\r\n      百岁侗医吴定元《草木春秋》书稿学术思想探讨<br>\r\n      苗医热水泡脚的保健作用<br>\r\n      晶珠降脂排毒胶囊对大鼠血脂的影响<br>\r\n      蒙药广枣七味胶囊药理研究<br>\r\n      诃子对草乌水煎剂双酯型二萜类生物碱溶出率的影响<br>\r\n      蒙药顺气口服液葸醌类成分含量测定<br>\r\n      薄层扫描法测定蒙药苏斯―7中胆酸的含量<br>\r\n      蒙药紫花高乌头微量元素的含量测定<br>\r\n      蒙药匝迪―5水溶性多糖提取及组成分析微量钙的测定<br>\r\n      柳叶钝果寄生性状及组织显微鉴定<br>\r\n      孟根乌苏―18质量标准研究<br>\r\n      薄层扫描法测定阿嘎日―35中士的宁的含量<br>\r\n      少数民族医药治疗乙型肝炎的临床研究<br>\r\n      肺心病藏西医结合护理<br>\r\n      藏医药浴的护理<br>\r\n      腰椎间盘突出症的藏医蒸疗护理<br>\r\n      藏药复方组合治疗坐骨神经痛58例<br>\r\n      藏医治疗慢性咽炎130例<br>\r\n      藏药二十五味珍珠丸治疗癫痫病2例<br>\r\n      蒙医药治疗眼底出血<br>\r\n      藏药独一味活血止痛化瘀止血运用<br>\r\n      蒙医放血疗法治疗血希拉性头痛</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (8, 4, 4, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;发病是由于人体赖以健康存在的物质基础――三根七素和血、黄水，因某种原因失去了相互依赖，相互促进，相互影响和相辅相成的平衡关系而导致病变，即转化为疾病的本质进而侵袭脏腑、器官、组织等。其中三根的作用比七素更为重要，由于三根是赖以七素而存在。所以，三根之任何一根发生反常，首先出现侵犯七素之现象，这里七素之一的血液和黄水二者失常的三根所侵袭的可能性更大。由此可见，三根七素既是构成人体进行生命活动的能量与物质基础，同时也是产生一切疾病的根本因素。因此，对于人体的生理功能和病理机制，均以此三者的生成变化为理论根据和说理工具。&lt;br&gt;<br>\r\n      &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;此外，血液受损时，易引起热性病变，并助长希拉之热，抑制巴达干之寒；黄水受损则与血、希拉相合而引起热性黄水病变，与巴达干或赫依相合则引起寒性黄水病变。&lt;br&gt;<br>\r\n      &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;还有肉眼看得见的和看不见的致病虫类亦可引起各种虫病。因而，近代蒙医将发病之根源归纳为赫依、希拉、巴达干、血、黄水、虫六种，并由此引起的疾病称为六基症。&lt;br&gt;<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-05-29');
INSERT INTO myy_text VALUES (9, 3, 8, '总第24期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>总第24期<br>\r\n      <br>\r\n      湘西土家族医、苗族医调查报告<br>\r\n      浅论藏药的发展与前景<br>\r\n      布依族医药杂病简介<br>\r\n      维药调节何里提祛瘀汤治疗高血压病60例疗效观察<br>\r\n      胃镜直视下清创喷洒蒙药治疗溃疡病的临床观察<br>\r\n      藏药二十五味儿茶丸治疗风湿性关节炎100例临床观察<br>\r\n      蒙药乌门―17味散治疗晚期食管贲门癌234例临床观察<br>\r\n      藏药独一味治疗骨折镇痛疗效机理探讨<br>\r\n      白族民间药双桃酊治疗传染性湿疹样皮炎40例<br>\r\n      蒙医药治疗银屑病50例<br>\r\n      蒙西医结合治疗血管性帕金森综合征临床观察<br>\r\n      蒙医药浴治疗荨麻疹的临床观察<br>\r\n      维医蒸汽药浴在类风湿性关节炎中应用<br>\r\n      蒙药广枣总黄酮对大鼠离体心脏的抗心律失常作用<br>\r\n      维药止汗平喘颗粒对小鼠免疫功能的调节作用研究<br>\r\n      藏药七十味珍珠丸对小鼠学习记忆再现缺失的作用<br>\r\n      藏药七十味珍珠丸对兔血流动力学的作用<br>\r\n      蒙药那如―3抗脂质过氧化作用的研究<br>\r\n      沉香八味散抗心肌缺血的实验研究<br>\r\n      用正交试验法探讨蒙药乌珠木―10止咳口服液制备工艺<br>\r\n      藏医起源浅析<br>\r\n      蒙药学专著《白晶鉴》简探<br>\r\n      壮药金果榄的研究进展<br>\r\n      藏医药治疗部分肝胆病现状综述<br>\r\n      藏医治疗脑包虫病2例<br>\r\n      七十味珍珠丸治疗风湿性关节炎108例<br>\r\n      藏医治疗慢性胆囊炎90例<br>\r\n      藏医艾灸治疗神经性头痛l例<br>\r\n      蒙药治疗糖尿病并发末梢神经血管病变<br>\r\n      朝鲜药冬古烈的形态与民间验方<br>\r\n      蒙药内服与保留灌肠治疗溃疡性结肠炎<br>\r\n      十五味龙胆胜花丸治慢性支气管炎120例<br>\r\n      蒙药治疗外科感染性疾病30例<br>\r\n      金沙丸治疗慢性肾炎尿毒症的临床观察<br>\r\n      蒙药嘎木珠尔治疗糜烂溃疡性皮肤病的疗效<br>\r\n      蒙医药治疗颈椎病50例的x线征象分析<br>\r\n      维吾尔族妇女宫颈糜烂1894例调查分析</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (10, 3, 8, '总第23期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>总第23期<br>\r\n      <br>\r\n      浅述蒙医学特点<br>\r\n      朝医体质学说概述<br>\r\n      蒙医热症基本病变概述<br>\r\n      蒙医热症基本病变概述<br>\r\n      壮医药线点灸疗法对麦粒肿的治疗作用观察<br>\r\n      傣药棉榔青止咳露治疗百日咳疗效观察<br>\r\n      蒙药六味安消散治疗消化性溃疡40例<br>\r\n      壮医结合中医治疗带状疱疹体会<br>\r\n      藏药十三味鹏鸟丸治疗脑中风45例临床观察<br>\r\n      维吾尔医对糖尿病足病变的治疗和认识<br>\r\n      佤药娘母良的临床应用<br>\r\n      藏医药浴电热控制装置的临床应用<br>\r\n      我国民族药研究略述<br>\r\n      浅谈壮医药膳的特色<br>\r\n      朝鲜族医对独活的酿制运用<br>\r\n      白族传统药食两用植物高河菜<br>\r\n      蒙医常用药品的分布特点 <br>\r\n      蒙药制剂中乌头碱的毒理作用及中毒解救<br>\r\n      哈尼族民间验方录<br>\r\n      土家医诊法研究<br>\r\n      蒙医传统手术疗法停滞的原因<br>\r\n      藏药七十味珍珠丸对大鼠缺血性脑梗塞作用的研究<br>\r\n      广枣总黄酮对离体大鼠心脏camp含量和腺苷环化酶活性的影响<br>\r\n      蒙药阿如拉对s180荷瘤小鼠肿瘤生长及血清il一2、ii一6的影响<br>\r\n      二阶导数光谱法测定蒙成药给旺一9中的胆酸含量<br>\r\n      蒙药顾马先蒿的形态组织研究<br>\r\n      色相色谱法测定蒙成药嘎日迪一5中麝香酮的含量<br>\r\n      蒙药风湿三味丸质量标准的研究<br>\r\n      朝医四象医学研究概况<br>\r\n      我国发展民族医药卫生思想概述<br>\r\n      发展中的西藏藏医药<br>\r\n      蒙药三子汤外敷治疗带状疱疹56例<br>\r\n      藏西医结合治疗肝硬化34例<br>\r\n      蒙西药结合治疗冠心病l0例<br>\r\n      术前加用藏药占皎达色配合治疗肝包虫病<br>\r\n      藏药如意珍宝丸治疗三叉神经痛56例<br>\r\n      土家药蒴藿治疗漆疮50例<br>\r\n      五味甘露浴精治疗风湿类风湿关节炎56例</td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (11, 3, 8, '总第22期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>总第22期<br>\r\n      <br>\r\n      概述藏医药理论中的全息思想<br>\r\n      蒙医对眼底血证的认识<br>\r\n      浅谈蒙医药治疗心律失常<br>\r\n      藏药佐塔德子玛临床研究<br>\r\n      维医药治疗各种疣94例观察<br>\r\n      玛日布一3汤治疗肾性血尿32例临床观察<br>\r\n      藏药六味安消散(胶囊)治疗便秘疗效小结<br>\r\n      蒙药赞丹一3口服液治疗冠心病心绞痛临床观察<br>\r\n      蒙药治疗慢性再障18例骨髓形态学观察<br>\r\n      藏医治疗知合乃75例疗效分析<br>\r\n      蒙医治疗慢性乙型肝炎80例疗效观察<br>\r\n      蒙药敖格友一8味治疗肝硬化26例疗效观察<br>\r\n      蒙药孟根乌素一18味治疗牛皮癣临床体会<br>\r\n      蒙药森塔拉一43治疗结核性胸膜炎9例<br>\r\n      蒙药治疗冠心病心绞痛50例<br>\r\n      傣西医结合治疗外阴瘙痒疗效观察<br>\r\n      藏西医结合治疗老年冠心病心绞痛<br>\r\n      清真养生学与自拟克糖方对Ⅱ型糖尿病回族患者疗效的影响<br>\r\n      蒙西药敷脐治疗小儿腹泻25例<br>\r\n      用蒙医理论浅析油类的功效与应用<br>\r\n      试论苗医侗医诊断疾病之异同<br>\r\n      新疆维族夜间哮喘发作期患者维吾尔医辨证分型特点分析<br>\r\n      《四部医典》和《黄帝内经》养生观的探讨<br>\r\n      蒙药六味安消散对实验性胃损伤及肠推进作用<br>\r\n      维药祖卡木颗粒的解热镇痛作用研究<br>\r\n      蓉嘎口服液对果蝇的抗衰老作用<br>\r\n      蒙药日喀楚德・古日古木一扎特巴对大鼠血液流变学的影响<br>\r\n      蒙药如达一6对实验性大鼠胃运动的影响<br>\r\n      天然锁阳与栽培锁阳抗衰老作用的比较<br>\r\n      蒙药广枣总黄酮对培养大鼠心肌细胞的作用<br>\r\n      蒙药水柏枝的生药学研究<br>\r\n      蒙药嘎日迪一5(11)栓剂的制备与研究<br>\r\n      蒙医治疗成人软骨病1例报告<br>\r\n      蒙医药治疗泌尿系感染100例<br>\r\n      蒙药结合针刺治疗肩周炎经验<br>\r\n      科尔沁红药膏外敷治疗急性软组织损伤疗效观察 </td>\r\n  </tr>\r\n</table>\r\n', 1, '0000-00-00');
INSERT INTO myy_text VALUES (796, 2, 58, '罗布桑楚勒如木', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>罗布桑楚勒如木<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(1740―1810)一他是察哈尔蒙古人，原籍正镶白旗。他是一位杰出的五明学家、翻译家和著名的作家。他精通蒙、藏、梵文。根据《罗布桑楚勒如木全集》记载共有一百六十多部作品。特别是他给后人留下了许多珍贵的传统蒙医学论著。即《认药学》(该书分为“珍宝、土、石类药物的识别”、“树木、滋补类药物的识别”、“动物药及盐、灰类药物的识别”、“草类药物的识别”等四卷总收载了678种，约39万字)、《油剂制法》(一卷)、《脉诊概要》(一卷)、还有《配制牛痘苗及其接种法》、《医典兰塔布奥词细解》、《香味药制法》、《丸药配制法》、《烟酒与健康》、《配制甘露药法》、{烟酒的危害》、《生死之道》、《五元学说》等。他撰写的《认药学》将 \r\n      678种植物药材的形态、产地、特征、性味、功能详细介绍外把这些药分成、珍宝、土、石、木、水、草、盐、灰、动物产品等十大类。这部著作对蒙药的学习、应用和研究，提供了重要依据。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-11');
INSERT INTO myy_text VALUES (12, 3, 2, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n        浩瀚的大漠，辽阔的草原，养育了淳朴、剽悍的蒙古民族，萌生了神奇、精妙的蒙古医药学。它为本民族人民的身体健康和繁衍昌盛做出了历史贡献。在与疾病做斗争中积累了传统医疗实践经验，在此基础上吸收了藏医学及古代印度医学的部分基础理论和汉医学知识，逐步形成了独特的蒙医学理论体系。<br><br>\r\n          早在元代以前，在我国北方，古代蒙古族人民过着逐水草而游牧和狩猎的生活，常年与寒冷、潮湿、风雪作斗争。人们的生活与疾病和当地的地理环境、气候条件、食物种类、生活习俗息息相关，在“天人相应”中体现着人与自然的矛盾统一。同时，蒙古人民长期以来是一个马背民族，游牧，狩猎和战争交替构成交宕起伏的历史画卷，摔伤，战伤、骨折、脑震荡都成了常见病和多发病，客观的生存需要时时刻刻都在呼唤医学和创造医学。当成吉思汗的儿子窝阔台受了箭伤，他的同伴用嘴吸吮伤口的瘀血，用烧红的铁块烙治新鲜的创面的时候，他们动用了全部的经验和智慧，利用身边一切自以为有效的药物和工具，以达到救死扶伤的目的。 \r\n      <br>\r\n      <br><br>\r\n          13世纪初，元太祖成吉斯汗，统一全蒙古民族后，与兄弟民族间的来往更加密切，并与相邻国家间的通商及文化交流也随之开始。于是蒙古民族的经济文化得以发展，创造了蒙古文字。<br>>BR> \r\n      在元代，饮食疗法得到进一步系统的总结和发展。著名的宫廷饮膳太医，蒙古族营养学家忽思慧所著的《饮膳正要》中，记载了大量蒙古族饮食卫生及饮食疗法的内容。该书在祖国医学营养学中占有重要的历史地位。元代蒙医学家沙图穆苏，用汉文撰成《瑞竹堂经验方》十五卷，书中反映出蒙医学的某些特点，所载方剂达数百首，其中有些方剂实用而有效，至尽仍为医家所采用。<br><br>\r\n          14世纪，蒙古族翻译家沙拉布僧格，将古代印度巨著《金光明盛王经》从维文、藏文译成蒙文，至此古代印度医学的部分基础理论初次传播到蒙古地区。16世纪，明代中、末叶，随着西藏喇嘛黄教输入蒙古，藏医也随之来到蒙古地区。1576年，藏医经典著作《四部医典》传到蒙古，印度佛教巨著《丹珠尔经》于17世纪末被译成蒙文。以上几部书在蒙医的发展史上起了重要作用。因此，历代蒙医学家们吸收了藏医学及古代印度医学的以五元学说为核心的三根理论和七素理论及临床治疗，并把它与中医的阴阳五行学说相互渗透，结合蒙古地区的的特点和民间疗法，创造性地加以改造和发展。<br>>BR> \r\n          18世纪的杰出蒙医学家伊希巴拉吉尔，在他写的《甘露四部》一书中，首次提出“六基症”理论，他认为一切疾病的发生都以赫依、希拉、巴达干、血、黄水、虫为根源。19世纪，蒙医学家罗布桑却因丕勒编著的《蒙医药选编》一书，以临床各种疾病的诊治为主，论述了包括基础理论、药物、外治、“酸马奶疗法”等。外科学方面的理论和技术，也有很大发展，如清代蒙医、杰出的手术家绰尔济墨尔根、觉罗-伊桑嘎等，对纠正四肢不能屈伸的关节脱位、骨折等，常能手到病除，觉罗-伊桑嘎采用冰冻麻醉法，切开整复粉碎性骨折，使其联缀，然后用桑白线缝合，效果颇佳。伊希巴拉吉尔在其编写的《甘露四部》中，详尽地论述了“创伤医治术”、“骨伤疗法”、“脱臼复位术”和“蒙古震脑术”等的理论与实际操作内容。<br><br>\r\n          新中国成立以后，民族医学受到高度重视，党和政府对蒙医药事业十分关心，设立了蒙医药研究机构，开办了蒙医药院校，培养了大量蒙医药人才，搜集、整理、翻译了大量蒙医药和藏文古籍。进一步学习和吸收兄弟民族医药学精华的基础上，广泛利用现代自然科学成果和现代诊察手段，不断地丰富和革新临床医疗技术，以成为崭新的民族医学，它为祖国医学的更加丰富和发展，为增进人类健康，做出应有的贡献。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '0000-00-00');
INSERT INTO myy_text VALUES (13, 2, 4, '丁香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n丁 香<br><br>\r\n      <br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:高勒图宝日<br><br>\r\n      <br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为桃金娘科植物丁香 engenia caryophyllata \r\n      thunb 的干燥花蕾。当花蕾由绿色转红色时采摘，晒干即可成品。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、微苦、温、重、油、固、柔、燥。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:镇赫依、散寒，温中，消食，开欲，解毒，利咽喉。用于命脉赫依病，心赫依病，失眠，精神病，音哑，痘疹等 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (14, 2, 4, '土沉香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n    土沉香<br><br>\r\n      <br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:查干阿嘎如<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为瑞香科植物白木香Aquilaria sinensis lour \r\n      gilg 的含树脂的芯材或树根。四季均可采，割取含树脂的木材，除去不含树脂的部分，阴干。,<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、辛、凉、重。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:镇赫依，平喘，止刺痛，清热。用于命脉赫依，心赫依，气短，失眠，虚热。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (15, 2, 4, '沉香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  \r\n    沉 香<br><br>\r\n      <br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:哈日阿嘎如<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为瑞香科植物沉香 aquilaria agallocha roxb \r\n      的含有树脂的芯材或树根。全年均可采收，割取含树脂的部分，阴干即可成品。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、苦、凉、重、和、油、燥、钝、柔。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:镇赫依，清热，止刺痛。用于胸刺痛，意乱，心悸，昏迷，失眠等热邪侵于心脏的一切心病。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (16, 2, 4, '降香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n      降香<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:乌兰阿嘎如<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物降香dalb．ergia odorifera t．chen的干燥芯材或根。四季均可采，割锯芯材，阴干。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，凉，油。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：镇赫依，清热，止痛。用于赫依热，心赫依，头痛，气喘，失眠等。<br></td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (17, 2, 4, '白豆蔻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n     \r\n    &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:苏格木勒,<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物白豆蔻Amomum Kravanh pierex Gagnep的干燥果实。10-12月果实呈黄绿色尚未开列时采收。除去残留的果柄，晒干。或姜科植物益智Alpinia \r\n    <br>\r\n    oxyph ylla Miq 的干燥成熟果实。夏、秋果实由绿变红时采收。晒干或低温干燥。<br><br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、辛、温、轻、燥苦、油苦、钝。<br><br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:镇赫依，调胃火，消食，开胃，止吐。用于肾赫依，肾虚腰痛，胃寒，食积。 \r\n  \r\n \r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (18, 2, 4, '肉桂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  \r\n    肉 桂\r\n      <br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:嘎毕拉音哈利斯<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为樟科植物肉桂cynnamomum cassia prosl的干燥树皮。多于秋季剥取，阴干。<br><br>\r\n      茜草本品为茜草科植物茜草rudia cordifolia L的干燥根、茎。秋季采挖，除去泥土及杂质，洗净，切片，晒干或制炭用。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:甘、辛、涩、微咸，热、油、燥、锐、动。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:祛寒赫依，调胃火，止泻，排脓。用于肝、胃赫依，腰膝冷痛，腹泻。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (19, 2, 4, '阿魏', '<table width="100%" border="0" cellspacing="0" cellpadding="0" style="font-size:12px">\r\n  \r\n    阿魏<br>\r\n      \r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:乌莫黑达布日海<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物阿魏eerula assafotidal．新疆阿魏eerula \r\n      sinkioanensis <br>\r\n      k．shen或阜康阿魏eerula fuka― nensis k．m．shen)的树脂。春末夏初盛花期至初果期，分次由基上部往下斜割，收集渗出的乳状树脂，阴干。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]:辛，温、油、燥。<br><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:镇赫依，祛巴达干，调胃火，消食，开胃，杀虫，止痛。用于心赫依，胃肠赫依，虫疾等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (20, 2, 4, '肉豆蔻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    肉豆蔻<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:匝迪<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为肉豆蔻科植物肉豆蔻myristica fragrans houtt的干燥种子。4―6月及11―12月各采一次。早晨摘取成熟果实，剖开果皮，剥去假种皮，再敲脱壳状的种皮取出种仁用石灰乳浸一天后，缓缓焙干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，温，油，重，柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：镇赫依，调胃火，消食，开胃。用于心赫依，心绞痛，心悸，昏迷，消化不良。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (21, 2, 4, '小茴香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    小茴香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:召日高德斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物小茴香eoeniculum vulgare mill的干燥果实。秋季采摘成熟果实，除去杂质，晒干。炒黄用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]涩、辛，温、油、轻、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]镇赫依，清热，解毒。用于增强视力，食欲不佳，睾丸肿痛，疝气。 </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (22, 2, 4, '紫硇砂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size：12px">\r\n  <tr>\r\n    紫硇砂<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌莫黑达布素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为紫色石盐(halitum violaceum)的块状结晶体，主含氯化钠。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸、辛，性温、重、腻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有抑制巴达干、赫依，调胃火，软便，止痛之功效。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (23, 2, 4, '大蒜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    大蒜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:莎日莫斯克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为百合科植物蒜allium sativum l．的干燥茎。春、夏季采收，除去外皮，晒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、温，锐、油、重。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：镇赫依，平喘，杀虫，解毒，祛黄水，调胃火，开胃，破痞。用于赫依症，主脉赫依，气喘，滴虫，牙虫、食欲不振。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (24, 2, 4, '小豆蔻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>小豆蔻 (印度豆蔻)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙古名]：额讷特和格苏格木勒、吉吉格苏格莫勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物小豆蔻(elettaria cardamomum maton)的干燥果实。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性、味、功能、应用范围及方法与白豆蔻相同。 <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (25, 2, 4, '益智仁', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size"12px">\r\n  <tr>\r\n    益智仁<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙古名]：宝茹苏格木勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物益智(alpinia oxyphylla miq．)的干燥成熟果实。夏、秋间果实由绿变红时采收，晒干或低温干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性、味、功效、应用与白豆蔻相同。 </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (26, 2, 4, '莱菔子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>莱菔子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 老邦因--乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为十字花科植物萝卜的干燥成熟种子。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 本品味辛、甘，性平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有祛胃、肾之寒，平喘，化痰，之功效。多用于气喘，咳痰不利等。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (27, 2, 4, '草 果', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>草 果<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:嘎古拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛，温、糙、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:祛脾、胃寒赫依，消食。用于脾脏寒热疾病，腹部胀满，呕吐泄泻。 <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (28, 2, 4, '胡桃仁', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size"12px">\r\n  <tr>\r\n    胡桃仁 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：胡西嘎茵楚莫<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为胡桃科植物胡桃 (juglans regia l)的干燥成熟种子，9―10月果实成熟时采收，将果实外皮沤烂，击开核壳，取其核仁，晒干。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (29, 2, 4, '蔗糖', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蔗糖<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 乌兰西赫日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为禾本科植物甘蔗(saccharum sinensis roxb)的茎杆。秋后采收，砍取地上部分。削去上部梢叶后捆扎，置阴暗不通风处，保持水分。制糖入药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 本品味甘，鲜用性凉、重；制糖后性温、重、腻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有祛寒赫依，补虚，止泻之功效。用于赫依病，做药引，肾寒、肺、心、肾、骨之赫依滞病，精少，遗精，阳萎等。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (30, 2, 4, '肉苁蓉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>肉苁蓉<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 查干高要<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为列当科植物肉苁蓉cistnche decerticola.y.c.ma的干燥带鳞状皮的肉质茎。多于春季苗未出土或出土时采挖，除去花序，切段，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、酸、咸，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清希拉、消食、补虚。用于希拉引起头痛、胃酸、吐酸水、阳萎、遗精。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (31, 2, 5, '金诃子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>金诃子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦阿如拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为使君子科植物诃子，terminalla chebula retz 的干燥金黄色的成熟果实。秋、冬季果实成熟时，选采黄色饱满者，除去杂质，阴干即可成品。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:涩、酸、微苦，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清希拉，调元、解毒、消食、愈伤、生肌。用于赫依，希拉、巴达干，包如巴达干病，外伤，骨折等。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (32, 2, 5, '连翘', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>连翘<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：扫龙吉木斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为木犀科植物连翘forsythia suspensa(thunb．)vahl．的干燥果实。秋季果实初熟尚带绿色时采收。除去杂质，蒸熟，晒干，习称“青翘”，果实熟透<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;时采收，晒干，除去杂质，习称“老翘”。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清希拉，止泻。用于腑热，肠刺痛、热泻。 </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (33, 2, 5, '木鳖子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>木鳖子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦其其根乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葫芦科植物木鳖mumordica cochinchinensis(lour) spreng \r\n      的干燥成熟的种子。冬季果采收成熟的种子，干燥。去壳，刮外皮用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、凉、钝、轻，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清希拉，清热。用于胃肠希拉，脾热，希拉性头疼。 <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (34, 2, 5, '山苦荬', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>山苦荬<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙古名]：苏斯乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物山苦荬(ixeris chinensis(thunb) nakai)的干燥全草。夏、秋花盛开时采收，除去枝叶、残茎、洗净泥土，通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、糙、钝、稀。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清希拉，清热之功效。用于希拉性热头痛，高烧，食欲不振，目黄，血热。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (35, 2, 5, '麦冬', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>麦冬<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：麦冬其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为百合科植物麦冬(ophiopogon japonicus (thunb) ker―gawl的干燥块根。夏季采挖，洗净，反复暴晒，堆置，至七八成干，除去须根，干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、微苦，性凉 。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有平希拉，解毒之功效，用于发烧，头痛，烦渴，目、身、尿发黄等热性希拉病。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (36, 2, 5, '熊胆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>熊胆<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴巴盖因苏斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为熊科动物黑熊selena rctos thibe tanus curier或棕熊ursus \r\n      arctos l的干燥胆。一般于冬季捕捉，抛腹取胆囊，将囊口扎紧，剥取胆囊外附着的油脂，用木夹扁，悬挂通风处阴干，或置石灰缸中干燥。不宜晒干或烘干，以防腐臭。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]锁脉止血，清希拉热，明日，止腐，生肌，止泻。用于各种出血症，黄疸、目赤、疮疡等。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (37, 2, 5, '锁阳', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>锁阳<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰高要<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为锁阳科植物锁阳gynomorium songaricumrupr．的干燥肉质茎。春、秋均可采挖，除去花序，切成薄片，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、涩，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品功效，应用与肉苁蓉同。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (38, 2, 5, '冰糖', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>冰糖<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：木森西赫日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为禾本科植物甘蔗(saccharum sinensis roxb)制成白糖后的再加工制品。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有凉血，清血希拉热，润五官，止烦渴，止呕逆之功效。用于希拉病及清热药，多作为药引用之。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (39, 2, 5, '香墨', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>香墨<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：伯赫<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为松烟和入胶汁、香料、冰片等加工制成之墨。入药以陈旧者为佳。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，消肿，止血，杀粘，改善视力之功效。用于皮肤红肿，发疹，身热等黄水性病。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (40, 2, 5, '丝瓜子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>丝瓜子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦曼吉拉干内乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葫芦科植物丝瓜(luffa cylindrica(l．)roem )或粤丝瓜(laffa \r\n      acutangla roxb．)的干燥成熟种子。秋季果实成熟时采攻，除去果皮，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，性凉、钝、轻、涩、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有催吐，解毒之功效。用于消化希拉病，胆外溢。</td>\r\n  </tr>\r\n</table>', 1, '2003-05-29');
INSERT INTO myy_text VALUES (41, 2, 5, '金腰草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>金腰草 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦博日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为虎耳草科植物裸茎金腰子(chrysosplenium ndieaulue bge．)或肾叶金腰子(cnrysosplenium \r\n      griffitni hook．et thoms．)的干燥全草。夏末花盛开时采收全草，除去枯茎、叶，洗净泥土，通风处阴亍。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]： 苦，性寒、轻、涩、稀、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有抑希拉，清热，止痛之功效。用于由希拉热引起的口苦，高烧，目黄。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-05-29');
INSERT INTO myy_text VALUES (42, 2, 5, '蒲公英', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蒲公英 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：毕力格图一娜布其<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物蒙古蒲公英(taraxacum mongolicum hand．―mazz )碱地蒲公英(taraxacum \r\n      sinicum htag，)或异苞蒲公英(taraxacum heterolepis nakaietkoixdzex kitag)的干燥全草。从春至秋蒲公英花初开对采摘全草，洗净泥土，通风处阴于。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、微甘、性凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有抑希拉，清热，解毒，开胃之功效。用于目、皮肤、尿液发黄，口苦，发烧等热性希拉病。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-05-29');
INSERT INTO myy_text VALUES (43, 2, 5, '苣荬菜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>苣荬菜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：嘎顺淖高<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物苣荬莱(sonchus brachyotus dc．)的干燥地上部分，夏季在开花前采收，除去杂质、枯叶，洗净泥土，通风处阴于。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，性凉。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有抑希拉，清热、解毒，开胃之功效。用于由于希拉热引起的口苦，发烧，食物消化时胃痛，胸胁刺痛。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-05-29');
INSERT INTO myy_text VALUES (44, 2, 5, '花猫', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>花锚<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：西依热迪格达<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙胆科植物花锚(haiehia sibirica borkh．)的干燥全草。秋季花盛开时采收，除去枯叶及杂质，洗净泥土，通风处阴干。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦，性平、软、腻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有抑希拉，清热，疗创伤之功效。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (45, 2, 5, '波梭瓜子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>波梭瓜子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴嘎阿拉坦其其格恩乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葫芦科植物波棱瓜(herpetospermum caudlgerum wall )的干燥成熟种子。秋季果实成熟时采收，取其种子，阴干，去外皮及绿皮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性味、功效、应用与木鳖子相同。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (46, 2, 5, '止泻子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>止泻子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：额讷特和格都格莫宁<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为夹竹桃科植物止泻木(holarhena antidysenterica wall. ex \r\n      a. dc．)的干燥成熟种子。6～12月间果实成熟时采收，明干，除去果皮，取其种子。 &nbsp;&nbsp;&nbsp;&nbsp;[性味]： \r\n      凉、钝、燥、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清希拉，止泻之功效。用于胆汁扩散引起的目、身发黄，希拉热邪侵于五脏之病。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (47, 2, 5, '篦齿蒿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font=size:12px">\r\n  <tr>\r\n    <td>篦齿蒿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌赫尔西鲁黑<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物栉叶蒿(artemisia peefinata pall)的干燥地上部分。夏、秋季割取地上部分，除去枯叶、残茎及泥土，洗净，切段制膏或通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩、微苦、辛，性凉、钝、稀。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有抑希拉，解毒，杀虫之功效。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (48, 2, 6, '石 榴', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石 榴<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿那日，本品为石榴科植物石榴purica granatum L的干燥成熟果实。秋季果实成熟时采摘,剖开,通风处晒干或烘干即可成品供使用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:甘、酸、热、锐、糙、动、油、燥、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:调理胃火，祛巴达干寒症，消食，开胃，止泻。用于消化不良、恶心、胃胀、肠鸣、胃腹胀满、肺、肝、肾赫依病。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (49, 2, 6, '寒水石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>寒水石<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：壮西<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为碳酸钙矿石方解石(caco3),全年皆可采挖，掘出后除去泥土，拣去杂石即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]： 辛，平、糙。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛巴达干热，止吐，止泻，消食，解毒，破痞，滋辣，消肿，接骨，调和体素之功效。用于因巴达干热引起胸部灼热感，吐酸水，不消化病，腹泻等。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (50, 2, 6, '木香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>木香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：如达<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物木香aucklandia lappa decne的干燥根。秋、冬季采挖，除去泥沙及须根，切段，大的再剖成瓣，干燥后除去粗皮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛、苦、温、油、糙、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛巴达干，破痞，调节体素，排脓，祛痰，止腐，平气血相讧，止痛。用于痧症，胃脘胀痛，肺脓肠。赫依血相讧，中耳炎，偏头痛。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (51, 2, 6, '菖蒲', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>菖蒲<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌莫赫依吉格素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本平为天南星科植物石菖蒲 (acorus gramineus solanad．)或水菖蒲(acorns \r\n      clamusl．)的干燥根茎。秋季采挖二年生菖蒲根茎，除去须根，洗净泥土，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，温、涩、燥、锐、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有消食，调理胃火，开胃，防糜烂，杀粘虫，除黄水之功效。用于不消化病，呃逆，气喘，巴达干赫依病，铁垢巴达干，剑突巴达干，胃腹胀满疼痛，脱发，全身瘙痒，麻风，白喉，粘病，皮肤病，关节痛等黄水病。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (52, 2, 6, '芫荽子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>芫荽子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌奴尔图・淖高茵乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物芜荽(coriandrum sativum l)的干燥成熟果实。秋季果实成熟时采割，晒干，打下果实，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、酸，凉、涩、轻、稀、腻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]有祛巴达干热，消食，开胃，止渴，止痛，表疹之功效。用于巴达干包如病初期，吐酸水，烧心，胃痛。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (53, 2, 6, '射干', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>射干<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：布舍勒则<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鸢尾科植物射干(belamcanda chinensis&lt;l&gt;dc．)的干燥根茎。春初，秋末采挖，除去须根，洗净泥土，切片，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]： 苦，凉、钝、稀、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛巴达干热，止吐之功效。用于恶心，呕吐，包如病增盛期及胃痛。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (54, 2, 6, '光明盐', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>光明盐 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：毛勒尔达布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为天然食盐结晶体，主要成分为氯化钠。全年可采，采得后刮净外面杂质即可。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、咸，性温、锐、重、软。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调理胃火，消食，祛巴达干赫依，改善视力之功效。用于药物及饮食不消化，巴达干赫依性胃病，巴达干偏盛引起的病症，由赫依引起的头晕，昏迷，眼睛翳障，眼花，流泪，眼昏朦症。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (55, 2, 6, '山 柰', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>山 柰<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:查干嘎<br>\r\n      &nbsp;&nbsp;&nbsp; 本品为姜科植物沙姜(kaempferia galanga L)的干燥根、茎。冬季采挖，除去须根，洗净泥土，切片，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、苦、涩、温、糙、锐、燥、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:祛巴达干赫依，调胃火，行血。用于巴达干病，巴达干赫依，消化不良，胃痛，恶心，月经不调，血痞，宫痞。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (56, 2, 6, '干姜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>干姜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：宝日嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物姜（zingber officinale(milla)rosc)的干燥根茎。冬季采挖，除去茎叶及须根，洗净泥土，切片，烘干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：本品性、味、<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;功效、应用与高良姜相同。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (57, 2, 6, '高良姜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>高良姜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物高良姜 (alpinia officinarum hance)的干燥根茎。夏、秋采挖，除去须根及地上部分，切片，晒干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、甘，温、糙、锐、燥、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调理胃火，开胃，消食，祛巴达干赫依，收敛脓、痰，强壮身体之功效。用于消化不良，恶心，胃火衰败，肾寒，腰腿疼痛，身体衰弱，阳萎。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (58, 2, 6, '荜拨', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>荜拨<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：荜荜灵<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为胡椒科植物荜拨piper longuml的干燥未成熟或成熟的果穗。9月间果穗由绿变黑时采摘，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛、温，油、锐、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]调理胃火，祛巴达干赫依，调元，祛痰，滋补强壮。用于胃腹冷痛，食欲不振，肾寒，呕吐，寒泻，哮喘。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (59, 2, 6, '辣椒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>辣椒 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：泽依特拉嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茄科植物辣椒(capsicum frutessens l．)的干燥成熟果实。秋季果实成热时采摘，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：极辛，性热、轻、拯、操。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调理胃火，消肿，开胃，杀虫，逐水，破痞。用于胃寒，不思饮食，消化不良，食痞，虫痞，腹水，痔疮，奇哈，吾雅曼病。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-05');
INSERT INTO myy_text VALUES (60, 2, 6, '川木香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>川木香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：布斯嘎日木拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物川木香(vldimiria souliei(franch)ling)的干燥根。秋季采挖，除去须根、泥土、根头胶质物，阴于后除去杂质，洗净切段，晒干或低温烘干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，凉、轻、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛巴达干热，滋养，止痛之功效。用于心口灼热感，吐酸水，痧症，包如病寒热兼杂期，肝区疼痛，胃痛，胸胁疼痛，包如热等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (61, 2, 6, '碱花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>碱花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：胡吉日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为咸水湖边生成的一种主含碳酸纳的分枝状结晶。或食用面碱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]咸、甘，苦、平、重。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]祛巴达干，消食，破痞，驱虫，止腐，通便润肠，解毒。用于不消化病，胃酸，呕吐酸水，大便干燥，胎盘滞留，经闭等症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (62, 2, 6, '芡实', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>芡实<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：嘎然萨<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为睡莲科植物芡实(euryale ferox salisb．)的干燥成熟种仁。秋末冬初果实成熟时采收，除去果皮，洗净再剥去种子外皮，取仁硒子。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涅，温、轻、涩、锐、燥、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调理胃火，消食，开胃，祛胃寒之功效。用于不消化病，胃腹胀满，火衰败，不思饮食，肾寒，腰腿疼痛。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (63, 2, 6, '黑胡椒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黑胡椒<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈热胡珠<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为胡椒科植物胡椒 (piper nigrum l．)的未除去外果皮者。即尚未成熟时摘收晒干后果皮皱缩而色黑的果实。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，热、轻、燥、涩、锐、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调理胃火，消食，开胃，祛巴达干寒之功效。用于铁垢巴达干，吐、泻、食物不消化，胃痛，腹胀，全身瘙痒。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (64, 2, 6, '蛇床子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蛇床子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：呼西格图乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物蛇床(cnidium monnieri(l)cusson)的干燥成熟果实。夏、秋季果实成熟时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、苦，温、轻、燥、涩、锐、浮，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]；有调理胃火，杀虫之功效。用于腹胀，嗳气，不消化病，胃火衰败，阴道虫，肛门疮，皮肤瘙痒，巴木病，黄水病，布鲁菌病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (65, 2, 6, '白胡椒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白胡椒<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干胡珠<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为胡椒科植物胡椒 (piper nigrum l．)的干燥去皮成熟果实。从秋末至次春果实变红时采收，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性味，功效，应用与黑胡椒同。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (66, 2, 6, '杜鹃花叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>杜鹃花叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干阿拉坦哈拉布日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为杜鹃花科植物杜鹃花叶 (rhododendron simsii planch．)的干燥叶和带叶枝梢或带花梢枝。夏、秋季采割，除去枯茎杂质，晒干或制药膏用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦、涩；温、轻、软、有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]；有调理胃火，开胃，祛巴达干，止痛，止咳，祛痰，消肿，滋养，调节体素之功效。用于不消化病，铁垢巴达干，剑突痞，胃痛，不思饮食等症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (67, 2, 6, '黑种草子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黑种草子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈尔乌热图乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛茛科植物黑种草(nigella glandulifera freyn)的干燥种子。春末夏初种子成熟时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、辛，温、轻、涩、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有调胃火，消食，固齿之功效。用于食后肝区疼痛，呕吐等肝功衰弱病及颜面浮肿，腹胀等肝病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (68, 2, 6, '蜂蜜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蜂蜜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为膜翅目蜜蜂种昆虫中华蜜蜂(apis cerana fabricius)或意大利蜂(apis \r\n      mellifera l．)所酿的蜜糖。多在春、夏、秋三季采收，采收时将人工蜂巢置离心机内把蜜摇出，过滤，除去蜂蜡的碎片及其他杂质即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，温、锐、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有祛巴达干，滑肠解毒，操脓及黄水。促进收敛及愈合创伤，增强营养的功效。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (69, 3, 5, '血余炭', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>血余炭<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌斯内奴乌日斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为人发的制炭品，收集人发，用碱水洗净，再用清水漂洗后，晒干，密闭制炭。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止血，排脓、除黄水之功效。用于脉伤，崩漏，肝包如渗漏引起的吐血，肺脉破裂咳嗽带血，九窍出血等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (70, 3, 5, '豌豆花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>豌豆花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：豌豆宝日其根一其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物豌豆(pisum sativum l)的干燥花。夏季采花，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涩，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：止血，凉血。用于各种出血，肾虚，尿频。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (71, 3, 5, '茜 草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>茜 草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：那郎海额布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茜草科植物茜草rudia cordifolia L的干燥根、茎。秋季采挖，除去泥土及杂质，洗净，切片，晒干或制炭用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、凉、糙、钝、柔、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清血热，止血，止泻。用于肾热、肾脉震痛，腑热泻下，肠刺痛，肺热咳嗽， 肺脓疡，血热，麻疹，吐血。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (72, 3, 5, '紫草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>紫草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：别日木格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为紫草科植物新疆紫草 (arnebia euchroma&lt;royle&gt;johnst．)或紫草(lithospermum \r\n      erythrorhizon s．etz．)的干燥根。秋季采挖，除去泥土，切片，阴干或研粉，羊奶浸制及制炭备用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清肺热、肾热、止血之功效。用于肺热咳嗽，咯痰不利，腰部痰痛，尿道灼痛，尿频，外伤出血，吐血，鼻衄，血尿，月经淋漓。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (73, 3, 5, '红花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>红花 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：古日古木<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物红花carthamus tinctoriusl的干燥花。夏季花开后，由黄变红时采摘、阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、微苦、凉、重、钝、和、柔、固。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清肝、调经，锁脉，凉血，止痛，消肿。用于肝损伤，肝肿大，肝血热，月经不调，妇女血热炽盛，肝包如引起，便血，外伤出血，血热头痛，目赤。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (74, 3, 5, '藏红花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>藏红花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：卡车一古日古木<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鸢尾科植物番红花crocus sativus l的干燥柱头。秋季开花时，摘下柱头，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性味，功能，应用与红花同，但质量优于红花。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (75, 3, 5, '瞿麦', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>瞿麦<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴沙嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为石竹科植物瞿麦dianthus superbus l．、石竹 dianthus chinensis \r\n      l的干燥地上全草；或杜鹃花科植物羊踯躅(闹羊花)rhododendron molle g．don的干燥花；或玄参科植物齿叶草odontites \r\n      serotina(lam)dum的干燥地上部分。夏季花开时采收。除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、凉、轻、钝、稀、枯。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 凉血，止刺痛。用于血热，血刺痛，肝热，痧症，产褥热。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (76, 3, 5, '栀子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>栀子 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：珠如拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茜草科植物栀子（gardema jasminoides ellis．)的干燥果实。9―11月间果实成熟呈红黄色时摘取，除去果柄及杂质，洗净，并微蒸或沸水(可加明矾)中微煮，取出后晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、钝、涩。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清血热，明目，祛巴达干希拉，滋养强壮及调节体素之功效。用于肝热病，面目发黄，腑器希拉病，血热增盛，正血与病血之分离，骚热，肾及膀胱热，尿频等新久热症，体虚、少精、遗精，由希拉引起的结膜病、眼睑赤烂、眼睑浮肿、多眵，血、希拉引起的诸眼疾。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (77, 3, 5, '紫檀香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>紫檀香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰赞旦<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物紫檀 (pterocarpus indicus willd)的干燥心材。全年可采，采得后切成小段，除去边材(制造紫檀器具时，剩下的碎材，亦可利用。)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩、咸，凉、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清血热，手气血不和，消肿之功效。用于心热盛，血热病，关节肿痛，胸闷，呼吸困难。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (78, 3, 5, '苏木', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>苏木<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：扫门毛都<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物苏木(caesalpinia sappan l．)的干燥心材。全年可采，采得后锯成小段，劈开，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、咸，凉、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清血热，化瘀血，调经之功效。用于血热性头痛，目亦，肝血增盛，脉热，产后发热，血痞，血症，闭经，月经不调，经痛，妇女之腰腿疼痛及头痛等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (79, 3, 5, '益母草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>益母草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：都尔布勒吉一乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为唇形科植物益母草(leonurus heterophyllua sweet)的干燥地上部分。夏、秋季花初开时采割，切段，阴干或制膏用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、钝、腻、糙。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有活血，调经，清肝，明目，去翳之功效。用于月经不调，痛经，月经不调，经闭，目赤翳障等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (80, 3, 5, '枸杞子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>枸杞子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：旭仁温吉拉嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茄科植物宁夏枸杞lycium barbarum l．的干燥成熟果实。夏、秋二季果实呈橙红色时采收，凉至皮皱后，再曝晒至外皮干硬，果肉柔软，除去果梗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、平、轻、钝、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]散恶血，清热。用于血瘀宫中，血痞，闭经，乳腺肿等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (81, 3, 5, '丹参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>丹参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰温都素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为唇形科植物丹参(salvia miltiorrhiza bge．)的干燥根茎。春、秋季采挖，除去泥土，洗净，切片，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、轻、钝、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清血热，祛恶血，止泻之功效。用于血热，脉热，腑热病泄泻。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (82, 3, 5, '鹿茸', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>鹿茸<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：措森额布热<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为鹿科动物梅花鹿cervus nippon temminck或马鹿cervus \r\n            elaphus linnaeus的雄鹿未骨化密生茸毛的幼角。夏、秋二季锯取鹿茸，经加工后，阴干或烘干。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]咸、甘、温。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]益精补血，强筋骨，强身，燥脓，燥黄水。用于遗精，阳萎，月经不调，体虚精衰，瘀血，肺脓肿，伤筋骨折。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (83, 3, 5, '马勃', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>马勃<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：都力莫古<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为灰包科植物脱皮马勃(lasiosphaera fenzliireich．)、大马勃(calvatia \r\n            giganten &lt;batach ex pers&gt;．)l loyd．)或紫色马勃(c．1ilacina lloyd)的干燥近成熟子实体。秋季子实体刚成熟时采集，去净泥沙，晒干。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，平。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止血，解毒，愈合伤口，除黄水之功效。用于鼻衄，吐血，外伤出血，尿血，便血，月经淋漓，蛇咬伤，烧伤等。 \r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (84, 3, 5, '相思子', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>相思子 <br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉格宝日楚克<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物相思藤(abrus precatorius l．)的干燥成熟种子。夏、秋季果实成熟时摘下，晒干，打出种子，除去杂质，备用。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦，平，锐，有大毒。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有开窍，催产，破痞之功效。用于妇女血症，子宫痞，闭经，难产，胎衣不下等。 \r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (85, 3, 5, '当归', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>当归 <br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：当贡<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物当归(angelica sinensis &lt;oliv&gt; \r\n            diels)的干燥根。移栽后当年霜降前后采挖，先在通风处晾至半干，微火烘至八成干，再晒干。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、辛，温、重、钝、燥。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清心热，解毒，调经，止痛，平赫依之功效。用于心热炽盛而刺痛，气血相搏而胸胁作痛，气喘，失眠，神志模糊，烦燥不安，食欲不振，中脉赫依，心慌不安，头晕、心跳，腰跨酸痛，体虚等赫依性疾病。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (86, 3, 5, '艾叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>艾叶<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：索依哈一乌布斯<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物艾蒿(artemisia argyilevl．et vant)的干燥叶，夏末采收，除去杂质，晒干。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，温，有小毒。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止血，消肿，制痈之功效。用于诸出血，肉痈。 </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (87, 3, 5, '紫草茸', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>紫草茸<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：恩格斯格<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为胶蚧科昆虫紫胶虫 (laccifer iacca kerr)在树枝上所分泌的树脂状胶质。一般采收，在7―8月间进行，将采集的胶放在干燥、阴凉通风处，直至完全干燥而不结块为止。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦、涩，性寒、钝、柔、软。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清血热，祛肺及肾之伤热，解毒之功效。用于肺热，肺刺痛，咳嗽，痰多，咯痰不利，月经淋漓不止。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (88, 3, 5, '藏羚角', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>藏羚角<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：敖莱高茵额布尔<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为牛科动物藏羚 (pantholops hodgsoni abel)的角。全年可采收，洗净，微炒或制炭用。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩、咸，寒。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有催产，燥脓液及黄水，止泻之功效。用于难产，胎衣不下，死胎不下，热性泄泻，肠刺痛，慢性腹泻，肺脓疡等。 \r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (89, 3, 5, '乌梢蛇', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>乌梢蛇<br>\r\n            <br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈热毛盖<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;本品为游蛇科动物乌梢蛇(zaocys dnumnades&lt;cantor&gt;)的除去内脏的全体。用柴火熏干，至表面略呈黑色为度，再晒干或烘干。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，平。<br>\r\n            &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有明目，通经活络之功效。用于视物不清，昏朦症，子宫血痞，便秘，腹胀肠鸣，嗳气，矢气不利，闭经，腹痛，麻风等。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (90, 3, 5, '荠菜', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>荠菜<br>\r\n            <br>\r\n            [蒙药名]：阿布嘎<br>\r\n            本品为十字花科植物荠 (capsellabursa-pastoris&lt;l&gt;mealc．)带花、种子的干燥全草。春、夏季花开结子时采收，除去杂质，洗净泥土,阴干。 \r\n            <br>\r\n            [性味]：辛，甘，平、柔、固、重。<br>\r\n            [功能与主治]：有止吐，利尿，止咳，止血之功效。用于呕吐、吐血，月经淋漓，产后流血过多等。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (91, 3, 5, '荠菜', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>荠菜<br>\r\n            <br>\r\n            [蒙药名]：阿布嘎<br>\r\n            本品为十字花科植物荠 (capsellabursa-pastoris&lt;l&gt;mealc．)带花、种子的干燥全草。春、夏季花开结子时采收，除去杂质，洗净泥土,阴干。 \r\n            <br>\r\n            [性味]：辛，甘，平、柔、固、重。<br>\r\n            [功能与主治]：有止吐，利尿，止咳，止血之功效。用于呕吐、吐血，月经淋漓，产后流血过多等。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-05');
INSERT INTO myy_text VALUES (92, 1, 4, '理血药', '', 1, '2003-06-06');
INSERT INTO myy_text VALUES (93, 2, 7, '决明子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>决明子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：塔拉嘎道尔吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物决明(cassia obtusifolia l．)或小决明(cassia tora \r\n      l．)的干燥成熟种子。秋季采收成熟果实、晒干后打下种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：微苦、涩，凉、涩、钝、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主]：有燥黄水，滋补强壮之功效。用于关节肿胀，疼痛，全身癌痒，痛风，痹病等黄水性疾病。脱发，皮肤瘙痒，秃疮，疥癣等皮肤病。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (94, 2, 7, '苘麻子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>苘麻子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：黑玛音乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为锦葵科植物苘麻(abutilon theophrasti medic．)的干燥成熟种子。秋季采收成熟果实，阴干，打下种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涩，平、涩、轻、燥、浮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛黄水，杀虫之功效。用于痛风，痹病，黄水病，疥癣，痘疮，秃疮，皮肤瘙痒。白癜风等皮肤病。淋巴结病，浊热病，麻风，白脉病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (95, 2, 7, '黄柏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄柏<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：霞日毛都<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为芸香科植物黄皮树phelloden chinenseschneid或黄檗phellodendron \r\n      amurense rupr．的干燥树皮。前者习称“川黄柏”，后者习称“关黄柏”。剥取树皮后，除去粗皮，晒干。川黄柏主产于四川、贵州、湖北、云南等省，关黄柏主产于内蒙古、华北和东北区，朝鲜、前苏联亦产。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、凉、糙、涩、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]燥协日沃素，清热，解毒，止泻，止血明目。用于痛风，游痛证，秃疮，癣，疥，皮肤瘙痒，吾雅曼病，毒热，鼻衄，吐血，血痢，月经过多，火眼，肾热，遗精等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (96, 2, 7, '川楝子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>川楝子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴如拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为楝科植物川楝melia toosendan sieb.et.zucc的干燥成熟果实。产于湖北、四川、河南、湖南等省。冬季果实成熟时采收，除去杂质，凉干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]涩、苦、凉、轻、淡、燥、钝，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清巴达干希拉，燥协日沃素，止痛，杀虫，明目。用于热性协日沃素病，浊热，巴达干希拉合并症，脱发，皮肤瘙痒，黄水疮，痘疹，湿疹，白癜风，秃疮，疥癣，游痛证，痛风。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (97, 2, 7, '汞（水银）', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>汞（水银）<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：孟根沃斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为液态金属汞[Hg]临床用药根据病情选用寒热软硬四种炮制法的一种。药用一般取水银、硫磺粉等量，置铁锅内，温火加热，不断搅动，避免燃烧，当硫磺融化与水银粘合变稠时，去火继续搅拌，变稀后在放火上加热，如此反复数次，放凉凝结，取出，掰开不见水银颗粒，呈青灰色即可。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、酸、凉、重，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:燥协日沃素，燥脓血，杀虫，消奇哈。用于协日沃素病，痛风，游痛证，结喉，发症，吾雅曼，奇哈，梅毒，疥癣，黄水疮，秃疮，豆疹，瘙痒，淋巴腺肿大。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (98, 2, 7, '硫黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>硫黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：呼呼日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为自然元素类矿物硫族自然硫。采挖后，加热熔化，除去杂质；或用含硫矿物经加工制得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]酸，温，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]燥脓血，燥协日沃素，止痒，杀虫。用于协日沃素病(黄水病)，疥癣，黄水疮，吾雅曼病，白癜风。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (99, 2, 7, '广角', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>广角<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：和日靳因哈热―额布尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为犀科动物黑犀(rhinoceros bicornis l)白犀(rhinoceros simus \r\n      cottoni)的角。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品采制、性味、功效、应用与犀角同，但质量较次。<br>\r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (100, 2, 7, '南沙参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>南沙参<br>\r\n      <br>\r\n      [蒙药名]：洪胡其其格<br>\r\n      本品为桔梗科植物轮叶沙参adenopnoratetra phylla&lt;tnunb&gt;fisch．)、长柱沙参(adenophorastenanthina&lt;ledeb．&gt;kitag．)的干燥根，秋季采挖，除去须根，洗净泥土，刮去外皮，阴干。<br>\r\n      [性味]：甘，凉。<br>\r\n      [功能与主治]：其功效与应用同党参。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (101, 2, 7, '犀角', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>犀角<br>\r\n      <br>\r\n      [蒙药名]：和日斯因额布尔<br>\r\n      本品为犀科动物印度犀(rhinoceros unicornis l．)、爪哇犀(rhinocerossonbaicus desmarest)或苏门犀(rhinoceros \r\n      sumatrensis cuvier)等的角。取犀角劈成瓣，置温水中浸泡后刨成片，晒干或锉成粉，研成极细粉末。<br>\r\n      [性味]：苦、酸、咸，凉、燥。 <br>\r\n      [功能与主治]：有清热，凉血，祛瘀、燥脓及黄水、解毒，止痛，疗伤之功效。用于肺脓疡，药物、肉食、食物中毒，高烧、头痛，胸背作痛，外伤，麻风等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (102, 2, 7, '松节', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>松节<br>\r\n      <br>\r\n      [蒙药名]：纳日松格树<br>\r\n      本品为松科植物油松 (pinus tabulaeformis cart．)、马尾松(pinus massoniana lamb．)等的枝干上的干燥结节。将所得松节劈段晒干。<br>\r\n      [性味]：甘、苦，温、燥、涩、腻。<br>\r\n      [功能与主治]：有祛巴达干赫依，除黄水，止痛，消肿之功效。用于关节红肿疼痛，风湿痛，寒性黄水病。白癜风，瘙庠，疥、疮、疹等皮肤病，赫依性伛偻病，骨关节剧痛，肌肉萎缩，赫依渗于骨及关节而浮肿等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (103, 2, 7, '火麻仁', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>火麻仁<br>\r\n      <br>\r\n      [蒙药名]：敖鲁孙--乌热<br>\r\n      本品为桑科植物大麻(cannabis sativa l．)的干燥成熟果实。秋季果实成熟时采收全草，晒干后打下种子，除去杂质。<br>\r\n      [性味] 本品味甘，性平、腻。<br>\r\n      [功能与主治] 有祛黄水，杀虫，润肠通便之功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (104, 2, 7, '掏子本', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>掏子本<br>\r\n      <br>\r\n      [蒙药名]：亚日盖<br>\r\n      本品为蔷薇科植物蒙古掏子catoneaster mongolicus pojark．)的干燥成熟果实。秋季果实成熟时采收，去柄洗净，晒干。<br>\r\n      [性味]：酸，温。<br>\r\n      [功能与主治]：有止血，收敛扩散之黄水及毒邪的功效。用于中毒扩散，骚热余邪，大关节肿窟，发烧，鼻衄，月经过多，咯血等。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (105, 2, 7, '党参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>党参 <br>\r\n      <br>\r\n      [蒙药名]：宋一敖如浩岱<br>\r\n      本品为桔梗科植物党参(codonopsis pilosula(franch)nannf．)的干燥根。秋季采挖，除去须根，洗净，阴干。<br>\r\n      [性味]：苦、辛、涩，凉、锐、软。<br>\r\n      [功能与主治]：有祛黄水，消肿，润僵。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (106, 2, 7, '鹿角', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>鹿角<br>\r\n      <br>\r\n      [蒙药名]：宝根额布热<br>\r\n      本品为鹿科动物马鹿或梅花鹿的已骨化的角。多于春季拾取脱角，除去泥沙，风干。<br>\r\n      [性味]咸、温。<br>\r\n      [功能与主治] 燥协日沃素，燥脓，燥恶血，消肿，解毒，止刺痛。用于肺脓肿，咯痰带血，胸伤，乳肿，奇哈，疮疡，水肿，刺痛症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (107, 2, 8, '可瓜子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>可瓜子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：玛茹泽<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物紫铆树 (butea monosperma&lt;lam．&gt;kunhtze)或紫藤(wisteria \r\n      sinensis sweet．)的干燥成熟种子。夏季果实成熟时采摘，除去杂质，取出种子晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、甘，温、钝。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有杀虫，止痒，止痛之功效。用于胃肠粘性痧症，亚玛虫，蛲虫，肠寄生虫，龋齿等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (108, 2, 8, '马蔺子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>马蔺子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:恰黑勒德各因乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鸢尾科植物马蔺Iris lacteal pall.var.chinensis(fisch)koidz \r\n      的干燥成熟种子。秋季果实成熟时割下果穗，晒干，收取种子，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、甘，平、重、固、糙、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:杀虫，解毒，解痉，助消化，退黄，疗伤，燥协日沃素。用于各种虫疾，胃痧，消化不良，黄疸，金伤，协日沃素病，烧伤，黄水疮等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (109, 2, 8, '天仙子 (莨菪)', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>天仙子 (莨菪)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[蒙药名]:特讷各额布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp本品为茄科植物茛菪hyoscyamus niger L的干燥成熟种子。夏末秋初果实成熟时割取植株，晒干，打下种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[性 味]:苦、平、糙、钝、油，有大毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[功能与主治]:杀虫，止痛，镇静，消奇哈。用于牙蛀，亚玛病，胃痧，脑刺痛，蛲虫，癣，癫狂，癫痫。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (110, 2, 8, '蔓荆子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蔓荆子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[蒙药名]：吉当嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp本品为马鞭草科植物单叶蔓荆(virex trifolia l．var．simplicifolia \r\n      cham．)或蔓荆(virex trifolia l)的干燥成熟熟果实。秋季果实成熟时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp本品性味，功效，应用与信茼子相同。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (111, 2, 8, '荆芥', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>荆芥<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[蒙药名]哈日吉如克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp本品为唇形科植物裂叶荆芥schizonepeta tenuifolia benth briq．的干燥地上部分。夏、秋二季花盛开时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[性味]苦、辛、涩、温、糙、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp[功能与主治]杀虫，止腐，疗伤，祛巴达干。用于胃肠寄生虫病，滴虫病，蛲虫病，疥癣、皮癣。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (112, 2, 8, '天南星', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>天南星<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:巴日森塔布格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为天南星科植物Arisaema erubescens (well)schott或异叶天南星Arisaema \r\n      heterophyllum BL的干燥根、茎。秋、冬二季茎叶枯萎的时候采挖，除去须根及外皮，干燥。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、辛、热、糙、轻、、锐、，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:杀虫，消肿，消奇哈，止痛。用于牙蛀，蛲虫病，奇哈病，痈肿，结喉，秃疮，黄水疮，疥疮，亚玛病。 \r\n      <br>\r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (113, 2, 8, '香 薷', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>香 薷<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 希拉一吉如格巴<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为唇形科植物海州香薷elsholtzia splendens benth．的干燥地上部分，夏、秋二季生长旺盛初开花时采割地上部分，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;性味、功效、应用与荆芥相同。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (114, 2, 8, '信筒子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>信筒子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：吉当嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为紫全牛科植物矩叶酸藤果(embelia oblongifolia hemsl．)或白花酸藤果(embelia \r\n      ribesburm．f．)的干燥成熟果实。秋、冬二季果实成熟时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、酸，平、涩、锐。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有杀虫，调胃火，消肿之功效。用于皮肤寄生虫病，肠寄生虫，亚玛虫，浮肿，水肿，胃火衰败，不消化病，食欲不振。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (115, 2, 8, '曼陀罗子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>曼陀罗子 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干曼德勒图其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茄科植物白花曼陀罗 datura metel l或毛曼陀罗datura innoxia will的干燥成熟果实。夏、秋二季果实成熟时采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性味，功效应用与天仙子相同。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (116, 2, 9, '麝 香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>麝 香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:扎嘎日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鹿科动物麝Moschus berezovskii flerov、马麝Moschus sifanicus \r\n      przewalski原麝Moschus moschiferus Linnaeus的成熟雄体香囊中的干燥分泌物。野麝多在冬季猎取，猎获后，割娶香囊；家麝直接从其香囊中取出麝香仁，阴干，密闭，置干燥处。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、苦、凉、钝、轻、糙、油。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:杀粘，解毒，开窍，止痛，燥协日沃素，消肿。用于粘症，瘟疫，虫疾，亚玛，毒热，中风，协日沃素病。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (117, 2, 9, '草 乌', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>草 乌<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]泵阿<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛茛科植物北乌头aeonitum kusnezoffiireichb的干燥块根。秋季茎叶枯萎时采挖，除去须根及泥沙，晒干。配方时用童便或诃子汤浸泡2―3日，至口尝稍有舌麻感时捞出，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛、温、轻；有大毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]杀粘，止痛，燥协日沃素。用于瘟疫，粘奇哈，刺痛，结喉，发症，痛风，游痛证，关节协日沃素，风湿病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (118, 2, 9, '黄 连', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄 连<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:昔日温都斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛莨科植物黄连 coptis chinensis franch.、三角黄连 coptis \r\n      deltoidea c.y.cheng et hsiao 或云连coptis deltoidea c.y.cheng 的干燥根茎。以上三中分别称“味连”、“雅连”、“云连”。秋季采挖，除去须根及泥沙，干燥，清除去残留须根即可成品。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、涩、凉、柔、钝、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清瘟、燥脓，疗伤。用于瘟疫，血希拉热，伤热，火眼，脓肿，肠刺痛等症状的疾病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (119, 2, 9, '角茴香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>角茴香 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：嘎伦塔巴克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为罂粟科植物角茴香(hypecoum erectum l)的干燥全草。夏、秋开花时采集，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，性寒、淡、涩、稀、钝、轻、动。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为杀粘、解毒、清热、止痛。用于疫热、肠刺痛、麻疹、白喉、炭疽，希拉热等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (120, 2, 9, '龙 骨', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>龙 骨<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：鲁音牙苏<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为古代大形哺乳动物如象类，犀牛类，三趾马等的骨胳化石。挖出后，除去泥土及杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：镇痛，杀粘，止血涩肠，生肌敛疮。主治惊痫癫狂，脑症刺痛，肠痧疼痛，吐血便血，溃疡久不收口，遗精淋浊。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (121, 2, 9, '旋复花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>旋复花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]阿拉坦多斯勒其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物旋复花inuta britannica l的干燥头状花序。夏、秋二季花开放时采收，除去杂质，阴干或晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]微苦、平、柔、糙、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]止刺痛，杀粘，燥协日沃素，疗伤。用于粘刺痛，粘热，发症，金伤，脑刺痛，肠刺痛。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (122, 2, 9, '细 辛', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>细 辛<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]哈日明占<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为马兜铃科植物北细辛asarum heterotropoides fr．var．mandshuricum(maxim．)kitag的干燥全草。夏季果熟期或初秋采收，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、凉、糙。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]杀粘，止刺痛，清热，消肿。用于瘟热，结喉，脑刺痛，阵刺痛，发症，腮腺炎。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (123, 2, 9, '藁本', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>藁本<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈热巴拉其日嘎纳<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物藁本(ligustieum sinense oliv)或辽藁本(ligusticumjeholense \r\n      nakai et kitag．)的干燥根茎及根。秋季茎叶枯萎或次春出苗时采挖，除去泥沙，晒干或烘干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：杀粘虫、止痛、消肿、防瘟、消黑、黄水。主治头痛、急刺痛、白喉、丹毒、淋巴腺炎、脓肿、炭疽、麻疹、猩红热、水痘、粘性痧症、疖痈、亚玛虫病、颈项强直等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (124, 2, 9, '东莨菪', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>东莨菪<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈热唐普如莫<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茄科植物赛莨菪(scopolia carniolicoides c．y， wu et c．chen)或山莨菪 \r\n      (anisodus tanguticus(maxim) pasch)的干燥根。秋末采挖，洗净泥土，切片或切块，晒干，或鲜根熬膏。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，凉、涩、动、燥、腻，有大毒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有杀粘，消肿、解痉、杀虫、止痛、镇静之功效。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (125, 2, 9, '龙齿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>龙齿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙古名] 鲁音一索药<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为古代大形哺乳动物如象类、犀牛类、三趾马等的牙齿化石。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 本品味甘、涩，性凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]镇静止痛，杀虫。主治脑刺痛，烦躁不安，失眠多梦，疼痛，湿热。用于小儿惊热如火，虫症刺痛，脐部刺痛等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (126, 2, 9, '大青叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>大青叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：呼和尔格讷<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为十字花科植物菘蓝(isatis tinctoria l．)的干燥叶。夏季生长旺盛时采叶，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、咸，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：杀粘，清热解毒。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (127, 2, 9, '紫菀花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>紫菀花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：浩宁尼敦其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物紫菀(aster tataricus l．f)的干燥花。夏秋开花时采收，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：微苦，平、钝、和。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为杀粘、清热、解毒、燥脓血、消肿，主治疫热、天花，麻疹，猩红热。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (128, 2, 9, '独 活', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>独 活<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]查干巴拉其日嘎纳<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物重齿毛当归(angelica pubescens maxim．f． biserrata \r\n      shanet yuan)的干燥根。春初刚发芽或秋末茎叶枯萎时采挖，除去须根及泥沙，烘至半干，堆置2―3天，发软后再烘至全干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，微温。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：功能杀粘虫，止血，止痛，除黄水。用于肿痛、炭疽、粘疫、白喉，各种出血，麻风病等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (129, 2, 9, '商 陆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>商 陆<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：希拉额木<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为商陆科植物商陆(pbytolacca acinosa roxb或垂序商陆phyyolacca \r\n      america l的干燥根。秋季至次春采挖，除去须根及泥沙，切成块或片，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒，有毒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有杀粘，消肿，润僵，止痛补虚之功能。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (130, 2, 10, '苦参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>苦参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]道格勒额布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物苦参sophora flavescens ai的干燥根。春、秋二季采挖，除去根头及小支根，洗净，干燥或趁鲜切片用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、干、润、油、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热解表，燥协日沃素、表疹。用于瘟病初起，感冒发热，风热，痛风，游痛证，天花，麻疹，风湿性关节炎，疮疡。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (131, 2, 10, '土木香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>土木香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：玛奴<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物土木香(inula helenium l．)的干燥根。霜降后地上部分枯黄时采挖，除去泥土，洗净后将根截成 \r\n      3寸长段，粗者纵切，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、苦、甘，平、腻、锐、重、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛巴达干热，气血不调，调胃火，消食，开胃，止痛之功效。用于胃腹胀痛，口苦，食欲不振，头痛、呕吐，腿部肌肉及关节疼痛，气喘，胸背游走性疼痛，赫依、希拉性头痛及血热刺痛。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (132, 2, 10, '冰 片', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>冰 片(borneo-camphora)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:锡乐嘎布日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙脑香科植物Dryobanops aromatica Gaertn.f.树脂内提炼出来的白色半透明的片状松脆结晶。产于东南亚地区和台湾省。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、辛、涩，凉、糙、轻、钝、稀、动、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清热，消肿，止痛。用于灼热，陈热，骚热，毒热，瘟热，丹毒，肿痛，牙痛，目赤等各种热性病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (133, 2, 10, '麻 黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>麻 黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:哲日根<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蔷薇科植物草麻黄Ephedra sinica stapf、中麻黄Ephedra intermedia \r\n      schrenk et C .A.Mey.或木贼麻黄Ephedra epuisetina Bge.的干燥草质茎。秋季采割绿色的草质茎，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、涩，寒、钝、糙、轻、淡。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清肝，止血，破痞，消肿，疗伤，发汗解表。用于肝痞热，震热，骚热，外伤出血，呕血，便血，咯血，子宫出血，痞症，内伤，新陈热。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (134, 2, 10, '枇杷叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>枇杷叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：额勒吉根奇很纳布其<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蔷薇科植物枇杷 (eriobotrya japonica&lt;thunb．&gt;iindt．)的干燥叶。全年均可采摘，晒干。除去绒毛入药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：凉、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：清热，祛痰咳，调经之功效，用于肺热咳嗽，气管炎，肾脏及膀胱热，尿路感染，月经不调，口唇糜烂、牙龈肿痛以及血、希拉性咽喉肿痛等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (135, 2, 10, '白檀香', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>白檀香<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]查干赞丹<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为檀香科植物檀香sanlalum album l的干燥芯材。四季均可采，采伐木材后锯成段，除去外皮及边材，阴干；加工后所剩下的碎材亦可入药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]涩、寒、轻、钝、燥、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清内热，滋补。用于肺热咳嗽，胸痛气喘，心悸，心前区痛。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (136, 2, 10, '胡黄连', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>胡黄连 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]宝日洪林<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为玄参科植物胡黄连pierorhiza scrophularifilora penneh的干燥根茎。秋季采挖，洗净泥土，除去须根，通风处阴干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、寒、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，解毒，燥恶血，清内热。用于血热炽盛，流行性感冒，外伤，发烧，瘟疫，痧症，骚热，黄疸。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (137, 2, 10, '葶苈子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>葶苈子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈木毕勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为十字花科植物腺独行莱(lepidium apetalum willd．)或播娘蒿(descurainia \r\n      sophia(l．)webbex prantl)的干燥成熟种子。初夏，当果色黄绿，植株顶端种子变硬即可收割，阴干后脱粒，除去杂质灰尘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，凉、钝、稀、柔、轻、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清骚热，解毒，祛痰，止咳，平喘之功效。用于气血不调，胸闷气喘，感冒引起的发烧，咳嗽，气喘，头痛，毒热症，血、希拉性骚热症等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (138, 2, 10, '木棉花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>木棉花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]毛敦呼泵一其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为木棉科植物木棉gossampianus malabariea (dc)merr．的干燥花。春季开花时采摘，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、涩，凉、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清心，清肺，清肝热。用于心热，肺肝热等各种热症。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (139, 2, 10, '牛黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>牛黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:给旺<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为牛科动物牛bos taurus domesticus gmelin．的干燥胆结石。宰牛时，如发现有牛黄。立即滤去胆汁，将牛黄取出，除去外部薄膜，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、甘，凉、重、钝、和、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，解毒，镇静，安神。用于肝胆热，疫热，黄疸，癫狂，抽风，巴达干包如病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (140, 2, 10, '五灵脂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>五灵脂<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]哈敦海鲁木勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鼯鼠科动物复齿鼯鼠trogopterus xanthipes milne edwards．的干燥粪便。全年均可采收，除去杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、微咸，凉、锐。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，调理体素，止痛，利尿，化瘀。用于胃热，肝热，淋巴结红肿，关节肿痛，黄水病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (141, 2, 10, '珊瑚', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="99">\r\n  <tr>\r\n    <td>珊瑚<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]旭日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为矶花科动物桃色珊瑚corallium japonicum kishinouye等珊瑚虫分泌的石灰质骨骼。从海底捞出，清水洗净杂物，即得。药用多为工艺制品残余的碎块，奶制入药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，解毒，镇静。用于肝热，脉热，偏瘫，毒热，白脉病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (142, 2, 10, '广枣', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>广枣<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：珠如很芍沙<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为漆树科植物南酸枣(choerospondias axillaris &lt;roxb．&gt;burtt \r\n      et hill)的干燥核果。秋季果实成熟时采收，除去杂质，通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：酸、甘，平、腻、重、柔。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清心火，改善心功能之功效。用于胸刺痛，神志昏迷，癫狂，心绞痛等心热症，胸闷疼痛，言语不清，心衰病，心赫依病，烦躁不安，健忘，失眠，心慌等。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (143, 2, 10, '甘松', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>甘松<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌奴尔图胡吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为败酱科植物甘松 nardostaehys chinensis batal及匙叶甘松(n．jara- \r\n      mansi cwall．dc．)的干燥根及根状茎。春、秋二季皆可采挖，而以秋季产者为佳。挖取后去净泥沙，不可用水洗，以免损失香气，除去残茎及须根，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，凉、轻、钝、稀、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其功效、应用与缬草相同。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (144, 2, 10, '木通', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>木通<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 钦达牟尼一毛都 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为马兜铃科植物东北马兜铃(aristoloehia manshuriesis kom．)、毛莨科植物小木通(clematis \r\n      armandii eraneb．)或木通科植物白木通(arebia trifoliata&lt;thunb&gt; koidz．var．alstralis \r\n      &lt;diels&gt;rehd．)的干燥茎。 9月采收，截取茎部，刮去外皮，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦，性凉、轻、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]有清热，止痛，止泻之功效。用于肝热诸症，腑热，肠刺痛，热泻，肺热咳嗽，咯黄色痰，气喘，血、希拉热及麻疹热等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (145, 2, 10, '秦艽花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>秦艽花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：吉勒哲<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙胆科植物大叶龙胆(gentiana macrophyllapall．)麻花秦艽(gentiana \r\n      straminea maxim．)或粗茎秦艽 (gentiana crassicaulis duthie ex burk，)的干燥花。夏、秋季花盛开时采摘，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、柔、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有清热，消肿，燥黄水之功效。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (146, 2, 10, '马钱子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>马钱子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：混其勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为马钱科植物马钱(strychnos pierriana a.w.hill)或 (strychnos \r\n      nux-vomica l)的干燥成熟种子。冬季采收成熟果实，取出种子，晒干。用时先将沙子置锅内炒热后，加入马钱子，不断翻动，烫至鼓起并显棕褐色或深棕色时取出，放凉，刮去毛备用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 苦，凉、轻、钝，有大毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有平喘，清热，解毒，止痛之功效。用于胸背刺痛，胸闷气喘，狂犬病，咽喉肿毒，炭疽等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (147, 2, 10, '水柏枝', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>水柏枝<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]巴乐古那<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为柽柳种植物河柏myricaria alopecuroides schrenk．的干燥嫩枝叶。夏季开花时采收，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]涩、甘，凉、钝、重、固。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，透疹，敛毒，祛协日沃素。用于毒热，血热、麻疹，协日沃素病。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (148, 2, 10, '草木樨', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>草木樨<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：扎阿热图胡吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物黄香草木犀melilotus officinalis l desv或草木犀melilotus \r\n      suaveolens ledeb的干燥全草。夏、秋二季采收，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、轻、钝、稀、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛陈旧性热，杀粘，清除收敛扩散之毒的功效。用于毒蛇、毒虫咬伤，狂犬病，胃中毒症，毒蛇、毒虫咬伤，咽喉肿痛等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (149, 2, 10, '土茯苓', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>土茯苓<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]陶丕浪<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为百合科植物光叶菝葜smilax glabra roxb的干燥根茎。夏、秋二季采挖，除去须根，洗净，干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘，干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]镇赫依，清热，解毒。用于赫依病，包如希拉病，巴达干病，吾雅曼病，梅毒，协日沃素病(黄水病)，粘症，血液病，妇女血症，滴虫病，消化不良等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (150, 2, 10, '红花绿绒蒿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>红花绿绒蒿 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰乌达巴拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为罂粟科植物红花绿绒蒿(meconopsis punicea maxim．)的干燥全草。6―8月间采收全草，除去杂质，阴干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，寒、钝、腻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，止咳之功效。用于血热及中毒性肝病，肺热咳嗽等。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (151, 2, 10, '山豆根', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>山豆根<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：卓伦木其尔图宝雅<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物柔枝槐(sophoraus bprostrata chun et t．chen．)的干燥根及根茎。秋季采挖，除去地上茎叶及泥土，洗净，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、稀、柔、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，平希拉，止渴，疗伤，祛黄水之功效。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (152, 2, 10, '棱子芹', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>棱子芹<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：益日莫克图朝古尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物紫茎棱子芹pleurospermum hookeri c．b． clarke．)的干燥全草。8-9月间采摘，除去杂质，洗净泥土，放通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、涩、轻、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒之功效。用于药物中毒，诸毒症。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (153, 2, 10, '骨碎补', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>骨碎补<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：伯钦苏勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为水龙骨科植物槲蕨(drynaria fortunei &lt;kunze&gt; j．smith)或中华槲蕨 \r\n      (d．baronii &lt;christ&gt; diels)的干燥根状茎。全年可采，洗净去毛，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，止血，疗伤之功效。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (154, 2, 10, '缬草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>缬草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：珠勒根胡吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为败酱科植物缬草(valeriana officinalis l．)及西北缬草(valeriana \r\n      tangutlca maxim．)的干燥根状茎及根。秋季采挖，除去秧苗及泥土，通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、轻、钝、稀、柔。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，镇静，消肿，止痛之功效。用于渗于正精之陈旧性隐伏热，诸毒热症，神心不安，失眠，癔病，癫痫等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (155, 2, 10, '三棱', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>三棱<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 阿尔札嘎尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为黑三棱科植物黑三棱(spargaium stoloniferum&lt;graebn&gt;bnch．-ham．)的干燥块茎。春季采挖块茎，洗净泥土，除去外皮，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品味苦，其性能、功效、应用与杉叶藻近似。孕妇忌用。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (156, 2, 10, '杉叶藻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>杉叶藻 <br>\r\n      <br>\r\n      [蒙药名]：阿木塔图哲格斯<br>\r\n      本品为杉叶藻科植物杉叶藻 hippuris vulgaris l．的干燥全草。秋季采收，除去杂质，洗净，在通风处阴干。<br>\r\n      [性味]：甘，凉、软、柔、钝。 <br>\r\n      [功能与主治]：有清热，祛瘀，改善肺功能之功效。用于肺、肝陈旧性热，浊热症，肺脓痈，咳嗽，咯脓血，骨伤、骨热等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (157, 2, 10, '松萝', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>松萝<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦乌塔素乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为松萝科植物节松萝(usnea diffracta vain．)的干燥全草。8― 9月间采收，除去杂质，洗净，通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、钝、软、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒之功效。用于敛毒、解毒，肠刺痛，腹鸣，泄泻，肺脓疡，因肺痨低烧，盗汗，削瘦，咯脓痰等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (158, 2, 10, '白花龙胆花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白花龙胆花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干珠勒根其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙胆科植物白花龙胆(gentiana algida pall．)的干燥花。7―9月间，花盛开时采收，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩、苦，寒、稀、柔、软。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，止咳，利咽喉之功效。用于感冒发烧，咽喉肿痛，口干舌燥，声音嘶哑，希拉性瘿病</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (159, 2, 10, '钩藤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>钩藤<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：嘎如迪茵浩木斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茜草科植物钩藤(uncaria rhynchophylla &lt;miq．&gt; jacks．)、大叶钩藤(uncaria \r\n      macrophylla wall．)、毛钩藤(uncaria hirsuta havii．)、华钩藤(sinensis(oliv．)havil．)、或无柄果钩藤(uncaria \r\n      sessilifructus roxb．)等的干燥带钩茎枝。秋、冬季采收，去叶，切段，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、钝、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒之功效。用于毒热症。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (160, 2, 10, '毛翠雀花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>毛翠雀花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：马斯太伯日其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛茛科植物毛翠雀花(delphinium frichophorum franch．)或宽距毛翠雀花(delphinium \r\n      trichophorum fr．var．platycentrum w．t．wang)的干燥地上部分。7一8月间花盛开时采割地上部分，除去杂质，洗净，切段，通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，止咳之功效。用于发烧，头及关节疼痛，时疫，咳嗽、气喘等肺热病等。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (161, 2, 10, '石花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈敦哈克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为地衣类梅花衣科植物藻纹梅花衣(parmelia saxatilis ach)的干燥全草。四季可采，用铲铲取，除去泥土杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦，凉、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，开胃，止吐，止泻之功效。用于根除渗于脏、腑、皮、脉、骨、肉之热邪及解毒热。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (162, 2, 10, '木腰子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>木腰子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：额力根芍沙<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物棵藤(entada pnaseoloides&lt;1)werr．)的干燥成熟种子。秋、冬二季果实成熟时采收，取出种子，阴干后去皮。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，性平、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有解肝中之毒，止痛，解痉之功效。用于肝区疼痛，因肝病引起的水肿，白脉病初期，腹痛、吐泻，两胁作痛，头痛，发烧等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (163, 2, 10, '穿山甲', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>穿山甲<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：玛塔日因浩木斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鲮鲤科动物鲮鲤(manis pentadactyla l．)的干燥鳞甲。捕得后杀死，置沸水中略烫，甲片自行脱落，除去杂质。洗净，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸，寒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，消肿，活血之功效。用于骨伤，局部肿痛，麻痹，产后发热等。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (164, 2, 10, '小叶锦鸡儿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>小叶锦鸡儿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦哈尔嘎纳<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物锦鸡儿(caragana mierophylla lam．)的干燥根。秋季采挖，除去外皮，洗净，切段，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、涩、轻。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，制痈，收敛之功效。用于咽喉肿痛，高血压，头晕。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (165, 2, 10, '龙胆花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>龙胆花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干吉勒哲<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙胆科植物西藏龙胆(gentiana tibetica king)、麻花秦艽(gentiana \r\n      straminea maxim．)或粗茎秦艽 (gentiana crassicaulis duthie ex burk，)的干燥花。夏、秋季花盛开时采摘，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，凉、轻、柔。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，消肿，止血，解毒，祛黄水之功效。用于诸热症，黄水热症，希拉热症，胃、肠等腑热症，皮肤炭疽、丹毒，痈疖，乳痈，鼻衄，吐血，崩漏，外伤出血等。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (166, 2, 10, '乌奴龙胆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>乌奴龙胆<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：索布尔根其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为龙胆科植物乌奴龙胆(gentiana urnula h．smith．)的干燥全草。7―9月间花盛开时采集全草，洗净，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒、钝、涩、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，解毒，止泻之功效。用于血热、希拉热势炽盛，希拉性泄泻，陈旧而扩散之毒热等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (167, 2, 10, '桑椹', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>桑椹<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：亚勒玛<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为桑科植物桑(morus alba l．)或蒙桑(morus mongolica schneid．)的干燥果穗。4―6月间，果穗红时采摘，略蒸通风处阴干或制膏。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、酸，凉、腻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清骨热，滋补养阴之功效。用于妇女骨热。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (168, 2, 10, '珍珠干', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>珍珠干<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干-干达嘎日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蔷薇科植物库页悬钩子(rubus sachalinensis leveille)的干燥茎，夏、秋二季采割，除去细枝及叶，阴干，刮去外皮和内心。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、微辛，平、软。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有促使瘟疫成熟，清骚热，止咳，调理三根之功效。用于未成熟热，骚热症，感冒，咳嗽，合并赫依之热症，肺热咳嗽、气喘，痰多而不易咳出等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (169, 2, 10, '樟脑', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>樟脑<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查松嘎布日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为樟科植物樟(cinnamomum camphora&lt;l．&gt;presl)的根、干、枝、叶等之经提炼制成的颗粒状结晶。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛、涩、凉、软、钝、稀、浮，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，止痛，开窍之功效。用于山滩界赫依性热，根治陈旧性潜伏热，骚热、疫热之增盛期，丹毒，毒热，胸部脓疡，脏腑热，脉热，血热，骨热等诸热症，卒然昏迷，不省人事，伤筋化脓等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (170, 2, 11, '冬葵果', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>冬葵果<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：额布勒珠尔其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为锦葵科植物冬葵(malva verticillata l．)的干燥成熟果实。夏秋二季果实成熟时采收，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，涩；性凉、锐、重、干。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：开脉窍，利尿，消肿，燥脓，止泻，止渴。用于治尿闭，膀胱结石，腰腹疼痛，小便不利，尿时作痛，血尿，尿路感染，膀胱热，肾热等症。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (171, 2, 11, '螃蟹', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>螃蟹<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乃莫勒吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为螃蟹科动物中华绒螯蟹(eriocheir sinensis h．milne-edwards．)的干燥全体。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有利尿，消肿之功效。用于水肿，尿闭，膀胱结石，肾伏热身重，腰部酸痛，颜面浮肿，肾热症及布鲁氏菌病，腰部、膀胱、尿道灼痛，尿频、尿血及膀胱热证等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (172, 2, 11, '花 椒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>花 椒<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:花珠(也日玛)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:辛、温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为芸香科植物花椒Zanthoxylum bungeanum maxim 的成熟干燥果实。秋季果实成熟时采收晒干，除去种子及杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:开窍通脉，驱虫，止痒，消食。用于巴达干粘液增多，消化不良，胃胀肠鸣，虫病腹痛，皮肤瘙痒，皮癣。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (173, 2, 11, '蜗牛壳', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蜗牛壳<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]布热浩如海<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蜗牛科动物同型蜗牛bradybana similarisfrusae的干燥贝壳。夏季捕捉，捕得后用沸水烫死，取壳洗净，晒干后再用火硝水煮或煅用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、咸、凉，有小毒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]利尿，逐水，杀虫，祛瘟。用于肾伏热，腰痛，腰腹坠痛，尿频，小便不利，尿道灼痛，膀胱热，膀胱石痞，水肿。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (174, 2, 11, '海金沙', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>海金沙（lygodium iaponicum(thunb)sw）<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉腾额勒斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]：苦、咸、寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：利尿，逐水，破石痞。用于肾伏热，尿闭，水肿，膀胱热，膀胱石痞。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (175, 2, 11, '蒺藜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蒺藜<br>\r\n      <br>\r\n      [蒙药名]亚曼章古<br>\r\n      本品为蒺藜科植物蒺藜tribulus terrestris l的干燥成熟果实。秋季果实成熟时采割，晒干，打下果实，除去杂质，微炒，晾干。<br>\r\n      [性味]甘、微苦，温、锐、轻，稀。<br>\r\n      [功能与主治]祛肾寒，镇赫依，强壮，利尿逐水。用于浮肿，水肿，尿闭，肾寒引起的腰痛，遗精，阳萎。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (176, 2, 11, '斑蝥', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>斑蝥<br>\r\n      <br>\r\n      [蒙药名]阿拉格斑布<br>\r\n      本品为芫青科昆虫南方大斑mylabris phaleratapall或黄黑小斑蝥mylabris cichorii l．的干燥全虫。 7―8月清晨露水未干时捕捉。捕捉时宜戴手套及口罩，以免毒素刺激皮肤、粘膜。捕捉后装入布袋中，用沸水烫死，然后除去足及白薄翅，放在炒热的麦子中，待麦子凉后，取出斑蝥。 \r\n      <br>\r\n      [性味]苦、辛，干，有大毒。<br>\r\n      [功能与主治]利尿，逐泻脉疾，攻毒。用于狂犬病，脉管病，秃疮，脓疱疮、鼠疮，恶疮，痈疖，妇血证。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (177, 2, 11, '槟榔', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>槟榔<br>\r\n      <br>\r\n      [蒙药名]：高优<br>\r\n      本品为棕榈科植物槟榔(areca catechu l．)的干燥种子。春末夏初槟榔成熟时，采收果实，取出种子，晒干或烘干。<br>\r\n      [性味]：苦、辛，温。<br>\r\n      [功能与主治]：有杀虫、驱虫，驱寒性赫依，益肾，利尿，养齿之功效。用于腰胯部坠痛，腰及下肢关节酸痛，肌肉痛，眩晕，心悸，气喘等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (178, 2, 11, '刀豆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>刀豆<br>\r\n      <br>\r\n      [蒙药名]：波仁芍沙<br>\r\n      本品为豆科植物刀豆(canavalia gladiata &lt;aeq&gt; dc．)的干燥成熟种子。秋季刀豆果实老熟时，采摘晒干，打下种子后，除去杂质，再晒干。<br>\r\n      [性味]：甘，温、腻、和。<br>\r\n      [功能与主治]：有清肾热，补肾虚之功效。用于伤肾，腰腿酸痛，肾热病，肾寒腰腿痛，头晕，遗精，胯部酸软疼痛或强直。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (179, 2, 11, '芒果核', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>芒果核<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]芒果日乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为漆树科植物芒果mangifera indica l的干燥成熟种子。7―8月间果熟时采摘，收集果核，干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、酸，温、重、油。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]补肾，祛肾寒。用于肾虚，肾寒，腰腿痛。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (180, 2, 11, '菥冥子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>菥冥子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：衡格日格乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为十字花科植物遏蓝菜(thlaspi arvense。1．)的干燥成熟种子。6―7月菥冥果实成熟时，采割晒干后，打下种子，除去杂质，晾干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、苦，温、腻、轻、和。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清肺、肾热，开胃。利尿、滋补之功效。用于肾内伏热而腰痛，颜面浮肿，睾丸肿，遗精，腰腿痛，肾虚，精亏，阳萎，食欲不振，消化不良，胃腹胀满等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (181, 2, 11, '刺柏叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>刺柏叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]乌日格斯图一阿日查<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为柏科植物杜松juniperus sigida sieb.et zucc．的叶和嫩枝叶。夏、秋季采收，除去老枝等杂质，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、涩、凉、糙、轻、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清肾热，利尿，燥协日沃素，疗伤，止血。用于肾热，尿血，尿道疼痛，痛风，游痛证，协日沃素病(黄水病)，淋病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (182, 2, 11, '滑石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>滑石<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：特尼格尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为单科晶系硅酸盐类矿物滑石的块状体。采挖后，除去泥沙及杂石。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，寒、重。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有利尿，清热，破痞，泻脉病，燥黄水，清热之功效。用于膀胱灼痛，手、脚心发热，月经不调或经闭，水肿，脉痞、脉伤等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (183, 2, 11, '蜀葵花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蜀葵花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：哈洛其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为锦葵科植物蜀葵 (althaea rosea&lt;l&gt;cay．)的干燥花。秋季花凋谢前采收，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸、甘，寒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有利尿、消肿、清肾及膀胱热、止遗精尿血之功效。用于月经淋漓，遗精，尿频，全身水肿，膀胱脉伤，尿闭，身疲发烧，浮肿，腰痛，肾热病等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (184, 2, 11, '白茅根', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白茅根<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]乌力吉图乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为禾本科植物白茅根(imperafa cylindricabeauv．var．manjor&lt;nees&gt;c.e.0hubb．)的干燥根茎。秋季采挖，除去泥土、须根、捆成小把，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 甘、涩、性平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有利尿、解毒、止血、补阳之功效。用于尿频，尿闭，水肿，内出血，鼻衄，外伤出血等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (185, 2, 11, '长毛风毛菊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>长毛风毛菊<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌素尔赫格朝如勒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物长毛风毛菊 (saussurea hieracioides hook．)的干燥全草。8―9月采集全草，除去泥及枯叶，洗净，放通风处阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有消肿之功效。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (186, 2, 11, '西藏点地梅', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>西藏点地梅<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：图布德达兰陶布其<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为报春花科植物西藏点地梅(anbrosace matiae kanifz var tibetica&lt;maxim．&gt;hand．――― \r\n      mazz．)的干燥全草。5―6月花盛开时采收，除去黄叶，洗净泥土，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有利尿，燥黄水，消肿，杀粘之功效。用于浮肿，水肿，扩散于关节的黄水病，炭疽，咽喉肿痛等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (187, 2, 11, '问荆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>问荆<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：呼呼格乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为木贼科植物问荆 equisetum arvenense l．的干燥茎。6-7月生长旺盛，营养充足时，采割后洗净泥土，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有开脉窍，利尿，破石痞，强身，止痛之功效。用于膀胱石痞，水肿，外伤，血崩、鼻衄、吐血等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (188, 2, 11, '牛蒡子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>牛蒡子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：西伯乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物牛蒡arctium lappa l．的干燥熟果实。秋季采果实，晒干，碾碎，除去杂质，再阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、苦，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有破痞，泻脉病，利尿之功效。用于尿闭，石痞，死胎不下，，脉痞，伤脉等。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (189, 2, 11, '卷柏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>卷柏<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：玛塔日音浩木斯乌布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为卷柏科植物卷柏 (selaginella famariscina &lt;beauv&gt;spr)或垫状卷柏(relaginella \r\n      puluinata &lt;hook．et grev&gt;maxim．)的干燥全草。四季均可采，但夏季采者为佳，采集后洗净泥土，除去杂质，剪去须根，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有利尿、止血、清血热、杀虫之功效。用于产后发热，陈久性伏热，阴道虫病，尿闭，经血淋漓，外伤出血，鼻衄等 \r\n      。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (190, 2, 11, '大托叶云实', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>大托叶云实<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：卓冷乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物大托叶云实(caesalpinia crista l．)的干燥成熟种子。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛胃、肾寒，补肾之功效。用于下身寒凉腰胯酸痛，尿频，遗精，肌肉拘痛，尿闭，膀胱石痞，肾热，肾脉震伤，肾性布鲁氏菌病，妇女下寒等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (191, 2, 11, '桧叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>桧叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌赫尔阿尔查<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为柏科植物圆柏(sabina chinensis &lt;l&gt; antoine)的干燥叶。夏秋采集，除去枯枝叶及杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;性味、功能、临床应用与刺柏叶同。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (192, 2, 11, '锦葵果', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>锦葵果<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：昭孙其其枯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为锦葵科植物大花葵(malva mauriatina l．)的干燥熟果。秋季果成熟时采摘，除去杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品性味、功能、主治与冬葵果相同。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (193, 2, 11, '藏青果', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>藏青果<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌楚很哈热阿如拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为使君子科植物青诃子(terminalia chebula retz．)的干燥幼果。9―10月采取未熟青果，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有增强视力，消肿之功效。用于暴发火眼，眼翳，眼睑糜烂，羞明，头痛流泪，腹水青筋暴露、水肿等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (194, 2, 11, '侧柏叶', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>侧柏叶<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿尔查<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为柏科植物侧柏(platyclab usorientalis &lt;l&gt; franco)的干燥嫩枝和叶。夏秋采收，除去枯枝杂质，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，微寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有凉血，止血，清肺止咳之功效。用于血热脱发，肺热咳嗽。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (195, 2, 12, '白贝齿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白贝齿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干益布海<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为宝贝科动物货贝(monetaria moneta (l．)、或环纹货贝(monetaria \r\n      &lt;ornanetaria &gt; annulusl．)的贝壳。产于南海，5---7月捕捞，除去贝肉，洗净晒干，或煅透备用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸，平。煅炭辛、苦，性温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为破瘤、燥湿、止血、去翳。主治胃癌、宫颈癌、瘀血、肿痛、肺脓疡、鼻衄，咯血，目赤翳膜等疾病。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (196, 2, 12, '紫贝齿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>紫贝齿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：宝如益布海<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为宝贝科动物阿拉伯绶贝(mauritia &lt;arabica&gt; arabica&lt;l&gt;)的贝壳。夏季捕捉，除去贝肉，洗净，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;该药材炮制，性味，主治用法与白贝齿相同。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (197, 2, 12, '鼠曲草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>鼠曲草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]；黑布日格纳<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物鼠曲草(gnaphalium affine d．don．)的干燥全草。开花时采收，除去杂质晒干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，苦、温、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 破痞，解毒，祛巴达干，止咳。用于寒性痞瘤,中毒症,感冒咳嗽气喘，恶心,麻风病，萨病，布鲁氏菌病，痛风等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (198, 2, 12, '毛茛', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>毛茛<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:浩勒都顺一其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛茛科植物毛茛ranunculus jopnicus thunb的全草。夏、秋季采集，切段，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]:辛、热、轻、燥、锐，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:破痞，温中，止腐，祛黄水，逐水，杀虫。用于胃痛，食积，虫痞，肝痞，黄水病，风湿病，痈肿，水肿。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (199, 2, 12, '胆矾', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>胆矾<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：呼和白矾<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为硫酸盐类矿物胆矾的天然晶体或用化学方法人工制造的胆矾(vitriolccm caeruleum \r\n      &lt;chalcanthitum&gt;)，系含水硫酸铜结晶。可于铜矿中挖得，选择蓝色透明的结晶，即得；亦可用硫酸作用于铜片或氧化铜而制得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：酸、辛，寒，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：功能破痞，燥湿杀虫。催吐，化痰消积，祛翳。主治口舌生疮，目亦肿翳，食物中毒，壅塞咽喉，舌肿，痔疮等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (200, 2, 12, '黄矾', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄矾<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]希拉白矾、斯日苏尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为硫酸盐类矿物黄矾 (fibroferrite)的矿石。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]本品味酸、涩、咸；性平，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]功能破痞，防腐收敛，止痛。主治各种痞块，恶疮，痔瘘，疮疡肿毒等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (201, 2, 12, '芒硝', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>芒硝<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]查森硝<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为硫酸盐类矿物芒硝族芒硝mirabilite，经加工精制而成的结晶体。主含含水硫酸纳(na2so4・ \r\n      10h20)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]咸、温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]破痞，温中，消食，逐水，缓泻。用于胃脘痞，食痞，消化不良，浮肿，水肿，乳肿，闭经，便秘。 \r\n      <br>\r\n      等配伍，制成寒水石灰剂用。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (202, 2, 12, '火硝', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>火硝<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]嘎勒硝<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为斜方晶系矿物硝石niter，经加工炼制而成的结晶。主含硝酸钾。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味)咸、苦、温，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治)破痞，散积，利尿，杀虫。用于膀胱石痞，食痞，食积，尿闭，闭经。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (203, 2, 13, '贝母', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>贝母<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：奴格图如一额布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为百合科植物川贝母fritillaria cirrhosa<br>\r\n      don、暗紫贝母fritillaria unibracteata hsiao et k．c hsia、甘肃贝母fritillaria przewalskii \r\n      maxim、或梭砂贝母 fritiuaria delavayi franch．的干燥鳞茎。前三者按性状不同分别习称“松贝”或“青贝”，后者习称“炉贝”。夏、秋二季或积雪融化时采挖，除去须根、粗皮及泥沙，晒干或低温干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：清肺，止咳，祛痰。用于肺热咳嗽，肺刺痛，气喘，感冒喉痛，鼻塞，不思饮食，咯痰带血，郁结。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (204, 2, 13, '桔梗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>桔梗<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：浩尔敦查干<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为桔梗科植物桔梗(platycodon grandiflorum &lt;jacq&gt; \r\n      a．dc)的干燥根。春、秋二季采挖，以秋季采挖为佳，去掉苗茎，洗净泥土，用竹刀或碗片刮去粗皮，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、甘，寒、涩、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为清肺热，止咳，祛痰、排脓。主治感冒咳嗽，肺刺痛，咯黄色脓性痰，胸闷气短，肺脓疡。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (205, 2, 13, '北沙参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>北沙参 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：查干索尔洛<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为伞形科植物珊瑚菜(glehnia littoralis fr schmidt．ex miq．)的干燥根。夏、秋二季采挖除去须根，洗净，稍晾，置沸水中烫后，除去外皮，干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、微苦，轻、凉、腻、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：功能清肺热，止咳，润肺。主治肺热咳嗽，咯痰带血，胸痛，胸闷，气喘，慢性气管炎，体虚无力。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (206, 2, 13, '拳参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>拳参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：莫何日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蓼科植物拳参(po1ygonum bistorta l．)干燥根状茎。春、秋二季挖取根状茎，去掉茎叶及须根，洗净、晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、涩，凉，钝、柔、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为清肺热，解毒，燥黄水，止泻，消肿。主治肺热咳嗽，急慢性气管炎，胸胁作痛，脉热病，痢疾，肠炎。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (207, 2, 13, '红景天', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>红景天<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰索尔洛<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为景天科植物红景天(rhodiola sacra cprain ex hamet fu．)，以全草入药。秋季采挖，除去粗皮，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦、涩、寒、柔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：功能为清热养肺，滋补。用于肺热，气喘、肺刺痛、咯黄痰。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (208, 2, 13, '火绒草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>火绒草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:查干阿荣<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物火绒草Leontopodium eontopodioides(willd) V.的地上部分入药，夏、秋二季采割，除去杂质，洗净，晒干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:苦、寒、钝、柔、和。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:清热，止咳，燥肺脓。用于肺热咳嗽，痰中带血，感冒咳嗽，咽喉肿痛，肺脓疡。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (209, 2, 13, '石灰花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石灰花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：西瑞音竹冈<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为一种主含碳酸钙的粉状物。采收后除去泥沙及杂石，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：微甘，凉，柔、和、重、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：清热，止咳。主治肺热咳嗽，久热不愈，痰中带血，低热盗汗，体乏无力，肺脓疡，慢性支气管炎，痈疽疮疡。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (210, 2, 13, '茵 陈', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>茵 陈<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]阿莱<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物滨蒿(artemisia scoparia waklst．et kit．)或茵陈蒿(artemisia \r\n      capillaris thunb．)的去根幼苗。春季幼苗高6-7cm时，挖出全<br>\r\n      草去根，除去杂质，晾干或晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 苦、辛；性凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]为清肺热，化痰，利胆退黄。用于肺热咳嗽，黄疸型肝炎。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (211, 2, 13, '朱岗(石膏、石灰华、天竺黄)', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>朱岗(石膏、石灰华、天竺黄)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]zhugang<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为单斜晶系硫酸盐类矿物石膏及一种主含碳酸钙的粉状块物石灰华，或禾本科植物青皮竹bambusa \r\n      textilis mcclure、华思劳竹schizostachyum chinense ren-dle的杆内分泌液干燥后的块状物。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、凉、柔、和、重、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，止咳，疗伤，退黄。用于肺热，肺脓肿，气喘咳嗽，咯血，气管炎，伤热，黄疸，跌打损伤。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (212, 2, 13, '石膏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石膏<br>\r\n      <br>\r\n      [蒙古名]：为楚龙竹冈<br>\r\n      本品为单斜晶系石膏(cypsum)的矿石，主含水硫酸钙。采挖后，除去泥沙及杂石。<br>\r\n      主治用法与石灰华相同。 </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (213, 2, 13, '甘草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>甘草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:希和日额布斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物甘草Glyeyrrhiza Uralensis Fisch 的干燥根及根茎。春、秋二季采挖，除去须根，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:甘，凉、稀、和、柔、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:止咳，祛痰，止吐，滋补，止渴，清热，解毒。用于肺热，咳嗽，咯痰，气喘，肺脓肿，妊娠呕吐，口干烦渴，咽喉肿痛，白脉病，中毒症。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (214, 2, 13, '青蒿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>青蒿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：毛仁希拉勒吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为菊科植物黄花蒿(artemisia annual)或(artemisia apiaeea \r\n      hanee)的干燥地上部分。夏季花开前枝叶茂盛时采割，除去老茎，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，凉，轻、钝、涩、燥。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为清热，利咽喉，消肿。用于热邪潜于肺，热邪渗于骨，咽喉肿痛，牙龈炎，天花、疹毒八喉。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (215, 2, 13, '远志', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>远志<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：朱日很其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为远志科植物远志(polygala tenuifolia willd或卵叶远志polygala \r\n      sibirica l．)的干燥根。春、秋二季采挖，除去须根及泥沙，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、苦、辛，平、柔、和、动。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为润肺，敛肺脓，消肿。主治肺脓疡，胸部创伤，咳嗽痰多、咯血。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (216, 2, 13, '香附', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>香附<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]萨哈勒乌布森温都素、拉刚<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为莎草科植物莎草(cyperus rotundus l．)的干燥根茎。秋季采挖，燎去毛须，置沸水中略煮或蒸透后晒干，或燎后直接晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛，涩，微甘，性平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 为清肺热，理气疏肝，止泻，止痛。主治肺热咳嗽，气喘，痰中带血，慢性气管炎，胃腹胀痛，泻泄。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (217, 2, 13, '葫芦巴', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>葫芦巴<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：安赫鲁莫勒宝尔楚克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物胡卢巴 (trigonella foenum graecum l．)的干燥成熟种子。夏、秋二季果实成熟时采割植株，晒干，打下种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，重，平，腻，燥，涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为燥肺脓，止泻，抑赫依。主治肺脓疡，热痢，腹泻。 </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (218, 2, 13, '绵参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>绵参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌格日茵达日雅干<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为唇形科植物绵参(eriophyton wallichii benth．)的干燥全草。盛花期采集，去净泥土，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，止咳，祛痰，排脓，疗伤，接脉之功效。用于肺脓肿，肺结核，肺热咳嗽，脏腑内伤，脉伤，失血等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (219, 2, 13, '花苜蓿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>花苜蓿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：奇日克<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为豆科植物花苜蓿(trigohella ruthenica l，)的干燥全草。6―7月采全萆，洗净，去残叶须根，晾干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，寒、燥、轻，和。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为清肺热，疗创伤，止血，解毒。主治肺热咳嗽，肺脓疡，痰带脓血，外伤出血，毒散于肉，食肉中毒。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (220, 2, 13, '葡萄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>葡萄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌珠木<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葡萄科植物葡萄(virtis vinfers l．)的果实。夏末秋初果熟时采<br>\r\n      收，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，微涩；重、柔、和、稀、凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为清肺热，止咳平喘，滋补强身。主治肺热咳嗽，慢性气管炎，咳嗽痰多，胸胁作痛，疹热入肺，肺水肿，温热烦渴，精神疲惫。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (221, 2, 13, '沙棘', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>沙棘<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]沏其日甘<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为胡颓子科植物沙棘hipophae rhamnoides l的干燥成熟果实。秋、冬二季果实成熟时或果实冻硬时采收，晒干，或制成浸膏用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 酸、涩，温、燥、油、固。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]止咳祛痰，祛巴达干，活血，助消化。用于咳嗽痰多、气管炎，气喘，消化不良，肺气肿，血痞，闭经，包如病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (222, 2, 13, '羚羊角', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>羚羊角<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：布洪给音额布尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为牛科动物赛加羚羊(salga tatarica linnaeus)的角。全年均可捕捉，捕得后，将角从基部锯下。一般以8―10月猎取者色泽最好。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：咸，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热解毒，平肝息风，破血痞、排脓，止泻，催肺脓疡，血痞，经闭等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (223, 2, 14, '大黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>大黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：格喜古讷<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蓼科植物掌叶大黄 (rheum palmatum l．)唐古特大黄(rheum tangutieum \r\n      maxim et baill )或药用大黄(rheum officinsle<br>\r\n      baill)的干燥根及根茎。秋末茎叶枯萎时或次春发芽前采挖，除去细根，刮去外皮，切瓣或段，绳穿成串干燥或直接干燥。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、酸，稀、轻、动、涩、凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：导泻，清热，破瘀，解毒，消食。主治实热便秘，积滞腹痛，消化不良，湿热黄疸，食欲不振，血瘀经闭，痈疖疗疮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (224, 2, 14, '酸模', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>酸模<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：浩日根其赫<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蓼科植物酸模(rumex acetosa l．)的干燥根。夏、秋季采收、晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：酸、苦、涩，稀、和、涩、重、柔、锐、平。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为泻下、杀虫，消肿、愈伤。主治疫热，炭疽、痈肿、乳腺炎，腮腺炎，创伤，骨折，恶疮，疥癣，烧伤、烫伤。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (225, 2, 14, '瑞香狼毒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>瑞香狼毒<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：达楞图如<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为瑞香科植物瑞香狼毒(stellera chamae jasme l．)的干燥根。春秋采挖，去茎叶、泥沙，切段，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，辛，涩、动、轻平，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：为逐水杀粘、治痈消肿。用于肉、骨、脉痈，乳腺炎，腮腺炎，骨、淋巴结核，天花、疹毒等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (226, 2, 14, '狼毒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>狼毒<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]塔日奴<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为大戟科植物狼毒大戟euphorbia fischeriana steud的干燥根。春、秋二季采挖，洗净，切片，晒干。祛巴达干病则用牛奶浸煎，破痞则用白酒浸煎，杀虫则用童便浸煎，取出狼毒，晾干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛、温、稀、锐、动、糙，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]泻下，消肿，消奇哈，杀粘虫。用于炭疽，白喉，粘热，疫热，痈肿，疥疮，水肿，黄水病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (227, 2, 14, '蓖麻子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>蓖麻子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉格麻吉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为大戟科植物蓖麻 (ricinus communis l)的干燥成熟种子。秋季采摘成熟果实，晒干，除去果壳，收集种子。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，辛，锐，平，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：消肿，拔毒，泻下，通滞。用于巴达干病、痞病、浮肿、水臌、虫病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (228, 2, 14, '巴豆', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>巴豆<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]丹茹格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为大戟科植物巴豆croton tiglium l的干燥成熟果实。秋季果实成熟时采收，晒干。用时取净巴豆仁，碾碎，用多层吸油纸包裹，加热，压榨去油，至不再粘结成饼为止。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]辛、平、重，有大毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]功下积滞，逐水消肿，祛巴达干。用于痈肿，巴达干病，癫狂，水肿，粘痧，炭疽，狂犬病，消化不良，粘症，毒症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (229, 2, 14, '牵牛子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>牵牛子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]混德根--其其更乌热 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为旋花科植物圆叶牵牛pharbitis purpurea (l．)voigt或裂叶牵牛pharbitis \r\n      hederacea choisy．的干燥成熟种子。秋末果实成熟、果壳未开裂时采割，晒干，打下种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、凉，有小毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]泻下，驱虫，祛协日沃素。用于腹胀便秘，腹水，虫疾，希拉病，协日沃素病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (230, 2, 14, '腊肠豆1', '', 1, '2003-06-06');
INSERT INTO myy_text VALUES (231, 2, 15, '五味子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>五味子<br>\r\n      <br>\r\n      [蒙药名]:乌拉乐吉甘<br>\r\n      本品为木兰科植五味子schisandra chinensis baill或<br>\r\n      [性 味]:甘、酸、平、燥、轻。<br>\r\n      [功能与主治]:止吐，止泻，开胃，平喘。用于胃火衰败，久泻不止，希拉性腹泻，气喘，呕吐。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (232, 2, 15, '车前子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>车前子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌和尔乌尔格讷<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为车前草科植物车前(plantago asiatica l)及平车前(plantago depressa \r\n      willd)的干燥成熟种子。夏秋季种子成熟时采摘果穗，晒干，搓去种子，除去杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涩，轻、燥、平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止泻，利尿，敛黄水，疗伤，止血之功效。用于肠刺痛，热带泄泻病，尿闭，尿路感染，尿频等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (233, 2, 15, '葫芦子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>葫芦子<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:胡林乌热<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葫芦科植物(lagenaria siceraria (molina)standl)的干燥成熟果实。秋季采收成熟果实，剖取种子，晒干。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:酸、涩，平、燥、固。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:止泻，润肺，疗伤。用于寒热性腹泻，肠刺痛。 <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (234, 2, 15, '木瓜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>木瓜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:嘎迪拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蔷薇科植物贴梗海棠chaenomeles speciosa<br>\r\n      sweet nakai或木瓜chaenomeles sinensis thouin koehne的干燥成熟果实。前者称“皱皮木瓜”，后者称“光皮木瓜”。秋季果实开始发黄时采收，纵剖成二瓣或四瓣后入沸水中略烫，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：酸、涩、凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：清热，止泻。用于热性泄泻，肠刺痛。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (235, 2, 15, '翠雀花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>翠雀花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：勃尔其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为毛茛科植物翠雀花(delphinium grandiflorum l．)的干燥带花全草。夏秋开花时采摘，除去泥土杂质，洗净，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦，钝、轻、凉，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止泻，疗伤，燥脓血、黄水，清热之功效。用于血、希拉性泄泻，肠刺痛。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (236, 2, 15, '报春花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>报春花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：乌兰哈布尔希勒其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为报春科植物带叶报春(primula vittata bur．et franeh)及红花粉叶报春(primulafarinosa \r\n      l．)的干燥全草。6―8月间，报春花开放时连根采挖，除去泥土，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、甘，寒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止泻，清热燥湿，止咳，止血之功效。用于肠刺痛，热性泄泻，肺脓疡，咳血等。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (237, 2, 15, '锡金报春花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>锡金报春花<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：希拉哈布尔希勒其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为报春科植物锡金报春花(primula sikkimensis hook．)的干燥全草。7―8月间开花时连根采挖，除去泥土，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，寒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，止泻之功效。用于肠痼疾。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (238, 2, 15, '珠芽蓼', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>珠芽蓼<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：浩尔干莫和尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为叉分蓼科植物珠芽蓼(polygonum viviparum l．)的干燥根茎。秋季茎叶枯干时挖取，除去泥土，洗净，阴干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：涩、甘、酸，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止泻，止痛，止血，清热之功效。用于肠刺痛，痧症，鼻衄，阴道出血，咳血、渗漏型包如病等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (239, 2, 15, '茯苓', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="64">\r\n  <tr>\r\n    <td>茯苓<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 那日松一西莫<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为多孔菌科植物茯苓(poria cocos (schw．)wolf)的干燥菌核。野生茯苓从7月至翌年三月间采挖；栽培者在第二、三年采挖。立秋后采挖者为佳。挖取后除去泥土，洗净，放入垫有一层稻秸之陶器内，用麻袋覆盖其口至发汗，取出擦去水珠放阴凉处，外皮干燥后，再用上法使之发汗，如此反复3---4次。待外表起皱，呈紫褐色时，放干燥阴凉处阴干。此谓全茯苓；菌核内之白色部分称白茯苓；菌核的外皮称茯苓皮；近菌核外皮部的淡红部分称亦茯苓；抱有松根的菌核称茯神。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]涩、甘，性平、燥、滴、轻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]有止泻利尿，助消化的功效。用于热性、寒性泄泻，缓解藜芦等峻泻剂之作用。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (240, 2, 16, '黄精', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄精<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:查干霍日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为百合科植物滇黄精Polygonatum Kingianum Goll.et Hemsl和黄精Polygonatum \r\n      Sibiricum Red的干燥根茎。春、秋二季采挖，除去须根，洗净，置沸水中略烫或煮至透心，晒干。用时置牛奶中煮煎至透心，取出，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性 味]:甘、涩、苦，温、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]:滋补壮阳，调胃火，开胃，祛巴达干，祛协日沃素。用于肾寒引起的遗精，腰腿痛，困乏无力，头晕，阳萎，协日沃素病，风湿病，肾达日干，体弱，营养缺乏。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (241, 2, 16, '天门冬', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>天门冬<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]赫日燕钕德<br>\r\n      本品为百合科植物天门冬asparagus cochinehine- sis lourmerr的干燥块根。秋、冬二季采挖，洗净，除去根头和须根，置沸水中煮煎或蒸至透心，趁热除去外皮，洗净，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]苦、涩、甘，温、轻、燥、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]滋补，锁精，祛协日沃素，镇赫依，清伏热及陈热。用于体弱，肾寒，关节麻木，阳萎，遗精，头晕，痛风，风湿病，黄水病，肾及膀胱伏热。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (242, 2, 16, '紫茉莉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>紫茉莉<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：茉乐热格其其格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为茉莉科植物喜马拉雅紫茉莉(mirabilis himalaica&lt;edgew．)heim．)的干燥根。秋季采集削去外皮，切片晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、辛，轻、温、燥。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有滋补壮阳、祛肾寒，除黄水，破石痞之功效。用于少精，阳萎，遗精，身体虚弱，腰腿痰痛，肾脏痼疾，妇女血症，子宫痞，膀胱石痞，尿闭等。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (243, 2, 16, '天花粉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>天花粉<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]巴斯布如<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为葫芦科植物栝楼trichosanthes kirilowii maxim的干燥根。秋末冬初采挖，洗净，除去外皮，切段，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、辛、微苦，温、轻、燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]滋补壮阳，祛肾寒，祛协日沃素，破石痞。用于肾寒体弱，阳萎，遗精，腰腿酸痛，妇女血痞，膀胱石痞，协日沃素病，风湿病。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (244, 2, 16, '人参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>人参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：奥尔浩代<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为五加科植物人参(panax ginseng c．a．mey．)的干燥根，栽培者为“园参”，野生者为“山参”。多于秋季采挖，洗净，晒干或烘干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]： 甘、微苦，温。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有滋养生津，补益正精，安神宁心之功效。用于心悸怔忡，久病体虚，心衰，气短喘促，赫依性疾病，正精耗伤，口渴多汗，气短喘促，昏厥、面色苍白，大汗肢冷，呼吸微弱，脾胃久虚，精华内耗，呕吐泄泻等。<br>\r\n      此外，人参芦有消食，开胃、调赫依，和内，解全石药物毒之功效；人参叶有健胃，降虚火，清暑热，益头目，强四肢的功效。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (245, 2, 16, '手掌参', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>手掌参<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]:额日赫腾乃嘎日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为兰科植物手掌参gymnadnia conopsea r broyn的干燥块茎。夏、秋二季采收，除去须根及泥土，晒干，置牛奶中煮至透心，取出，干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涩，温、重、油、柔、稀、钝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：滋补益精，壮阳。用于遗精，滑精，萎，消瘦无力，久病体虚，神志恍惚，风湿病，巴木病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (246, 2, 16, '冬虫夏草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>冬虫夏草<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]浩如海莫古<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为麦角菌科真菌冬虫夏草cordyceps sinensis (berk．)sacc．寄生在蝙蝠蛾科昆虫蝙蝠蛾hepialus \r\n      ar― moricanus oberthvr幼虫上的子座及尸体的复合体。夏初子座长出地面时挖取，晒至6―7成干后，除去丝状附着物及杂质，再晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘，温。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]益肾添精，补肺，祛痰，止血。用于身体虚弱，月经不调，阳萎，遗精，失血过多，肺痨咳嗽，咯血，盗汗。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (247, 2, 16, '蛤蚧', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蛤蚧<br>\r\n      <br>\r\n      [蒙药名]：哈敦古日布勒<br>\r\n      本品为壁虎科动物蛤蚧gekko gecko l的干燥全体。全年四季均可捕捉，除去内脏，洗净，伸展固定于竹阪上，用微火烘干。去掉鳞片及头、足，切段浸泡于麝香水中，或浸泡于黄酒中，取出，烘干。<br>\r\n      [性味]：咸、温。<br>\r\n      [功能与主治]：补精壮阳，增强体力，祛肾寒。用于遗精，阳痿，早泄，腰跨酸痛。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (248, 2, 16, '沙蜥', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>沙蜥<br>\r\n      <br>\r\n      [蒙药名]：浩宁古如布勒<br>\r\n      本品为草原沙蜥科动物沙蜥(phrynoce-phalus frontalis strauch)的干燥全体。夏、秋二季捕杀，清除内脏及爪等，通风处阴干，包于新鲜牛肉内埋在火灰中煨干；或浸泡于麝香水中，以去其毒后，切去头部涂搽黄油，在火炭上烘干。<br>\r\n      [性味]：甘、咸，温。<br>\r\n      [功能与主治]：有益精壮阳，解毒，祛肾寒赫依，杀粘之功效。用于少精，遗精，阳萎，中毒症，白喉、炭疽等。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (249, 2, 16, '黑蕊虎耳草', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黑蕊虎耳草<br>\r\n      <br>\r\n      [蒙药名]：格热勒图乌布苏<br>\r\n      本品为虎耳草科植物黑蕊虎耳草saxifraga melanocentra franch．)的干燥地上部分。7-8月花开时采收，除去枯叶枝及泥土，洗净，阴干。<br>\r\n      [性味]：甘、苦，凉。 <br>\r\n      [功能与主治]：有滋养，明目，清热之功效。用于视力减退，昏蠓症，结膜病，身体虚弱，精气耗损，阳萎，血、希拉性热症。<br>\r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (250, 2, 16, '白及', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白及<br>\r\n      <br>\r\n      [蒙药名]：浩勒森查黑尔码<br>\r\n      本品为兰科植物白及bletilla striata ichb．f．的干燥块茎。夏、秋二季采挖，除去须根，洗净置沸水中煮或蒸至半干，撞去外皮，晒干。中煮至透心，取出，干燥。<br>\r\n      本品性能、功效及应用与黄精同。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (251, 2, 16, '海龙', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>海龙<br>\r\n      <br>\r\n      [蒙药名]洛一扎咙斯<br>\r\n      本品为海龙科动物刁海龙(solenognathus hardwiekii&lt;gray))、拟海龙<br>\r\n      (syngnathoides biaeuleatus &lt;bloch&gt;)、或尖海龙(syngnathus aeug l．)的干燥全体。大都在夏、秋二季捕捞，除去皮膜及内脏，洗净，晒干。<br>\r\n      本品味甘，性温。其功效及应用与蛤蚧相同。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (252, 2, 16, '海马', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>海马<br>\r\n      <br>\r\n      [蒙古名] 莫林扎嘎斯<br>\r\n      本品为海龙科动物线纹海马(即克氏海马)(hippoeampus kelloggi jordan et snyder)、刺海马(hippcampus \r\n      histrix kaup)、大海马(hippocampus kuda bleeker)、三斑海马(hippoeampus trimaculatus \r\n      leach．)或小海马(hippocampus japonicus ksup)等的干燥全体。夏、秋二季捕捞，除去皮膜及内脏，洗净，晒干。<br>\r\n      本品味甘，性温，其功效及应用与蛤蚧同。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (253, 2, 16, '玉竹', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>玉竹<br>\r\n      <br>\r\n      [蒙药名]毛浩日查干<br>\r\n      本品为百合科植物玉竹polygonatum odratum (mill)druce．的干燥根茎。秋季采挖，除去须根，洗净，晒至半干后置牛奶中，煎至奶汁全为药物所吸收，取出晒干。<br>\r\n      [性味]甘，温、轻、柔。<br>\r\n      [功能与主治]滋补强壮，燥协日沃素，祛肾寒，镇赫依，调胃火。用于体弱肾虚，阳萎，遗精，腰腿痛，寒性协日沃素病，肾赫依，胃火衰败。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (254, 2, 17, '珍珠', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>珍珠<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]扫布德<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为珍珠贝科动物马氏珍珠贝pteria martensii (dunker)、蚌科动物三角帆蚌hyriopsis \r\n      cumingii(lea)或褶纹冠蚌cristaria plicata(1each)等双壳类动物壳中产生的颗粒状珍珠。自动物体内取出，洗净，干燥。置牛奶中煮4―6小时，取出，晾干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘、咸，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]安神定惊，益脑解毒，抗白脉病。用于脑患，口眼歪斜，神志不清、半身不遂，四肢麻木，白脉病，毒症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (255, 2, 17, '朱砂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>朱砂<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]绰伦雄胡<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为硫化物类矿物辰砂族辰砂cinnabar。主含硫化汞(hgs)。采挖后，选取纯净者，用磁铁吸净含铁的杂质，再用水淘去杂石和泥沙。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]甘，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清心镇惊，清热解毒，疗伤，抗白脉病。用于白脉病，偏瘫，癫痫，脑患，小儿惊风，骨折，疮疡。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (256, 2, 17, '石决明', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石决明<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]黑苏嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为鲍科动物杂色鲍haliotis diversicolor reere．皱纹盘鲍haliotis \r\n      discus hannai lno．耳鲍haliotis asinina linnaeus的贝壳。夏、秋二季捕捉，去肉，洗净，干燥。煅至酥脆，碾碎。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 咸，凉、糙。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]祛脑患，疗伤，解毒，退云翳，燥协日沃素。用于白脉病，中风，脑伤，协日沃素病，眼翳白斑，骨折创伤。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (257, 2, 17, '磁石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>磁石<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：索仁金<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为氧化物类矿物尖晶石族磁铁矿，主含四氧化三铁(fe3o4)。采挖后，除去杂石。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、咸，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有镇惊安神，接骨疗伤，祛脑疾，敛疮疡之功效。用瘟疫、毒热渗于白脉，气血不和而头晕，半身麻木，口眼歪斜，言语不利，昏厥，抽搐，血、希拉性中耳炎，发热，耳内流脓，头痛，利刃伤，骨折，脑伤，头伤等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (258, 2, 17, '玛瑙', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>玛瑙<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：玛奴湖<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为三方晶系，一种石英类矿物。全年皆可采挖，除去杂质即可。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，寒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有祛脉疾，镇刺痛之功效。用于白脉病，麻风、中风、刺痛等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (259, 2, 18, '蟾酥', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蟾酥<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：巴哈因熟斯<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为蟾蜍科动物中华大蟾蜍bufo bufo gargarizans gantor或黑眶蟾蜍bufo \r\n      melanostictus schneider等的耳后腺及皮肤腺分泌的白色浆液，经加工干燥而成。夏、秋季捕捉蟾蜍后，将体表洗净，然后刺激其耳后腺及皮肤腺，取分泌浆液，筛去杂质，置干净瓷器内，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、辛、温，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：消肿，止痛，解毒。用于咽喉肿痛，白喉，乳痈，瘰疬，苏日亚，疮疡。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (260, 2, 18, '禹粮土', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>禹粮土<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]森都拉<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为一种含铁粘土。采挖后，除去杂石。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味)甘、凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治) 清热，止血，疗伤，消肿，祛脑患，用于烧伤，烫伤，脉热，白脉病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (261, 2, 18, '海螺', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>海螺<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：拉白<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为骨螺科动物红螺(rapana thomasiana crosse)或玉螺的壳。采得后除去残肉和泥土，煅后碾碎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、咸，凉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有燥脓及黄水，化痰破积，清骨热之功效。用于疮疡，痈疽，瘰疬，脉伤，骨折，伤筋，利刃伤，翳障。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (262, 2, 18, '雄黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>雄黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：阿拉坦呼呼日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为单斜晶系硫化物类矿物雄黄(realgar)的矿石。雄黄在矿中质软如泥，见空气即变坚硬，一般用竹刀剔取其熟透部分，除去杂质泥土。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：苦、辛，温、重，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有止防腐烂，疗创伤，燥黄水，消淋巴腺肿，解毒，杀粘之功效。用于骨质失荣变色而流污浊脓汁，痈疽，苏日亚等疮疡病，炭疽，口唇、口盖、咽喉肿痛，喉瘠，白喉，吞咽困难，扁桃体炎、咽喉肿痛，疥癣，皮癣等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (263, 2, 18, '雌黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>雌黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：额木・阿拉坦一呼呼日<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为单斜晶系硫化物类矿物雌黄(orpiment)的矿石。采挖后，除去杂石、泥土。在鲜牛肉中包裹煨之，或在食盐水中浸泡后，晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，平，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有防腐烂，疗伤，消淋巴肿，燥黄水，杀粘虫之功效。用于咽喉肿痛、炭疽等，疥癣，皮癣，疮疡，创伤化脓，粘毒性淋巴腺肿痛，外伤疼痛，创伤化脓，疮疡等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (264, 2, 18, '琥珀', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>琥珀<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：浩珀<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为古代松科植物的树脂埋藏地下经久凝结而成的碳氢化合物。从地层或煤层中挖出后，除去砂石、泥土等杂质。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘，平。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有明目，利水，散瘀，止血，镇惊，安神之功效。用于目赤，翳障，陈久性疮疡，化脓性溃疡，腰腿疼痛，慢性肾病。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (265, 2, 18, '自然铜', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>自然铜<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：帕旺隆布<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为等轴晶系黄铁矿(pyrite)，方块状的硫化铁矿石。全年皆可生产，通常7～8月间产量较多，因此时大雨冲去泥土，容易寻找。拾取矿石，除去杂石即得。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有接骨，续脉，明目之功效。用于伤骨，视力减退，昏朦症，目翳，癫狂症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (266, 2, 18, '姜黄', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>姜黄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：希拉嘎<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为姜科植物姜黄(curcuma longa l．)的干燥根茎。冬至前后采挖其根茎，洗净，煮熟至透心为度，晒干，撞去外皮，再晒干。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、微苦，温、稀、钝、清。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有杀粘，防腐烂，解毒，疗伤之功效。用于白喉，炭疽，痈疽等粘毒性病症，口干舌燥，尿黄而混浊，膀胱及尿道灼痛，一切肿疡，遗精，淋病，痔疮，中毒症。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (267, 2, 18, '黄丹', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄丹<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]浩恩都、利日黑、浑达<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为铅加工制成的四氧化三铅。制法是将纯铅放在铁锅中加热，频频搅动，利用空气使之氧化，然后放在石臼中研成粉末。用水漂洗，将粗细粉末分开，漂出之细粉，再经氧化24小时，研成细粉过筛即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味] 本品味辛，性凉、重，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 有清热、解毒，生肌，防腐烂之功效。用于经久不愈之创疡，刀伤，目赤肿痛，羞明，流泪，血、希拉性眼疾等。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (268, 2, 18, '银朱', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>银朱<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：兴胡<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为人工制成的赤色硫化汞。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：辛、甘，凉、轻，有毒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有清热，收敛，破痈，疗伤之功效。用于痈疽，苏日亚等疮疡，梅毒，胸满、咳嗽，气喘等肺热病，肝区痛，两胁痛，消化不良，全身无力，发黄，肝热症，脉肿胀暴露，手足麻木等黑脉病，疫热，脑刺痛，肠刺痛，白喉，炭疽等粘性热病，创伤化脓，脉伤。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (269, 2, 18, '赤石脂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>赤石脂<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名]：包如莫勒黑朝鲁<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为硅酸盐类矿物多水高岭土的一种红色块状体。全年均可采挖。挖出后，选择红色滑腻如脂的块状体，拣去杂石、泥土。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性味]：甘、涩，平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]：有疗伤，接骨，燥脓及黄水，止血，除脑疾之功效。用于骨伤，脉断，伤筋，刀伤，黑白天花疹毒。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (270, 2, 18, '白石脂(高岭土)', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白石脂(高岭土)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[蒙药名] 查干莫勒黑一朝鲁<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为单斜晶系，硅酸盐类矿物高岭土。全年均可采挖。挖出后，拣去杂石、泥土等即可。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品味甘、酸，性平。其功效、应用与赤石脂同。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (271, 2, 18, '硼砂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>硼砂<br>\r\n      <br>\r\n      [蒙药名]：通萨<br>\r\n      本品为单斜晶系矿物硼砂(borax)之精制而成的结晶。一般于8一11月间采挖矿砂，将矿砂溶于沸水中，滤净后，倒入缸内，冷却后捞出，晒干。<br>\r\n      [性味]：甘、咸，凉、轻。<br>\r\n      [功能与主治]：有活血化瘀，破痞、疗伤，收敛，燥黄水之功效。用于陈久性疮疡，化脓性溃疡，胃、大肠之包如痞，妇血症，闭经，血痞等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (272, 2, 18, '三七', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>三七(人参三七)<br>\r\n      panax pseudo ginseng wall var.notoginseng(burkill)hoo&amp;tseng<br>\r\n      <br>\r\n      [蒙药名]:刚双日额布斯<br>\r\n      [性 味]:甘、苦，凉、钝、轻、柔。<br>\r\n      [功能与主治]:清热，解毒，止腐，止血，疗伤，消肿，止痛。用于骨折筋伤，创伤出血，创伤化脓，痈疽，溃烂，毒热扩散，协日沃素病，关节痛等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (273, 2, 18, '木贼', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>木贼<br>\r\n      <br>\r\n      [蒙药名] 朱勒古尔一乌布斯<br>\r\n      本品为木贼科植物木贼equisetum hyemale l．的干燥地上部分。夏、秋季采割，除去杂质，枯茎、残根，切段，晒干。<br>\r\n      [性味] 甘、微苦，性平、稀、钝、轻、柔。<br>\r\n      [功能与主治]有疗伤，明目，燥脓及黄水之功效。应用与通经草同。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (274, 2, 18, '独一味', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>独一味<br>\r\n      <br>\r\n      [蒙药名]：达布克<br>\r\n      本品为唇形科植物独一味(lamiopnlomis &lt;bentn&gt;kudo&lt;rotata&gt;)的干燥全草。秋季采收，拔起全株，抖去泥沙，晒至五成干时，截去地上部分及须根，再晒至足干即成。<br>\r\n      [性味]：甘、苦，温、燥、轻、清。 <br>\r\n      [功能与主治]：有疗伤，接骨，除黄水，消肿，止痛之功效。用于创伤疼痛，创伤化脓，黄水性关节肿痛、拘挛而伸屈不灵，骨折，利刃伤，皮肌肿痛。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (275, 2, 18, '金莲花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>金莲花<br>\r\n      <br>\r\n      [蒙药名] 阿拉坦花其其格<br>\r\n      本品为毛茛科植物金莲花trollius chinensis bunge．的干燥花。夏季花盛开时采集，除去杂质，通风处阴干。<br>\r\n      [性味]苦、寒、钝、轻、柔。<br>\r\n      [功能与主治]清热，燥脓，止血，止腐，疗伤。用于金伤，脉伤，淋巴腺疮，脉管炎，创伤恶化流脓，血希拉性眼病，咽喉肿痛。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (276, 2, 18, '杜仲', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>杜仲<br>\r\n      <br>\r\n      [蒙药名)浩日图宝日<br>\r\n      本品为杜仲科植物杜仲eucommia utmoides oliv．的干燥树皮。春季剥皮，刮去外层粗皮，晒干。<br>\r\n      [性味)甘、微辛、平、燥、钝、重、柔、和。<br>\r\n      [功能与主治]清热，接骨。用于骨伤，骨折，骨热。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (277, 2, 18, '绢毛菊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>绢毛菊<br>\r\n      <br>\r\n      [蒙药名]：斯其格利克乌布斯<br>\r\n      本品为菊科植物绢毛菊(soroseris hookeriana &lt;g．b．clarke&gt;stebb)的干燥全草。秋季花开时采集，除去杂质及泥土，洗净，通风处阴干。<br>\r\n      本品性、味、功效、应用与星状凤毛菊同。 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (278, 2, 18, '石韦', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石韦<br>\r\n      <br>\r\n      [蒙药名] 哈敦胡吉<br>\r\n      本品为水龙骨科植物庐山石韦(pyrrosia sheareri &lt;bak&gt; ching)、石韦(pyrrosia lingua &lt;thunb&gt; \r\n      farwell)或有柄石韦(pyrrosia petiolosa<br>\r\n      &lt;christ&gt;ching)的干燥叶。全年均可采收，除去根茎及根，晒干或阴干。<br>\r\n      [性味] 苦、涩，性凉、燥、钝。<br>\r\n      [功能与主治]有清热，解毒，燥脓，收敛之功效。用于骨伤，脉断，创伤化脓，跌扑肿痛，烧伤，诸中毒症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (279, 2, 18, '水母雪莲花', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>水母雪莲花<br>\r\n      <br>\r\n      [蒙药名]：孟和其其格<br>\r\n      本品为菊科植物水母雪莲花(saussurea medusa maxim．)的干燥全草。秋季采集，除去杂质，切段，晒干。<br>\r\n      [性味]：苦，凉。<br>\r\n      [功能与主治]：有清热，除黄水，消肿，止痛之功效。用于炭疽，无名肿毒，手足拘挛，风湿病，白脉病，陈旧性疮疡，关节黄水病，经闭，胎衣不下，刀伤出血，脑伤。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (280, 2, 18, '百合', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>百合<br>\r\n      <br>\r\n      [蒙药名] 萨日娜<br>\r\n      本品为百合科植物百合(lilium brownii f．e brown)、卷丹lancifolium<br>\r\n      thunb．)或细叶百合(lilium pumium dc．)的干燥肉质鳞片。秋季采挖，洗净，剥取鳞片，置沸水中略烫，阴干。<br>\r\n      [性味] 甘、微苦，性凉、轻、钝、燥、滴。 <br>\r\n      [功能与主治]有清热，止咳，解毒，止血，燥黄水，疗伤，接骨之功效。用于骨伤，胸部及关节外伤，铅中毒，咳痰带血，肺扩散型包如病等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (281, 2, 18, '阳起石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>阳起石<br>\r\n      <br>\r\n      [蒙药名] 续日布宋朝鲁<br>\r\n      本品为单斜晶系石棉类矿石透闪石或闪石石棉。全年均可采，挖出后，去净泥土及杂石即可。<br>\r\n      [性味]：咸，微温。<br>\r\n      [功能与主治]：有强壮筋脉之功效。用于皮、肉、脉、筋、骨、脑等受损或头、头颈部、胸部及关节外伤，筋腱受伤，手足拘挛，肿痛，外伤化脓等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (282, 2, 18, '白矾', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>白矾<br>\r\n      <br>\r\n      [蒙药名]：白邦<br>\r\n      本品为三方晶系，硫酸盐类矿物明矾石经加工提炼制成。采得后，打碎，用水溶解，收集溶液，蒸发浓缩，放冷后即析出结晶。<br>\r\n      [性味]：酸、涩，寒。<br>\r\n      [功能与主治]：有清热，解毒，防腐烂，止血，杀虫之功效。用于疥疮，咽喉肿痛，舌巴木病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (283, 2, 18, '赭石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>赭石<br>\r\n      <br>\r\n      [蒙药名]：乌兰吉必朝鲁<br>\r\n      本品为三方晶系矿石赤铁矿石(hematite),挖出后去净泥土杂质。<br>\r\n      [性味]：苦，寒。<br>\r\n      [功能与主治]：有疗伤，接骨，燥脓及黄水，除脑疾，祛目翳之功效。用于头伤，外伤，创伤化脓，视力模糊，昏朦症，目翳，目亦，眼睑干性糜烂，筋、脉伤，白脉损伤所致的肢体拘挛。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (284, 2, 18, '密陀僧', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>密陀僧<br>\r\n      <br>\r\n      [蒙药名]：哈热舒得尔朝鲁<br>\r\n      天然的密陀僧矿极少见，市售品多用铅为原料加工制成。全年均可加工，以前取自提炼银、铅的方铅矿时沉积于炉底的副产品，目前则系将铅熔融，用长形铁棍在熔铅中旋转几次，部分熔铅粘附在铁棍上，然后取出浸入冷水中，熔铅冷却后变成氧化铅固体(即密陀僧)，再放入熔铅中转几次，然后再取出浸入冷水中，如此反复多次，至密陀僧积聚至约重十几斤时，将其打下即得。<br>\r\n      [性味]：咸、辛，温，有毒。<br>\r\n      [功能与主治]：有疗伤，养骨之功效。用于头伤。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (285, 2, 18, '钟乳石', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>钟乳石<br>\r\n      <br>\r\n      [蒙药名]：胡浑朝鲁<br>\r\n      本品为一种钟乳状的天然碳酸钙。钟乳石(stalactitum),此泛指成钟乳状之岩石，呈圆柱形或圆锥形，一般产于洞穴内。全年皆可采收。<br>\r\n      [性味]：甘，温。<br>\r\n      [功能与主治]：有疗伤，强筋之功效。用于四肢损伤，关节黄水病，手足拘挛，痛风，风湿病，巴木病。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (286, 2, 18, '石燕', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>石燕<br>\r\n      <br>\r\n      [蒙药名]：宝力珠木尔陶鲁盖<br>\r\n      本品为古生代腕足类石燕子科动物中华弓石燕(cgrtiospifer sinensis &lt;graban．&gt;)及其近缘动物的化石。采得后洗净泥土，晒干。用时捣碎或水飞，也有煅后敲碎或水飞用。<br>\r\n      [性味]：甘，温。<br>\r\n      [功能与主治]：有除黄水，疗伤，接骨、生肌之功效。用于头骨受伤，颈项外伤。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (287, 2, 18, '海螵蛸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>海螵蛸<br>\r\n      <br>\r\n      [蒙药名]：伯赫图扎嘎松雅苏<br>\r\n      本品为乌贼科动物曼氏无针乌贼(sepiella maindroni de rochebrune)或金乌贼(sepia esculental hoyle)的干燥内壳。收集从乌贼鱼中剥下之内壳；或于4―8月间，捞取漂浮在海边的乌贼内壳，漂净，晒干。<br>\r\n      [性味]：咸，微温。<br>\r\n      [功能与主治]：有敛疮，止血之功效。用于疮疡，肿痛，黄水疮，创伤出血。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (288, 2, 18, '猪殃殃', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>猪殃殃<br>\r\n      <br>\r\n      [蒙药名]：查干桑泽<br>\r\n      本品为茜草科植物猪殃殃 galium aparina l．var tenerum &lt;gren．et godr．&gt; rcichb)的干燥全草。夏季结果时采收，除去杂质，洗净泥土，通风处阴干。<br>\r\n      [性味]：辛、苦，平、燥。<br>\r\n      [功能与主治]：有平希拉，疗伤，止血，接骨，利尿之功效。用于目及全身发黄，吐黄水，不思饮食，肠刺痛，口苦，食欲不振，便血，腹痛等肠腑疫病，腰胯部疼痛，尿血，尿道灼痛，肾风湿病，骨伤，刀伤，诸出血症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (289, 2, 18, '象牙', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>象牙<br>\r\n      <br>\r\n      [蒙药名]：札阿乃索要<br>\r\n      本品为象科动物亚洲象(elephas maximus linnaeus)或非洲象(elephas<br>\r\n      africanus blumenbach)的牙齿。本品多系象牙雕刻工艺剩下的副产品。<br>\r\n      [性味]：甘，寒。<br>\r\n      [功能与主治]：有解毒，愈伤，生肌之功效。用于骨伤，刀伤，脏腑内伤。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (290, 2, 18, '黄芪', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>黄芪<br>\r\n      <br>\r\n      [蒙药名]：浩恩其尔<br>\r\n      本品为豆科植物膜荚黄芪(astragalus membranaceus &lt;fisch．&gt;bunge)或蒙古黄芪(astragalus \r\n      mongolicus bunge)的干燥根。春、秋采挖，除去泥土、须根及根头，晒至六、七成干时，分选粗细捆扎成把，阴干。<br>\r\n      [性味]：甘，凉。<br>\r\n      [功能与主治]：有清热，止血，疗伤，生肌之功效。用于金疮刀伤，脏腑内伤，跌扑损伤，疮疡，脉热。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (291, 2, 18, '诃子', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>诃子<br>\r\n      <br>\r\n      [蒙药名]：阿如拉<br>\r\n      本品为使君子科植物诃子(terminalia chebula retz．)、绒毛诃(terminalia chebula retz．var． tomentella \r\n      kurt)或恒河诃子(terminalia chebula retz．var．gangetica roxb)的干燥成熟果实。秋末冬初果实成熟时采收，除去杂质，晒干。<br>\r\n      [性味]：涩，平。<br>\r\n      [功能与主治]：有祛三弊所引起的诸疾，调理体素即三根，解毒之功效。用于赫依、希拉、巴达干合并症和聚合性诸证，脏腑病，中毒症，五官科疾病，血、希拉相搏，头亚玛虫病等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (292, 2, 18, '全蝎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>全蝎<br>\r\n      <br>\r\n      [蒙药名]：西林沏图浩如海<br>\r\n      本品为钳蝎科动物东西钳蝎(buthus martensii karsch)的干燥体。春末至秋初捕捉，除去泥沙，放沸水或沸盐水中，煮至全身伸展，腹部僵硬后捞出，置通风处，阴干。<br>\r\n      [性味]：甘、辛、咸，平，有毒。<br>\r\n      [功能与主治]：有抑赫依，镇痉，祛脑疾，通白脉，明目之功效。用于昏朦症，视物模糊，突然昏仆不省人事，手足抽搐，口眼歪斜，口吐白沫等。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (293, 2, 18, '长果婆婆纳', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>长果婆婆纳<br>\r\n      <br>\r\n      [蒙药名]：巴阿巴盖茵・苏斯一乌布斯<br>\r\n      本品为玄参科植物长果婆婆纳(veronica ciliata fisch．)的干燥全草。开结果时采收，除去杂质及泥土，洗净，阴干。<br>\r\n      [性味]：苦，凉。 <br>\r\n      [功能与主治]：有清热，止血，疗伤，生肌，止痛之功效。用于外伤，骨折。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (294, 2, 19, '肉苁蓉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>&nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉是阿拉善珍贵中药材，阿拉善位于我国西北边陲，境内沙漠广布，冬季干冷、夏季酷热少雨，具有适合肉苁蓉生长的自然环境，且分布面积大、蕴藏丰富，具有良好的资源优势。阿拉善苁蓉集团1000吨技改工程和10万亩苁蓉基地被国家列入苁蓉产业化示范项目工程。做好肉苁蓉的开发利用，发展地方经济，保护生态环境十分重要，要制定切实可行的开发利用措施，保证肉苁蓉资源的可持续开发利用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉别名大芸、肉松蓉、纵蓉、地精、金笋等，具有补肾壮阳、填精益髓、润燥滑肠的功能，为滋补上品，有沙漠人参之称。肉苁蓉做为中药材在我国使用已有近2000年的历史，始载于《神农本草经》、古典《本草经疏》、《本草汇言》、《本草逢原》、《本草求真》、《玉楸药解》、《本草正义》、《月华子本草》、《制论》等多有记载。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;阿拉善位于我国西北边陲，内蒙古自治区最西端，位于东经97°10′―106°53′，北纬37°24′―42°47′，管辖阿拉善左旗、阿拉善右旗、额济纳旗三个地区，北与蒙古国交界，西南与新疆、甘肃、宁夏连接，全盟面积27万平方公里。阿拉善境内自东向西分布着乌兰布和、腾格里、巴丹吉林沙漠三大沙漠，该地区冬季干冷，夏季酷热干旱，年平均降雨在49.3mm―200mm之间，蒸发量达3325.6毫米。三大沙漠连贯在全盟境内形成大片荒原沙漠地带及广阔的戈壁沙漠。广布沙漠湖泊，额旗境内有黑河、额济纳河等有名的河流，在沙漠边缘地区分布着大量适合肉苁蓉寄生的藜科植物梭梭，其高度2-3米，由于特殊的地理气候环境，群落结构较简单，群落植物有白刺、沙鞭、锁阳、沙蓬等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉是列当科多年生寄生草本植物，通常取用肉苁蓉、迷肉苁蓉、沙苁蓉末开花的肉质茎。喜生于荒漠地带湖边、沙地、沙丘上，常寄生在梭梭根部，主要分布在阿拉善境内的巴音树贵、塔木苏、笋布尔、吉兰泰、罕乌拉、敖伦布鲁格、乌力吉、银根、吉日乃、捌子湖、通古淖尔边境地区、赛汉陶来等地区，肉苁蓉除寄生在梭梭属植物根部外，还寄生在盐爪爪属、珍珠柴芨芨草、白刺、红沙等植物根部。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉为多年生寄生草本植物，高50-150厘米，茎肉质肥厚，扁圆柱形，黄色，不分枝或有时从基部分2-3枝。被多数肉质鳞片状叶，黄色至褐黄色，覆瓦状排列，卵形至长圆状披针形，长1-2.5厘米，宽4-8毫米，在茎下部者较短且排列转紊密，上部者较长，排列较疏松，穗状花序圆柱形，长8-25厘米，宽6-8厘米，花多数而密集；每花的基部有1张大苞片和2枝对称的小苞片，大苞片卵形或长圆状披针，与花萼尔等长，花萼钟形，淡黄色或白色，长1-1.3厘米，5浅裂，裂片近圆形，无毛或多少被有绵毛；花冠管状钟形，5浅裂，裂片近圆形，紫色，管部白色；雄蕊4，花药倒卵圆形，先端有短尖的药隔，花药与花丝基部被皱面的长柔毛，子房上位，长椭圆形，花柱细尖，蒴果椭圆形，2裂。种子多数，花期5-6月，果期6-7月。沙苁蓉、迷肉苁蓉略有差异。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉药材通常呈扁圆柱形，体重质硬，表面棕褐色或灰褐色，气微，味甜微苦，阿位善盟的肉蓉，尤以树贵产的肉质肥厚、油性足、鳞片清晰为肉苁蓉优质品。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肉苁蓉药材是用其不带花序的肉质茎，采集在 药序冒出地面前，有经验的牧民常根据其寄生植物周围出现的裂缝来寻找，寄主苗龄越大，肉苁蓉生长点与寄生间距较远，寄主苗龄小，则肉苁蓉离寄主较近。也可采收刚出土的尚末开花的肉苁蓉，都为上等品，如开花后，其肉质茎已中空，质差，已无法药用，阿拉善肉苁蓉的采收可分为春秋两季，3-5月间、9-10月间，春季采者，通常半埋于沙中晒干，除去杂质、沙土，称为“甜大芸”、“淡大芸”或“淡苁蓉”；秋采者，水份多，不易晒干，有投入盐湖中1-3个月后，取出晒干，称为“盐大芸”、“咸苁蓉”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;由于境内有适合肉苁蓉生长的自然环境，阿拉善肉苁蓉资源很丰富，但由于历年来不注意保护，人为乱砍梭梭林，过量超载放牧，肉苁蓉价格上涨，乱采乱挖现象日趋严重，又因阿拉善地区连年干旱，地下水位下降，致使梭梭林大片枯死，使肉苁蓉资源严重破坏，产区大大缩小，梭梭林从解放初期的1700万亩缩小到630多万亩，年总产量亦逐年下降。近年来，由于人们生活水平日益改善，生态环境保护意识的逐步提高，及对医药保健事业的发展，经过阿拉善政府大力合理的治理，通过围栏封育、禁牧，合理组织采挖，人工栽培试验的成功及推广等切实有效的措施，已使我盟肉苁蓉生态资源得到有效的保护的发展，肉苁蓉年总产量已有所增长，据调查，最多时达干苁蓉200多吨，年需求量达250吨，随着苁蓉保健的研制和市场的进一步开发，供求差异明显加大。目前我公司的肉苁蓉1000吨技改工程 \r\n      10万亩肉苁蓉基地2000年度被国家列为肉苁蓉产业化示范项目，给予专项资金支持。面对前景广阔的大好市场，做好肉苁蓉的开发利用工作十分重要，满足市场需求，发展地方经济，保护好自然生态、资源，要齐头并举，协调一致，阿拉善苁蓉集团在致力于肉苁蓉保健产品及沙生药物开发研究的同时，制定出切实可行的肉苁蓉开发利用措施，积极推进肉苁蓉产业化进程，以保证肉苁蓉这一良好资源的可持续发展利用，使这珍奇的沙漠人参更好地造福人类。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (295, 2, 19, '枸杞', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;枸杞为茄科植物，茎杆高三、五尺。春天发芽、生叶，枸杞叶嫩绿，可以直接食用。到六、七月开始开小红花，随后就结出鲜红色的果实，形状如枣核。枸杞的结果期较长，从七月直到十月底霜冻来之前，每隔15天就要摘一次果，枸杞主要生长在中国北方的内蒙、宁夏、河北等省。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞是我国传统名贵中药材，素有“红宝”美称，其富含有植物多糖、蛋白质、维生素、生物碱、类胡萝卜素、脂肪酸、各种微量元素以及人体必需的多种氨基酸。其中：<br>\r\n      1、植物多糖成份为枸杞多糖、还原糖、糖醛酸；<br>\r\n      2、维生素主要有：维生素a、维生素e、硫胺素、核黄素、尼克酸、维生素b6、抗坏血酸等；<br>\r\n      3、人体不能合成的必需的氨基酸有：苏氨酸、缬氨酸、蛋氨酸、异亮氨酸、亮氨酸、苯丙氨酸、赖氨酸、色氨酸；人体可以合成的氨基酸有：天门冬氨酸、丝氨酸、谷氨酸、甘氨酸、丙氨酸、胱氨酸、酪氨酸、精氨酸、脯氨酸；儿童必需的组氨酸等；<br>\r\n      4、生物碱类有甜菜碱等；<br>\r\n      5、类胡萝卜素主要以β―胡萝卜素为主；<br>\r\n      6、脂肪酸中含量较高的是亚油酸；<br>\r\n      7、微量元素主要含有：钾、钠、钙、磷、镁、铁、锌、硒等20多种；<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;近年来发现，枸杞子中的枸杞多糖是其重要的有效成份，枸杞中多糖的含量随产地不同而异，其中内蒙、宁夏枸杞中的多糖含量最高，达到8.2%，枸杞子多糖（lbp）是由六种单糖ara、clc、gal、man、xyl和rha组成，能提高人的免疫功能，促进机体产生nk-杀伤细胞，并提高其活性，通过提高机体免疫功能可以间接起到抗病毒作用，对恶性肿瘤、艾滋病的预防与治疗起到积极作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;另外，枸杞子含有丰富的类胡萝卜素，类胡萝卜素是自然界中具有重要生理功能的一类化学物质，其中β―胡萝卜素不仅具有维生素a的活性（其作用可以明目，含量高于羊肝），而且近年来各国研究人员发现它在抗癌、抗衰老和预防心血管疾病方面也有明显的作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;甜菜碱是枸杞子中主要有效成分之一，近代药理研究表明，枸杞子有降血脂、保肝、抗脂肪肝、抗癌及有生长刺激等作用，这与甜菜碱的药理作用相一致。亚油酸的主要功效在于降低血浆胆固醇，减少血管壁中胆固醇含量，防止高血脂及动脉粥样硬化。 \r\n      枸杞子中维生素―e对人体具有抗衰老、防止心血管疾病等功效，是冠心病的辅助治疗用药；磷脂可参与脑代谢，促进儿童大脑发育。微量元素有维持人体内水、渗透压、酸碱平衡的作用，并且是与酶、激素、维生素、核酸等一起保持生命代谢过程所必需的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;综上，枸杞具有植物性滋补药用和营养食品的双重功效，是一种世所公认的健康长寿补品。参考文献中也均有记载： \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1、枸杞子具有增强免疫和免疫调节功能<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞子、枸杞多糖均可显著提高人体吞噬细胞吞噬百分率和吞噬指数。枸杞子还能显著提高血清溶菌酶活力，给73位老年人口服枸杞子每天15克，连服45~65天后，血清溶菌酶含量由服用前的8.73mg/ml，上升到15.03mg/ml，达到中年水平。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞可以显著提高中老年人细胞免疫功能，临床上选择中老年人“虚证”者194例，让其长期服用枸杞子（每天15g，服食2个月或半年），结果不仅临床病症改善，而且淋巴细胞转化和e-花环两项细胞免疫指标均增强。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2、枸杞可以促进多能造血干细胞的增殖，增加粒--单系集落形成单位数量，加速粒--单系组细雨胞的分化，增加粒系细胞比例，促进外周血网组织红细胞比例。恶性肿瘤患者在放疗过程中口服枸杞干果50g/天，连续10天能使其白细胞数量显著增加，在食用枸杞子过程中患者可以持续进行放疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3、枸杞子具有降血脂、抗脂肪肝作用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞可降低大鼠血胆固醇，并明显抑制灌饲胆固醇和动物油的家免的血清胆固醇增高，长期给大鼠饲喂含枸杞水提取物的饲料，对ccl4引起的肝损害有保护作用，能抑制ccl4引起的血清及肝中的脂质变化，降低谷草转氨酶。<img src="pic/gouqi2.jpg" width="166" height="120" align="right" border="1"><br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4、枸杞子具有抗肿瘤作用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞子提取液对致癌剂诱导的突变有抑制作用，抑制率分别为91.8%和82.6%。通过细胞体外培养的方法观察枸杞对癌细胞的生物效应，证明枸杞子对人胃腺癌、宫颈癌细胞均有明显抑制作用，枸杞子结合放疗有明显的放射增效作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5、枸杞子具有抗衰老作用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;现代衰老理论认为，衰老过程中引起机体免疫功能退化的主要原因是胸腺随着年龄的增长而萎缩，胸腺是衰老的主要环节。枸杞子中的枸杞多糖，在人体内主要选择作用与胸腺t细胞，枸杞多糖可以促进成熟细胞数目增多，而且增强排空功能，使胸腺细胞向外周转移增多，可以逆转衰老的胸腺退行性变。枸杞多糖还可以增强骨髓造血功能，所以枸杞子具有抗老作用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;6、枸杞子的其它作用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;枸杞子中含有枸橼酸、甜菜碱，对治疗各种性肝病，有较好疗效。另外枸杞子对慢性萎缩性胃炎、白细胞减少症、肥胖症、精子减少不育症等也有一定的辅助疗效。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (296, 2, 19, '沙棘', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> &nbsp;&nbsp;&nbsp;&nbsp;1、沙棘概况及分布<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;沙棘（hippophae rhamnoides L.）为胡颓子科沙棘属植物，是落叶灌木或乔木，雌雄异株。沙棘发源于喜马拉雅山及其毗邻地区，广泛分布于欧亚大陆的温带地区，在中国分布于东经75°--121°，北纬27°--48°之间，主要集中生长在青藏高原和内蒙古高原，在海拔600--5000米的范围内均有生长。 \r\n      <br>\r\n      　　沙棘属植物全属现有四个种，在中国都有生长，即：中国沙棘（h. rhamnoides l.）、西藏沙棘（h. thibetana schlechtend）、肋果沙棘（h. \r\n      neurocarpa s. w. liu et y. n. he）、柳叶沙棘（h. salicifolia d. don）。<br>\r\n      　　全世界的沙棘林面积目前约为150万公顷，其中，中国超过130万公顷，且大部分为野生沙棘。中国不仅是世界上沙棘种质资源最丰富的国家，也是沙棘资源最大的国家。<br>\r\n      　　沙棘藏语称“达日布”，蒙语称“沏其日甘”，一般呈灌木状或乔木状，其水平根系发达，延展半径可达10米以上，总根量的80%以上主要分布于70厘米土层以内；树型有主干型、灌木型、双干型；果实有圆形、扁圆形、长圆形；果实颜色有黄色、橘黄色、黄绿色、橘红色、红色五种，味酸甜可口。 \r\n      <br>\r\n      　　沙棘不仅能生长在中国内蒙古、青藏高原等光、热充足区和多瑙河三角洲温湿地区，而且也能生长在瘠薄、盐碱、潮湿、极度低温、高温的恶劣环境中。在中国的黄土区、山石区、风沙区和荒漠区，甚至在西伯利亚、珠穆朗玛峰北坡等高寒区均有天然分布。 \r\n      <br>\r\n      　　“适者生存”在这种悠久而古老的植物上体现的淋漓尽致。无论在优越环境还是在恶劣环境中，沙棘都能通过其自身的光合、化合作用充分从大自然中汲取营养。沙棘在阳光、空气、土壤和大地中采其能量和精华，造就了它特殊的适应性和抗逆性。这种神奇的植物不仅富含190多种生物活性物质和营养成分，而且细胞活力和生理功效特别强，根瘤固氮能力远远高于大豆，种子得以在恶劣环境下繁衍生息，由一株可延展为一大片密集群丛。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2、沙棘的营养成分<br>\r\n      　　沙棘是一种富含生物活性物质的天然植物，目前已发现的生化成分达190多种。沙棘所含维生素种类之多、含量之丰富是任何其它果蔬所无法比拟的，因此专家赋予它"维生素宝库"的美称。沙棘果中vc含量为800--850mg/100g，高者可达1500--1700mg/100g，是中华猕猴桃的2-8倍，苹果的20--35倍，且极为稳定。沙棘果中ve含量为2.9--18.4mg/100g，最具活性的是α-ve，还含有γ-ve和δ-ve，后两者具有很强的抗氧化作用。此外，沙棘还含有vk1、va、vb1、vb2，其中va原有β-胡萝卜素、γ-胡萝卜素等，在所有浆果中，沙棘中的含量最高。 \r\n      <br>\r\n      　　沙棘中含有多种微量元素，从十几种到三十几种不等，沙棘中的微量元素有三个特点，一是目前已知人体必需的微量元素为14种，沙棘中竟含11种，其次是高钾低钠，三是有害微量元素种类少，未测出有砷和镉。 \r\n      <br>\r\n      　　沙棘中有机酸含量约2-3.5%，其中有苹果酸、草酸、这些酸具有缓解巴比妥类、抗生素类和其它药物的毒性，预防畸胎、x射线、超大气压输氧和应激反应的损伤等生理活性作用。沙棘中含有种类齐全的氨基酸，人体8种必需的氨基酸沙棘中全有。42种脂类化合物中的磷脂类作为构成膜线粒体的成分，参与电子转移和氧化磷酸化作用，促进细胞代谢，有利于抗脂肪肝、抗肝硬化作用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3、沙棘种类<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我国天然生长的沙棘主要有以下几种: <br>\r\n      　　中国沙棘（h. rhamnoides l. subsp. sinensis rousi） 世界沙棘名种之一,是沙棘属植物的一个原始类群。它的分布面积最大，数量最多，占我国沙棘资源面积的80%以上，主要分布在黄河中游地区。目前我国在水土流失地区大面积种植的即中国沙棘。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;中亚沙棘（h. rhamnoides l. subsp. tunkestanica rousi） \r\n      主要分布在新疆的天山以南。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;西藏沙棘 主要分布在青藏高原。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肋果沙棘 主要分布在青藏高原。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古沙棘：主要分布在新疆的天山以北，蒙古沙棘果实在成熟期间可以将枝条压弯，俄罗斯主要以这种沙棘为育种材料培育出了大果沙棘良种。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;柳叶沙棘 主要分布在西藏东南部，开发研究较少。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;江孜沙棘：主要分布在四川西部，青藏高原东部。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;云南沙棘：主要分布在云贵高原等地区，开发研究较少。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;大果沙棘：大果沙棘最早由俄罗斯培育，目前已有50多个品种，1992年由中国引入，大果沙棘抗寒、抗病、少刺或无刺、果大、高产等特点。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4、沙棘与环境<br>\r\n      　　沙棘，又名醋柳，酸刺、黑刺，系落叶灌木或小乔木。沙棘集生态效益，经济效益于一身，是治理水土流失，改变生态环境，促进贫困地区经济发展，脱贫致富的有效武器。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(一) 保持水土：<br>\r\n      　　沙棘在控制土壤腐蚀、水土流失、改善环境、缓解贫困、提高收入水平、治理荒山等方面成效显著。经研究比较，沙棘覆盖地区水土流失减少75%，中国种植沙棘每年超过60,000公顷。<br>\r\n      　　开垦土地，提高土壤肥力 沙棘是一种固氮植物，一亩（十五分之一公顷）生长十年的沙棘林可固氮12公斤，相当于25公斤的肥料；沙棘对于矿区、荒山、盐碱地的开垦尤其有效。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(二) 沙棘的储水能力：<br>\r\n      　　储水能力反映了植物在干旱环境中的储水能力，根据这种能力的大小，我们可以推测出植物的抗旱能力。12、24、36小时后，沙棘叶失水的百分比分别为13.6%-25.8%，24.3%-43.9%和37.9%-63.6%。这些表明沙棘的抗旱能力强于锦鸡属小型叶植物，在半干旱的黄土山区，沙棘具有更稳定的蒸发强度、较低的含水量。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5、沙棘开发与利用<br>\r\n      　　早在2000多年前，沙棘的药用疗效即引起了中、蒙、藏医的重视，藏医经典著作《四部医典》（作者玉妥・元丹贡布于公元773年--783年完成）记载沙棘具有祛痰、利肺、化湿、壮阴、升阳的作用，其中60余处还记述了沙棘的健脾养胃与破瘀止血的功用。唐代藏医古典医著《月王药珍》中记述“沙棘医治‘培根’，增强体阳，开胃舒胸，饮食爽口、容易消化”。藏医典《晶珠本草》中称：“沙棘利肺止咳，活血化瘀，利心脏血脉，消痰浊”，还称“沙棘种子含微量油质，补益人体血液。” \r\n      <br>\r\n      　　二十世纪五十年代，沙棘油被苏联首次开发，并用于航天医学。<br>\r\n      　　1977年，沙棘作为蒙药被列入《中华人民共和国药典》，《药典》中记述：“沙棘果表面橙黄色，种子斜卵形，褐色有光泽，种仁乳白色，有油性，气微，味酸、涩， \r\n      具有止咳祛痰、消食化滞、活血散瘀功能。”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1985年，在全国水土保持领导小组下设立全国沙棘协调办公室，随后，各有关省区也相继设立了沙棘开发的相应机构，并联合计委、经委、科委、财政、银行、轻工、卫生、农业、水利、林业以及商业等部门和多学科的专家，系统地开展沙棘综合利用。<br>\r\n      　　1995年，来自蒙古、日本、加拿大、印度、尼泊尔、芬兰、瑞典、南非等国家和联合国开发计划署、联合国工业发展组织、联合国粮农组织及国际山地发展中心、世界银行的代表一致表决同意，在中国成立“国际沙棘研究与培训中心（icrts）”，该组织每两年召开一次国际学术会议，促进了沙棘开发的国际化及沙棘产品国际市场的发展。 \r\n      <br>\r\n      　　“从经济和生态角度看，沙棘立足于边远和生态脆弱土地是非常有益的。因为沙棘能为人类提供许多独特的产品，沙棘油对人类有很好的药用价值。” <br>\r\n      ――国际山地综合发展中心（icimod）代表在1999年国际沙棘研讨会上的讲话“我们发现，许多年来，中国始终坚持在水土流失严重的沙漠边缘地区种植沙棘作为治理荒漠化的一项重要措施。实践证明，沙棘对保护这些地区的表土和提高这些脆弱生态系统的生产力具有明显的作用。种植沙棘必将有利于农业、林业及畜牧业的持续发展。” \r\n      <br>\r\n      ――联合国粮农组织驻中国、朝鲜和蒙古代表 ajmal m. qureshi（库瑞希）先生在国际沙棘研讨会上的讲话“研究表明，沙棘全身都是宝，它不仅能有效保持水土、改善生态环境，而且宜于畜牧；沙棘果营养丰富，具有很高的开发价值，沙棘集保持水土和经济开发于一身，在医药、保健和食用等方面具有广阔的发展前景。” \r\n      <br>\r\n      ――中华人民共和国水利部副部长朱登铨先生在1999国际沙棘研讨会上的讲话<br>\r\n      　　“中国国际经济技术交流中心还将在今后的区域合作，如在东北亚区域合作、喜马拉雅山地区合作中优先考虑沙棘项目，以支持成员国之间开展活动。”<br>\r\n      ――外经贸部中国国际经济技术交流中心主任梁丹在1999国际沙棘研讨会上的讲话 \r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (297, 2, 20, '阿嘎如一35', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿嘎如一35<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[异名]&nbsp;&nbsp;三十五味沉香散<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[来源]&nbsp;《诃黎勒串珠》 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[处方]&nbsp;&nbsp;沉香&nbsp;&nbsp;&nbsp;3g &nbsp;&nbsp;白木香 \r\n        &nbsp;&nbsp;&nbsp;3g &nbsp;&nbsp;降香&nbsp;&nbsp;&nbsp;&nbsp;3g &nbsp;北沙参&nbsp;&nbsp;&nbsp;3g&nbsp;&nbsp;广酸枣&nbsp;1.2g&nbsp;&nbsp;木香&nbsp;&nbsp;&nbsp;2g&nbsp;白檀香&nbsp;&nbsp;15g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;豆蔻&nbsp;1.2g&nbsp;&nbsp;&nbsp;公丁香&nbsp;&nbsp;1.2g \r\n        &nbsp;&nbsp;草果&nbsp;&nbsp;1.2g &nbsp;肉豆蔻&nbsp;1.2g &nbsp;地格达&nbsp;1.2g \r\n        &nbsp;瞿麦&nbsp;1.2g&nbsp;紫檀香&nbsp;&nbsp;15g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;诃子&nbsp;1.2g&nbsp;&nbsp;&nbsp;川楝子&nbsp;&nbsp;1.2g&nbsp;&nbsp;&nbsp;栀子&nbsp;&nbsp;1.2g \r\n        &nbsp;胡黄连&nbsp;1.5g &nbsp;旋复花&nbsp;1.5g &nbsp;石榴&nbsp;1.5g&nbsp;白头翁&nbsp;l.5g \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;细辛&nbsp;1.5g \r\n        &nbsp;&nbsp;草乌(制)1.5g &nbsp;&nbsp;麝香&nbsp;&nbsp;1.5g&nbsp;&nbsp;黑云香&nbsp;1.5g \r\n        &nbsp;野兔心&nbsp;1.5g &nbsp;&nbsp;山柰&nbsp;12g 土木香 &nbsp;12g <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;红花&nbsp;1.2g \r\n        &nbsp;&nbsp;珍珠干&nbsp;&nbsp;&nbsp;12g &nbsp;&nbsp;石膏&nbsp;&nbsp;1.2g &nbsp;白云香&nbsp;&nbsp;12g \r\n        &nbsp;木棉花&nbsp;&nbsp;12g &nbsp;&nbsp;苦参&nbsp;12g 马钱子(制)&nbsp;12g <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 止咳，平喘，调理赫依、热粘三者相搏交争。用于山滩界热，空虚热，心赫依热，心悸，失眠，胸闷气喘，干咳痰少，赫依性刺痛，赫依热兼盛，神昏谵语。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[附注]本方药味较多，性平而无副作用，方中三种沉香(即沉香、降香、白木香)及肉豆蔻、白豆蔻、丁香、草果为镇赫依主药；地格达、胡黄连、广酸枣、瞿麦、木棉花为清热药；石膏、沙参、檀香、广木香为止咳、平喘药；麝香、黑云香、草乌、兔心为杀粘药；此方由镇赫依药物、清热药物和杀粘药物等三部分主药配成，所以调理赫依、热、粘三者相搏交争有特效。</p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (298, 2, 20, '阿嘎如一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　阿嘎如一15<br>\r\n        <br>\r\n        　　[异名]&nbsp;&nbsp;十五味沉香散<br>\r\n        　　[来源] 《诃黎勒串珠》 <br>\r\n        　　[处方] &nbsp;紫檀香&nbsp;&nbsp;2g &nbsp;&nbsp;沉香&nbsp;10g &nbsp;&nbsp;广酸枣&nbsp;4g \r\n        &nbsp;&nbsp;栀子&nbsp;5g红花&nbsp;4g石灰华&nbsp;4g &nbsp;&nbsp;&nbsp;&nbsp;北沙参 \r\n        4g &nbsp;&nbsp;白檀香&nbsp;4g &nbsp;&nbsp;<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;土木香&nbsp;10g \r\n        &nbsp;&nbsp;&nbsp;&nbsp;苦参&nbsp;10 g 珍珠干&nbsp;5g &nbsp;&nbsp;山柰&nbsp;2.5g \r\n        &nbsp;&nbsp;诃子&nbsp;5g &nbsp;&nbsp;川楝子&nbsp;5g&nbsp;&nbsp;肉豆蔻&nbsp;2g \r\n        &nbsp;&nbsp;<br>\r\n        　　[功能与主治] 平息粘、热、赫依三者相搏，止咳、平喘止刺痛。用于赫依血相搏引起的哮喘、咳嗽、咯白沫痰，胸部刺痛，山滩界赫依热，心赫依、心热，赫依血上逆等症。<br>\r\n        　　[附注] 本方性凉，为平息粘、热、赫依相搏之良方。方中前八味为阿嘎如一8、后三味为霞日汤(即三籽汤)、余下四味是查干汤(即玛奴一4汤)。本方由三种方剂组合配成，故能促使热症成熟、收敛、清除作用，并平息粘、热、赫依三者相搏以及对心肺赫依热症，均有疗效。 \r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (299, 2, 20, '噢必德顺・阿嘎如一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　噢必德顺・阿嘎如一8<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[异名] 八味沉香秘诀散<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[来源] 《诃黎勒串珠》 <br>\r\n        　　[处方] 沉香50g 肉豆蔻50g 白豆蔻50g 阿魏50g 广酸枣50g 紫硇砂50g 兔心50g 天仙子l0g<br>\r\n        　　[功能与主治]镇赫依，平喘，止痛。用于昏厥，赫依性佝偻病，健忘症，癫狂症，哮喘，胸部刺痛等。<br>\r\n        　　[附注]本方性温，为昏厥、癫狂病常用良方。方中镇赫依良药沉香、肉豆蔻为主，配合天仙子、兔心等镇静止痛药物。如病情热盛，则减阿魏、紫硇砂、白豆蔻三味，病情寒盛、则减广酸枣、天仙子、免心三味；二者均与丁香、马钱子(制)、细辛等三味药交替用， \r\n        疗效更为显著。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (300, 2, 20, '阿敏额尔敦', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿敏额尔敦<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[异名] 扫日格庆・淖日布、保命丹<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[来源] 《蒙医传统验方》<br>\r\n        　　[处方]沉香6g 白檀香3g 白云香3g 石膏3g 草乌芽3g 北沙参3g 诃子3g 胡黄连3g 黑云香3g 拳参3g 广木香3g 肉豆蔻3g \r\n        旋复花3g 马钱子(制)3g 人造牛黄3g 木棉花4.2g 野兔心4.2g 公丁香1.5g<br>\r\n        　　[功能与主治]调理赫依、热、粘三者相搏交争，止刺痛。用于命脉赫依病，癜狂症，昏厥，心悸，赫依性心刺痛，山滩界热，空虚热，未成熟热，白脉病，巴达干希拉隐伏症<br>\r\n        　　[附注]本方性平，方中以赫依热良药沉香为主药，配有肉豆蔻、兔心等益心药物，草乌芽、黑云香、马钱子等杀粘止痛药物及清热药牛黄、胡黄连、檀香、木棉花等，对赫依热性心刺痛有明显的缓解作用。因本方主治命脉赫依症，故《中国医学百科全书》(蒙医学分卷)称为保命丹。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (301, 2, 20, '阿米・巴日格其一11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿米・巴日格其一11<br>\r\n        <br>\r\n        　　[异名] 扫日格庆・朱吉各、十一味保命丸<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方]沉香15g 公丁香15g 野兔心15g 阿魏15g 肉豆蔻12g 广木香11g 诃子12g 广枣18g 白云香6g石灰华9g 木棉花9g<br>\r\n        　　[功能与主治]镇赫依，理气，止刺痛，用于乳腺和腋窝刺痛。赫依性哑结，心脏刺痛。<br>\r\n        　　[附注]本方性平，由吉如很・阿嘎如一8味上兔心、丁香、阿魏等三味药组成。方中以改善心功能药广枣作主味药，由肉豆蔻、沉香、兔心、丁香、阿魏；镇心赫依药物作次味药；配合调理体素药诃子和平息赫依血紊乱相搏药广木香，故为心、主脉症良方。</p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (302, 2, 20, '阿米・巴日格其一25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿米・巴日格其一25<br>\r\n        <br>\r\n        　　[异名] 扫日格庆・尼日阿 、　二十五味维命散<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方]阿魏15g　野兔心15g 公丁香i5g 沉香15g　肉豆蔻12g 白胡椒12g 　　干姜12g 白蒜炭12g 小茴香12g　筚拨12g \r\n        广木香12g 土木香12g 诃子12g 　　黑冰片12g　陈牛肉6g 肉桂6g 黑云香9g 白豆蔻9g 苦参9g　紫草茸9g 光明盐9g 菖蒲18g \r\n        草乌(制)18g　白云香6g 石榴30g\'<br>\r\n        　　[功能与主治]镇赫依，理气。用于司命赫依等五种赫依病，侵入皮肌脉骨之内赫依病，五脏六腑之内赫依病等诸病。<br>\r\n        　　[附注]本方性温，乃专治诸赫依病之总方，方中以阿魏、丁香、兔心为主，加配肉豆蔻、白豆蔻、沉香、肉桂、紫硇砂、小茴香等镇赫依诸药，对人体内，外各器官的赫依症均有治疗作用，再加配草乌、黑云香、陈牛肉等杀粘止痛药以及"三热药&quot;(即干姜、荜拨、白胡椒)和石榴、光明盐、广木香等祛巴达干寒药物，故对人体上中下各部的赫依症均有专治良效。如专治脏腑之赫依病，则以免心作主味药配方；专治皮肌骨脉之赫依病，则以阿魏作主味药配方；如专治司命赫依等五种赫依病，则以丁香作主味药配方。上述三种主味药量均在原方中药量上加倍配伍。</p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (303, 2, 20, '噢必德顺・乌日勒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　噢必德顺・乌日勒<br>\r\n        <br>\r\n        　　[异名] 满阿各・日力布、秘诀丸<br>\r\n        　　[来源] 《蒙医内科学》<br>\r\n        　　[处方] 天仙子12g 紫河车10g 广木香8g 朱砂6g 肉豆蔻5g 广酸枣5g 沉香5g 白豆蔻5g 公丁香5g 阿魏5g 紫硇砂5g \r\n        当归4g 兔心3g <br>\r\n        　　[功能与主治] 镇赫依，镇静。用于赫依性痫病。<br>\r\n        　　[附注]本方性温，方中以天仙子、朱砂作主味药；沉香、阿魏、肉豆蔻、丁香、白豆蔻等赫依病专药作为辅佐药；配以调理赫依血相搏药广木香及益心活血药广酸枣和当归，故称治赫依性痫病的有效良方。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (304, 2, 20, '匝迪一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>匝迪一15<br>\r\n        <br>\r\n        　　[异名] 匝迪壮阿、十五味肉豆蔻丸<br>\r\n        　　[来源] 《蒙药验方》<br>\r\n        　　[处方] 肉豆蔻12g 手掌参12g 公丁香9g 沉香9g 天门冬9g 广酸枣6g 　黄精6g 白豆蔻6g 紫硇砂6g 干姜3g 光明盐3g \r\n        芫荽子3g 荜拨3g 白胡椒3g　　马钱子(制)3g<br>\r\n        　　[功能与主治]镇赫依，理气。用于心、肾赫依瘀症，心悸，失眠，健忘症，昏厥，颤抖症，内外及隐伏之赫依病。<br>\r\n        　　[附注]本方性温，方中以心赫依良药肉豆蔻和补肾良药手掌参作主味药，以强心药广酸枣和益肾药白豆蔻为辅佐药，加配主脉良药丁香及沉香、紫硇砂等镇赫依诸药，配合祛巴达干寒的&quot;三热药&quot;(即干姜、荜拨、胡椒)更加强了药力，故对心、肾赫依瘀证及内外隐伏之赫依病均疗效显著。 \r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (305, 2, 20, '高优一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　高优一13<br>\r\n        <br>\r\n        　　[异名] 高优朱苏木、十三味槟榔散<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方] 槟榔10g 肉豆蔻l0g 广酸枣10g 当归10g　　葶苈子10g 草乌(制)10g 沉香20g 公丁香8g 　白胡椒 7g \r\n        干姜7g 荜拨7g 　广木香6g 红盐5g<br>\r\n        　　[功能与主治]调节赫依，止痛。用于主脉赫依病，赫依性刺痛，心颤，癫狂，失眠。<br>\r\n        　　[附注] 方中以主脉赫依良药丁香、益心良药肉豆蔻、理气止痛药沉香和木香及补益温肾良药槟榔为首选药，佐以紫硇砂、广酸枣、当归、草乌等镇赫依、益心、活血、止痛药物。故本方对主脉赫依病有较好的治疗作用。此外，方内加配三热药亦能起补肾益气和改善消化作用。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (306, 2, 20, '苏格木勒--3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>苏格木勒--3汤<br>\r\n        <br>\r\n        　　[异名] 三味豆蔻汤<br>\r\n        　　[来源] 《四部医典》<br>\r\n        　　[处方] 白豆蔻15g　白苣胜l0g　　筚拨5g<br>\r\n        　　[功能与主治]镇赫依、镇静、用于失眠。<br>\r\n        　　[附注]本方性温，为安眠之良药。方中白豆蔻味苦、辛，性温，为镇赫依主药；白苣胜味甘、性平作镇赫依辅佐药；加配调理体素药荜拨，三药合用相互协调，为安眠之良方。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (307, 2, 20, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿嘎如--19 </p>\r\n      <p> 　　[异名] 阿嘎日朱古、十九味沉香散。<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方] 沉香12.5g 降香12.5g 白木香12.5g 细辛12.5g 土木香6g 珍珠干6g 苦参6g 广酸枣6g 木棉花6g 诃子6g \r\n        胡黄连6g 山柰6g 丁香l0g 肉豆蔻10g 旋复花10g 木香10g 马钱子(制)10g 川楝子7g 栀子40g <br>\r\n        　　[功能与主治] 镇赫依，平喘，止痛。用于胸闷哮喘，胸部刺痛，山滩界热，主脉赫依病。<br>\r\n        　　[附注] 本方性凉。方中三种沉香(即沉香、降香、白木香为抑赫依药，丁香、肉豆蔻为赫依病专药，均为主药；理血专药三籽(即诃子、楝子、栀子)、胡黄连，调理赫依血相搏药广木香及土木香，缓解止痛药马钱子、旋复花和细辛等为辅佐药，故对一切赫依血、热性刺痛具有较好的疗效。</p>\r\n      <p><br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (308, 2, 20, '阿嘎如一17', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿嘎如一17<br>\r\n        <br>\r\n        　　[异名] 阿嘎如朱敦、十七味沉香散<br>\r\n        　　[来源] 《蒙医金匮》 <br>\r\n        　　[处方] 沉香20g 肉豆蔻10g 马钱子(制)10g 丁香10g 野兔心10g 草乌(制)15g 刀豆2.5g 旋复花3.5g 广酸枣7.5g \r\n        苦参7.5g 萝卜干7.5g 广木香5g 诃子 5g 川楝子5g 黑云香5g 栀子5g 野牛心5g<br>\r\n        　　[功能与主治] 调理赫依血紊乱，止痛。用于赫依血相搏引起头痛，心悸气喘，恶血扩散于肾，肾病上逆而导致颈僵直，主脉赫依病，上行赫依病。<br>\r\n        　　[附注] 方中以清命脉赫依药沉香为主药，辅佐以肉豆蔻、丁香、野牛心、兔心、广枣等利于心脏药和草乌、黑云香、马钱子、旋复花等杀粘止痛药合用，适用于赫依血相搏，心悸胸痛。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (309, 2, 20, '阿嘎如--19', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿嘎如--19 </p>\r\n      <p> 　　[异名] 阿嘎日朱古、十九味沉香散。<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方] 沉香12.5g 降香12.5g 白木香12.5g 细辛12.5g 土木香6g 珍珠干6g 苦参6g 广酸枣6g 木棉花6g 诃子6g \r\n        胡黄连6g 山柰6g 丁香l0g 肉豆蔻10g 旋复花10g 木香10g 马钱子(制)10g 川楝子7g 栀子40g <br>\r\n        　　[功能与主治] 镇赫依，平喘，止痛。用于胸闷哮喘，胸部刺痛，山滩界热，主脉赫依病。<br>\r\n        　　[附注] 本方性凉。方中三种沉香(即沉香、降香、白木香为抑赫依药，丁香、肉豆蔻为赫依病专药，均为主药；理血专药三籽(即诃子、楝子、栀子)、胡黄连，调理赫依血相搏药广木香及土木香，缓解止痛药马钱子、旋复花和细辛等为辅佐药，故对一切赫依血、热性刺痛具有较好的疗效。</p>\r\n      <p><br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (310, 2, 21, '希拉大剂', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>希拉大剂<br>\r\n        <br>\r\n        　　[异名] 希拉因・奈日拉嘎、芒召日臣木<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方]地格达3g 木鳖子(制)3g 山苦荬3g 连翘3g 金腰草3g　秦艽花3g　　　麦冬3g 黄柏皮3g 玉簪花3g 熊胆3g 石灰华3g \r\n        白檀香3g 鱼胆3g 紫檀香3g 草河车3g 甘草3g 瞿麦3g 红花3g 冰片3g<br>\r\n        　　[功能与主治] 清希拉，清热。用于目黄，皮肤发黄或发黑等希拉诸病。<br>\r\n        　　[附注] 方中地格达，译音蒙药名，根据蒙医用药情况，使用扁蕾或紫花地丁。本方性凉，以清希拉药地格达、木鳖子、连翘、麦冬、山苦荬、金腰草；清热药冰片、秦艽花、玉簪花、黄柏皮及清心、肺热药白檀香、石灰华、草河车、甘草和清血热药红花、瞿麦、紫檀香等诸药组成；熊胆、鱼胆为锁脉止血药。本方有清希拉，清热诸药组成，故对各种热性希拉病均有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (311, 2, 21, '阿拉腾・波热--25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿拉腾・波热--25<br>\r\n        <br>\r\n        　　[异名] 二十五味金腰花散<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方] 金腰草30g 圆叶碱毛莨30g 五灵脂30g　贯众30g 地格达30g 　木通30g 连翘30g 麦冬30g 木鳖子（制）24g \r\n        角茴香24g 丝瓜子24g 山苦荬15g 诃子15g 川楝子15g 秦艽花15g 栀子15g 漏芦花15g　黄柏皮15g 瞿麦15g 肉豆蔻3g \r\n        光明盐3g 大黄3g　亚大黄3g 菥冥子3g 冰糖180g<br>\r\n        　　[功能与主治] 清希拉，清热。用于希拉增盛，希拉热，胆溢，肝热，口渴烦躁等各种热性希拉诸症。<br>\r\n        　　[附注]本方性凉，为希拉病总方。方中以金腰草、秦艽花、麦冬、连翘、山苦荬、地格达等清希拉诸药作为主药；三籽(诃子、楝子、栀子)、瞿麦、贯众、漏芦花、角茴香等清热解毒、杀粘抗病毒药物，均为辅佐药；加配大黄、亚大黄等清热泻下药，故对由希拉紊乱而引起的各种热性疾病均有疗效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (312, 2, 21, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>查干汞阿-13<br>\r\n        <br>\r\n        　　[异名] 十三味麦冬散<br>\r\n        　　[来源] 《蒙医药选编》<br>\r\n        　　[处方] 查干泵阿30g 连翘36g 木鳖子(制)24g　红花21g 地格达15g 秦艽花15g 瞿麦15g 山苦荬15g　黄柏皮15g \r\n        牛黄l5g 金腰草15g 角茴香l2g 胡黄连12g<br>\r\n        　　[功能与主治]清希拉，清热，解毒。用于希拉热，肝胆热，胃肠热，血热，瘟疫，目及皮肤发黄等。<br>\r\n        　　[附注]方中&quot;查干泵阿&quot;，蒙药译名，根据蒙医药用情况，此方使用&quot;查于泵阿&quot;即麦冬，此方牛黄剂量较大，不易配方，可用人工牛黄。本方性凉，为热性希拉病之主方。方中以清热解毒、清希拉药麦冬为主药；辅佐以地格达(扁蕾或紫花地丁）木鳖子、连翘、秦艽花、金腰草、牛黄等清热、利胆药物，故对热性希拉病、肝胆热、血热、瘟疫等病均有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (313, 2, 21, '伊赫哈日一12', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　伊赫哈日一12<br>\r\n        <br>\r\n        　　[异名] 大黑剂<br>\r\n        　　[来源] 《蒙成药标准》<br>\r\n        　　[处方] 黑冰片100g 地格达30g 土木香30g　红花30g 胡黄连30g 川楝子30g 诃子30g 栀子30g　石灰华30g 甘松30g \r\n        牛胆膏粉20g 人工牛黄5g<br>\r\n        　　[功能与主治] 清希拉热，助消化。用于胃希拉，肝热瘟疫，目肤发黄，口渴烦躁，消化不良。<br>\r\n        　　[附注]本方性凉，方中以清希拉、助消化药黑冰片，为主药；以地格达、诃子、牛胆膏粉等清希拉药和三籽、红花、人工牛黄、甘松等清血热、解毒、杀粘药物，均为辅佐药。故对胃、肝、胆诸热症均有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (314, 2, 21, '洪林一16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>洪林一16<br>\r\n        <br>\r\n        　　[异名] 十六味黄连散<br>\r\n        　　[来源] 《王永福医案》<br>\r\n        　　[处方]胡黄连5g 木鳖子(制)5g 黄柏皮5g 五灵脂5g 地格达5g 黑冰片5g 　 西红花5g 止泻子5g　巴沙嘎5g 角茴香5g \r\n        香青兰5g 山苦荬5g 牛黄5g　查干泵阿5g 木通5g 草乌叶l0g<br>\r\n        　　[功能与主治] 清希拉，清热，解毒。用于目、肤发黄，发热，口渴烦躁，肝热等病。 <br>\r\n        　　[附注] 本方性凉，方中以清粘热，消炎药草乌叶为主药；地格达、止泻子、山苦荬、木鳖子、麦冬等清希拉热药物为辅佐药；牛黄、香青兰、五灵脂、瞿麦、木通等清肝利胆药；均为佐使药；故对热性希拉病，肝胆热症，黄疸病，均有良效。 \r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (315, 2, 21, '查干汞阿-13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　查干汞阿-13<br>\r\n        <br>\r\n        　　[异名] 十三味麦冬散<br>\r\n        　　[来源] 《蒙医药选编》<br>\r\n        　　[处方] 查干泵阿30g 连翘36g 木鳖子(制)24g　红花21g 地格达15g 秦艽花15g 瞿麦15g 山苦荬15g　黄柏皮15g \r\n        牛黄l5g 金腰草15g 角茴香l2g 胡黄连12g<br>\r\n        　　[功能与主治]清希拉，清热，解毒。用于希拉热，肝胆热，胃肠热，血热，瘟疫，目及皮肤发黄等。<br>\r\n        　　[附注]方中&quot;查干泵阿&quot;，蒙药译名，根据蒙医药用情况，此方使用&quot;查于泵阿&quot;即麦冬，此方牛黄剂量较大，不易配方，可用人工牛黄。本方性凉，为热性希拉病之主方。方中以清热解毒、清希拉药麦冬为主药；辅佐以地格达(扁蕾或紫花地丁）木鳖子、连翘、秦艽花、金腰草、牛黄等清热、利胆药物，故对热性希拉病、肝胆热、血热、瘟疫等病均有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (316, 2, 21, '地格达一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>地格达一8<br>\r\n        <br>\r\n        　　[异名] 八味当药散<br>\r\n        　　[来源] 《医疗手册》<br>\r\n        　　[处方]地格达10g 木鳖子(制)８g 苦荬菜8g　广木香8g 黄柏皮7g 胡黄连5g 　麦冬8g 角茴香4g<br>\r\n        　　[功能与主治] 清希拉，泻肝火，利胆。用于希拉引起的头痛，目、肤及小便赤黄，肝胆热，黄疸等。<br>\r\n        　　[附注]本方性凉，为热性希拉病之主方。此方以清希拉热药地格达为主味药；以木鳖子、麦冬、苦荬菜及胡黄连、角茴香、黄柏皮等清希拉、清热解毒药物，为辅佐药。故对希拉热，肝胆热，黄疸均有良效，<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (317, 2, 21, '地格达一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>地格达一3汤<br>\r\n        <br>\r\n        　　[异名] 地格达三味汤<br>\r\n        　　[来源] 地格达一3汤<br>\r\n        　　[处方] 地格达25g 秦艽花15g 查干泵阿15g<br>\r\n        　　[功能与主治]清希拉热。用于希拉引起的头痛。<br>\r\n        　　[附注]方中&quot;查干泵阿&quot;用船形乌头或麦冬，本方性凉，对热性希拉病，特别对希拉引起的头痛有特效。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (318, 2, 21, '巴特日一6汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　巴特日一6汤<br>\r\n        <br>\r\n        　　[异名] 巴布如克汤<br>\r\n        　　[来源] 《蒙药验方》<br>\r\n        　　[处方] 木鳖子{制}15g 白豆蔻15g 诃子15g　甘草15g　白糖15g 粳米30g<br>\r\n        　　[ 功能与主治] 清希拉，助消化。用于热、寒性希拉病，希拉引起呕吐、头痛。<br>\r\n        　　[附注] 本方性凉，此方以木鳖子、诃子、白糖等清希拉药和甘草、粳米、白豆蔻等止吐药组成故对热、寒性希拉病及希拉引起的呕吐均有效。 <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (319, 2, 21, '阿拉坦・阿如拉一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿拉坦・阿如拉一5<br>\r\n        <br>\r\n        　　[异名] 五味金诃子散<br>\r\n        　　[来源] 《蒙医药选编》<br>\r\n        　　[处方] 金诃子66g 黑冰片51.5g 五灵脂18.5g木鳖子(制)7g 全石榴16g<br>\r\n        　　[功能与主治] 调节赫依、希拉，健胃，助消化。用于胃胸积热，积食不消，肝胆热症，黄疸等病。<br>\r\n        　　[附注]本方性平，治赫依、希拉病常用主方。方中金诃子味涩，性平，清希拉，消食，为主药；黑冰片清希拉，助消化，为辅药，木鳖子、五灵脂清腑希拉，石榴祛巴达干赫依、暧胃、助消化，均为辅佐药。各药合用，对胃肠赫依希拉病及消化下良症，均有功效。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (320, 2, 21, '哈日嘎布日一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>哈日嘎布日一10<br>\r\n        <br>\r\n        　　[异名] 十味黑冰片散<br>\r\n        　　[来源] 《蒙医药选编》<br>\r\n        　　[处方] 黑冰片30g 全石榴24g 木鳖子(制)15g　连翘15g 白豆蔻12g 金诃子12g 光明盐9g 荜拔6g　熊胆粉3g 肉桂2.7g<br>\r\n        　　[功能与主治] 清除寒性希拉，助消化。用于消化不良，胃脘痞满，暧气泛酸，巴达干痞症，尤其对寒性希拉有效。<br>\r\n        [附注] 本方性温，以清希拉，助消化良药黑冰片为主药；金诃子、木鳖子、连翘、熊胆等清希拉药为辅药；四味石榴散和光明盐等祛巴达干，均为辅佐药，各药合用，对与消化不良，泛酸均有良效。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (321, 2, 21, '阿那日・莲花一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿那日・莲花一8<br>\r\n        <br>\r\n        　　[异名] 八味石榴莲花散<br>\r\n        　　[来源] 《蒙医药选编》<br>\r\n        　　[处方]全石榴48g 荜拔12g 白豆蔻9g 黑冰片9g　木鳖子(制)9g 金诃子9g 玫瑰花6g 肉桂6g<br>\r\n        　　[功能与主治]祛巴达干希拉，散瘀，消食，止痛 。用于消化不良，胃寒疼痛，胃脘胀满，胃酸呕吐。<br>\r\n        　　[附注]本方性温，为巴达干希拉病主方。方中以寒症药之首石榴为主；肉桂、荜拨、豆蔻为巴达干病专药；黑冰片、金诃子、玫瑰花、木鳖子为希拉病专药，作为辅佐药。各药合用，对胃希拉、巴达干及消化不良，胃痛具有功效。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (322, 2, 22, '阿那日-4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿那日-4<br>\r\n        <br>\r\n        　　[异名] 四味石榴散<br>\r\n        　　[来源] 《蒙医传统验方》<br>\r\n        　　[处方]石榴25g 荜拨9.5g 肉桂5g 豆蔻5g<br>\r\n        　　[功能与主治] 补充胃火，开胃，助消化，除巴达干赫依，揭除隐热之掩蔽。用于不消化病，胃脘寒湿，食欲不振，胃火衰败等病。<br>\r\n        　　[附注] 本方性热，为治寒症之基本方，方中石榴乃是祛寒药物之首，为本方主药；肉桂味辛性热，具抑制巴达干赫依、祛巴达干寒邪的作用，为辅药；荜拨性温以调理体素、补充胃火，除巴达干赫依，为佐药，豆蔻性温，止吐，开胃、消食，补允胃火，为使药。四药合用对各种寒症巴达干病，均有奇效。 \r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (323, 2, 22, '通拉嘎一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>通拉嘎一5<br>\r\n        <br>\r\n        　　[异名] 五味清浊散<br>\r\n        　　[来源] 《中华人民共和国药典》<br>\r\n        　　[处方] 石榴400g 肉桂50g 豆蔻50g　 筚拨50g　　红花200g<br>\r\n        　　[功能与主治] 暖胃消食，清糟归精，化滞除湿。用于胃火衰退，食欲不振，消化不良，胸闷暖气，胃脘冷痛。<br>\r\n        　　[附注] 本方性温，为精华保持正常的主方。本方由四味石榴散加红花所组成，故对寒热症均可适用，亦不引起肝热增盛。方中石榴味酸性热，能暖胃增火，祛巴达干，为主药；红花清肝热，增强体质，为辅药；荜拨消食，调理体素；肉桂祛巴达干寒邪，益胃火；豆蔻消食，均为佐使药。诸药合用，不但祛除巴达干，又不生热，平衡体内寒热，使精华等七素安置复位。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (324, 2, 22, '壮西一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　壮西一6<br>\r\n        <br>\r\n        　　[异名] 六味寒水石散<br>\r\n        　　[来源] 《蒙医传统验方》<br>\r\n        　　[处方] 寒水石(煅)30g 红花24g 土木香15g 荜拔9g 广木香15g 砂仁9g <br>\r\n        　　[功能与主治] 温中和胃，祛巴达干，止吐。用于呕吐酸水，胃脘胀痛，胃巴达干症。<br>\r\n        　　[附注] 本方性温，为胸内灼热及吐酸水的良方。方中寒水石味辛性平、祛巴达干热，止吐，为主主药；红花清血热，为辅药；砂仁开胃，止吐，为佐药；荜拨调理体素，广木香、 \r\n        土木香清巴达干热，均为使药。六味合用，对巴达干热所致的嘈杂吐酸水有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (325, 2, 22, '阿木日一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿木日一6<br>\r\n        <br>\r\n        　　[异名] 六味安消散<br>\r\n        　　[来源] 《蒙成药标准》<br>\r\n        　　[处方] 寒水石(煅)250g 大黄150g　面碱（制）100g　山柰100g 土木香50g 　　诃子50g<br>\r\n        　　[功能与主治] 消食化积，和胃润肠，解痉。用于食积不化，胃腹胀满、大便秘结，胎衣滞留，胃痉挛，巴达干赫依性哮喘，嗳气等。<br>\r\n        　　[附注] 本方性干，为不消化病的主方；方中大黄及面碱均润肠下泻，寒水石除巴达干热，山柰治积食不化，胃火衰退及巴达干性哮喘。六味合用，乃为治大便秘结之良方。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-06');
INSERT INTO myy_text VALUES (326, 2, 22, '查干乌口勒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>查干乌口勒<br>\r\n        <br>\r\n        　　[异名] 白甘露完<br>\r\n        　　[来源]《蒙医药选编》<br>\r\n        　　[处方]万年灰（制）60g 荜拨6g 紫硇砂3g　沙棘3g 　 山柰3g<br>\r\n        　　[功能与主治] 健脾温胃，消积化痞，利水，用于巴达干引起的各种积聚痞块，消化不良，浮肿，水肿，胃痉挛，寒性虫疾。<br>\r\n        　　[附注] 本方性温，为不消化病的主方。方中万年灰以助消化，祛胃巴达干，破痞，为主药；配以山奈、荜拨以祛巴达干及补胃火；沙棘除巴达干，化血，紫硇砂通便，止痛，均为辅佐药。五药合用，对不消化病、胃巴达干、胸口痞等均有良效。 \r\n      </p>\r\n      <p></p>\r\n      <p><br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (327, 2, 22, '阿那日一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>阿那日一8<br>\r\n        <br>\r\n        　　[异名] 森布如・扎塔巴、 八味石榴散。<br>\r\n        　　[来源] 《蒙医成方选》<br>\r\n        　　[处方] 石榴24g 肉桂30g 豆蔻30g 白胡椒30g 光明盐30g 高良姜30g 紫硇砂30g 荜拨60g<br>\r\n        　　[功能与主治] 助消化，祛巴达干。用于胃巴达干，新旧不消化病，腹胀肠鸣，寒症引起的吐泻等。<br>\r\n        　　[附注] 本方性热，为治不消化病的常用方。方中以四味石榴散为主，配以紫硇砂、光明盐、白胡椒、高良姜(或干姜)等具有补胃火，助消化之药为辅，故对胃寒，消化不良，均有疗效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (328, 2, 22, '达布苏一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>达布苏一4汤<br>\r\n        <br>\r\n        　　[异名] 毛勒日・达布苏一4汤、扎木萨一4<br>\r\n        　　[来源] 《四部医典》<br>\r\n        　　[处方] 光明盐125g 干姜125g 金诃子125g 荜拨125g<br>\r\n        　　[功能与主治] 温胃，消食，解毒。用于消化不良，药物、食物中毒，巴达干包如病及巴达干引起的吐泻、嗳气、胃胀等诸寒症<br>\r\n        　　[附注] 本方性温，为不消化病之主方。方中光明盐味咸，性温、锐，能除巴达干，消食；干姜、荜拨味辛，性温，锐，补胃火，除巴达干赫依；金诃子能中和，协调体素，并具解毒作用。四药合用，对饮食<br>\r\n        起居不当而引起的胃火衰退及消化不良等诸症，均奏良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (329, 2, 22, '哈日嘎日迪一5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　哈日嘎日迪一5汤<br>\r\n        <br>\r\n        　　[异名] 冲纳各阿汤、五味红盐汤，五味黑鹏汤。<br>\r\n        　　[来源] 《珊瑚验方集》<br>\r\n        　　[处方] 光明盐20g 金诃子20g 干姜20g 荜拨20g 紫硇砂20g<br>\r\n        　　[功能与主治] 祛巴达干赫依，助消化，止痛。用于消化不良，胃火衰退，腹胀肠鸣，胃痛，暖气，巴达干病等。<br>\r\n        　　[附注] 本方性温，由四味光明盐汤加紫硇砂所组成。为治腹胀，助消化之良药，亦是巴达干寒症的总方。紫硇砂可补胃火，适用于嗳气、腹胀、肠鸣等，为主药；配四味光明盐汤对不消化病颇有良效。<br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (330, 2, 22, '吉如干查干-6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>　吉如干查干-6 <br>\r\n        <br>\r\n        　　[异名] 嘎日布一6、六白剂。<br>\r\n        　　[来源] 《蒙医金匮》<br>\r\n        　　[处方] 碱花18g 寒水石(煅)6g 光明盐6g 土木香6g 山柰6g 硼砂(制)3g 雕粪3g<br>\r\n        　　[功能与主治] 温胃补火，消积化痞。用于包如巴达干，胃火衰退，消化不良，食欲不振， 糟粕未消，嗳气吞酸，胃脘痞满。<br>\r\n        　　[附注] 本方性温，为不消化病的主方。方中碱花味咸性温，消食润肠，祛巴达干，为主药；寒水石、土木香消食、祛巴达干热；光明盐、雕粪补胃火、消积；山柰祛巴达干赫依；均为辅佐药。各药合用，对<br>\r\n        积食不化、口苦、泛酸等均有功效。方中每味药为灰色或白色，故称吉如干查干(六白剂)。<br>\r\n        <br>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-06');
INSERT INTO myy_text VALUES (331, 2, 23, '伊赫乌兰-13汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>伊赫乌兰-13汤<br>\r\n      <br>\r\n      　　[异名] 十三味大红汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]珍珠干l2g 紫草茸9g 土木香3g 栀子9g　橡子9g 川楝子3g 苦参3g 　　诃子3g 枇杷叶3g 茜草3g　五台花3g 山奈1.5g \r\n      紫草1.5g<br>\r\n      　　[功能与主治] 清血热。用于血热上盛、头痛，目赤,高血压、白脉病，成熟热及未成熟热等诸热症。<br>\r\n      　　[附注]本方性凉，为治血热的良方。本方由查干汤、沙日汤、四红汤等三方复合，并配以五台花、橡子所组成。方中查干汤对未成熟热及感冒有效，沙日汤清血热，四红汤清肺热及血热。三方合用，对诸热症均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (332, 2, 23, '古日古木一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>古日古木一8<br>\r\n      <br>\r\n      　　[异名] 八味红花止血散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]西红花10g 熊胆10g 豌豆花8g 紫檀香7g　地锦草7g　木鳖子5g　射干7g银朱7g<br>\r\n      　　[功能与主治] 止血。用于胃肠出血，月经淋漓,吐血，咯血，鼻衄等各种出血症。<br>\r\n      　　[附注]本方性凉，为止血良药。方中红花及熊胆锁脉止血有奇效，同为主药；豌豆花、地锦草亦能止血，同为辅药；木鳖子清希拉热，射干除巴达干热，同为佐使药。八味合用，对各种出血症，均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (333, 2, 23, '霞日汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>霞日汤<br>\r\n      <br>\r\n      　　[异名] 三籽汤<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方]金诃子10g 川楝子10g 　水栀子10g<br>\r\n      　　[功能与主泊]清热，解毒，凉血，分离正血与恶血，用于血热引起的头痛、牙痛、眼红，瘟热，内热，希拉热，恶血与正血混合症。<br>\r\n      　　[附注]本方性凉，为清血热，分离正血与恶血的常用汤剂。方中栀子味酸、苦、性凉，对血热病颇有治疗作用，为主药。诃子味涩、性平清希拉热、调理体素，为辅药；川楝子味苦、性凉，清浊热，为佐使药，本方三药各等量，临床用药，根据病情，可酌情加咸各药用量。如希拉热盛音，可加金诃子量，血热盛者，可加水栀子量，浊热盛者，可加川楝子量。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (334, 2, 23, '霞日毛都一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>霞日毛都一3汤<br>\r\n      <br>\r\n      　　[异名] 三味黄柏汤<br>\r\n      　　[来源] 《诊治医典》<br>\r\n      　　[处方]黄柏皮25g 小白蒿25g 熊胆5g<br>\r\n      　　[功能与主治]止血。用于吐血，鼻衄包如病。<br>\r\n      　　[附注]本方性凉，为鼻衄良药，方中黄柏、小白蒿、熊胆，均有止血作用，故本方对出血症，特别对鼻衄颇有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (335, 2, 23, '古日古木一3', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>古日古木一3<br>\r\n      <br>\r\n      　　[异名] 三味红花汤<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]西红花5g 紫草茸5g　 熊胆5g <br>\r\n      　　[功能与主治]止血。用于吐血，鼻纽，包如病穿漏，月经淋漓等症。 \r\n      <p></p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (336, 2, 23, '地格达一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>地格达一4汤<br>\r\n      <br>\r\n      　　[异名] 四味当药汤<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]地格达25g 胡黄连25g 　栀子25g 　巴沙嘎25g<br>\r\n      　　[功能与主治]清希拉，凉血，清糟归精。用于血希拉热，肝胆热，咽喉肿痛，口渴烦燥。<br>\r\n      　　[附注]本方性凉，为血热相搏及肝胆热症良方。方中地格达味苦性寒以清希拉热，为主；胡黄连味苦性寒以清内热，为辅；栀子及巴沙嘎以清血热，为之佐使。四药配用，性味及功能相互协调，对血热相搏症有效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (337, 2, 23, '额日赫腾・嘎日一8汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>额日赫腾・嘎日一8汤<br>\r\n      <br>\r\n      　　[异名] 八味手参汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]手掌参25g 苦参15g 杜仲15g 龙骨10g　地格达10g　诃子5g 川楝子5g 栀子5g<br>\r\n      　　[功能与主治]清血热。主治巴木病。 <br>\r\n      　　[附注]本方性平，为巴木病的专方。方中手掌参滋身益精，为主药；苦参、川楝子清黄水热，栀子清血热，共为辅药；配以杜仲健筋骨，诃子调理体素解毒，龙骨止腐、缓解刺痛，地格达清希拉热，为之佐使。诸药配用，对血盛引起的巴木病有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (338, 2, 23, '措森・嘿木日嘎一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>措森・嘿木日嘎一8<br>\r\n      <br>\r\n      　　[异名] 措森・嘿木日嘎因・塔拉哈、骚血普清散、日哈克・如克・棍斯勒。<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 寒水石(煅)51g 紫草51g 土木香7.8g 石灰华6.3g 牛黄6.6g 栀子21g 巴沙嘎9g 甘草3g<br>\r\n      　　[功能与主治)清血热。用于血热引起的头痛，赤，牙痛，中暑，不宜放血治疗之热病等。<br>\r\n      　　[附注]本方性凉，为不宜放血治疗之血热病的专用方。方中以血热药紫草，巴达干热药寒水石同为主药；配以栀子、巴沙嘎等清血热药，协助紫草和寒水石清泄血热；肺热药石灰华、肝热药牛黄、赫依血相搏药：土木香，均为辅佐药；甘草调和诸药并清除脉热，是为佐使药。各药合用，对不宜放血之血热病，起到如同放血治疗的效果。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (339, 2, 23, '措森・达如拉嘎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　措森・达如拉嘎 <br>\r\n      <br>\r\n      　　[异名] 止血散<br>\r\n      　　[来源] 《中国医学百科全书--蒙医学》<br>\r\n      　　[处方] 西红花150g 熊胆粉25g 栀子10g 银朱5g五灵脂5g 甘草5g<br>\r\n      　　[功能与主治] 止血。用于吐血，鼻衄，便血，尿血，月经淋漓，外伤出血等各种出血症。<br>\r\n      　　[附注] 本方性凉，为止血良方。方中以清血热药红花为主，止血药熊胆为辅；配以栀子清血热，甘草、银朱清脉热，石灰华清肺热，五灵脂清肾及肝热，为之佐使。六药合用，对肾热引起的尿血，肺脉损伤引起的咯血、包如穿漏引起的吐血及便血等九窍出血症，均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (340, 2, 23, '别日木格一5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>别日木格一5汤<br>\r\n      <br>\r\n      　　[异名] 别日木和・塔本汤、东日勒・阿汤、五味紫草汤。<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 熊胆3g 紫草15g 地锦草15g 巴沙嘎12g 基力哲3g<br>\r\n      　　[功能与主治] 止血。用于脉口出血，月经淋漓。<br>\r\n      　　[附注] 本方性凉，为止血专方。方中以凉血止血药紫草及地锦草为主药；以凉血药巴沙嘎为辅药；以销脉止血药熊胆及清希拉热药基力哲为佐使。方中基力哲是译音名，根据蒙医用药情况，使用秦艽花。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (341, 2, 23, '土茯苓一7汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>土茯苓一7汤<br>\r\n      <br>\r\n      　　[异名] 七味土茯苓汤。<br>\r\n      　　[来源] 《珊瑚验方集》<br>\r\n      　　[处方] 土茯苓100g 金银花10g 金诃子8g 川楝子8g 水栀子8g 巴沙嘎15g 黄连15g<br>\r\n      　　[功能与主治] 凉血，解毒，止血。用于血热引起头痛、鼻肿、喉塞、面部出疹、血崩等诸血证。<br>\r\n      　　[附注] 本方性凉，为血热症的有效验方。方中以清热解毒药土茯苓为主药；解热药金银花为辅；配以三籽汤及巴沙嘎凉血药，为之佐使；故对血热、毒热症均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (342, 2, 24, '森登--4汤　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　森登--4汤　　<br>\r\n      <br>\r\n      　　[异名] 四味文冠木汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]文冠木15g 川楝子9g 金诃子3g 水栀子3g　<br>\r\n      　　[功能与主治]清热，消肿，燥黄水。用于痛风痹病，关节黄水病，水肿等。<br>\r\n      <br>\r\n      　　[附注]本方性凉，为黄水病常用方。方中文冠木以燥黄水、消肿止痛，为主药，川楝子清热、燥黄水为辅药；栀子清血热，诃子解毒、调理体素，均为佐使。四味药合用，对黄水病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (343, 2, 24, '森登一25　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>森登一25　　<br>\r\n      <br>\r\n      　　[异名] 二十五味文冠木散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]文冠木膏3g 白云香3g 秦艽花3g 　宽苞棘豆3g 决明子3g 玫瑰花3g 黄柏皮3g 苘麻子3g　金诃子3g 川楝子3g 水栀子3g \r\n      草乌(制)3g 广木香3g 水菖蒲3g 　 白蒺藜3g 石决明3g 水牛角3g 天花粉3g 玉竹3g 苦参3g 天门冬3g 黄精3g铁屑（制）3g \r\n      麝香3g<br>\r\n      　　[功能与主治] 燥黄水，舒经通脉。用于痛风，游痛证，风湿病，白脉病，黄水病，皮肤病，萨病。<br>\r\n      　　[附炷]本方性凉，为黄水病和白脉病的常用方。本方主要由黄水病三药(白云香、苘麻子、决明子)、三籽汤(诃子、栀子、楝子)、五根药(玉竹、黄精、天门冬、天花粉、蒺藜)及嘎日迪一5等药方组成。方中十六味药皆能祛风湿，燥黄水，故对黄水病及白脉病均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (344, 2, 24, '孟根沃斯一18　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　孟根沃斯一18　<br>\r\n      <br>\r\n      　　[异名] 十八味水银丸<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 水银(制)60g 草乌(制)60g 硫黄 (制)45g 金诃子60g 文冠木膏45g 白云香45g 决明子45g 苘麻子45g 　广木香30g \r\n      石菖蒲21g 石灰华12g　白豆蔻2g 西红花9g 公丁香9g 肉豆蔻9g 黑云香9g　草果仁9g 麝香9g <br>\r\n      　　[功能与主治] 燥黄水，杀粘，止痒，收敛生肌。用于痛风，游痛证，巴木病，黄水病，风湿，类风湿性关节炎，布病，皮癣，疮疡，疥癣，瘰疬，鼠疮，奇哈，吾雅曼病等。 \r\n      [附注] 本方性凉，为根除黄水病良药。本方主要由黄水病三味药、六味良药及嘎日迪一5等药方组成，方中水银燥黄水、杀虫、文冠木燥黄水、消肿、止痛，草乌燥黄水、杀粘，均为主药；配以硫黄、黄水病三药专治黄水病及皮肤病，嘎日迪一5消肿止痛，杀粘；配加六味良药对治疗黄水病及皮肤病起到辅助作用，并防止各器官积热而引起功能紊乱。各药合用，其药效增强。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (345, 2, 24, '萨仁・嘎日迪　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　萨仁・嘎日迪　　<br>\r\n      <br>\r\n      　　[异名] 月光宝凤丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 水银(制)27g 草乌（制）24g 硫黄(制)15.3g　狼毒(制)18g 金诃子12g 广木香9g 西红花6g　白豆蔻6g 黑云香6g \r\n      白硇砂5.7g 草决明5.4g 苘麻子5.4g 白云香4.5g 石菖蒲3.6g 石灰华3.6g 肉豆蔻3g 草果仁3g 海金沙3g 翻白草3g \r\n      公丁香2.4g 方海2.7g　牛黄3g 麝香2.1g <br>\r\n      　　[功能与主治] 燥黄水，杀粘，消肿，愈合白脉损伤.。用于痛风、游痛证，关节疼痛，麻风病等黄水病类；结喉,炭疽等粘病类；萨病，亚玛病，白脉病等。 \r\n      [附注] 本方性凉，为黄水病及白脉病的常用方。此方主要由黄水病三药、利尿三药、六味良药及嘎日迪一5等药方组成。方中以燥黄水、杀虫药水银、硫黄为主药；配用牛黄、麝香，乃增强药力，故对黄水病、粘病、白脉病均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (346, 2, 24, '嘎日迪一15　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　嘎日迪一15　 <br>\r\n      <br>\r\n      　　[异名] 十五味云凤丸<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]白云香9g 黑云香9g 石菖蒲9g 草决明6g　苘麻子6g 五灵脂6g 草乌(制)6g 苦参6g 金诃子4.5g 川楝子4.5g 水栀子4.5g \r\n      巴沙嘎4.5g 广木香3g 儿茶3g 麝香0.9g<br>\r\n      　　[功能与主治] 燥黄水，杀粘，消肿。用于痛风，游痛证，关节黄水病，狼头疮，巴本病，疥疮，丘疹等皮肤病。<br>\r\n      　　[附注]本方性凉，为黄水病的主方。本方由十味白云香散和嘎日迪一5配合组成。方中十味白云香散主治风湿病、嘎日迪一5主治粘病，两方合用对风湿，骨关节肿痛均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (347, 2, 24, '额勒吉根・措斯-25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　额勒吉根・措斯-25<br>\r\n      <br>\r\n      　　[异名] 二十五味驴血丸<br>\r\n      　　[来源] <br>\r\n      　　[处方]驴血15g 白云香4.5g 苘麻子4.5g 草决明4.5g 肉豆蔻3.6g 紫檀香9g 　　苦参3g 水栀子3g　巴沙嘎3g 麝香2.1g \r\n      牛黄2.1g 西红花2.1g 草果仁2.1g 白豆蔻2.1g 金诃子2.1g 川楝子2.1g 地格达2.1g 玉簪花2.1g 漏芦花2.1g 公丁香2.4g　木棉花蕊2.4g \r\n      杜仲2.4g 木棉花2.4g 白檀香1.8g 石膏0.6g<br>\r\n      　　[功能与主治]燥黄水，消肿，用于痛风游痛症，痹病，巴木病，布氏病，关节肿痛等黄水及皮肤病。<br>\r\n      　　[附注]本方性凉，为痹病主方。此方主要以燥黄水药驴血为主药，以黄水病三药、三籽汤、三凉药、三温药(合称六味良药)、两檀香等，共为辅佐药。配加以黄水病常用药苦参、凉血药巴沙嘎、健骨药杜仲、清热解毒杀粘药牛黄、地格达、漏芦花、麝香等。诸药合用，对全身性黄水病及布氏病均有良效。并防止受黄水影响器官功能紊乱而生热。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (348, 2, 24, '查干古古勒一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　查干古古勒一10<br>\r\n      <br>\r\n      　　[异名] 别嘎日・朱瓦、十味白云香散。<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 白云香12g 决明子9g 五灵脂9g 苘麻子9g 川楝子9g 广木香6g 金诃子3g 水栀子3g 苦参6g 巴沙嘎3g<br>\r\n      　　[功能与主治] 清热，燥黄水。用于痛风，痹病，，关节疼痛等黄水病。<br>\r\n      　　[附注] 本方性凉，为黄水病的常用方。本方由黄水病常用三味药(即白云香、苘麻子、决明子)及三籽汤(即诃子、栀子、楝子)组成。故对热性黄水病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (349, 2, 24, '森登一10汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　森登一10汤<br>\r\n      <br>\r\n      　　[异名] 森登朱汤、十味文冠木汤。<br>\r\n      　　[来源] 《蒙医药方汇编》<br>\r\n      　　[处方] 文冠木膏10g 白云香10g 决明子10g 苘麻子10g 黄柏皮10g 金诃子10g 川楝子10g 水栀子10g 黑云香10g 五灵脂10g<br>\r\n      　　[功能与主治] 清热，消肿，止痛。用于痛风，游痛证，关节肿痛等黄水病。<br>\r\n      　　[附注] 本方性凉，为热性黄水病常用汤剂。本方主要由黄水病常用三味药白云香、苘麻子、决明子及文冠木膏组成。配用三籽汤和黄柏皮，以增强药力；故对关节肿痛均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (350, 2, 24, '那如一3', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　那如一3<br>\r\n      <br>\r\n      　　[异名] 那如・苏木召日、三味纳如丸。<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 诃子30g 草乌15g 荜拨9g<br>\r\n      　　[功能与主治] 除黄水，祛巴达干赫依，杀粘，散寒，止痛。用于关节痛，牙痛，黄水病，虫疾，结喉、炭疽等粘性疾病，腰胯疼痛等。<br>\r\n      　　[附注]本方性温，为寒性黄水病良方。方中草乌杀粘，止痛，燥黄水，为主药；诃子调理体素，解毒，为辅药；荜拨补胃火，祛巴达干赫依，为佐使。三味合用，对寒性黄水病及粘病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (351, 2, 24, '粘冲一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　粘冲一13<br>\r\n      <br>\r\n      　　[来源] 《都格尔扎布医案》<br>\r\n      　　[处方] 金诃子3g 川楝子3g 水栀子3g 草乌(制)3g 石菖蒲3g 广木香3g 白云香4g 苘麻子4g 决明子4g 文冠木膏5g 苦参5g \r\n      黎豆5g 麝香18<br>\r\n      　　[功能与主治] 祛黄水，杀粘，消肿，止痛。用于粘与黄水合并症，风湿病，骨关节肿痛，皮肤骚痒，牛皮癣等。杀粘，消肿，止痛。用于骨关节肿痛，皮肤骚痒，<br>\r\n      　　[附注] 本方乃都格尔扎布专治风湿病验方。都格尔扎布(1923一)系内蒙古哲盟科左后旗人，原内蒙 古自治区医院蒙医科主任，主任医师，著名蒙医药学专家。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (352, 2, 25, '浩如亥因・吉登嘎　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　浩如亥因・吉登嘎　　<br>\r\n      <br>\r\n      　　[异名] 七味信筒子散<br>\r\n      　　[来源] 《诊治医典》<br>\r\n      　　[处方] 信筒子10g 可瓜子10g 苘麻子10g 马蔺子10g 大蒜l0g 铁杆蒿炭10g 麝香1g<br>\r\n      　　[功能与主治] 驱虫。用于消化道虫病，亚玛病，脑虫、肛门虫、阴道滴虫、皮肤虫等虫病。<br>\r\n      　　[附注] 本方性温，为内外虫病主方。方中以信筒子杀虫，为主药，大蒜、马蔺子、可瓜子亦杀虫，为辅药；配以铁杆蒿炭和麝香杀粘虫，苘麻子驱皮肤虫，祛黄水，各药合用，对各种虫病均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (353, 2, 25, '吉登嘎一7　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉登嘎一7　　<br>\r\n      <br>\r\n      　　[异名] 七味蔓荆子散<br>\r\n      　　[来源] 《诊治医典》<br>\r\n      　　[处方] 蔓荆子7g 阿魏6g 可瓜子5g 干姜4g　麝香3g 铁杆蒿炭2g 荜拨1g<br>\r\n      　　[功能与主治]杀虫。用于肛门虫，阴道滴虫，皮癣，黄水疮等皮肤病，胃肠之内虫病等。<br>\r\n      　　[附注]本方性温，为消化道虫病常用方。方中蔓荆子杀虫，为主药；阿魏性热，温中、杀虫，为辅药；可瓜子驱虫，干姜性热，祛寒、补胃火，荜拨调理体素，铁杆蒿炭和麝香均杀粘虫，均为佐使；各药合用，对各种虫病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (354, 2, 25, '吉如格一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉如格一8<br>\r\n      <br>\r\n      　　[异名] 吉如格・扎塔巴、八味荆芥散。<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 荆芥10g 信筒子10g 辣椒10g 花椒10g 大蒜10g 藁本10g 天南星10g 铁杆蒿炭10g<br>\r\n      　　[功能与主治] 杀虫。主治秃疮，黄水疮，疥疮等皮肤虫病及肛门虫病。<br>\r\n      　　[附注] 本方性热，为皮肤虫病专方。方中荆芥杀虫，为主药；信筒子、天南星、花椒、辣椒、大蒜以止痒，杀虫，共为辅药；藁本祛黄水并杀虫，铁杆蒿杀粘虫，共为佐使。各药合用，对皮肤虫病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (355, 2, 25, '胡椒一3', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　胡椒一3<br>\r\n      <br>\r\n      　　[异名] 那勒沙木一3、三味古月散。<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 黑胡椒9g 白矾9g 硫黄15g<br>\r\n      　　[功能与主治] 止痒，杀虫。用于疥疮，黄水疮，疥癣等皮肤病。<br>\r\n      　　[附注] 本方性温，为疥疮等皮肤病常用方。方中三味药，均有止痒杀虫性能，故对皮肤骚痒症均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (356, 2, 25, '呼呼日一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　呼呼日一6 <br>\r\n      <br>\r\n      　　[异名] 六味硫磺散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 白云香3g 硫磺(制)6g 草乌(制)3g 草决明 3g 苘麻子 3g 金诃子 3g<br>\r\n      　　[功能与主治] 燥黄水，止痒，杀粘。用于黄水疮、疥疮，秃疮，骚痒症等皮肤病。<br>\r\n      　　[附注] 本方性平，为皮肤病常用方。方中硫黄以燥黄水，杀虫，止痒，为主药；草乌祛黄水，杀粘。草决明、苘麻子、白云香乃黄水病常用药；诃子调理体素，解毒，共为辅药。六药合用，对黄水疮等皮肤病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (357, 2, 25, '恰黑乐德各一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　恰黑乐德各一3汤<br>\r\n      <br>\r\n      　　[异名] 热敏布如一3汤、 三味马蔺子汤。<br>\r\n      　　[来源] 《白清云验方》<br>\r\n      　　[处方] 马蔺子l0g 蔓荆子10g 可瓜子l0g<br>\r\n      　　[功能与主治] 杀虫。用于阴道滴虫病。<br>\r\n      　　[附注] 本文乃白清云专治滴虫病验方。白清云(1912―1989)系辽宁省阜新县人，生前内蒙古自治区中蒙医系任教，首届蒙医教授，著名蒙医内、儿、妇科专家。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (358, 2, 25, '孟根沃斯一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　孟根沃斯一5<br>\r\n      <br>\r\n      　　[异名] 乌力础・阿瓦、乌力础--5<br>\r\n      　　[来源] 《都格尔扎布医案》<br>\r\n      　　[处方] 水银(制)21g 硫黄(制)21g 草乌(制)15g 西青果21g 荜拨9g<br>\r\n      　　[功能与主治] 杀粘，驱虫。用于囊虫病。<br>\r\n      　　[附注] 本方乃都格尔扎布专治囊虫病验方。都格尔扎布(1923一)系内蒙古哲里木盟科左后旗人，原内蒙古自治区医院蒙医科主任，主任医师，著名蒙医药专家。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (359, 2, 26, '嘎日迪-5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>嘎日迪-5 <br>\r\n      <br>\r\n      　　[异名] 五凤丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]草乌(制)30g 金诃子30 广木香9g 水菖蒲6g 麝香3g<br>\r\n      　　[功能与主治]杀粘，消肿，止痛，燥黄水。用于粘疫，粘痧，粘刺痛，白喉，炭疽，丹毒，麻风，痛风，游痛证，黄水病，瘰疬疮病，疥癣等。<br>\r\n      　　[附注]本方性温，为粘性疾病主方。因其功能奇特，临床应用甚广，故以鸟中之王嘎日迪（凤凰）比喻，即命名嘎日迪五味丸。方中草乌，麝香以燥黄水，止痛、杀粘为主药；菖蒲以杀粘、祛黄水、为辅药。木香以抑制赫依紊乱、止痛，诃子以调理体素、解毒，共为佐使，五药合用，其杀粘、祛黄水、止痛之作用。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (360, 2, 26, '巴特日--7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>巴特日--7 <br>\r\n      <br>\r\n      　　[异名] 七雄丸<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方]草乌芽25g 金诃子25g 茜草10g 翻白草10g银朱10g 麝香1.5g 黑云香10g<br>\r\n      　　[功能与主治]清瘟解毒，杀粘止痛，散瘀止痢，用于瘟疫热盛,脑刺痛,赤白痢疾，结喉，目黄，音哑,麻疹,霍乱转筋。<br>\r\n      　　[附注]本方性凉，为粘热症及肠炎常用方；方中草乌芽以杀粘、止痛，为主药；配以黑云香杀粘、止痛，麝香杀粘、开窍，翻白草亦杀粘，茜草凉血、止泻，诃子解毒。共为辅佐，各药合用，对粘热、瘟疫、肠炎、痢疾等病均有奇效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (361, 2, 26, '呼和嘎日迪一9', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　呼和嘎日迪一9 <br>\r\n      <br>\r\n      　　[异名] 九味青鹏丸<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方]草乌(制)25g 金诃子25g 土木香15g 漏芦花l5g 黑云香l0g 翻白草15g 胡黄连10g 北沙参10g 草河车10<br>\r\n      　　[功能与主治]消粘，解热，止咳。用于瘟疫热症，感冒咳嗽，咽喉肿痛。<br>\r\n      　　[附注]本方性凉，为瘟热，流感的主方。方中草乌以杀粘，为主药；翻白草、漏芦花、黑云香以杀粘，解热,为辅药;配以胡黄连清内热,土木香调理体素,沙参、拳参、止咳。共为佐药，各药合用，对流感咳嗽效果甚佳。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (362, 2, 26, '哈特拉嘎一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　哈特拉嘎一6<br>\r\n      <br>\r\n      　　[异名] 六味止痛散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]草乌芽15g 土木香9g 马钱子(制)9g 广木香3g 肉豆蔻3g 山沉香6g<br>\r\n      　　[功能与主治]杀粘，镇痛。用于瘀血刺痛，气滞刺痛，粘刺痛。<br>\r\n      　　[附注]本方性平，为治刺痛的常用方。方中草乌芽以止痛、杀粘、为主药；马钱子、土木香以止痛，祛赫依血相搏，为辅药，沉香、木香对赫依性刺痛亦有功效。各药合用为刺痛诸病之总方且效果显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (363, 2, 26, '吉召木道尔吉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉召木道尔吉<br>\r\n      <br>\r\n      　　[异名] 无敌金刚丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]草乌(制)3g 白硇砂3g 西红花6g 刺柏叶6g 贯众6g 麝香0.9g 熊胆0.9g 甘松6g 牛黄0.9g<br>\r\n      　　[功能与主治]清热，解毒，杀粘,止痛,燥黄水,用于粘热症，瘟疫发烧，久热，炽热,扩散热,炭疽，结喉，肠炎，妇科病，黄水病，吾雅曼病,亚码病,萨病，便秘，尿闭等病。<br>\r\n      　　[附注]此方性凉，为粘热症及黄水病常用方。方中麝香、草乌以杀粘，牛黄、红花、甘松、贯众等以清热解毒，白硇砂、刺柏叶以利尿、燥黄水，熊但为清热止血。各药合用，对粘热症及各种热症均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (364, 2, 26, '洪古乐朱日一12', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　洪古乐朱日一12<br>\r\n      <br>\r\n      　　[异名] 帮赛一12、十二味漏芦花丸、洪格日一12、帮赛朱尼<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 漏芦花5.7g 草乌叶7.2g 角茴香7.2g 黑云香5.1g 麦冬3.9g 翻白草3.6g 石膏3g 红花3g 白檀香3g 五灵脂3g \r\n      牛黄2.4g 麝香2.1g<br>\r\n      　　[功能与主治] 清瘟，解热，止痛。用于瘟疫，粘病，咽喉肿痛，牙痛，头痛。<br>\r\n      　　[附注] 本方性凉，为粘热症的主方。方中漏芦花以杀粘，止痛，为主药；配以杀粘药物麝香，黑云香、草乌叶、角茴香、翻白草等，清热药物牛黄、红花、白檀香、石膏等。各药合用，杀粘、清热作用齐备，从而对粘热症，特别是对瘟疫病有极好的治疗效果。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (365, 2, 26, '敖其日・奈日拉嘎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>敖其日・奈日拉嘎 <br>\r\n      <br>\r\n      　　[异名] 道日吉套格巴<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 草乌(制)20g 金诃子20g 石菖蒲3g 翻白草10g 西红花10g 五灵脂 5g 石灰华5g 黑云香 5g 广木香5g 刀豆5g \r\n      方海5g 牛黄1.5g 麝香1.5g<br>\r\n      　　[功能与主治] 杀粘，止痛。用于粘热，结喉，炭疽，刺痛症，巴木病等。<br>\r\n      　　[附注] 本方性凉，为粘症及巴木病的常用方。方中主要以草乌、麝香、黑云香、石菖蒲、翻白草等杀粘药物组成，故对结喉、发症等粘性疾病均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (366, 2, 26, '巴勒其日根一29', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　巴勒其日根一29<br>\r\n      <br>\r\n      　　[异名] 布如纳格一29、二十九味藁本丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 藁本30g 草乌(制)15g 麝香1.5g 苦参15g 黑冰片 9g 铁杆蒿 9g 地格达 9g 草河车 9g 五灵脂9g 牛黄9g \r\n      麦冬9g 黑云香9g 翻白草9g 沉香9g 木鳖子9g 草乌芽6g 丁香9g 山豆根6g 白檀香6g 石膏6g 齿缘草6g 角茴香6g 蓝刺头6g \r\n      细辛6g 草乌叶3g 红花3g 野兔心3g 石菖蒲3g 硫黄(制)3g<br>\r\n      　　[功能与主治] 清粘热。用于疫热，肠刺痛，结喉，炭疽，痘疹，瘟疫，感冒等粘性疾病。<br>\r\n      　　[附注] 本方性凉，为除粘热与赫依相搏之良方。方中藁本、草乌全草(包括根、叶、芽)以杀粘，为主药；配以齿缘草、黑云香、角茴香、翻白草、菖蒲、铁杆蒿、麝香、细辛等杀粘药，共为辅药；牛黄，红花、檀香、地格达、麦冬、石膏等均为清热药，丁香、沉香、兔心均为镇赫依药，共为佐使；本方由杀粘药、清热药和镇赫依药等三组药组成，故对粘、热、赫依相搏症，均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (367, 2, 26, '伊顺哈日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　伊顺哈日<br>\r\n      <br>\r\n      　　[异名] 伊顺哈日・奈日拉嘎、纳格布古召日、九黑丸<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 麝香1g 牛黄2g 沉香2g 香墨2g 硫黄2g 阿魏2g 菖蒲2g 黑云香2g 草乌2g <br>\r\n      　　[功能与主治] 杀粘，防疫，解毒，止痛。用于防治粘疫，流感，脑膜炎，菌痢，结喉，炭疽及亚玛病等传染性疾病。<br>\r\n      　　[附注] 本方性平，为预防瘟疫专用方。方中除牛黄、硫黄外其余药色基本发黑，故命名为伊顺哈日(九黑)。伊顺哈日有甲乙两种配方，乙方以红花、雄黄、独头蒜代替沉香、硫黄、香墨。两方皆有杀粘良药麝香、草乌、黑云香、菖蒲和清瘟解毒药牛黄及杀虫祛寒药阿魏。因而本方杀粘清热驱虫之功具全，故对防治传染性疾病，均有良效。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (368, 2, 27, '查干汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>查干汤<br>\r\n      <br>\r\n      　　[异名] 四味土木香汤<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]土木香30g 苦参30g 珍珠干15g 山奈7.5g<br>\r\n      　　[功能与主治]清瘟解表。用于瘟病初起，包如病初起，包如巴达干，发冷发烧，血热头痛，气血不调,胸胁刺痛，气短咳嗽，感冒初起等症。<br>\r\n      　　[附注]本方性平，为促使未成熟热之主方。方中土本香、珍珠干以调理体素，清热止咳，苦参解表祛黄水，山柰以祛寒化瘀，四味均具全甘、苦、辛味,故能抑制赫依、希拉、巴达干增盛及三者相搏均有功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (369, 2, 27, '额日敦一7汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　额日敦一7汤<br>\r\n      <br>\r\n      　　[异名] 克感灵<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]苦参20g 珍珠干15g 山奈10g 土木香10g　栀子15g 川楝子10g 诃子5g<br>\r\n      　　[功能与主治] 清热凉血，解毒止痛。用于感冒，发烧，瘟疫热，内热引起的口渴舌干，目赤，小便赤黄，气喘痰稠，血热头痛，关节疼痛等。<br>\r\n      　　[附注] 本方性凉，为温病初起，炽热病常用良方。本方由查干汤(四味土木香汤)和沙日汤(三籽汤)组成。二者合用能使未成熟热成熟、收敛、解热之功俱全，故称额日敦七味汤，额日敦是宝的译音，临床实践证明，本方治感冒具犹如法宝之力量，故亦称克感灵。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (370, 2, 27, '道古勒・额布斯一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>道古勒・额布斯一7<br>\r\n      <br>\r\n      　　[异名] 七味苦参汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 苦参30g 土木香30g 诃子27g 川楝子27g 黄连15g 水栀子27g 当药24g<br>\r\n      　　[功能与主治] 清热，解表，发疹。用于疫热，感冒，麻疹等。<br>\r\n      　　[附注] 本方性凉，为流感、疫热常用方。方中解表药苦参为主，以除巴达干热、调赫依血相讧药土木香为辅；配以清瘟解热药黄连、地格达及三籽汤。故本方具有清热发汗，使疫热成熟等功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (371, 2, 27, '额日赫木一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　额日赫木一8<br>\r\n      <br>\r\n      　　[异名] 八贵散<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方]石灰华8g 西红花8g 白檀香8g 人工牛黄8g　麦冬8g 胡黄连8g 巴沙嘎8g 地格达8g<br>\r\n      　　[功能与主治]清热，解毒。主治脏腑之热，肺热咳嗽，肝火胁痛，血热目赤，内热疫热等新旧热症。<br>\r\n      　　[附注]本方性凉。为治脏腑热病总方。方中以凉药之首牛黄、白檀香，精药之冠石灰华、红花，汤药之首地格达、巴沙嘎，草药之贵胡黄连、麦冬等八味良药配制而成。故本方在脏腑之热方剂中占首要地位。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (372, 2, 27, '嘎布日一25　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>嘎布日一25　　<br>\r\n      <br>\r\n      　　[异名] 二十五味冰片散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]冰片1.8g 石膏6g 西红花2.7g 肉豆蔻 0.6g 丁香2.4g 白豆蔻5.7g 草果仁5.1g 沉香3g 紫檀香0.9g 白檀香0.9g \r\n      菊花3g 木棉花瓣6g 木棉花萼6.9g 白苣胜2.4g 广本香5.4g 甘草5.4g 甘松4.8g　木通3g 卷柏3.6g 射干6.9g 石花14.4g \r\n      千金子3g　诃子8.4g 栀子5.1g 川楝子4.2g　　　　<br>\r\n      　　[功能与主治] 清瘟，解热。主治炽热，震热，内热，瘟疫，毒热，新旧热，丹毒．痛风，疥癣等。<br>\r\n      　　[附注] 本方性凉，为根治炽热总方。方中以凉药之首炽热良药冰片为主，以木棉花、木通、菊花、甘松、沉香等清热药为辅，配以六味良药(白豆蔻、肉豆蔻、石膏、红花、丁香、草果)利于五脏及主脉；三籽汤(诃子、栀子、川楝子)以清血热；两种檀香以清心肺热共为佐使。故本方具有根治诸热症功效。 \r\n      <p></p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (373, 2, 27, '合日呼一5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>合日呼一5汤<br>\r\n      <br>\r\n      　　[异名] 忠伦阿汤、五味润僵汤。<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 苦参15g 栀子15g 诃子9g 地格达6g 川楝子9g<br>\r\n      　　[功能与主治)清热，凉血，舒经，止痛。用于陈旧热，内热，热性黄水病，痛风，游痛证。<br>\r\n      　　[附注] 本方性凉，为陈旧性热及热性黄水病之主方。方中以苦参为主，以三籽汤为辅，配加地格达。故对陈旧性热病及关节强直均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (374, 2, 27, '噢必德顺一23', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>噢必德顺一23<br>\r\n      <br>\r\n      　　[异名] 满阿各・斯勒召日、秘诀清凉散。<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 寒水石(制)15g 草果仁15g 炉甘石(制)9.3g 菊花7.5g 金诃子7.5g 木鳖子(制)7.5g 全石榴5.4g 广木香4.8g \r\n      水栀子4.8g 人工牛黄4.5g 紫檀香4.2g 白檀香4.2g 止泻木3.9g 白豆蔻3g 石灰华3g 麦冬3 沉香2.4g 肉豆蔻2.1g \r\n      红花2.1g 荜拨2.1g 丁香1.5g 麝香2.1g 地格达1.5g<br>\r\n      　　[功能与主治] 清热，解毒。用于疫热陷脉，肝脾血热，毒热，巴达干包如热等。<br>\r\n      　　[附注] 本方性凉，为巴达干热及根除热病余邪之主方。方中以清巴达干热药寒水石为主；佐以牛黄、菊花、地格达、炉甘石等清热利肝药；红花、紫檀香、栀子等凉血药，石榴、荜拨等祛巴达干药，麦冬、止泻木、木鳖子等清希拉热药，配以调理五脏及主脉的六味良药(石膏、红花、丁香、草果、豆蔻、肉豆蔻)。故本方具备清疫热，毒热及巴达干热之功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (375, 2, 27, '阿日本・呼勒因汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　阿日本・呼勒因汤<br>\r\n      <br>\r\n      　　[异名] 汤钦一10汤、十味大汤。<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 金诃子15g 川楝子15g 水栀子15g 地格达15g 苦参15g 胡黄连15g 拳参10g 连翘10g 木通5g 麦冬10g<br>\r\n      　　[功能与主治] 分离正血与恶血，清除陈旧热。主治陈旧热症。<br>\r\n      　　[附注]本方性凉，为陈旧热良方。此方由合日呼一5汤和苏龙嘎-4汤加胡黄连组成。故对陈旧热症具有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (376, 2, 28, '浩日因汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　浩日因汤<br>\r\n      <br>\r\n      　　[异名] 解毒汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]土茯苓24g 文冠木15g 冬青叶15g 绿豆 15g 五台花9g 北沙参9g 　　黑云香9g 西红花9g　草河车6g 金诃子6g \r\n      甘草6g 黄连3g<br>\r\n      　　[功能与主治] 清热，解毒。用于毒热，伤热，药物毒。<br>\r\n      　　[附注]本方性凉，为解毒专方。方中以解毒药土茯苓为主，以解毒食物绿豆为辅；诃子、甘草、五台花、草河车以清热解毒，红花、沙参以清肝肺之热，黑云香以杀粘，冬青叶以调理体素，均为佐使，各药合用，清热解毒功效俱全，故对毒热及各种中毒症均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (377, 2, 28, '巴乐古那一7汤　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　巴乐古那一7汤　　<br>\r\n      <br>\r\n      　　[异名] 七味水柏枝汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 水柏枝9g 猪血9g 马先蒿6g　 黄柏皮3g　五灵脂3g 刺玫果3g 小狼毒[制]3g<br>\r\n      　　[功能与主治] 清热，解毒。用于毒热，食物中毒，配制毒，病变毒等。<br>\r\n      　　[附柱] 本方性凉，为解毒常用方。方中以清热、收敛扩散毒热之药水柏枝为主，猪血为辅，配以刺攻果、黄柏皮、马先蒿等以收敛扩散之毒，小狼毒以泻毒，五灵脂调理体素，共为佐使，各药合用，解毒之功效俱全。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (378, 2, 28, '土茯苓一14汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>土茯苓一14汤<br>\r\n      <br>\r\n      　　[异名] 十四味土茯苓汤<br>\r\n      　　[来源] 《珊瑚验方集》<br>\r\n      　　[处方]土茯苓180g 金银花6g 紫草茸3g 茜草3g 枇杷叶3g 草乌叶３g 巴沙嘎3g 诃子3g 白云香3g 栀子3g 苘麻子3g 黑云香3g \r\n      西红花3g 文冠本膏3g<br>\r\n      　　[功能与主治]解热，疗疮，消肿，用于梅毒，男女生殖器糜烂、流脓、肿胀，痔疮等。<br>\r\n      　　[附注]本方性凉，为性病专方。方中以清热解毒药土茯苓为主，配以金银花清热，草乌叶、黑云香以杀粘，文冠木、白云香、苘麻子以燥黄水、三红药(枇杷叶、茜草、紫草茸)、红花、栀子、巴沙嘎以凉血，诃子以解毒并调理体素共为辅佐。各药合用，清热解毒、疗疮消肿功能具全，故对梅毒、淋病有奇效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (379, 2, 28, '汉毕勒一7汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>汉毕勒一7汤<br>\r\n      <br>\r\n      　　[异名] 高恩套格一7汤、七味葶苈子汤。<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方] 葶苈子10g 水柏枝10g 苦苣苔10g 金诃子10g 野豌豆10g 黄蓍10g 沙芥10g<br>\r\n      　　[功能与主治] 解毒。用于毒热，肉毒，食物中毒。<br>\r\n      　　[附注] 本方性凉，为解毒良方。方中七味药皆有解毒功效。故对毒热症，食物中毒等均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (380, 2, 28, '给旺一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　给旺一15<br>\r\n      <br>\r\n      　　[异名] 十五味牛黄散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 人工牛黄15g 水菖蒲9g 白云香9g 止泻木9g 金诃子9g 苘麻子9g 绿绒蒿9g 公丁香6g 五灵脂6g 西红花3g 石灰华3g \r\n      硼砂(制)3g 关白附3g 黄连3g 熊胆3g<br>\r\n      　　[功能与主治] 清热，解毒。主治病变毒。<br>\r\n      　　[附注] 本方性凉，为病变毒之专方。方中以清热解毒良药牛黄为主，(原方为牛黄，本方量较大，因此用人工牛黄)；配诃子以解毒、调理体素，白云香、丁香以解毒，关白附以解实毒，黄连、菖蒲以清粘热，石灰华以清肺热，绿绒蒿以清肝热，红花以清血热，熊胆及止泻木以清希拉热，共为辅佐。各药合用，清热解毒功效具备，故本方对病变毒具有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (381, 2, 29, '伊赫汤　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　伊赫汤　　<br>\r\n      <br>\r\n      　　[异名] 大剂汤<br>\r\n      　　[来源] 《内蒙古中蒙医医院验方》<br>\r\n      　　[处方] 西红花25g 猪血干25g 金诃子15g 川楝子15g 水栀子15g 土木香12.5g 川木香12.5g 全石榴10g　 角茴香10g \r\n      地格达10g 芫荽子10g 野菊花10g　　　　　　蓝盆花10g 香青兰l0g 胡黄连10g 酸梨干log 秦艽花10g 款冬花10g 五灵脂l0g \r\n      木鳖子(制)l0g 益智仁l0g 贯众1010g 麦冬10g 巴沙嘎10g 细辛10g<br>\r\n      <br>\r\n      　　[功能与主治] 调解寒热，收敛，解毒，开胃。用于包如扩散，赫依、希拉、巴达干三者失调，身倦体怠，食欲不振、胃脘疼痛，陈旧热，毒热等症。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (382, 2, 29, '壮西一21　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　壮西一21　　<br>\r\n      <br>\r\n      　　[异名] 二十一味寒水石散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 寒水石(煅)30g 全石榴30g 沙棘30g 五灵脂30g　 酸梨干24g 广木香24g 紫檀香24g 水栀子21g　金诃子21g \r\n      麦冬21g 人工牛黄15g 地格达15g　　砂仁15g 木鳖子(制)15g 蓝盆花12g 巴沙嘎12g 芫荽子12g　 香青兰9g 连翘9g 土木香9g \r\n      荜拨15g <br>\r\n      　　[功能与主治] 祛包如，止吐。用于包如病初、中期嗳气吞酸，胸背作痛，气滞血瘀，血热陷中，包如扩散、潜伏及紊乱等症。<br>\r\n      　　[附注] 本方性干，为包如病总方。方中寒水石清巴达干热，助消化，为主药；配合石榴、沙棘、砂仁、荜拨等温性药以祛巴达干赫依；牛黄、地格达、香青兰、连翘、麦冬、蓝盆花等凉性药以清希拉热；紫檀香、栀子、巴沙嘎等凉性药以清热凉血；土木香、广木香等以调节赫依血相搏；诃子、五灵脂以调理体素。诸药合用，对聚合症、包如病诸症，均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (383, 2, 29, '给旺一13　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　给旺一13　　<br>\r\n      <br>\r\n      　　[异名] 十三味牛黄散<br>\r\n      　　[来源] 《兰塔布》<br>\r\n      　　[处方]牛黄15g 红花9g 菊花9g 木通9g 地格达9g 五灵脂9g 土木香9g 广木香9g 芫荽子9g 香青兰 9g 巴沙嘎9g 沙棘9g \r\n      木鳖子(制)9g<br>\r\n      　　[功能与主治] 清热，凉血。用于肝包如，胃肠包如,包如血热扩散症。 <br>\r\n      　　[附注] 本方性凉，为肝包如病专用方；本方由给旺-9味上加包如三药(沙棘、土木香、芫荽子)及香青兰所配成，给旺一9是专治肝热的有效良方，再加包如三味药合用 \r\n      ,对包如血热增盛者,均有功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (384, 2, 29, '如达-6　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　如达-6　　<br>\r\n      <br>\r\n      　　[异名] 六味木香散<br>\r\n      　　[来源] 《蒙医药选编》 <br>\r\n      　　[处方] 广木香7.5g 水栀子4.5g 全石榴3g 闹羊花3g 荜拨2.1g 砂仁2.1g<br>\r\n      　　[功能与主治] 调节赫依血相搏，止吐，解痉,用于巴达干、包如引起的呃逆，呕吐，胃痉挛。<br>\r\n      　　[附注] 本方性温，为包如寒热兼杂期及痧症主方。方中以广木香为主药，栀子以清巴达干希拉及血热，为辅药；配石榴祛巴达干,砂仁镇赫依，阐羊花以止痛、荜拨以祛寒止吐，故本方对痧症，胃疼均有疗效。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (385, 2, 29, '壮西一20　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>壮西一20　<br>\r\n      　　<br>\r\n      　　[异名] 寒水石凉剂<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 寒水石(煅)30g 广木香l8g 人工牛黄15g　 金诃子15g 石灰华12g 公丁香12g 肉豆蔻12g　白豆蔻12g 西红花12g \r\n      草果仁l2g 白檀香9g 紫檀香9g 地格达9g 　巴沙嘎9g 麦冬9g 连翘9g 蓝盆花6g　荜拨6g 　木鳖子6g 麝香0.9g<br>\r\n      　　[功能与主治] 清陈热，潜伏热，解毒。用于包如病，包如热，毒热，陈旧热，潜伏热。<br>\r\n      　　[附住] 本方性凉，为聚合性包如热潜伏专用方。方中寒水石以清除巴达干热，为主药；配合牛黄、红花、檀香、地格达等凉性清热，解毒，凉血药及荜拔、豆蔻、丁香、草果等温性祛巴达干赫依药；金诃子以调理体素并解毒；广木香以调节赫依血相搏,麝香以清粘热；麦冬以清毒热；诸药合用，对毒热,隐热，陈热及包如热均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (386, 2, 29, '查森多斯勒一16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　查森多斯勒一16<br>\r\n      <br>\r\n      　　[异名] 岗替格一16、十六味炉甘石散<br>\r\n      　　[来源] 《诃黎勒串珠》<br>\r\n      　　[处方] 炉甘石27g 金诃子24g 五灵脂21g 人工牛黄18g 紫檀香15g 猪骨炭12g 牛胆膏9g 姜黄6g 蒲公英3g 黑冰片3g \r\n      石膏3g 西红花3g 公丁香3g 肉豆蔻3g 白豆蔻3g 草果仁3g<br>\r\n      　　[功能与主治] 祛聚合症，调理体素。用于聚合病， 包如病。<br>\r\n      　　[附注] 本方性凉，为聚合症专方。方中炉甘石以清 肝热，为主药；配合六味良药(石膏，红花、丁香、豆蔻、肉 豆蔻、草果)，能除五脏诸症；诃子、五灵脂以调理体素， \r\n      牛黄以清热解毒，熊胆以锁脉止血、清希拉热，各药合 用，对聚合症、包如病均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (387, 2, 29, '沃优乌日勒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>沃优乌日勒<br>\r\n      <br>\r\n      　　[异名] 优日勒一13。<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 广木香7.5g 全石榴 3g 水栀子3g 蓝盆花3g 雕粪(煅)3g 蔓荆子3g 巴沙嘎3g 丹参3g 香青兰3g 干姜2.1g \r\n      荜拨2.1g 白豆蔻2.1g 芫荽子2.1g<br>\r\n      　　[功能与主治] 消食，健胃，止痛。用于包如病，胃肠痧症，呕吐，腹泻，消化不良，虫疾， 血瘀症。<br>\r\n      　　[附注] 本方性平，为包如病总方并包如寒热兼杂 期常用方。本方以六味木香散为主，加干姜、雕粪、 蔓荆子、芫荽子等所组成。六味木香散主要治包如病 \r\n      寒热兼杂期所出现的症状，干姜温胃祛寒，雕粪破痞 助消化，蔓荆子驱虫，芫荽子清巴达干热，各药方配 用，故对包如寒热兼杂期，疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (388, 2, 30, '塔斯音-10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>塔斯音-10<br>\r\n      <br>\r\n      　　[异名] 十味雕粪散<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方]雕粪炭10g 万年灰(制)10g 全石榴10g　寒水石（煅）10g 荜拨l0g 干姜l0g 砂仁8g 光明盐8g　肉桂2g 透骨草8g<br>\r\n      　　[功能与主治]温胃消积，破痞。用于胃火衰弱,消化不良，胃寒食积。<br>\r\n      　　[附注]本方性热，为消化不良，胃寒症的总方。方中以助消化，破痞消积药雕粪为主，以破痞消积,祛巴达干药万年灰为辅；配以寒水石以消积，清巴达干热，肉桂、石榴、干姜等热性祛寒药，同为佐使。对消化不良胃巴达干寒症有显著疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (389, 2, 30, '哈日嘎布日-11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>哈日嘎布日-11<br>\r\n      <br>\r\n      　　[异名] 十一味黑冰片散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]黑冰片9g 贝齿灰9g 沙棘膏9g 玫瑰花 9g 硼砂(制)6g 草河车3g 胡黄连3g 连翘3g 麦冬3g 熊胆3g 木通3g<br>\r\n      　　[功能与主治]清血希拉热,破痞,用于血痞,希拉痞,子宫痞等。<br>\r\n      　　[附注]本方性凉，为胃、大小肠等腑器希拉性痞及子宫血痞的主方。方中以清希拉，消积破痞药黑冰片为主；以破痞药贝齿灰、化瘀消化药沙棘及收敛扩散之希拉药玫瑰花为辅；配以麦冬、连翘、木通、草河车等四味，清腑热止泻，均为佐使，胡黄连以清血热，熊胆以锁脉止血，故本方具备了清热破痞之功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (390, 2, 30, '火轮丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　嘎勒呼日德 火轮丸<br>\r\n      <br>\r\n      　　[异名] 十一味黑冰片散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方]万年灰(制)30g 寒水石(煅)18g 贝齿灰9g 海螺炭9g 鱼骨、水獭骨、狼骨各9g，以上七味共混合，煅成炭剂；金诃子9g 草乌(制)9g \r\n      狼毒(制)9g 亚大黄9g 鹫食道9g 雕食道9g 毛茛9g 铁线莲9g 石龙芮9g 干姜9g 荜拨9g 古月9g 火硝9g 硫黄9g 蓖麻子9g \r\n      以上十五味，共研细末，用黄油或骨髓调合后制成灰剂。<br>\r\n      　　[功能与主治]消积，破痞。主治寒性痞证，消化不良症。<br>\r\n      　　[附注]本方性热，为寒性痞主方。方中炭剂及灰剂，属破痞常用剂型，加五热药(干姜、荜拨、古月、铁线莲、辣椒)以调养胃火，开胃；五种盐(白硇砂、紫硇砂、光明盐、卤盐，黑盐)，以温胃，消食，破痞，故本方对寒性痞类，积食，均有良效。 \r\n      <p></p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (391, 2, 30, '伊布海一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　伊布海一10<br>\r\n      <br>\r\n      　　[异名] 十一味黑冰片散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[异名] 软塔拉一10、十味贝齿散。<br>\r\n      　　[处方] 贝齿灰9g 寒水石(煅)9g 五灵脂9g 广木香6g 金诃子6g 地格达3g 木鳖子3g 胡黄连3g 西红花3g 全石榴3g<br>\r\n      　　[功能与主治] 破痞消积。用于食痞，胸口痞，血痞，希拉痞等各种痞证。<br>\r\n      　　[附注] 本方性凉，为痞证总方。方中地格达，为蒙药译名，根据蒙医用药情况，本方使用梅花草为希拉痞药。伊布海一10，乃治寒热性痞常用方，如治热性希拉痞可加黑冰片9g，治血痞可加沙棘、朴硝各6g，则疗效更佳。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (392, 2, 30, '郭日本・浩毕图・乌日勒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>郭日本・浩毕图・乌日勒<br>\r\n      <br>\r\n      　　[异名] 囤苏木・日力布、三剂丸。<br>\r\n      　　[来源]《蒙医药选编》 <br>\r\n      　　[处方] 白硇砂30g 紫硇砂30g 光明盐30g 黑盐30g 卤盐30g 角盐30g 灰盐30g 朴硝30g 肉桂30g(以上九味蒙医传统习称为九盐)，干姜90g \r\n      荜拨90g 古月90g (以上三味蒙医习称为三热药) 金诃子90g 川楝子90g 水栀子90g(以上三味蒙医习称为三籽) 红糖20g<br>\r\n      　　[功能与主治] 破瘀化滞，软坚消积。用于胸口痞，子宫痞，血痞。<br>\r\n      　　[附注] 本方性温，为破痞常用方。本方由九盐1分，三热药1分，三籽1分，三种方剂，配合组成。九盐主要以软坚化瘀，破痞，消食；三热药主要以调胃火、祛巴达干赫依；三籽主要以凉血。三方合用，调胃火祛寒，化瘀消积之功具全，故本方对寒性痞，特别是血痞及子宫痞，均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (393, 2, 30, '壮西因・乌讷顺额莫', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>壮西因・乌讷顺额莫<br>\r\n      <br>\r\n      　　[异名] 壮西・普如塔拉、寒水石灰剂。<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方] 寒水石(煅)500g 光明盐100g 金诃子100g 硼砂l00g 荜拨l00g 麦冬l00g 硫黄(制)50g<br>\r\n      　　[功能与主治] 消食化瘀。用于食积不消，胃溃疡，胃肠痞等。<br>\r\n      　　[附注] 本方性温，为寒性痞证，特别是治胃肠痞证常用方。方中以消食，破痞，祛巴达干热药寒水石为主；配光明盐以消食，硼砂以化痞，麦冬以清希拉、解毒，荜拨以温胃、祛巴达干，诃子以调理体素，各药合用，对胃肠痞证均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (394, 2, 31, '特木仁・塔拉哈-10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　特木仁・塔拉哈-10 <br>\r\n      <br>\r\n      　　[异名] 十味铁粉散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 铁屑(制)45g 金诃子5g 川楝子5g 水栀子5g 黑胡椒5g 干姜5g 荜拨5g 草河车5g 蔓荆子5g 辨椒5g<br>\r\n      　　[功能与主治] 消肿。主治浮肿。<br>\r\n      　　[附注]本方性平，为浮肿病主方。方中以消肿药铁屑为主，配合三热药及辣椒以祛寒，助消化，川楝子以燥黄水，栀子以凉血，各药合用，对浮肿有明显疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (395, 2, 31, '哈崩因一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>哈崩因一7 <br>\r\n      <br>\r\n      　　[异名] 七味红花消肿散<br>\r\n      　　[来源] 《兰塔布》<br>\r\n      　　[处方] 西红花2.4g 香青兰4.8g 水栀子5.7g 蓝盆花3.9g 木鳖子(制)2.4g 麦冬4.2g 巴沙嘎1.5g<br>\r\n      　　[功能与主治] 清热，消肿。用于热性水肿，合并水肿。<br>\r\n      　　[附注] 本方性凉，为热性水肿之主方。方中西红花以清血热，香青兰、蓝盆花以清肝热，水栀子以分离病血与正血，麦冬、木鳖子以清希拉热。故本方凉血，清肝之功俱全，对肝硬化腹水均有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (396, 2, 31, '哈日阿布日一16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td><br>\r\n      　　哈日阿布日一16<br>\r\n      <br>\r\n      　　[异名]达力一16、十六味满山红散、达力朱仁<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方] 杜鹃叶60g 全石榴40g 广酸枣60g 石灰华58g 公丁香50g 山沉香50g 西红花50g 葡萄干50g<br>\r\n      广木香40g 肉豆蔻40g 草河车48g 甘草33g 方海20g<br>\r\n      荜拨10g 肉桂5g 白豆蔻5g<br>\r\n      　　[功能与主治] 理气，止咳，化痰，消肿。用于浮肿，肺气肿，头昏眩晕，咳嗽音哑，胸满腹胀，消化不良等。<br>\r\n      　　[附注] 本方性温，为气喘及浮肿常用方。方中以止咳祛痰药杜鹃叶为主；配以通拉嘎一5，能调养胃火，温通脉道，甘草、石灰华、草河车、葡萄干以止咳清肺，止喘；肉豆蔻、广酸枣以强心；方海以利尿消肿，为之佐使。故本方健胃消食，清肺安心，利尿消肿之功具全。对各种水肿、气短咳嗽均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (397, 2, 31, '乌珠莫--11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　乌珠莫--11\r\n<br>\r\n      　<br>\r\n      　　[异名] 贡布如木一11、十一味葡萄散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 白葡萄干19g 全石榴25g 石灰华15g 西红花15g 海金沙15g 香附子15g 方海15g 甘草25g 冬葵果5sg 铁屑(制)7.5g \r\n      肉桂5.5g<br>\r\n      　　[功能与主治] 利尿，消肿。用于全身性浮肿，特别是肺水肿。<br>\r\n      　　[附注]本方性凉，为全身性浮肿及肺水肿常用方。方中以清肺止咳药白葡萄干为主；配以石榴、肉桂以补养胃火，助消化；铁屑、方海、海金沙、冬葵果以利水消肿；甘草、石灰华、香附子以清肺热，红花以清肝热，为佐使。本方主要以益肺方七味葡萄散加利水消肿药组成，故对肺水肿有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (398, 2, 31, '沙查古一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>沙查古一15<br>\r\n      <br>\r\n      　　[异名] 尼亚木召日一15、十五味等分散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 白硇砂5g 海金沙5g 方海5g 冬葵果5g 芫荽子5g 蒺藜5g 芒果核5g 建莲子5g 栀子5g 石莲子5g 全石榴5g 肉桂5g \r\n      白豆蔻5g 西红花5g 荜拨5g<br>\r\n      　　[功能与主治] 利尿，消肿。主治寒热合并水肿。<br>\r\n      　　[附注]本方性平，为寒热合并水肿常用专方。方中配有通拉嘎一5，故有温养胃火，疏通脉道，健脾燥湿之效；配以利尿三强药(白硇砂、海金沙、方海)，冬葵果、蒺藜以消肿，下三籽(芒果核、建莲子、石莲子)以温肾补养，为之佐使。故本方具备了以利尿消肿，温胃补肾功效，对全身浮肿，特别是寒热合并性水肿，疗效尤佳。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (399, 2, 31, '朱如拉一6汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　朱如拉一6汤<br>\r\n      <br>\r\n      　　[异名] 六味栀子汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 水栀子9g 冬葵果9g 水金凤9g 甘草9g 芫荽子9g 宽苞棘豆9g <br>\r\n      　　[功能与主治] 消肿。主治热性水肿。<br>\r\n      　　[附注] 本方性凉，为热性水肿之专方。方中以清血热药栀子为主，配以止咳解毒药甘草，利尿消肿药冬葵果、宽苞棘豆及水金风，芫荽子以清巴达干热，为之佐使。故本方具备清热解毒，利水消肿之功，对一切热性水肿均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (400, 2, 32, '玛努一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　玛努一10 <br>\r\n      <br>\r\n      　　[异名] 十味土木香汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 金诃子9g 木鳖子(制)6g 地格达9g 玫瑰花6g 龙骨(制)6g 土木香3g 珍珠干3g 苦参3g 山柰3g 天灵盖(煅)6g<br>\r\n      　　[功能与主治]清热、镇痛。用于赫依希拉引起的各种头痛。<br>\r\n      　　[附注] 本方性凉，由四味土木香汤、三味天灵盖汤、三味木鳖子散等三种方配合而成，为治赫依希拉性头痛的主方，方中三味天灵盖汤专治头痛、三味木鳖子散清希拉，四味土木香汤清赫依热。三方配用对希拉性头痛均有功效。 \r\n      <p></p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (401, 2, 32, '噢必德顺・古日古木', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>噢必德顺・古日古木 <br>\r\n      \r\n      　　[异名] 十三味红花秘诀丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]西红花9g 旋复花9g 商陆(制)9g 石花9g 麦冬12g 木鳖子(制)9g 草乌叶18g 炉甘石{制} 18g 地格达6g 山茶花6g \r\n      龙骨(煅)3g 绵羊颅骨 (煅)3g 熊胆l.5g<br>\r\n      　　[功能与主治] 清热,止痛,杀粘，用于血热头痛,正偏头痛，胸部刺痛,牙痛,亚玛病。<br>\r\n      　　[附注]本方性凉，为专治血热性头痛主方，方中以凉血良药红花为主，配合三味天灵盖汤(原方为天灵盖，根据蒙医用药情况，现以绵羊颅骨代用)；草乌叶、商陆、以杀粘虫，止痛；麦冬、山茶花、木鳖子、熊胆以清希拉热；炉甘石、石花以清肝热；旋复花以止刺痛。各药配用，对血热、希拉性头痛及亚玛病均有特效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (402, 2, 32, '查格德日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　查格德日 <br>\r\n      <br>\r\n      　　[异名] 加味十三味红花散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 绵羊颅骨6g 草乌芽4g 商陆(制)6g 龙骨 (制)3g 西红花3g 地格达3g 山茶花3g 通经草3g 石花(制)3g 旋复花3g \r\n      炉甘石(制)3g 石菖蒲3g 黑云香3g 木鳖子(制)3g 草决明3g 白云香3g 文冠木膏3g 苘麻子3g 翻白草3g 水银(制)3g 硫黄(制)3g麦冬3g \r\n      熊胆粉1.5g 麝香0.9g<br>\r\n      　　[功能与主治] 杀帖，凉血，燥黄水，止痛，用于血热头痛，脑刺痛，吾亚曼病，亚玛病。萨病，白脉病，黄水病。<br>\r\n      　　[附注] 本方性凉，为亚玛病，血热头痛的主方。本方由十三味红花秘诀丸加黄水病诸味药和杀粘药所配合组成。方中绵羊颅骨、原方为天灵盖，方中十三味红花秘诀丸专治血热头痛方；三味天灵盖，为头痛病总方；加祛黄水三药(即白云香、草决明、苘麻子)和燥黄水药文冠木膏、水银、硫磺、菖蒲等，对痛风，游痛证，巴木病，风湿病及皮肤病有较好的治疗作用；再加麝香、黑云香等杀粘、止痛药，故本方对脑刺痛、三种亚玛病，脑虫病均奏效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (403, 2, 32, '嘎波拉一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎波拉一3汤<br>\r\n      <br>\r\n      　　[异名] 陶德巴一3汤、三味天灵盖汤<br>\r\n      　　[来源] 《蒙药传统验方》<br>\r\n      　　[处方] 天灵盖(煅)50g 地格达45g 龙骨(制)40g <br>\r\n      　　[功能与主治] 清热，止痛。用于头痛，脑刺痛。<br>\r\n      　　[附注] 本方性凉，为头痛病总方。方中以头骨为主，专治头痛；龙骨以杀粘、清热、止痛；地格达(蒙医用扁蕾或紫花地丁)以清希拉热。三药合用，对各种头痛病，特别是血、希拉性头痛均有效果。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (404, 2, 32, '胡日查一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　胡日查一6<br>\r\n      <br>\r\n      　　[异名] 闹朝格一6、六锐丸<br>\r\n      　　[来源] <br>\r\n      　　[处方] 西红花9g 金诃子9g 闹羊花9g 广木香4.5g 黑云香4.5g 麝香4.5g<br>\r\n      　　[功能与主治]杀粘，祛翳，止痛。用于血、希拉、粘、亚玛等引起的偏正头痛，脑刺痛，翳障沙眼，目赤红肿等。<br>\r\n      　　[附注] 本方性凉，为专治头痛方。方中红花为凉血、利肝良药，闹羊花以止痛，诃子以调元、解毒，三药为主；木香以止痛、调理赫依血紊乱，麝香和黑云香以杀粘、止痛，三药为辅。六药合用，对粘与血引起的头痛，均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (405, 2, 32, '那仁・萨仁・奈日拉嘎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　<br>\r\n      　　那仁・萨仁・奈日拉嘎<br>\r\n      <br>\r\n      　　[异名] 尼达哈召日、日月丸<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方] 草乌(制)l0g 葶苈子10g 草乌花l0g 西红花10g 乳香10g 松香10g 麦冬10g<br>\r\n      　　[功能与主治] 杀粘虫，止痛。用于粘及亚玛虫引起的头痛，牙痛。<br>\r\n      　　[附注] 本方性凉，为亚玛病主方。方中草乌、草乌花以杀粘虫、止痛，乳香、松香以止痛，红花以凉血、消肿，葶苈子以清热、解毒，麦冬以清希拉热。各药等量合用，可奏杀粘、止痛之功，对亚玛引起头痛、牙痛疗效显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (406, 2, 32, '布很讷・托斯拉嘎其', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　布很讷・托斯拉嘎其<br>\r\n      <br>\r\n      　　[异名] 盘巴公丹、万益丸<br>\r\n      　　[来源] 《兰塔布》<br>\r\n      　　[处方] 商陆浸膏15g 蔓荆子12g 青羊血10.5g 天灵盖9g 龙骨(制)9g 地格达9g 紫草茸9g 茜草9g<br>\r\n      　　[功能与主治] 杀粘，止痛。用于血希拉引起头痛，亚玛引起偏头痛，脑刺痛，白脉病等。<br>\r\n      　　[附注] 本方性凉，为脑虫病主方。方中商陆浸膏以杀粘虫、止痛，为主药；以杀虫药蔓荆子为辅药；配青羊血以清热解毒，三味天灵盖汤专治头痛，茜草、紫草茸属清血热药，故对血热及亚玛虫引起的头痛症，均有良效。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (407, 2, 32, '昭日格德斯--5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　昭日格德斯--5汤 <br>\r\n      <br>\r\n      　　[异名] 五味茴香汤<br>\r\n      　　[来源] 《蒙医药方汇编》<br>\r\n      　　[处方] 小茴香10g 广木香10g 公丁香10g 草果仁10g 肉豆蔻10g<br>\r\n      　　[功能与主治] 祛巴达千赫依，止痛。用于巴达干赫依性头痛，头重，胃痛，呃逆等。<br>\r\n      　　[附注] 本方性温，为巴达干赫依性头痛专方。方中小茴香性温，镇赫依，解毒，开胃；广木香性温，调理体素，止痛；草果仁、丁香、肉豆蔻皆能祛巴达干，镇赫依，助消化，五药合用，具备开胃，祛巴达干赫依，止痛之功。故本方对巴达干赫依性头痛具有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (408, 2, 32, '乌莫黑・达布日海一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　乌莫黑・达布日海一5<br>\r\n      <br>\r\n      　　[异名] 兴棍阿瓦、五味阿魏散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 阿魏4.5g 干姜3g 黑云香3g 肉豆蔻6g 光明盐218<br>\r\n      　　[功能与主治] 镇赫依，止痛。用于赫依性头痛，头昏，头重，呃逆等。<br>\r\n      　　[附注] 本方性热，为赫依性头痛专方。方中阿魏性热，镇赫依，杀虫、止痛，为主；肉豆蔻以镇赫依，黑云香以杀粘、止痛，为辅；配合光明盐、干姜以祛巴达干赫依，加强镇赫依、止痛之效，故对赫依性头痛具有良效。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (409, 2, 33, '陶都日哈奇--16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　陶都日哈奇--16 <br>\r\n      <br>\r\n      　　[异名] 十六味明目丸<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]枸杞子9g 通经草3g 麦冬3g 石决明3g 夜明砂3g 当归3g 草决明3g 西红花3g 木贼3g 菊花3g 蒺藜3g 全蝎3g \r\n      青葙子1.5g 川楝子1g 防风1.5g 羚羊角lg<br>\r\n      　　[功能与主治] 清血热，明目。用于视力减退，青盲，夜盲，目赤眼花等诸眼病。<br>\r\n      　　[附注] 本方性平，为增强视力专方。方中枸杞子性平以散恶血为主，通经草以明目，石决明以治疗、退云翳，草决明以生津、滋补，当归以活血，红花以凉血，木贼、全蝎以明目，菊花以清肝明目。共为辅药佐，故对目赤眼花，视力减退均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (410, 2, 33, '陶都日哈奇一25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　陶都日哈奇一25 <br>\r\n      <br>\r\n      　　[异名] 二十五味明目丸 <br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 枸杞子4g 五味子4g 玉竹4g 茺蔚子4g 菟丝子4g 麦冬4g 草决明3g 石决明3g 茜草3g 川楝子3g 青葙子3g 当归3g \r\n      土茯苓3g 野菊花3g 怀牛膝3g 车前子3g 密蒙花3g 人参3g 蒺藜3g 西红花2g 麝香0.5g 田三七0.5g 羚羊角1g 牛黄0.2g \r\n      珍珠0.2g <br>\r\n      　　[功能与主治] 清热凉血，去翳明目。用于目赤眼花，眼干，白翳，云翳，白内障，青光眼，视力减退，眼底病变等眼睛诸病。<br>\r\n      　　[附注] 本方性凉、为老年性眼病专方。方中枸杞子性平以散恶血，茺蔚子治目赤肿痛，菟丝子以补肝、肾、明目，五味子、玉竹均滋补壮阳，共为主药；石决明以退翳，草决明以生津滋补，青葙子治目赤、障翳，密蒙花以润肝，明目，蒺藜、车前子以利尿，川楝子以祛巴达干希拉、明目，野菊花以清热解毒，共为辅佐；配合红花、茜草以清血热，人参以滋补壮阳，当归以火血，羚羊角、牛黄以清热解毒，珍珠以清脉热，麝香以杀粘止痛。各药合用，清热凉血，去翳明目功效，故本方对白内障，青光眼，视力减退均有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (411, 2, 33, '特木仁一5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　特木仁一5汤<br>\r\n      <br>\r\n      　　[异名] 特木仁・塔拉哈一5汤、扎克策阿汤、五味铁粉汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 铁屑(制)150g 金诃子150g 栀子150g 黄柏皮l00g 川楝子50g<br>\r\n      　　[功能与主治] 清热，明目。用于肝热、血热引起的目疾。<br>\r\n      　　[附注] 本方性凉，为血，希拉性目疾主方。方中 铁屑以清肝，明目，为主，黄柏皮以清热，明目，为<br>\r\n      辅；配以栀子、楝子、诃子等三籽汤以清血热，故对<br>\r\n      肝火引起的目赤、血希拉性目疾均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (412, 2, 33, '萨仁汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　萨仁汤<br>\r\n      <br>\r\n      　　[异名] 达顺一6汤、月明汤、尼敦汤<br>\r\n      　　[来源] 《珊瑚验方集》<br>\r\n      　　[处方] 西青果10g 川楝子10g 栀子10g 黄柏皮 10g 广木香10g 苏木10g <br>\r\n      　　[功能与主治] 清热凉血，明目。用于眼红肿痛 ，血希拉性目疾。<br>\r\n      　　[附注] 本方性凉，为血、希拉性目疾专方。方中黄柏皮为清热解毒，明目；栀子凉血，明目；苏木以清血热；西青果以消肿，明目；广木香以防溃烂、止痛。六药合用，对血热引起的眼红肿痛及结膜炎等均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (413, 2, 33, '吉斯・额布斯一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉斯・额布斯一15<br>\r\n      <br>\r\n      　　[异名] 阿哇壮阿、十五味通经草丸<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 通经草3g 金钱蛇(制)3g 金诃子3g 水栀子3g 川楝子3g 公丁香3g 石膏3g 甘草3g 西红花3g 小茴香6g 铁屑(制)9g \r\n      五灵脂9g 代赭石(制)9g 寒水石(煅)12g 蓝盆花1.5g<br>\r\n      　　[功能与主治] 清热，明目。用于视力减退，夜盲，青盲，云翳，眼红肿、结膜溃烂，血热引起的各种目疾。<br>\r\n      　　[附注] 本方性凉，为明目之总方。方中以明目通经草为主；红花、栀子、五灵脂、蓝盆花以清肝，共为辅；配以金钱蛇、铁屑、小茴香以明目，甘草以清脉热，寒水石以滋补，诃子以调理体素。各药合用，利肝，明目，滋补，调理体素功能具全。对视力减退等各种眼病均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (414, 2, 33, '奇其给一4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　奇其给一4<br>\r\n      <br>\r\n      　　[异名] 扎拉目一4、四味利瞳散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 西红花3g 麝香3g 黄柏浸膏3g 冰糖3g<br>\r\n      　　[功能与主治] 明目，杀粘。用于血热引起眼红肿，多眵，结膜炎，角膜炎等。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (415, 2, 33, '珠古一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　珠古一7<br>\r\n      <br>\r\n      　　[异名] 卡布苏一7、七针液<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方] 胆矾9g 白矾9g 硼砂9g 海螵蛸9g 沙棘膏9g 铁针7枚 杏仁7枚 <br>\r\n      　　[功能与主治]去翳明目。用于胬肉攀睛，眼红肿，障翳，沙眼流泪等。<br>\r\n      　　[附注] 本方性凉，为去翳专用方。方中针以明目、去翳、清肝热，为主药；配合胆矾、白矾以止腐，去翳，硼砂以活血疗伤，海螵蛸以疗伤止血，沙棘膏以化瘀活血，杏仁以清热、止腐。各药合用，具备了活血化瘀，止腐，去翳等功效，故对翳障，胬肉，目赤肿痛，沙眼，视力模糊等各种目疾均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (416, 2, 34, '乌兰・托鲁麻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　乌兰・托鲁麻<br>\r\n      <br>\r\n      　　[异名] 乌格曲一6、六味角蒿散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 角蒿15g 独头蒜12g 麝香9g 铜灰9g 萝卜干3g 广木香9g<br>\r\n      　　[功能与主治] 燥脓，止痛。用于耳内化脓，中耳炎。<br>\r\n      　　[附注] 本方性凉，为耳脓常用方。方中角蒿味苦性凉、燥黄水，止痛，为主药；铜灰燥脓，燥黄水，为辅药；配合麝香以杀粘，广木香以祛脓，止痛，独头蒜、萝卜干以燥黄水。各药合用，具备了祛脓止痛之功，故对耳内化脓流黄斯πА。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (417, 2, 34, '萝泵-6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　萝泵-6<br>\r\n      <br>\r\n      　　[异名] 辣福格一6、六味莱菔散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 白萝卜干10g 广木香5g 孔雀羽炭5g 角蒿20g 白硇砂2.5g 磁石(制)15g<br>\r\n      　　[功能与主治] 燥脓，止痛。用于耳内化脓，耳刺痛。<br>\r\n      　　[附注]本方性凉，为耳内化脓专用。方中萝卜干以燥黄水，为主药；配合角蒿以燥黄水，止痛，磁石以止痛，广木香以燥脓，止腐，孔雀羽炭以燥脓，白硇砂以止腐。各药合用，对耳流脓血、流黄水，耳痛均有功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (418, 2, 35, '昂给鲁麻-12', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　昂给鲁麻-12 <br>\r\n      <br>\r\n      　　[异名] 十二味芬芳散 <br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 白檀香12g 紫檀香12g 石膏9g 西红花9g 肉豆蔻3g 丁香6g 蔓荆子3g 白豆蔻3g 冰片3g 可瓜子3g 草果仁3g \r\n      麝香1.5g<br>\r\n      　　[功能与主治] 杀粘虫，止痛，调理赫依血相搏。用于赫依血紊乱而引起鼻塞，鼻肿，鼻疮，鼻息肉，鼻渊及粘虫、亚玛虫引起正偏头痛等。<br>\r\n      　　[附注] 本方性凉，为鼻塞，鼻窦炎常用方,方中紫檀香味涩性凉以清血热、消肿,白檀香味涩性寒以清内热，共为主药；配合冰片以清热消肿，麝香以杀粘虫、开窍止痛，可瓜子和蔓荆子以驱虫，加配红花、豆蔻、草果、丁香等六味良药以调理脏腑之寒热，故对粘虫、亚玛虫引起的头痛和赫依血相讧引起的鼻塞、鼻疮、鼻内溃烂等鼻病均有功效。特别方中配檀香、丁香、麝香及冰片等芳香药物，具备了开窍止痛，疏通鼻塞之功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (419, 2, 35, '哈莫仁一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　哈莫仁一4汤<br>\r\n      　<br>\r\n      　　[异名] 塔・都日本汤、止鼻衄汤、赫吉嘎日<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 地锦草15g 紫草15g 秦艽花9g 巴沙嘎12g<br>\r\n      　　[功能与主治] 凉血，止血。用于血热上头，鼻出血。<br>\r\n      　　[附注] 本方性凉，为止鼻出血良方。方中以清血 热、止血药地锦草、紫草为主；配合巴沙嘎以清血热、 止痛；秦艽花以清热、解毒、消肿。四药合用，对血 \r\n      热引起鼻出血均有良效。方中巴沙嘎，蒙药译名，蒙 医使用闹羊花或瞿麦。本方用瞿麦疗效较好。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (420, 2, 35, '霍・塔本塔拉哈', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　霍・塔本塔拉哈<br>\r\n      <br>\r\n      　　[异名] 浪日各一5、五味百草霜散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 百草霜30g 水银(制)30g 白铅(制)15g 黑铅(制)15g 朱砂(制)15g<br>\r\n      　　[功能与主治] 杀粘，疗伤。用于鼻疮，喉疮，黑白亚玛病，鼻痈，鼻塞。<br>\r\n      　　[附注] 本方性凉，为亚玛病，鼻毒疮常用方。方 中百草霜以止血、疗疮，水银以止痒、燥黄水，共为 主药；白铅、黑铅以解毒，朱砂以解热、敛疮，共为 \r\n      辅药；五药合用，对鼻痈、鼻渊，黑白亚玛病及头虫 病均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (421, 2, 36, '高勒图・宝日一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　高勒图・宝日一6 <br>\r\n      <br>\r\n      　　[异名] 六味丁香散<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方] 公丁香25g 甘草15g 玉簪花10g 石膏15g 广木香10g 诃子10g<br>\r\n      　　[功能与主治] 清热,利咽,止咳,主治外感,咳嗽,失音声哑,咽喉肿痛。<br>\r\n      　　[附注] 本方性凉，为喉热咽炎常用良方。方中丁香以利咽、解毒为主药；石膏、甘草以清肺止咳，共为辅药；配合玉簪花以清热止咳，广木香以祛痰、止痛，诃子以解毒、调元。六药合用，对肺热咳嗽及咽炎均有功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (422, 2, 36, '哈日白矾一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　哈日白矾一3汤<br>\r\n      <br>\r\n      　　[异名] 纳格粗日一3汤、三味黑矾汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 黑矾(制)3g 金诃子6g 枇杷叶9g<br>\r\n      　　[功能与主治] 止腐，敛疮。用于口舌及齿龈部溃烂等口腔病。<br>\r\n      　　[附注] 本方性凉，为治口腔溃烂专用方。方中黑矾以止腐，敛疮为主；配合枇杷叶以清热，诃子以解毒。三药合用，对口舌溃烂、龈部肿痛有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (423, 2, 36, '查干白矾一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　查干白矾一6<br>\r\n      <br>\r\n      　　[异名] 粗日嘎日一6、六味白矾散、白矾一6<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 白矾10g 硇砂5g 朱砂5g 胆矾2.5g 姜黄2.5g 麝香2.5g<br>\r\n      　　[功能与主治)愈合疮伤，消肿，止痛。用于口疮，舌肿，舌巴木，白喉，亚玛病。<br>\r\n      　　[附注] 本方性凉，为舌巴木病专用方。方中白矾性寒以清热、止腐、敛疮为主；配合胆矾以杀虫、疗伤，朱砂以清热、解毒，硇砂以止腐，姜黄以止腐、杀粘，麝香以杀粘虫、止痛。六药合用，对舌肿，舌巴木病等口腔疾患均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (424, 2, 36, '哈斯・哈图古日一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　哈斯・哈图古日一15<br>\r\n      <br>\r\n      　　[异名] 帮占壮阿、十五味玉簪花散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 五簪花9g 栀子10.5g 广木香3.6g 金诃子3.6g 巴沙嘎3.9g 北沙参3.3g 广酸枣 3g 苦参3.3g<br>\r\n      肉豆蔻3g 公丁香1.8g 甘草2.4g 川楝子1.8g 白檀香1.5g 沉香2.4g 天竺黄5.4g<br>\r\n      　　[功能与主治] 清巴达干热，止咳，平喘，消肿。用于咽喉肿痛，气喘音哑，胸胁刺痛，肺热咳嗽。<br>\r\n      　　[附注] 本方性凉，为咽喉肿痛良方。方中以清热利咽药玉簪花为主药；以清肺止咳药天竺黄、甘草、北沙参为辅；以降气、平喘药沉香、丁香为佐；并含有三味檀香汤(白檀香、广酸枣、肉豆蔻)，益于心脏；配合巴沙嘎、栀子等凉血药。各药合用，对咽喉肿痛，胸闷气喘均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (425, 2, 36, '查干泵阿一5汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　<br>\r\n      　　查干泵阿一5汤<br>\r\n      <br>\r\n      　　[异名] 泵嘎日阿汤、五味查干泵嘎汤、五味麦冬汤<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 麦冬10g 甘草10g 冰糖10g 玉簪花10g 天竺黄10g<br>\r\n      　　[功能与主治] 清热，止咳，利咽。用于喉热，音哑，喉干，肺热咳嗽。<br>\r\n      　　[附注] 本方性凉，为喉热症常用方。方中麦冬以清热解毒、玉簪花以清热利咽，共为主药；配合甘草、天竺黄、冰糖等，以增强清热止咳作用。各药合用，对喉热症均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (426, 2, 37, '赞丹一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　赞丹一3汤<br>\r\n      <br>\r\n      　　[异名] 赞丹.郭日本汤、三味檀香汤<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方] 白檀香10g 广酸枣10g 肉豆蔻10g<br>\r\n      　　[功能与主治]清热，强心。用于心热，心悸，心慌不安，心前区痛。<br>\r\n      　　[附注] 本方性凉，为清心热之主方。方中以清心热药白檀香为主，配合以清心、强心药广酸枣和益心、补心药肉豆蔻。故对心热引起心跳、心慌、心悸、心绞痛等心脏病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (427, 2, 37, '匝迪一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　匝迪一5 <br>\r\n      <br>\r\n      　　[异名] 五味肉豆蔻丸<br>\r\n      　　[来源] 《蒙医药方汇编》<br>\r\n      　　[处方]肉豆蔻30g 土木香24g 广木香24g 广酸枣15g 荜拨3g<br>\r\n      　　[功能与主治]镇心赫依，调理赫依血相讧。用于心烦失眠，心神不安，胸闷气短，心赫依引起心跳、心慌、心绞痛等。<br>\r\n      　　[附注] 本方性温，为心赫依病常用良方。方中以味辛、性温、治心赫依良药肉豆蔻为主；以调理气血不和、止痛药土木香、广木香共为辅；配以强心药广酸枣和去寒调元药荜拨。故对心赫依诸病均有功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (428, 2, 37, '吉如很・阿嘎如一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　吉如很・阿嘎如一8 <br>\r\n      <br>\r\n      　　[异名] 八味沉香清心散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 沉香15g 广酸枣18g 石膏9g 肉豆蔻12g 广木香12g 木棉花9g 诃子l2g 白云香6g<br>\r\n      　　[功能与主治]清赫依热，强心，安神。主治赫依热攻心，神昏谵语，心肌损伤，心前区疼痛，胸胁刺痛。<br>\r\n      　　[附注]本方性凉，为心赫依热专用方。方中以性凉，清赫依热、止痛、平喘药沉香为主；配以强心药广酸枣为辅；配以理气益心药肉豆蔻，调元药诃子，调节气血不和药广木香，共为佐使；配合以清肺止咳药石膏、清心肺热药木棉花及燥黄水、祛风湿药白云香。各药合用，对赫依热攻心，心悸，心前区疼痛均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (429, 2, 37, '芍沙一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　芍沙一7 <br>\r\n      <br>\r\n      　　[异名] 七味广枣丸<br>\r\n      　　[来源] 《中华人民共和国药典》<br>\r\n      　　[处方]广酸枣450g 沉香75g 肉豆蔻75g 广木香75g 丁香75g 白云香75g 野牛心75g<br>\r\n      　　[功能与主治]养心益气,安神定志,用于胸闷疼痛,心跳气短,心神不安，失眠健忘,心力衰竭，风心病,冠心病。<br>\r\n      　　[附注]本方微温，为养心益气药。方中以强心药广酸枣为主；配以野牛心养心，沉香祛风热、止痛，丁香理气，肉豆蔻安神宁心，广木香调理体素、气血不和,白云香祛风湿，共为佐使。故对心慌、心悸、心衰、心绞痛均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (430, 2, 37, '吉如很・古日古木一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉如很・古日古木一7<br>\r\n      <br>\r\n      　　[异名] 吉如很・哈伦乃・古日古木一7 、七味红花清心散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 西红花6g 石膏6g 广酸枣6g 广木香6g 沉香3g 肉豆蔻3g 人工牛黄3g<br>\r\n      　　[功能与主治] 清心热。用于心热，心悸，心绞痛。<br>\r\n      　　[附注] 本方性凉，为心热症专用方。方中以三臣药(红花、牛黄、石膏)为主；配合沉香以理气、平喘、止痛；广木香以调节气血不和；广枣以强心，肉豆蔻以专治各种心脏病。各药合用，专治轻度心热病。本方加白檀香为主则称为八味檀香清心散，用于中度心热病。再加冰片为主则成为九味冰片清心散，用于重症心热病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (431, 2, 37, '吉如和一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉如和一6<br>\r\n      <br>\r\n      　　[异名] 宝荣宁如克巴、宝荣宁一6、六味牛心丸<br>\r\n      　　[来源] 《查干金巴验方》<br>\r\n      　　[处方] 野牛心5g 肉豆蔻5g 广酸枣5g 公丁香5g 广木香5g 白云香5g <br>\r\n      　　[功能与主治] 理气，强心，镇静。用于心慌身抖，胸闷气喘，心神不安，心力衰竭。<br>\r\n      　　[附注]本方来源于查干金巴验方。查干金巴(1906―1985)系锡林郭勒盟白旗人，著名老蒙医，蒙医理论学家。生前在内蒙古医学院中蒙医系理论基础教研室任教。本方性温，为增强心功能药。临床验证，对心功能衰竭、心慌、胸憋等疗效显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (432, 2, 37, '匝迪一16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　匝迪一16<br>\r\n      <br>\r\n      　　[异名] 十六味肉豆蔻丸、匝迪朱如克<br>\r\n      　　[来源] 《苏荣扎布验方》<br>\r\n      　　[处方] 肉豆蔻30g 广酸枣24g 广木香21g 野牛心21g 沉香21g 西红花15g 公丁香15g 麦冬21g 石灰华12g 紫檀香9g \r\n      人工牛黄12g 白云香9g 紫硇砂9g 栀子9g 白檀香6g 阿魏6g<br>\r\n      　　[功能与主治] 调理赫依血，补心。用于心内伏热，心内赫依血不调，心功能衰竭，心区闷痛等。<br>\r\n      　　[附注] 本方乃苏荣扎布专治心功能衰竭验方。苏荣扎布(1929一 )系锡林郭勒盟镶黄旗人，原内蒙古蒙医学院院长，蒙医内科教授、著名的蒙医学专家。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (433, 2, 38, '扫日劳-4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　扫日劳-4 <br>\r\n      <br>\r\n      　　[异名] 四味沙参汤<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]北沙参15g 紫草茸9g 甘草9g， 拳参9g<br>\r\n      　　[功能与主治]清热，止咳，社痰，用于感冒咳嗽,肺热多痰，胸背刺痛。<br>\r\n      　　[附注]本方性凉，为清肺止咳主方。方中以味甘性凉，清肺止咳良药北沙参为主药；配以甘草清肺、止咳、祛痰，拳参以清热解毒、清肺，紫草茸以凉血、清肺，共为辅佐药。故本方为肺热及各种热性咳嗽常用良方。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (434, 2, 38, '扫日劳一7汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　扫日劳一7汤 <br>\r\n      <br>\r\n      　　[异名] 七味沙参汤<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]北沙参90g 紫草15g 紫草茸15g 金诃子15g 茜草15g 川楝子9g 水栀子15g<br>\r\n      　　[功能与主治]清肺热，止咳，祛痰，用于肺热咳痰中带血，胸胁作痛，气喘，急慢性气管炎。<br>\r\n      　　[附注]本方性凉，为肺热症主方。方中以清肺，止咳良药北沙参为主；配以凉血之汤三籽汤及三红汤（紫草、紫草茸、茜草）配伍。故本方具备了清热、止咳功效、对肺热咳嗽、痰中带血均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (435, 2, 38, '赞丹-8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　\r\n      <p>赞丹-8 <br>\r\n      </p>\r\n      <p>　　[异名] 八味檀香散<br>\r\n        　　[来源] 《中华人民共和国药典》<br>\r\n        　　[处方] 白檀香20g 石膏10g 红花10g 北沙参10g 甘草10g 丁香10g 白葡萄干20g 拳参10g<br>\r\n        　　[功能与主治]清热润肺，止咳化痰。用于肺热咳嗽，痰中带脓，久咳胸痛。<br>\r\n        　　[附注]本方性凉，为肺热病良方。方中以清肺、心讧热良药白檀香为主；配合石膏以清肺止咳，红花以清肝凉血，甘草以清热止咳，北沙参以清肺止咳、锁脉止血，白葡萄干以止咳平喘，丁香以理气利咽。各药合用，对肺热咳嗽，咯脓痰均有良效。<br>\r\n      </p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (436, 2, 38, '吉森・乌讷斯', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　吉森・乌讷斯<br>\r\n      <br>\r\n      　　[异名] 二十五味铜灰散 <br>\r\n      　　[来源] 《蒙医常用方剂选》<br>\r\n      　　[处方] 铜灰48g 菊花48g 火绒草48g 木棉花萼48g 沙棘48g 广本香48g 木鳖子(制)32g 红花24g 肉豆蔻24g 白苣胜24g \r\n      广角24g 本棉花蕊24g 白豆蔻24g 石膏24g 木棉花瓣24g 紫檀香24g 麦冬24g玉簪花24g 白檀香24g 牛黄2.4g 草果仁24g \r\n      公丁香24g 熊胆2.4g 草河车24g 麝香1.6g<br>\r\n      　　[功能与主治] 清热解毒,止咳化痰,用于肺脓肿,咳血脓痰,肺痨,肺结核。<br>\r\n      　　[附注]本方性凉，为肺结核专用方。方中以燥脓血良药铜灰为主；广木香以祛痰，菊花以清热解毒，火绒草以清肺止咳，沙棘以止咳化痰，木棉花以清心肝肺热，共为辅佐；配合六味良药和牛黄、麝香、熊胆、广角等清热解毒、止血贵重药材。多药合用，对肺脓肿、肺结核、咯脓痰、咯血均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (437, 2, 38, '沏其日甘一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　沏其日甘一5<br>\r\n      <br>\r\n      　　[异名] 达日布一5、五味沙棘散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 沙棘30g 广木香25g 甘草15g 白葡萄干20g 栀子10g<br>\r\n      　　[功能与主治] 清热，化痰，止咳。用于肺热多痰，久咳喘促，咯痰不利，慢性支气管炎。<br>\r\n      　　[附注] 本方性平，为止咳祛痰主方。方中以味酸性温止咳祛痰药沙棘为主药；以排脓、祛痰药广木香为辅药；配白葡萄干以止咳、平喘，甘草以清热、止咳、祛痰，栀子以凉血、调元，共为佐使药。故对久咳喘促，咯痰不利均有功效，特别对慢性气管炎有良效。若用于虚寒型慢性支气管，则可加适量荜拨，疗效更为显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (438, 2, 39, '额力根乃汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　额力根乃汤 <br>\r\n      <br>\r\n      　　[异名] 利肝退黄汤<br>\r\n      　　[来源] 《蒙医传统验方》 <br>\r\n      　　[处方]五灵脂21g 木通18g 金诃子15g 巴沙嘎15g 水栀子15g 红花15g 川楝子15g 熊胆粉9g<br>\r\n      　　[主治与功能] 清肝，凉血。用于肝热，肝血增盛，肝肿大，肝区闷痛，黄疸。 <br>\r\n      　　[附注] 本方性凉，为专治肝热汤。方中以清肝，调元药五灵脂为主；以清肝热药木通为辅；配以红花、巴沙嘎以凉血，熊胆以清热解毒、退黄，三籽汤以凉血解毒。各药合用，对肝热，肝区痛，黄疸均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (439, 2, 39, '额力根・古日古木一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　额力根・古日古木一7 <br>\r\n      <br>\r\n      　　[异名] 七味红花散<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方]西红花l0g 石膏l0g 人工牛黄10g 蓝盆花5g 巴沙嘎5g 香青兰5g 五灵脂5g<br>\r\n      　　[功能与主治]清肝，解毒。用于肝热目赤，黄疸，肝区疼痛，发热口渴，肝肿大，肝损伤，肝血热盛等肝热诸症。<br>\r\n      　　[附注]本方性凉，为肝血热症专用方。方中以三臣药(石膏、红花、牛黄)为主；配以蓝盆花、五灵脂、香青兰等清肝热药及凉血药巴沙嘎。故对肝热诸症及肝血热均有效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (440, 2, 39, '古日古木一16', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　古日古木一16 <br>\r\n      <br>\r\n      　　[异名] 十六味红花丸<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]红花24g 五灵脂24g 香青兰2lg 石膏18g 蓝盆花18g 炉甘石(制)18g 人工牛黄18g 黄柏皮15g 刺玫果15g 木通18g \r\n      土木香15g 巴沙嘎15g 水栀子9g 瞿麦15g 胡黄连9g 地格达9g 轻粉(制)9g<br>\r\n      　　[功能与主治]清肝，解毒，凉血。用于新旧肝热，急慢性肝炎，肝区疼痛，食欲不振，黄疸，肝热诸症。<br>\r\n      　　[附注]方性凉，为新旧肝热常用方。方中以凉血益肝良药红花为主；清肝、调元药五灵脂为辅；配合牛黄、蓝盆花、地格达、香青兰、炉甘石等清肝解毒药物及巴沙嘎、栀子等凉血药；石膏以清热、退黄，胡黄连以清热、燥恶血，黄柏皮以清热、解毒、止血，土木香以清巴达下热、止痛。各药合用，具备了清热解毒，凉血之功，故本方对新旧肝热，急慢性肝炎均有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (441, 2, 39, '给旺一9', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　<br>\r\n      　　给旺一9<br>\r\n      <br>\r\n      　　[异名] 给旺古瓦、九味牛黄散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 人工牛黄15g 红花9g 五灵脂9g 地格达6g 巴沙嘎6g 广木香6g 木鳖子(制)6g 木通6g 蓝盆花6g<br>\r\n      　　[功能与主治] 清肝，凉血。用于肝损伤，肝血热盛，黄疸，肝包如及包如巴达干病。<br>\r\n      　　[附注] 本方性凉，为肝热常用方。方中以清热解毒药牛黄为主；配合益肝良药红花和凉血止痛药巴沙嘎(蒙医用瞿麦或闹羊花)及地格达(蒙医用扁蕾或紫花地丁)、蓝盆花、木鳖子、木通等清希拉热药，为了完善药力配加五灵脂和广木香，以调理体素并调理赫依血相搏及止痛功效。故本方对肝热、肝炎、肝包如引起肝区疼痛均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (442, 2, 39, '德格都・古日古木一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　德格都・古日古木一7<br>\r\n      <br>\r\n      　　[异名] 古日古木・朝格敦、七味红花散、古日古木-7<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方] 西红花25g 石膏15g 麻黄15g 地格达10g 木通10g 诃子10g 蓝盆花10g<br>\r\n      　　[功能与主治] 清肝，凉血。用于肝瘀血，肝区疼痛，目、肤及尿黄，肝肿大，肝损伤，肝血热盛，新旧肝热。<br>\r\n      　　[附注] 本方性凉，为肝瘀血常用方。方中以凉血益肝良药红花为主；石膏以清热、退黄，麻黄以清肝、消肿，共为辅；配合木通以清热、止痛，地格达(为蒙药名，蒙医常用扁蕾或紫花地丁)以清希拉热，蓝盆花以清希拉、止痛，诃子以调理体素、解毒，共为佐使。各药合用，对新旧肝热，肝区疼痛及黄疸均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (443, 2, 39, '古日古木一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　古日古木一13<br>\r\n      <br>\r\n      　　[异名] 古日古木朱苏木、十三味红花丸<br>\r\n      　　[来源] <br>\r\n      　　[处方] 西红花6g 牛黄6g 紫檀香6g 水牛角3g 麦冬6g 建莲子3g 水栀子3g 银朱3g 广木香3g 川楝子3g 麝香1.5g 公丁香3g \r\n      金诃子3g<br>\r\n      　　[功能与主治] 清肝，解毒，杀粘。用于肝肿大，肝硬化，肝功衰竭，肝中毒，肾伤，小便淋漓，亚玛病及血热引起的眼病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (444, 2, 40, '德伦乃・古日古木一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　德伦乃・古日古木一7 <br>\r\n      <br>\r\n      　　[异名] 七味红花清脾散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]红花6g 荜拨6g 牛黄3g 石膏3g 丁香3g 诃子3g 木鳖子(制)3g<br>\r\n      　　[功能与主治]清脾热。主治脾伤，腹胀，左胁刺痛，脾热诸症。<br>\r\n      　　[附注]本方性凉，为脾热诸症主方。方中以清热解毒三臣药(红花、牛黄、石膏)为主；配荜拨以调胃火，丁香以理气止痛，木鳖子以清希拉，诃子以调元解毒。各药合用，对轻性脾热诸症均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (445, 2, 40, '嘎古拉一19', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎古拉一19 <br>\r\n      <br>\r\n      　　[异名] 十九味草果散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]草果仁15g 石膏l5g 五灵脂9g 木棉花6 豆蔻6g 地格达3g 金诃子3g 巴沙嘎3g 紫草绒3g 水柏枝3g 紫草3g 广木香3g \r\n      菥冥子3g 麦冬3g 建莲子3g 水栀子3g 甘松3g 西红花2.13g 木鳖子(制)3g<br>\r\n      　　[功能与主治]理中健脾。用于脾赫依，脾热，脾巴达干等脾脏寒热诸症。<br>\r\n      　　[附注]本方性平，为脾病总方。方中以调火、祛寒,益脾良药草果为主,以清热、益肺药石膏为辅；配白豆蔻以祛寒益肾，红花以凉血益肝，菥冥子和建莲子均补肾气，地格达、麦冬、木鳖子均清希拉热，巴沙嘎、栀子、紫草茸均凉血，诃子以调元解毒。各药合用，故本方对脾脏寒热诸症均有疗效。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (446, 2, 40, '嘎古拉一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎古拉一4汤<br>\r\n      <br>\r\n      　　[异名] 嘎古拉・都日本汤、四味草果汤<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 草果仁15g 广木香15g 小茴香9g 公丁香9g<br>\r\n      　　[功能与主治] 调节赫依，健脾胃。用于上行赫依、持命赫依之病，腹胀肠鸣，脾胃之赫依，赫依引起头痛、脾虚 <br>\r\n      　　[附注] 本方性温，为治脾虚、脾赫依常用主方。方中以健脾良药草果为主；以调节赫依血相搏药木香为辅；配合以持命赫依良药丁香和镇赫依、开胃药小茴香。故对上行赫依病，尤其对脾虚症有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (447, 2, 40, '阿如拉一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　阿如拉一7<br>\r\n      <br>\r\n      　　[异名] 阿如敦巴、七味诃子散<br>\r\n      　　[来源]《四部医典》<br>\r\n      　　[处方] 诃子l0g 草果仁10g 荜拨10g 木棉花10g 甘松10g 木鳖子(制)10g 丁香10g<br>\r\n      　　[功能与主治] 清脾，止痛，健脾。用于脾疼痛，脾肿大，肠鸣，腹胀，腹泻。<br>\r\n      　　[附注] 本方性平，为脾疼痛常用方。方中以健脾良药草果为主，配以诃子以调理体素、解毒，荜拨以调胃火，木鳖子以清希拉，甘松以清热，丁香以理气止痛。各药合用，对脾热、脾虚、脾痛均有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (448, 2, 41, '郭日本・乌兰汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　郭日本・乌兰汤<br>\r\n      <br>\r\n      　　[异名] 玛日布一3汤、三红汤<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方] 紫草茸10g 茜草10g 枇杷叶10g<br>\r\n      　　[功能与主治] 清血热。用于肾、肺震伤热，膀胱 尿痛尿血，肺热咳嗽。<br>\r\n      　　[附注]本方性凉，为肾、肺震伤热良方。方中紫草茸以凉血，清肾、肺震伤热，茜草以凉血、止血，枇杷叶以清热、止咳。三药合用，对肾、肺震伤热均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (449, 2, 41, '波仁・阿如拉一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　波仁・阿如拉一10<br>\r\n      <br>\r\n      　　[异名] 阿如拉一10、哈勒玛・阿如一10、十味诃子清肾散<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]诃子9g 白豆蔻6g 刀豆9g 枇杷叶6g 紫草茸6g 刺柏叶6g 五灵脂6g 地格达3g 茜草6g 红花 6g<br>\r\n      　　[功能与主治] 清肾热。用于肾热，肾震伤，小便不利，尿频，腰腿酸痛。<br>\r\n      　　[附注] 本方性凉，为肾热专用方。方中以调元解毒药诃子为主，以清肾热药刀豆为辅；配以益肾良药白豆蔻，三红汤以清血热，红花以凉血活血，地格达以清希拉热，五灵脂以调理体素、利尿、止痛，刺柏 \r\n      叶以清肾热、利尿。各药合用，对肾热及肾震伤所致 热症均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (450, 2, 41, '萨丽・嘎日迪', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　萨丽・嘎日迪 <br>\r\n      <br>\r\n      　　[异名] 十七味宝凤丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 草乌(制)l5g 诃子15g 益智仁l5g 紫草茸13.2g 木香15g 大蜀季10.5g 白刀豆10.5g 银朱9g 石菖蒲9g \r\n      石决明(煅)7.2g 红花3.6g 黑云香7.2g 枇杷叶7.2g 熊胆3.6g 茜草3g 香墨3g 麝香0.6<br>\r\n      　　[功能与主治] 清肾热，固精，杀粘。用于肾热诸病症，梦遗滑精，睾丸肿大，腰膝疼痛。<br>\r\n      　　[附注] 本方性凉，为肾热常用良方。方中以嘎日迪一5(五凤丸)为主，配补气益肾良药益智仁和白刀豆及三红汤以清肾热，大蜀季以清肾、膀胱热，利尿，黑云香和香墨以杀粘，熊胆以止血，红花以凉血，银朱以清脉热。各药合用，对肾热症均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (451, 2, 41, '希莫音・满都拉', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　希莫音・满都拉<br>\r\n      <br>\r\n      　　[异名] 朱吉德・尼吉勒<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方] 全石榴9g 天门冬8g 紫硇砂6g 小蜀季6g 玉竹8g 肉豆蔻5g 五灵脂5g 肉桂8g 西红花5g 葡萄干5g 人参6g 白蒺藜4g \r\n      天花粉4g 沉香5g 寒水石(煅)4g 益智仁3g 黄精5g 手掌参3g 草果仁 2g 荜拨3g 广酸枣2g<br>\r\n      　　[功能与主治] 健胃暖肾，滋补壮阳。用于肾寒肾虚，尿频，遗精，浮肿，腰腿酸痛，全身泛力，食积胃胀，胸闷头晕。<br>\r\n      　　[附注] 本方性温，为强身壮阳药。此方主要由滋补壮阳五根药(即玉竹、黄精、天门冬、天花粉、蒺藜)及健胃方阿纳日一4(四味石榴散)组成。配合人参、手掌叁、荜拨、寒水石等滋补强身药，加强强身壮阳作用，故本方对肾虚，乏力具有疗效。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (452, 2, 41, '波仁・阿如拉一18', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　波仁・阿如拉一18<br>\r\n      <br>\r\n      　　[异名] 阿如拉一18、哈勒玛・阿如一18、十八味诃子清肾丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方]阿如拉一10，加芒果核，建莲子、石莲子各6g，海金沙、方海、冬葵果各3g，草乌(制)12g，麝香1.5g<br>\r\n      　　[功能与主治] 清肾热，利尿，杀粘。用于肾热，肾脉震伤，小便不利，尿频，腰腿酸痛，肾瘀血，睾 丸肿痛，遗精早<br>\r\n      　　[附注]本方性凉，为肾粘热常用方。方中阿如拉一10以清肾热，为主；加芒果核、建莲子、石莲子(蒙医习称下三籽)以祛肾寒、补肾；海金沙、方海、冬葵果以利尿；草乌、麝香以杀粘，止痛。故本方具备了清肾热，利尿，杀粘功效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (453, 2, 41, '苏格木勒一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　苏格木勒一10<br>\r\n      <br>\r\n      　　[异名] 十味豆蔻散、苏格木勒朱瓦<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 苏格木勒15g 方海24g 干姜6g 荜拨6g 蜀葵花6g 芒果核6g 建莲子6g 麝香1.5g 白硇砂 6g 石莲子 6g<br>\r\n      　　[功能与主治] 祛肾寒，利尿。用于肾寒，肾虚，尿闭，肾结石，腰腿疼痛等。<br>\r\n      　　[附注] 本方性温，为治肾寒常用主方。方中以祛肾寒、温中益肾良药苏格木勒为主；配合方海、白硇砂、蜀葵花以利尿；芒果核、建莲子、石莲子(蒙医习称下三籽)以祛肾寒、补肾；干姜、荜拨以祛巴达干赫依、开胃；麝香以杀粘、止痛。各药合用，对肾寒、尿闭疗效显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (454, 2, 41, '苏格木勒--13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　苏格木勒--13<br>\r\n      <br>\r\n      　　[异名] 十三味豆蔻散<br>\r\n      　　[来源] 《蒙医选编》丛书<br>\r\n      　　[处方] 苏格木勒30g 干姜24g 白硇砂15g 冬葵果12g 荜拨 15g 芒果核9g 建莲子9g 姜黄9g 石莲子9g 黄柏皮9g 方海3g \r\n      麝香0.3g 水栀子9g<br>\r\n      　　[功能与主治] 祛肾寒，利尿，补肾。用于肾伏热，肾结石、膀胱结石，尿闭，尿中毒，肾炎。<br>\r\n      　　[附注]本方乃白清云专治肾伏热、肾炎常用验方。白清云(1912―1989)系辽宁省阜新县人，生前为内蒙古医学院中蒙医系临床教研室主任，首届蒙医教授，著名蒙医内儿科专家。方中苏格木勒，系蒙药译名，根据本人用药习惯，本方使用白豆蔻，本方以苏格木勒一10为主，加黄柏皮、姜黄、栀子所组成。以上三味各有止血解毒，杀粘疗伤，凉血调元功效。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (455, 2, 41, '那仁满都拉--11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　那仁满都拉--11<br>\r\n      <br>\r\n      　　[异名] 扫吉德・尼吉勒、补益还阳丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 石榴30g 肉桂3g 益智仁15g 荜拨12g 红花9g 小蜀季9g 玉竹12g 黄精9g 天门冬9g 蒺藜9g 天花粉9g<br>\r\n      　　[功能与主治]温中消食，补肾利水，燥黄水。用于肾寒腰痛，胃寒腹泻，宫寒带多，遗精淋下，消化不良，浮肿，水肿，黄水疮。<br>\r\n      　　[附注] 本方性温，为补肾壮阳良方。此方由通拉嘎一5(五味清浊散)和五根药(玉竹、黄精、天门冬、天花粉、蒺藜)加小蜀季配合组成。通拉嘎一5以暖胃消食，五根药以补气壮阳，蒺藜以利尿。故本方专治肾寒肾虚，宫寒带下，消化不良均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (456, 2, 41, '高优--7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　高优--7<br>\r\n      <br>\r\n      　　[异名] 高优敦巴、七味槟榔散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 槟榔30g 白豆蔻24g 石榴24g 白硇砂3g 荜拨16.5g 肉桂15g 干姜 16.5<br>\r\n      　　[功能与主治] 祛肾寒。用于肾寒，睾丸寒湿，腰痛，白带过多。<br>\r\n      　　[附注] 本方性热，为肾寒症总方。方中以祛肾寒良药槟榔为主，以益肾良药白豆蔻为辅，白硇砂以利尿消肿，石榴、干姜以调胃火， 肉桂以祛寒气，荜拨以调理体素，为之佐使。各药合用，对肾寒诸症有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (457, 2, 41, '霞日毛都一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　霞日毛都一8<br>\r\n      <br>\r\n      　　[异名] 吉日顺一8、八味黄柏散、沙日毛都一8 <br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 黄柏皮60g 香墨45g 牛胆膏 45g 水栀子15g 甘草24g 西红花15g 荜拨24g 麝香2.4g<br>\r\n      　　[功能与主治] 清热，止血，固精。用于肾热，膀胱热，宫热，尿频，尿痛，尿血，月经过多，遗精，滑精，腰痛<br>\r\n      　　[附注]本方性寒，为专治肾热，尿血良方。方中以清热止血药黄柏皮为主；以止血良药熊胆(牛胆膏代用)和香墨为辅；配麝香以杀粘止痛，红花、栀子以凉血，甘草以清脉热，荜拨以调火。各药合用，对肾及膀胱热，尿血，赤白带下均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (458, 2, 41, '呼础吉古路其一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　呼础吉古路其一15<br>\r\n      <br>\r\n      　　[异名] 补肾壮阳丸<br>\r\n      　　[来源] 《包景荣验方》<br>\r\n      　　[处方] 蛤蚧5g 天花粉10g 红花5g 鹿茸5g 草茸10g 手参10g 玉竹10g 枇杷叶10g 黄精10g 茜草10g 黄柏皮10g \r\n      石榴10g 天冬10g 白豆蔻10g 蒺藜10g<br>\r\n      　　[制法]以上十五味，分别挑选，粉碎成细粉，筛，混匀，凉开水泛丸，打光，干燥即得。<br>\r\n      　　[性状]本品为褐黄色水丸，气微香，味苦、甘。<br>\r\n      　　[功能与主治] 固精，壮阳。用于肾虚腰痛，尿不利，阳萎，遗精。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (459, 2, 42, '浩道敦-10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　浩道敦-10 <br>\r\n      <br>\r\n      　　[异名] 十味诃子暖胃散 <br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 石榴18g 寒水石(煅)9g 肉桂9g 诃子3g 光明盐6g 荜拨9g 山柰3g 五灵脂6g 胡椒9g 苏格木勒9g<br>\r\n      　　[功能与主治] 祛寒暖胃，助消化，用于寒湿积聚，消化不良，胃胀不适，呕吐泄泻、寒痧。<br>\r\n      　　[附注] 本方性热，为胃巴达干寒症主方。方名以解毒、调理体素之药诃子为名称；方中以阿纳日一4 (四味石榴散)为主，配寒水石以消食、止吐泻，光明盐、胡椒、山奈以调胃火，祛巴达干赫依，五灵脂以调元、止痛。方中苏格木勒，系蒙药译名，苏格木勒有白、紫、红三种，本方使用红的，即砂仁。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (460, 2, 42, '海鲁木勒一9', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　海鲁木勒一9<br>\r\n      <br>\r\n      　　[异名] 九味五灵脂散<br>\r\n      　　[来源] 《蒙医常用方剂选》<br>\r\n      　　[处方] 拳参7.5g 五灵脂15g 红花9g 诃子9g 香青兰7.5g 麦冬7.5g 苏格木勒7.5g 熊胆6g 麝香1g<br>\r\n      　　[功能与主治] 清胃，凉血，止泻，用于胃肠希拉热，腹痛下痢，胃肠疫热，上吐下泻。<br>\r\n      　　[附注] 本方性凉，为胃希拉热主方。方中以清胃热药五灵脂为主；以凉血药红花和清希拉药诃子为辅；配合麦冬、香青兰以清希拉、清胃热，拳参以止泻，熊胆以清希拉、止血，麝香以杀粘、止痛。各药合用，对胃血、希拉热病均有疗效，方中苏格木勒，系蒙药译名，根据蒙医用药情况，此方用益智仁。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (461, 2, 42, '七味胆汁散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　七味胆汁散<br>\r\n      <br>\r\n      　　[异名]<br>\r\n      　　[来源] <br>\r\n      　　[处方]止泻子18g 麦冬15g 木鳖子(制)15g 香附子l2g 丹参9g 关本通12g 熊胆粉3g<br>\r\n      　　[功能与主治] 清热，止泻，止痢。用于希拉引起的腹泻,腹胀，赤白痢疾。<br>\r\n      　　[附注]本方性凉，为肠热腹泻主方。方中主要起作用的是熊胆，故称方名为苏斯一7，苏斯是胆的译名，熊胆以清希拉，锁脉止血，为主；配泻药止泻子。止泻子属藏医常用止泻药，蒙医广泛应用连翘，两者功效相近，但清希拉用止泻子较好，故本方选用了止泻子。方中麦冬,木通、香附，木鳖子均有清热清希拉而止泻作用,故本方对希拉引起的腹热腹泻疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (462, 2, 42, '嘎地拉-11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎地拉-11<br>\r\n      <br>\r\n      　　[异名] 十一味木瓜散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 木瓜15g 西红花9g 麦冬9g 香附9g 五味子9g 连翘9g 橡子6g 关木通6g 茯苓3g 丹参3g 木鳖子(制)6g<br>\r\n      　　[功能与主治] 清热，止泻。用于血、希拉热引起的腹泻。<br>\r\n      　　[附注]本方性凉，为热泻常用方。方中以清热,止泻药木瓜为主；配合五味子、香附、木通、橡子等清热、止泻药物，共为辅；红花、丹参以凉血，连翘，木鳖子，以清希拉热。各药合用，对希拉热血热引起的腹泻均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (463, 2, 42, '高药一4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　高药一4<br>\r\n      <br>\r\n      　　[异名] 玛格西瓦、壮西希召日<br>\r\n      　　[来源] 《阿拉善验方》<br>\r\n      　　[处方] 肉苁蓉或锁阳9g 地格达3g 寒水石(煅)9g 冰糖18g<br>\r\n      　　[功能与主治] 清希拉，助消化，止吐止泻。用于胃希拉，泛酸，希拉引起头痛、胃痛及吐泻，妇女手脚发热等。<br>\r\n      　　[附注]本方性平，为助消化，治巴达干、希拉病 的良方。此方乃内蒙古阿拉善盟阿左旗吉日嘎拉赛汗 苏木的老蒙医陶音扎拉僧所创，经过百余年的实践验 \r\n      证。此方在阿盟地区应用甚广，疗效显著。方中以阿拉善特产素有“沙漠人参’之称的肉苁蓉和锁阳为主； 配以寒水石以祛巴达干热，止吐止泻，消食解毒，调元，地格达以清希拉热，健胃，加冰糖以清热止吐， \r\n      均为辅佐药。四药合用，对消化不良，巴达干希拉病， 特别对酒后胃痛、头痛均有奇效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (464, 2, 42, '喜和日・额布斯一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　喜和日・额布斯一6<br>\r\n      <br>\r\n      　　[异名] 兴阿日一6、六味甘草散<br>\r\n      　　[来源] 《四部医典》<br>\r\n      　　[处方] 甘草10g 粳米(微炒)10g 茴香10g 蔓荆子10g 芫荽子10g 柿子10g<br>\r\n      　　[功能与主治] 止吐。用于妊娠呕吐等各种呕吐。<br>\r\n      　　[附注] 本方性平，为止各种呕吐之良方。方中甘 草、粳米以止吐，小茴香、芫荽子以祛巴达干赫依、 开胃，柿子以祛巴达干热，蔓荆子以调胃火。各药合 \r\n      用，对各种呕吐均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (465, 2, 42, '吉格斯一4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　吉格斯一4<br>\r\n      <br>\r\n      　　[异名] 舒达格一4、四味菖蒲散[处方] 石菖蒲30g 广木香18g 干姜12g 紫硇砂12g<br>\r\n      　　[功能与主治]祛巴达干赫依，镇静。用于胸闷气短，胸胁刺痛，消化不良，呃逆频作，膈肌痉挛。<br>\r\n      　　[附注] 本方性温，为治上行赫依运行受阻所致的呃逆之良药。方中以消食开胃药石菖蒲为主；配合广木香以调理气血不和，干姜以消食，紫硇砂以祛巴达干赫依。四药合用，具备了消食化滞，祛巴达干赫依，平喘开胃之功效，故本方对各种呃逆，气喘有特效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (466, 2, 42, '哈敦・嘎日迪一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　哈敦・嘎日迪一13<br>\r\n      <br>\r\n      　　[异名] 巴日格冲一13、十三味岩鹏丸<br>\r\n      　　[处方] 五灵脂25g 红花15g 白豆蔻25g 香青兰15g 诃子15g 草乌叶15g 石菖蒲15g 麦冬15g 广木香15g 草河车15g \r\n      熊胆1.5g 黑冰片8g 麝香o.6g<br>\r\n      　　[功能与主治] 清腑热，杀粘，止痛。用于胃肠痉挛，胃粘热，粘痧，胃刺痛，呕吐，腹泻。<br>\r\n      　　[附注] 本方性凉，为胃肠粘痧常用方。方中以清胃热药五灵脂和杀粘总方嘎日迪一5为主；配合以香青兰、麦冬、红花、熊胆等清热凉血药，故本方对胃肠希拉及胃粘痧均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (467, 2, 42, '苏龙嘎--4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　苏龙嘎--4汤<br>\r\n      <br>\r\n      　　[异名] 音达拉一4汤、四味止泻汤<br>\r\n      　　[处方] 止泻子24g 拳参21g 关木通2lg 麦冬15g<br>\r\n      　　[功能与主治] 清热，止泻。用于肠热腹泻，腹痛，痢疾。<br>\r\n      　　[附注] 本方性凉，为肠热腹泻主方。方中以清热止泻良药止泻子为主，配以拳参、木通、麦冬有清热、止泻功效，故对肠热腹泻均有特效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (468, 2, 42, '音达拉一15', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　音达拉一15<br>\r\n      <br>\r\n      　　[异名] 音达拉壮阿、十五味连翘散<br>\r\n      　　[处方] 连翘15g 五灵脂15g 麦冬9g 拳参9g 草乌叶6g 关木通9g 石膏6g 黑冰片6g 荜拨6g 红花6g 黑云香6g 麝香0.6g \r\n      牛黄1.5g 木鳖子(制)6g 光明盐3g<br>\r\n      　　[功能与主治] 清腹热，杀粘，止泻。用于热性腹泻，腹痛，痢疾。<br>\r\n      　　[附注] 本方性凉，为粘热性泄泻主方。方中以清腹热、止泻方音达拉一4汤为主；配合清热三臣药(牛黄、红花、石膏)及麝香、黑云香、草乌叶等杀粘止痛药。诸药合用，对腹热腹泻，腹痛，痢疾均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (469, 2, 42, '喜木乐德格一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　喜木乐德格一3汤<br>\r\n      <br>\r\n      　　[异名] 尼亚劳一3汤、三味叉分蓼汤<br>\r\n      　　[处方] 叉分蓼25g 麦冬15g 茜草15g<br>\r\n      　　[功能与主治] 清大肠热。用于大肠热，痢疾，大肠血希拉热症。<br>\r\n      　　[附注] 本方性凉，为大肠热主方。方中以清大肠 热、止泻叉分蓼为主；配麦冬以清希拉热，茜草以凉血、止泻。故对大肠热、腹泻均有显著疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (470, 2, 42, '阿那日一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　阿那日一13<br>\r\n      <br>\r\n      　　[异名] 捞龙森布如一13、十三味石榴散、敖勒盖-13<br>\r\n      　　[处方] 石榴9g 砂仁9g 黑苣胜3g 草果6g 荜拨6g 白苣胜3g 干姜6g 红花6g 光明盐3g 诃子6g 肉桂3g 紫硇砂1.5g \r\n      胡椒3g<br>\r\n      　　[功能与主治] 开郁顺气，化滞消胀。用于大肠赫 依，下行赫依受阻，腹胀，肠鸣，胃肠虚弱，消化不良。<br>\r\n      　　[附注] 本方性温，为大肠赫依病主方。此方以阿纳日一4为主，故有祛巴达干、开胃、助消化功效；配三热药(即干姜、胡椒、荜拨)，两种苣胜子，两种盐，故具备了开郁顺气、润肠通便，助消化功效，对腹胀、肠鸣、大肠赫依病具有特效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (471, 2, 42, '阿那日一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　\r\n      <table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n        <tr> \r\n          <td>　　阿那日一13<br>\r\n            <br>\r\n            　　[异名] 捞龙森布如一13、十三味石榴散、敖勒盖-13<br>\r\n            　　[处方] 石榴9g 砂仁9g 黑苣胜3g 草果6g 荜拨6g 白苣胜3g 干姜6g 红花6g 光明盐3g 诃子6g 肉桂3g 紫硇砂1.5g \r\n            胡椒3g<br>\r\n            　　[功能与主治] 开郁顺气，化滞消胀。用于大肠赫 依，下行赫依受阻，腹胀，肠鸣，胃肠虚弱，消化不良。<br>\r\n            　　[附注] 本方性温，为大肠赫依病主方。此方以阿纳日一4为主，故有祛巴达干、开胃、助消化功效；配三热药(即干姜、胡椒、荜拨)，两种苣胜子，两种盐，故具备了开郁顺气、润肠通便，助消化功效，对腹胀、肠鸣、大肠赫依病具有特效。<br>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (472, 2, 43, '乌力吉-18', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　乌力吉-18<br>\r\n      <br>\r\n      　　[异名] 吉祥丸<br>\r\n      　　[来源] 《医疗手册》<br>\r\n      　　[处方]益母膏24g 沙棘l5g 赤瓞子l5g 金诃子15g 山柰9g 西红花9g 五灵脂9g 鹿茸6g 刺柏叶9g 广木香9g 虫草3g \r\n      硼砂(制)3g 公丁香3g 朱砂1.5g 小白蒿6g 土木香6g 熊胆0.9g 牛黄0.9g<br>\r\n      　　[功能与主治]调经活血，补气安神。用于月经不调，产后发烧，心神不安，头昏头痛，腰膝无力，四肢浮肿，乳腺肿胀等妇科诸症。<br>\r\n      　　[附注]本方性凉，为妇科诸病专用主方。方中以活血调经药益母膏为主，配合鹿茸以活血强身，冬虫夏草以补精血、赤瓞子以活血调经，沙棘、山柰以行血，诃子、五灵脂以调元，广木香、土木香以调理气血，熊胆、小白蒿以止血，红花以凉血、活血，牛黄以解热、镇静；诸药合用，调经活血功效具全，治妇科诸病疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (473, 2, 43, '苏格木勒一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　苏格木勒一7 <br>\r\n      <br>\r\n      　　[异名] 七味豆蔻散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方]白豆蔻90g 黄精l5g 肉豆蔻15g 手拳参15g 沉香15g 天门冬15g 公丁香15g<br>\r\n      　　[功能与主治] 镇赫依，暖宫止带。用于心、肾赫依症，气滞腰痛，小腹冷痛，月经不调，白带过多。<br>\r\n      　　[附注]本方性温，为妇科赫依症主方。方中以暖 宫益肾药白豆蔻为主；配合手拳参、黄精、天门冬以养身壮阳，肉豆蔻、沉香、丁香以养心理气。各药合用，镇赫依、强身之功俱全，故本方对心、肾赫依症，白带过多，宫寒等均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (474, 2, 43, '格喜古讷一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td><br>\r\n      　　格喜古讷一6 <br>\r\n      <br>\r\n      　　[异名] 六味大黄散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 大黄15g 碱花18g 沙棘9g 山柰9g 广木香6g 朴硝6g<br>\r\n      　　[功能与主治] 行血，化瘀，通经。用于闭经,血瘀，血痞，小腹疼痛，赫依郁结而腰酸腿痛。<br>\r\n      　　[附注] 本方性温，为妇科赫依血郁结症专方。方中以缓泻药大黄为主，以化瘀润肠药碱花为辅，配合沙棘、山奈以行血，朴硝以破瘀，广木香以调理气血不和，共为佐使。各药合用，对闭经，赫依血郁结症疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (475, 2, 43, '沏其日甘-17', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　沏其日甘-17<br>\r\n      <br>\r\n      　　[异名] 十七味沙棘散<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 沙棘l5g 五灵脂6g 大黄12g 石榴12g 光明盐3g 干姜12g 芒硝3g 天花粉3g 朴硝3g 诃子3g 海金沙3g 滑石3g \r\n      红花3g 寒水石(制)3g 硇砂3g 硼砂(制)3g 赤瓞子3g<br>\r\n      　　[功能与主治] 行血，化滞、破瘀。用于妇女胃,肝,肾瘀血症,血痞,闭经,胎盘滞留。<br>\r\n      　　[附注] 本方性平，为妇女血瘀症主方。方中以行血化瘀药沙棘为主，配以泻下药大黄和温中药石榴，干姜及五灵脂以清胃、肝、肾之热，共为辅，芒硝、朴硝、滑石以化滞破痞，硇砂、海金沙以利尿并促进子宫收缩，红花凉血，赤瓞子活血，共为佐使。诸药合用，对妇女血瘀症，尤其对胃、肾、肝瘀血症均有良效。 \r\n      <br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (476, 2, 43, '扫门毛都一6汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　扫门毛都一6汤<br>\r\n      <br>\r\n      　　[异名] 遭木如克汤、六味苏木汤<br>\r\n      　　[来源] 《珊瑚验方集》<br>\r\n      　　[处方] 苏木24g 高良姜9g 槟榔9g 广木香6g 益智仁6g 草果仁6g<br>\r\n      　　[功能与主治] 祛巴达干赫依，行血化瘀。用于闭经腹痛，血瘀痛经，宫冷肾寒，腰腿疼痛。<br>\r\n      　　[附注] 本方性温，为血瘀痛经有效方。方中以行血调经药苏木为主，配合槟榔、高良姜以暖宫温中，为辅，益智仁以补肾祛寒，草果以健脾消食，广木香以调理赫依血不和、止痛，共为佐使。各药合用，对闭经腹痛、宫寒痛经疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (477, 2, 43, '扫门毛都一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>扫门毛都一4汤<br>\r\n      <br>\r\n      　　[异名] 遭木西汤、四味苏木汤<br>\r\n      　　[来源] 《蒙医药方汇编》<br>\r\n      　　[处方] 苏木12g 山柰9g 血竭9g 白硇砂4.5g<br>\r\n      　　[功能与主治] 行血，调经。用于精华不消而引起闭经，血痞，妇女血瘀症。<br>\r\n      　　[附注] 本方性平，为妇女血瘀症常用方。方中以行血调经药苏木为主；配合白硇砂以利尿、宫缩，血竭以化瘀，山柰以行血，共为辅佐。四药合用，对妇女血瘀症颇有功效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (478, 2, 43, '额布热一4', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>额布热一4 <br>\r\n      <br>\r\n      　　[异名] 吉祥丸  四味羚羊角散、扎如西瓦<br>\r\n      　　[来源](蒙医传统验方)<br>\r\n      　　[处方] 羚羊角10g 耧斗菜10g 鼯鼠肉5g 藜芦(制) 5g<br>\r\n      　　[功能与主治] 增强子宫收缩，催产。用于胎衣滞留，子宫收缩无力所致的难产，死胎不下。<br>\r\n      　　[附注] 本方性平，为增强子宫收缩良方。方中以催产、破血痞药羚羊角为主，配耧斗莱、鼯鼠肉以增强宫缩，并催产，藜芦以峻泻，共为辅佐。故对难产，胎衣不下等均有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (479, 2, 43, '卓楞乌热一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　卓楞乌热一6<br>\r\n      <br>\r\n      　　[异名] 詹巴列一6<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方)建莲子15g 石榴80g 肉桂10g 西红花10g 白豆蔻10g 荜拨10g<br>\r\n      　　[功能与主治] 精华归位，暖宫止带。用于赫依所致白带过多，月经淋漓，腰胯垂痛，食积胃痛。<br>\r\n      　　[附注] 本方性温，为白带过多症有效良方。方中以温肾补肾药建莲子为主，配石榴以祛寒，为辅，肉桂、荜拨以开胃助消化，豆蔻以祛肾寒、益肾，红花以凉血益肝，共为佐使。各药合用，对白带过多，食积胃痛颇有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (480, 2, 43, '旭仁・温吉拉嘎一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　旭仁・温吉拉嘎一7<br>\r\n      <br>\r\n      　　[异名] 庞巴列一7、七味枸杞子散<br>\r\n      　　[来源]&lt;蒙医传统验方)<br>\r\n      　　[处方] 枸杞子105g 沙棘15g 山柰15g硼砂(制)15g 朴硝15g 肉桂15g 广木香15g<br>\r\n      　　[功能与主治] 行血，化瘀。用于血郁宫中，血痞，闭经。<br>\r\n      　　[附注]本方性温，为妇女血瘀症专用方。方中以散恶血、活血药枸杞子为主，配合沙棘、山柰、硼砂以行血、朴硝以破痞，广木香以调解赫依血相讧，肉桂以祛巴达干赫依，共为辅佐。各药合用，对妇女血瘀症，血痞均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (481, 2, 43, '格喜古讷--11', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>格喜古讷--11<br>\r\n      <br>\r\n      　　[异名] 毛仁扎格朱<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]大黄15g 寒水石(煅)12g 金诃子9g 干姜6g 土木香6g 碱花(炒)3g 沙棘3g 白硇砂3g 赤瓞子3g 方海3g 乌蛇肉3g<br>\r\n      　　[功能与主治] 暖宫补虚。用于子宫脱垂，胎盘滞留，大便干燥。<br>\r\n      　　[附注]本方性温，为妇科郁结症常用方。方中以缓泻剂格喜古讷一3汤为主；配合白硇砂以增强宫缩，方海以利尿，干姜、沙棘以行血，赤瓞子以活血，蛇肉以通窍。各药合用，对胎盘滞留，妇女血瘀症，闭经均疗效显著。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (482, 2, 43, '尼达金道格', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>尼达金道格<br>\r\n      <br>\r\n      　　[异名] 九味瞿麦普宁散<br>\r\n      　　[来源]《蒙医传统验方》<br>\r\n      　　[处方] 瞿麦21g 紫檀香12g 红花9g 黄连6g 白豆蔻6g 石榴3g 姜黄3g 地格达3g 熊胆3g [功能与主治] 清血热，止赤白带下。用于赤白带下，腰痛，痛经，膀胱区疼痛，月经崩漏。<br>\r\n      　　[附注] 本方性凉，为赤白带下良方。方中以凉血药瞿麦为主，配紫檀香、红花、熊胆以凉血、止血，共为辅佐；黄连以清瘟，姜黄以杀粘，地格达以清希拉热，白豆蔻、石榴以温肾暖宫。各药合用，对赤白带下，月经过多均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (483, 2, 43, '霞日明占--7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　霞日明占--7<br>\r\n      <br>\r\n      　　[异名] 七味毛莲菜散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 毛莲菜15g 细辛15g 银朱9g 方海3g 牛黄3g 熊胆3g 麝香3g<br>\r\n      　　[功能与主治] 杀粘，消肿，止痛。用于乳房肿胀，乳腺炎。<br>\r\n      　　[附注] 本方性凉，为专治乳房肿痛常用方。方中以杀粘、消肿药毛莲菜和细辛为主，配合麝香、牛黄、熊胆等杀粘、清热、解毒药。故对乳房肿胀，乳腺炎具有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (484, 2, 44, '胡勒森朱岗一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>胡勒森朱岗一8<br>\r\n      <br>\r\n      　　[异名] 肺热普清丸 <br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 天竺黄50g 麦冬50g 北沙参40 草河车40g 红花40g 胡黄连5g 甘草40g 牛黄15g<br>\r\n      　　[功能与主治] 清肺热，止咳定喘。用于小儿肺热，发烧气促，感冒咳嗽，瘟疫热盛。<br>\r\n      　　[附注] 本方性凉，为小儿肺热良方。方中以三臣药为主，配北沙参、草河车、甘草等清肺止咳药,故对小儿肺热咳嗽，感冒发烧均有显著疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (485, 2, 44, '图喜木勒一3', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　图喜木勒一3<br>\r\n      <br>\r\n      　　[异名] 伦布苏木召日、三臣药<br>\r\n      　　[来源] 《兰塔布》<br>\r\n      　　[处方] 天竺黄30g 牛黄24g 西红花24g<br>\r\n      　　[功能与主治] 熄风降火，止咳。用于小儿瘟热高烧，肺热咳嗽，各种惊风。<br>\r\n      　　[附注] 本方性凉，为小儿各种热病，尤其是小儿肺热、肝热之主方。方中以清肺止咳良药天竺黄为主，以清热、解毒、镇静药牛黄和凉血清肝良药红花为辅 \r\n      佐。故具备了清热、解毒、镇静功效，对肺热、肝热 等各种瘟热病均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (486, 2, 44, '朝伦雄胡一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　朝伦雄胡一5<br>\r\n      <br>\r\n      　　[异名] 抽风阿瓦、五味朱砂清瘟丸<br>\r\n      　　[来源] 《蒙医药方汇编》<br>\r\n      　　[处方] 朱砂15g 巴豆(制)15g 牛黄0.3g 麝香0.3g 胡黄连9g<br>\r\n      　　[功能与主治] 镇惊安神，顺气通便。用于小儿急 热惊风，腹胀便秘，乳积不消，四肢抽搐，肺热高烧。 [附注] 本方性凉，为小儿惊风，高烧专用方。方 \r\n      中以清脉热，镇静药朱砂为主，配牛黄以解热，镇静， 麝香以杀粘，通窍止痛，巴豆以泻热驱毒，胡黄连以清热，解毒，共为辅佐。各药合用，具备了解热镇静功效，对小儿高烧惊风，便秘腹胀颇有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (487, 2, 44, '查拉高德一5', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>查拉高德一5<br>\r\n      <br>\r\n      　　[异名] 清肺阿瓦、五味朱砂清肺丸<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 朱砂15g 川贝母15g 甘草15g 石膏30g 寒水石(煅)60g<br>\r\n      　　[功能与主治] 清肺止咳。用于肺热咳嗽，感冒气 喘，百日咳，麻疹，天花等。<br>\r\n      　　[附注] 本方性凉，为小儿肺热咳嗽常用良方。方中石膏、甘草、川贝母皆能清肺止咳，朱砂以清热镇惊，寒水石以滋补调元、消食。各药合用，对小儿 肺热咳嗽、感冒咳嗽、出疹引起各种咳嗽均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (488, 2, 45, '额日敦乌日勒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　额日敦乌日勒 <br>\r\n      <br>\r\n      　　[异名] 珍珠丸<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方] 紫檀香l5g 石膏l5g 白豆蔻15g 藏红花15g 诃子15g 草果仁15g 白檀香15g 栀子15g 川楝子15g 白云香15g \r\n      沉香15g 海金沙l5g 珍珠(制)10g 犀角15g 公丁香10g 肉豆蔻10g 肉桂10g 地锦草10g 黑苣胜10g 甘草10g 白苣胜10g \r\n      苘麻子10g 方海10g 草决明10g 土木香l0g 荜拨l0g 广木香10g 牛黄2.5g 麝香2.5g<br>\r\n      　　[功能与主治] 清热，安神，舒筋活络，祛黄水。 用于白脉病，黑脉病，萨病，痛风，游痛证，吾雅曼病，痹证，黄水病，肌筋萎缩，四肢麻木，肾损脉伤，久热不愈等诸症。<br>\r\n      　　[附注] 本方性凉，为专治白脉病常用良方。方中以专治脑患、白脉药珍珠为主，配以脏腑之六味良药、黄水病三药、凉血三籽汤和两个檀香，共为辅；配合牛黄、麝香、犀牛角(可水牛角浓缩粉代替)等清热解毒、镇静止痛药物，具备了清热安神，舒筋活络功效、故对白脉受损所致半身不遂，神经麻痹及黄水病、风湿病、高血压病等均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (489, 2, 45, '嘎日迪一13', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎日迪一13<br>\r\n      <br>\r\n      　　[异名] 十三味大鹏金翅丸<br>\r\n      　　[来源] 《蒙成药标准》<br>\r\n      　　[处方] 诃子l0g 草乌(制)10g 菖蒲8g 木香6g 珊瑚(制)3g 珍珠3g 沉香4g 禹粮土3g 丁香2g 甘草4g 磁石(制)2g \r\n      麝香1g 肉豆蔻2g<br>\r\n      　　[功能与主治] 杀粘止痛，舒筋通窍，祛黄水。用于白脉病，黄水病，关节肿痛，四肢麻木，萨病，痹证，中风，吾雅曼病、亚玛病，白喉，炭疽，丹毒，转筋，粘刺痛等诸症。<br>\r\n      　　[附注] 本方性平，为白脉病和黄水病常用主方。方中以嘎日迪一5(五凤丸)为主，故杀粘止痛，祛风湿功效俱全，对风湿、关节疼痛，白喉、炭疽等各种粘性疾病均有疗效。加配专治白脉病及脑脊髓疾病良药磁石、珍珠和清脉清脑药珊瑚、禹粮土及理气药香、沉香、肉豆蔻等，故对半身不遂、左瘫右痪，口眼歪斜，言语不清颇有奇效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (490, 2, 45, '扫布德一25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　扫布德一25<br>\r\n      <br>\r\n      　　[异名] 莫迪格一25、二十五味珍珠丸<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 珍珠(制)3g 石膏3g 红花3g 肉豆蔻 3g 白蔻3g 草果3g 紫檀香3g 沉香3g 丁香3g 海金沙3g 檀香3g 木香3g \r\n      冬葵果3g 方海3g 肉桂3g 白苣胜3g 牛黄1.5g 犀角1.5g 黑苣胜3g 荜拨3g 麝香0.5g 川楝子3g 栀子33g 诃子3g 地锦草3g<br>\r\n      　　[功能与主治] 清热安神，舒筋活血。用于白脉病，痹证，萨病，布氏病，风湿病，神经麻痹，肾损脉热，久热不愈等症。<br>\r\n      　　[附注] 本方性凉，为专治白脉病良方之一，其成分，功能主治与额日敦乌日勒(三普拉淖日布)基本相似。但对治疗黄水病、风湿病及骨关节病，额日敦乌日勒的疗效比较强。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (491, 2, 46, '哈布德仁-9', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　哈布德仁-9 <br>\r\n      <br>\r\n      　　[异名] 八味瑞香狼毒散 <br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]小狼毒l0g 大黄l0g 亚大黄l0g 翻白草 l0g 姜黄10g 水菖蒲l0g 生草乌l0g 黄精10g 天冬10g<br>\r\n      　　[功能与主治]杀粘、消肿，止痛。用于急性腮腺炎，乳腺炎，痈疖，蜂窝组织炎，淋巴结炎，皮下脓肿，丹毒，无名肿毒等红肿热痛症。<br>\r\n      　　[附注]本方性凉，为外用消肿良方。方中以杀粘，止腐消肿药小狼毒为主，配大黄、亚大黄以疗伤，黄精、天冬以燥黄水，翻白草以清热解毒、杀粘消肿，姜黄以杀粘、止腐疗伤，水菖蒲、草乌以杀粘、燥黄水、止痈。各药合用，具备了杀粘消肿，止腐疗伤，燥黄水，止痛功效，对各种无名肿毒均有良效，尤其对腮腺炎、乳腺炎颇有奇效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (492, 2, 46, '亚森乃一25', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　亚森乃一25<br>\r\n      <br>\r\n      　　[异名]　二十五味接骨散<br>\r\n      　　[来源] 《蒙医成方选》<br>\r\n      　　[处方]田三七10g 龙骨10g 通经草l0g 西红花10g 甘草10g 蓝刺头10g 文冠木10g 杜仲10g 金诃子10g 黑云香10g \r\n      石韦10g 白云香10g 秦艽花10g 麻黄l0g 翻白草10g 胡黄连10g 酸模10g 赤石脂10g 亚大黄l0g 朱砂10g 代赭石10g \r\n      白苣胜10g 熊胆1g 耧斗菜10g 麝香1<br>\r\n      　　[功能与主治]疗伤接骨，止血镇痛。用于跌打损伤，外伤骨折。<br>\r\n      　　[附注]本方性凉，为接骨良方。此方主要以三七、赤石脂、赭石、杜仲、朱砂、龙骨、蓝刺头、石韦、亚大黄等疗伤接骨药和熊胆、红花、麻黄、秦艽花、通经草、翻白草等清热止血药及黑云香、麝香等杀粘消肿、止痛药物所组成。故对跌打损伤，骨折各种外伤出血均有显著疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (493, 2, 46, '阿日森乃・额莫', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　阿日森乃・额莫<br>\r\n      <br>\r\n      　　[异名] 治癣搽剂<br>\r\n      　　[来源] 《阿拉坦仓医案》<br>\r\n      　　[处方] 水银(制)l0g 铜灰10g 硫磺（制）10g 雄黄10g 轻粉10g<br>\r\n      　　[功能与主治]燥黄水，止痒。用于牛皮癣，黄水疮，疥疮，苔癣，癣，粉刺，骚痒症等皮肤病。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (494, 2, 46, '哈塔嘎其一7', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　哈塔嘎其一7<br>\r\n      　<br>\r\n      　　[异名] 嘎木朱尔、七味枯疮散<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方] 寒水石(制)21g 冰片15g 雄黄6g 决明(制)3g 朱砂3g 银朱3g 麝香0.3g<br>\r\n      　　[功能与主治] 生肌，收敛，杀粘。用于口舌生疮，咽喉红肿，皮肤溃烂，外伤感染，宫颈糜烂，各种溃疡，炎症<br>\r\n      　　[附注] 本方性凉，为外用消炎良方。方中以清巴达干热、疗伤药寒水石为主，配以清热解毒、消肿止痛药冰片为辅；雄黄以止腐敛疮，杀粘消肿，银朱、朱砂以清热，疗伤，石决明以燥黄水，疗伤，麝香以杀粘，止痛；共为佐使。各药合用，具备了生肌疗伤，杀粘消炎功效，故对各种溃疡，炎症均有特效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (495, 2, 46, '呼其图一7　　', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　呼其图一7　　<br>\r\n      <br>\r\n      　　[异名] 七力散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 血竭30g 儿茶7.2g 西红花4.5g 朱砂3.9g 白云香4.5g 冰片3.6g 麝香3.6g<br>\r\n      　　[功能与主治] 化瘀消肿，止痛止血。用于跌打损伤，骨折瘀血，筋腱内伤，脉伤出血。<br>\r\n      　　[附注] 本方性凉，为专治跌打损伤良药。方中以止血、生肌、消肿药血竭为主，配合儿茶以燥黄水、生肌、敛疮，为辅；配合白云香以燥黄水，消肿疗伤，红花以凉血活血，朱砂以固骨质，麝香以杀粘止痛，共为佐使药。各药合用，对跌打损伤，外伤出血均有显著疗效。原方为八味，乃不符合其方名，故本方去掉黑云香。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (496, 2, 46, '呼其图一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　 呼其图一8 <br>\r\n      <br>\r\n      　　[异名] 八力散。<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 乳香9g 血竭6g 朱砂6g 熊胆6g 象牙6g 鹿茸6g 粳米6g 青羊血6g<br>\r\n      　　[功能与主治] 疗伤，止血，镇痛。用于跌打损伤，内外伤，瘀血疼痛，骨折，刀伤，肌腱脉伤，外伤出血等。<br>\r\n      　　[附注] 本方性凉，为专治跌打损伤良药。方中以活血止痛，燥黄水药乳香为主，配合熊胆以锁脉止血，青羊血以镇痛、止血化瘀，象牙以生肌、疗伤，鹿茸以舒经活血、强筋骨，血竭以止血、生肌、消肿，粳 \r\n      米以清希拉热、止吐壮阳，朱砂以疗伤、固骨质。各药合用，对跌打损伤，外伤出血，骨折等均有疗效。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (497, 2, 46, '孜德辣嘎一6', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　孜德辣嘎一6<br>\r\n      <br>\r\n      　　[异名] 尚布如木乃一6、六味痔克散<br>\r\n      　　[来源] 《蒙医传统验方》<br>\r\n      　　[处方] 辣椒5g 诃子10g 信筒子10g 连翘10g 铁线莲10g 山柰10g<br>\r\n      　　[功能与主治] 温中，散瘀，杀虫。用于内外痔疮，肛瘘等症。<br>\r\n      　　[附注] 本方性温，为专治痔疮常用方。方中以辣椒(小米椒)为主，原方为各药等量，本方参阅&lt;蒙成药标准)辣椒量为5g。辣椒以温中，杀虫，消水肿，消“奇哈”，信筒子以消肿、杀虫，山柰以行血化瘀，连翘以清腑热、止泻，铁线莲以破痞、消肿、燥黄水，诃广以调理体素，解毒。各药合用，对内外痔疮，肛瘘均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (498, 2, 47, '粘乃・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>粘乃・脱落布日<br>\r\n      <br>\r\n      　　[异名] 泻粘丸<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方]狼毒(制)30g 大黄15g 五灵脂24g 草乌 (制)6g 藁本6g 水菖蒲6g \r\n      广木香6g 姜黄6g 黑云香6g 硫黄(制)3g 诃子6g 雄黄3g 黑矾(制)1.5g 麝香1.5g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]泻粘热。用于粘热引起的胸胁刺痛，胃痉挛，胃脘胀满，结喉，炭疽，疥痈等粘、血热性疾病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;　　[附注]本方性凉，为泻粘主方。方中以峻泻药狼毒为主，以缓泻药大黄为辅；配合以草乌、黑云香、麝香、水菖蒲、姜黄、藁本等杀粘药物。故泻粘邪功效具全,对粘热引起的各种粘病具有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (499, 2, 47, '乌兰・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　乌兰・脱落布日<br>\r\n      <br>\r\n      　　[异名] 泻粘红丸<br>\r\n      　　[来源] 《库伦蒙药厂验方》<br>\r\n      　　[处方] 狼毒(制)10g 诃子6g 西红花l0g 黑云香6g 甘松4g 藜芦(制)6g 石菖蒲6g 贯众4g 刺柏叶4g 草乌(制)4g \r\n      麝香0.1g 白硇砂4g<br>\r\n      　　[功能与主治]凉血，解毒，杀粘,泻火。用于血热，枯热、瘟疫，结喉，炭疽，虫痧,乳腺炎,肌筋抽搐，食积不消，胃痉挛。<br>\r\n      　　[附注]木方性凉，为泻粘热常用方，方中以峻泻药狼毒、藜芦为主，以凉血药红花为辅；配以草乌、黑云香、麝香、石菖蒲等杀粘邪药物为佐使。故泻粘，凉血功效具全,对粘热,血热,疫热,毒热等热症均有显著疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (500, 2, 47, '额力根・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　额力根・脱落布日 <br>\r\n      <br>\r\n      　　[异名] 泻肝丸<br>\r\n      　　[来源] 《蒙医金匮》<br>\r\n      　　[处方] 藜芦(制)30g 诃子l5g 腊肠果25g 西红花5g 瞿麦5g， 硼砂{制}5g 五灵脂5g<br>\r\n      　　[功能与主治]泻肝火。用于肝热，肝硬化，肝肿大，腹水，肝区作痛等。<br>\r\n      　　[附注]本方性凉，为专治肝脏病泻剂。方中以峻泻药藜芦为主，以缓泻药腊肠果为辅；配合红花、瞿麦、五灵脂等凉血清肝药，硼砂以活血、破痞，诃子以解毒、调元，均为佐使。故对肝火，肝硬化腹水，肝肿痛等肝脏病均有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (501, 2, 47, '吉如很・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　吉如很・脱落布日<br>\r\n      <br>\r\n      　　[异名] 泻心丸<br>\r\n      　　[来源] 《古纳巴达拉验方》<br>\r\n      　　[处方] 巴豆(制)1.5g 沉香3g 肉豆蔻3g 广酸枣3g 朱砂3g<br>\r\n      　　[功能与主治]泻心赫依热，强心,镇静.用于陈旧性心赫依热，心绞痛、癜狂症。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (502, 2, 47, '嘎勒注因・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　嘎勒注因・脱落布日<br>\r\n      <br>\r\n      　　[异名] 泻狂剂<br>\r\n      　　[来源] 《蒙药验方》<br>\r\n      　　[处方]斑蝥(制)25g 车前子15g 关木通15g白云香l5g 稻米l0g<br>\r\n      　　[功能与主治]利尿，泻狂犬毒。主治狂犬病。<br>\r\n      　　[附注]本方性凉，为专治狂犬病良药，方中以利尿，逐泻脉疾，攻毒药斑蝥为主，配合车前子以利尿，关木通以清热解毒、白云香以疗伤，解毒，稻米以滋补强身并止吐；五味合用，具备利尿解毒功效，故对狂犬病颇有疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (503, 2, 47, '格喜古讷一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　格喜古讷一3汤<br>\r\n      <br>\r\n      　　[异名] 朱木查一3汤、三味大黄汤<br>\r\n      　　[来源] 《珊瑚验方集》<br>\r\n      　　[处方] 大黄15g 面碱15g 诃子15g<br>\r\n      　　[功能与主治] 缓泻。用于大便秘结，胃胀，腹痛，闭经。<br>\r\n      　　[附注] 本方性凉，为临床常用缓泻剂。方中以缓泻药大黄为主，配润肠通便药面碱和解毒调元药诃子为辅佐。三药合用，对大便干燥，闭经颇有疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (504, 2, 47, '哈日西无盖・脱落布日', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　哈日西无盖・脱落布日<br>\r\n      <br>\r\n      　　[异名] 敏吞滚召木<br>\r\n      　　[来源] 《蒙医药选编》<br>\r\n      　　[处方] 狼毒(制)9g 面碱6g 藜芦(制)9g 巴豆(制)6g大黄 15g 草乌(制)6g 广木香6g 诃子12g 五灵脂6g 黑云香6g \r\n      麝香1.5g 石菖蒲 3g<br>\r\n      　　[功能与主治] 杀粘，泻毒。用于巴达干包如，食积不消，瘟疫，麻疹，天花，白喉，炭疽，丹毒，粘痧，黄水疮<br>\r\n      　　[附注] 本方性平，为寒热症常用泻剂。方中以寒热症总泻药藜芦和峻泻药狼毒为主，配合格喜古讷一3汤(三味大黄汤)和嘎日迪一5(五凤丸)，故杀粘、攻毒、泻火攻效俱全，对瘟疫，天花，白喉，粘痧等粘症及长期食积不消症均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (505, 2, 48, '章古一3汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　<br>\r\n      　　章古一3汤<br>\r\n      <br>\r\n      　　[异名] 依曼章古一3汤、色玛一3汤、三味蒺藜汤<br>\r\n      　　[处方] 蒺藜20g 冬葵果20g 方海20g<br>\r\n      　　[功能与主治] 利尿。用于膀胱热，尿闭，水肿，浮肿。<br>\r\n      　　[附注] 本方性平、偏凉，为尿闭专用方。方中以利尿消肿药蒺藜为主，配方海、冬葵果均有利尿消肿作用。故对肾、膀胱病引起的各种尿闭、浮肿、水肿均有奇效。本方加海金沙适量，疗效更佳。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (506, 2, 48, '霞日嘎一4汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　霞日嘎一4汤<br>\r\n      <br>\r\n      　　[异名] 沙日嘎一4汤、勇瓦西汤、四味姜黄汤<br>\r\n      　　[处方] 姜黄15g 蒺藜15g 栀子12g 黄柏皮9g<br>\r\n      　　[功能与主治] 利尿，泻湿热。用于小便闭止，尿频，尿急，尿中带血，膀胱热，膀胱刺痛，淋病。<br>\r\n      　　[附注] 本方性凉，为尿路感染常用方。方中以杀粘、解毒药姜黄为主，配合蒺藜以利尿消肿、补肾，栀子以凉血、调元，黄柏皮以清热、止血。四药合用，对膀胱炎，尿路感染，尿血均有良效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (507, 2, 48, '洛亚斯一3', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　洛亚斯一3<br>\r\n      <br>\r\n      　　[异名]布茹各瑞一3、三味龙骨散<br>\r\n      　　[处方] 龙骨(制)30g 车前子30g 熊胆3g<br>\r\n      　　[功能与主治] 利尿，固精，止血。用于寒热性尿频，尿浊，尿中带血，遗精。<br>\r\n      　　[附注] 本方性凉，为治遗精良方。方中以杀粘、敛伤药龙骨为主，配合利尿、止泻药车前子和止血药熊胆。三药合用，对遗精、尿浊、尿中带血均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (508, 2, 48, '额勒斯一8', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　额勒斯一8<br>\r\n      <br>\r\n      　　[异名] 阿拉坦・额勒斯一8、八味海金沙散<br>\r\n      　　[处方] 海金沙9g 白豆蔻9g 冬葵果15g 白硇砂15g 天花粉12g 蒺藜6g 方海12g 蜗牛壳42g<br>\r\n      　　[功能与主治] 利水，消肿。用于寒热性尿闭，水肿，泌尿道结石。<br>\r\n      　　[附注] 本方性平，为利尿良方。方中以利尿，化石药海金沙为主，配合冬葵果、白硇砂、方海、蒺藜、蜗牛壳等利尿消肿药，白豆蔻以祛肾寒、补肾，天花粉以滋补、壮阳。各药合用，对尿闭、泌尿道结石均有良效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (509, 2, 48, '霞日嘎一10', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　　霞日嘎一10<br>\r\n      <br>\r\n      　　[异名] 勇瓦尼吉乐、十味姜黄散<br>\r\n      　　[处方] 姜黄10g 天花粉10g 荜拨10g 蒺藜 10g 天门冬10g 红花10g 肉桂15g 冬葵果15g 玉竹15g 白豆蔻 15g<br>\r\n      　　[功能与主治] 滋补，益气，利尿。用于尿道灼痛，尿频，尿浊，遗精。<br>\r\n      　　[附注] 本方性温，为补益肾气，利尿良方。方中以杀粘、解毒药姜黄为主，配以冬葵果、蒺藜等利尿药，白豆蔻以补气益肾，玉竹、天门冬、天花粉以滋补壮阳，肉桂、荜拨以温中调火，红花以凉血益肝。各药合用，对肾寒肾虚引起尿频、遗精均有良效。 \r\n      <p></p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (510, 2, 49, '小儿清热三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　三臣丸(小儿清热三味丸、图喜木勒-3)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 牛 黄 250g&nbsp;&nbsp; 红 花 250g&nbsp;&nbsp; 天竺黄 \r\n      250g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上三味，除牛黄外，红花、天竺黄粉碎成细粉，将牛黄研细，与以上细粉配研，过筛，混匀，用水泛丸，用朱砂0.25g包衣，打光，干燥、即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为红色水丸，除去包衣显黄色；气香，味苦、微甘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 熄风降火。用于小儿瘟热高烧，肺热咳嗽，各种惊风。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，满一月小儿，一次3粒，逐月增加1粒，满一周岁小儿，一次服15粒，两岁以上儿童遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每25粒重lg。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (511, 2, 49, '土木香十味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　土木香十味汤散(玛努一10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 土木香 5g&nbsp;&nbsp; 木鳖子(制)15g&nbsp;&nbsp; 龙骨(制)15g&nbsp;&nbsp; \r\n      梅花草 15g &nbsp;&nbsp;诃 子 10g &nbsp;&nbsp;苦 参 10g&nbsp;&nbsp; 玫瑰花10g&nbsp;&nbsp; \r\n      珍珠(奶制) 5g &nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;干 姜2.5g &nbsp;&nbsp;鹿颅骨(制)15g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十味，除珍珠研细外，其余九味粉碎成中粉，与珍珠研细配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为浅灰白色粉末；气香，味微涩，苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 熄风火，除湿热。用于脑热头痛，血热头痛，鼻炎，牙痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3g，―日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每瓶装30g。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (512, 2, 49, '土木香五味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　码努-5(土木香五味汤散)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 土木香 200g &nbsp;&nbsp;悬钩子木100g&nbsp;&nbsp; 苦参200g&nbsp;&nbsp;&nbsp; \r\n      干 姜 50g &nbsp;&nbsp;马钱子(制)10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上五味，粉碎成中粉，过筛，混匀,即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为浅黄色粉末；气香，味极苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 调“赫依”血，止痛，消喘。用于“赫依”“血”引起的头痛，头晕，胸闷，气短。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3--5g，一日l--2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (513, 2, 49, '土茯苓七味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　土茯苓七味汤散(陶匹浪--7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 土茯苓 100g &nbsp;&nbsp;金银花 100g &nbsp;&nbsp;诃子80g \r\n      &nbsp;&nbsp;瞿麦150g &nbsp;&nbsp;栀 子 80g &nbsp;&nbsp;黄 连 150g &nbsp;&nbsp;川楝子 \r\n      80g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上七味，粉碎成中粉，过筛，混匀，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为浅黄色粉末；味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清血热，止痛。用于血热引起的头痛，鼻子红肿，咽喉肿痛，经血过多<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3～5g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (514, 2, 49, '小儿清肺八味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　小儿清肺八味丸(胡勒森竹岗一8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方)&nbsp;天竺黄 50g &nbsp;&nbsp;红 花 40g &nbsp;&nbsp;牛 \r\n      黄 15g &nbsp;&nbsp;拳 参 40g &nbsp;&nbsp;北沙参 40g &nbsp;&nbsp;胡黄连 5g &nbsp;&nbsp;檀 \r\n      香 5g &nbsp;&nbsp;麦 冬 50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上八味，除牛黄外，其余天竺黄等七味粉碎成细粉，过筛，将牛黄研与以上细粉配研，混匀，用水泛丸，用朱砂0.1g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为暗红色水丸，除去包衣显浅黄色；气香，味苦、微甘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清肺热，止咳定喘。用于小儿肺热，发烧，咳嗽，气促，瘟疫热盛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一周岁小儿，一次7～11粒，一日l～3次，或遵医嘱。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (515, 2, 49, '云香十五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　云香十五味丸(嘎日迪一15)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 制草乌 40g &nbsp;&nbsp;木 香 40g &nbsp;&nbsp;石菖蒲 \r\n      40g &nbsp;&nbsp;栀 子 40g &nbsp;&nbsp;川楝子 40g &nbsp;&nbsp;决明子 40g &nbsp;&nbsp;枫香脂 \r\n      40g &nbsp;&nbsp;五灵脂 40g &nbsp;&nbsp;文冠木 40g &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑云香 \r\n      40g &nbsp;&nbsp;沉 香 40g &nbsp;&nbsp;麝 香 2g&nbsp;&nbsp;&nbsp; &nbsp;诃 子 40g \r\n      &nbsp;&nbsp;瞿 麦 40g &nbsp;&nbsp;&nbsp;苘麻子40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十五味，除麝香研细，其余制草乌等十四味粉碎成细粉，与麝香细粉配研，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治)&nbsp;燥“协日乌素”，消“粘”，消肿。用于风温性关节炎，类风湿，“巴木”病游痛症，疮疡，梅毒。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，―次9～15粒，一日l～2次，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(注意) 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (516, 2, 49, '止吐六味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　止吐六味散(希和日额布斯一6)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方)&nbsp;甘 草 100g &nbsp;&nbsp;大米(微炒)100g &nbsp;&nbsp;柿子100g \r\n      &nbsp;&nbsp;蔓荆子 100g &nbsp;&nbsp;小茴香 100g &nbsp;&nbsp;芫荽果 100g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上六味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄色粉末；气香，味甘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 止吐。用于呕吐，妊娠呕吐等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次1.5--3g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (517, 2, 49, '止血八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　止血八味散(琪顺古日古木一8) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;(处方) 西红花 100g &nbsp;&nbsp;熊 胆 100g &nbsp;&nbsp;豌豆花 \r\n        80g &nbsp;&nbsp;木鳖子(制) 50g &nbsp;&nbsp;紫檀香 70g &nbsp;&nbsp;银 朱 50g &nbsp;&nbsp;地锦草 \r\n        70g &nbsp;&nbsp;射 干 50g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(制法)&nbsp;以上八味，除西红花、熊胆分别研成细粉，与上述细粉配研，过筛，混匀，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为棕红色粉末；气微香，味微涩、苦。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清热、止血。用于“宝日”病，胃肠出血，外伤流血，鼻衄，月经过多等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次1.5―3g，一日1--2次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。</p>\r\n    <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (518, 2, 49, '止痢十五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　止痢十五味丸(音达拉一15)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 连 翘 50g &nbsp;&nbsp;麦 冬 &nbsp;40g &nbsp;&nbsp;拳 \r\n      参 &nbsp;40g &nbsp;&nbsp;&nbsp;石 膏 &nbsp;30g &nbsp;&nbsp;&nbsp;牛 黄 40g &nbsp;五灵脂&nbsp;&nbsp;100g \r\n      &nbsp;&nbsp;黑云香 42g &nbsp;&nbsp;黑冰片 26g &nbsp;&nbsp;草乌叶 40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;荜 茇 40g &nbsp;&nbsp;光明盐 \r\n      10g &nbsp;&nbsp;红花 &nbsp;&nbsp;40g &nbsp;&nbsp;&nbsp;关木通 34g &nbsp;&nbsp;&nbsp;麝 \r\n      香 8g &nbsp;&nbsp;&nbsp;木鳖子(制)&nbsp;34g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十五味，除牛黄、麝香外，其余连翘等十三味粉碎成细粉，将牛黄、麝香研细，与以上细粉配研，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黑褐色水丸；气微，味苦、辛、咸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清腹热，消“粘”，止泻。用于热性腹痛，腹泻，痢疾。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次11～15粒，一日1--2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (519, 2, 49, '止痢十五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　止痢十五味散(音达拉一15)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp; (处方)&nbsp;连 翘 &nbsp;50g&nbsp; 麦 冬 &nbsp;40g&nbsp;&nbsp; \r\n      拳 参 34g &nbsp;&nbsp;关木通 &nbsp;&nbsp;&nbsp;30g &nbsp;石 膏 70g &nbsp;牛黄 &nbsp;&nbsp;40g \r\n      &nbsp;五灵脂 l00g &nbsp;&nbsp;麝 香 8g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑云香 42g \r\n      &nbsp;黑冰片 26g &nbsp;&nbsp;草乌叶 40g &nbsp;木鳖子(制)34g &nbsp;荜 茇 40g &nbsp;光明盐 \r\n      10g &nbsp;红 花 &nbsp;40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法)&nbsp;以上十五味，除麝香、牛黄外，其余连翘等十三味粉碎成细粉，将麝香及牛黄研细，与以上粉末配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黑褐色粉末；气微香，味咸 、苦、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清腹热，消“粘”，止泻。用于热性腹痛，腹泻，痢疾。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次1.5～3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (520, 2, 49, '止痢七味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　止痢七味散(苏斯--7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 牛胆粉 50g &nbsp;连 翘 150g &nbsp;木鳖子(制)125g&nbsp;香 \r\n      附 l00g &nbsp;关木通 100g &nbsp;丹 参75g &nbsp;麦 冬 125g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上七味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄色粉末；气微腥，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治)&nbsp;清热，止泻止痢。用于“协日”引起的腹泻，腹胀，赤白痢疾。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次1.5―3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。 <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (521, 2, 49, '牛黄十三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　牛黄十三味丸(给旺-13)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 红 花 3g &nbsp;&nbsp;关木通 3g &nbsp;木鳖子(制)3g &nbsp;瞿 \r\n      麦 3g &nbsp;苦地丁 3g&nbsp;五灵脂 3g &nbsp;土木香 3g &nbsp;蓝盆花3g &nbsp;牛黄3g &nbsp;木香3g \r\n      &nbsp;香青兰3g &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沙棘3g&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;芫荽菜3g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十三味，除牛黄外，其余红花等十二味粉碎成细粉，牛黄研细，与上述细粉配研，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄棕色水丸；气微，味苦。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清热，凉血，止血。用于肝“宝日”，月经过多，呕血，外伤出血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次9～13粒，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (522, 2, 49, '扎冲十三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　扎冲十三味丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方)&nbsp;诃 子 100g 麝 香&nbsp;&nbsp;&nbsp;10g 肉豆蔻 \r\n      &nbsp;30g &nbsp;&nbsp;甘 草 &nbsp;40g 制草乌 100g 珊瑚(制) 30g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沉 香&nbsp;40g \r\n      &nbsp;石菖蒲 &nbsp;80g 珍珠(制)30g &nbsp;&nbsp;禹粮土 30g 木 香&nbsp;&nbsp;60g &nbsp;丁 \r\n      香 &nbsp;&nbsp;&nbsp;20g &nbsp;&nbsp;磁石(煅)20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十三味，除麝香、珊瑚、珍珠、磁石外，其余诃子等九味，粉碎成细 粉，将麝香、珊瑚、珍珠、磁石粉碎成细粉，与上述细粉配研，过筛，混匀，用水泛丸，用朱砂0.15g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为暗红色水丸，除去包衣显棕红色；气香，味苦、辛、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 祛风通窍，舒筋活血，镇静安神，除“协日乌素”。用于半身不遂，左瘫右痪，口眼歪邪，四肢麻木，腰腿不利，言语不清，筋骨疼痛，神经麻痹，风湿，关节疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次5～9粒(2g／10粒)，10～20粒(1g／10粒)，一日l次，晚间临睡前服，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(注意) 孕妇忌服，年老体弱者慎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g；每10粒1g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (523, 2, 49, '升阳十一味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　 升阳十一味丸(那仁满都拉)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 石 榴 100g &nbsp;蒺藜(微炒)&nbsp;30g &nbsp;荜 茇 40g \r\n      &nbsp;益 智 50g &nbsp;冬葵果 30g &nbsp;肉 桂 10g &nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄 精&nbsp;30g \r\n      &nbsp;&nbsp;红 花 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;30g &nbsp;天 冬 30g &nbsp;玉 \r\n      竹 40g &nbsp;天花粉 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十一味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄褐色水丸；气香，味辛、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 温肾，利水，消食，燥“协日乌素”。用于胃寒，消化不良，浮肿， 水肿，肾寒腰痛，遣精淋下，寒性腹泻，宫寒带多。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次11--13粒，一日 1―2次，红糖水或温开水送服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (524, 2, 49, '乌兰十三味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　乌兰十三味汤散<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 悬钩子木 40g &nbsp;山 柰 &nbsp;&nbsp;&nbsp;&nbsp;5g \r\n      &nbsp;&nbsp;&nbsp;&nbsp;栀 子 30g &nbsp;&nbsp;&nbsp;&nbsp;茜 草 10g &nbsp;&nbsp;&nbsp;橡 \r\n      子 30g &nbsp;&nbsp;紫 草 5g &nbsp;&nbsp;土木香&nbsp;10g &nbsp;&nbsp;诃子&nbsp;10g \r\n      &nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枇杷叶&nbsp;10g \r\n      &nbsp;&nbsp;&nbsp;金莲花&nbsp;&nbsp;&nbsp;10g &nbsp;&nbsp;&nbsp;&nbsp;苦参&nbsp;&nbsp;10g \r\n      &nbsp;&nbsp;&nbsp;&nbsp;川楝子10g &nbsp;&nbsp;&nbsp;紫草茸30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十三味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为红棕色粉末；气微香，味苦、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清血热。用于血热上盛，头痛，目赤，高血压症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3～5g，一日l一3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (525, 2, 49, '乌兰三味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　 乌兰三味汤散 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 紫草茸 100g&nbsp;&nbsp;&nbsp; 茜 草100g &nbsp;&nbsp;枇杷叶 \r\n      100g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上三味，粉碎成中粉，过筛，混匀,即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为棕红色粉末；气微香，味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 清血热。用于肺、肾损伤性热，肺热咳嗽，痰中带血，膀胱刺痛，尿频尿痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3～5g，一日l～3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (526, 2, 49, '风湿二十五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　风湿二十五味丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方)&nbsp;檀 香&nbsp;12g &nbsp;&nbsp;闹羊花&nbsp;&nbsp;&nbsp;20g \r\n      &nbsp;&nbsp;白豆蔻&nbsp;14g &nbsp;&nbsp;麝 香&nbsp;&nbsp;14g &nbsp;&nbsp;肉豆蔻&nbsp;&nbsp;&nbsp;24g \r\n      &nbsp;&nbsp;木棉花蕊&nbsp;16g &nbsp;&nbsp;紫檀香&nbsp;20g &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牛 黄&nbsp;14g \r\n      &nbsp;&nbsp;紫花地丁&nbsp;14g &nbsp;&nbsp;漏芦花&nbsp;14g &nbsp;&nbsp;苘麻子&nbsp;30g \r\n      &nbsp;&nbsp;木棉花瓣&nbsp;16g &nbsp;&nbsp;驴血粉&nbsp;&nbsp;&nbsp;100g &nbsp;栀子&nbsp;&nbsp;&nbsp;20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;草果&nbsp;&nbsp;14g \r\n      &nbsp;&nbsp;川楝子&nbsp;&nbsp;&nbsp;14g &nbsp;&nbsp;玉簪花&nbsp;14g &nbsp;&nbsp;草决明&nbsp;30g \r\n      &nbsp;&nbsp;杜仲&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;16g &nbsp;&nbsp;苦参&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20g \r\n      &nbsp;&nbsp;西红花&nbsp;14g &nbsp;&nbsp;诃子&nbsp;14g &nbsp;&nbsp;石膏&nbsp;4g \r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枫香脂&nbsp;30g \r\n      &nbsp;丁香16g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上二十五味，除牛黄、麝香、西红花外，其余驴血粉等二十二味粉碎成细粉，将牛黄、麝香、西红花分别研细，与上述细粉配研，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄棕色水丸；气微香，味微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 燥“协日乌素”，散瘀。用于游痛症，关节炎，类风湿。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次11―15粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (527, 2, 49, '风湿三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　 风湿三味丸(琪素一3)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 驴血粉 50g &nbsp;&nbsp;细 辛50g &nbsp;&nbsp;山 柰 \r\n      50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上三味，粉碎成细粉，过筛，混匀用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为红褐色水丸；味辛，有腥气。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治)&nbsp;祛风除湿，止痒，止痛。用于风湿性关节疼痛，腰腿疼痛，风湿疙瘩，周身瘙痒。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 口服，一次5--15粒(2g／10粒)，8～18粒，(1.25g／10粒)，一日1-2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每10粒重2g；每10粒重1.25g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(贮藏) 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (528, 2, 49, '文冠木十味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　文冠木十味汤散(森登--10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(处方) 诃子 &nbsp;&nbsp;&nbsp;50g &nbsp;&nbsp;&nbsp;栀 \r\n      子 &nbsp;50g &nbsp;&nbsp;草决明 50g &nbsp;&nbsp;川楝子 50g&nbsp;&nbsp;黑云香 50g &nbsp;&nbsp;黄 \r\n      柏 50g &nbsp;&nbsp;枫香脂 50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;苘麻子 &nbsp;50g&nbsp;&nbsp;&nbsp;&nbsp;五灵脂 \r\n      50g &nbsp;&nbsp;文冠木 25g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(制法) 以上十味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(性状) 本品为黄棕色粉末；气微，味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(功能与主治) 燥“协日乌素”，行血止痛。用于游痛症，关节肿痛，类风湿。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(用法与用量) 水煎服，一次3～5g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(规格) 每袋装15g。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (529, 2, 49, '文冠木软膏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　 文冠木软膏(伊利敦阿日善) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 文冠木浸膏 309g &nbsp;&nbsp;黄 连 70g &nbsp;&nbsp;牛 \r\n        黄 2.8g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上三味，黄连、牛黄分别粉碎成细粉，将凡士林127g、羊毛脂63g加热至100度c灭菌半小时，放冷至80~c，加入80~c熔融的硬脂酸85g、白腊123g、松香142g，搅拌均匀。再渐渐加入预热溶解的氢氧化钾6g、甘油21g的溶液，边加边搅拌，趁热加入文冠木浸膏、黄连粉、牛黄粉、滑石粉，充分搅拌均匀，降温后加入香精，继续搅拌至冷凝，即可。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为绿黑色的软膏；味苦，气微香。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热解毒，活血化瘀，生肌敛疮，祛风燥湿。用于各种类型的顽固性牛皮癣，对神经性皮炎及其它皮肤病也具有一定的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 洗净患部，涂抹患部；一日1―2次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶装40g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[注意] 用药期间禁饮酒类，忌辛辣食物。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，置阴凉处。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;注：文冠木浸膏的制法：取文冠木，切成厚2mm，长宽不超过4cm的碎片，加4― 5倍量的乙醇，回流提取三次，提取时间依次为2小时，1.5小时，1小时，滤过，合并滤液，回收乙醇、收膏备用。得膏率为10％。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (530, 2, 49, '巴特日七味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　 巴特日七味丸\r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 草乌叶 250g &nbsp;&nbsp;诃 子 250g &nbsp;&nbsp;翻白草 \r\n        l00g &nbsp;&nbsp;茜 草 l00g &nbsp;&nbsp;黑云香 l00g &nbsp;&nbsp;麝 香 0.5g &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;银 \r\n        朱 &nbsp;50g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，除麝香、银朱外，其余草乌叶等五味，粉碎成细粉，将麝香研细，与以上细粉配研，过筛，混匀，用水泛丸，银朱包衣，打光，干燥，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸，除去包衣显棕褐色；气香，味酸、涩麻、微苦。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清瘟解毒，消“粘”，止痛，散瘀，止痢。用于瘟疫盛热，脑炎，赤白痢疾，白喉，目黄，音哑，转筋。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～13粒，一日1～2次，或遵医嘱。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏} 密闭，防潮。 </p>\r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (531, 2, 49, '巴特日六味汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　巴特日六味汤(巴特日--6)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 木鳖子(制) 100g &nbsp;&nbsp;诃 子100g &nbsp;&nbsp;甘草&nbsp;100g&nbsp;&nbsp;白豆蔻 \r\n      100g &nbsp;&nbsp;大 米(微炒)100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，加冰糖100g，粉碎成中粉，过筛,混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅黄色粉末；气香，味甘、涩。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调“协日”，止痛，止吐。用于寒热“协日”，呕吐，头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3―5g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (532, 2, 49, '玉簪清咽十五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>　玉簪清咽十五味散(哈斯哈图呼日--15)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 玉簪花 90g &nbsp;&nbsp;川楝子 &nbsp;15g &nbsp;&nbsp;广枣&nbsp;&nbsp;&nbsp;30g \r\n      &nbsp;&nbsp;檀香&nbsp;&nbsp;&nbsp;105g &nbsp;&nbsp;诃子&nbsp;&nbsp;&nbsp;36g \r\n      &nbsp;&nbsp;苦参&nbsp;&nbsp;33g &nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;肉豆蔻&nbsp;30g \r\n      &nbsp;&nbsp;瞿麦&nbsp;&nbsp;&nbsp;&nbsp;39g &nbsp;&nbsp;石膏 &nbsp;&nbsp;54g \r\n      &nbsp;&nbsp;北沙参 33g &nbsp;&nbsp;&nbsp;栀子&nbsp;&nbsp;105g &nbsp;&nbsp;丁香 \r\n      &nbsp;18g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甘 草 &nbsp;24g \r\n      &nbsp;&nbsp;木 香 &nbsp;&nbsp;36g&nbsp;&nbsp;&nbsp;沉香&nbsp;&nbsp;&nbsp;24g&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十五味，粉碎成细粉，过筛,混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色水丸；味苦，气香。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“巴达干”热。用于咽喉肿痛，气喘，音哑，胸肋刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1．5～3g，―日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (533, 2, 49, '玉簪清咽十五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>玉簪清咽十五味丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 玉簪花 90g &nbsp;&nbsp;川楝子 &nbsp;15g &nbsp;&nbsp;广枣 \r\n      &nbsp;&nbsp;30g &nbsp;&nbsp;檀香&nbsp;&nbsp;15 &nbsp;&nbsp;石膏 54g &nbsp;&nbsp;北沙参 \r\n      33g &nbsp;&nbsp;诃子 36g &nbsp;&nbsp;苦参33 &nbsp;&nbsp;栀子 105g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 丁香 &nbsp;&nbsp;18g \r\n      &nbsp;&nbsp;沉香 &nbsp;&nbsp;&nbsp;24g &nbsp;&nbsp;肉豆蔻&nbsp;30g &nbsp;&nbsp;甘 \r\n      草 24g &nbsp;木香 36g &nbsp;&nbsp;瞿 麦 &nbsp;39g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十五味，粉碎成细粉，过筛，混匀，用水泛丸，打光。干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅黄色粉末；气芳香，味甘、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“巴达干”热。用于咽喉肿痛，气喘，音哑，胸肋刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次20～30粒，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶30g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (534, 2, 49, '石膏二十五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 石膏二十五味散(朱岗--25)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 拳参&nbsp;&nbsp;&nbsp;25g &nbsp;北沙参&nbsp;25g \r\n      &nbsp;茵陈&nbsp;&nbsp;&nbsp;25g &nbsp;白巨胜&nbsp;25g &nbsp;关木通&nbsp;15g &nbsp;白葡萄&nbsp;25g \r\n      &nbsp;胡黄连&nbsp;5g &nbsp;&nbsp;丁香&nbsp;15g &nbsp;紫檀香&nbsp;15g &nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;肉豆蔻&nbsp;10g \r\n      &nbsp;檀 香&nbsp;&nbsp;15g &nbsp;白豆蔻&nbsp;15g &nbsp;石膏&nbsp;&nbsp;&nbsp;50g \r\n      &nbsp;茜草&nbsp;&nbsp;&nbsp;15g &nbsp;草果&nbsp;&nbsp;&nbsp;15g &nbsp;川楝子&nbsp;10g \r\n      &nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;牛黄&nbsp;&nbsp;&nbsp;25g \r\n      &nbsp;诃子&nbsp;&nbsp;&nbsp;10g &nbsp;远志&nbsp;&nbsp;&nbsp;25g &nbsp;沙棘&nbsp;&nbsp;&nbsp;25g \r\n      &nbsp;红花&nbsp;&nbsp;&nbsp;25g &nbsp;甘草&nbsp;&nbsp;&nbsp;25g &nbsp;青蒿&nbsp;&nbsp;&nbsp;15g \r\n      &nbsp;木香&nbsp;25g &nbsp;栀子&nbsp;&nbsp;&nbsp;10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十五味，除牛黄外，其余石膏等二十四味加冰糖180g粉碎成细粉，与牛黄配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡黄棕色粉末；气香，味微甘、涩苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肺止咳，祛痰，镇痛。用于肺热咳嗽，咯血，胸膜炎，肺脓肿，百日咳，肺痨。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格]&nbsp;每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (535, 2, 49, '地丁三味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 地丁三味汤散(地格达--3)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 苦地丁 250g &nbsp;&nbsp;龙骨(焖煅) 150g &nbsp;&nbsp;黑云香150g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上三味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰白色粉末；气香，味微苦。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 活血止痛。用于血热，头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煮服，一次3～5g，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g或30g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏) 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (536, 2, 49, '地锦草四味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 地锦草四味汤散(码勒根扎拉--4) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 地锦草 25g &nbsp;&nbsp;紫 草 25g &nbsp;&nbsp;瞿 \r\n        麦 20g &nbsp;&nbsp;白花龙胆 15g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为紫褐色粉末；气特异，味微涩，苦。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清血热，止血。用于鼻出血。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次2―3g，一日1～2次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶或袋装30g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (537, 2, 49, '吉祥安坤丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 吉祥安坤丸(乌力吉--18) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 益母草 80g &nbsp;沙 棘 &nbsp;50g &nbsp;五灵脂&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;30g \r\n        红花 &nbsp;30g 刺柏叶 30g &nbsp;&nbsp;土木香 20g&nbsp;&nbsp;&nbsp;&nbsp;丁 香 &nbsp;&nbsp;&nbsp;10g \r\n        &nbsp;&nbsp;鹿茸&nbsp;&nbsp;&nbsp;&nbsp;20g &nbsp;&nbsp;牛黄&nbsp;6g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;朱 砂 \r\n        &nbsp;5g &nbsp;&nbsp;牛胆粉&nbsp;6g &nbsp;&nbsp;硼砂(微炒)&nbsp;10g&nbsp;诃 子&nbsp;50g \r\n        山 柰&nbsp;&nbsp;30g&nbsp;&nbsp;&nbsp;小白蒿&nbsp;20 g &nbsp;&nbsp;冬虫夏草&nbsp;10g \r\n        &nbsp;&nbsp;赤匏子&nbsp;&nbsp;50g &nbsp;&nbsp;木香&nbsp;30g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法]&nbsp;以上十八味，除牛黄、鹿茸、朱砂外，其余益母草等十五味粉碎成细粉，将牛黄、鹿茸研细，与上述细粉配研，过筛，混匀，用水泛丸，朱砂包衣，打光，干燥，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显浅黄色；气香，味苦、微酸。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调经活血，补气安神。用于月经不调，产后发烧，心神不安，头昏头痛，腰膝无力，四肢浮肿，乳腺肿胀。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15粒，一日1～2次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g；每10粒重0.8g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (538, 2, 49, '协日嘎四味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>协日嘎四味汤散 \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 姜 黄 150g 黄 柏 90g 栀 子 120g 蒺藜(微炒) 150g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄色粉末；味苦、涩。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 利尿，泻湿热。用于小便闭止，尿频，尿急，尿中带血，膀胱刺痛。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3-5g，―日1--3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (539, 2, 49, '光明盐四味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>光明盐四味胶囊(毛勒日达布苏--4) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 光明盐 225g &nbsp;&nbsp;诃 子 225g &nbsp;&nbsp;筚茇 \r\n        225g &nbsp;&nbsp;干 姜 225g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，光明盐粉碎成细粉；诃子加水煎煮二次，第一次3小时，第二次2小时，合并煎液，滤过，滤液浓缩成膏；荜茇、干姜加70％乙醇回流提取二次，第一次2小时，第二次1.5小时，合并提取液，滤过，滤液减压回收乙醇、浓缩成膏，与上述诃子膏合并，加入光明盐细粉，混匀，低温干燥，粉碎成细粉，制粒，干燥，装胶囊制成1000粒，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为黄色的颗粒；味咸、辛。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 温胃，消食，解毒。用于消化不良，药物，食物中毒。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次4―6粒，一日2～3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.3g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (540, 2, 50, '肉蔻五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>肉蔻五味丸(匝迪--5) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 肉豆蔻 100g &nbsp;&nbsp;土木香 80g &nbsp;&nbsp;木 \r\n        香 80g &nbsp;&nbsp;广 枣 50g &nbsp;&nbsp;荜 茇 10g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄褐色水丸；气香，味辛。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛心“赫依”病。用于心烦失眠，心神不安。对心“赫依”病尤为有效。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～15粒，一日1～3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (541, 2, 50, '行气止痛丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 行气止痛丸(混其勒--7) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 马钱子(制) 150g &nbsp;&nbsp;木 香 60g &nbsp;&nbsp;沉 \r\n        香 120g &nbsp;&nbsp;红 花 60g&nbsp;&nbsp;石 膏 60g &nbsp;&nbsp;诃 子 90g &nbsp;&nbsp;枫香脂 \r\n        60g <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色水丸；气微，味苦、涩。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 开郁行气，活血止痛。用于胸闷，气短，胸肋刺痛，“巴木病”。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次7―13粒，一日1～2次，或遵医嘱。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (542, 2, 50, '肋柱花四味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>肋柱花四味汤散(地格达--4) \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 肋柱花 250g &nbsp;&nbsp;黄 连 250g &nbsp;&nbsp;栀 \r\n        子 250g &nbsp;&nbsp;瞿 麦 250g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰黄色粉末；味苦。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“协日”，凉血，调糟归精。用于血热相搏，肝胆热，咽喉肿痛，口渴烦燥。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3―5g，一日1～3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (543, 2, 50, '壮西六味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>壮西六味丸 \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;[处方] 寒水石(热制)&nbsp;100g &nbsp;&nbsp;荜 茇 50g &nbsp;&nbsp;木 \r\n        香 50g &nbsp;&nbsp;土木香 50g &nbsp;&nbsp;红花 80g &nbsp;&nbsp;白豆蔻 30g<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成细粉，用水泛丸，打光，干燥。每50g约出400粒。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为圆球形水丸；表面灰黄色，味辛、涩。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“巴达干”病，止吐。用于吐酸水，胃脘腹胀，“巴达干宝日”病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2.5g―3g，一日1―3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶30g。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (544, 2, 50, '安神补心六味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 安神补心六味丸(吉如和--6)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 牛 心 100g &nbsp;&nbsp;木 香 100g &nbsp;&nbsp;枫香脂 \r\n      100g &nbsp;&nbsp;丁 香 100g &nbsp;&nbsp;肉豆蔻 100g &nbsp;&nbsp;广 枣 100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法]&nbsp;以上六味，粉碎成细粉，过筛，混匀，用水泛丸，用朱砂0.2g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显褐色；气微，味酸、涩、微苦、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“赫依”，镇静。用于心慌，气短。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15粒，一日1～2次，蜂蜜为引。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (545, 2, 50, '壮西六味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>壮西六味散 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 寒水石(热制)&nbsp;100g &nbsp;&nbsp;荜 茇 30g &nbsp;&nbsp;土木香 \r\n      50g &nbsp;&nbsp;红 花 80g&nbsp;&nbsp;木香&nbsp;50g &nbsp;&nbsp;白豆蔻&nbsp;30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅黄色粉末；气微香，味辛、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“巴达干”病，止吐。用于吐酸水，胃脘腹胀，“巴达干宝日”病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5g～3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。 \r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (546, 2, 50, '安神补心六味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>安神补心六味丸(吉如和--6)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 牛 心 100g &nbsp;&nbsp;木 香 100g &nbsp;&nbsp;枫香脂 \r\n      100g &nbsp;&nbsp;丁 香 100g &nbsp;&nbsp;肉豆蔻 100g &nbsp;&nbsp;广 枣 100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法]&nbsp;以上六味，粉碎成细粉，过筛，混匀，用水泛丸，用朱砂0.2g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显褐色；气微，味酸、涩、微苦、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“赫依”，镇静。用于心慌，气短。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15粒，一日1～2次，蜂蜜为引。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (547, 2, 50, '安神镇惊二十味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>安神镇惊二十味丸(少日新敖力卜)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉 香&nbsp;100g &nbsp;肉豆蔻 60g &nbsp;&nbsp;青 皮&nbsp;&nbsp;60g \r\n      &nbsp;木 香 &nbsp;60g &nbsp;&nbsp;石 膏 &nbsp;60g &nbsp;&nbsp;重 楼&nbsp;40g &nbsp;&nbsp; \r\n      马钱子(制)&nbsp;&nbsp;&nbsp;40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 檀 香 40g &nbsp;&nbsp;白云香&nbsp;40g \r\n      &nbsp;&nbsp;黑云香 40g &nbsp;旋复花 40g &nbsp;&nbsp;丁 香&nbsp;&nbsp;40g &nbsp;&nbsp;菊 \r\n      花 40g &nbsp;&nbsp;&nbsp;波棱瓜子(炒)&nbsp;40g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 桔 梗&nbsp;40g \r\n      &nbsp;&nbsp;广 枣 &nbsp;60g &nbsp;&nbsp;胆汁膏 10g&nbsp;&nbsp;制草乌 40g &nbsp;&nbsp;猪心粉 \r\n      40g&nbsp;&nbsp;&nbsp;远 志 40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十味，粉碎成细粉，用水泛丸，打光，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰褐色水丸；味辛，气香。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 镇惊，安神，行气。用于心慌气短，心神不安，气血瘀滞，胸肋刺痛等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次20～25粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶30g(约240粒)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (548, 2, 50, '那仁明目汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 那仁明目汤散(那仁汤)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 高良姜 100g &nbsp;&nbsp;草 果 100g &nbsp;&nbsp;木 \r\n      香100g &nbsp;&nbsp;槟 榔 100g&nbsp;&nbsp;益 智 100g &nbsp;&nbsp;黄 柏 200g &nbsp;&nbsp;苏 \r\n      木&nbsp;200g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色粉末；气香，味辛、涩、微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛寒补肾，明目。用于肾虚，肾寒引起的眼疾。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (549, 2, 50, '那如八味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>那如八味丸(那如--8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 100g &nbsp;&nbsp;制草乌 100g &nbsp;&nbsp;荜 \r\n      茇 80g &nbsp;&nbsp;芒果核 60g &nbsp;&nbsp;莲 子 60g &nbsp;&nbsp;白豆蔻 60g &nbsp;&nbsp;苦石莲 \r\n      60g &nbsp;&nbsp;石决明(煅)&nbsp;60g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混匀，用水泛丸，用朱砂0.2g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显棕褐色；气微，味涩、辛麻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛寒，消“粘”。用于胃寒食积，消化不良，肾虚，肾寒，腰腿酸痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9―11粒，一日1次，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (550, 2, 50, '那如三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 那如三味丸(那如-3)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 300g &nbsp;&nbsp;荜 茇 90g &nbsp;&nbsp;制草乌 \r\n      150g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上三味，粉碎成细粉，过筛，混匀，用水泛九，用银朱0.2g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸或糊丸，除去包衣显棕黄色；味微酸，辛麻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 消“粘”，除“协日乌素”，祛风，止痛，散寒。用于风湿，关节疼痛，腰腿冷痛，牙痛，白喉等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次3～5粒，一日1次，临睡前服，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服，年老体弱，幼儿慎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g；每10粒重1.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (551, 2, 50, '红花清肝十三丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>红花清肝十三丸(古日古木--13)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 红 花 60g &nbsp;&nbsp;木 香 &nbsp;30g &nbsp;&nbsp;紫檀香 \r\n      30g &nbsp;&nbsp;银 朱 30g &nbsp;&nbsp;丁香 30g &nbsp;&nbsp;诃子 30g &nbsp;&nbsp;麝香0.5g \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 莲 子&nbsp;30g \r\n      &nbsp;&nbsp;川楝子&nbsp;30g &nbsp;&nbsp;麦冬&nbsp;&nbsp;&nbsp;60g &nbsp;&nbsp;栀子&nbsp;&nbsp;30g \r\n      &nbsp;&nbsp;牛黄&nbsp;30g&nbsp;&nbsp;&nbsp;水牛角浓缩粉&nbsp;30g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十三味，除银朱外，麝香、牛黄研细，其余红花等十味粉碎成细粉与上述细粉配研，过筛，混匀，用水泛丸，银朱包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸，除去包衣显浅棕色；气香，味苦、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肝热，除“亚玛”病，解毒。用于肝功衰退，配毒症，“亚玛”病，腰肾损伤，尿频，尿血。尤其对血热引起的眼病有效。 \r\n      <br>\r\n      [用法与量] 口服，一次11―15粒(2g／10粒)，或一次25～30粒(1g／10粒)，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意]&nbsp;孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格]&nbsp;每10粒重2g；每10粒重1g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏]&nbsp;密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (552, 2, 50, '克感额日敦胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>克感额日敦胶囊(额日敦--7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 50g &nbsp;&nbsp;川楝子 l00g &nbsp;&nbsp;栀 \r\n      子 150g &nbsp;&nbsp;土木香 l00g &nbsp;&nbsp;苦 参 200g &nbsp;&nbsp;悬钩子木 150g &nbsp;&nbsp;山 \r\n      柰 l00g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，苦参粉碎成细粉；土木香、山柰加70％乙醇回流提取2次，每 次2小时，合并提取液，滤过，滤液减压回收乙醇，浓缩成膏；其余诃子等四味加水煎煮3次，每次2小时，合并煎液，滤过，滤液浓缩成膏；两膏合并，加入苦参细粉，混匀，干燥，粉碎成细粉，制粒，干燥，压制成1000片，包糖衣，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为糖衣片，除去糖衣后显黄褐色；气微，味苦、辛、微酸。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清瘟解毒，解表，止痛。用于瘟病初期，感冒发烧，咳嗽，全身酸痛，头痛，咽喉肿痛，胸肋刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次4片，一日3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每片重0.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (553, 2, 50, '麦冬十三味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>麦冬十三味丸(查干泵阿--13) <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 木鳖子(制)&nbsp;80g &nbsp;&nbsp;苦荬菜&nbsp;10g &nbsp;&nbsp;山茶花&nbsp;50g \r\n      &nbsp;&nbsp;黄 柏&nbsp;50g&nbsp;&nbsp;&nbsp;胡黄连&nbsp;40g &nbsp;&nbsp;&nbsp;牛 \r\n      黄50g &nbsp;&nbsp;紫花地丁&nbsp;50g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 小秦艽花&nbsp;&nbsp;&nbsp;5g&nbsp;&nbsp;&nbsp;&nbsp;红 \r\n      花&nbsp;&nbsp;70g &nbsp;&nbsp;麦冬&nbsp;&nbsp;&nbsp;100g &nbsp;瞿麦&nbsp;&nbsp;50g \r\n      &nbsp;&nbsp;角茴香&nbsp;40g&nbsp; &nbsp;&nbsp;连翘&nbsp;120g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十三味，除牛黄研细外，其余麦冬等十二味粉碎成细粉，与上述细粉配研，过筛，混匀，用水泛丸，打光，干操，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色水丸；味极苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“协日”热，解瘟。用于瘟疫热，炽热，血热，肝胆热，胃肠热。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9一15粒，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (554, 2, 50, '豆蔻五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>豆蔻五味散(匝迪--5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 肉豆蔻 50g &nbsp;&nbsp;土木香 40g &nbsp;&nbsp;木 香 \r\n      40g &nbsp;&nbsp;广 枣 25g &nbsp;&nbsp;荜 茇 5g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅灰白色粉末；气芳香，味微苦、麻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调和中气，镇惊宁心。用于神气不宁，心烦失眠，诸气不调。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶20g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (555, 2, 50, '苏木六味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 苏木六味汤散<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方]&nbsp;苏 木 240g &nbsp;&nbsp;木 香 60g &nbsp;&nbsp;白豆蔻 \r\n      60g &nbsp;&nbsp;草 果 60g &nbsp;&nbsp;槟榔90g &nbsp;&nbsp;高良姜&nbsp;90g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红褐色粉末；气微香，味辛、涩。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“巴达干・赫依”，行血化瘀。用于肾寒,腰腿疼痛，妇女痛经，血瘀闭经。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日1～3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (556, 2, 50, '蒙花猫肝宁片', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>蒙花猫肝宁片(喜给日地格达)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本品为花锚(halenia sibirica borkh。)加工制成的浸膏片。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为糖衣片，除去糖衣后呈褐色；气微，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]&nbsp;清“协日”，利胆退黄。用于“协日”热引起的肝胆热症，黄疸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2～4片，一日3次，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每片0.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (557, 2, 50, '连翘四味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>连翘四味汤散(苏龙嘎--4)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 连 翘 240g &nbsp;&nbsp;拳 参 210g &nbsp;&nbsp;关木通 \r\n      210g &nbsp;&nbsp;麦 冬 150g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄白色至黄棕色粉末；气微香，味苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清腑热。用于肠热，痢疾，腹痛，腹泻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日1―3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (558, 2, 50, '连翘四味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>连翘四味胶囊<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 连 翘 480g &nbsp;&nbsp;拳 参 420g &nbsp;&nbsp;关木通 \r\n      420g &nbsp;&nbsp;麦 冬 300g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，关木通粉碎成细粉；其余连翘等三味粉碎成粗粉，照流浸膏与浸膏剂项下的渗漉法(附录10)，用乙醇作溶剂，浸渍24小时后，渗漉，渗漉液减压回收乙醇得稠膏状物，加入木通细粉，制粒，干燥，粉碎成细粉，干燥，制成1000粒胶囊，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为黄棕色颗粒；味苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清腹热，止泻。用于肠热，痢疾，腹痛，腹泻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2～3粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.5g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (559, 2, 50, '利肝和胃丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 利肝和胃丸(壮西--21)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 土木香&nbsp;30g &nbsp;酸梨干&nbsp;30g &nbsp;诃 子&nbsp;30g \r\n      &nbsp;荜 茇&nbsp;&nbsp;30g&nbsp;&nbsp;&nbsp;瞿 麦20g&nbsp;&nbsp;降香&nbsp;20g \r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;木 香&nbsp;&nbsp;20g \r\n      &nbsp;五灵脂&nbsp;30g &nbsp;益智&nbsp;&nbsp;40g &nbsp;香青兰&nbsp;30g &nbsp;&nbsp;栀子&nbsp;20g&nbsp;&nbsp;麦冬 \r\n      20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石榴&nbsp;&nbsp;&nbsp;40g \r\n      &nbsp;沙棘&nbsp;&nbsp;&nbsp;20g &nbsp;地丁&nbsp;&nbsp;20g &nbsp;芫荽果&nbsp;20g \r\n      &nbsp;&nbsp;牛黄&nbsp;1g &nbsp;&nbsp;连翘&nbsp;20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;寒水石(凉制)&nbsp;100g&nbsp;&nbsp;木鳖子(制)&nbsp;20g&nbsp;&nbsp;蓝盆花&nbsp;20g&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑褐色大蜜丸，或为棕褐色水丸；气香，味辛、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 舒肝健胃，制酸消胀。用于肝胃气滞，痞闷不舒，噎膈反胃，恶心吐酸，消化不良等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1丸，或一次15粒，一日1～2次或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每丸重9g(蜜丸)；每10粒重2g(水丸)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (560, 2, 50, '利胆八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 利胆八味散(地格达--8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 苦地丁 100g &nbsp;&nbsp;木鳖子(制)&nbsp;80g &nbsp;&nbsp;麦 \r\n      冬 80g &nbsp;&nbsp;木香&nbsp;80g&nbsp;&nbsp;龙 胆 80g &nbsp;&nbsp;黄 连 50g &nbsp;&nbsp;角茴香 \r\n      40g &nbsp;&nbsp;黄柏&nbsp;70g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄绿色粉末；气微香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“协日”，泻肝火，利胆。用于“协日”热引起的头痛症，目肤和小便赤黄，黄疸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量]&nbsp;口服，一次1.5～3g，一日1～2次。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (561, 2, 50, '利尿八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 利尿八味散(阿拉坦额勒斯--8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 海金沙 30g &nbsp;&nbsp;白豆蔻 30g &nbsp;&nbsp;冬葵果 \r\n      50g &nbsp;&nbsp;硇砂&nbsp;50g &nbsp;&nbsp;蜗牛(煅)&nbsp;260g &nbsp;&nbsp;方 海 \r\n      40g &nbsp;&nbsp;天花粉 40g &nbsp;&nbsp;蒺藜(微炒) 20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混匀，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰黄色粉末；气微香，味咸，微辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 利水。用于寒热性尿闭，水肿，泌尿道结石等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5g―3g，一日1―3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭；防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (562, 2, 50, '沉香安神散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 沉香安神散(阿嘎日--35)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉 香 &nbsp;30g &nbsp;紫檀香 20g &nbsp;&nbsp;红 花 \r\n      &nbsp;20g &nbsp;&nbsp;诃 子 20g &nbsp;&nbsp;旋复花 20g &nbsp;&nbsp;川楝子 &nbsp;&nbsp;&nbsp;&nbsp;20g&nbsp;&nbsp;木棉花 \r\n      20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 胡黄连 20g &nbsp;黑云香 \r\n      20g &nbsp;&nbsp;山沉香 30g &nbsp;&nbsp;檀 香 20g &nbsp;&nbsp;石 膏 &nbsp;20g &nbsp;&nbsp;白豆蔻&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20g \r\n      &nbsp;栀 子 &nbsp;20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白头翁 20g \r\n      &nbsp;石 榴 &nbsp;20g &nbsp;&nbsp;北沙参 20g &nbsp;&nbsp;丁 香 20g &nbsp;&nbsp;苦 \r\n      参 &nbsp;20g&nbsp;&nbsp;&nbsp;紫花地丁 &nbsp;&nbsp;20g&nbsp;&nbsp;细 辛 &nbsp;20g \r\n      &nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山 柰 &nbsp;20g \r\n      &nbsp;广 枣 &nbsp;20g &nbsp;&nbsp;兔 心 &nbsp;20g &nbsp;&nbsp;麝 香 0.1g &nbsp;降 \r\n      香 &nbsp;30g &nbsp;&nbsp;马钱子(制)&nbsp;20g &nbsp;草 果 &nbsp;20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 制草乌&nbsp;20g \r\n      &nbsp;枫香脂&nbsp;10g&nbsp;&nbsp;&nbsp;肉豆蔻 20g &nbsp;&nbsp;瞿 麦&nbsp;20g &nbsp;&nbsp;木 \r\n      香&nbsp;&nbsp;20g &nbsp;&nbsp;悬钩子木&nbsp;&nbsp;&nbsp;20g &nbsp;土木香&nbsp;20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上三十五味，除麝香外，其余沉香等三十四味粉碎成细粉，将麝香研细，与以上细粉配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色粉末；气香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调节“赫依”、热、“粘”交争。用于“山川间热”，“赫依”、热兼盛；胸满气喘，干咳痰少，游走刺痛，心悸失眠，神昏谵语。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (563, 2, 50, '沉香十七味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 沉香十七味丸(阿嘎日--17)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉 香 &nbsp;80g &nbsp;&nbsp;&nbsp;栀 子 &nbsp;20g \r\n      &nbsp;&nbsp;刀 豆 &nbsp;40g &nbsp;&nbsp;兔心 &nbsp;&nbsp;40g &nbsp;&nbsp;苦 参&nbsp;40g \r\n      白萝卜干 &nbsp;&nbsp;40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;丁 香&nbsp;&nbsp;40g \r\n      &nbsp;&nbsp;&nbsp;川楝子 20g &nbsp;&nbsp;木 香&nbsp;&nbsp;40g &nbsp;&nbsp;制草乌 \r\n      60g&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;紫河车(干)&nbsp;20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑云香 20g \r\n      &nbsp;&nbsp;&nbsp;诃子&nbsp;&nbsp;&nbsp;20g &nbsp;&nbsp;旋复花 20g &nbsp;&nbsp;肉豆蔻 \r\n      40g &nbsp;&nbsp;广枣 180g&nbsp;马钱子(制)&nbsp;40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十七味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色水丸剂；气微香，味涩、苦、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 镇“赫依”通脉，止痛。用于“赫依”血不调，颈项强直，烦燥不安，恶血陷肾，肾损血盛，“白脉病”，脑部刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量) 口服，一次7―13粒(2g／10粒)，或一次14―24粒(1.25g／10粒)，一日1～2次，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g；每10粒重1.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (564, 2, 50, '沙参止咳汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 沙参止咳汤散(扫日劳--4)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 北沙参 150g &nbsp;&nbsp;甘 草 90g &nbsp;&nbsp;紫草茸 \r\n      90g &nbsp;&nbsp;拳 参 90g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡红色粉末；气微香，味甘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热，止咳，祛痰。用于肺热，咳嗽，多痰，胸背刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日l～3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (565, 2, 50, '诃子五味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 诃子五味胶囊(阿拉担阿如--5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 200g &nbsp;&nbsp;&nbsp;&nbsp;石 榴 50g &nbsp;&nbsp;&nbsp;&nbsp;木鳖子(制) \r\n      20g&nbsp;&nbsp;五灵脂 55g &nbsp;&nbsp;黑冰片 115g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，黑冰片、五灵脂粉碎成细粉；其余诃子等三味粉碎成粗粉，加水三次，每次1小时，合并煎煮液，滤过，滤液浓缩成稠膏，加人上述细粉，混匀， \r\n      80度烘干，粉碎成细粉，制粒，干燥，制得1000粒胶囊，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为黑褐色颗粒；气香，味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调节“赫依”、“协日”，健胃，助消化。用于胃胸积热，宿食不消，肝胆热症，黄胆。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次3―5粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.3g。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (566, 2, 50, '补肾健胃二十一味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 补肾健胃二十一味丸(希莫音满都拉)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 榴 &nbsp;45g &nbsp;&nbsp;紫硇砂 30g &nbsp;&nbsp;肉豆蔻 \r\n      25g &nbsp;&nbsp;红 花 25g &nbsp;&nbsp;白豆蔻 15g &nbsp;&nbsp;天 冬&nbsp;40g &nbsp;&nbsp;&nbsp;蒺藜(微炒)&nbsp;&nbsp;&nbsp;20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 五灵脂&nbsp;25g \r\n      &nbsp;&nbsp;荜 茇&nbsp;&nbsp;15g &nbsp;&nbsp;草 果&nbsp;&nbsp;10g &nbsp;&nbsp;肉 \r\n      桂 40g &nbsp;&nbsp;沉 香&nbsp;25g &nbsp;&nbsp;&nbsp;白葡萄&nbsp;25g &nbsp;&nbsp;手掌参&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15g \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小蜀季&nbsp;30g \r\n      &nbsp;&nbsp;玉&nbsp;竹 &nbsp;&nbsp;40g &nbsp;人&nbsp;参&nbsp;&nbsp;&nbsp;30g \r\n      &nbsp;黄&nbsp;精&nbsp;25g &nbsp;&nbsp;天花粉&nbsp;20g &nbsp;&nbsp;广&nbsp;枣&nbsp;10g&nbsp;&nbsp;&nbsp;&nbsp;寒水石(奶制) \r\n      20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十一味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄褐色水丸；味微甘、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛寒，健胃，补肾壮阳。用于食积胃胀，胸满头晕，肾寒浮肿，水冲，腰腿痛，尿频等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15丸，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (567, 2, 50, '阿那日十四味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿那日十四味丸 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 榴 125g 红 花 50g&nbsp;草 果 50g&nbsp;肉豆蔻 50g \r\n      栀 子 &nbsp;50g 干 姜 &nbsp;50g &nbsp;白豆蔻 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 肉 桂 50g &nbsp;荜 \r\n      茇 50g 诃 子 50g 木 香 &nbsp;50g 芫荽果 50g 光明盐 50g&nbsp;&nbsp;寒水石(热制) 200g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十四味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄白色粉末；气微，味辛、微咸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 健脾消积，温中散寒。用于脾胃寒湿，胸肋胀满，恶呃痞满，消化不良。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5-3g，一日1-2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。&nbsp;</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (568, 2, 50, '阿那日八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿那日八味散(阿那日巴达码)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 榴 320g 益 智 60g 肉 桂 40g 荜拨&nbsp;80g 黑冰片 60g \r\n      木鳖子(制) 60g 诃 子 60g 玫瑰花 40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰黑色粉末；气香，味辛、微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“巴干达协日”，散瘀，消食，止痛。用于胃溃疡，消化不良，胃寒疼痛，胃脘胀满，胃酸呕吐，腹泻浮肿。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (569, 2, 50, '阿那日五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿那日五味散<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 榴 250g 肉 桂 150g 砂 仁 150g 筚拨&nbsp;100g 干 姜 \r\n      100g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄色粉末；气香，味辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 温胃，清食。用于胃脘寒痛，清化不良，肾寒腰疼。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (570, 2, 51, '阿如健脾散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿如健脾散(阿如--7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 l00g 丁 香 l00g 木鳖子(制)100g 甘 松 100g&nbsp;&nbsp;木棉花 \r\n      l00g 荜 茇 l00g 草 果100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕褐色粉末；气香，味辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热健脾。用于脾区疼痛，肠鸣，腹胀，腹泻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (571, 2, 51, '阿拉坦五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿拉坦五味丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 400g 石 榴 100g 木鳖子(制) 40g&nbsp;&nbsp;五灵脂 \r\n      110g 黑冰片 310g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑色水丸；气香，味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“赫依、协日”病，健胃，助消化。胃肠炽热，宿食不消，肝胆热症，黄疸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15粒(2g／10粒)，或一次16―24粒(1.25g／10粒)，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g；每10粒重1.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (572, 2, 51, '阿拉坦五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿拉坦五味散(阿拉坦阿如--5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 400g 石 榴 100g 五灵脂 110g 黑冰片 310g 木鳖子&nbsp;40g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑褐色粉末；气香，味涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调节“赫依”，“协日”，健胃，助消化。用于胃肠热，宿食不消肝胆热症，黄疸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日 1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (573, 2, 51, '阿魏八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿魏八味散(兴棍--8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 阿 魏 100g 黑云香 70g 肉豆蔻 100g 沉 香 100g&nbsp;&nbsp;丁 \r\n      香 100g 木 香&nbsp;100g 小茴香 70g 当 归 50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混匀，用水泛丸，用银朱0.2g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸，除去外衣显浅黄褐色；气臭，味涩、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛“巴达干赫依”病，止痛。用于“巴达干赫依”性头痛，头晕，恶心，呕吐。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～15粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (574, 2, 51, '阿魏五味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 阿魏五味散(兴棍--5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 阿 魏 7.5g 干 姜 5g 黑云香 5g&nbsp;&nbsp;光明盐 35g 肉豆蔻&nbsp;10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅黄白色粉末，气特异臭，味咸，微辛辣。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 开脾郁，理诸气。用于心气郁结，脾气失运，寒火气积，胸肋刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶装20g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，置通风干燥处。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (575, 2, 51, '枫香脂十味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 枫香脂十味丸(查干古古勒--10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 枫香脂 120g 决明子 90g 川楝子 90g 茴麻子 90g&nbsp;&nbsp;广木香 \r\n      60g 苦 参 60g 诃 子 30g 栀 子 30g &nbsp;&nbsp;瞿 麦 30g 五灵脂 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为深棕色水丸；气微，味苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 燥“协日乌素”，止痛。用于游痛症，风湿性关节疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9一13粒，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (576, 2, 51, '明目十六味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 明目十六味丸(萨拉吉德--16)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 枸 杞 &nbsp;90g 当 归 30g &nbsp;&nbsp;夜明砂 30g 通经草 \r\n      30g&nbsp;&nbsp;红 花 &nbsp;15g 麦 冬 30g 石决明 30g 蒺藜(微炒)&nbsp;30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青葙子 15g \r\n      菊 花 30g &nbsp;&nbsp;全 蝎 &nbsp;30g 防 风 &nbsp;15g&nbsp;&nbsp;决明子 30g 木 贼 30g&nbsp;川楝子 \r\n      10g 羚羊角(煨)&nbsp;5g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十六味，羚羊角研成细粉，其余枸杞等十五味粉碎成细粉，与羚羊角细粉配研，过筛，混匀，用水泛丸，用银朱0.15g包衣，打光，干燥、即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸，除去包衣显黄褐色；气微，味苦、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清血热，明目。用于肝热引起的眼病，目赤痒病，流泪等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～13丸，一日 1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (577, 2, 51, '明目六味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 明目六味汤散(尼敦汤)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 100g 黄 柏 100g 栀 子 100g 苏 木 100g&nbsp;&nbsp;川楝子&nbsp;100g \r\n      木香&nbsp;100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红黄色粉末；气微，味涩、微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热，凉血，明目。用于血热引起的眼病，眼红，眼眵增多，刺痛，怕光等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3一5g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (578, 2, 51, '参竹精颗粒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 参竹精颗粒(毛浩日查干-5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 手掌参 100g 黄 精 l00g 玉 竹 l00g 何首乌(制)100g&nbsp;&nbsp;广 \r\n      枣 600g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，手掌参、黄精、玉竹、何首乌(制)以70％乙醇1000ml，加热回流提取三次，每次1小时，滤过，合并三次滤液，回收乙醇，成膏状物，重80g，为膏i。广枣粉碎成粗粉，用70％乙醇1200ml加热回流提取三次，每次1小时，滤过，合并三次滤液，回收乙醇成浓缩膏状物为膏二，重100g。将膏i、膏2，可溶性糊精，糖粉按0.8:1.2：6.1配制，充分混匀，用70％乙醇为湿润剂，制成颗粒，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色颗粒；味酸、甘。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 补肾壮阳，滋补强身。用于肾寒，肾虚，精血不足，筋骨酸痛，年迈体弱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 开水冲服，一次1袋，一日2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋重5g，相当于原生药量5g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (579, 2, 51, '珍宝丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 珍宝丸(额日敦-乌日勒)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 膏 15g 栀 子 15g 决明子 10g 土木香 10g 降 香 &nbsp;15g \r\n      方 海 &nbsp;10g 肉 桂 &nbsp;10g 珍珠(制)&nbsp;20g&nbsp;枫香脂 15g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;丁 香 10g \r\n      红 花 15g 草果仁 15g 木 香 &nbsp;l0g 地锦草 10g 海金沙 15g&nbsp;麝 香&nbsp;0.5g 川楝子 &nbsp;&nbsp;15g \r\n      白豆蔻 15g 水牛角浓缩粉 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;檀 香 15g \r\n      牛黄&nbsp;&nbsp;5g &nbsp;荜 茇 &nbsp;10g 诃子&nbsp;&nbsp;&nbsp;15g 肉豆蔻&nbsp;10g \r\n      苘麻子&nbsp;10g 甘草&nbsp;&nbsp;&nbsp;10g 白巨胜&nbsp;&nbsp;&nbsp;10g 沉香&nbsp;&nbsp;&nbsp;15g&nbsp;黑种草子 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;10g &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十九味，除牛黄、麝香、珍珠分别粉碎成细粉，其余石膏等二十六味粉碎成细粉，与上述细粉配研，过筛，混匀，用水泛丸，用朱砂0.1g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显黄褐色，气香，味微苦甘、涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]清热，安神，舒筋活络，除“协日乌素”。用于白脉病，半身不遂。风湿，类风湿，肌筋萎缩，神经麻痹，肾损脉伤，瘟疫热病，久治不愈等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次13―15粒(2g／10粒)，20～30粒1g／10粒)，一日 1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格]每10粒重2g；每10粒重1g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏]密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (580, 2, 51, '胡日查六味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 胡日查六味丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 150g 木 香 75g 红 花 150g 黑云香 75g 甘 松 75g 闹羊花&nbsp;150g \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕褐色的水丸；气香，味苦、酸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 消“粘”，清“协日”，止痛。用于“协日”性头痛，目赤红肿，“亚码”引起的偏、正头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～15粒，一日1～2次，饭后服，或遵医嘱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (581, 2, 51, '草果四味汤', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 草果四味汤(嘎古拉-4)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 草 果 250g 丁 香 150g 木 香 250g 小茴香 150g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成中粉，过筛，混匀，即得。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色粉末；气芳香，味辛、苦。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调节“赫依”，健脾胃。用于上行“赫依”，持命“赫依”之病，赫依”引起的头痛。尤其对脾虚有显著疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日1～3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g或50g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (582, 2, 51, '草果健脾丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 草果健脾丸(嘎古拉-19)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 草 果 100g 石 膏 10g 瞿 麦 20g 诃 子 20g 红 花 14g&nbsp;&nbsp;五灵脂 \r\n      60g 菥冥子 10g&nbsp;白豆蔻 40g&nbsp;&nbsp;紫花地丁20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莲 子 10g \r\n      &nbsp;紫 草 20g 木 香 20g 栀 子 20g&nbsp;麦 冬 20g&nbsp;&nbsp;缬 草 &nbsp;20g 水柏枝 \r\n      20g 木棉花 40g&nbsp;&nbsp;紫草茸 &nbsp;20g木鳖子(制)10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十九味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色粉末；味涩、苦、微辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 理中健脾。用于脾寒，脾热诸症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日 l一2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (583, 2, 51, '哈日十二味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 哈日十二味散(伊赫哈日-12)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 黑冰片 100g 土木香 30g 苦地丁 30g 诃 子 30g 栀 子 30g 牛 \r\n      黄 5g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 川楝子 30g &nbsp;牛胆粉 \r\n      20g 胡黄连 30g&nbsp;石 膏 30g 红 花 30g 甘 松 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十二味，除牛黄外，其余黑冰片等十一味粉碎成细粉，与牛黄配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑色粉末；气微香，味苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清“协日”热。用日“协日”病，目肤发黄，瘟疫，瘟疫陷胃，口渴烦燥，消化不良。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (584, 2, 51, '哈敦海鲁木勒九味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 哈敦海鲁木勒九味丸(海鲁木勒-9)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 五灵脂 25g 香青兰 15g 诃子30g 麦 冬 15g 甘松&nbsp;30g 牛胆粉&nbsp;20g \r\n      拳参&nbsp;25g 红花&nbsp;30g 白豆蔻&nbsp;25g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十三味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑褐色水丸；气微香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清腑热，消“粘”，止痢。用于胃肠痉挛，呕吐，腹泻，赤白痢疾。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9―13粒，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (585, 2, 51, '顺气十三味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 顺气十三味散(敖勒盖-13)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 榴 30g 胡 椒 10g 红 花 10g 干 姜 10g 光明盐&nbsp;10g \r\n      黑种草子&nbsp;20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;筚&nbsp;拨&nbsp;20g \r\n      草&nbsp;果 20&nbsp;&nbsp;诃&nbsp;子&nbsp;20g&nbsp;肉 桂&nbsp;10g 益&nbsp;智&nbsp;&nbsp;30g \r\n      紫硇砂&nbsp;&nbsp;&nbsp;&nbsp;2g 白巨胜&nbsp;20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十三味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色粉末；气香，味微咸、辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 开郁顺气，化滞消胀。用于腹胀，肠鸣，消化不良，胃肠虚弱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (586, 2, 51, '顺气补心十一味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 顺气补心十一味丸(阿密别日各其--11)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉香&nbsp;50g 肉豆蔻 40g 广 枣 60g 石 膏 30g 枫香脂 20g \r\n      木 香 40g 诃 子 50g 木棉花 30g 丁香&nbsp;40g 阿 魏 50g 紫河车 50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十一味，粉碎成细粉，过筛，混匀，用水泛丸，用朱砂0.1g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸；味涩、苦、微辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 镇“赫依”，镇静，安静。用于胸肋刺痛，“赫依”，性癫狂，言语不清等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11―15粒，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (587, 2, 51, '顺气安神丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 顺气安神丸(阿敏-额日敦)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉 香 30g 丁 香&nbsp;7.5g 木 香 15g&nbsp;兔&nbsp;心&nbsp;21g \r\n      木棉花 21g 胡黄连 15g 旋复花 15g 枫香脂 15g 马钱子(制) 15g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石 膏&nbsp;15g \r\n      牛 黄&nbsp;15g &nbsp;檀&nbsp;香&nbsp;15g 诃&nbsp;子&nbsp;15g 拳&nbsp;参&nbsp;&nbsp;15g&nbsp;北沙参 \r\n      15g 肉豆蔻&nbsp;15g 草乌叶&nbsp;15g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十八味，牛黄研成细粉，其余石膏等十六味粉碎成细粉，与牛黄细粉配研，过筛，混匀，用水泛丸，用朱砂0.1g包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为暗红色水丸，除去包衣显黄褐色；气香，味苦、涩。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 调节“粘”，热，镇静安神。用于“赫依”，“粘”热交争，山川间热，发烧，“赫依”引起的癫狂，昏迷，心神不安。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次7―13粒，一日 1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (588, 2, 51, '洪林五味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 洪林五味汤散<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 胡黄连 100g 瞿 麦 100g 小秦艽花 100g 紫花地丁 100g 栀子100g \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成中粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄绿色粉末；味极苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热，解毒。用于炽热，讧热，血热头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次3～5g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (589, 2, 51, '活血六味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 活血六味散(给喜古讷-6)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沙 棘 30g 碱花 60g 大 黄 50g 山 柰 30g 木香&nbsp;20g \r\n      芒硝(制&nbsp;)20g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成细粉，过筛、混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡黄色粉末；气微香，味涩，微咸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治]&nbsp;活血，化瘀，调经。用于血瘀，闭经，小腹疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (590, 2, 51, '冠心七味片', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 冠心七味片(乌兰温都顺-7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 丹 参 230.4g 檀 香 25.6g 降 香 51.2g 山 柰 38.4g 肉豆蔻 \r\n      76.8g 广 枣 76.8g 沙 棘 76.8g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，檀香、降看、山柰、肉豆蔻四味混合粉碎成细粉，其余丹参等三味提取三次，第一次加乙醇回流提取2小时，第二次加70％乙醇提取2小时，滤液合并，滤过，回收乙醇得稠膏状物，第三次加水煎煮2小时，滤过，滤液浓缩成稠膏，两膏合并，将檀香等细粉与稠膏拌匀，低温干燥，粉碎成细粉，制粒，干燥，压制成1000片，包糖衣，即得。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为糖衣片，除去糖衣后，显浅棕红色；气微香，味微苦、酸。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 活血化瘀，强心止痛。用于冠心病，心烦心悸，心绞痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次3～4片，一日3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每片重0.3g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (591, 2, 51, '扁蕾颗粒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 扁蕾颗粒(地格达-布日莫勒)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 湿生扁蕾 2000g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 取湿生扁蕾，水煎两次，第一次2小时，第二次1小时，滤过，合并滤液，液缩成浸膏，加入适量糊精及糖粉，制粒，干燥，即得。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为褐色或深褐色颗粒；味甜而微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热燥湿，利湿干黄水，用于小儿腹泻，肠胃炎。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 开水冲服，一次1―1.5g，一日3～4次，小儿酌减。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每瓶装12g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮，置阴凉干燥处。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (592, 2, 51, '祛痛橡胶膏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 祛痛橡胶膏(嘎日迪-5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 生草乌 75g 水菖蒲 52.5g 木 香 52.5g 枫香脂 32.5g 决明子 52.5g \r\n      苘麻子 52.5g 薄荷油 11.9g 樟 脑 19.7g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，除枫香脂、樟脑、薄荷油外；其余生草乌等五味粉碎成粗粉，用乙醇浸渍24小时后，滤过，合并滤液，浓缩成稠膏状物备用。将枫香脂粉、薄荷油、樟脑、稠膏状物，加入3.7～4.0倍重的由橡胶、松香等制成的基质，制成涂料。进行涂膏，切段，盖衬，切成小块，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡黄色片状橡胶膏；气芳香。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 消肿，止痛，燥“协日乌素”。用于风湿性关节炎，游痛症，腰酸腿痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 外用，贴患处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 5x 6cm2。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，置暗处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 对橡胶膏过敏者，皮扶破裂糜烂者，不宜贴用。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (593, 2, 51, '给喜古那丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 给喜古那丸<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 土木香 200g 干 姜 200g 诃 子 300g 方 海 100g 赤匏子 100g寒水石(制) \r\n      400g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;乌梢蛇 100g&nbsp;沙 \r\n      棘 100g 大 黄 500g 碱 花 100g 白硇砂 100g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十一味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜110～130g，制成大蜜丸，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色的大蜜丸；气微，味辛、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 暖宫，益气。用于子宫脱垂，小腹坠痛，胎盘滞留，大便秘结。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1丸，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每丸重6g。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (594, 2, 51, '桔梗八味片', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 桔梗八味片 宝日苏日劳-8<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 桔 梗 166.6g 沙 棘&nbsp;166.6g 紫 草 83.3g 拳 参 83.3g \r\n      绵马贯众&nbsp;83.3g 甘 草 83.3g 枇杷叶 83.3g 琐琐葡萄&nbsp;83.3g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，拳参、琐琐葡萄粉碎成粗粉；桔梗等六味加65％乙醇浸泡24小时，回流提取二次，第一次3小时，第二次2小时，滤过，滤液合并，浓缩成膏，加入上述粗粉，干燥，粉碎成细粉，制粒，压制成1000片，包糖衣，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为糖衣片，除去糖衣后显棕黑色；味甘、微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热，止咳，化痰。用于肺热，咳嗽，多痰，预防小儿麻疹，感冒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次4～6片，一日3次，小儿酌减。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每片重0.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (595, 2, 51, '桔梗八味颗粒', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 桔梗八味颗粒(宝日扫日劳-8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 桔 梗 1000g 沙 棘 1000g 紫 草 500g 拳 参 500g 绵马贯众&nbsp;500g \r\n      枇杷叶 500g 甘 草 500g 琐琐葡萄 500g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，拳参、琐琐葡萄粉碎成粗粉，桔梗等六味加65％乙醇浸泡24小时，加热回流提取二次，第一次3小时，第二次2小时，滤过，滤液合并，浓缩成膏，加入上述粗粉，干燥，粉碎成细粉，加入适量糊精及糖粉，制成颗粒，干燥，得6000g，分装，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色颗粒；味甘、微苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热，止咳，化痰。用于肺热咳嗽，多痰，预防和治疗小儿麻疹及流感。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 一次服1袋，一日2～3次，用开水冲服，小儿酌减。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋重6g(相当于原生药量5g)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (596, 2, 51, '健胃十味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 健胃十味丸(浩道敦-10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] &nbsp;石 榴 60g 白豆蔻 30g 诃 子 10g 肉 桂 30g&nbsp;光明盐&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20g \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;荜 \r\n      茇 30g&nbsp;五灵脂 20g 胡 椒 30g 山 柰 10g 寒水石(热制) 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，粉碎成细粉，过筛，混匀，用水泛丸，打光，干燥、即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为浅棕色的水丸；气香，味辛、咸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 暖胃助消。用于寒热积聚，消化不良，胃胀不适，呕吐泄泻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次9～15粒，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (597, 2, 51, '健胃止痛五味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 健胃止痛五味胶囊(哈日嘎日迪-5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 干 姜 200g 诃 子 200g 紫硇砂 200g 光明盐 200g 荜 茇 200g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，光明盐、紫硇砂粉碎成细粉；诃子粉碎成粗粉，加水煎煮三次，每次1小时，合并煎煮液，滤过，滤液浓缩成稠膏，干姜、荜茇粉碎成粗粉，加70％乙醇回流提取二次，第一次2小时，第二次1.5小时，合并提取液，滤过，滤液减压回收乙醇，浓缩成膏，并与诃子膏合并，加入上述细粉，80\'c烘干，粉碎成细粉，制粒，干燥，制成1000粒胶囊，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为浅棕色颗粒；气香，味辛、咸。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛寒健胃，止疼。用于胃肠痉挛，脘腹冷疼，食欲不振，寒性呕吐，腹泻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次3～5粒，一日1～2次<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.5g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n</body>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (598, 2, 51, '健脾五味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 健脾五味丸(布特格勒其-5)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子 60g 五灵脂 15g 寒水石(煅) 30g 土木香 15g 胡黄连 30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上五味，粉碎成细粉，过筛，混习，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄褐色的水丸；气微香，味微苦、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 健脾和胃，理气镇痛。用于“赫依协日”，“宝日”，“巴达干”病引起的胃脘胀满，上腹疼痛诸症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次11～15粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (599, 2, 51, '舒筋十二味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 舒筋十二味丸(亚孙乌日勒)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 木 瓜 50g 制草乌 25g 白 芷 50g 海风藤 50g &nbsp;川牛膝&nbsp;100g&nbsp;豹骨(砂烫)&nbsp;50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;党 参 65g&nbsp;制川乌 \r\n      25g 当 归 50g 威灵仙 50g &nbsp;青风藤 &nbsp;50g 川 芎&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十二味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜110―130g，制成大蜜丸，即得。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑褐色的大蜜丸；味苦、微酸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 舒筋活血，祛风止痛。用于手足麻木，腰膝疼痛，筋骨无力，步履艰难。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1丸，一日2次，饭后服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每丸重9g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (600, 2, 52, '凉血十味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 凉血十味散(乌兰-10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 紫 草150g 寒水石(凉制) 150g 木 香 60g 胡黄连 60g 瞿 麦 90g \r\n      石 膏 60g 红花 90g 甘 草 60g 土木香 60g 北沙参 60g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕红色粉末；味苦、微涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 凉血，明目。用于肝火，肺热，“宝日”中期，头痛目赤。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (601, 2, 52, '消肿九味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 消肿九味散(哈布德仁-9)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 京大戟 100g 大 黄 200g 翻白草 100g 玉竹100g 姜 黄 100g \r\n      水菖蒲 100g 生草乌 100g 天门冬&nbsp;100g 亚大黄 100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上九味，分别挑选，粉碎成细粉，过筛，混匀，即得。&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄绿色粉末；气微，味微辛、涩、麻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 退热，消肿，止痛。用于急性腮腺炎，乳腺炎，软组织损伤，疖肿，痈肿，蜂窝组织炎，急性淋巴管炎，淋巴结炎，皮下及深部脓肿，丹毒，无名肿毒，红肿热痛，风湿寒痹，关节疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 外用，用醋、鸡蛋清或香油调敷患处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每包装30g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。&nbsp;</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (602, 2, 52, '消肿橡胶膏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 消肿橡胶膏(哈布德仁-13)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 大戟 200g &nbsp;&nbsp;亚大黄 200g 生草乌 200g 冰 片 50g \r\n      大黄&nbsp;200g&nbsp;&nbsp;水菖蒲&nbsp;200g 冬青油&nbsp;100g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;玉竹&nbsp;200g&nbsp;&nbsp;&nbsp;翻白草&nbsp;200g \r\n      天门冬&nbsp;200g 姜&nbsp;黄200g 樟脑&nbsp;&nbsp;50g&nbsp;&nbsp;薄荷脑&nbsp;100g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十三味，除冬青油、薄荷脑、冰片外，其余粉碎成粗粉，加90％乙醇浸渍24小时后，滤过，滤液浓缩，制成浸膏。取橡胶、松香、氧化锌、凡士林、羊毛脂、椰子油等制成的基质，加上述浸膏及冬青油，薄荷油、樟脑、冰片共搅拌约9小时制成涂料，滤过，放置24小时后进行涂料膏，切段，盖衬，切成小块，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色片状橡胶膏；气芳香。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 退热，消肿，止痛。用于急性腮腺炎，乳腺炎，软组织损伤，疖肿，痈肿，蜂窝组织炎，急性淋巴管炎，淋巴结炎，皮下及深部脓肿，丹毒，无名肿毒，红肿热痛，风湿疼痛，腰，肩，背酸痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 外用，一次1块，贴于患处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 5x6cm2。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，置暗处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 对橡胶膏过敏，皮肤破裂糜烂者，不宜贴用。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (603, 2, 52, '益肾十七味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 益肾十七味丸(萨丽嘎日迪)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃 子&nbsp;50g 紫草茸 44g 制草乌 50g 银 朱 30g 枇杷叶(制)&nbsp;24g \r\n      茜 草 &nbsp;10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刀豆 &nbsp;35g \r\n      石菖蒲&nbsp;30g 牛胆粉&nbsp;12g 红 花&nbsp;12g 石决明(煅)&nbsp;24g&nbsp;白豆蔻&nbsp;50g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;木 香 50g \r\n      黑云香 24g 麝香&nbsp;0.18g&nbsp;香&nbsp;墨&nbsp;10g&nbsp;大蜀季花 &nbsp;&nbsp;35g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十七味，麝香、银朱研成极细粉，其余诃子等十五味粉碎成细粉，加入部分银朱、麝香极细粉，与上述细粉配研，过筛，混匀，用水泛丸，银朱包衣，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为红色水丸，除去包衣显黑褐色；气香，味苦。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肾热，消“粘”，固精。用于肾寒肾热诸症，腰膝疼痛，梦遗滑精，睾丸肿大。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量]&nbsp;口服，一次5～1l粒(2g／10粒)，或一次12～26粒(0.8g／10粒)，晚间临睡前服。或遵医嘱<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意]&nbsp;孕妇忌服，年老体弱者慎用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g；每10粒重0.8g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (604, 2, 52, '益智温肾十味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 益智温肾十味丸(苏格木勒-10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 益 智 150g 干 姜 60g 白硇砂 60g 荜 茇 60g 榧 子 60g&nbsp;&nbsp;冬葵果 \r\n      60g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 莲 子 60g &nbsp;方 \r\n      海 150g苦石莲 60g&nbsp;榧 子 60g 麝 香0.1g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，取麝香研细，其余益智等九味粉碎成细粉，与麝香细粉配研，过筛，混匀，用水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色的水丸；气香，味辛、咸。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 祛肾寒，利尿。用于肾寒肾虚，腰腿痛，尿闭，肾结石等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1l一15丸，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。 \r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (605, 2, 52, '透骨灵橡胶膏', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 亚顺额日敦(透骨灵橡胶膏)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 透骨草 6g 川附子 9g 白附子 9g 制草乌 9g 制川乌 9g 天南星 9g 鸡血藤 \r\n      9g 薄荷脑 6g 冰 片 6g 樟 脑 4g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，除樟脑、薄荷脑、冰片外，其余七味粉碎成粗粉，用食醋煎煮三次，合并煎液，浓缩、醇沉、回收乙醇，浓缩成稠膏，加入到由橡胶、松香、氧化锌、凡士林、羊毛脂、椰子油等制成的胶浆中，再加入薄荷脑、冰片和樟脑，搅匀，制成涂料，涂膏，切断，盖衬，切成小块，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为橡胶膏，浅褐色，气芳香。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 解毒，消肿，止痛，燥“黄水”。用于骨质增生，风湿性关节炎，腰腿疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 外用，每十二小时左右更换一次，贴患处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 4x6cm2。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，置暗处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 对橡胶过敏者，不宜贴用。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (606, 2, 52, '调元大补二十五味汤散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 调元大补二十五味汤散(伊赫汤)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 红 花 25g &nbsp;秦 艽 10g 贯 众 10g 芫荽果 &nbsp;&nbsp;10g \r\n      蓝盆花 10g 白豆蔻 10g 五灵脂&nbsp;10g&nbsp;&nbsp;土木香 &nbsp;&nbsp;12.5g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;石 榴&nbsp;10g&nbsp;&nbsp;瞿 \r\n      麦 10g 麦 冬 10g 川木香 12.5g 款冬花&nbsp;10g&nbsp;川楝子&nbsp;15g 苦地丁&nbsp;10g&nbsp;&nbsp;小秦艽花 \r\n      &nbsp;&nbsp;10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;诃 子 15g&nbsp;&nbsp;细 \r\n      辛&nbsp;10g&nbsp;栀 子&nbsp;15g 野菊花&nbsp;&nbsp;&nbsp;10g 猪血粉&nbsp;25g 香青兰&nbsp;10g \r\n      胡黄连&nbsp;10g &nbsp;&nbsp;酸梨干&nbsp;&nbsp;&nbsp;&nbsp;10g 木鳖子(制) 10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十五味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色粉末；气微香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 收敛，解毒，调节寒热。用于“宝日”扩散，“赫依”、“协日”，“巴达干”失调，久病不愈，身倦体怠，口干，食欲不振，胃脘疼痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 水煎服，一次1.5―3g，一日1―3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (607, 2, 52, '菖蒲四味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 菖蒲四味丸(述达格-4)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石菖蒲 100g 高良姜 400g 木 香 100g 紫硇砂 200g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，粉碎成细粉，过筛，混匀，用凉开水泛丸，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰褐色水丸，味辛涩，气香。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 镇“巴达于赫依”，消喘止痛。用于隔肌痉挛，胸闷气短，胸肋刺痛。 消化不良。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次20―25粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每盒30g(约240粒)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (608, 2, 52, '菖蒲四味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 菖蒲四味胶囊<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石菖蒲 l00g 木 香 l00g 高良姜 400g 紫硇砂 200g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上四味，紫硇砂粉碎成细粉；其余石菖蒲等三味加乙醇提取2次，每次2小时，合并提取液，滤过，滤液减压回收乙醇，浓缩成稠膏；加入紫硇砂细粉混匀， \r\n      80℃烘干，粉碎成细粉，制粒，干燥，装入1000粒胶囊，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为棕褐色颗粒；气香，味咸、辛、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 镇“巴达千赫依”，消喘止痛。用于隔肌痉挛，胸闷气短，胸肋刺痛，消化不良。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2～3粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.25g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (609, 2, 52, '菊花七味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 菊花七味胶囊(乌达巴拉-7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 牛 黄 240g 五灵脂 400g 石 膏 160g 红花 240g 麦 冬 400g \r\n      苦 参 240g 菊 花 400g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，石膏粉碎成细粉、牛黄研细，其余五灵脂等五味，照流浸膏与浸膏剂项下的渗漉法(附录i \r\n      o)，用乙醇作溶剂，浸渍24小时后，渗漉，减压回收乙醇得稠膏状物；加石膏细粉，晾干，粉碎成细粉，加入牛黄细粉，配研混匀，制粒，干燥，装入1000粒胶囊，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为棕黄色颗粒；气微，味苦、涩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 凉血，清“协日”。用于胃“协日”，胃酸口苦，食欲不振，血热头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2～3粒，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.5g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (610, 2, 52, '黄柏八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 黄柏八味散(沙日毛都-8)<br>\r\n     <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 黄柏 200g 香墨 150g 栀子 50g 甘草 80g 红花 50g 荜茇 80g \r\n      牛胆粉 150g 黑云香 80g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，分别挑选，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黑绿色粉末；气香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清热凉血，固精。用于肾热，尿路感染，尿中带血，经下过多。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (611, 2, 52, '痔瘘六味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 痔瘘六味散(尚布如木-6)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 辣 椒 50g 诃 子100g 蔓荆子 100g 细叶铁线莲 100g 山柰 100g \r\n      连 翘 10g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上六味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄色粉末；味辛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 散瘀。用于内外痔疮，肛门瘘等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1―2次，用酸奶送服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (612, 2, 52, '清心沉香八味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清心沉香八味丸(吉如痕-8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 沉 香180g 广 枣180g 檀 香 90g 紫檀香 90g 红 花 90g 肉豆蔻 \r\n      60g 天竺黄 60g 北沙参 60g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，粉碎成细粉，过筛，混均，用凉开水泛丸，打光，干燥，每 50g约400粒。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡棕色水丸；味微涩而苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清心肺，理气，镇静安神。用于心肺火盛，胸闷不舒，胸肋闷痛，心慌气短。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次20～25粒，一日l～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇忌服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋30g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (613, 2, 52, '清血八味胶囊', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清血八味胶囊(琪孙哈木拉-8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 寒水石(凉制) 340g 紫 草 340g 土木香 52g 牛 黄 44g 栀 子 140g \r\n      瞿 麦 60g 石 膏 42g 甘 草 20g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，牛黄、寒水石分别研细；紫草、土木香加乙醇回流提取二次，第一次2小时，第二次1.5小时，滤过，合并滤液，回收乙醇，浓缩成膏；其余栀子等四味加水煎煮二次，第一次3小时，第二次2小时，煎液合并，滤过，浓缩成膏；与上述醇浸膏合并，加入寒水石粉混匀，低温干燥，粉碎成细粉，与牛黄粉配研混匀，制粒。自然干燥，制成1000粒胶囊，即得。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为胶囊剂，内容物为灰紫色颗粒；气香，味微甘。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清讧血。用于血热头痛，口渴目赤，中暑。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次2～3粒，一日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每粒装0.45g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密封。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (614, 2, 52, '清血八味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清血八味散(琪孙黑木拉-8)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 寒水石(凉制) 170g 紫 草 170g 土木香 26g 栀 子 70g 瞿 麦 30g \r\n      石 膏 21g 甘 草 10g 牛 黄 22g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上八味，除牛黄外，其余寒水石等七味粉碎成细粉，并牛黄与以上细粉配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为灰紫色粉末；气微，味微甘。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清讧血。用于血热头痛，口渴目赤，中署。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (615, 2, 52, '清肝二十七味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清肝二十七味丸(钦纳得棍斯勒)<br>\r\n     <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 石 膏 30g 红 花 40g 草 果 &nbsp;5g 诃 子&nbsp;7.5g \r\n      川楝子 &nbsp;8g 猫儿眼 15g&nbsp;&nbsp; 胡黄连 15g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沉&nbsp;香 \r\n      &nbsp;7g 檀 香 &nbsp;7g&nbsp;牛 黄 &nbsp;3g 木 香&nbsp;&nbsp; 8g 使君子 30g&nbsp;款冬花 \r\n      15g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;茵 陈&nbsp;&nbsp;5g \r\n      连 翘 &nbsp;5g 地 丁 15g 漏 芦 &nbsp;15g&nbsp;草乌叶 15g 肉豆蔻 &nbsp;5g&nbsp;&nbsp; \r\n      白豆蔻 &nbsp;5g <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;丁 香&nbsp;&nbsp;5g \r\n      栀 子 &nbsp;8g 降 香 &nbsp;7g 五味子 &nbsp;5g 黑冰片&nbsp;15g 苦荬菜&nbsp;15g&nbsp;&nbsp;&nbsp;寒水石(凉制) \r\n      30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上二十七味，除牛黄研细外，其余寒水石等二十六味粉碎成细粉，与牛黄细粉配研，过筛，混匀，每100g粉末加140g炼蜜，制成大蜜丸；同上制法过筛，混匀，用水泛光，打光，干燥，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状]本品为黑棕色大蜜丸或棕褐色水丸；气香、味苦。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 舒肝清热，健胃消食。用于急、慢性肝炎，脾胃虚热，骨蒸烦闷， 食欲不振，恶心呕逆等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次l丸，一日1～2次；一次11～15粒<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[注意] 孕妇慎服。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每丸重9g；每10粒重2g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (616, 2, 52, '清肝七味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清肝七味散(额力根-7)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 红 花&nbsp;180g 牛 黄&nbsp;80g 石 膏&nbsp;180g 香青兰 \r\n      60g 瞿 麦 60g 五灵脂&nbsp;60g 蓝盆花 60g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上七味，除牛黄外，其余红花等六味粉碎成细粉，将牛黄与以上细粉配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为棕黄色粉末；气微香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肝热。用于肝热目赤，黄疸，肝区疼痛，发烧口渴，头痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，―次1.5―3g，一日1～2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (617, 2, 52, '清肾热十味丸', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清肾热十味丸(波仁阿如-10)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 诃子 90g 红花 60g 白豆蔻 60g 紫花地丁 30g 刀豆 90g 枇杷叶 60g \r\n      紫草 60g 刺柏叶60g 五灵脂 60g 茜草 60g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法] 以上十味，粉碎成细粉，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为黄棕色粉末；气微，味微涩、苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肾热。用于肾热，肾损伤，小便不利，尿频，腰腿酸痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5―3g，一日 l一2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[贮藏] 密闭，防潮。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (618, 2, 52, '清肝九味散', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> 清肝九味散(给旺-9)<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[处方] 牛黄 50g 苦地丁 20g 木鳖子(制) 20g 红花&nbsp;30g 五灵脂&nbsp;30g \r\n      蓝盆花 20g 木香 20g 关木通 20g 瞿麦&nbsp;30g<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[制法]以上九味，除牛黄外其余红花等八味粉碎成细粉，与牛黄细粉配研，过筛，混匀，即得。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[性状] 本品为淡黄色粉末；气香，味苦。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[功能与主治] 清肝，凉血。用于受损性肝热，肝血热盛，黄疸，肝热，肝“宝日”，“宝日巴达干”等症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[用法与用量] 口服，一次1.5～3g，―日1―2次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[规格] 每袋装15g。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (619, 1, 6, '蒙医药治疗白血病初探', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n                         <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药治疗白血病初探<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;阜新市蒙医蒙药开发中心 \r\n      邢鹤林<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;"白血病"是十个高发恶性肿瘤之一，是35岁以下发病率、死亡率较高的恶性肿瘤，到目前为止，对此病尚无较满意的疗法，严重的危胁着人类的健康。我们几年来在用蒙医药治疗"再生障碍性贫血"病的同时，也收拾了一部分白血病患者，意外的收到了较好的疗效。几年来我们共收治了18例，现作以分析报告。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、临床资料<br>\r\n      本组病例男11例，女7例。年龄最小4岁，最大37岁，病程最短1个月，最长一年。其中：急性粒细胞性白血病4例，慢性粒细胞性白血病2例，急性淋巴细胞性白血病6例，急性非淋巴细胞性白血病3例，急性单核细胞性白血病2例，红白血病1例。全部病例既往经中西医治疗，非一例首治病例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、疗效<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组病例服蒙药后经骨穿检查完全缓解3例，占16.7%；部分缓解5例，占27.7%；未级解10例，占55.6%；其中一例急性淋巴细胞性白血病观察4年无复发，一例急性单核细胞性白血病观察2年无复发。其余正在继续观察之中，远期疗效有待于长期观察。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、蒙药对白血病的认识<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;血液在蒙医学理论中为七种体力之一。七种体力也称七体气，是维持身体的七种物质基础。血液方面的疾病为"血症"或"血分病"，为六种基本类型病之一。蒙医学无"白血病"之病名，但有血症一词，血症则分为体内恶血泛滥及血分败坏两大类型，我认为"白血病"亦属于后者，即血分败坏之病。血分败坏亦指维持身体的七种物质之血(健康血)，因致病而出现衰败之象的病症。蒙医的理论是血生于肝，即驻胃院的"平拉风"分离饮食的精华与糟粕，将其精华输入肝脏，经驻肝脏的"容光胆"将此精华生化为血液。由于饮食、起居不调，情志不畅等引起体内胆火和体力失去平衡，则可引起"不消症"，不消症的初期属"浊物不消"，由于浊物不消，则胃院的"平位风"不能分出饮食之精华，肝脏的"容光胆"不能将此精华生化为血液，造成"精华不消"，成聚、漏、散、突兀四种类型的痼疾，即"既久不消病病因变痼疾"。其中的突兀型则导致脾病及合病。合病是风、胆、涎三毒，并以血为主，兼有黄水综合为患的痼疾，其症状表现错综多变。"四部医典"论及合病章节中有云"合病入肝则同血症"，"合病入脾则为脾病"。合病按其病因病机可分为三种，其一称为"巴达干鲍日"，译为合病涎分淤紫症，在"四部医典"中译为聚合症紫色培根，简称"淤紫症"。我们认为白血病类似于淤紫症。淤紫可分为寒热两大类，两者均以血分病变为主，其发病部位有自点和他点，自点有四，其中"其点在肝症象如血病"。他点又分内外，可在皮肉、脉道、四肢关节、头部及肺心脾肾造成病变。淤紫按其证侯又分扩散、增展、突兀、卷聚四大类别。扩散还可分为内散与外散，增展又分穿与未穿(穿即出血)；突兀又分优与未优；卷聚又有新卷与旧卷之分等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;据此，便形成了我的以合病为入门，以其中的合病入肝脾为着眼点治疗白血病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;四、治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们按以上理论，选药配伍，组成方剂，辩证施治，无疑是以整体治疗为原则的，医治合病须从病因、缘起、病点、分期、类别、症象、治疗方法和预后八个方面去观察分析而施治，将其症侯分为肝脾两型。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;脾型：面色苍白，乏力，消瘦、消化不良、腹胀，常有出血现象，多有脾大，脾脉大多虚弱，舌苔白厚；投蒙药主方"血液一号"，即草果仁、石膏、地丁、木别子、小青皮、白叩仁、茜草、广木香、麦冬、石莲子、枝子、降香、广枣、西红花、射香等，共为细末，炼蜜为丸，每丸重5克，每次服1丸，日服3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;对合病入肝或入脾，若有以下症状较突出或由病情转化而出现，在服"血液1、2号"的同时加服散剂如同药引。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;腹胀：加服广木香、枝子、石榴、熟季花、白叩仁、荜拨(即蒙药如达柔巴)，共为细末，水煎，每次服2.5克。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;食欲减退：加服甘草、香菜籽、梨干、大米、茴香籽、蔓荆子(即蒙药兴阿柔巴)用法用上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;发烧：加服沉香、肉叩、广枣、红花、石膏、白檀香、降香、枯梗、青木香、苦参、山奈、勾腾、柯子、枝子(即蒙药阿嘎日召阿)用法同上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;鼻或牙龈出血：加服柯子、川楝子、枝子、紫草、草茸、枇杷叶(即蒙药"别日苏木汤"和"玛日布苏木汤")用法同上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;失眠：加服沉香、肉叩、广枣、广木香(即蒙药阿嘎日西汤)，用法同上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;以治疗过程中，对于其它并发症同样按蒙医学有关论述辩证施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;五、典型病例<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;例1：康艳，女，6岁，辽宁省阜新市人；于一九八五年五月二十日起无何原因的周身乏力，面色呈进行性的苍白，伴牙龈出血，食欲减退，发烧，去锦州铁路中心医院做骨穿确诊为"急性淋巴细胞性白血病(l2型)"经几大医院抗肿瘤治疗无效，于一九八五年六月一日来我所就诊，服用蒙药。服药二个月后骨穿检查"部分缓解(一级)"；服药三个月后骨穿检查"完全缓解"。以后停服蒙药约四年，于一九九o年二月五日随访，患者无自觉症状，现已上学三年了，学习成绩良好。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;例2：庄宁宁，男，6岁，辽宁省阜新市人，于一九八六年一月起无明显诱因的发烧，全身皮肤出现散在的小红点、乏力，伴牙龈出血少量，食欲减退，恶心，去阜新矿务局总医院做骨穿诊为"急性单核细胞性白血病"，后经天津等地几大医院治疗无效，于八七年一月六日来我所就诊，服药四个月后骨穿检查"完全缓解"。于一九九o年二月九日随访，患儿无自觉症状，现已上学一年，学习成绩较好。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;六、小结<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组18例，完全缓解3例，部分缓解5例，蒙药治疗白血病显现出一定的疗效，但因我所条件有限，治疗白血病的时间尚短，病例较少，远期治疗有待于继续观察，预计蒙医药以他的独到之处，治疗白血病可能成为有希望的新疗法。<br>\r\n      本论文没有讨论一项，我们考虑蒙医药治疗白血病刚刚起步，几年来仅仅作一些探索性试验，为此不好提出更多的论理。<br>\r\n      谢谢各位。 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (620, 1, 6, '癫狂病蒙医治疗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    \r\n      <br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                                                                                           &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;癫狂病蒙医治疗<br>\r\n      \r\n      \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青海省海西州都兰县蒙藏医脘 \r\n      罗 达<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;癫狂病即现代医学之精神病。在蒙医临床上分赫依型、希拉型、巴达干型、聚合型、精神意识型、中毒型和中邪型七种。现将各型症状和治疗简要分述如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;赫依型癫狂，由于体内三根紊乱，特别是赫依偏盛之后侵垄心脏，进而影响白脉功能所致。症见舌干而质红，尿清澈如水而多泡沫，脉芤而时有停搏或虚细难以摸到。头常痛，心颤、神心不定，突然嚎哭或狂笑，谵语，手足颤抖等。治宜以抑赫依，疏通白脉为原则。药物早投十三味纳木吉格散与七味广枣散各1.5g、开水送服。午前9时许，投如意至宝丸(额尔德尼丸)1.5g。用四味列公汤送服。下午1时，再服早晨的药。下午5时，投希木吉德(安神养心丸)加各种动物心脏水调为丸，如豌豆大，以朱砂为衣，日服5～9丸，用四味沉香汤送服。晚9时，投二十味沉香丸用三味檀香汤送服。饮食起居方面，按赫依病之护理原则加以护理。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;希拉型癫狂，由于体内三根紊乱，特别是希拉偏盛，与赫依交搏而侵及心脏，进而阻拦白脉之通路所致。症见舌呈灰黄，尿赤黄而气味大，脉紧而数，体热口苦，头痛，胸部刺痛。易怒打人，癫狂发作多在中午和午夜，有破坏性。治宜以抑希拉镇赫依，清热通经为原则。治疗先投缓泻剂泻之，其次每日投八味地丁散与拉利纳木吉拉各1.5g，日服2～3次，用白糖水送服。并投八贵散加五凤丸5粒，开水送服。晚投二十五味维命石榴散1.5g，用三味檀香汤送服。退热后，早投十四味纳木扎格散，中午和晚间投安神养心丸或投二十五味维命石榴散用四味檀香汤送服。饮食起居护理同赫依型。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;巴达干型癫狂，由于体内三根紊乱，特别是巴达干偏盛与赫依交搏而侵及心脏，进而阻拦白脉通道所改。症见舌苔灰白而厚，尿呈灰白色，气味小，脉迟而沉，不思饮食，消化不良，唾液增多，时有呕吐，少语发呆，动作笨重，困倦嗜眠，喜一人独居。治宜以祛巴达干镇赫依通经活络为原则。药物早投十四味纳木扎格散与六味安消散共服。亦可投十六味达利散，用二味木香汤送服。午间投十一味兔心散与安神养心丸共服。晚投如意至宝丸，用三味檀香汤送服。饮食起居护理与依赫型相同。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;聚合型癫狂，本病主要由于癔病或其它型癫狂病之加重而继发。其它癫狂病及血病、黄水病等之症状，都可混杂出现。脉象粗而芤紧或芤迟，舌呈青红苔灰黄。一般病势较重而易于复发。治宜以调理三根，抑赫依通白脉为原则。药物早空腹投七味广枣散加五味金色诃子散共服。并投二十五味大汤与三十五味沉香散加红糖共煎，日服2～3次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;精神意识型癫狂，由于三根失调，赫依偏盛后与希拉、巴达干相搏，阻拦白脉通路而影响思维功能所改。症见心悸、失眠等赫依偏盛性特征。心中不舒，一经感触，即行发病。脉象粗而紧，出现类似赫依型症状。治宜以抑赫依，安神宁心为原则。药物投七味广枣散与十四味纳木扎格散共服，日服2次。晚投安神养心丸，用三味檀香汤送服，连服5至7日，患者应在安静舒适的环境中静养。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;中毒性癫狂，全身皮肤色泽干憔不华，神志错乱，舌呈黑色，脉象虚细。治宜以解毒镇静为原则。药物投以七味黄花黄芩散。同时给三籽汤与七味沙棘散共服。晚投三十五味沉香散，用羊骨汤送服。<br>\r\n      中邪型，中邪而伤，行为突变如魔。治法按《四部医典》密诀本所述疗法施治。<br>\r\n      对各型癫狂病，还辅以薰，涂、灸等外治则疗效尤佳。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;七味黄花黄芩散配制法：黄花黄芩30g、党参30g、查干榜嘎20g，茜草l0g、姜黄 l0g、甘草20g，共研细末，用健康8岁童尿调和为丸，每丸0.5g，日服2～3次，每次 \r\n      3―7丸。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (621, 1, 6, '蒙医治疗癫痫症72例观察', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n\r\n      <br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医治疗癫痫症72例观察<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古阿拉善盟蒙医药研究所 \r\n      阿拉腾巴根<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;癫痫症是临床常见的一种严重发作性神经异常的疾病，属疑难杂症之一。本人以《四部医典》理论为论据，结合脑电图为诊断依据，用蒙医药治疗癫痫收到了满意的效果，现报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      本治疗组72例均为门诊病例。发病年龄最小8岁，最大62岁．平均年龄35岁；发病时间最短6年，最长12年，平均9年；发作间隔时间最短3d，最长60d，平均31d；治疗时间最短21d，最长105d，平均63d。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物组成：沉香30g、肉豆蔻30g、朱砂 30g、条姜黄25g、三七20g、全蝎50g、甘草 25g、胡黄连18g、黄柏15g、蜈蚣15g、巴豆 \r\n      15g、珍珠10g、绿松石l0g、丁香6g、马钱子 6g、旋覆花6g、牛黄0.6g、麝香0.6g。加减：痰火型加天仙子12g、兔心1个、广枣6g；风痰型加阿魏6g、紫硇砂6g、白豆蔻6g。以上组方药物共研细面口服，每d3次，每次3― \r\n      5g。<br>\r\n      五疗：痰火型者针刺曲池、小海，放微量血，但不可深刺；久迷不醒者针刺十宣，放微量血；风痰型者用温针强性针刺或灸风池、风府、百会、上星、印堂、鸠尾、曲池、合谷、后溪、通星、三阴交、太冲等穴位。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 治疗结果：治疗72例病中痊愈59例，有效10例，无效3例。治愈率81.8％，总有效率97.4％。治疗中发现年龄不同，疗效各异，年龄越小，疗效越佳，随着年龄递增，疗效降低。另外病程长短也是影响疗效因素之一，病程越短疗效越好，随着病程延长，疗效越差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;癫痫症系医学疑难杂症之一，其发生主要由情志失调、大惊大恐、饮食失节、劳累过度、热毒所伤以及外伤等因素，导致心、肝、胃、肾等脏腑功能失调所致。《四部医典》记载，人体有赫依、希日、巴达干、血、黄水和虫六种病基(即六种内因)。而癫痫症，正是前四种病基太过与不是引起的合病，它们的具体分布巴达干(胃)、希日(小肠)、赫依(盲肠)、血(肝)。由希日、血引发的为痰火型，由巴达干、赫依所致的为风痰型。故治疗本症应分辨寒热虚实，佐以调整前4种病基为主。嘱患者坚持一定的时间服药调理，巩固疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;除药物治疗外，还要加强心理治疗，帮助患者正确对待本病，解除紧张、焦虑情绪，树立战胜疾病之信心，医患积极配合，才能达到满意疗效。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (622, 1, 6, '蒙医对冠心病诊治的探讨', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n\r\n    </td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医对冠心病诊治的探讨<br>\r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 \r\n    布仁达来 乌仁图亚<br>\r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;蒙医历代医药文献中都有对类似冠心病心绞痛、心肌梗塞等症候的较详细的描述。如“心刺痛”、“粘剌痛”、“心热症”、“心虫症”等与冠心病心绞痛、心肌梗塞以及由此所致心源性休克的临床表现相一致。现概述如下： \r\n    在《甘露四部》中描述“粘刺痛在左胸部剑刺样疼痛”。在《蒙医金匮》中描述“心热症为血潴留于心脉而左乳处刺痛和心脏增大，粘刺痛伤心肌，在左胸阵发性刺痛”。在《蒙医药选编》中描述“心刺痛分赫依型和血型刺痛。症状是头昏、目赤、舌干、气短、左乳处针刺样痛、散及左臂”。叙述了心痛的主要部位及胸痛彻背，痛如针刺，呈发作性的特点。对无痛性的描述，如《蒙医药选编》中“心闷症的症状是健忘、多虑、嗜睡，心前区有石塞感闷痛”。叙述了发作性闷痛或压榨性钝痛的特点。对心绞痛心肌梗死的描述，如同书“心虫症的症状是眼睛发黑，心悸气短，心前区有拉锯感疼痛”。蒙医心虫症则指心肌大面积坏死。<br>\r\n    对心肌梗塞所致心源性休克的描述，如《观者之喜》中“心热炽盛的症状是心悸、胸部刺痛，前胸后背好似火烧，神志不清，昏厥”。又如《蒙医临症医药鉴》中“心血热症在左乳处或胸背剧烈刺痛而昏迷”。说明历代蒙医对本病的严重性有了一定的认识。 \r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;对病机病变部位的描述，《甘露四部》指出：“心为五脏之首，其病因为思虑过度，悲伤等而赫依紊乱，堵塞心脉所致”。说明其主要病变部位在心脏与滋养心脏的血脉。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;1．病因与病机<br>\r\n    发病因素，有饮食因素、起居因素、时季因素、精神情志因素、劳逸失当因素、年龄因素、先天秉性因素等。体内三根失调，赫依血运行不畅，清(津)不消化，滋养心脏的血脉瘀滞不通是发病的内在因素。其病理发展是一个复杂的过程，主要病变部位在心脏及滋养心脏的血脉。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;2．辨证施治 <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;2.1 血热性心痛：主要症状为左乳处固定不移针刺样痛，气短，面色苍白，出冷汗，口干舌燥，脉洪、数。治则：分化清浊，分离混浊之血液，清热凉血、化瘀，镇痛。治法；通拉嘎一5味散3g温开水送服，吉如很・阿嘎日―一8味散1.5g加吉如很・古日古木一一7味散1.5g用赞丹――3味汤引服，黑木日嘎散1.5g加哈图拉嘎一6味散，用古古勒汤引服。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;2.2 赫依性心痛：主要症状为左胸或胸骨后不定地刺痛，引肩背、散及左臂至手，头晕头昏，气短，眼脉张红，口干舌燥，脉虚、微。治则：调理赫依血运行，疏通全身血脉，镇痛。治法：匝迪一5味散3g煎服；吉如很・阿嘎日一8味散1.5g，加哈图拉嘎一6味散1.5g，用古古勒汤引服；阿嘎日一35味散3g用毛勒日・达布斯一4味汤引服。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;2.3 心热症：主要症状为心慌气短，心前区灼热，胸痛彻背，心痛如刀割，重者晕厥。治则；清热凉血，强心镇痛。治法：额日赫木――8味散3g用赞丹一3味汤引服，嘎布日一25味散1.5g加哈图拉嘎一6味散1.5g开水送服，吉如很・古日古木一7味散3g用赞丹一3味汤引服。同时根据患者年龄和体质情况，可在肘内脉针刺放血(微量)。以上药剂可根据情况分早午晚3次给药。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;3．疗效机理探讨<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;从心绞痛的缓解与心电图显示的心肌损害、冠状动脉供血不全的好转和恢复的治疗效果来看，施治方法中的促进体内清浊分化、改善赫依血运行与活血化瘀、通脉镇痛等药物所组成的前述方剂，确有疏通心脉的瘀滞，使赫依血循行得以流通的作用。其机理，大体有以下4个方面。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;3.1．调理三根之失调，促进清浊分化，改善全身赫依血运行。赫依与血的关系是赫依畅则血行，赫依滞则血瘀。当三根相搏，特别是普行赫依功能发生紊乱时，滋养心脏之血脉发生瘀滞．则出现心胸疼痛或胸闷气短等病症。调理赫依的药物能疏理赫依，使其运行舒畅，则有利于血液循行。活血化瘀药物能消除与改善血脉中血液之瘀滞，则有利于血液循行通畅，亦有利于被瘀浊侵损之脉道的恢复。从而使滋养心脏之血脉中的赫依血循行得以改善通畅，心胸疼痛之病症得以缓解。 \r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (623, 1, 6, '再论蒙医骨伤学对骨折修复的认识', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n\r\n    </td>  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再论蒙医骨伤学对骨折修复的认识<br>\r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 \r\n    和日伦巴特尔<br>\r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;我们在1997年第6期《中国骨伤》杂志中初步论述了蒙医骨伤学将骨折的修复愈合过程分为血肿期、血痂期、软骨痂期和骨痂期等四期。从血肿期到骨痂期的全部过程中，骨痂的质变是由外向内渐进的。随着骨痂的质变，各期对干扰骨痂生长的骨折端活动的承受度也不同。因为与骨折端同位的骨痂中心，其硬度总是次于外围骨痂，故骨折端总有一定范围的活动度。这一活动度刺激促进着骨痂的形成与质变，随着骨痂的质变逐渐由外向内缩小，肢体承受外力的能力也逐渐增强，直至骨折端全部连接。并认为各期骨痂的形成到质变所需的时间在不同年龄段是相对恒定的。即蒙古族俗语中所说“骨折愈合所需天数同自身年龄相同”。所以骨折愈合的速度取决于各期向下期质变的早与晚。其中血痂的形成和向软骨痂期的转变尤为关键，因为蒙医骨伤学认为在骨折修复愈合的全过程中血痂期的血痂形成与质变最快，又是整个骨痂形成，骨折连接的起点和基础。所以蒙古正骨各家们在治疗骨折时特别重视血痂的形成与质变、并力争在血痂形成前将骨折一次性正复到位。尤为忌违因整复固定或活动不当而破坏已形成的血痂。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;在临床中我们发现，蒙医骨伤学中所说血痂期实际就是现代医学中的伤部血肿及邻近组织的机化期。我们认为骨折后断端部的血肿及周围组织的机化，也同疼痛一样本是制止损伤部活动度的机体本能的保护性反映。这一反映在靠近活动度大的关节部的骨折尤其明显。例如踝关节部的骨折、四五天后手法整复时会因损伤部的机化所致极难复位。这种机化反映实质上弥补了骨折初期断端不可能形成足够骨痂来限制骨折端活动之不足。这种机化，早期在血痂中心骨折断端的适度活动的应力刺激中逐渐强化，并激化骨痂的快速形成和质变。中后期则随着骨痂的量与质的增强，肿痛的缓解和患肢活动量的随之增强时(即血痂期进入软骨痂期)机化度反而相应衰减，又起到了舒筋活血，消除组织粘连和斑痕挛缩的作用。但骨折断端的活动若超出血痂所能承受的活动度范围时，自然要对已修复的组织及骨痂造成新的损伤和破坏，因为血痂的质相对比较弱，极易被人为的破坏。破坏后再从新形成到质变，必然延长整个骨折的愈合。如反复破坏则因此期的缺损，势必造成骨折愈合的迟缓乃至骨不连症。若彻底制动，骨伤部早期则会因应力遮挡，使组织机化的速度、强度迅速减弱。此时骨折断端所形成的血痂因其量与强度不足，造成骨折端失去自然保护的空白期。同时固定与制动也使组织受压迫，气血运行受阻(失去了肌肉泵血的作用)，妨碍了损伤部的正常新陈代谢及修复，延长了骨折的愈合。这就是骨伤患者中自然愈合者虽有畸形，但愈合时间往往不比经合理治疗者的长，而经正规骨牵引治疗的骨折病人，在血痂期活动度已明显僵化了的骨折端，过了此期反而再度软化的原因。也是越早越易整复，愈合也快的主要原因。也是蒙古族正骨各家对肢体远端部的骨折(如前臂、腕部、胫腓骨、踝关节处等)整复后尽量短期固定或不于固定都能有良好疗效的原因之一。尤其是对那些天性比较谨慎的成人或老人的常用方法。它是利用骨折远端自身重力影响少，整复后若无特殊外力作用，一般不会移位的特点和患者惧怕疼痛及复位的断端再度移位的怵惕心理不仅发挥了病人的主观能动性，又消除了因固定所致的伤部组织受压，气血运行受阻，影响血痂期血痂的量变与质变如期进行的副面作用，使骨折的修复愈合达到了最理想的快速高质程度。所以血痂期―一即机化期本是骨折连接愈合过程中不可缺少的重要环节之一，是机体自我修复本能的一种。因此我们的治疗手段必须是师法自然，与这修复本能的机化结痂规律特点相结合、相同步并促进之，才能取得理想的疗效。它说明传统蒙医骨伤学中对骨折修复过程中的分期及不同骨痂的量与质的变化认识确有其科学性，很值的我们进一步探讨研究。', 1, '2003-06-07');
INSERT INTO myy_text VALUES (624, 1, 6, '红斑狼疮', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n\r\n    </td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                                                &nbsp;                                                             &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   红斑狼疮<br>\r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;1 一般治疗<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;本组病例均在各地医院确诊，并经治疗效果不显的典型全身性红斑狼疮(sle)患者。其中女性28例，男性l例；年龄最小的18岁，最大的42岁；病程最短的1年，最长的11年，其余均在3～5年。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;根据病情的轻重，病程的长短及病情侵蚀脏腑程度，按蒙医“三根”理论，进行分型、辨证施治。按“三根”的盛衰，属巴达干、赫依型或赫依偏盛型者，以助消化，分清化浊为主，用当玛 \r\n    5味，嘎日那各10味或扫吉德口服。属希拉 (热)偏盛型者，以降肝火，清热解毒为主，用古日古木13味，巴特尔7味，嘎日迪5味等口服，加用玛日布3汤剂作引子，为增加其清瘟解毒作用可加用适量牛黄、麝香为好。属风湿热痹者，用古古勒11与达敖德・淖日冲；午晚交替口服。对月经不调者，晚用乌力吉18、给喜贡讷 \r\n    6味口服。本组病例中17例均有不同程度的肾损害，并表现少尿、浮肿、蛋白尿(++-+++)及尿毒症表现，就此我们用萨丽充阿与吉召木・道尔吉交替口服，同时据浮肿程度及每日尿量，配用色玛3汤、诃日嘎4汤及斯日吉8等利尿药作引子。由于本病临床症状错综复杂，在治疗上不应只局限于单方，视病情不同阶段及临床特征，应辨证论治。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;3 疗效判定<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;基本治愈：症状、体征完全消失，实验室检查各项指标达正常范围；显效：症状、体征明显减轻，实验室检查大多恢复正常；好转：症状减轻，受损脏腑的功能治疗前好转；无效：经充分治疗后症状、体征未改善者。 \r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;4 治疗结果<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;本组治疗观察29例中，基本治愈者13例，占 44.8％；显效6例，占20.6%；好转5例，占17.2%,无效5例，占17.2,总有效率为82.6%。<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;5 临床体会<br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为，红斑狼疮起因主要由于三根失调，七素失养而导致恶血增生及黄水逆增，或因肝胆受损致血热炽盛而生成恶血、黄水；或由于巴达干赫依之邪、恶血散之全身，使人体对侵蚀物的抵抗力下降，体内脏器相应的出现异常或功能减弱而致。恶血在偏盛情况下，可以单独致病，亦可以与其它病邪混合成为多种并发症、聚合症的病因，引起复杂的病变。其治疗据“三根”之盛衰，辨证用药治疗。如以肾侵蚀为主时，以调和肾寒肾热，通利水道，温补肾气的原则。以肝侵蚀显著时，则治宜清热解毒、清黄水热、益肝保肝及燥黄水为原则，并据病情侵蚀脏器的程度、部位而用药。 \r\n    <br>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;本组29例患者平均疗程为6个月。这与病邪累及心脏、肝肾脏器的程度及服用肾上腺皮质激素的量及时间有密切相关。病邪累及脏器程度轻，激素服用量少，时间短者疗程相对较短，且疗效较好，预后也好。据笔者综观分析及资料查阅，sle与脑垂体细胞分泌功能失调有密切关系，尤以泌乳素和甲状腺素分泌浓度相关。如狼疮性肾炎比例之高，是与抗利尿激素的分泌失调极为相关。用蒙药以平衡体内“三根”，调节内分泌之机制治疗，取得较好疗效，可能与改善垂体细胞分泌功能有关，有待进一步的探讨。\r\n    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;系是赫依畅则血行，赫依滞则血瘀。当三根相搏，特别是普行赫依功能发生紊乱时，滋养心脏之血脉发生瘀滞．则出现心胸疼痛或胸闷气短等病症。调理赫依的药物能疏理赫依，使其运行舒畅，则有利于血液循行。活血化瘀药物能消除与改善血脉中血液之瘀滞，则有利于血液循行通畅，亦有利于被瘀浊侵损之脉道的恢复。从而使滋养心脏之血脉中的赫依血循行得以改善通畅，心胸疼痛之病症得以缓解。 \r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (625, 1, 6, '蒙药治疗传染性肝炎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;蒙药治疗传染性肝炎 &nbsp;&nbsp;&nbsp;<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刘毅<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自1993年以来笔者在临床上共收治40例传染性肝炎，采用蒙药“侵那大滚思勒”治疗收效良好，报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;40例患者中黄疸型19例，无黄疸型2l例；男性25例，女性15例；年龄最小15岁，最大60岁；食欲减退者33例，腹胀乏力者34例，肝区痛22例，肝肿大者31例，发热者4例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;方药：寒水石、石膏、红花、丁香、肉蔻、草果仁、诃子、川楝子、栀子、牛黄、白云香、沉香、紫檀香、广木香、使君子、冬花、猫儿眼、黑片、胡连、地丁、漏芦、苦麻菜、草乌叶。上药除牛黄外共研细粉，然后兑入牛黄粉拌均做成密丸，每日2次，每次l丸，有黄疸者加茵陈、大青叶，板蓝根1个月为1个疗程。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;疗效分析<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗标准：近期治愈：共35例(主要症状消失，肝肿大恢复正常，肝功能检查正常)其中黄疸型18例，无黄疸型17例，占87.5％；明显好转：4例(主要症状消失，肝肿大恢复正常，肝功能检查麝浊试验进步，其他项目正常)，黄疸型l例，无黄疸型3例，占10％；无效： \r\n      1例(服药1疗程无明显疗效改服西药治疗)，占2.5％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;传染性肝炎属蒙医学的“粘性黄疸”病范畴，本病系由粘虫引起的肝脏传染病，起因为致病粘虫经口入胃内，随后借血行窜入脉道，降于肝胆，使胆汁逆溢脉道及皮肤，故皮肤及巩膜发黄，发病日久肝失疏泄，肝之清阳不升，水谷不能运化，气血生化乏源，故出现纳差、乏力。肝失条达，脾运不健，水湿不运，故出现腹胀、肝肿大等症状。侵那大滚思勒具有疏肝理气飞健脾和胃、杀粘虫之功效，方中以草乌叶、白云香、大青叶、板蓝根杀粘虫，以牛黄、栀子、胡连、苦麻菜、地丁、石膏等清热，以木香、草果仁、肉蔻等药疏肝理气、健脾，诸药合用，取得满意疗效，则临床疗效恒定持久，有应用推广的价值。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (626, 1, 6, '用蒙药利尿方医治狂犬毒之经验', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;用蒙药利尿方医治狂犬毒之经验<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古锡盟正蓝旗蒙医医院 \r\n      桑如布<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;狂犬毒主要被疯狗咬伤而发生。其毒侵及白脉和脊髓。如不及时治疗，一经发作，使病者发疯而死亡。本病潜伏期从一周至一年零八个月不一，而多数为两周至两个月。兹将本人用蒙药利尿方治疗狂犬毒之点滴经验概述如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;狂犬毒分新，旧两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、初期咬伤，伤口发肿，呈紫黑色，流黑色之血，或不流血。发作时，发热头痛，面赤、恶寒，全身有蚁行感，不欲近人。加重则心神不舒，眼脉发黑，神志疯狂或昏厥不省，恐水，目瞪直视，舌长发黑，尿赤黄而不转变，大便发黑。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;对初期咬伤，用绷带在伤口之上一寸处扎紧，再用吸角吸去恶血。如伤口较大，可先用植物油涂敷，以小铁片烧热烙烫，然后用姜黄、草乌、麝香、茜草等共研细末，植物油调敷，亦可用铁青色种马之粪汁与奶酪调和涂之。药物可投红花、狼毒研细服用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、利尿治疗：利尿之前，先以三籽汤加入疾病之对治药各等分，煎汁于夜间内服，然后用八味雪蛙散为引将脉道洗涤干净，再以小白蒿、酒糟水煎趁温擦洗全身，其次以黄油或植物油配斑蝥涂擦全身，继而饮服花椒、冬葵果汤，使脉口开放。接服引发之药，以收毒邪骚扰引发之，药方：硇砂、丁香，螃蟹、朱砂、冬葵果，用红糖或白糖为引，开水送服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;对狂犬毒利尿治疗剂有以下三方：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1）齐宁东斯乐。成分：诃子12g、草乌 6g、菖蒲9g、斑蝥12g、雄黄6g、紫硇砂 3g、木香6g、红花6g、肉豆蔻6g、丁香 \r\n      6g、石膏6g、豆蔻6g共研细末，水调为丸(豌豆大)，以朱砂为衣。每次服7～9丸。用白酒或四味蒺藜汤送服，根据患者病情和体质。分别以7、14、21天为一疗程。<br>\r\n      本方为凉性，治狂犬毒之上药。如以具利尿、泻毒之功的斑蝥为主药，配诃子以解毒、调理体素，草乌以杀粘消炎，木香以清粘热，破凝聚，红花以消肿，丁香以解毒愈创，肉豆蔻、豆蔻以抑赫依，增进食欲，紫硇砂以清热，石膏以愈伤，菖蒲以理白脉， \r\n      杀虫，雄黄以杀粘愈伤。<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2）十六味齐都克桑布。成分：六良药、冰片、查干榜嘎、紫茉莉、紫檀、白檀、紫草茸、自然铜、诃子、黑云香、姜黄等配伍，共研细末为散剂，每日服1.5g开水送服。此方亦可涂于伤口或由鼻孔吸入。系凉性药，具有解狂犬毒之功。配诃子以解毒，红花、紫檀以清热消肿，丁香以抑赫依解毒，肉豆蔻、豆蔻、草果以抑赫依，增进食欲，石膏以清热愈伤，冰片以清脏器伏热，黑云香以杀粘，紫草茸以破凝聚，白檀以滋补身体，查干榜嘎、紫茉莉以解毒，自然铜以治骨伤，姜黄以制粘毒。此方具有改善五脏六腑功能之作用，为调理体素，消肿愈伤，解除狂犬毒之上药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3）四味蒺藜汤：蒺藜9g、冬葵果6g、螃蟹6g、海金沙3g，共研细末，每次3～ 6g。水煎内服。对儿童可加豆蔻或十味豆蔻散共服。此方之利尿作用显著。服利尿剂后，可出现血泻或粘液性便，尿血和尿道灼痛等现象。如经一定时间，尿血消失则证明狂犬毒已除。外治可辅以黑白际穴。<br>\r\n      治疗后，在一年之内不宜饮酒，避免剧烈活动，不宜吃鱼、猪肉和奇蹄类之肉。亦不宜过食苦、辛、涩味之品。须在安静的环境疗养。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本人在1981-----1987年间，用上述疗法受治百余例被疯狗咬伤的患者，治愈率达 98％。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (627, 1, 6, '蒙药治疗类风湿性关节炎40例', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药治疗类风湿性关节炎40例<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽宁省阜新蒙医药研究所 \r\n      海秀智&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽宁省阜新卫校蒙医分校 \r\n      代成宏<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药治疗类风湿性关节炎具有明显的疗效。自1991年以来在临床上我们收治了40例，就其治疗效果报导如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;40例均为门诊病例。其中男性17例，女性23例；年龄最小 9岁，最大57岁；病程1年以内者5例，2～4年者18例，5～9年者 \r\n      10例，10年以上者7例。40例中均有多发性对称性关节肿痛，指、趾、腕、膝、脊柱形成畸型，足不能着地，皮下结节，血沉加速，类风湿因子阳性，x线关节拍片有类风性关节炎改变。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;方药：白檀香15g、紫檀香15g、石膏 10g、西红花15g、公丁香10g、肉豆蔻10g、草果10g、白云香15g、草决明15g、苘麻子15g、诃子10g、栀子10g、川楝子10g、小青皮10g、辛夷10g、苦参10g、莨菪子10g、蜀季花10g、玉珍花10g、杜仲10g、地丁10g、白豆蔻10g、牛黄3g、麝香10g、干驴血25g。上药除麝香、牛黄共研细面，然后兑入麝香、牛黄拌均做成水丸豆粒大，成人每次7～9粒，日服2次，儿童酌量。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;基本控制12例，(临床症状全部消失，化验指标正常)占30％；显效14例，(临床症状大部分消失，化验指标正常或基本正常)占 \r\n      35％；好转12例，(临床症状部分缓解)占 30％；无效2例，(临床症状无改变)占5％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本病属蒙医学“图例”病范畴，其发病内因主要是体内“黄水”积聚，与局部气血搏结而成；外因主要是长期起居湿地，汗水当风，长期进寒凉、油腻不易消化食物等，致使胃火衰弱，不能充分消化所进之食物，进而不能分清浊，使糟粕误流入精华的脉道，使精华变浊，促使“黄水”增多，流注于关节、筋骨、肌肉等处，与注处气血相搏，注处气血紊乱，运行阴滞，因而引起关节红肿热痛，功能障碍，皮下结节形成，久之与存在于骨内“赫依”相搏引起关节强直，畸型等临床表现。“嘣・日阿格一25”具有燥“黄水”调和气血之功效。方中干驴血味甘、咸，性温，燥“黄水”，是该方的主药，与白云香、草决明、苘麻子合用，燥“黄水”之功效更显著；白豆蔻、杜仲补肾止痛，调补气血；诸药合用共凑燥“黄水”调补气血之功效，对类风湿性关节炎有显著疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (628, 1, 6, '蒙医辨证治疗1000例淋巴结核', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医辨证治疗1000例淋巴结核<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院 \r\n      白云 格日乐图 乌兰<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;笔者在20多年的临床实践中，用传统蒙医理论指导。研究出新型治疗淋巴结核系列验方，灵活应用药物和食疗辨证施治，取得了事半功倍的效果。现将其千例淋巴结核患者的治疗体会报告如。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 资料来源：千例患者主要来源于内蒙古牧业地区和蒙古国。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;l.2 诊断标准:1) 淋巴结肿大，液化成脓或破溃成瘘。符合淋巴结结核的临床表现和病情发展；2) \r\n      经病理检查和血液化验确诊；3)排除先天性，非结核性增生性赘瘤和其它各种类型的淋巴结肿大，囊肿或慢性瘘道形成等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 临床分型：依据病因和病变特点，把结核分为五种类型，1)赫依型：肿块变化和疼痛不定，触之有粗糙感：2) \r\n      血希拉型：红肿、发热、剧痛及很快化脓。脉象、小便出现希拉病之症；3)黄水偏盛型：消化不良，体质衰弱，消瘦盗汗，肿块之红肿热痛相对较轻；4) 粘偏盛型：发病中期或后期疼痛剧烈或触之有明显疼痛感，肿块化脓溃破流出粥样脓液的同时，逐渐形成大面积疮瘘，病情加重而不易愈合。偶有由于粘性传染引起高热、燥渴，病情进―步恶化危及生命；5)巴达干偏盛型：肿块坚硬，疼痛柔和，化脓溃破过程缓慢或反复发病多年不愈。偶有瘘道扩大不易生肌收口而形成漫长病变过程。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 临床归类：依据患者实际体质和寒热虚实，分为寒性和热性两大类。1)寒性：病程长、体质弱、脾胃虚、肿块多为化脓溃破，反复发病以成瘘道，除淋巴病变外，全身结核活动不明显。2)热性：病程短、起病慢，红肿热痛明显，常伴有其他部位和器官的活动性结核，脉弦紧促，大便燥结，口渴，目赤。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 一般资料：千例患者中，男134例，女 866例；30岁以下者527例，占52.7％，31―50岁者363例，占36.3％，50―70岁者110例，占11％；发病6个月～一年者485例，1～ \r\n      3年者443例，3年以上者72例；病程最短6个月，最长为10年。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：1)杀粘、燥黄水、清恶血、消肿愈疮为主，并结合病位、类型、随症施治；2)三期分治法：硬结期(初期)，以消肿内治为主，中期(即脓肿期)以消肿溃坚为主；后期也叫破溃期，以清疮生肌为主。3)归类对证法：寒性，应强脾胃、补中气；热性，清胃肠、泻火气。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;首先应放恶血，用传统蒙医放血疗法。常用放血脉窍主要是舌脉和发病最附近之脉。放血前1～2天，寒性配以希拉汤；热性配以查干汤，连服数剂，常规放血之后，24小时内给予花椒、冬葵果等量煎汤，用来开通脉窍。隔日黎明时，根据患者体质情况，服用十三味阿拉格斑布脉泻丸5―7粒，2小时之后加用催泻剂进行泄泻。发病初期，亦即硬结期，一般用药l一3次便可痊愈。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;如用上述疗法未能治愈或淋巴结肿块化脓时，调制药用马骨、鱼肉、姜活、瑞香狼毒、寒水石为主，加用五味甘露方温浴施治。如脓肿已溃破成疮，则用十一味萨拉玛日药条放人疮瘘内。清除腐肉和脓液，止痛。待疮瘘清理之后。放人八味巴拉都格生肌散，能使肉芽迅速生长，疮瘘收口，疾病痊愈。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;归类对证主要是依据寒热不同类型和患者的临床表现服用对证药物或食疗。如寒型应以补脾健胃暖胃的五味清浊散或四味石榴散，胃寒胀气时给予十三味石榴散，女性患者还应给予调经去寒的药物。有条件的可长期服用酸马奶用于补虚抗痨。热型病应在治疗前连服两天查干汤或十五味沉香散，治疗期间应对症给予三红汤、十八味水银丸，注意务使大便燥结，给予清胃肠的药物。食疗上应根据疗程进展，配以鲜羊肉汤和四骨汤、酸马奶以巩固疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 结 果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 疗效标准：痊愈：肿块消失或疮瘘全部愈合，随访6个月，未有复发；好转：肿块未能全部消失或疮瘘未能全部愈合，或愈合后又复发；无效：治疗后临床表现和体征无变化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 治疗结果：在千例患者中痊愈957人，治愈率达95.7％；好转共43人；有效率达 100％。硬结期治愈率为99.5％；脓肿期治愈率为99.5％。治愈天数最短7天，最长为28天，平均治愈天数为14天。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;淋巴结核是由粘毒感染引起的慢性传染性疾病。此病属蒙医学“粘痈”范畴，起因乃恶血、黄水和粘毒。古典著作《兰塔布》载有：“恶血、黄水隐伏之首，由赫依所包卷聚集，与人体共生之虫结合，钻人肌肉、骨骼、脉道而称痈疽。”临床上主要以淋巴结肿大和多发生在颈部、耳前耳后、腋下、大腿根部等皮肉薄之处，逐渐蔓延变大，起初皮肤颜色没有变化，后期化脓、溃破成瘘为特征。在千例患者中，均用西医方法或其它方法治疗效果不显或无效而来就诊。应用传统蒙医系列验方十三味阿拉格斑布脉泻丸、十一味萨拉玛日药条、八味巴拉都格生肌散为主方治疗淋巴结结核，归类对症、辨证施治、配合酸马奶、鲜羊肉汤、四骨汤等食疗，明显地缩短了疗程和提高了疗效，同时发扬了传统蒙医特色，优于或有别于其它治疗方法。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (629, 1, 6, '蒙西医结合治疗慢性溃疡性结肠炎疗效观察', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;     蒙西医结合治疗慢性溃疡性结肠炎疗效观察 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古民族大学附属医院 \r\n      催海山<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;笔者自1994年～1997年采用蒙西医结合的方法治疗慢性非特异性溃疡性结肠炎25例，取得较好疗效，现报道如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组25例中男性14例，女性11例；年龄最小17岁，最大58岁，平均年龄36岁；病程0.5～ 1年者16例，1年以上者9例；其中仅累及直肠者13例，累及降结肠及乙状结肠者10例，累及全结肠者2例；偏热型19例，偏寒型6例；疗程一般需3～9周。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 蒙医辨证分型<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;偏热型：脐周刺痛或绞痛，腹泻，脓血便，粘液血便或腹泻与便秘交替，口渴，发热，颜面发红，喜凉，脉象细而紧，尿黄，舌苔黄厚。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;偏寒型：腹部胀痛钝痛，肠鸣，腹泻，大便稀水样便或粘液便，内含未消化之物，矢气不通，喜暖喜按，面色苍白，体质虚弱，脉象缓而弱，尿清澈，舌苔薄白。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 偏热型：清包如热，凉肠，止泻为治疗原则。内服茵达拉一15味散，巴特尔一7味丸，敖勒盖一13味散，茵达拉一4味汤及查干汤等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 偏寒型：助胃火，抑赫依，益大肠为治疗原则。内服哈日嘎布日一10味散，达格布满那格一15味散，敖勒盖一13味散及伊赫汤一25味散等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.3 灌肠治疗：以上两型均应用柳氮磺胺吡啶片及地塞米松片7mg研末后与蒙药嘎木朱尔拌匀溶于looml温开水中每晚睡前保留灌肠。3周为1疗程，治疗前后均做结肠镜检查。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;近期治愈19例(76％)，有效5例(50％)，无效1例(4％)，总有效率96％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;溃疡性结肠炎是一种原因不明的直肠及结肠的慢性炎症，属蒙医“敖勒盖包如”病范畴，系包如热邪久积于大肠而引起的一种聚合性慢性病。治疗上以调理三根，清包如热，止泻为原则，并针对病情进行辨证施治。本组所用内服药哈日嘎布日一10味散及达格布满那格一15味散具有益胃火，助消化，止泻等作用；茵达拉一15味散，巴特尔一7味丸具有杀粘，清腑热，止泻之功效；查干汤及伊赫汤一 \r\n      25味散具有调理体素，解毒，收敛扩散之包如热等功效。因大肠据于赫依之位置，也是病变赫依窜行之道，因此治疗大肠疾病时尤其注意赫依的治疗，故述两型均服用具有抑赫依，开郁顺气，化滞消胀，涩肠止泻之功效的敖勒盖一13味散。灌肠所用的柳氮磺胺吡啶及地塞米松具有消炎，调节免疫反应的作用。蒙药嘎木朱尔具有消肿止痛，收敛止血，祛腐生肌之功效。且局部用药使药物直接作用于病变部位，收效迅速，缩短疗程，提高疗效，并避免了口服柳氮磺胺吡啶及地塞米松所引起的诸多毒副反应。局部用药与内服诸药协同作用达到了治疗的目的。本疗法具有疗效显著，疗程短，复发率低等特点。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-07');
INSERT INTO myy_text VALUES (630, 1, 6, '蒙医治疗急慢性乳腺病50例', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医治疗急慢性乳腺病50例<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                &nbsp;&nbsp;&nbsp;内蒙古中蒙医院 \r\n      乌力吉特古斯 沙仁<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;笔者自1988年，在蒙医门诊和蒙医病房用传统蒙药治疗了50例急慢性乳腺病，收到了良好的治疗效果，现总结如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;50例患者之中，年龄最大的43岁，最小的24岁；哺乳期妇女38例，非哺乳期妇女士12例；病程最长者12年，最短的2个月；确诊为哺乳期急性乳腺炎27例，哺乳期慢性乳腺炎 \r\n      ll例，乳腺腺病7例，乳腺纤维腺瘤5例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;似据以上3种疾病的病程，临床症状及疼痛程度和肿块大小选用了不同的蒙成药。哺乳期急性乳腺炎，首选“吉胆木道尔吉”的同时配合“古日本乌兰汤”和“古很汤”疗程为 \r\n      21～49天不等。哺乳期慢性乳腺炎在选用以上药物的基础上配合巴特尔一7位丸。乳腺腺病和乳腺纤维腺瘤则选用拉亚那木青粒和达敖德弄日冲的同时外用哈布德尔一―9位散．疗程为35-49天。外用法为：取哈布德尔一9位散5g，用陈醋调成糊状，敷于患处，每日1次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;疗效标准 痊愈：为哺乳期乳腺炎：局部红肿、热、痛全部消失，乳腺腺病和乳腺纤维腺瘤：疼痛消失，扪及囊性包块消失者；有效：肿胀及肿块消失，患部轻微的疼痛扪及囊性包块没有完全消失者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;结果 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;38例急慢性哺乳期乳腺炎患者，治愈35例，有效3例，有效率为100％。乳腺腺病和乳腺纤维腺瘤，治愈8例，有效4例，有效率为100％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;依据“四部医典”所述，妇女哺乳期乳腺炎、乳腺腺病及乳腺纤维腺瘤都属于蒙医妇科学“妇血症”范畴。临床统称为“古很哈布塔日”(意：乳房血肿)其临床症状与西医学基本相似。中国医学百科全书蒙医学里谈“妇血症”的病因时强调了由于各种因素使体内三根失去相对平衡，气血、希拉偏胜，随血行扩散于脏腑而引起。在治法上强调了清热、解毒、通络、消肿。在以上理论基础的指导下我们在临床上首选了具有清热、解毒通络，消肿之功效的蒙成药。如吉昭木道尔吉具有清热、通络、开窍、杀菌、止痛之功用。拉亚一那木吉拉具有调经养血、理气、止痛之功效，主要用于妇女血热上行，月经不调，心烦意乩，气喘，浮肿、闭经等。古日本乌兰汤与古很汤都具有清热、祛瘀、养血祛肿之功效；为了加快乳房血肿的愈合还选用了哈布德乐一9位散外敷：具有消肿止痛之良效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;通过对50例患者的临床观察，笔者认为蒙医药治疗哺乳期乳腺炎、乳腺腺病、乳腺纤维腺瘤方法简单，疗效好，痛苦少，具有很大的推广价值。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-07');
INSERT INTO myy_text VALUES (631, 1, 6, '蒙药胃痛散治疗慢性胃炎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药胃痛散治疗慢性胃炎<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院 旺其格 白长明 斯日古楞 敖敦 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自1994年7月至1997年7月对160例慢性胃炎应用蒙药胃痛散、蒙药传统组方逆行对照研究．前者疗效明显高于后者。现报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 诊断标准和病例选择：参照《内科疾病诊断标准》选择慢性浅表性和萎缩性胃炎患者160例，随机分二组。治疗组80例，其中男 \r\n      36例，女44例；年龄20～70岁，平均41岁；病程6月～20年，平均5.9年；慢性浅表性胃炎23例，慢性浅表萎缩性胃炎36例，慢性萎缩性胃炎21例。对照组80例中，男40例，女40例；年龄21―70岁。平均45.5岁；病程 \r\n      6月～20年，平均6年；慢性浅表性胃炎28例，慢性浅表萎缩性胃炎23例，慢性萎缩性胃炎29例。两组各型之间无明显差异(p> 0.5)，具有可对照性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 治疗方法：治疗组均服用蒙药胃痛散，基本方药是通拉嘎5味和如达6味二方组合散剂，每d3次，每次1.5g，三餐前30min温开水送服，3周为1疗程。对照组按照蒙医学辨证施治原则，以浩道敦10味为主，胃痛者加如达6味；反酸者加哈日嘎布日l0味；胃腹胀满、便秘者加阿木日6味；恶心、呕吐者加壮西6味；腹胀、肠鸣者加敖勒盖13味。每次3g，每d3次，温开水送服，3周为1疗程。治疗期间忌剌激性、生冷或寒性食物，三餐定时，易消化为原刚。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 疗效观察：两组病例服药期间每1―2周随访汜录1次症状、体征和疗效变化，根据疗效情况，1～3疗程后检查判定疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 结 果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗组与对照组疗效对比：全部病例根据疗效情况服药1―3个疗程。治疗组80例中，显效63例(78.75％)；有效14例(17.5％)；无效3例(3.75％)；总有效率96.25％；治疗组服药期间未见明显副反应。对照组80例中，显效35例(43.75％)；有效31例 \r\n      (38.75％)；无效14例(17.5％)；总有效率 82.5％。治疗组疗效明显高于对照组，两组对比差异非常显著(p<0.01)，说明蒙药胃痛散疗效明显高于传统组方疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;慢性胃炎起因为巴达干、赫依互结寒盛，挫伤胃火，致饮食不化所致。食无定时，过食生冷而不易消化之物，长期心劳过度或过饱过饥等均为诱发本病之因素。性寒，胃功能衰弱为本病的主要特征。蒙医治疗胃衰病，以祛寒助火、健胃为原则，一病多方，根据不同症状选用若干方药。为了总结研制出治疗胃衰病的专用成方，以蒙医学理论为指导，辨证施治原则，结合临床经验，把通拉嘎5味和如达6味二方优化组合，按传统方法配制成蒙药“胃痛散”，结果可看出，治疗组与对照组疗效有非常显著差异(p<0.01)，治疗组总有效率96.25％，对照组总有效率82.5％。研究结果表明，该成方集暖胃，化积消食，清糟归精，开郁顺气，祛寒性“协日”，止吐解痉等多种方药作用于一方，而且疗效明显高于传统组方。因此，蒙药胃痛散可成为治疗慢性浅表性和萎缩性胃炎的首选蒙药制剂。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (632, 1, 6, '蒙医药治疗慢性乙肝临床研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;     蒙医药治疗慢性乙肝临床研究<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古锡林郭勒盟蒙医研究所 \r\n      王宁波 包龙 白平安<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;河北省张家口市传染病医院 李金科 杨喜枝 王淑芳 1995年3月至1996年12月我所与张家口市传染病医院协作，采用西医辨病蒙医辨证论治的方法，应用蒙药乙肝系列方剂治疗慢性乙肝病人39例取得了较好的近期疗效，部分病例经过6～12月的随访观察，远期疗效亦较稳定，现总结报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 资料与方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 病例选择标准：39例患者均符合1995年北京第五次全国传染病寄生虫学术会议修订的慢性乙肝标准。既往有乙型肝炎或hb- \r\n      sag携带史或急性肝炎病程超过半年，而目前仍有症状、体征；alt反复或持续升高或 sb反复增高，或白蛋白明显减低，或a／g比值异常，血清两对半大三阳或小三阳，而抗 \r\n      havigm与抗hcv均阴性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 一般资料：治疗组39例，男29例，女 10例；年龄17～55岁；肝炎病史6月～10年；西医临床分型，轻度11例，中度25例，重度3例；蒙医辨证分型，肝胆血希拉热证22例。肝热并巴达干证7例，肝热并赫依证4例，肝包如症(早期)6例。对照组20例，男12例，女8例；年龄18―60岁；西医临床分型轻度11例，中度8例，重度1例；肝炎病史6月～7月。两组基本条件无显著性差异，具有可比性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 蒙医辨证分型及标准：①肝胆血希拉热证(简称i证型)：恶心厌油，乏力腹胀、肝胆区胀痛、口苦唇干，身目黄或无黄，小便黄赤，大便臭秽不爽，舌苔黄厚，脉弦、滑、数。②肝热并巴达干证(11证型)：肝胆区胀痛，食欲不振，口淡乏味、脘痞腹胀，情绪急躁，乏力，面色萎黄，舌苔薄白、脉沉弦。⑧肝包如证(3证型)：肝区前后夹痛，乏力，食欲不佳，腹胀，面色晦暗，肝掌，蜘蛛痣，舌质暗紫或有瘀斑，肝脾肿大、脉沉、细、弦涩。④肝热并赫依证(4证型)：头晕耳鸣，口燥咽干，失眠多梦，手掌脚心烦热，腰膝酸软，女子经少或经闭，舌红干燥或裂纹，脉、细、数无力。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 治疗方法：治疗组用蒙药乙肝1号一4号系列方剂(本研究所蒙药制剂室制备)辨证论治。①i证型重用l号方，每d2次，每次 \r\n      15―30g，煎服；2证型重用2号方，每d2次，每次5g(约120丸)开水送服；Ⅲ证型重用3号方，每d2次，每次5g，开水送服；Ⅳ证型重用4号方，每d2次，每次15g煎服。②乙肝1号为主方。由并头黄芩、肋柱花、紫花蒿乌头为君药，共13味药组成，煮散剂，主要功能为清热解毒，兼有活血散瘀作用。因此除i证型重用1号外，其余3个证型可每d1次，每次 \r\n      5g。③辨证联用，兼见肝脾不调，脘痞腹胀明显者联用2号；兼见恶血瘀积，肝区痛明显联用3号；兼见头晕耳鸣，肝肾赫依热者联用4号。对照组用联苯双酯，每次7.5mg，每d3次，(锦州市制药五厂960301)；肌注肝炎灵注射液，每d4ml(江苏常熟制药厂920303)。两组在同等条件下均加用维生素类药或对症治疗，均不用免疫制剂与有抑制病毒作用药物。两组均3月1个疗程，随访6月以上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;讨论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我国是hbv感染的高发区，hbsag携带率达10%，约1.2亿人，其中慢性乙肝患者约3千万，部分病人发展为肝硬化和肝癌。危害性极大，迄今为止尚未无满意的疗法，因此慢性乙肝的治疗是一重点研究课题。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药学是祖国医学宝库的重要组成部分，有完整的理论体系和独特的医疗方法，蒙药乙肝系列方剂是在总结本所名老蒙医学术思想与临床经验的同时吸收锡盟地区民间草药单方验方的基础上拟定的系列制剂，我们应用该系列制剂对39例慢性乙肝病人进行了临床观察，获得了显著的疗效。蒙药治疗组的临床症状体征的改善显著优于对照组。治疗组alt改善的总有效率为96.7％，而对照组为68.8％，两组相比有显著的差异(p< \r\n      0.05)，治疗组hbeag转阴率54.2％，抗一 hbe的转阳率40.7％分别优于对照组的18.2%,和6.3%。治疗组总有效率为94.9％，显著优于对照组的70.0%(p<0.05)。对22名患者进行半年以上的随访观察，结果与疗程结束时的疗效基本相同，提示有较好的远期疗效。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学并无慢性乙肝病名，但相似于慢性肝炎的症状与病因病机的描述早有记载。我们认为宜归属于蒙医“热性肝病”“肝包如”等范畴。粘邪疫毒残留于肝脏，血、希拉与巴达干、赫依聚合，肝内恶血瘀积，体内“三根”失调，机体免疫功能紊乱是形成慢性肝炎的病理机制。蒙医认为“肝脏是希拉之总位，火元素精华所藏之处，也是病变希拉之窜行之道”肝病多属血、希拉的热性疾病，因此慢性肝炎无论辨为何证型，始终不能忽视祛粘清热这一基本治则，我们遵循这一理论，将乙肝i号作为主方，灵活用于各证型，达到抗乙肝病毒、消除免疫复合物，保护肝细胞损伤的目的。2号方以温胃化湿、调理赫血相搏为主要功能，3号方以活血散瘀为主要功能，4号方则以消食健脾、补益肝肾为主要功能。临床观察结果提示蒙药乙肝系列方剂的抗乙肝病毒与保肝降酶作用优于联苯双酯十肝炎灵组，可能与用祛粘清热、活血散瘀、疏月干解郁、补益肝肾等多法联用，起到抗乙肝病毒，改善肝脏微循环，调节肝细胞免疫反应，促进肝细胞再生等作用有密切关系，也证明蒙医整体观与辨证论治理论有很高的科学价值与优越性。本系列方剂的剂型改进与药理作用机理有待进一步深入研究。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (633, 1, 6, '金沙丸治疗慢性肾炎尿毒症的临床观察', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;金沙丸治疗慢性肾炎尿毒症的临床观察<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医学院附属医院 \r\n      李红 白纲<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;慢性肾炎，尿毒症是临床常见的当今世界上疑难症之一。在多年的医疗实践中，总结出攻克慢性肾炎、尿毒症的有效蒙药验方金沙丸，我们自1977年-1997年用金沙丸治疗慢性肾炎、尿毒症420例，取得了较满意的疗效，总有效率达72.8％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      本组420例中，慢性肾炎382例；其中男性230例，女性152例；年龄15～50岁，平均年龄为32.5岁；病程1～25年，6―10年201例，占52.3％；疗程 \r\n      3―6个月，3―5个月为212例，占55.4％。尿毒症 38例，其中男性26例，女性12例；年龄31―62岁，平均年龄46.5；病程4～25年，疗程4～6个月。420例患者均以慢性肾炎和尿毒症的统一诊断标准为依椐。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;金沙丸组方：海金沙、天花粉、白蔻、海马、肉桂、方海、砂仁、益智仁、乌药、榧子、石莲子、建莲子、手掌参、狗肾、蒺藜、川军、香墨、姜黄、黄柏、白脑砂、鹿角霜、鹿角胶、阿胶、蜗牛炭、水牛角粉、车前子、木腰子等30味纯天然药物组成。配制方法，把上药共研细面打黄豆粒大的水泛丸银珠为衣，每次2.5粒(约 \r\n      2.5g)日服3次，小儿酌情减量。30天为1个疗程。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 疗效标准 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治愈：主要症状消失，化验、b超等均恢复正常者。好转：症状比前减轻或明显改善，化验、b超大部分转正常者。无效：症状及化验、b超未改善者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;慢性肾炎382例，治愈184例，占48.1%，好转104例，占27.1%,无效94例，占24.8%；尿毒症38例，治愈12例，占31.6%,好转6例，占15.8%,无效20例，占52.6%。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;慢性肾炎、尿毒症均属蒙医学中的“肾性水肿”范畴，蒙医认为本病内因是体内的“三元、赫依、希日、巴达干机能失调而黄水增多，外因是饮食伤脾肾，劳伤过度，感受寒冷潮湿侵袭，导致脾肾火衰，未能完全消化精微、黄水糟粕滞留于体内损伤肾气营血而成病。蒙药经验方金沙丸是根据我们多年的蒙医治疗肾病的“祛风化湿，先养精血”，“除湿利水、先健脾胃、祛邪与扶正”为一体的标本兼治的治疗原则而研制成的。金沙丸具有滋阴补阳，补肾健脾，养血祛瘀，除湿固本，解毒利水，调正阴阳，提高机体免疫力的功效，用药后在较短的时间内能够消除肾小球和肾间质的炎症增生，增加肾脏有效血流量，抑制机体异常免疫，恢复和提高肾脏的功能，消除尿蛋白、水肿、血尿、中毒、高血压等，而达到根除肾病不易复发的治疗目的。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (634, 1, 6, '蒙医药浴治疗牛皮癣', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr> \r\n    <td align="left" valign="top" class="text" height="10"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药浴治疗牛皮癣<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;包头市蒙中医院 \r\n      达格巴雅尔 傲特根其木格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp; <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我科自1990年，开始开展蒙药浴以来，收治典型病人20例，经过蒙药浴及蒙医治疗，疗效显著，现报到如下：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;20例患者中，女性15例，男性5例；年龄8―38岁；其中学生3名，干部3名，工人 14名，从事企业者居多；女性多于男性，冬春季多见。治愈19例，占95％；疗效不明显1例，占5％。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1&nbsp;蒙药浴组方：五甘露(称塔本阿日山，由杠柳、小白蒿、麻黄、刺柏、杜鹃叶组成)，另加楝子、栀子、苘麻子、草决明、文冠木、蒺藜、葶苈子、大黄、姜黄、生硫黄、白云香等，称“乌呼日依利特药浴散”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2&nbsp;制剂方法：五甘露、楝子、栀子、苘麻子、草决明、蒺藜、葶苈子、文冠木等进行煎煮提取药汁。首次加水煮2～3小时，过滤，取煎液，再加水煮重复3―4次，合并煎液浓缩成膏；另将姜黄、白云香、大黄、生硫黄等药粉碎，加入膏中搅拌，烘干后再粉碎，分装。规格为250g或500g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3&nbsp;用法用量：取上述药散250g，加水适量加热煮开，备用。浴盆内放l／2水，用气加温至37～42~c，放入药液搅匀，病人以全身入浴，浸泡治疗。根据病人的情况，可调整水温、药量和浸泡时间。一般每日浴1次，每次 \r\n      30～60分钟，28天为1疗程。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.4&nbsp;护理：蒙药浴治疗是一种全身性治疗。因出汗多，体力消耗大，故要加强护理，严格观察病人，防止意外。浴后应卧床休息，防止受风受凉，调整饮食，加强营养，补充水分。禁用辣椒、酒等刺激性饮食，以免劳累等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3 药浴分析及体会<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;方中苘麻子、草决明、白云香、文冠木等有治疗、干燥“希日乌苏”，解毒功效；楝子、栀子有调节代谢作用；麻黄、杠柳有发汗、祛毒作用；大黄、姜黄、生硫黄起杀“粘”虫，治疮、解毒作用，刺柏、蒺藜有治疮，提高免疫作用。此方有干燥“希日乌苏”，杀“粘”虫，止痒，调节代谢及内分泌，提高免疫功能之功效。药浴可经过皮肤吸收药物，起到止痒，治疗病邪，解除疾病根源及表面症状的作用。<br>\r\n        牛皮癣蒙医称为“乌呼日依利特”，认为主要是饮食不协调，大汗后受风凉，心情不畅，精神受刺激等因素引起体内机能发生变化，新陈代谢发生障碍，皮肤“赫依”和血循环受限，“希日乌苏”增多，合并“粘”虫所引起，北方多见，冬春季易发的慢性皮肤病。治疗原则为调节皮肤机能和新陈代谢，改善循环，燥“希日乌苏”，杀“粘’’虫。蒙药浴符合上述原理，从临床观察，治疗效果显著，也深受患者的欢迎。<br>\r\n      牛皮癣又称“银屑病”，是一种常见易复发的慢性、炎症性皮肤病。根据蒙医辨证施治之理论，采用纯蒙药制剂进行治疗，取得了较满意的疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗前嘱患者忌辛辣、海鲜等刺激性饮食，不宜饮酒。勿劳累、受风、着凉，避免精神、神经方面及物理、化学性物质和药物的刺激，防止滥用药物。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;发病初期以促进精糟之分解，调理“三根”，促进气血运行，燥黄水为主要治则。进行期以凉血、清热及活血为主。静止期以活血散风、养血、润肤为主。药物治疗每天早晨可配以温胃养胃，促进精、糟之分离的药物，如通拉嘎一5味等；中午配以清热、解毒、活血、凉血的药物，如别嘎日一10味等；每晚睡前必服乌力楚一18味，并配以森登一4味汤或别力布一3味汤等做引子送服，此外。还可以配合外用药物，如嘎木朱尔，每日2―3次涂于患处。静止期可配合五味甘露药浴治疗。门诊患者5―7天复诊1次，轻型患者一般7～9天见效，重型患者10～15天见效。疗程为3～5周。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医认为黄水之邪是该病的主要原因之一，而黄水又是机体的组成部分，黄水流注全身各处，尤以皮肤、关节为多。饮食入胃，经消化吸收，其精华生化成血．其糟粕归于胆腑，成为胆汁，胆汁之精华生化为黄水。当人体由于饮食不节、胃火受损、汗后受凉、情志受刺激、愁苦抑郁、外伤或其它疾病的影响等导致体内三根失调，功能紊乱。巴达干，赫依增盛而生寒，胃阳衰降，不能温煦七素．以助其正常生化功能，精糟之分离障碍，故血液之生成受遏，而黄水趋于偏盛，并在普行气的作用下，扩散于机体表里，与血粘滞交搏，侵袭肌肤，再加之“粘虫”的感染而发病。<br>\r\n      在治疗之中抓住胃火衰降．精糟之分离障碍，黄水偏盛，气血运行不良，粘虫感染等主要病因，采用辨证施治的理论，主选乌力楚一18味方剂，配以通拉嘎一5味、古古勒一11昧、阿嘎日一15味及外用药嘎木朱尔等方剂。乌力楚――18味之中水银、紫榆膏、硫黄、苘麻子、草决明、石菖蒲等具有燥黄水之功效，草乌有消“粘虫”之功效，牛黄能清热、解毒，黑、白云香能消肿、止痒、止痛，广木香可理气、活血，诃子、公丁香、石膏、红花、草果、肉豆蔻、白豆蔻可调理“三根”。<br>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td align="left" valign="top" class="text" height="10">&nbsp; </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (635, 1, 6, '蒙医药治疗再生障碍性贫血405例临床疗效研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药治疗再生障碍性贫血405例临床疗效研究<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽宁省阜新市蒙医蒙药开发中心 \r\n      邢鹤林<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp; 再生障碍性贫血(以下简称再障)是骨髓干细胞及造血微环境损伤，而致骨髓机能衰竭的疾病。近几年来，国内外诊治本病有较大进展，但仍有三分之一的病例，使用已知的疗法不能获得缓解和明显进步。1985年辽宁省阜新蒙医药研究所(简称蒙研所)名誉所长邢布利德与其子邢鹤林同沈阳军区总医院陈泊主任合作，开展“蒙医药治疗再障’’的临床研究，取得了预试的成绩。次年在该所副所长齐荣先倡议，海龙宝书记支持下，达成了沈阳军区总医院，蒙研所及阜新县医院协作科研的协议，成立了以邢鹤林、陈泊、齐荣先为首的课题组，由陈泊提出了盲法的前瞻性科研设计。1986年12月经专家课题论证后，被列为阜新市重点科研课题，次年被列为辽宁省卫生厅及省科委科研课题。1987年9月写出阶段性论文“蒙医药治疗再障的初步疗效分析”，相继被选为全国第三届血液学术会议及第一届全国少数民族医院工作会议论文。至 \r\n      1990年6月止，按科研设计要求，已进行本课题3年半，现将科研观察病例的405例加以分析。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 资料来源与方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 病例来源：凡符合下例条件者被选为科研观察病例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1.1 诊断确切：由沈阳军区总医院或市级以上医院经血液学检查确诊，经科研小组3人以上，按全国诊断标准确定诊断。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1.2 资料完整：有骨髓检查及有关血液学检查。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1.3 能坚持长期治疗，可承受医疗经费。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1.4 自愿接受蒙医药治疗，服从医嘱，并签定自愿合同者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本文405例，其中8例为住院病人，397例为门诊观察病例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 诊断标准：按1987年6月宝鸡全国再障会议标准。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2.1 全血细胞减少，网织红细胞绝对值减少。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2.2 一般无脾肿大。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2.3 骨髓检查至少一部位增生减低或重度减低(如增生活跃须有巨核细胞明显减少，骨髓小粒成份中应见非造血细胞增多)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2.4 能除外其它引起全血细胞减少的病变。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2.5 一般抗贫血药物治疗无效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 疗效标准：按1987年宝鸡再障会议修改标准制定。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.1 基本治愈：贫血、出血消失，血红蛋白男性120g／l，女性100g／l，白细胞4x109／l，血小板80x \r\n      109／l，随访1年以上无复发。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.2 缓解：贫血、出血消失，血红蛋白男性120g／l，女性100g／l，白细胞3.5x109／1，血小板有一定程席的恢复。随访3个月病情稳定或继续进步者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.3 明显进步：贫血、出血明显好转，不输血，血红蛋白较治疗前月的常见值增长30g／l，维持3个月不降。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;判定以上三项疗效标准均应3个月内不输血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.4 无效：经充分治疗后症状血象不能达到明显进步者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组病例采用课题组集体评议方法，按以上标准至少4人参加评定，对骨髓增生型再障进行专门评定。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 治疗方法：全部病例均口服蒙药，同时间断输血者37例。 蒙药投入根据蒙医理论进行辨证分型。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4.1 脾型：面色苍白、消瘦、消化不良，常有出血现象，脾脉大多虚弱，舌苔白厚，投蒙药“再障1号”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4.2 肝型：面色发黄，尿黄，恶心欲吐，头晕，头重脚轻感，多有发热，肝脉虚，舌苔黄，投蒙药“再障二号”。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其中“再障1号”主含草果仁、石膏、地丁、木鳖子、小青皮、白蔻仁、茜草、广木香、麦冬、石莲子、栀子、降香、广枣、西红花、麝香等。共为细面，炼蜜为丸，每丸重5克，每次1丸，日服3次。“再障2号”含红花、石膏、熟季花、木鳖子、沉香、紫草、茜草、猫儿眼、五灵脂、栀子、建莲子，共为细面，炼蜜为丸，剂量，服法同上。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 检验方法：在治疗前做血象，骨髓象及其它有关鉴别诊断所需项目，治疗后每1个月做1～4次血象检查，并对检验结果进行标准化，如采用氰化高铁法检测血红蛋白值(库尔特血球计数仪)者。则标准化值为hb值―(2+3)/2。疗效判定以治疗前一次与治疗后最近一次血象为判定值，进行统计学分析。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 一般情况<br>\r\n      405例中，男性258例，占63.7％；女性147例，占36.3％；男女之比为1.75：1。年龄最小3岁，最大76岁。病程最短1个月，最长35年，中位值1～2年。既往经西药治疗162例，骨髓移植失败2例，切脾后复发 \r\n      1例，经中西医相继或同时治疗者239例。首治病例1例，非首治病例404例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 疗效分析<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;按1987年全国再障宝鸡会议标准，基本治愈65例，占16.0％；缓解62例，占15％；明显进步105例，占 \r\n      26.0％；无效173例，占42.7％”。基本治愈及缓解率31.3％。为便于与全国各单位进行参照，特按临床血液学所订标准统计如下：基本治愈65例，占16.0％；缓解62例，占15.3％；明显进步105例，占26.0％；稳定141例，占34.8％；无效32例，占7.9％，总有效率92.1％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 疗效与因素分析<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.1 疗效与年龄<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;幼少组治愈缓解率为35.21％，青年组为26.2％，壮年组为34.58％，老年组为18.18％。以幼少年及青壮年疗效较好，年过57岁以上的慢性再障疗效较差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.2 疗效与性别：405例中，男性258例，基本治愈32例，缓解31例，明显进步80例，稳定89例，无效 \r\n      26例。女性147例，其中基本治愈33例，缓解31例，明显进步25例，稳定52例，无效6例。男性基本治愈缓解率为24.4％，女性为43.5％。本组病例中3～17岁女孩43例，基本治愈缓解22例，治愈缓解率高达 \r\n      51.2％，但女性缓解率高于男性，而与国内资料男性疗效优于女性相异。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3 疗效与病情轻重<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.1 按国际再障研究组(iaasg)提出的重症再障标准，将405例分为轻、重两型。重型153例，其中基本治愈5例，缓解6例，明显进步29例，稳定102例，无效11例。轻型252例，其中治愈60例，缓解56例，明显进步76例，稳定39例，无效21例。重型的基本治愈缓解率为7.2％，轻型为46％。经统计学分析，轻、重不同类型的疗效间有非常显著的差异(p<0.001)，说明轻型病例疗效明显优于重型患者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.2 贫血程度：按血红蛋白40g／l以下为重度贫血，40～80g／l为中度贫血，80g／l以上为轻度贫血。<br>\r\n      贫血轻者治愈缓解率为65％，贫血中度为28.8％，重度贫血为12.4％。贫血程度与疗效间有非常显著的统计学差异(p<0.001)；说明贫血轻者疗效最佳，中度贫血次之，重度贫血疗效较差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.3 疗效与骨髓增生程度：按宝鸡再障会议诊断标准，将再障分为骨髓增生减低与增生活跃两类。 \r\n      骨髓增生活跃的117例中，基本治愈及缓解者共37例，治愈缓解率为31.62％，骨髓增生减低的288例中，基本治愈和缓解病例为90例，治愈缓解率为31.25％，两者未见统计学差异(p<0.005)。而无效者的比率，以骨髓增生型再障为高，为11.1％，增生减低者为6.6％，反以增生型再障无效者较骨髓减低型为多。此亦与国内报导有异。说明蒙药对慢性再障的疗效与骨髓增生程度有同样的作用，对骨髓增生减低者其疗效并非全差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.4 疗效与病: 病程1年以内的191例，基本治愈及缓解共74例，治愈缓解率为38.74％，病程1至5年的117例中，基本治愈及缓解32例，治愈缓解率为27.35％，病程5年以上的97例，基本治愈和缓解21例，治愈缓解率为 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;21.65％。病程长短与疗效有非常明显的统计学差异(p<0.001)，说明病程在1～5年者，治愈缓解率为 \r\n      34.42％，明显优于5年以上的病例。尤其是病程短于1年的病例效果较好。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.5 服药时间与疗效：按服药时间分析，服蒙药半年以内174例，其中基本治愈和缓解率为 19.54％，1年以内156例，基本治愈及缓解率为37.18％，1年以上者75例，基本治愈缓解率是46.67％。服药起效时间，最短20天，一般多在3个月以后，其好转顺序，先有症状好转，输血间隔延长，乃至不需输血，继而血红蛋白增加，部份病人血小板有所增多，但部份病人血小板增加缓慢，而仅达到缓解的疗效。少数病例在治愈后复查骨髓象，见骨髓象接近或恢复正常。亦有少数病例临床虽已达到基本治愈标准，而骨髓象仍未恢复正常。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3.6 住院与门诊治疗的疗效：住院病例8例，其疗效为缓解2例，明显进步3例，稳定2例，无效1例。门诊病例397例，其疗效为基本治愈65例，缓解60例，明显进步102例，稳定139例，无效31例。<br>\r\n      经ridit检验，两组无显著统计学差异(p>0.05)。可能因为服用蒙药与住院与否关系不大。本组病例住院病例少，也与其治疗方法有关。1日3次蒙药丸，许多病人宁愿在家服药，而不愿住院，尤其是超过3个月以上的住院，我们给予补助，也只有2例住6个月，1例稳定，另1例进步。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 药物副作用：405例中腹泻2例，出血点增多2例，皮肤发痒3例，可自行缓解。死亡2例，均死于脑出血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药已有二千多年的历史，我国蒙医的发祥地辽宁阜新蒙古族自治县，世代相传，并以文娄记载其丰富的临床治疗经验，成为中华民族优秀遗产。我们在继承和发展蒙医的工作中，开展了“蒙医药对再障的临床疗效”的研究历时3年余。现就诊断，临床疗效和蒙医对再障的认识及治疗加以讨论。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.1 诊断问题：本课题按科研设计，诊断均应由沈阳军区总医院血液科负责，治疗由蒙研所承担，疗效由沈阳军区总医院判定，进行盲法科研。但由于病人不通过沈阳而直接来蒙研究，而蒙研所由于条件所限，不能承受大量病人的血液及骨髓检查，而且许多病人苦于往返，迫不得已采用了全国市级以上医院的骨髓报告单为骨髓诊断的依据，为避免各城市、各医院对诊断标准的掌握不一，所以，又采取了课题组集体确诊的方法。无疑这些方法对诊断带来一定的偏性。<br>\r\n      本文405例中，沈阳军区总医院确诊者50例，沈阳中国医科大学确诊者40例，其余分别为全国各市级以上医院确诊的病例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.2 蒙医治疗再障的疗效：通过405例疗效分析，基本治愈缓解率为31.3％，与国内各家报导的中医、西医或中西医结合治疗的总平均率相似(1)证实临床血液学对疗效总的估计“三分之一可缓解”的论点是正确的。<br>\r\n      通过临床疗效因素分析，发现病情较轻，血红蛋白在60g几以上，病程较短，服药时间超过半年，年纪较小者，蒙药效果较好。而重型再障或年龄超过57岁，则疗效较差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3 蒙药治疗再障的适应证：凡病程不太长，血红蛋白在40g／l以上，出血倾向不重的慢性再障，即可服用蒙药。而急性再障或重型再障，或服蒙药半年无效的慢性再障，则不适于此治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.4 蒙医对再障的认识：血液是蒙医学理论中的七种体力之一，血液方面的疾病为“血症”或“血分病”是六种基本类型病之一。蒙医学无再障病名，但有血病一词。蒙医的理论是血生于肝，即驻胃脘的“平位风”分离饮食的精华与糟粕，将其精华输入肝脏，经驻肝脏的“容光胆”将此精华生化为血液。由于饮食、起居不调，情志不畅等，引起人体内胆火和体力失去平衡，则可引起“不消症”，不消症的初期属“浊物不消”，由于浊物不消，则胃脘的“平位风”不能分离饮食之精华，肝脏的“容光胆”不能将此精华生化为血液，造成“精华不消”，成聚、漏、散、突兀四种类型的痼疾，即“即久不消病因变痼疾”。其中的突兀型则导致脾病及合病。合病是风、胆、涎三毒，并以血为主兼有黄水综合为患的痼疾，其症状表现错综多变。《四部医典》论及合病有云“合病人肝则同血症”。合病按其病因病机可分为三种，其一称为“巴达干鲍日”，译为合病涎分淤紫症，在《四部医典》中译为聚合症紫色培根，简称“淤紫症”。我们认为再障类似于淤紫症。淤紫可分为寒热两大类，两者均以血分病变为主。其发病部位有自点和他点，自点有四，其中“其点在肝症象如血病”。他点又分内外，可在皮肉、脉道、四肢关节、头部及肺心脾肾造成病变。淤紫按其证候又分扩散、增展、突兀、卷聚四大类别。扩散还可分为内散与外散，增展又分穿与未穿(穿即出血)；突兀又分优与未优：卷聚又有新卷与旧卷之分等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们按以上理论，选药配伍，组成方剂，将其症候分为肝、脾两型。淤紫症是合病，“如土难养久病易沉涎”。所以，本病在蒙医学中亦属疑难病症。医治此病须从病因、缘起、病点、分期、类别、症象、治疗方法和预后八个方面去分析而施治。对合病入脾的患者采用主方“再障1号”；对合病入肝的患者，主方为“再障 \r\n      2号”，无论合病入肝或入脾，若有以下症状较突出或由病情转化而出现，在服“再障1、2号”同时，加服散剂如同药引。腹胀：加服广木香、栀子、石榴、熟季花、白蔻仁、荜茇(即蒙药“如达柔巴”)；食欲减退：加服甘草、香菜籽、梨干、大米、茴香籽、蔓荆子(即蒙药“兴阿柔巴”)；发烧：加用沉香、肉豆蔻、广枣、红花、石膏、白檀香、降香、桔梗、青木香、苦参、山奈、勾膝、柯子、栀子(即蒙药“阿嘎日召阿”)，鼻或齿龈出血，加服柯子、川楝子、栀子、紫草、茸草、枇杷叶(即蒙药“别立布苏木汤”和“玛日布苏木汤”)；失眠：加服沉香、肉豆蔻、广枣、广木香、 \r\n      (即蒙药“阿嘎日西汤”)。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (636, 1, 6, '内外结合治疗“乳房肿痛症', '<table width="100%" bP1?r="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内外结合治疗“乳房肿痛症”<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院 \r\n      &nbsp;&nbsp;于满喜 &nbsp;&nbsp;黄志刚 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp; 我们的老师----阿拉坦仓老医生治疗妇科疾病有丰富的经验。两年来，我们在继承老蒙医经验中耳闻目染了许多临床经验。下面我们把妇科常见病之一乳房肿痛症的治疗经验整理如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;阿老认为乳房是一个遍布微细脉络的由许多小腺体组成的重要器官。该器官内含五行，聚阴阳，其乳汁具有滋补赫依、希拉、巴达干的功效。<br>\r\n      乳房肿痛多见于哺乳期青年妇女，该症在早期抓紧时机积极治疗效果较好，如果拖延不治或治疗不当形成慢性乳房肿痛则诊治困难还可能导致其它疾病。病因主要是产后气血虚弱，饮食起居不当所致。乳房肿痛症包括妇血症中阐述的乳房血肿和经血变白症的内容。并把该病分为急、慢性，或轻、重型，偏热型、偏寒型。其主要症状是乳房肿、刺痛；心慌、下腹痛、恶心、发热、乳房局部红肿；经血象乳水相混等。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在诊断时首先要询问病史，如发热史，有无乳房肿痛，有无乳房流脓史，有无外伤史，并检查乳房局部温度，疼痛程度，肿的硬度，肿块能否移动，乳孔是否堵塞，乳房肤色是否改变等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治则上以清血热，燥黄水，消肿祛毒为主。治疗方法有内服药、外敷药和局部洗的药。病程久者要结合火针外治，起到散毒生新作用。 \r\n      产后当月患的乳房肿痛症称为急性或“外感型”，用发汗法治疗效果好。方法是让患者多饮开水或茶水或吃温热饭后多穿衣服便可发汗，但要注意受风、受凉，从产后开始每天少许出点汗比较好，但不要居室太热而虚火上炎加重病情。乳房肿热时用研好的好墨汁反复涂敷或用蛋清伴白矾外敷，或用雄黄、五灵脂、冰片研细加食醋伴匀外敷在肿热的乳房上。如果肿胀的乳房不热，疼痛较轻，颜色未变时，用白酒涂擦肿处后反复轻揉凸若乳汁梗阻时用吸乳器把残余乳汁吸出来。在哺乳期的妇女气血虚、体质弱、易患乳腺病，所以用发汗治疗乳房肿痛是把血之糟粕汗液排出体外，净化血液，促进血循环及营养的重吸收，从而达到治疗目的。经发汗治疗一般2― \r\n      3日内即乳脉相通，热毒外泻，乳肿便可消退。有的患者来月经时乳房肿痛，也是一种乳房肿痛症，这种病症以调经治疗为主。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;对病期较长，病情较重，要采取内外结合治疗。如热象为主，早服“西莫替格-5味散”，午服“粘乌兰乌日勒”，晚服乌力吉一18味丸，用阿木尔一6味散为引子，寒象为主时，早服苏格木勒一7味散，午服乌力吉一18味丸，晚服萨仁嘎日迪，若合并黄水症时要辅服孟根乌素一18味丸，古古勒嘎日迪一15味丸，额尔敦乌日勒、沙日仁钦等，一个月为一疗程。洗剂：黄柏、姜黄、五灵脂、诃子等量，水煎待温后反复洗，上法若不愈时可选用外敷药蟾蜍一16味散(雄黄、五灵脂、鸽子粪、胡黄连、黄柏、珠砂、巴豆(制)、蟾蜍、血竭、龙骨、姜黄、铅粉、银珠、禹粮土、冰片、白云香共研细粉，拌糊状涂之，肿的重就敷的厚些，肿的轻可拌成稀糊状敷的薄些，一日一次，连敷三日)。其作用是拔毒消肿，还可以使有脓肿的肿块溃烂，把脓引流出来，从而愈合。如果肿处已破溃可用上方的干粉剂，直接反复外敷可使伤口收敛生新加快愈合。上述方法对一般乳房肿痛症均可治好。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;外敷药若不愈时可用火针，火针是用银或金为原料制成的针，刺入穴位后外加热达到治疗目的。有泻毒、杀“粘”、促进肿物成熟。治疗时首先取事先消毒后的火针直接刺入肿大部位的中央处，深度根据肿块的大小而定，然后用酒精灯烧针柄末端，间隔五分钟烧一次，头两次疼痛的较重，然后疼痛减轻，这样反复烧烤一个半小时至二个小时即可。体质差或晕针者要慎重，并根据实际情况减短烧烤时间。治疗有效的标志是用银针针炙一两次后便从肿块里溢出脓血，乳房表面出现皱纹，肿块缩小，皮肤颜色变浅接近正常。用火针治疗后仍不要间断外敷药的应用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;产后由经血不调所致的乳房肿痛症，西医一般称乳腺炎，在诊治上较易；对40岁以上的妇女，乳房反复肿痛，肿块坚硬，西医诊断为恶性肿瘤的患者也采用上述方法治疗取得较好效果。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;乳房肿痛症的预防，主要是注重乳房的卫生、喂奶方式及保持体内“三根”的平衡。如每次喂奶前，首先用干净的温湿毛巾擦净乳头，并挤出少许奶汁后再喂较好，喂完奶后用手掌轻轻地揉一下乳房；如果压着乳房入睡可造成乳孔堵塞。如果乳头过大，小儿吸奶困难时，用手轻轻地反复揉搓乳头后再让孩子充分吸奶。。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (637, 1, 6, '蒙药八味狼毒散外敷治疗乳腺增生', '<table width="100%" bP1?r="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药八味狼毒散外敷治疗乳腺增生<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古锡盟蒙医研究所 \r\n      何毕力格 包英珠 包玉英<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们自1993年6月至1994年12月用蒙药八味狼毒散外敷治疗育龄妇女乳腺增生症50例，疗效满意，现报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1、临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组病例50例，年龄24～42岁，平均年龄31.6岁；病程15天～6个月，主要临床表现为周期性乳房胀痛，乳房内(单侧或双侧)多个大小不等的结节状、颗粒或条索状肿块； \r\n      50例均做b型超声诊断及包块穿刺后经病理证实。其中小叶增生型18例，囊性增生型 9例，纤维腺病型12例，硬化性腺病11例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2、材料与方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1&nbsp;蒙药八味狼毒散方剂组成：瑞香狼毒15g、酸模15g、多叶棘豆15g、黄精15g、天冬15g、菖蒲15g、姜黄loog、生草乌100g。取上述8味药配研，过100目筛混合均匀备用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2&nbsp;治疗方法：a组25例，用温水洗净患侧乳房，取该药20g，用蛋清调成糊状，均匀涂在纱布上，厚度约0.5cm，敷在患处，上盖纱布固定。 \r\n      b组25例，用温水洗净患侧乳房，取20g该药用食用陈醋调成糊状，外敷方法相同。每日1次，每次敷药前洗净患侧乳房，按上述方法使用，9次为1个疗程。用该药治疗时不加其它任何药物。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3&nbsp;疗效标准:痊愈：乳房疼痛及乳房内肿块消退；显效2乳房疼痛消失，肿块缩小1／ 2；有效:乳房疼痛明显改善，肿块缩小1／2；无效:乳房疼痛无明显改善，肿块变化不明显。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3、结 果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组50例中，用药2个疗程后痊愈18例，占36％；显效15例，占30％；有效8例，占16％；无效9例，占18％；局部皮肤过敏1例，占2％，有效率82％。用药4个疗程后痊愈40例，占80％；显效6例，占12％，有效2例，占4％；无效2例，占4％。a、b两组间对照疗效无显著差异。临床结果表明，该药对乳腺增生症总有效率96％。临床提示对囊性增生型疗效不如其它类型。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4、讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为乳腺增生改变症主要是由于忧虑、恼怒等精神因素导致机体三素(赫依、希拉、巴达干)循经紊乱，乳腺末梢血管痉挛，乳房供血不足及局部瘀血，致经络不畅，气血凝结而形成痞症。蒙药八味狼毒散具有活血化瘀、消肿、通络飞、散结、止痛及组织修复等功效。其中狼毒、酸模、菖蒲有消肿、化瘀、散结作用；黄精、多叶棘豆、天冬有止痛、散结、组织修复作用。临床观察中发现该药对囊性增生型的疗效不如其它类型。至于该方最佳配方，剂型和剂量等方面有待进一步研究。临床应用中偶有局部过敏者值得注意。<br>\r\n  \r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (638, 1, 6, '蒙医对萨病的治疗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医对萨病的治疗<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古中蒙医院 \r\n      阿古拉<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医对"萨病"的认识和治疗历史悠久，《四部医典》中就有对本病的病因，分类，症状，诊断，防治等方面的记载，并论述，"萨病有火(热)性萨病及水 \r\n      (寒)性萨病之别。而病症以突然半身瘫痪、失语、口眼歪斜、哭笑等症状为主。治疗方面也有内服药，熏药，外用洗药，年过六十岁病者治疗难等记载"。十六世纪经典医著《兰塔布》中根据病情对"萨病"进一步论述："分为五种类型，即火型，水型，气型，土型，空间型等萨病。还有病变部位的特征，据记载：火型萨病表现为右侧瘫痪，水型萨病表现为左侧瘫痪，气型萨病表现为上肢及头面部麻痹为主，土型萨病表现为下肢瘫痪等，空间型萨病表现为鼻腔出血、瞳仁散大、耳鸣、其它五官病变"等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．发病因素<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在萨病的病因的方面，古代文献中曾有："赫依与琪素(血)相搏引起白脉之海一脑之受损，精神受刺激，外伤"等记载，现在根据我们多年的临床经验和研究表明，本病的发病原因除上述之外，另外一个重要的原因是黑脉病(高血压病)，动脉硬化，过度饮酒、吸烟、喜、怒、用力过猛等诱因，使气血流通障碍。赫依与琪素相搏以及运行失调而损伤脑部的黑脉而出血或堵塞并继而引起侵伤脑与自脉(蒙医白脉与西医神经相类似)导致萨病。蒙医"黑脉病"(在蒙医学中，黑脉为动脉总称。黑脉病是指由心脏分出来的动脉疾病，现经反复研究论证，综合判断认为与西医高血压病属同一范畴)。关于蒙医"黑脉病"的病因多而复杂，据《蒙医内科学》所述：黑脉病的主要病因是 \r\n      (赫依、希日、巴达干)三根功能失调而侵伤黑脉、脏腑所致。人体白脉分布全身，所以病变后出现多种症候。蒙医学认为这种病理变化在脑部发生的就引起萨病；在心脏发生的就引起心脏刺痛症(蒙医心脏刺痛症与西医冠心病相类似)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．辨证诊断与治疗<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;根据患者的不同临床症状，提出以赫依希日、巴达于、琪素、希日乌素(黄水)型等五种类型的新论点进行分型诊断治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1赫依型萨病症状：发病后病情不稳定，有时反复发作，瘫痪部位发麻以左侧为主，伴有痉挛、失眠、血压不稳，脉象虚而缓，舌苔薄而白，尿色发白而气泡沫多。类似气型萨病。治疗方剂：用主方剂同时晚上用阿嘎如--35味3g单用或煎汤作引子送服具有镇静安神作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2希日型萨病症状：突然发病、瘫痪部位疼痛以右侧为主，体温偏高，血压上升，发病时失去意识语言障碍，脉象洪大，舌苔黄厚，尿色发黄，气味大，类似火型萨病。治疗方剂：服用主方剂同时晚上用希日汤3g煎汤作引子送服，具有解表发汗作用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3巴达干型萨病症状：发病缓慢，身体肥胖，病变肢体发凉，表现迟钝，记忆力减退，以左侧为主弛缓性瘫痪，脉象迟缓，舌苔少而薄白，尿色清如水状,类似水型萨病。治疗方剂服用主方剂同时晚上查干汤3g煎汤作引子送服，具有清热解表作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.4琪素型萨病症状：发病突然，症状较重以右侧瘫痪为多，面部发红或紫色，体温血压升高，鼻衄；亘吐，头痛剧烈，脉象、舌苔、尿色基奉上和希日型相司，类似空间型萨病。治疗方剂：服用主方剂同时晚上用伊赫乌兰 \r\n      -- 13味汤3g煎汤作引子送服，具有清热凉血作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.5希日乌素型萨病症状：发病缓慢，症状较轻，瘫痪部位关节疼痛和肿胀，发痒，有时出现斑疹，血压不稳，肌肉酸痛，脉象弱而滑，尿色稍黄气味大，舌苔白而薄，类似土型萨病，治疗方剂：服用主方剂同时晚上壮龙-5味汤3g煎汤作引子送服，具有疏筋祛热作用。另外，各型适当服用通拉嘎-5味，每次3g温开水送眼，能清粕糟津，助消化作用。以上药物总的具有活血化瘀，疏通脉络，镇静安神，恢复白脉功能等作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们运用蒙医辨证论治方法，采用内服蒙成药为主，结合闻药，酒挫(推拿)，针刺，药浴，功能锻练等方法治疗近千例患者取得了可喜的疗效。通过ct等现代仪器检查确诊，前后对照总结，总有效率达 \r\n      95%以上，我们观察中发现寒冷季节、高血压动脉硬化，嗜酒成瘾，5l一60岁之间发病率高，所以称为患萨病的危险年龄。男多于女，应引起高度重视。经过上述临床实践证实了蒙医药研治萨病具有疗效高，而且无毒副作用，病情反复性小等优点。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (639, 1, 6, '蒙医药治疗糖尿病118例临床体会', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药治疗糖尿病118例临床体会<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    辽宁阜新蒙医药研究所 \r\n      齐淑芬<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;糖尿病属祖国医学消渴范畴，蒙医称本病是乌木达苏病。临床多以饥饿多食、口渴多饮、多尿甜味、乏力消瘦为特征，生化检查则见血糖、尿糖升高。病因除体质因素外，还与过食脂膏、嗜酒和精神紧张过度耗损肺、脾、肾诸脏有关。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;近年来笔者治疗糖尿病118例，其中男 49例，女69例；1--20岁30例，20～40岁24例，40以上91例；工人59例，农民15例，干部40例，学生4例；病程1年以内39例，1― \r\n      3年27例，3～5年23例，5～10年25例，10年以上4例。诊断标准空腹血糖及尿糖增高诊断为糖尿病。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治则：清热解毒，滋阴增液，补肾固精，培补脾肾。方药；沙力冲嘎；诃子、草乌、大菖蒲、广木香、麝香、大蜀季花、煅石决明、黑云香、芭叶、茜草、草茸、红花、熊胆、香墨、银珠、白蔻、白大豆共为细面，面糊丸豆粒大，日2次，每次7粒。配合降糖基本方；陈皮、半夏、茯苓、甘草、枳实、苍术、山药、僵蚕等随症加减。水煎服，1个月为1个疗程。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 治疗标准<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;患者经过用药治疗后饮食正常，小便正常，空腹检查血糖不高及尿糖正常为治愈。饮食逐渐正常，尿量减少为显效。在用药期间食用量、尿量适当减少为好转。服药治疗1个星期不见好转者无效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;痊愈72例，显效41例，好转5例。治愈率61.1％，显效率34.7％，好转率4.2％，总有效率为100％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药沙力冲嘎和降糖基本方是选用增液、生脉、补肾药物组成，两方均入肺脾肾三脏，着重补肾培土。清热养阴生津，健脾祛湿，补中有消，滋补而不腻，使燥热消，气阴复，恢复肺脾诸脏之功能，使水谷运化正常，上述所用药物相互配伍，作用相加，三消之症可愈。<br>\r\n      从生理病理两方面充分说明阳气盛衰是产生消渴病与否的内因，是起根本作用的。饮食不节，情志失调，劳欲过度等不过是产生糖尿病的外部条件而已。从三多一少症状显而易见，健康生理状态的阴阳被打破，口渴多饮，饥饿多食、绝非气盛有余之壮火，倘若壮火，应饮水自救，使火热平熄。相反，多饮水越发多尿多渴，多食越发消瘦乏力，正是肾气虚弱，如釜底无水，水难蒸腾，谷难熟化，气化失职，水液有降无升，气血津液难以化生：肾气虚也难可其固摄封藏，营阴流失。这种以脾肾气虚为根本，气虚血瘀，互为病理因素的恶性循环发展变化，形成了人体脏腑组织器官错综复杂的病理变化，临床缠绵难愈的糖尿病症。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (640, 1, 6, '蒙药“爱克泻”治疗婴儿腹泻', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药“爱克泻”治疗婴儿腹泻<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    阿拉腾托娅<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;婴幼儿腹泻是小儿常见多发病之一，主要是3岁以下小儿在夏秋季节发病较多。腹泻是本病最具特征性的表现，由于损耗大量的体液，致使赫依、希拉、巴达干的相对平衡失调，影响小儿的健康，造成小儿营养不良，生长发育障碍，甚至威胁生命。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们在临床实践中运用了蒙医传统疗法，取其精华，不断更新，发挥其优越性，创出了蒙医独特的治疗方法，使腹泻病儿童不住院、不输液不打针就能治愈，并且疗效稳定，收效快，无毒付作用，价廉，服法简便，适用于城镇、农村牧区。该疗法不需要特殊器具，只要开水冲服就能吸收奏效。这个蒙药便是“爱克泻”。 \r\n      1989―1992年，在我所观察治疗100例腹泻患儿收到了满意的效果。现报告如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(一)观察对象为1989―1992年在我所门诊及住院治疗的3岁以下日腹泻5次以上 100例患儿，其中门诊治疗99例，住院治疗1例；男62例，女38例，发病年龄最小 \r\n      25天，最大3岁，1岁以内72例。病情分类：轻型80例，重型20例。病程分类：急性81例；迁延性 15例；慢性4例。蒙医分类：热性腹泻58例；寒型42例。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(二)、蒙医分类诊断<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;①热型腹泻：发热、腹痛、黄绿色便、腥臭，脉细数，舌苔黄薄，小便黄而量少。②寒型腹泻：腹胀肠鸣、泻物呈绿色而带有不消食物及奶瓣、泡沫及粘液等，舌苔白，小便色白，脉象缓沉。⑧粘型腹泻：起病急、发热、腹痛、便溏带血、味腥臭，脉细数，舌苔黄，小便赤而气味大。④伤乳型：因乳母进用寒凉热性食物而奶水改变，导致婴儿腹泻，便呈黄水样，味臭或带奶瓣 \r\n      。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗组以自身对照为主，未设对照组。 100例患儿在我所科研组治疗前64例已用中、西医抬疗48小时以上，有的住院治疗5天以上未好转，有的服药数月未治愈，求治者都要求蒙药治疗，所以避免了同时服用其它药物，以免影响疗效问题。经查体符合条件的患儿服科研组指定的统一方剂。为了随时掌握患儿的病情，门诊患者只开1―2天药量。 \r\n      100例患儿未禁食停食，未输液。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;服药方法：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．“爱克泻”(每勺2.2g)l一 3月每次服0.5g，日服4次，3―6月内每次 0.7g，3次／日；6―12月内每次1.1g，3次／日；1―3岁每次服1.5g，3次／日用母乳或开水调服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．“哈敦嘎日迪一13”(每粒0.16g)未满月者服1／4粒，l一3月内服1／3粒，3―6月内服1／2粒，6―12月服2／3粒，)，1一3岁服l一2粒，晚睡前研末开水调服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．脱水或呕吐频繁者服“陶土日嘎一3味散”(每勺2g)200g水中加药2g，水煎5分钟分多次服，直至脱水、呕吐痊愈为止。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4。对症治疗：高热不退者另外加服“图喜木勒一3味”(每勺2.4g)每次0.5―1.0g，一日2次开水调服。感冒咳嗽者投用“尼拉哈森一乌兰汤”或“扫日劳一4汤’煎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;急性腹泻治疗3―5 日，迁延性及慢性腹泻治疗7―10日，对无效者另行换药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组急性腹泻患儿6l例，治愈80例，显效1例，其中48小时治愈率达三91.3％。迁延性腹泻患儿15例，治愈15例，其中96小时治愈率达86.6％，慢性腹泻4例，全部于72小时内治愈。所以本组有效率达99％，有的患儿服一次药就能止泻，尤其对经常性腹泻患儿效果更佳，服3―5日药后食欲增加，消化功能好转，腹泻停止，并能促进生长发育，服法简便，可口，无毒副作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;四、讨论 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．小儿赫依、希拉、巴达干的功能尚未健全，脏腑娇嫩，尤其胃肠功能娇弱、，如果食用污染、变质或过凉、过热食物或受寒冷暑热之气，或因惊吓而引起食物不消，可致精华糟粕分离不畅，则出现腹泻。小儿本质乃属巴达干，所以巴达干的偏盛或偏衰都直接危害机体而致病。腹泻是由于消化希拉，腐熟巴达干，调火赫依的功能失调，巴达干、希拉所致的大量津液耗损丢失，希拉增多，巴达干耗竭，便出现一系列临床症状。此时用药必须以祛希拉，补巴达干，养体质的原则治疗之。否则将会导致体液耗尽而危急生命。本组100例患儿热型腹泻48例，寒型腹泻34例，乳食所伤18例。治疗未分寒热，只是因症施治，用规定的统一方药治疗，收到了满意的效果。所以，“爱克泻”不论对寒热伤食性腹泻均能适应。收到同样效果。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．“爱克泻”是该课题组牵头人笔者在多年临床实践中积累创新的验方。经对临床观察研究多次应用，已成为适合小儿体质的独具一格的防治腹泻病的新药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;现代医学对腹泻病，尤其是对病毒感染引起的腹泻病还没有特效疗法，世界卫生组织(who)对于抗腹泻药物制定了六条标准’(1)高效，(2)可口服，(3)可为口眼补液伍用，(4)不被肠道吸收，(5)不影响肠道吸收功能，尤其是葡萄糖和氨基酸的吸收，(6)可抵御一系列肠道病原等。“爱克泻”恰恰符合上述标准，解决了临床上不易解决的问题。其功能主要调整体内三根，促进食物精华与糟粕的代谢，助消化，止泻。该药经服用后l一2小时内，通过黑冰片、寒水石等药物的作用，可覆盖胃肠粘膜，控制粘毒及细菌的侵入，加快秽物的排泻；石榴、桂皮、紫叩、白叩等药物则能促进下清赫依的功能，缓解腹痛腹胀而止泻。该药不被肠道吸收，服后 \r\n      3―4小时即随粪便排出。晚上伍用“哈敦嘎・日迪一13味”更能增强药物的效能，清落腹之血希拉热，祛肠道中残留的粘毒、细菌，有解热镇惊作用。 ．<br>\r\n      研究发现“陶土日嘎一3味散”不仅有止吐作用，还有很好的补液功能，经对本组脱水患儿全部采取该药口服补液，得到了满意的效果。这是本研究中的又一个新发现。<br>\r\n      通过临床研究掌握了所用药的功效、作用、服药方法及适合小儿用量的准确数据。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.“爱克泻”由石榴、锁阳、黑冰片、寒水石、桂皮等十四味药组方。上述前物按药材标准选料后，寒水石需热制，黑冰片等药则按要求存性烧炭爹粉碎成细粉，120目筛过筛，混匀。性状：本晶为黑祸色粉末，气味微香，味微苦等。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (641, 1, 6, '蒙药通拉嘎601治疗原发性血小板减少性紫癜80例临床研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药通拉嘎601治疗原发性血小板减少性紫癜80例临床研究<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院 \r\n      陈玉良 陈沙娜 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;原发性血小板减少性紫癜(idiopathic thrombocytopenic purpura简称itp)是常见的免疫性出血疾病的综合症。主要由于患者体内存在抗血小板因子，致使血小板破坏过多，血小板计数减少为特征的疾病。至今且无理想的治疗方法。现代医学主要用肾上腺皮质激素或免疫抑制剂治疗，但疗效不理想，且常引起严重的不良反应。集多年的临床实践，研制出蒙药通拉嘎601以治疗itp，结果表明疗效显著，现将临床治疗80例的结果报告如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 患者：在80例患者中男性35例，女性45例，年龄在4个月～60岁之间。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 诊断依据：周围血中血小板计数少于100x109／l；出血时间延长；血块收缩不佳；束臂试验阳性：骨髓象中巨核细胞总数正常或增加、产生血小板的巨核细胞减少；临床表现出血倾向、伴有皮肤、粘膜、内脏等有自发性出血。全部病例符合第二届全国血液学会所制定的诊断及分型标准。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 病程情况：蒙药治疗前病程在一年以内者19例；1～5年者53例；5年以上者8例。除1例未经激素治疗外其余均经皮质激素等治疗后一度好转又复发者。激素用量达60mg／日者11例，30mg／日者35例， \r\n      10―20mg／日18例；出现明显副作用者32例。曾服过中药者32例。曾用环磷酰胺的7例。80例中急性型 1例，慢性型79例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 临床表现：80例患者均有自发性出血倾向，将出血症状分为四级：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;工级：无出血症状或仅在挫碰后有轻度皮肤出血，此级15例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;Ⅱ级：皮肤、粘膜轻度出血；女性月经血量过多、但有自限性，此级36例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;Ⅲ级：皮肤：粘膜出血明显、月经血量过多、或伴有内脏出血、无贫血症，此级24例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;Ⅳ级：皮肤、粘膜出血量多、内脏出血量多、hbl00mg／l以下、常需采取积极的止血措施，此级5例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 蒙医辨证：按蒙医“三元”理论及“血症”论分型为：血热偏盛型为64例；毒瘟合并型11例；寒气相辅型5例。 \r\n      2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 治疗主方：蒙药通拉嘎601。其主要功能为清热凉血、清瘟解毒、和血止血、活血散瘀等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 用法：成人1―2次／日，13～15粒／次(约3g)，儿童酌减。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 配伍用药：依据分型属性辅助用药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;血热偏盛型：以清热凉血为主，配用蒙药希日―3味汤或乌兰―3味汤。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;毒瘟合并型：以清瘟解毒为主，配用巴特尔―7味。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;寒气相辅型：以促进清浊分清为主，配用当玛―5味。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;如若血小板过低(10x109／l以下)出血症状严重时配用古日古木―8味或加服熊胆粉适量或以其它动物胆粉代替。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.4 禁忌：避免应用对本症的治疗不利的药物，着重调整患者的饮食及起居，接受治疗前服用激素的患者，逐渐减少激素的用量至停服激素。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.5 疗程：蒙药治疗该病的1个疗程为3个月。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 疗效判定标准<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 基本治愈：周围血中血小板计数恢复正常值，无任何出血症状，持续3个月以上，随访两年无复发者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 显效：血小板计数增多升至50x10 9/l或较原水平上升30x10’以上，无或基本无出血症状，持续两个月以上者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.3 进步：血小板计数上升不明显，出血症状改善，持续一个月以上者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.4 无效：经两周治疗血小板计数及出血症状无改善者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4&nbsp;治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;基本治愈37例，占总数的46．3％；显效者18例，占22．5％；进步者16例，占20％；无效9例，总有效率为88．8％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;现代医学认为itp是血小板成为抗原与抗血小板抗体结合成复合物在脾脏被破坏的自身免疫性疾病国内外采用许多不同治疗措施和方案，但疗效不甚理想，易复发，甚至会引起许多副作用。<br>\r\n      我们根据itp的临床特点，依据蒙医药独特的理论，结合多年的临床观察和治疗经验，研制了蒙药通拉嘎601试治itp取得了较理想的效果。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.1 所观察治疗的80例患者中除1例幼儿未用过肾上腺皮质激素治疗外，其余均属下例两种情况一是经肾上腺皮质激素及免疫抑制剂治疗而无效者；其次是反复发作的慢性患者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.2 蒙医认为itp属血症范畴，其主经原因是“三元”失衡而引起气火上亢、热伤血络、气失统摄、血不循经所致。紫癜、出血等症是火盛气逆、血热妄行、溢于血络之外、渗于肌肤之间的表现，妄行离经之血既是瘀血。故临床上采用分清清浊、清热凉血、止血清瘟解毒及活血化瘀等方法辨症论治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3 经通拉嘎601治疗后患者的病情有以下四方面的改变：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3．1 明显提高血小板的质和量。我们在治疗中发现，服药3周后患者的出血症状明显减轻，瘀点消褪先于血小板计数的上升。这可能是改善血小板功能先于血小板数的增加。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3．2 紫癜的迅速消失，推测是降低了毛细血管的脆性与通透性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3．3 血小板计数升高，这可能是调节机体免疫功能，降低自身抗原抗体复合物的产生而使血小板破坏减少。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3．4 使骨髓中的幼巨核细胞及颗粒巨核细胞数下降，使产生血小板巨核细胞数增多，从而促进巨核细胞成熟及释放血小板。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.4 为了以现代医学检验蒙药的疗效，与西医结合设强的松治疗组。经蒙药治疗后治愈率达46.3%。西医组治愈率为23．3％。蒙药组显著高于西医组。蒙药治疗itp总有效率为88．8％，在治疗过程中没有出现任何副作用。持续治疗可明显提高疗效和治愈率，这表明蒙药疗效显著有很多突出的优点，有深入研究的价值。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (642, 1, 6, '蒙药治疗冠心病心绞痛50例', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<p align="center">蒙药治疗冠心病心绞痛50例<br>\r\n        <br>\r\n        &nbsp;内蒙古医学院中蒙医系 奇太宝 南海欧 道布庆<br>\r\n      </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp; 笔者自1995年至1999年运用自拟蒙药明月心宁丸治疗冠心病心绞痛50例，疗效满意，现介绍如下：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;本组50例病人，男38例，女12例；年龄最小33岁，最大72岁，平均52岁；全组均有不同程度的高血压病史，血压平均在25.43／17.22kpa。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;明月心宁丸(广枣65g、栀子45g、肉豆蔻 35g、石榴35g、阿魏25g、红花20g、荜茇5g、肉桂5g、白豆蔻5g，另加紫檀香25g为a组，加白檀25g为b组，分为两组共研细末，混匀后水调为丸，每丸0.3g左右备用)，日服 \r\n        1―3次，每次1.5～3g，开水送服，血盛型者用a组，赫依型者用b组，服7天为一疗程，每疗程间隔2天，连用3个疗程复查血脂、心电图，服药期间停服其它药物。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3 治疗结果<br>\r\n        按1979年全国中西医结合防治冠心病心绞痛、心律失常座谈会制定的疗效判定标准，显效39例，有效9例，无效2例，总有效率96％。经半年以上观察，心绞痛发作次数明显减少，疼痛程度明显减轻，血脂下降，有 \r\n        21例连续6个月无心绞痛发作。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4 讨 论<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;冠心病心绞痛属蒙医“心刺痛”之范畴，病因病机多与饮食精华未消，普行赫依不足有关。蒙医认为人体受纳饮食以后，由胃肠内的“三阳”分别精华与糟粕进行输布，如三阳失调，消化功能发生障碍，饮食精华未消而造成脂肪吸收过多或脂肪代谢紊乱，就会使体内胆固醇含量增高。普行赫依寓位于心脏，它能鼓动心脏搏动，促进血液循环。若普行赫依不足，则使心脉不通而引起一系列冠心病的症状。故此，调理胃肠之三阳，增强普行赫依的功能活动，是治疗冠心病心绞痛的关键所在。蒙医对冠心病心绞痛的分型有二，即赫依型者症见游走性疼痛，由心前区放射至肩和背部，病属凉性；血盛型者症见在心前区固定位疼痛，病属热性。方中广枣、檀香、栀子有强心、宁心作用，肉豆蔻、阿魏能增强普行赫依之功能活动，红花活血化瘀，石榴、肉桂、豆蔻、荜茇则调理三阳，分解饮食精华与糟粕。诸药相配，标本同治，经临床验证疗效确切。效果近、远期皆优于消心痛和烟酸肌醇脂。 \r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (643, 1, 6, '浅谈蒙医药治疗心律失常', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浅谈蒙医药治疗心律失常 \r\n      <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医院 \r\n      毕力格 巴根那 额尔敦<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;心律失常属蒙医心悸、眩晕、胸痹等病范畴，常由于人体“赫依”“希拉”“巴达干”三者间失去平衡，脏腑功能失调，气血亏虚及内伤七情，外感六淫，痰湿阻滞，气滞血瘀等因素引起。下面介绍临床用蒙医药辨证施治心律失常的体会。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 分型及治疗<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 “赫依”神经失调型：心悸气短，善惊易恐，坐卧不安，少寐多梦，头晕乏力，舌淡红，苔薄白，脉细数或弦细，结代。治法为调节“赫依”，镇惊定志，宁心安神。用以阿魏、丁香、肉豆蔻、酸枣仁、鹿心或兔心、龙骨、朱砂、沉香等为主，佐药的方剂如匝迪―5、布如宁―6、少砂―7、兴棍―25、吉如和―25等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 巴达干、赫依相成型(寒型)：症状见心悸气短，少气懒言，腹胀纳呆，面色不华、头晕乏力、舌淡苔白，舌质胖大，脉细弱，迟缓或结代。治法：祛寒益气，温补脾肾，养心安神。方药：用以草果、白豆蔻、甘草、槟榔、阿魏、肉豆蔻、党参、肉桂、远志、酸枣仁、石菖蒲、土木香、广木香、荜茇、石榴等为主，方剂如高尤― \r\n      13、苏格木勒―7、兴棍―25、莫达―12、舒达嘎―9、匝迪― 15、达力―16、宁让阿嘎日―8等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 血“希拉”型或“粘”热型(热型)：症状心悸而烦，胸闷气短，体倦乏力，两颧发赤，口干自汗、惊悸难眠，心区不适或疼痛，舌苔黄腻，脉数而兼促。治法：清热解毒，杀“粘”清心，宁心安神。方药：用以紫檀香、白檀香、栀子、牛黄、麝香、熊胆、天竹黄、藏红花、沉香、广酸枣、木棉花、麦冬、苦参、丹参、冰片、草乌叶、黑云香、赤芍、胡黄连、连翘、石决明等为主，方剂如赞丹―3汤、雄阿嘎日―8、阿敏―额尔敦、吉日很古日古木―7、额日赫木―8、嘎布日-7或9或25等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 “赫依”热夹杂型或“赫依”血夹杂型或“赫依”热“粘”夹杂型：症状见心悸气短、口干而不思饮，胸闷或胸肋刺痛、自汗、心烦、舌红少苔，脉细数而无力或结代。治法：调节“赫依”清心热，杀“粘”，宁心安神。方药：用以沉香、降香、肉豆蔻、紫檀香、白檀香、丹参、广酸枣、细辛、旋复花、土木香、广木香、苦参、远志、黑云香、麝香、诃子、甘草等为主，方剂如阿嘎日―35、让阿嘎日―8，赞丹―3汤、阿敏额尔敦、阿嘎日―<br>\r\n      17、苏日各申―11等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 寒热夹杂型：症状见动辄气短，心悸胸闷、形寒 舌淡、舌苔薄黄、纳食不振、心前区憋闷隐痛、头晕眼 \r\n      花，健忘，多梦、入夜难寐，脉细数无力、结代。治法； 平衡阴阳、调节寒热、宁心安神。方药；用以土木香、 广木香、栀子、广酸枣、阿魏、苏格不勒、丁香、肉豆蔻、白檀香、沉香、珍珠干、麦冬、苦参、桂枝、荜茇、石榴、牛黄、熊胆、连翘、丹参、红花、石膏等为主，方剂 \r\n      如通拉嘎―5、竹岗―德吉德敛木、赞丹―3、阿敏额尔额、苏日各申―11，阿嘎日―15、敖必德斯―23等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.6 痰浊扰心：症状见心悸胸闷，眩晕呕恶，痰多气短，神昏谵语，舌苔滑腻、脉滑结代。治法：化痰、定悸、安神。方药用以远志、石菖蒲、甘草、广木香、天竹黄、桔梗、沙棘、羚羊角、广酸枣、天南星、杜鹃花、枇杷叶等为主，方剂如达日布―5、达力―16、仁钦―乌克套布、达日布―17、阿嘎日―35、胡勒森・竹岗―8、帮占―15<br>\r\n      等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.7 心血瘀阻：症状见心悸，胸闷不适，时发心痛，唇甲青紫舌紫暗或有瘀点，脉涩结代。治法；活血化瘀，通脉宁心。方药：用以红花、丹参、益母草、当归、苏木、酸枣仁、山奈、沙棘、荜茇、赤芍等为主，方剂如达日布―11或17，扎木萨―4汤、通拉嘎―5、玛努―4汤、额尔敦・乌日勒等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;心律失常种类繁多，病因病情复杂，证型错综，兼证各异，在心律失常的诊断治疗中必须凭脉察色，辨证精详，用药得当，方可取效。尤其是一些反复投药而欠佳者，往往寒热夹杂或“赫依”热或“粘”夹杂，阴阳互损，更需悉心推究、正确把握病机，利用蒙医药的辨证施治，表本兼治，整体局部兼顾，用药灵活。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (644, 1, 6, '蒙药治疗小脑萎缩', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药治疗小脑萎缩<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;       内蒙古中蒙医医院&nbsp;&nbsp;苏木亚<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为小脑萎缩属于白脉病(中枢神经系统疾病)范畴，主要是由于某种因素造成体内存在的三根失衡，使赫依、琪素(气血)不畅而引起疾病。三根(赫依、希拉、巴达干)学说是蒙医理论的基础部分，汉译赫依是气体、希拉是火，热性，巴达干是寒、凉性之意。蒙医学理论用它来解释机体内的错综复杂的关系和不可缺少的基本物质，这三种物质的存在不是孤立的而是在它们之间保持着相互对立，相互制约，相互依存的相关的辨证关系。在正常情况下，它们的关系处于相对平衡状态，致使机体生理功能得以正常运转，如果三者之任何――方出现成变，失去平衡时势必导致疾病的发生，因此蒙医学认为三根的关系是人体生理病理的内在因素，是生命赖以生存的主要矛盾，决定着生命中一切活动。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医理论把人体神经系统和动静脉系统分别称为白脉和黑脉，并且它们在正常三根的作用―下发挥着各自的功能。全身的各系统和器官也分别都有自己的三根，主要依靠赫依使器官收舒活动，输送血液及营养物。通经络，希拉和巴达干保持人体温度，消化食物，稳定情绪、调节器官功能等作用，完成体内的新陈代谢从而达到各系统器官的正常运行。白脉主要靠赫依的运行来完成作用，如果因某种原因三根失衡，那么就会出现气血(赫依、琪素)运行不畅，致白脉功能失调，在临床上出现神经系统疾病的各种症状。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;根据蒙医理论小脑萎缩的治疗原则是以消除外部诱因、调节体内失衡的三根，使气血运行通畅、恢复神经功能。用药食分主药和辅助药之分。<br>\r\n      主药：“额尔敦乌日乐”(珍珠丸)，“嘎尔迪一13味”。以上两种古成药由麝香、珍珠、红花、犀角、草乌、广木香、青木香、牛黄等药材组成，具有清热、祛风除湿、通经活络、舒筋活‘血等功效，因此临床上常用于疫热扩散血脉、中风瘫痪、手足麻木、言语不利。<br>\r\n      辅助药：“伊赫汤一25味”、“阿命额尔敦”、“阿嘎尔-35味”、“查嘎都尔”、“阿拉坦阿如热一5味”等具有调和气血、解毒、制气盛而消痰热、改善消化机能、镇静安神等作用。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (645, 1, 6, '呼春吉呼-1号治疗阳痿', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;呼春吉呼-1号治疗阳痿<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医院 \r\n      博音图 乌力吉特古斯<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们遵循蒙医学辨证施治的原则，用呼春吉呼一i号(回春一i号)治疗阳萎病103例并与“那仁满都拉”对照组48例，做了比较。结果具有显著差异，现将临床观察结果总结如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;全部病例均符合“实用男性学”所述诊断标准，均为功能性阳萎病，以18岁以上成年男性为主。治疗组患病2年以上的12例，1年到2年的20例，半年到1年的30例，半年之内的41例；合并早泄病的45例，合并遗精或滑精的 \r\n      29例；蒙医辨证分型肾寒型45例，心肾赫依虚型13例，肝赫依希拉混浊型12例，肾亏损型18例，肝胆希拉哈伦下注型15例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;对照组病程2年以上的12例，1年到2年的16例，半年到1年的12例，半年之内的 8例；合并早泄病的9例，遗精或滑精的17例；蒙医辨证分型肾寒型14例，心肾赫依虚型4例，肝赫依希拉混浊型8例，肾亏损型 \r\n      10例，肝胆希拉哈伦下注型12例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.l 呼春吉呼一i号主要由黄精、手参、红参、益智仁、海马、蛤蚧、冬虫夏草、鹿鞭等蒙药组成。具有益气养阴，滋温祛寒和补肾补阳等功效，可治疗阳气不摄，精漏阴湿，精气不足，遗精、阳萎等。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 辨证分型及选用药方<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.1 肾寒型：下肢寒冷，寒痛，腰膝酸软四肢麻木、阴茎萎软，阴囊潮湿，多尿、尿无色无味，脉沉而弱。选用主方加苏格木勒一10。<br>\r\n      &nbsp;&nbsp;&nbsp; 2.2.2 心肾赫依虚型：心律不齐、神志不清，健忘，心悸心慌、多梦，精神萎靡，遗精阳萎，头晕耳鸣，尿白泡沫多，舌苔红燥，脉洪。选用主方加槟榔一13。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.3 肝赫依希拉混浊型：多有悲伤脑怒，情绪紧张和精神受到刺激史。阳萎不举，举而不坚，时轻时重，情志抑郁，伴胸胁痛、口唇发干，善太息、肝区不适，消化不良等症状，舌质红、脉洪而速。选用主方加红花一13。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.4 肾亏损型：病史反映多有从高处跌打摔伤，受惊恐、长期手淫和先天发育不良等史。阴茎易兴，临房则软，心烦意乱，失眠盗汗，头晕健忘，腰膝酸软，精神郁闷，多怯多疑，闻声则惊，苔淡白，脉虚弱。选用主方加朱吉德尼米易吉乐浩尔。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.5 肝胆希拉哈伦下注型：阴茎萎软，阴囊潮湿、坠胀，甚则肿痛，有时阴茎肿痛、发红，心烦潮热，口唇发干，小便赤黄、灼痛、尿有味，舌苔黄腻，脉滑而速。选用主方加嘎布尔一25。服用时先用辅方半月之后，再用主方。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 给药方法：主方每次服15丸～17丸(约3g)，根据个体差异每d服1～2次，l／疗程为7d，一般服2～4个疗程。对照组口服那仁满都拉水丸，每次服15～17丸(约3g)，每d1～2次。其辨证分型与所服辅方与治疗组一样，确保观察指标的统一性。两组合并早泄者，配服莎仁嘎日迪；每 \r\n      d1次，每晚睡前服13丸。1个疗程为7d，一般服2～4个疗程。合并遗精、，滑精者配服吉日顺一8，每d1次，1次15\'丸～17丸。服2／u 3个疗程。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 疗效评定标准<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床治愈：阴茎勃起有力且持久，可进行正常性交，有性满足感，随访3个月无复发；显效：阴茎可以勃起，进行性交，但有时勃起不坚或不能持久，其伴随症状有所改善，阳萎分级好转2级以上；好转：阳萎分级好转l级以上，偶而有勃起，但不能性交者；无效：治疗前后病情无明显改善。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗组(103)例，治愈64例，占62.1%；显效15例，占14.5％；好转13例，占12.6%；无效11例，占10.8％；对照组(48例)治愈8例，占21.0％；显效11例，占28.4％；好转5例，占10.4％；无效14例，占29.5％。治疗组合并早泄者45例，其中治愈35例，占 \r\n      78.8％；有效39例，占88.2％；无效5例，占 11.8％。合并遗精或滑精者29例，其中治愈率84.3％，有效率87.8％o对照组合并早泄者9例，治愈率55.5％；有效率77.7％。对照组合并遗精者17例，治愈率52.9％；有效率 \r\n      82.3％。两组总有效率分别为阳萎89.2％、 62.5％，早泄88.2％、77.7％，遗精89.8％、 82.3％。治疗组优于对照组。分型与疗效的关前与治疗后病情无明显改善。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp; 5 讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;阳萎病，蒙医学称为勃勤呼斯易倒热他拉。本病发病机理与心肾气虚，肾寒和肾亏损，肝胆热、湿热等有关。故治疗以益气养阴、滋温祛寒、补肾壮阳为主，方中黄精甘能守，平能助阳具有补脾润肺，益气养阴、补诸虚，填精髓之功效；手参甘平，滋阴生津，补肾益精；红参甘能守，微苦补阴，温能助阳，助运化，输精微，化阴液，为扶阳益阴之佳品；益智仁具有辛温，温脾，益肾，固精之功；海马甘温，固精，补气，祛肾寒；蛤蚧；咸温，具有固精、补气、祛肾寒等功能，以上诸药合用，具有养血滋阴、补阳固精，阳气不摄，精漏阴湿，精气不足之功效。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;呼春吉呼一l号组方是用于治疗以阳萎为主，合并症早泄、遗精的有效新开发蒙药方剂，临床试验证明治疗阳萎的总有效率89.2%，治疗早泄的总有效率87.8％，治疗遗精的总有效率88.2％，效果迅速显著，无毒副作用。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (646, 1, 6, '腰间盘突出', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;腰间盘突出<br>\r\n<br>\r\n<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院&nbsp;&nbsp;乌兰&nbsp;白云&nbsp;元登&nbsp;齐乌兰&nbsp;马殿武&nbsp;特木尔 \r\n      <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们从1989年开始应用蒙医五种疗法 (包括温针、针刺、水针、放血、拔罐及药浴)来治疗腰椎间盘突出，取得了满意的疗效，现报道如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;l、一般资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;125例中，男75例，女50例；年龄18～ 30岁19例，3l-40岁42例，4l-50岁38例，51～60岁15例，6l一70岁10例，7l一83岁3例；病程l～10天者45例，10天～2月者52例，2～3月为22例，3～5月者6例。 \r\n      125例经检查均符合腰椎间盘突出诊断标准。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2、治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1患者俯卧位，局部常规消毒，取患者椎间盘突出部位的棘突，旁开0.3―0.5寸，等得气后，上下提插、捻转至患侧下肢似触电样感觉放射至足跟及趾尖为宜，即可拔针，此针法强度根据患侧下肢疼痛程度而定。取针后，休息5分钟，然后将50％的银针按原位刺入，用艾绒或酒精烧10分钟后，间隔10分钟，再烧1次。同时，在环跳、委中等穴进针，共针30分钟，此法隔日1次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2取针后，在腰椎间盘突出部位及患侧下肢压痛点拔罐，15分钟后，应用三棱针及插花针快速针刺后，局部出血，再行拔罐，每罐拔出5ml血，此法5天1次。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3然后用骨宁注射液2ml加vit―b12 1ml在椎间盘突出部位、周围压痛点及患侧下肢压痛点，进行穴位注射，注射针头长3寸。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.4最后蒙药浸浴治疗，患者全身或腰部及双下肢浸泡在药液中20―40分钟，和针灸隔日进行交替治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3、疗效标准<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;痊愈：自觉临床症状全部消失，活动自如，x线复查，侧弯棘突及椎间隙正常；显效：自觉临床症状，体症基本消失，但偶有局部不适感，x线复查，侧弯棘突及椎间隙有明显改善；好转：自觉临床症状及体征基本改善，腰及坐骨神经疼痛部位部份减轻，能做一般运动及轻体力工作，x线复查，侧弯棘突及椎间隙略有改善；无效：治疗3个疗程，临床症状和体征，未见明显变化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4、治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在治疗当中观察到，病程时间越短，效果越佳；病程越长，治疗疗程越长，效果较差。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5、体会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医火针针刺综合作用后，能促进局部新陈代谢，加快血液循环，同时通过负反馈信息，使功能失调的神经自行调节，改变其走行，抑制神经传导递质，终断疼痛传导通路，使痉挛的肌肉、韧带放松，减轻椎间隙的压力，解除疼痛。<br>\r\n      放血、拔罐、蒙药浴具有活血化瘀，通经活络之功效，使受损的神经得到修复和再生或建立代偿性侧支循环。<br>\r\n      针刺、水针可刺激小血管扩张，改善微循环及组织的缺氧，促进血肿的吸收，分离神经粘连，降低毛细血管通透性，抑制椎间盘组织的炎症，促进炎性物质的吸收，抑制毒性物质的反应。同时，对消除组织堆积的丙酮酸、乳酸，增加组织能量，促神经对致痛因子的抵抗，促进神经鞘脂、蛋白合成，保持神经纤维功能的完整性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;有关资料表明，腰椎间盘脱出症患者血清lpo较正常的人明显增高，而血清中 sod活力较正常人明显降低，而自由基的增高和组织细胞损伤可继发性引起sod活力绝对或相对下降。资料表明，针刺、火针、及放血等外治方法可使血清中cpo明显下降， \r\n      sod活力明显提高。这对于清除体内自由基，提高sod的活力起到了一定的作用。本法治疗腰椎间盘突出经临床观察，病程短，治愈率高，疗效快。资料表明，髓核是胶原粘多糖、蛋白和碳水化合物之复合体，早期具有一定的弹性和坚韧性，这时，最容易还纳，在临床上，这个时期是症状表现最为疼痛的时期，也是治疗的最佳时期。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (647, 1, 6, '蒙医药治疗银屑病50例', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药治疗银屑病50例<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    内蒙古中蒙医院 \r\n      张海玉 高志清 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;50例银屑病中，男性36例，女性14例；年龄最小者为14岁，最大者为65岁；病程最短者为5天，最长者为15年。每一疗程为1个月，最长治疗3个疗程，最短治疗一个疗程。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 主要方剂：孟根・乌苏―18味丸，来源于《内蒙古蒙成药标准》，1983年版。伴有消化不良者加服通拉嘎―5味；月经不调者加服乌力吉―18味或毛仁・扎格朱。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 辨症治疗<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.1 赫依琪苏型：久治未愈，病程较长，皮损呈大片状，着色较浅，出血点现象不明显，全身症状不显著；并且伴有舌干、口燥，舌质红，苔少；尿微黄；脉沉缓或沉迟。宜改善气血、活血、祛黄水。配合蒙药别冲一15味治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.2 黄水型：皮疹呈片状，基底肥厚，阴雨天加重，瘙痒较剧；并伴有口干、口苦、食欲不振。舌质微红，苔厚腻，尿黄，脉弦滑或弦缓。宜清热、燥黄水、活血治疗。配合蒙药通拉嘎―5味。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2.3 血热型：皮损发展迅速，皮疹呈红色或深红色，境界清楚的斑丘疹及小片红斑散布于躯干、四肢，亦可见于头皮、颜面，表面覆有银白色鳞屑，容易脱落，剥离后有点状出血，瘙痒剧烈；病程较短，并伴口干、咽干、咽痛等症状，舌质红，苔薄黄，尿黄味大，脉浮数。治宜清热解毒、凉血、活血。并配合蒙药古日古木―13味治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 禁忌：祛除病因，解除患者精神负担，限制饮酒，忌食辛辣刺激之品，居住环境宜干燥。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 治疗结果<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;50例银屑病中，痊愈19例，占38％；显效22例，占44％；好转9例，占18％。总有效率达100％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;银屑病是一种慢性皮肤病，病因尚不清楚。病程较长，易复发，个别病人终生不愈。是由于饮食不当，胃火受损，环境潮湿，汗后受凉，情志受到刺激，愁苦抑郁，外伤或其它疾病的影响等诱因，致使三根失调而皮肤气血运行受阻，随而黄水增生扩散于皮肤，均为诱发本病。故治疗上清热、凉血、活血，调理三根，改善气血运行，燥黄水为原则。蒙药孟根・乌苏―18味等药物治疗，取得了比较满意的疗效。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (648, 1, 6, '蒙药药浴治疗类风湿关节炎', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药药浴治疗类风湿关节炎<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古包头蒙中医院 \r\n      达格巴雅尔 傲特根其木格 乌兰其其格<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医认为类风湿性关节炎属图来病范畴，又称痛风。1991年以来，我们应用蒙医传统治疗方法蒙医药浴，对50例病例进行临床观察，效果显著。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 一般资料<br>\r\n      50例患者中，女性32例，男性18例；16～ 40岁38例，41岁以上12例；病程最短的3个月，最长的20年。上述病例均经现代医学辅助检查，蒙医、西医相结合诊断的类风湿性关节炎。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 方药及应用方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1.1 蒙医药浴组方：五甘露[刺柏、杜鹃叶(可用冬青叶)]各500g，杠柳、麻黄各 1000g，小白蒿1500g，苘麻子、草决明、文冠木、楝子、栀子、地丁、红花各250g，苦参、草乌叶、白云香各200g，广木香，石菖蒲，称图来阿日山。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1.2 五甘露、草乌叶、苦参、文冠木等13味药，在提取锅内，煎煮，首次煮2～3小时，过滤药浴，反复煮4―5次，提取药液，进行浓缩，成膏，红花，白云香，诃子，广木香等4味药，粉碎后加入膏中，搅拌、烘干，再粉碎，包装，(250～500g，1袋)备用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1.3 用法、用量、蒙药浴，每日1次，每次30―60分，2周为1个疗程，每次取药散50一 l00g，先在煮药桶内，放水煮开，浴盆(池)内，放适量水(能够浸泡全身)，加温(37―42℃)，放入药液，搅匀，患者更衣入浴，浸泡治疗，根据病情，体质，调节水温，药量，时间。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1.4 蒙药浴护理：药浴前后加强护理，非常重要，直接关系到医疗效果，浴前，首先做好患者心理工作及浴前准备，浴中、浴后严密观察患者的病情，一般状况，卧床休息，严防受风，受凉，受累，受潮，观察病人的血压、呼吸、脉搏的变化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 口服药及疗法：根据病情、疾病的分型，可选用巴日嘎雄阿汤、图来乌日勒、伯冲仲阿、三普勒闹日布、扎冲珠苏木、扎冲珠吉格、乌勒楚照布吉德等药物。治疗可用火针、针灸、红外线理疗、穴位封闭等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 疗效观察<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 治疗指标：治愈：病情控制，肿胀、疼痛等症状消失，活动自如，各种辅助检查正常。显效：肿胀、疼痛消失，症状明显好转，各种检查正常。有效：症状减轻，病程长期得到控制，病情虽有得发，但症状较轻。无效：治疗无明显效果，病情加重，继续发展，活动障碍等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 治疗结果：50例患者治疗观察治愈4例，占8％；显效32例，占64％；有效12例，占24％；无效，2例占4％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 体 会<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医称类风湿性关节炎为图来病，主要以体内希日乌苏病变引起关节疼痛，周围组织肿胀。发病初期，局部红肿、热，皮下结节，疼痛为主，中期侵犯筋骨，关节，肿胀加重，关节活动受限，疼痛加重，后期关节肿大，周围组织及肌肉萎缩，致残。图来病主要以手足小关节病变多见以外，肘、膝、腕、踝、肩、髋关节，腰椎变形，对称性发展。图来病女性多见，主要以劳累过度，受风寒，潮湿，长期凉水中作业等诱因，引起体内赫依、希日、巴达干、楚苏、希日乌苏失调，体内希日乌苏增多与楚苏混合，存在于关节及周围组织，造成赫依楚苏循环受阻，发生肿胀，肿大。蒙医精典著作《阿日山耐胡日玛》(甘露精华)，一书中称图来病是骨质病。所以蒙医药浴及口服蒙药，完全符合蒙医理论和特色在临床上疗效显著。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (649, 1, 6, '蒙药萨乌日勒治疗老年性中风', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药萨乌日勒治疗老年性中风<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医院 \r\n      阿古拉 元登 &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n<br>      &nbsp;&nbsp;&nbsp;&nbsp; 我们从1995年3月至1996年9月应用萨乌日勒治疗老年缺血性中风150例，其中60例患者进行了血液流变学观察。蒙医萨病，中医称中风或脑卒中，西医称为脑血管病，缺血性中风，即缺血性脑血管病(脑梗塞)观察病例都是老年前期45岁以上脑梗塞患者，现分析如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本组150例病人均为住院患者，符合全国第二届脑血管专题会议制定的脑梗塞诊断标准，均经ct检查确诊。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 一般资料：年龄组45～50岁59例， 51-60岁63例，6l～70岁17例，71岁以上11例，男性97例，女性53例，男女比例为 \r\n      1.85:1。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 定位诊断：临床诊断脑梗塞部位在大脑中动脉105例，基底动脉33例，小脑下动脉12例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3蒙医分型<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.1 寒性98例。其中赫依型48例。主症：偏瘫，以左侧多见，口舌歪斜，偏身麻木伴痉挛，晕目眩，烦躁失眠，老年人为多见，脉象虚而浮，舌质红涩，苔薄，尿色白，泡沫多。巴达干型50例，主症：偏瘫，以左侧多见，口舌歪斜，肢体发凉，健忘发呆，痰多，口流涎，肥胖者多见，脉象沉缓，舌苔薄白，尿清。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.2 热性42例。其中希拉型29例，主症：偏瘫，以右侧多见，口舌歪斜，言语蹇涩或不语，申志昏蒙，眩晕头痛，面红目赤，口苦咽干，心烦易怒，脉洪弦，舌苔厚黄，尿黄便干。琪素型13例，主症：起病急骤，神昏，昏愤，鼻鼾痰呜，偏瘫，以右侧多见，颈强身热，躁扰不宁，呕吐，鼻衄，偶见呕血，脉弦坚，舌苔黄，尿色深黄。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3.3寒热两性：即希拉乌素型10例，主症：偏瘫，口舌歪斜，偏身麻木，手足肿胀，四肢逆冷，关节酸痛，脉虚滑，舌苔薄白，尿淡黄味臭。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4病情分级：重度：病例肌力0～l级，有意识障碍者；中度：肌力2～3级；轻度：肌力4级。150例中属重度53例(34.9％)，中度62例(40.9％)，轻度33例(24.2％)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 治疗方法：治疗组：均用口服蒙成药萨乌日勒(由本院制剂室制)15粒(3g)，每日 2次口服，根据病情分型特点用苏木汤、嘎饮汤(均包含在萨乌日勒组成内)调理。昏迷，吞咽困难患者予以鼻饲。28天为1疗程。根据病情可间隔3～4天后继续进行若干疗程。其中用药1个疗程25例，2个疗程107例，3个疗程11例，4个疗程7例。对照组：以复方丹参片(由广州花城制药厂生产)每次3片，日 \r\n      3次口服，28天为1疗程，视病程间隔4～7天后继续进行若干疗程。其中用药1个疗程4例，2个疗程12例，3个疗程18例，4个疗程17例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.6疗效标准：基本治愈：肌力提高2级以上，神经系统体征基本消失，达到生活自理并能参加一般性工作；显效：肌力提高1～2级，生活基本自理；无效：肌力提高不足l级，生活不能自理。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.7 治疗结果：显效36例，基本治愈87例。有效23例，无效4例。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp; 2．讨 论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医萨病又称为嘎日格病，中医称为中风或脑卒中，类似西医的脑血管病。缺血性中风即缺血性脑血管病(脑梗塞)。本病不仅是危害人类健康的三大疾病之一，而且老年多发病。缺学性中风的发病率与出血性中风相比高66％。对该病目前还没有特殊治疗方法。我们根据蒙医萨病理论，总结多年临床经验研制蒙成药萨乌日勒(治瘫丸)对老年缺血性中风进行立题研究，对萨乌日勒的作用机制，我们初步探讨如下：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 改善中风病人血液流变学变化。我们对60例缺血性中风病人的治疗前后血液流变学的对比观察，本组病人在治疗前红细胞电泳时间延长，表示了细胞表面电荷减少，而容易凝集，全血粘度和红细胞压积增加，血流性差，说明了趋于粘滞，经萨乌日勒治疗后，病人不仅神志、语言和肌力有明显改善，血液流变学测定也表示了全血粘度下降，红细胞电泳时间加快和红细胞压积降底，说明了萨乌日勒有利于血液流动，有利于红细胞在血浆中处于分散状态，防止聚集凝结，有助于减轻或消除血栓形成，从而增加脑血流量，改善脑血流情况，起到治疗作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2根据蒙医学理论，萨病属于白脉病范畴，认为气血淤滞导致黑脉(血脉)阻塞而使白脉之海的脑失去功能是本病的主要原因之一。萨乌日勒成分中的红花、草乌、诃子和黑云香等具有活血化瘀，疏通脉道，调解气血运行功效；该方药中石决明剂量较大，蒙医认为石决明与珍珠功能相似，具有安神益脑，扶正固本作用，因而取得疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (650, 1, 6, '蒙医药学对肿瘤的认识', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药学对肿瘤的认识<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医院&nbsp;赛西娅&nbsp;乌力吉・特古斯&nbsp;那顺达来<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学在其发展过程中积累了宝贵而丰富的医疗经验，不断地完善着蒙医药理论。蒙医对肿瘤的认识可以追溯到公元8世纪之前。最早见于《月王药帝》记载的“伯特格”病。伯特格为蒙古语。其汉语译文为“肿块、肿物、痞块、圪瘩”等广泛的词义。最初把“伯特格’病译为汉浯时称为“痞病”。蒙医把人体任何部位出现的肿块都称作“伯特格病”，因此现代医学的肿瘤病，蒙医也认为是“伯特格病”的范围，临床治疗时，采用“伯特格病’辨治方法治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医认为“伯特格病”是因长期的精神抑郁，或饮食不节．或过嗜烟酒，或受外伤损害，致命人体内精津运化失常，凝积成邪，使赫依失去平和而变热，热邪不断反复地薰蒸精律，使其浑浊，与瘀血互凝，逐渐形成痞块。这一类痞块可以发生在人体任何部位，如肺、肝、脾、子宫、肠等处。称之为“某血痞”，肿瘤发生在肝，称为“肝血痞”，发生在肠称之为“肠血痞”。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;另有一类痞病，如“食痞” ”石痞” “水痞” “虫痞’ “脓肿痞”，其病因是因食积石化，误食动物体毛等病因所致的痞块，或形成结石，或水邪停积。临床上多属胃结石，胆结石，泌尿系结石，囊虫病，脓肿。这些痞病不属肿瘤病的范畴，故在此不作讨论。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;19世纪末，在《蒙医药选编》一书中，论有“纳里病”。蒙语的“纳里”，汉语译为“狭窄”。对“纳里病”的记载，其临床症状和疾病发展过程，相同于现代医学的食道癌和胃癌。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肝癌(肝血痞)蒙医认为本病因长期情绪抑郁不畅，或因精神创伤，或因饮食不节，或长期嗜好烟酒，或因外伤损害，使人体内的精律不能正常的运行，精津不化成邪，瘀积在肝，体内三邪失调肝希拉三热迁延不去，使肝血运失常削成瘀，精津瘀血与热邪相凝，使赫依失其平行变热，不断地熏蒸凝聚了的津精瘀血，使其浑浊，陈恶，逐渐变硬。在肝内逐渐形成痞块，热愈深，毒愈盛，痞块愈加增大变硬，胆腑受热毒所迫，胆汁外溢，而且发黄。临床症状：肝血痞(肝癌)患者初起食欲不佳，上腹胀满、乏力、右上腹部灼痛。病情加重，疼痛剧烈难忍，迅速消瘦，发热，乏力．时有恶心，食欲减退。肝部肿瘤不断增大，疼痛更甚，肝区可触到肿大的硬块，有时出现黄疸，黄色晦暗，或出现水膨，沁便黄赤，大便难，舌红黄苔腻，脉细数。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：清希拉和血热，解肝内热毒，破肝内血痞，继服泻剂，以祛病之余邪。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：清除血热服查干榜嘎十八味散(查干榜嘎30g、木鳖子、贝齿灰各18g、巴沙嘎、牛黄、黄柏皮、地丁、五灵脂、香青兰、黑冰片、肉豆蔻各15g，丁香、草乌叶各9g，连翘6g,熊胆1g \r\n      (共研细末）。及八味绿松石散(绿松石15g，梅花片6g，白檀香12g、公丁香12g、红花12g ,营实12g、熊胆3g、寸香0.15g，研细末，制成散剂）交替用开水送服。亦可用红花十三味散。(番红花 \r\n      10g、丁香、牛黄、麝香、犀角、银珠、紫檀香、大托叶云实、查干榜嘎、诃子、川楝子、栀子、木香各5g)。燥化肝血痞服刺玫果子、五灵脂各30g，白檀，红花各3g共研细末，浸泡于120g白酒中，经3――7日成酵后，再加冰糖60g搅合制膏剂，每日二次，每次服一汤匙。继服除痞八味攻泻剂<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：服药期间可针刺肘内外两脉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;脾癌(脾血痞)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;脾癌(脾血痞)是恶血激增，遂渐扩散，瘀积于脾脏在借赫依之作用而形成脾脏处的血痞。本病是主要以巴达干热象为特征的―种热性脾病。<br>\r\n      病因：脾脏在五元中虽属水土二元和具巴达干性，但在造血及其运行方面与肝脏有密切关系，故精华不消，恶血激增可引发脾血痞。<br>\r\n      临床症状：食欲不振，腹胀肠鸣，消化不良概似肝脏血痞症，同时左上腹部灼痛，伴脊椎第十一节疼痛以手触摸可见肿大且有压痛，病热加剧则由腹部左侧开始积水而而腹部膨大；颜面发青，唇结白，皮破裂或溃烂，脉象虽沉而底为紧，舌青黑而有灰白苔。如不及时治疗，病热逐步加重，或可转变为脾痞渗漏型水膨症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：健脾消食，清饿血热破痞块为原则。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：诃子七味散(诃子5g，丁香10g，波棱瓜子15g，缬草20g，木棉花蕊25g，荜拨30g，草果35g，配合组成)草果十九味散(草果，天竹黄或石灰华各25g，瞿麦，花锚，紫草茸，木香，木鳖子(制)，诃子，水柏枝，紫草，查干榜嘎，菥萤，缬草，栀子，大托叶云实各5g，红花4.5g，木棉花萼，豆蔻各10g，五灵脂15g配合组成)或红花七味散(天竹黄50g，红花，牛黄各40g，木鳖子(制) \r\n      15g，丁香，诃子，荜拨各10g配制组成)交替用开水送服。待血热清除后，可按肝血痞治疗法施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：脾血痞如呈血热征象偏盛，可取脾脉或无名指背侧脉微量放血。如巴达干，血偏盛可取脊椎第十―节之三族缘穴艾炙或火针。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;胃肿瘤(胃血痞)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本病是由于血与黄水增盛，瘀积凝结于胃而形成胃血痞。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：恶血与黄水增溢于胃，由巴达干，赫依之作用而凝结，致胃功能紊乱所致。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床症状：患病部位瘀积坚实，痛时按之有滚动感以外有发热，刺痛，体力衰弱，食欲不振等症状。尿呈红色，脉细速，如痞块形成迅速，发展快者属凶险难治者(即与恶性肿瘤相似)发展缓慢而日久溃破者较为易治。(即与良性肿瘤相似)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;伴随症状有：消化不良，虚逆暖气，吐酸水，吐少量苦水及食物，胃胀肠鸣，上腹部胀闷作痛等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：先医渗漏出血，以调理三根，清热邪为主，并结合具体病情进行治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：寒水石六味散(寒水石[寒法或营养法炮制] 50g，川木香，土木香各25g，红花40g，豆蔻，荜拨各15g \r\n      配合组成)加止血红花八味散(红花，熊胆各50g，豌豆花40g，紫檀香，地锦草，射干各35g，木鳖子，银朱各25g 配合组成)或芫妥五昧散(芫荽，青木香，酸梨干，沙棘。石榴各15g \r\n      配合组成)。还可投寒水石二十一位散(寒水石(制)，石榴，沙棘，五灵脂荆络50g，柿子或酸梨干，紫檀香，木香各40g，查干榜嘎，栀子，诃子各35g，豆蔻，荜拨，木鳖子(制)，牛黄，当药或扁蕾各25g，芫荽干，绿绒蒿，瞿麦各 \r\n      20g，止泻子或连翅，香青兰，土木香各15g 配合组成）等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;大肠癌(大肠血痞)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;依据蒙医理论，大肠血痞往往由大肠包如病转化而来。即包如热邪久积于大肠而引起的―种聚合性慢性病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：在肝脏未运化之精华所产生的恶血流往大肠而滞留于某―薄弱处．再与赫依相混，进而由赫依，血热致使大肠受损所致。故其性质属聚合性慢性病。本病大多为大肠扩散热型，当病热加重，则血热元盛而转化为大肠渗出型包如病，进而在此基础上久聚凝结成为大肠血痞。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床症状：发病初期，除出现类似大肠赫依病之症状外，下腹右侧及脊椎第十六节周围作痛。在大肠部位接诊，病患处有压痛。病变初期疼痛较轻，随着疾病的发展疼痛逐渐加重。大便燥结呈黑色或带血粘液，下气闭阻如通气则臭为较大，腹痛 \r\n      腹痛稍安。如果血热亢盛而转化为渗出型包如，则大肠内有微量血渗出阵发性剧烈疼痛，低烧，大便带少量黑褐色血液。如病热进一步加重则出血量亦增多，脸色苍白，皮肤干枯，肌肉消瘦等。脉象粗芤而底紧，口干，舌呈淡红色，苔少，尿赤黄。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：首先以调理三根，清除包如病以及赫依，血热邪为原则，并针对病情及变化特点进行治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：查干汤3g(土木香，苦参各50g，珍珠干25g，山奈 12.5g配合组成)加芒硝，胡连各1g服用。大肠赫依石榴十三味丸2g \r\n      [石榴，豆蔻各15g，肉桂，白占月，黑巨子，白巨子，光明盐各 5g，干姜，荜拨，红花，诃子，草果各l0g，红盐lg配合组成)加五风丸lg(诃子60g，菖蒲45g，草乌（制）30g，木香15g，麝香7.5g配合组成)开水送服，可祛大肠赫依与血热邪。若热邪症状较甚者，可取清胃热散2g加七雄丸1g \r\n      (草乌芽，诃子各25g，多叶棘豆，茜草，黑云香各10g，银朱5g，麝香6`5 配合组成）服用。如寒症甚者可投光明盐五味汤(光明盐15g，诃子15g，干姜13g，荜拨6g \r\n      等制成)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：脊椎第十三，十五．十六节施以热针或艾炙等外治法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;子宫瘤（子宫血痞）<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;血液凝阻宫中积久而成之硬化物，谓之“血痞”又称“硬化痞”、“血病硬化”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：产后恶露不下，或经水闭阻，与赫依邪交结，积聚日久而成。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：小腹坚硬阵痛，腰骶疼痛如裂，颜面烧热感，怒，经闭，有时遭受人外因引起痞破．会出现骤然大出血，应与胎堕之大出血鉴别。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：活血化瘀，破痞祛瘀，调补气。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：选用七味枸杞子散(枸杞子掏17.5g，沙棘，广木香，山奈，肉桂，硼砂(制)，朴硝各25g配合组成)，四味沙棘散(沙棘，豆蔻，草果，河子)分别加蛇肉，光明盐，芒硝，以破痞，为防邪滞子宫，可酌情估用安坤丸(棕炭，艾炭，金血炭，蔚炭，姜各 \r\n      30g，汉三七15g，熊胆7g，蔗糖为丸)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;子宫肌瘤(子宫肉痞)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本病是发生于子宫的一种肉痞，又称“肥厚痞’与现代医学之“子宫肌瘤’相似。多见于中年妇女，<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：对本病的发病原因尚未明确。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床症状：经血淋漓，非时而行，白带量多，腰痛如裂，随着痞块日惭增大，而出现少量流血不止，小腹束缚样胀痛感，小便艰涩，腰骶允沉身重。腹部触诊可得子宫有程序不一的痞块。病初症状不明显，迄至患者因不生育，月经不调或腹部不同程度膨大而进行检查时，始发现痞块已增大。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：根据病情采用破痞或进行手术切除。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：初期或痞块较小时，投以十一味沙棘散(肉豆蔻，豆蔻，草果，甘草，沙棘，诃子，铁衣，天竹黄，木香，栀子，丁香)四味沙棘散加贝齿灰，硼砂内服以破痞。若痞块已增大，可施手术切除。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;子宫腺痞<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;宫颈部位生长腺体般鼓包者，称为“腺痞”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：由于产后，月经期饮食起居不当，嗜啖过寒饮食或长期受寒凉刺激，病邢客于子宫所制。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：病热轻浅时，少自觉症状，仅见白带量多，迄至病热较重，则出现浑身困倦无力，腰围附痛，尿闭，腰腹常有冷飕感，白带量比初期时更多，且每与血丝夹杂而下，或诸色带俱下，宫颈患处肌肉失去正常色泽，长出腺状彭包，新肉不得生，并化脓，触之有血液流出。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：化痞，生肌，祛除宫山巴达子寒邪，采取内外兼施法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：长期内服蔻补益散(五根，三子，羊睾丸，雪蛤蟆干姜等配合组成)，破痞直投四味沙棘散加贝齿灰，硼砂内服。外撒嘎木朱尔粉末，一般轻症撤3―5次，重者撒7一l0次即可痊愈。病热恶化而转化为其他病症者，可施手术切除。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;子宫浊汁痞<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;邪留子宫日久产生一种以流浊为主症的疾患，谓之“浊汁痞”或简称“浊汁”。。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：产后胞衣残留，败血下之不尽，停阻宫中，积之日久化为浊汁，淋漓而下所致。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：子宫增大肿胀，领近组织坚硬阵痛黄水样浊液流出，<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：泻病毒，放血治疗，防治邪留。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：坤儿(棕炭，艾炭，血全炭，茺蔚炭，姜炭各30g，汉三七15g，熊胆7g 蔗糖为丸)，六昧化瘀散(火硝，干姜，荜拔，古月，阿，光明盐各10g配合组成)。亦可服十一味沙棘散，四味沙棘加贝灰以破痞。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;黑色毒痞<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;胞宫内及宫颈处长出一种毒痞，称为“黑色毒痞’。本病多发生于产后，多见于青年妇女。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：由于产后恶露等败物余毒停蓄胞宫不去，临产时产遭受伤或患腺痞治不及时等所致。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：初期无明显症状，仅见白带量多，并与血夹杂而下，过度用力或直接触措常导致出血，久之则可化脓。若病势恶化，则白带量更多，各色俱下，其所下物如稠奶渣，奇臭难闻；痞处长疮，流脓血物，肌肉麻木，关节肿胀疼痛，迄至病热更进一步恶化，阴道中不断流出大量棕黑色腥臭血水时，生命己濒临险期。除上述症状还可伴有体虚，颜面苍白，神萎，消瘦，最后亦有体衰难支而卧床不起者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：根据病热轻重及体擀具体情况采用化痞，祛痞法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：选用四味沙棘散加贝齿灰破痞，然后长期服用十―味寒水石散，十八味水银散。(水银(制)，草乌(制)，文冠木浸膏，诃子各50g，硫黄(制)造45g，天竹黄或石灰华，黑云香各10g，红花，丁香各15g，白云香，豆蔻各25g，麝香5g配合组成)。护理方因注意休息，食易消化有营养之炊食。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;外阴糜肿<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;以阴道口及尿道相领部位红肿成疮疼痛为特征的―种外阴疾病，称为“外阴糜肿”。系妇科杂证之一，临床多见。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：本病发病原因，需进―步探讨。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：分为急性和慢性两类。急性者表现血热为迫，外阴赤肿，成疮溃烂，刺痛或灼痛。慢性者，由于下身寒盛，巴达干降于阴道所致，外阴部肿胀发硬，似无痛感，进而糜烂，常湿漉漉，并渗出黄水，瘙痒甚。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：急性者宜清热，杀粘，燥黄水。慢性者宜怯巴达干燥黄水。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：急性者内服五风丸(诃子60g，菖蒲45g，草乌(制) 30g，木香15g，麝香7.5g配合组成)及配有文冠木之成药。外涂嘎木先尔粉。慢性者内服补益还阳丸(又名纳仁满都拉。由石榴50g，肉挂5g，豆蔻25g，荜拨，黄精各20g，红花，冬葵果，玉竹，天门<br>\r\n      冬，自，白蒺藜各15g，配合组成)。三味天仙散(荆芥，天南星，独活)以三味文冠木汤(文冠木，秦艽待疮热有好转，可施行温热疗)以驱下身之寒。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;水泡痞 <br>\r\n      &nbsp;&nbsp;&nbsp; 生长于子宫的水泡样坚硬泡块群，谓之“水泡痞”。此与现代医学所载之“葡萄胎;近似。本病危割性很大，临床较常见。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：本病由于男子精液与女子经血相合过程中受到损害而影响生殖器的正常功能而引起本病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：阴疲乏中有淋漓不断地有不等量之血液或黄水流出，宫体迅速增大，逐渐出现小腹胀痛，腰痛，食欲不振，呕吐，亦有个别仅出现停经而无明显的特殊症状，患痞之腹比妊腹增大块，之痞块上下波动，柔软，听不到胎心音，亦无胎动征。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：破痞，逐瘀，补体，同时辩明寒热,分别施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：投以十一味沙棘散(肉豆蔻，豆蔻，草果，甘草，沙棘，诃子铁衣，天竹黄，木香，栀子，丁香)，以及豆蔻补益散(五根，三子，羊睾丸，雪蝓蟆，干姜等配合组成)等剂。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：热盛者，取肘外脉配合针刺放血；寒赫依盛者，油炒食盐热罨小腹，腰部。在此期间注意饮食起居调理，忌用过热，过寒饮食．宜食温和，富营养，易消化之品，适当，休息病痊愈前严禁房事。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;脓肿痞<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本病系指人体某―部位形成之脓包而言。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;病因：主要由粘虫所致或因增盛热，伤热等势病之余邪，滞留于某―部位，迁延日久则恶血与黄水激增聚积该部凝结而形成。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床表现：脓痞多发斗于肺，肝，脾等脏腑和皮肉之间及肌肉深部。因而临床表现呈现该部位的病变征象。本病属热性，但依当时季节，病者体质特性，年龄等因素，亦可合并赫依或出现以血与黄水为主的聚合病状。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肺脓痞多因肺热症的治疗不彻底转化而发。主要表现为高烧，咳嗽，无痰或微量泡沫粘痰，有时咯粘性脓痰。病灶扩展则胸部刺痛，<br>\r\n      呼吸急促，脊椎第四，五节附近有压痛。一般不能睡矮枕，并伴肌肉消瘦，颜面及下肢浮肿，脉象，舌苔，小便等均呈血热征象。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肝脾脓痞的临床症状与肝脾血痞略同，但病热多较重。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;皮肉间或肌肉深层之脓痞，局部压痛剧裂，恶寒，高烧，痞部周围红肿，接之柔软而波动。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：肺脓痞宜采取清血热，化痞破积，或催吐，攻下，最后须用除脓毒余邪等方法。肝睥脓痞宜采用清除血热,燥化肝脾脓痞的原则。皮肉间或肌肉深层之脓痞宜采用化痞清热，攻泻病毒，燥使用黄水为治则。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：肺脓痞宜投等分八味汤开水送服，亦可取沙棘，川楝子，广木香，甘草，荜拨，面碱等分头研细末，蜂蜜搅合内服，可奏化痞缓敛之效。其次，再取发芽期采集之大黄肾橐吾，大小蓟等分研末，在上述沙棘为首六方中每次加1―2g，空腹时开水送服,则可吐出脓毒。最后，如病者体力尚佳可用犀角，番红花，丁香，牛黄各 \r\n      1.5g，天竹黄4.5g，肉豆蔻，草果，白苣子，豆蔻，木棉花蕊，木棉花萼各2.5g，广木香，蓝盆花，紫檀香各3g，白檀香，决明子，苟麻子，栀子各4g，羚羊角，川楝子5.5g，石竹lg，拳参5g，诃子6g,，沙棘6.5g，鹿茸1.5g等配合成散剂，引用之红汤(枇把30g，茜草30g，紫胶30g)送服。身体虚弱者引用牛乳送服，可除脓痞余邪。<br>\r\n      肝脾脓痞宜投查干榜嘎30g，木鳖子，贝齿灰各18g，巴沙嘎，牛黄，黄柏皮，地丁，五灵脂,香青兰，黑冰片，肉豆蔻各15g，丁香，草乌叶中9g，连翅6g，熊胆1g共研细末制成散，及绿松石 \r\n      15g，梅花片6g，白檀香12g，公丁香12g，红花12g，营实12g，熊胆3g，寸香0.15g研成细末制散剂交替用开水送服。诃子5g，丁香 10g，波棱瓜子15g，缬草20g，木棉花蕊25g,荜拨30g，草果35g配成七味诃子散与草果，天竹黄或石灰华各25g，瞿麦，花锚，紫草茸，木香，木鳖于(制)，诃子，水柏枝，紫草，查干榜嘎，菥萤，缬草栀子，大托叶云实各5g，红花4.5g，木棉花萼，豆蔻各10g，五灵脂15g配合组成草果十九散，交替用开水送服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;皮肉间或肌肉深层之脓痞初期局部可施热敷同时多服三子汤(栀子30g，诃子25g，川楝子15g)，三红汤(枇杷30g，茜草30g，紫胶 \r\n      30g)。以促使热化。再取希拉泻剂(豆砂18g，豆霜9g，大黄6g，灵脂6g，牛羊脑浆为丸)可清黄水热邪及粘毒，待脓痞成熟后，可穿刺，切开排脓后取文冠木，诃子，川楝子，栀子，铁悄，玉竹，黄精，天冬茉莉，蒺藜，白云香，决明子，苟麻子，苦参，荜拨，黄柏，草乌(制)，麝香，木香，水菖蒲，石决明，秦艽花，翠雀花，野攻瑰花 \r\n      各等分配合组成文冠木二十四维味散开水送服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：如上身脓痞，可取肘外脉或肘内脉放血。如系下身脓痞，可取踝脉或腓肠肌梢脉放血。饮食起居方面，居处通气不，过于温热,经常强力劳作过度疲惫，食用锐而热性饮食等，均需禁忌。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;消化系癌(纳里病)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一：概述<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;l、名称注释：“纳里”蒙语，其意为“狭窄”故“纳里病”即“狭窄病”。别名有“咽部阻塞症” “咽部阻塞巴达干” \r\n      “铁垢巴达干”等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;分类：按病变部位分食管纳里病和胃纳里病。按病变性质分为赫依并型，温热合并型，寒凉偏盛型等三种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;发病机理：蒙医学认为长期的情志忧郁，久饮苦涩，浓茶，酗酒和吸烟均为本病发病之一重要因素，19世纪末著成的《蒙医药选编》一书中，对纳里病灶的形成作了如下记述： \r\n      “食道阻塞不通畅的原因是食道中赘生疣状物所致。胃腑阻滞不畅者，系在贲门处赘生疣状物所致。胃腑阻滞不畅者，系在贲门处赘生疣状物所致----此特情况，如果病程迁延日久，则有饮食点滴还进而导致死亡之虞-----主要发病于春秋之季，如不及时加以治疗，速则七个月，迟则八个月多趋死亡。------幼婴儿及壮年人罹患该病者寥若晨星”。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;食道癌(食管纳里病)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床症状:患病初期无明显症状，只是偶而在吞咽较硬食物时，在胸骨后侧有部位不显著的不适感或噎膈感，迁延日久，症状逐渐加重，则出现噎膈，呃逆及暖气不畅等现象。与此同时，在食道上，中，下某一段病灶形成的局部指征愈加明显，时作噎塞。特别是吞咽较硬食物出现的咽下困难为该病万为突出的特征。当噎塞不畅的食物尚可吞咽下去后，病人自感稍舒。随着症状的加重，较硬之食物完全不能入咽，流食亦只勉强咽下。病者口流清涎，时而干哕，或呕吐粘液性泡沫样物及进食的食物。患者迅速消瘦，元气衰惫，颜面及手呈灰白色出现现代食道痛之晚期症状。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;分型症状：巴达干，赫依偏盛型则脉象驰缓芤，舌苔白腻。合并热型则脉象快而滑，舌苔微黄。合并赫依型者则舌苔干燥，略红，头晕，梦多，耳鸣等。大便秘结，小便多呈浅黄色。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;好发部位：食管上段之咽喉后壁或食管中段之“黑白际"穴位处，或食管下段之剑哇突部等处。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：在补益胃火，祛除巴达干前提下，审视病情，施以辩证调治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：投查干汤(青木香30g，苦参20g，珍珠干20g，山奈 14g)及寒水石十一味散(寒水石30g，全石榴98g，豆呐9g，必9g，诃子9g，青木香9g，栀子花9g，沙棘6g，木香6g，五灵脂6g配成散)，热邢偏盛则可用黄精玉竹，天冬，白芨，蒺藜各9g制成散加胡连，旋复花，黑芸香，石菖蒲，广木香，千年见，与上述五味药之总量相等加以调配，每次30g用白开水送服。到用诃子，硼砂，荜拨，查干榜嘎，光明盐各50g，寒水石500g，硫黄5g配合组成的散剂。巴达干，赫依偏盛者可选用寒水石(制)石榴，沙棘，五灵脂各 \r\n      50g，柿子，紫檀香，木香各40g，查干榜嘎，栀子，诃子各35g，豆蔻，荜拨，木鳖子，牛黄，当药，芫荽干，绿绒蒿瞿麦各20g，连翅，香青兰，土木香各15g配合而制的散剂加沉香25g，广枣30g，诃子，肉豆蔻，木香各20g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;胃癌(胃纳里病)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;临床症状：初期因无明显症状，故难与其他胃病相鉴别。又因本病发病徐缓，―般不易发觉肿瘤究系何时开始生长。体重减轻，而色苍白，身体困乏，食欲不振，厌油逆及肉食等现象，系该病早期出现之症状。伴随肿瘤生长．生长之部位不同，其临床症状亦有不同。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;分类：(1)按病变部位分：贲门纳里病、幽门纳里病、胃体纳里病(2)合并物质分为：合并赫依，热邪、寒凉三种。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;分类症状：贲门纳里则出现吞咽之食物，阳滞在心窝部位而不下。幽门纳里则出现胃部痞满并有下坠 感，暖腐吞酸，恶心,时而胃胀呕吐食物之残渣或血。胃体纳里病则因症状不明显而往往延误就医时间。此病的肿块大多可以从腹壁触模而得，多位于腹部，可模到凹凸不平较硬之物，初则伴随呼吸和体位之变化而移动或推之亦动。后因与其它器官粘连而固定不动。上述这几种其脉象，舌苔，尿象等基本上与食道纳里病相同。<br>\r\n      本病迄至晚期，患者之营养陷于极度不良，显著贫血，皮肉枯萎苍白，元气极度虚弱。若合并热邪则拌有头痛，胃部烧热感，口干等症状。合并寒凉则饮食起居皆不耐寒．病热愈益加重．鼻涕，口涎及吐物均呈胶粘而多。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗原则：调补胃火，祛除巴达干的前提下根据病情对症下药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物治疗：诃子，硼砂．荜拨，查干榜嘎，光明盐各50g，寒水石500g，硫黄5配合组成的散剂为主，以光明盐25g)蛇床子50g，荜拨100g，干姜125g，诃子灰375g，芹叶铁线连制成的引子送服。用寒水石烧灰，石灰等不同灰剂开水调和内服。均能引吐，若合并赫依则投沉香，肉豆蔻，广枣，木香，旋复花，檀香等分研制成汤煎服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;辅助治疗：采用火炙，炙其巴达于总穴，胃前九穴脊椎第十二节等穴位。饮食宜温水忌襄，禁用重腻，锐性及阵腐变味之食物。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;综上所述：蒙医学对肿瘤有独特的理论和治疗方法。长期以来，为保障人类的健康作出了重要的贡献。但应该指出的是，蒙医学对肿瘤的认识远不止于这些，还有许多需要挖掘继承和研究之处，这是今后我们基础，文献和临床研究之重点。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (651, 1, 6, '蒙医治疗过敏性紫癜108例临床观察', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医治疗过敏性紫癜108例临床观察<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古呼和浩特市中蒙医医院 \r\n      &nbsp;&nbsp;巴特尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古呼和浩特市第一医院 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;乌兰<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;过敏性紫癜是较为常见的疾病。治疗难度大，尤其转为肾炎后治疗难度更大。应用蒙药紫癜灵(紫癜灵1995年获第3届全国科技人才技术交流会金奖)治疗紫癜类患者108例取得了较为满意的效果。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．临床资料<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;为了便于观察疗效，我们将108例患者分做两组讨论。第1组：皮肤紫癜，腹型紫癜、风湿型紫癜三者及其复合型共99例。第2组：过敏紫癜性肾炎30例。其中跨组病例21人，分型依据参考“简明症状鉴别诊断学”。<br>\r\n      第l组99例患者，绝大多数来自呼市及其周围地区。其中，女性58例，男性41例；1―5岁的9例，6―10岁者42例，11―15岁 28例，16―20岁7例，21岁以上者13例；单纯皮肤紫癜者28例，占28.3％；皮肤紫癜伴消化道症状(腹型)者18例，约占18.2％；皮肤紫癜伴关节症状(风湿型)者12例，约占 \r\n      12.1％；皮肤紫癜伴肾炎(肾型)者19例，约占19.2％；复合型(皮肤紫癜伴胃肠症状、关节症状及肾赃受累)者22例，约占22.2％。<br>\r\n      第2组30例过敏紫癜性肾炎患者中男性12例，女性18例。肾炎伴单纯皮肤紫癜者 10例，占33.3％；单纯肾炎9例，占30.0％；肾炎伴皮肤紫癜及胃肠道症状者3例，占 \r\n      10％；肾炎伴皮肤紫癜及关节症状者3例，占 lo％；肾炎伴皮肤紫癜，胃肠症状及关节症状者5例，占16.7％。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．治疗方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在治疗中我们以验方“紫癜灵”(主要由栀子、瞿麦、苏木、地锦草、人工牛黄、灵脂、木通等19味组成散剂)为主，日2次，每次3g口服。根据不同发病类型及临床表现分别施治。如：出现皮肤紫癜时，再配“额尔和穆一8味散”等清热药。出现关节红肿、活动受限时以主方加用“嘎日迪一15味丸”，疼痛严重时用“泵哈拉嘎一25味丸”等。出现腹痛，呕吐，便血时加服“如达一6味散”及“寒水石一6味散”等解疼，止吐等助消化药。出现腰痛、血尿、蛋白尿、红白血球时配用“萨丽冲阿丸”或“冲一27味丸”等。服蒙药期间停服一切其它中西药物。治疗过敏性紫癜6周为1个疗程，过敏紫癜性肾炎3个月为1个疗程。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．疗效标准及治疗结果<br>\r\n      过敏性紫癜疗效分为治愈、显效、好转及无效四种。皮肤紫癜及其它临床症状消失为治愈；皮肤紫癜及其它临床症状基本消失为显效；服药后复发次数及皮诊明显减少者(减少约2／3以上)为好转；无明显好转者为无效。该组99例患者中治愈94例，占94.9％；显效3例，约占3.0％；好转2例，约占2.0% \r\n      。总有效率为100%。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (652, 1, 7, '安代舞疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> &nbsp;&nbsp;&nbsp;&nbsp;安代是流传在科尔沁草原上的蒙古族民间歌舞，是蒙古族民间艺术宝库中的一朵光彩夺目的奇花异葩。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;有关“安代”的起源，发祥地及形成年代，由于缺乏史料可考，历来研究这方面问题的同志，不得不向民间传说中去探求。在辽阔的科尔沁草原，特别是被誉为“安代之乡”的哲里木盟库伦旗，确实流传着大量有关“安代”的民间传说，为我们研究“安代”的起源提供了许多珍贵的资料。有关“安代”的起源有许多传说，我们在诸多传说中摘录了几种以供大家参阅。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;据说在很久以前，东北方有一个很“古老”的地方，这个地方的国王是哈日苏大可汗。哈日苏汗有个美丽的皇后，名叫罕格哈拉。罕格哈拉皇后又有三个公主，老大叫扎兰都贵，老二叫乌森海洛尔，最小的老三叫额勒格西拉。这三位公主可非同一般，她们是使人闻风丧胆的白鸠！这三个鸠公主个个神通广大，魔法无边，经常展动她们的神翼遨游世界。当她们在天空遨游的时候，地面上便掠过她们的影子。一旦她们的影子照在地面上的姑娘，媳妇身上，一种可怕的病症便在姑娘、息妇身上发作了。这种病谁也叫不出名来，吃药不见好，喇嘛治不了，病人只能在痛苦中煎熬。恐怖的阴影吓得人们惶惶不可终日。大姑娘、小媳妇成天躲在屋里不敢外出，生恐被三个鸠大慈大悲的释迦牟尼佛祖，为了拯救人间这种苦难，他变成了巴布伦师傅，向聪明的歌手嘎达苏和“博”苏海传授医治这种病的方法：由“博”苏海来主持治病。方法是让病人打着鼓和镲坐着，由“博”苏海和歌手嘎达苏围着病人唱歌。接着是古老的唱"安代"程序，所以，唱“安代”有人叫“唱鸠”，盖来源于此。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (653, 1, 7, '拔罐穿刺法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;拔罐穿刺法 \r\n      本法是拔罐与放血相结合的外治新法。先在所选穴位或某一部位进行拔罐，取罐后在隆起部位用三棱针或皮肤针浅刺几针，再行拔罐，吸出恶血与黄水，以达到改善气血运行，治疗疾病的目的。多选择肌肉丰满、富有弹性、没有毛发和骨骼凹突的部位。其特点是奏效快、疗程短、简便易行，病人无痛苦，没有危险性，根据临床验证，在肺大叶穴或肺小叶穴用此法施治，对肺痼疾、咳喘病有显著疗效；在第一胸椎或胸椎棘突肩肋骨内上缘之间进行拔罐穿刺，对白脉黄水病引起的肩背拘急、颈项强痛、肘臂麻木等，有显著疗效。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (654, 1, 7, '穿刺疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;穿刺法，又称火针疗法。即用金针或针刺入人体的特定穴位给以刺激；或通过加温或冷却针体传导温热或冰冷，以增强刺激；或用特制的穿刺器械，穿破皮肌，排除瘀滞于局部的病气、黄水及脓液等，以达到治疗目的的一种外治法。<br>\r\n      穿刺疗法之治疗范围很广，见效又快，器械简便，费用少而便于携带，治疗期很短，治愈率高，多根治，少复发，深受群众欢迎，为历史悠久而颇具信誉的一种传统疗法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[适应症]：凡是赫依性疾病、巴达干性寒症、消化病、痞块、水肿、瘀积病、脓肠、黄水病及用其它疗法久治无效的疾病，皆可用穿刺法施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[禁忌症]：肝痞、脾痞渗漏引起的热性水肿，热痞扩张和心等脏腑热性疾病余邪未除及外伤等疾病，由于血和希拉引起的一切热性疾病，肌肉、骨骼、脉络、脂肪等之要害部位，老年人、婴幼儿以及病危者等，都禁忌穿刺施治。<br>\r\n      &nbsp;&nbsp;&nbsp; [具体方法]：具体刺法有直刺、横刺、下刺、上刺、十字形外翻、避开脏腑和要害部位等八种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;直刺：针身与皮肤成90度的直角，针尖垂直而下，适用于肌肉丰满部位的穴位和脊椎关节、下腹部等处的穴位。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;横刺：将针尖横向刺入，针身与皮肤成15度角，适用于头部穴位和肝、脾、肾等主要脏腑的穴位。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;下刺：针尖须向下刺入，适用于喉部穴位、心肺穴及剑突部穴位。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;上刺：针尖向上刺入，适用于火衰穴，局部穿刺排脓。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;十字形刺法：针尖刺入后向四方旋转，适用于胃中部痞块及寻找脓液。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;外翻：针尖刺入后向外翻，由一穴位向另一穴位穿透，适用于胃犏牛眼穴和大腿下方穴位。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;避开脏腑：在胸部或腹部穿刺时，必须注意不伤及脏腑。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;避开要害部位：病在要害部位而进行穿刺施治时，要注意不伤害部位，如眼病、心脏积水、肝痞、肾痞及排脓等。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (655, 1, 7, '放血疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;放血疗法是在人体的表面的一定部位和穴位，用特制的器械，将浅部脉道(静脉)切开或穿破，放出病血(恶血)，以达到预防和治疗疾病的目的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本疗法以蒙医学基础理论为指导，通过放出脉道之病邪和恶血、 镇痛、止痛、消肿、根除脓液和黄水，治疮疡，促生肌，减肥，并使瘦弱者健壮，特别是对排出恶血和毒邪有奇效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;（一） 放血疗法之适应症及禁忌症<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;适应症：多适用于由血、希拉引起的热性疾病：如伤热扩散、骚热、疫热、痈疽、疖肿、疮疡、痛风、索日亚(包括结核)、丹毒、黄水病、麻风等热症。巴达干与赫依引起的疾病虽系寒症，但若与血和希拉合并时，亦可采用放血疗法。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;禁忌症：凡属正精耗竭，体质虚弱，孕妇、产后、浮肿、大痨痼疾、胃火衰败等寒性疾病。总之，由巴达干、赫依引起的疾病。儿童和老年人，用泻下法、催吐法、鼻药及灌肠法施治之后，亦禁用放血疗法。还有，易于出血者和低血压者，不能施以放血疗法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;（二）放血时机<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;根据病情的具体情况可分早期、中期、后期三个阶段施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;早期：即发病初期宜于放血者，如体腔出血侵及脏腑，流血过多而不止者，以及扩散热、骚热、食物中毒等，起病急骤，不循常规发展成熟，应掌握时机，在发病初期及时放学施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;中期：疾病中期，无刺痛，恶寒现象，身体沉重麻木时，可放血施治。或正血与病血充分分离，血液无混杂现象时宜于放血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;后期：凡血和希拉热邪或病血散步于脉道时，易于转化为其它疾病，因此，应用不当之饮食引发其余热，然后放血施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;（三） 术前准备<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;术前准备，又分远行与近行两个阶段。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;远行：在放血施治前，如热病尚未成熟时，先给予汤药，促其成熟后放血；分离正血与病血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;（四）正式放血<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;放血前之步骤：包括部位选穴、放血前之缩扎，即鼓脉、操作法、观察血色、放血量及辅助治疗等内容。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．从放血的穴位或脉向心三横指处结扎，结扎之缚带粗细要适宜。如在两小尖脉放血时，从放血的脉至另一侧腋下结扎；在头部血脉放血时，注意不至妨碍呼吸，在颈项处结扎；在舌脉放血时，用消毒之纱布缠裹竹夹板或木夹板夹住舌体上卷，使血管暴露。<br>\r\n      此外，在任何部位放血时，须注意不使皮肤摺皱，使脉管充分暴露。当拉扯绳子两端缠裹时，用力要均匀，松紧度要相等。如结扎结不善，则下刀难中，若皮肤摺皱，血管曲折，则血不能出。血管鼓张后，用手指揉擦，使肌肉作麻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．操作；首先用手指将血管按压固定：用力按捺，使脉管不易移动。进刀之部位，扎处的三横指以下。进刀的方法是：纵划法、复切法、横断法、斜切法、侧刺法等五种。但是，现今临床上主要用弹刺、点刺、叩刺、推刺、射刺、散刺等手法。放血疗法以泄出血气为宜，因此，切口宜稍大，过小则容易发生肿胀，血气出不来，最好脉管与皮肤切口要对称。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;（五）观察血色<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;放出血液，首先要审辨正血与病血。放出的血液出现紫黑色之花纹者，是正血与病血尚未分离之象，须立即止血饮汤药分离后再行放血。如血质稀薄，色黄，气味浓，有白色泡沫，上结粘液、脓汁样物者为病血。凡血色黑而粗糙，现红黄色袍沫着为赫依血，血色黄而稀，有浓沫着为希拉血。血色淡红，稠而柔滑着为巴达干血。若色如朱砂样鲜红着为正血。&nbsp;</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (656, 1, 7, '灸疗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;灸疗是用灸草炷或灸草条在体表一定的穴位上烧灼、熏熨的治病防病的一种疗法。灸疗有温通经脉，调和气血，调理体素，增强抵抗力，以防治疾病的功能。蒙医应用灸疗历史悠久，如在《四部医典・总则本》第六章外治段里有“蒙古灸”的记载；又如在中医古代医书《素问・异法方宜论》也有：“……其治宜变，故灸册者，亦从北方来”的记载。说明灸若疗法可能是北方蒙古族常用的治病方法。如今，在内蒙古自治区内外闻名的灸疗名医赤峰市阿鲁科尔沁旗的图旺扎布、乌兰察布盟的罗市桑沙德拉布等，不但擅长于灸若疗法，且有所创造和发展。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;灸疗的适应症为消化下良、冒火衰败、浮肿、水肿、痞病、寒性希拉性头病、关节黄水病、疗痈、炭疽、虚热病、癫狂、健忘症、新旧疮疡、妇女赫依病和血病、赫依性腰关节伛偻、一切脉病，以及热病后的多数疾病，皆可用灸法施治。总之，凡是巴达干、赫依所转化之一切寒性疾病，尤其是脉病、黄水病等，均适用灸法治疗，效果显著。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;灸疗的禁忌症为凡属于希拉性热症、血症、五官疾病、男生育脉道疾病。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (657, 1, 7, '尼入哈疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>                                         尼入哈疗法\r\n    \r\n<td>内蒙古锡盟蒙医研究所 韩玉珍 <br>\r\n     &nbsp;&nbsp;&nbsp;&nbsp;蒙医疗法大体分康复治疗和消除治疗两大类。尼入哈疗法相当保留灌肠或肠道输液，是消除治疗中一项。蒙医医典记载：尼入哈制剂按其不同力度分锐、中、柔三种。锐剂适用于重症，柔剂适用于尿频、热型腹泻、难治型肠炎、骚布各哈崩、精血枯竭等。从临床观察发现康复治疗药剂中的汤药、散剂、草药合剂均可作为尼入哈制剂使用。有如下特点：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．尼入哈可作为一种给药方式。对于下消化道及泌尿系疾病以尼入哈形式对症给药，疗效高于其它形式给药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．用药剂量可加大口服量的2―3倍，显效快。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．结肠主要回吸收水分。药剂通过加水加温，形成小分子扩散在水中，以尼入哈形式给药，其有效成分直接接触肠壁利于吸收，又减少了被消化酶分解的机会，从而提高药效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4．药物直接进入肠腔疏导体内有毒物质并与其结合或置换促使有毒物质排除体外，有利加快康复，方法简便安全。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;方法简介<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;材料：熬药锅(瓷盆可代之)、无菌滴瓶、输液器、与输液器管相应的(中号)导尿管、无菌纱布、胶布。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;方法：取5～l0g蒙成药放入盆中加清水300～500ml，熬开10―15分钟离火片刻，用无菌纱布过滤药液装入滴瓶备用。嘱患者排解大小便后侧卧位，双下肢自然弯曲待术。术者手背触摸滴瓶不烫(约40℃)时连接输液器拧紧调节器，将输液管另一端过滤球剪掉接通无菌导尿管。肛门用肥皂水擦洗，l％新洁尔灭液消毒后导尿管上涂擦润滑剂，排除气体缓缓送入直肠一乙状结肠20一30cm，胶布固定放松调节器，滴速每分种110滴左右为宜。滴速过快引起排便反应，药液温度过低时患者腹内有不适感，余无不良反应。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;适应症：泌尿系统疾病；慢性肾功能损伤，低蛋白性浮肿；下消化道疾病：便秘、结肠炎、过敏性点状出血性肠炎。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;禁忌症：脱肛、不明原因的肠道出血、心力衰竭、呼吸衰竭、休克者以及血压不稳者。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (658, 1, 7, '蒙医肉类疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医肉类疗法</p>\r\n      <p>  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院 高娃 谭冬梅<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医饮食疗法，早在元代得到了较为系统的总结和发展。元代著名的饮膳太医，蒙古族营养学家忽思慧编撰的《饮膳正要》是一部典型的药膳专著，该书收集和记载了食、养、医结合疗法，十分重视药物与食物的滋补和治疗价值，并介绍了营养疗法、食物卫生、食物中毒、延年益寿的药膳等内容。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族人民在长期的游牧生活中，习惯以牛、羊 (绵)、马、驼、山羊五畜肉及奶食品为主食，因此他们对这些动物肉有了更进一步的认识，同时对医治疾病积累了相当丰富的知识和经验。蒙医饮食疗法，有独特的民族特色，尤其对蒙医传统医药的发展有着重大意义。现简述如下：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．牛肉疗法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;牛肉味甘、性凉腻，具有滋元补身之功效。蒙医认为新鲜牛肉有清虚热，陈牛肉有抑制赫依之特点，故常用于年迈体虚及合并赫依的各种病症。此外牛油、骨髓、胃、脑、骨汤等性温。牛骨髓可补精，润肺；牛脑可治头部疾患；牛肝补肝明目、养血，治肝衰症、贫血、视力衰退等症；牛心有补心气治遗忘症等功效。牛肾补益肾气、益精、去肾寒。牛骨汤有滋元补身、强筋壮骨之效；牛血有收敛遗散毒邪，止血便之效，用法近似绵羊肉。牛肾加少许紫豆蔻煮熟食之，治肾虚及肾气不足引起的耳鸣耳聋。牛心加肉豆蔻煮熟食之可调补心气，抑制赫依，治疗遗忘症。常食牛肉、牛肉汤对年迈体虚和赫依性体质人有较好的滋元调制赫依的作用。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．马肉疗法<br>\r\n        马肉味甘、性热、重、腻。冬季炖煮或切成肉条凉干食之。马肉具有抑制赫依、祛寒性黄水、燥脓等功效。此外其油脂、肝、心、肺、肾、胃等除可食用外也可治病。如马油可治皮肤瘙痒症、牛皮癣等；马心有收敛散于关节之邪毒之效；马肾可祛肾热；马骨髓有柔润僵拘的关节及治疗痈疮之功能；马血可疗伤；马骨汤可燥黄水症；马肉500g加干姜2.5g，小茴香5g，炖煮食肉喝汤、治赫依病，尤其对胃、肝、肾等脏器的寒赫依症疗效更佳。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．绵羊肉疗法<br>\r\n        自古以来蒙古民族就认为食嫩草尖，饮清泉之水的蒙古五畜之肉及肉汤有滋养身体、防治疾病的功能。绵羊肉味甘，性温、腻、柔、轻。具有滋元，祛巴达干、赫依，增欲之功能。故医疗上绵羊肉、肉汤主要用于滋元、抑赫依，其血多用于妇女气血两亏及产后出血所致昏厥等症。绵羊肉可炖、炒、烧、烤食用，或炖成老汤饮服，还可以做成汤面条、肉汤米粥食用。据以脏补脏之理，五脏之病可煮食相关绵羊的脏器补益之。临床上将成年羯羊的瘦肉煎成老汤一碗，加黄油、红糖各2―3g，最佳白酒少许配成4味补益汤，在晨晚赫依升盛时饮服，可治各种赫依偏盛病症。食用羊肝可治肝衰症、眼花、夜盲症等。羊肾补肾气、益精髓，用于肾虚劳损腰膝酸软、足膝痿，耳聋、消渴、阳痿、尿频、遗尿等症。将4岁羯羊的头炖烂浓缩成的汤肉，各加适量阿魏、干姜、光明盐或将羊头捣烂加阿魏2．5g、紫硇砂2g、红糖50g、葱白少许，密封煮熟食用，治疗各种赫依病症，尤其对赫依性头痛疗效更佳。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4．山羊肉疗法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;山羊肉食法与绵羊肉相同，因其性寒，所以常与温性佐料配食。山羊肉味甘、性寒、重。具有滋元、抑制梅毒疮、黑痘、烧伤等功效。常用于体虚、眼疾、便血等疾病。如山羊肉250g、莲姜5g、干姜5g、葱白3个、蒜2瓣与食盐适量煎汤喝或煮大米粥吃可治膝髋疼痛症。山羊瘦肉500g切碎加阿魏lg、古月10g、荜拨5g、香菜子5g、食盐适量配制成馅作包子吃可滋元补身；山羊切碎加葱白煮食可增强视力；山羊血加醋热服可治腹泻带血等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;5．驼肉疗法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;驼肉味甘、性凉，具有滋元、强筋壮骨之功效。驼油有祛寒赫依病，治白脉病、皮肤瘙痒、痈伤的功效；驼峰有抑制赫依、补身益气、柔润肌肤的功能：驼肚子可助消化等功能；常用于希拉疯癫、肢体僵拘等病症；如常食炖煮的驼肉可解肢体僵拘、强筋壮骨。尤其炖食鲜驼羔肉可治希拉性疯癫病；炖驼峰与葡萄酒同饮食可滋补体虚气衰、祛寒赫依症；炖食驼胃驼肚助消化解食痞症。 \r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (659, 1, 7, '色搏斯疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;</p>\r\n      <p>    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;色搏斯疗法</p>\r\n      <p>    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;内蒙古医学院第一附属医院 &nbsp;&nbsp;&nbsp;巴雅尔<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院蒙医系 \r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陶・苏和 <br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医有“放血疗法”、“泥疗”等众多外治法，色搏斯疗法是蒙医诸种疗术之一，是传统疗法，它适应于蒙古民族在寒冷高原地区生活的习俗和气候特点的地区性疗法。是一种经济、简便、易操作、使用性较广的具有稳定疗效的疗法之一。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在巴・吉格木德所著《蒙医简史》中讲：色搏斯疗法可分为瘤胃疗法、皮张疗法和脏器疗法3种，它是用刚杀的羊、牛、驼的瘤胃内热色搏斯，体外吸敷来治疗疾病的一种外治法。色搏斯疗法是广大牧民的发明创造，具有悠久的历史。在13世纪时有把伤者的肢体浸入牛腹内治疗外伤的记载。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;色搏斯疗法可开通汗窍及各种细微通道，改善气血运行，调理胃火，增强器官功能，润泽皮肤，加强体力和美容之功能。治疗气血痰病，筋腱疾病及白脉症。总之色搏斯疗法对肌肉、皮肤、骨骼、脉管、筋腱疾病都有治疗效果，尤其对调理胃火、滋养脾血，滋肾壮阳，妇女气病，寒性疾患，血脉疾病、黄水疾病和肢体强直有明显的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;治疗方法：进行色搏斯疗术前做好相应的准备工作；把操作间的卫生、温度调理到对应度，更换衣帽，准备消毒毛巾、白面、黄油、白酒及治疗时所用的药物，选好无病绵羊。行术前先给患者食用羊肉汤食，为防止患者气盛，休克可服用相应的蒙药，如阿嘎日一35味等。施术时将新屠杀的羊皮内面涂散白酒、黄油和专用蒙药，令患者裸体躺于皮张上，然后将羊心置于患者心区，肝胆置于肝区、脾置于前额，将肾纵切，置于患者对应的肾区，另两瓣置于左右耳窍，直肠及肠道置于患者相应的部位。瘤胃内放入对症药物，如滋补壮阳可放入苏格木勒一25味等。然后患者把双脚入进瘤胃内或骑在瘤胃上，而后把羊皮裹在患者身上，为了防止气血上涌，用热毛巾在患者顶部、额部不停地热敷。施术时依患者的所患疾病、性质，患者的体力所决定，一般情况下1～2小时为宜。施术对症则患者全身发汗，安稳就眠。若不对症则出现烦燥不安，出虚汗，心动加快，甚至出现休克，这时立即停止治疗，让患者平躺在床上饮奶茶和黄油及羊肉汤并做适当的休息。进行色搏斯疗法时首先考虑是否对症，认为适应百症则会容易出现误差而犯错误。禁止对瘟症、骚热、心脏病、血盛病，体弱者及癌症患者进行此疗法。进行色搏斯疗法后的护理是至关重要的，室内温度要适当，注意受风着凉，不得强烈活动禁房事。<br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (660, 1, 7, '酸马奶疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;</p>\r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;“酸马奶”是用马奶酵制而成的一种饮料。远在六朝蒙古民族酵制酸马奶的技术和酸马奶用于治病之事即已驰名国外。14世纪《饮膳正要》中详细记载：其性轻而温，味甜、酸、涩。具有增强胃火，助消化、条理体质，促进精华与糟粕得分解，柔软皮肤、活血化瘀，改善睡眠，解毒，补血等功效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“酸马奶”是每年夏天6、7、8、9月份用奶多质量好的马奶酿制。制法是先用正宗发酵的牛奶2市斤作起子，再把等量的马奶放入大石缸内，每天搅拌l0多次，缸口用干净沙布盖好，3～5天后缸内的奶子颜色变为淡青色，逐渐中间隆出沫子，香气扑鼻。品尝之酸而香甜。这是真正的“酸马奶”的起子。然后按1：10的比例把“酸马奶”起子与新鲜马奶放入大缸内拌搅。每天加4次鲜奶，每次拌搅10多次，3～5天后就可饮用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;马奶属于白蛋白性乳类，易消化吸收。含丰富的蛋白质、糖、不饱和脂肪酸及低分子脂肪酸，维生素的含量丰富而种类多，有维生素a、b1、b2、b6、b12、c、e、f、p、h、叶酸、泛酸、和胞碱及钙、铁、镁、磷、钾、碘、铜、锰、锌等多种元素，其中维生素c的含量比其他任何动物奶中的含量都多。这些成份参与人体新陈代谢，具有调节人体生理功能，提高人体免疫力及防治疾病，不饱和脂肪酸和低分子脂肪酸对预防高胆固醇血症、动脉硬化有良好作用。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在马奶发酵成酸马奶时产生0.5-1%的乳清酸和1%的低密度的乙醇。乳清酸能抑制肝脏合成胆固醇，降低血液中的胆固醇的总量，扩张血管，降低血压的作用。低密度的乙醇也有降低胆固醇，防止动脉硬化的作用。且对胃肠道疾病、肺结核等肺部疾病、高血压、冠心病、神经衰弱、神经性头痛、坏血病、老年性腰腿疼痛、贫血、月经不调、痔疮、便秘等均有良好的治疗作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“酸马奶”的用法：疗程可分14天、21天、28天。一般每天上午2次、下午2次，每次250～400毫升。服量多少，按自己的身体情况决定以服用后多汗多尿为见效。服用期间要补充营养，如新鲜羊肉及羊肉汤等，禁止饮酒及酸辣等刺激性食物，防止雨淋及感冒。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“酸马奶”要放在通风阴凉处，或将缸底部埋入 60cm深，周围泼洒凉水，保持室温在摄氏10℃左右。“酸马奶”内不要加盐、碱，防止变质，也不要用铁、铝、铜等容器。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (661, 1, 7, '罨敷法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>  &nbsp;&nbsp;&nbsp;&nbsp;罨敷法是利用药物或物品，敷于人体表面的某一部位或穴位，以达到治病目的之一种外治法。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[适应症]： 巴达干性病、赫依性病、不消化病、痧症，黄水病、血寒凝结以及热刺痛等疾病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[禁忌症]： 浮肿、黑黄胆、瘟疫病、麻风、中毒症、热性水肿、肥胖症、痘疹等病，以及刚进食后，均不宜用本法施治。本法有冷罨敷与热罨敷两种。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(一)冷罨敷法<br>\r\n        对由伤热及骚热引起的刺痛等症，在局部喷激冷水或用星水(即星夜时所取之水)灌于塑料袋中进行冷敷。或把冰块或冷水装入塑料袋中冷敷亦可。对瘟热、骚热之热邪扩散和陈热等病，宜以冷水拍击。眼外伤肿胀者，将冷水装入塑料袋子以冷敷。由肉类中毒引起的喉闭发热者，药用查干榜嘎与冷水配伍，装入塑料袋冷敷。对肠刺痛，药用查干榜嘎、枇杷叶与冷水配伍，在腹部冷敷。对血症、希拉性病、热性痧症等症，用水底冷石冷敷。对由血、希拉引起的热性刺痛，宜用水底冷石或冷铁冷敷，或用沙参与乳酪配伍冷敷。鼻衄不止，宜用星水和水潭底的黑泥浆，在前额两眉间与脑后枕骨部冷敷。对血热齿痛及因赫依齿根作肿。用星水或水潭底泥浆冷敖。痛风病患部灼热者，用苦参与牛乳酪调和冷敷。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(二)热罨敷法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;对不消化病及痧症，将青盐炒热，装入布袭，在腹部热敷。受伤瘀血者，用热石罨敷。肝血瘀结，取旧墙上的松土炒热，布包热敷。对产生小腹、腰、骶等处疼痛，挖取一尺以下之土烘热，以酒润湿敷于患部。肾寒疼痛及小便不利，将干酒精烘热，布包热敷。对兼有包如之痧症、毒物或恶血侵于胃者，取卷丝苦苣苔、酸模叶、枇杷叶浸泡于水中煮热后，布包热敷，能消胃中之毒与恶血。不消化病及寒性痞块，取鸽粪烘热，布包热敷。寒症发作时，可用热砖热瓦热敷。虫痧症，取独活根叶，烘热外敷。蛲虫病，取铁杆蒿、马粪烘热外敷。寒性脱肛，先用温热之酒或牛乳洗脱出之肛，然后用热砖或陶热熨。赫依性刺痛，用油毡片热敷患部，然后取加温之水中石置于油毡上热敷。腰肾寒症，用猞猁毛或狼毛敷于患部。胃、大肠、小肠寒症、手掌涂油按摩揉搓，关节黄水病，用酒浸石加温后热敷，或用酒和羊粪加温罨敷。或用于酒糟 \r\n        、旧墙上松土烘热外敷。寒性闭尿，宜用油渣及鸽粪加温热敷。陈热及赫依性刺痛，将鹅卵石或陈骨砸碎后加酒烘热外敷。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，罨敷法有增强胃火，健壮身体，改善五官功能，以及促进气血运行，止痛消肿等作用。</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (662, 1, 7, '浴疗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp;浴疗系蒙医学系统传统疗法--软疗法之一种，是利用水的作用使人体的外部浸渍，以达到防病治病目的一种疗法。属于现代医学物理疗法之范围。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;内蒙古自治区地域辽阔，各种自然资源极为丰富，各种天然温泉和矿泉也很多，这些温泉和矿泉，对有些疾病具有显著疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医用天然温泉和矿泉治病，历史悠久，积累了丰富的经验。尽管由于当时的历史条件所限，对温泉、矿泉水的水质、成分等，未能进行科学的分析和鉴定，但凭着多年的实践经验，对各泉能治哪些疾病的情况，当地蒙医了解得比较准确，基本符合现在的科学鉴定结果。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;浴疗之原理是逐体内之热邪与病气于黄水之中，再通过汗水自毛孔排出体外．并能开启所有孔窍，从而改善赫依、血之运行；此外，人进水后，水给人体 \r\n        以压力，如胸腹部受压之后，膈肌上提，呼吸与血液循环随之加快，促进精华与糟粕之分解生化运动，以调理体素。总之，浴疗具有增强胃火和脏腑及五官的功能，使人的皮肤柔润，容光焕发的作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;浴疗之适应症：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;一、内科病<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;伤热扩散、毒热余邪深伏或扩散、结核、脉病、高血压病、血管神经病、初期动脉硬化、心脏病（代偿性）、胃肠功能紊乱、功能性大肠炎、习惯性便秘、肥胖症、风湿性肌肉痛、风湿性关节炎、痛风、肾病、多汗症、铅及水银中毒。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;二、外科病 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;慢性湿疹、荨麻珍、皮肤瘙痒症、牛皮癣、溢脂性皮炎、疖肿、多发性毛囊炎、慢性梗塞性动脉内膜炎、烧伤感染、大面积瘢痕收缩、四肢强直或挛急、弓腰驼背、肌肉萎缩、痔疮、前列腺炎、肌肉及骨酪之黄水积贮以及外伤后期功能锻炼等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;三、神经科病<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;神经衰弱、植物神经功能紊乱、神经痛、神经末梢神经麻痹等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;四、妇科病<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;闭经、月经不调、卵巢功能不全、慢性盆腔炎。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;浴疗之禁忌症：瘟疫、骚热、扩散热、水肿、身体衰弱、重症动脉硬化、心、肾功能代偿不全、活动性结核、癌症、各种出血病等，均不宜用浴疗法施治。 \r\n        此外，尚有由于身体部位和病位不适于浴疗者，如眼、面颊、两肘、两脚心和拇趾、睾丸、心区、腹部等部位的疾病，都不宜用浴法施治。浴法分为水浴和缚浴两种。水浴包括天然矿泉浴 \r\n        (温泉及冷泉)、五味甘露汤浴(人工药浴)、色布苏疗法、骨汤浴；缚浴分清热缚浴和祛寒缚浴两种。<br>\r\n       <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天然温泉浴<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;天然温泉浴疗法，为水浴法中最好的方法。天然温泉水因其水所含矿物元素之不同，因而有不同性质的矿泉。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[蒙医学关于矿泉浴的理论]<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;矿泉浴属于蒙医五种术疗之水疗范围的一种 《蒙医药选编》中记载，温泉常见者有如下五种，用作温泉浴，以适应不同疾病的治疗。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．硫黄温泉：硫黄温泉含有硫黄矿物质、泉色呈黄绿、硫黄气味逼人，附近满布硫磺。主治：黄水、肉斑、麻风、疖痈等病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．寒水石温泉：寒水石温泉之水清澈，无任何气味，其地满布寒水石，如用此水煮茶酿酒，亦不致变味。主治：巴达干病、毒病、陈热等疾病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．矾石温泉：矾石温泉呈青黑色，略有浑浊，泉水含矾石气味，该处遍布矾石。主治：疖痈、痞瘤、铁垢巴达干等病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4．五灵脂温泉：五灵脂温泉在石质山中，岩壁缝隙间经常有五灵脂水涓滴而下，泉水作紫色，味苦，气香郁，泉水经过这，形成青绿色之蜿蜒痕迹。主治：巴达干病、垢甲病、小便不通等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;5．石灰石温泉：温泉之水有烧燎动物角类之气味，该地区遍布青灰色之石炭石。主治：胃脾虚寒，消化不良，铁垢巴达干及剑突巴达干病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;温泉浴之方法及步骤：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;进行温泉浴，开始时水的温度不宜过热，入水泡浸一会以后，再逐步加温。边浸边洗，在患部徐徐按摩，对于体内之痞瘤肿块病，须缓缓捶打、揉搓。 \r\n        在温泉浴之同时，饮食起居方面，也须与之相应配合；保持温暖，切忌潮湿，出浴以后，即穿温暖衣服，卧床覆被休息。按泻下法进行善后调理，并根据身体情况和病情需要，进行涂敷按摩，作健身措施。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[现代医学对矿泉疗法的理论概要]<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;根据临床经验和科学实验，温泉水之治病作用可归纳为如下几点。温泉水治病，主要由于温度作用、机械作用和化学作用所致。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．温度作用：用温泉治病，起重要作用者为其温度。温热能降低神经的兴奋，有镇静作用。当热水浴时，皮肤血管扩张，心跳、脉搏加快，血压下降，促进新陈代谢。因而，水温越高，对心血管的影响也就越大。心血管病人一般不能在超过44℃的温水里入浴，而适当的温度，能改善血液循环，故对动脉硬化高血压病人、脑溢血后遗症半身不遂病人的功能恢复，有良好的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．机械作用：入浴时，人在矿泉水浮力和压力作用下，躯体和四肢关节在水中活动很自然而省力。所以，对骨折整复后的关节僵硬，神经麻木，肌肉萎缩以及各种关节功能障碍等，能起促进其恢复的作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．化学作用；矿泉水的化学作用，主要取于矿泉所含的化学成分。饮用矿泉水后，其化学成分被胃膜吸收进入血液而发挥作用。在矿泉入浴时，其有化学成分，能渗透皮肤进入体内，有的则不能被吸而附着在皮肤上，形成有医疗作用的分子薄膜，对体的周围神经感受器发挥作用。此外，矿泉水中有气体成分和挥发物质，还可经呼吸遭进入体内可起用。<br>\r\n      <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五味甘露汤<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在不能获得天然温泉的情况下，可用五味甘露汤进行药浴。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[制法]<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;配方：照白杜鹃、侧柏叶各1份、水柏枝、麻黄各2份，小白蒿3份，以上五味为主药，每份之量以 1斤以上为佳。将上列药物入锅，加满清水煎煮，约烧至半锅时，取出清汁药渣锅中再加满清水，重复煎煮，待烧至六分之四时，再次取出药汁，复加满清水煎煮，待干去十分之七，剩余三份时，用筛掂去药渣，将三次药汁合并即可入浴。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;药浴以7天或21天为一疗程，每天入浴1次。但视病情及患者体质可灵活调节，如体弱且赫依偏盛者，可隔日入浴。入浴时先将药水加热至适当温度，入水浸浴，稍凉时频加热的药水补充，调节水温，须始终保持凉温适度，如患部在头部，可用盆舀药水进行浇淋。在药浴过程中，每天需添煮少量的五味甘露汤，以补充药力。此外，五味甘露汤中，应加入黄精、天冬、玉竹、栝楼、蒺藜各15g，共研细末，每日一剂，加入五味甘露汤中。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;[五味甘露汤各药之功效]：侧柏叶主治肾病，照白杜鹃以平骚乱引发之巴达干之邪等病，水柏枝以清肉毒，麻黄扑杀窜入脉中之虫而清肝热，小白蒿能调理血液，兼清黄水而疗关节肿胀。五味配合，具有祛巴达干，除黄水清热解毒，活血化瘀，益肾壮腰等功效。用以治疗四肢僵直或拘孪，胃火衰败，脾血不足，肾脏寒症赫依，外伤疮疡以及皮肤疾病等，颇有良效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;除此之外，可根据病情，再加专门药物，如胸部血盛或头晕、希拉热盛者，宜以白檀香、紫檀香、硫黄各15g，共研细末，加入药水中。由于寒赫依而致饮食未消者，宜以寒水石、山奈、五灵脂、荜拨各15g，共研细末，加入药水中。黄水盛者，宜以白云香、草决明、苘麻子、朱砂、黑云香、文冠木各 \r\n        12.5g，共研细末，加入药水中。白脉病者，宜以麝粪或兔粪20g，或羊粪和碱亦可，共研细末，加入药水中。如加上列方药浴后无效而催动赫依者，用马、狗、驴骨各50g酒曲100g，奶酒500g，加入五味甘露汤中，进行药浴，对肌肉、骨骼、皮肤、筋腱等各种疾病，必有良效。以上诸方，如无必要，则须另加。药物剂量大小，须与疾病轻重情况相适应，剂型也不必拘泥为药粉，如能煎成汤剂，浴水清净，更佳。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;寒症或赫依病人，药浴疗程结束后，宜进行火灸。希拉病者，药浴疗程结束后，使正血与病血分离后，出现手或足麻木征象时，宜放血配合服药施治。有的患者入浴初期疾病无发作现象，可令其继续入浴，细心观察。如入浴确无效果则应立即停浴，以防意外。如妇女入浴后，出现头晕或昏迷，胸闷等现象时，可投豆蔻补益散或沉香八味散内服。肾脏或精府有病者入浴后可有赫依与血相搏而引起眼翳或耳聋的情况，因而，为了防备，在入浴前给以沉香八味散内服。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (663, 1, 7, '震脑术', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医“震脑术”的研究 &nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n      <p> &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医药学院 &nbsp;金玉 &nbsp;博・阿古拉 &nbsp;满都拉 &nbsp;扎拉嘎 &nbsp;&nbsp;萨仁图雅<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n        &nbsp;&nbsp;&nbsp;“震脑术”是蒙古族民间广为流传具有悠久历史的一种专治脑震荡的奇特疗法，具有取材容易，便于掌握，用之有效等特点。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1“震脑术”的形成与发展<br>\r\n        蒙古族长期以来繁衍生息于我国北方的广阔草原，日常从事游猎和放牧的生产活动，每天与野兽、家畜打交道。他们在野外放牧时，经常遇到某些牲畜的“塌腰子”等情况，有时那些“塌腰”走不起来的牲畜通过主人的扶持、震动等，得到缓解或恢复正常。这种现象的重复(偶然的或有意识的)，使人们认识到有关“以震治震”的原理，为蒙医“震脑术”的起源与发展起到一定科学实验性的作用。随着人类知识的增长，以及治疗疾患的需要，他们对动物和人体内脏的观察逐渐变成比较自觉的认识活动，并把人体解剖和医疗活动联系起来。据《新元史》记载，公元1262年，有一位名叫匣刺的蒙古大将，在战场上“被三创镞中左肩不得出，钦察惜骁勇，取死囚二人，部其肩，视骨节浅深，知可出，凿其创拔出之，匣刺神色不为动”。这样，不仅丰富了对人体构造的认识，而且大大促进了蒙医骨伤科及“震脑术”等各种内伤外治方法的发展。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在大量医疗实践的基础上，历代蒙医师不断总结经验，重视利用和推广这一富有民族医疗特色、具有临床实效的外治疗法一“震脑术”。尤其在18世纪以来不少的蒙医经典著作中都记载了有关“震脑术”的内容。如著名蒙医学家伊希巴拉珠尔所著的《甘露点滴》中，记载了最基本的一种“震脑术”一手震术。另著名蒙医师吉格木德丹曾扎木苏在他行医生涯中重视“震脑术”，在其代表著作《观者之喜》的《骨折、脱位、内伤治法》篇中详细论述了带震术、板震术等几种常用“震脑术”。鄂尔多斯著名医师巴依斯古愣(1908年～1990年)更擅长于各种“震脑术”。随着近代蒙医学的逐步完善，“震脑术”的研究也更加受到重视。蒙古民间流传的各种“震脑术”相继被搜集、整理，有关学术杂志上先后发表了不少运用各种“震脑术”治疗脑震荡的好经验。有的地方还开展了“震脑术”的科研项目，收到预期成果。近年来，“震脑术”还引起国外一些学者的兴趣，英、日、俄及蒙古的医疗团体都相继来内蒙古考察这一奇特疗法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2 治疗原理<br>\r\n        随着临床经验的积累和解剖知识的进一步发展，蒙医震治脑震荡的理论体系和疗术渐趋完善。“震脑术”是以具有朴素辩证观点的“以震治震，震静结合，先震后静”学说为临床指导思想。根据震荡部位及轻重，选用不同程度不同方式的各种“震脑术”进行震治。蒙医认为脑震荡属头部内伤范畴，分“脑气震荡”和“脑髓震荡”两类。头部受到外力，脑力和脑髓必然受震，轻则震脑气，重则震脑髓。由于头部外伤，脑气和脑髓等有关脑组织解剖位置失常，使气血循行受阻，“不通则痛”。如不及时治疗，进一步加重“不通则痛”的病理变化，则会出现一些后遗症，影响工作和生活。“以震治震”是蒙医治疗脑震荡的基本治则，根据不同情况，采取相应的的震动方法，有助于脑功能的紊乱得以整复，气血得以畅通，故脑震荡可震治也。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，蒙医震脑术及其治疗原理符合现代医学所认为的脑震荡是脑组织某些病理形成学改变所致的观点。 \r\n        通过适当的震动方法，纠正有关脑组织的紊乱。使脑组织间不协调的力学关系得到改善，促进局部气血循行，从而消除脑震荡而产生的“不通则痛”，达到“以震治震”的目的。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3 操作方法<br>\r\n        蒙医“震脑术”的方法丰富多彩，各地施行的“震脑术”都不同。我们研究小组在广泛搜集民间各种震脑术的基础上，对震脑术的操作方法进行了标准化、规范化改进和统一各种器械，提高了震脑术的疗效。震脑术的操作分为震动和固定两步。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1 震动方法&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.1 手震术：①患者坐位，医者面对患者而立，用双掌分别扶住头部两侧，稍向上提起患者头部并向健侧微屈其颈，双手协同用力，将患者头部向前后两个方向小幅度的快速震动几下。②医者面对患者而立其患侧，一手掌按扶患者下颌部，另一手按扶患者后枕部，双手协同用力，将患者头部向左右两侧小幅度的快速震动几下。③医者立于患者之后，一手掌面按于健侧项肩部，另一手将患者头颈被动置于功能活动明显受限位，尔后手掌按于患侧头颞部，两手协同用力，将患者头颈迅速向健侧推压扳动。有时可听到“咯嗒”一串响声。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.2 带震术：患者坐位，医者用震感带从患者前发际沿两耳上缠绕固定。医者一手掌按扶患者头项，另一手从套管外端拿住震感带两端交叉处，从健侧开始依次向前后左右抖震患者头部各3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.3 \r\n        杆震术：患者坐位，医者立于患者之后，用震感带从患者前发际沿两耳上缠绕，一手合住震感带两端交叉处。让患者咬住一根震感杆的中间部位，震感杆紧靠嘴角。医者用另一手拿震感锤敲击震杆两端，先敲患侧，再敲对侧，依次反复敲3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.4 板震术：患者取仰卧位，四肢伸直，手掌朝内，脚掌上翘90度。紧贴脚掌旋转一块震感板，医者用一手扶住震感板，另一手拿震感锤敲击震感板，先在患侧脚掌处敲击3下，后在健侧脚掌处敲击3下，依次敲击3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.5 杵震术：患者取仰卧在铺毡的平地面上，让患者枕在一个装满细沙的木碗上。医者用震感杵分别离患者患侧肘关节、肩关节、耳及头项，对侧耳、肩关节、肘关节、膝关节，患侧膝关节l0cm处地面上墩击3下，依次反复墩击3次。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2 固定方法<br>\r\n        术后用震感固定带从患者前额部，沿两耳上围头固定，松紧可以随时调节，静养3～7天。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4 疗效观察<br>\r\n        脑震荡是颅脑损伤的一种，主要表现为脑机能一过性障碍，无明显结构上的器质性损害表现，但在脑组织其些病理形态学的改变，许多患者虽经过治疗，但仍常出现一些后遗症，或因外伤后颅内或颅外疤痕形成，牵拉痛觉敏感结构引起；或外伤不明显，中枢神经经过强的震荡刺激发生机能障碍；或病人对外伤过分紧张、恐惧引起头项部肌肉的持久收缩或动脉扩张而产生。往往出现长期局限性头痛或全头痛、眩晕、失眠、纳呆、记忆力减退、精神萎靡等。本组根据蒙医“以震治震”原理，采用“震脑术”治疗脑震荡取得满意疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.1 检查方法：手法检查：能在以下部位摸到条索状物及明显的压痛点。触摸眉毛中点(鲁腰穴)、两眉梢后凹陷处(太阳穴)，后发际两侧凹陷处(风池穴)等部位有明显压痛；有的在颞部皮下可摸到大小不等的条索状物或呈片状酸胀痛的敏感区，刺激时疼痛难忍；有的在头部有轻度浮肿并有压痛；有的还在颈项部有压痛，并伴头部活动不利等。以上压痛反应均以患侧重，健侧轻，全头痛则无明显差异。线测检查：蒙医传统细线测量检查方法是用一根细线分别测量患者两眉中点(印堂穴)到两侧后发际凹陷处的距离，两耳尖直上，头项正中点(百会穴)到两耳尖的距离等，再进行两侧对比。一般测量出的结果患侧长，健侧短，全头痛则无差异。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.2 治疗方法：用上述手震术、带震术、杆震术、杵震术等5种方法分别治疗20例脑震荡患者，前三组症状均较轻，后两组症状均较重。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.3 注意事项<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.3.1 应做好术前全身检查，如有高血压、冠心病等严重内科疾病应该严格掌握适应症，以防发生意外。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.3.2 应按照脑震荡的诊断标准诊断清楚，注意有否颅内压增高等征象出现。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.4 治疗结果：本组100例经1―5次治疗后，显效(症状消失)85例；有效(症状减轻)15例。先敲患侧，再敲对侧，依次反复敲3次。<br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (664, 1, 7, '正骨术', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>   &nbsp;&nbsp;&nbsp;&nbsp; 蒙医正骨术是历代正骨医家们所积累的，具有民族特色的治疗各类骨折与关节脱位、软组织损伤等一系列正骨疗法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医正骨术具有方法简炼、粗俗，但临床效果好的特点，只有经过长时期的临床实践与刻苦锻炼，才能掌握其真谛，达到“机能于处，巧生于内，手随心转，法从手出”的程度。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;当施疗之际，重视人体生理功能之内在作用，注意保护人体骨骼之完整性，最大限度地发挥组织器官的自愈能力，使伤折部位及早愈合，恢复正常生理功能。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医正骨术可分整复、固定、按摩、药物治疗护理和功能锻炼等六个步骤进行。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;一、 整 复<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;就是运用熟练的手法，使移位的骨折端正确地复位并治疗软组织损伤的一种疗术。因此，在施行手法整复时，首先要做好骨折的检查和诊断。在此基础上，通过眼观、手摸和心会才能达到手随心转，使骨折整复的目的。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;直接暴力或间接暴力作用于肢体或躯干，使局部的骨骼发生骨折。由于该部受力大小，所持体位的不同及肌肉的收缩等，可造成各种类型的骨折，因骨折的类型不同，整复手法亦不同。如完全骨折，用拢按法对接；凹陷型骨折，用提推法使陷者复起；粉碎性骨折，用捏挤法复位；螺旋型骨折，用旋按法衔接；凸起型骨折，用展按法平复；尾椎骨等嵌入型骨折，用钩提按捺和伸压展法整复；肱骨内外髁等之青枝骨折，用蹭推捏挤法复位；对于骨折断端重叠移位而缩短者，则按“欲合先离，离而复合”。的原则进行整复。在施术前轻轻地按压和按摩伤部，使收缩的肌肉放松，疼痛缓解，尔后用拔伸牵引的方法，使重叠的骨折断端复位；对向侧移位者可用提摇按推法复位；对于两根骨并列的部位发生骨折，则用扣挤分骨法使断端分离，然而用夹挤法复位。在正骨过程中，除运用恰当、熟练的手法外，治疗必须及时，动作要果敢、细致，准确而敏捷。如果施术时用力过大，就会有损伤骨软组织的危险；力量过小则达不到复位的目的。施术时，要正确运用手技，力求一次完成整复。反复多次施术，非但有损骨折断端，对愈合亦有不良影响，而且会给患者带来较多痛苦。施术越早越好。四肢骨折，宜于伤后半小时内进行。此时伤肢麻木，尚未发生严重水肿，患部张力小，疼痛轻微，是最佳正整时期。如果局部肿胀严重，或皮肤破裂等，需要经过适当治疗，待肿胀消退，皮肤愈合，筋肉痉挛松懈后，再行手法整复。但要防止延误治疗，以免人为地造成陈旧性骨折。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;二、 固 定<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;固定的目的，在于强化整复后的效果和局部的相对稳定，以加速骨折的愈合。固定的范围要适宜。太大，会影响伤肢活动；太小，达不到固定的目的，伤肢长期固定不动，则气血运行受阻，易产生肌肉萎缩，关节僵硬，骨质疏松，延缓骨折愈合等不良后果。如系横形骨折，整复固定后沿骨干轴线推挤，若下肢骨则除推挤外，可使其踩地，这样可测知是否已正确复位，且利于连结。对于初次未能整复，或者移位骨折，可运用“以动治动”的方法，再行拔伸、摇动后整复固定予以矫正。对长骨骨折采用三点包扎固定法。三点加压的作用，有利于骨折段的轴线对正，既能使骨折的肢体达到解剖复位，又可解决因肢体“动静”矛盾所造成的伤肢功能障碍。骨折断端对接良好，肢体功能恢复。x线显示骨痂通过骨折线，骨折线已基本模糊，即可解除固定。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;三 按 摩<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;按摩是蒙医正骨的一种独特疗术。也是正骨辅助治疗手段之一。骨折的阶段的不同，所用之按摩手法亦异。计有：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;初期按摩：骨折后一周内，由于局部软组织、血管和淋巴管等受到不同程度的损伤，故导致血、希拉热亢盛，出现瘀血、疼痛、肿胀等症状。对此，宜采取化仁瘀消肿手法。即每日在夹板固定的空隙和上上段，喷激白酒进行蹭摩；衬垫处实行按摩；伤肢施行向心捋摩；疼痛部位则要用嵌摩法；伤肢邻近关节采用掐摩法；另外，有关穴位辅以搓摩。上述按摩，有助于改善血行，促进新陈代谢，加快骨折愈合。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;中期按摩：经过初期综合治疗，骨折部位基本得到稳定，筋腱舒展，肿消痛止。根据“肾藏精，精生髓，髓养骨”及“肝生血，血养筋”的理论，在此时期，应以补肾益肝健骨为主要治疗原则。因此，按摩时宜取肝、脾、心、肾、肺等穴位。先喷激白酒，用大拇指进行抖摩或搓摩；用手掌沿脊柱向上推摩或按摩；或横向蹭摩，以进一步改善血液流通，加速骨折愈合。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;后期按摩：经过初、中期治疗骨痂基本形成，骨折完全愈合。但由于内损外伤，长期卧床，往往导致三根平衡失调，气血虚弱，关节僵滞，肌肉萎缩等。此时，除继续进行中期按摩外，尚应在胃、大肠、膀胱等有关穴位上喷以白酒，施搓摩、抖摩、推摩法；对局部则进行按摩、蹭摩；对患肢肌肉，则施行抟摩、拿摩、搓摩；对患肢关节采取揉摩、摇摩、展摩法；对患肢指(趾)，则采用曳摩法。通过上述按摩，可使伤肢气血畅通，强骨健筋，恢复功能。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;按摩方法：由轻到重，范围略宽，操作要细致。对瘦弱者，手法宜轻而缓；对健壮者稍重而快。但必须注意保持患部稳定。喷酒按摩法是据“血受养于食物精华；血随气行”的观点而创用的。既能防止伤热内陷，筋腱拘紧，又能加快骨折愈合和肢体功能恢复。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;四、 骨折的药物治疗<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;指内服药物疗法。根据骨折的不同时期及不同的症状表现，用药原则也如按摩一样，分初、中、后三期投方。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;初期：表现血、希拉热亢盛，恶血瘀滞，患部明显肿胀疼痛。常用方有：七珍散，七雄丸、冰片十七味散、冰片二十五味散、黑云香四味汤等。如伴有胸部灼热，惊悸不安者，可投以沉香八味汤、如意至宝丸等方。咳嗽、咯痰不利，则投以沙参四味汤，亦可投以三七单味散，以除伤毒。如有尿闭者，可投以骨伤传统验方――吃鸡蛋黄一个；大便燥结者可投芝麻单珠汤，或可加少量核桃仁、蜂蜜内服；局部发热则宜涂乌龟血。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;中期：可选用具有排黄水、降热势，续筋接骨功效的药，如六味铜钱散、八味朱砂散、三味赭石散、六味石决明散、六味杜仲散、二十五味接骨散等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;后期：宜服强筋健骨，抑赫依，壮身润僵药物，如健胃石榴五味散、二十五味大剂散、沉香三十五味散，水银十八味散等。在患部特别是关节处可施行药水浸洗或药物涂擦。一般用白酒、醋、水等分，水煎浸洗，或用青盐热水浸洗，或用水柏枝、紫檀木、山蒿三味药酒煎浸洗。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;对开放性骨折，应先清创复位涂药，缝合伤口。除使用杀粘药和清热药防止感染外，还要考虑使用具有生肌功效之药，以促进伤口愈合。如珍珠五味散、麝香十七味散外敷伤口，红花八味散内服，有续脉接骨、止血生肌之效。对白脉受伤，可投予十三味大鹏金翅丸，并用香獐粪或麝香、羊粪加白酒烘热外敷。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;五、 骨折的护理<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;护理工作对骨折的治疗颇为重要。患者的被褥单、内衣要经常换洗，保持清洁，尤其患部更应注意。大、小便时要保持患肢稳定。要防止褥疮的发生，经常注意勿使夹板移位及保持缚带的松紧度。要很好调理饮食和起居。饮食方面，骨折初期阶段，宜食稀粥、水果、蔬菜类，牛、羊、山羊等的新鲜奶酪、酸奶、奶油等，禁食肥肉、酒、鱼肉等食物。中期阶段，宜食炒米，小米等谷物类、牛、羊和猪等之软骨、肝、肾及瓜类及黄豆等于营养之物，并经常给羊骨汤，禁食瘦死畜肉、醋及豆腐之类食物。后期宜食五谷类，牛奶、雉、鸡、鹌鹑等禽肉和蛋、蔬菜，禁食变质之肉类及不易消化之生冷食物。起居方面，宜在安静、凉爽的环境中疗养，睡眠要充足，心情要舒畅；慎勿忧虑与劳累过度，绝对禁止房事。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;六、功能锻炼<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;功能锻炼是骨折治疗全过程中不可缺少的辅助疗法。它可以进一步改善局部和全身血液循环，使患部获得充足的养料，有助于加速骨痂形成。促进骨折愈合。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;其步骤是：由轻到重，由小到大，循序渐进地进行。如上肢骨折，初期阶段，以伸屈手指、握拳及前臂肌肉舒缩活动为主；中期阶段，要增加肘关节和肩 \r\n        关节的活动；后期阶段，要扩大活动量，如旋转前臂，活动腕关节，抬举患肢，握物等。下肢骨折，初期阶段，以脚趾(无损伤时)，小腿肌肉的舒缩活动为主；中期阶段，主要动髋关节，抬举伤肢；后期阶段，除活动膝、踝关节，还要进行站立、行走的锻炼。腰椎骨折，如脊髓、白脉无损伤时，从整复后三周左右开始，以挺胸姿势下地行走，待骨折完全连接后，才做弯腰锻炼，使功能逐渐恢复。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (665, 1, 7, '泥疗', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp; 将泥加热稀释后入浴或包缠患病部位，利用其温热作用进行治疗谓之泥疗。泥疗所用之泥有粘土泥、沃土泥、炭泥、人工泥等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一、 医疗泥之分类：根据泥土之结构条件及其成分的不同，医疗泥分为如下几种。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(一)、粘土泥：粘土泥都出自海岸、江河入海口、矿泉、盐湖、沼泽地等处。这种泥经过多年淤积，内含动植物的残骸、水藻类、水草、甲壳类等，在缺氧情况下，由于各种微生物的活动发生了复杂的物理化学变化，由胶质物、有机物及其他分解产生的物质混合而成。粘土泥呈深黑褐色或黑青色，有硫化氢的气味，味似苦碱，由于弹性及粘性大，所以粘在皮肤上不易洗净。粘土泥里一般不包含植物残余。粘土泥里无机物含量大，因此也叫无机粘土泥。粘土泥之晶体成分占全泥重量之20―50％，胶质成分及有机物占2―9％，粘性成分占50―60％。淡水泥之粘性成分的盐类浓度仅数mg／l，而盐湖泥之粘性成分的盐类浓度则可达250―300g／1；渗透压 \r\n        0.2―1.0气压，比重约1.2～1.6。上述情况说明，淡水湖由于降雨、蒸发及冰冻等因素之影响而起变化。而矿泉和海水则由于深而基本上不发生变化。粘土泥之酸碱度ph6.0―8.0。粘土泥之热度相当低，约0.5左右，传热系数比其他种泥高，约0.002，热能容量比其他种泥低，约 \r\n        450秒。交叉强度比其他种泥高，有1500―8000―12000达因／cm2，最高可达98000达因／cm2，粘度达6000― 9800。我国盛产粘土泥，尤其在沿海岸地带为多，仅大连海岸有七、八处出产医疗泥土。天津一塘沽之海泥亦属此类医疗泥。此外，汤岗子、克什克腾温泉泥，由于泉水渗进泥土内，所以泥土本身之温度即达60―70℃，可以直接取来用于医疗。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(二)、沃土泥：淡水湖畔沼泽地之粘土、胶质物、动植物残余等物质，在无氧情况下，由于各种微生物的作用而分解形成沃土。沃土中含有很多有机物，表面呈各种中华诃子色和咖啡色好似冰冻的胶。由于沃土含有丰富的有机物，所以亦称有机泥。湿沃土中晶体只占1～2％，胶质成分及有机物约 \r\n        5％，水占90―97％。干沃土之晶体约占30％，胶质成分及有机物约占70％或80％。因湿沃土之含水量大，所以交叉强度达1400― 1900达因／cm2，最低750～850达因／cm2，粘度1400―1900，热度1.0，传热系数比粘土低，热能容量较粘土大。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(三)、碳泥：碳泥存在于沼泽地。由各种植物之有机物残余，在无氧条件下，经各种微生物的长期作用而形成的物质与矿泉水相互作用而构成。碳泥大都呈深褐色，不光滑，形状如揉好的面，干燥则极脆。由于碳泥之无机物及胶质物含量较无机粘土泥大，所以，此泥之泥疗为有机泥及无机泥之混合式泥疗。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(四)、人工泥：没有天然泥的地方，可以在粘土中加入各种微生物，有机物及矿物盐类之溶解物，制成与粘土泥同样 \r\n        的具有物理、化学及生物特点的医疗泥。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二、 泥疗之生理作用：泥疗之效果主要赖于温度及物理、化学成分之总的作用而得以实现。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(一)、温度作用：泥疗中温度中主要地位，医疗泥之热容量小，有相当的弹性与粘性，由于影响分子运动，所以传热力低，而保温性则相当高。如加热后用则温度作用更为突出。加热之医疗泥不出现互相传热现象，散热过程慢，与皮肤接触时向人体传热过程亦慢。因而，在接受泥疗时人体能耐受较水里更高的温度，同时泥的冷却时间长，人能得到长时间的温热作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(二)、机械作用：在医疗泥中，包含各种小粒沙砾，粘土颗粒及大量的胶质物，因而具有相当程度的交叉强度，粘度和比重。与皮肤接触时给人体的相当的压力，同时泥中之分子运动与皮肤发生摩擦而给以刺激。以上刺激综合起来就是机械作用。 \r\n        泥中之分子运动与皮肤发生摩擦的同时局部产生电流，这种电流改变周围神经的兴奋，增加泥中某些化学物质对皮肤的渗透力，随而增强医疗泥对人体所起的化学作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(三)、化学作用：泥中的各种盐类、有机物、胶质物、溶解性物、气体、维生素及某些激素等，被皮肤、粘膜吸收进入人体或被皮肤粘膜吸着，对器官发生化学刺激作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(四)、其他作用：有些医疗泥中含有放射性物质和抗菌物质，发挥放射作用与抗菌作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(五)、生理作用：在施行泥疗的部位，在其作用下，由于交感神经兴奋性降低，毛细血管扩张，皮肤充血，局部血液及淋巴循环得到改善。泥中之磷酸类具有促进组织的渗水，增加盐、汗腺及皮脂腺分泌的作用。经泥疗后皮肤表层细胞蛋白分解，产生组织之类氨基物质，这些物质随血液、淋巴循环作用于全身，使之引起反应。泥疗施疗过程中，身体吸收相当大的热量，钙、 \r\n        镁、钠、硫化氢等化学物质附着于皮肤表层影响散热。因而改变体温调节，影响体温之平衡，使之增高2℃左右。在进行40℃～46℃温度的腹部泥疗时，局部温度可升高1.5℃～3℃，并能持续40～50分钟；胃温升高0.3℃一0.8℃，持续45～90分钟。身体反应正常时，使胃温升高与提高泥疗温度，相互是有关系的。如身体反应性降低时，胃温在热泥影响下却较正常温下降；身体反应性亢进的人，施以46℃的泥疗时，其胃温下降，而施以40℃～42℃的泥疗时，其胃温却增高。以此也可以检查人体的反应性。也可以作为检查泥疗效果的标准。如身体所起的反应与上述情况符合则为病情好转的表现，否则提示病情加重。泥疗对神经系统、循环系统、新陈代谢、内分泌以及消化系统，均有良好的医疗作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三、泥疗之方法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(一) 泥疗室之准备：泥疗室包括更衣室、浴疗室、妇科治疗室、洗澡间、热泥间。治疗室之大小，根据床位数来决定，每张床位应准备被子、毛毯、枕头、床罩、塑料布或胶布等。对上肢的施疗需要小桌一张。治疗室温度应保持在22℃，～24℃之间，要通风良好。夏季可在湖畔等场所施疗。为了便于妇女患者，应设有妇科治疗室，需配备妇科椅、妇科冲洗器、床位等器械设备。泥疗后需要洗澡，所以应设洗澡间。热泥室应配备泥架、洗涤盆、容泥器、干燥器、揉泥板、温度计等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(二)、医疗泥加温法：泥土加温有水热箱、中间有水的双层锅、旋转加热器、电热箱以及借日光加热等。加热前将泥中的大颗粒、蛤壳、小砾石等杂质去掉放于加热箱内加热。加热至50℃～60℃时加进冷泥搅拌使泥温降至适合于施疗之温度。加热不得超过 \r\n        50℃，亦不能采取直接加热的方法，泥土烧焦则失去作用，温度过高则影响其胶性而减弱医疗效果，同时杀灭泥中之微生物而更新后再用。测泥温时将温度计深插泥中，并测几处，以其平均温度作为标准。利用碳泥时，加热前用筛子筛好，取出杂质、大块植物等之残存物，然后加热水或热盐水搅拌至稠糊状，其湿度达70―85％时放进加热箱加温。利用粘土时，加热前使之干燥，研细，用筛子筛去砾石、沙粒等。然后以6―7公斤粘土加1公升水的比例加热水后加以搅拌混匀。如果用粘土泥施以涂敷疗法时将泥搅拌成浸膏样，然后作泥饼备用。如若施以全身泥疗则泥宜稀。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(三)、施疗法：根据发病部位、病情及患者体质等，采取全身泥疗、局部泥疗、腹部泥疗和电泥疗等疗法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．全身泥疗：将医疗泥放进澡盆内，加盐水或泉水至需要稀度，患者犹如洗澡躺在其中，水深达乳部即可。头部及心区敷以冷毛巾。泥温34℃～37℃，泥浴时间15～20分钟，隔日或隔两天施疗一次，10―15次为一个疗程。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;全身包缠法：即利用日光加热埋身疗法。令患者卧于日光加热到适度的泥中，只把胸部露于泥外，泥之厚度一般为4～8cm，胸、腹部的泥稍薄，患者的头和心区敷以冷毛巾。全身包缠之泥的温度37℃～42℃，时间15―20分钟，隔1―2日施疗次，10～15次为一个疗程。泥浴，包缠结束后，用35℃～37℃热水洗身，然后静卧30～60分钟休息。因全身泥疗增加神经和循环系统的负担，引起消极作用，所以，只有在特殊情况下，才用此疗法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．局部包缠泥疗：局部包缠泥疗可对四肢、背、腹部、关节、颜面、颈项、胸部及神经等部位施疗。局部泥疗可分局部包缠法、局部泥浴法、泥罨法、间接泥疗等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;局部包缠法：在床上铺被或毛毯，上面再铺布、塑料布和粗布，再把搅拌好的泥按所疗部位的需要铺开其上，厚度为3～7cm。碳泥和粘土泥的厚度有5～l0cm即可。令患者卧于泥上，然后将布、毯等按序卷起包缠其身以保温，并在其头部和心区敷以冷毛巾。因泥疗部位的不同，局部包缠法分如下几种：①耳部泥疗。②肩部泥疗。③背、腹部泥疗。④泥裤疗法：即把腰、腹、大腿这上三分之一用泥缠如短裤形。⑤背、下肢的泥疗：即把背部和患病的下肢进行包缠。⑥脊柱的泥疗。⑦关节泥疗，即在肩关节、肘关节、腕关节、髋关节、膝关节、踝关节施以包缠泥疗。⑧手部泥疗：即把患手插入桌上铺开之泥中。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．局部泥浴法：把手、肘部、足部浸渍于木器中。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4. 泥罨法：把加热之医疗泥装于布袋内敷于患部施疗。此疗法可降低化学及机械作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;5．间接泥疗法：不把医疗泥直接放在患病部位上，而放于其侧旁。用于局部之医疗泥的温度根据病情、患者体质来决定。体质强、无心血管病、无内分泌及神经系统障碍则可用42℃～48℃的泥；有轻度心血管病和神经障碍，体弱者用37℃一42℃的泥；低温泥疗则用32℃一33℃的泥。泥疗时间每次20～30分钟，开始时隔日施疗，以后则施疗3日，休息1日，以15―20次为一疗程。局部泥疗结束后用35℃～37℃的热水将局部洗净，不得用肥皂，静卧30―40分钟休息。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;四、 泥疗之反应及注意事项当泥疗作用于炎症性病灶时，有大量炎性渗出物进入血液。患者反应性低下时，这些物质不能很好氧化而滞留于血中，因而血沉加速，病灶部疼痛加剧，出现红、肿、热及运动障碍症状。妇女则出现腹股沟痛，下腹部不适，白带增多，尿频等症状。表现为全身不适、倦怠、无力、头晕、头痛、脉搏加速、呼吸急促、体温增高、大汗淋漓及失眠，泥疗又减弱脾功能，引起食欲不振，消化不良。以上反应的程度与医疗泥之物理、化学特点，泥疗部位，泥浴泥量、包缠泥量、泥温度、泥疗时间、间隔时间、医疗次数、泥疗目的以及配合其他医疗等有关。同时还与病情、合并症、患者身体反应性等有关。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;泥疗过程中可能出现失水和电解质平衡失调现象，因此应准备盐水或热茶。泥疗过程中如果出现头晕、心悸、恶心、呕吐、大汗或局部剧痛、浮肿等征象时，应立即停止泥疗。泥疗结束后应静卧休息30分钟，患者体弱、泥疗面积大则应延长休息时间，要避免着凉。接受泥疗之当天禁止进行大量活动，亦不能进行日光浴疗、游泳及郊游等。由于泥疗能增强蛋白质、碳水化合物的代谢，所以令患者进食富于蛋白质、糖、维生素b1等之饮食。泥疗之疗效，疗后一个月才能出现，并可持续2―3个月因而下一疗程的开始，必须在3个月以后，最好间隔4―6个月。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;五、泥疗之适应症与禁忌症<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(一)、适应症：慢性多发性关节炎(除结核病)、风湿性关节炎(急性期过后六个月以上才能进行泥疗)、慢性脊椎关节炎、脊髓及脊髓膜外伤后遗症、骨折迟迟不愈合、骨髓炎(除结核病)、慢性肌炎、腱鞘炎、神经炎、多发性脊髓神经根炎、神经痛、周围神经系病、周围神经外伤后遗症、营养性溃疡、静脉曲张、周围静脉炎、血栓闭塞性静脉炎(急性则用冷泥、慢性用热泥)、外伤瘢痕、抽搐、粘连或萎缩、腹腔内脏腑慢性炎症、腹腔器官粘连、慢性前列腺炎、慢性副睾丸炎(除结核病)、妇女盆腔炎、卵巢功能减弱、慢性副鼻窦炎、慢性中耳炎等病症宜用泥疗施治。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;(二)、禁忌症：肺结核及结核病、心血管系统病、代偿功能障碍、大血管瘤、脑动脉硬化、肾性高血压病、重症哮喘、全身无力衰弱、痞瘤、肿瘤、出血性疾病、甲状腺机能亢进、糖尿病、皮肤病、白血病、恶性贫血，在泥疗部位有急性炎症、湿疹等病则禁忌进行泥疗。</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (666, 1, 7, '脉泻疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;脉泻即用专门药物，给消化道以刺激，从而柔和开通脉道，使疾病从脉道或尿道排出的一种疗法。适应范围为子宫痞、脉痞、妇血症扩散等妇科疾病，血、希拉性疾病，中毒症，陈旧性热病，痛风、痹病、疖痈、皮肤瘙痒症、头部外伤所致的毒邪扩散于陈旧疮疡，索日亚病，水肿，扩散黄水病，肾性伛偻病，麻风病，脉伤，不孕症，死胎不下等多种疾病。特别是对脉道疾病和黄水聚积症有显著效果。但对年老体弱，孕妇，赫依性疾病，癫狂，胃火衰弱，下清赫依失常，呕吐病，月经过多，因外伤异物遗留于体内，遗精，肾或尿道损伤等患者及冬季禁用脉泻疗法。其治疗过程分先导、正式用药和善后调理三个部分。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、先导<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;即在脉泻施治前的准备阶段。先导又为急与缓两种。所谓急者，三日前用小白蒿、酒糟200g煎汤温熨全身，然后用新鲜黄油或植物油、缬草配伍擦涂全身，特别要擦囟门、生殖器两边、关节等处，内服花椒、冬葵果汤，以便启开脉窍。所谓缓者，半月前对所患疾病作对症施治，在所用药剂再加三籽等分煎汤，夜晚内服。儿童患者和肾病患者内服雪鳖八味散，以开通脉道和尿道。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、正式用药<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;分开路药、启病药、斑蝥加工、配方、功能、用法、效果、清脉、反压等九部分。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．开路药：豆蔻、螃蟹、瓦松、冬葵果各等量共研细末，热盛则加冰糖、寒盛则加红糖，以白酒为引内服。开通路窍，以利治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．启病药：硇砂、丁香、螃蟹、银朱、蜀葵各等量，共研细末，热盛则以凉性药为引，寒盛则用热性药为引内服。此药能启动疾病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．斑蝥加工；先察看斑蝥的优劣，然后加工去毒，使之药性柔和，方能启动疾病。斑蝥之优者，色红花白，产自刺丛中，干旱秋季扑杀者为佳品。而体色黄色，产自青蒿丛中，秋后雨季采集者为劣品。斑蝥之毒在硬壳下白色膜翅面上，必须拔去，但斑蝥之足必须齐全，否则影响疗效。炮制参见药物本。常与水甲虫或蝗虫头配伍。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4．配方：斑蝥头足全者十一只，头足不全者用十七只。有传统方：斑蝥七味丸(斑蝥七只、硇砂、螃蟹，为斑蝥量的1／4、银朱0.1g、红花、滑石、硼砂各为斑蝥量的1／8，为了减弱斑蝥毒性，另加甘草、荜拨各0.05g，共研细末，酒调为丸，每丸0.025g)；羚羊角九味丸(羚羊角、鼯鼠骨、牛蒡子、赤瓞子、硇砂、海金砂、螃蟹、豆蔻、银朱各等量，共研细末，制成丸剂)内服，此方可疏通脉道。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5．功效：凡心、肺、肝、脾、肾患病，中毒，黄水病等，皆可在上方的基础上，根据病情增加药物，使疾病从脉道或尿道排出，或与启药共同配伍施治。如肺脓症加天竺黄、地锦草；心脏病加肉豆蔻、广枣；肝病加五灵脂、牛黄；脾病加草果、荜拨；肾病加豆蔻、芒果，配合毒病加钩藤、查干榜嘎、绵马贯众；黄水病加黄水三药、贯众；巴达干包如病加芫荽子、大黄；血症加巴沙嘎、胡黄连；希拉病加木鳖子、京大戟；精华未消化症加光明盐、诃子、干姜、荜拨；痞病用贝齿灰、秃鹫粪；水肿用塔黄、铜屑；肾脉损伤加三红药；陈热加甘松或缬草；眼病加通经草、诃子、铁蛇；耳病加角蒿；喉症或舌肿加蛙肉、狼舌；鼻塞加地锦草；疖痈加狼毒或蓍草；痛风加苦参、川楝子；痹病加阿胶；陈旧性白脉病加珍珠、石决明、甘草；皮肤病加蛇蜕；索日亚、丹毒加六良药、水银；妇科病加牛膝、硇砂、海金砂、螃蟹；麻风病加黑云香、硫磺、水银各5g，则药效更佳。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;6．用法：服脉剂前之傍晚用冬葵叶作菜粥佐餐，薄暮服开路药剂，夜半服启病药，黎明时服脉泻丸7―9粒，并根据病情之寒热，选用药引，一般在日出之际，便见效果。如果没有生效再加服7粒。生效后，各种药物再服两次，每次服5粒，前一种药物消化后再服另一种药物。如此分段服用，斑蝥的毒性一时不会产生，病者病痛不大，尿道并无阻塞。反复连续使用，以助长和持续药效，但服用剂量须依次递减。一旦疾病解除，须立即停用。若诸药一齐服用，斑蝥毒性会发作，同时亦可产生黄水病与尿闭症，疼痛剧烈，危及生命。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;7．效果：药物生效时，身体麻木，发病部位疼痛，下腹有发热感，生殖器灼痛发痒作胀，小便点滴淋漓而出。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;8．清脉：脉泻剂生效后，可频用薄酒一再催激，如势较盛，或将薄酒改为淡茶或酸奶。如为深重之久病，须以引发药与催激法轮流进行。何处疼痛可炒热小石子，乘酒淬热烫熨何处。身体须保暖，不要活动。如身受寒凉，黄水聚积，疾病未除尽时，身体剧痛，不洗脉道，疾病不易根除。因此，洗脉和保温实为重要。施行脉泻时，用器皿承接泻物，若呈观出紫黄色脓血以及茸毛状与细小颗粒状等杂质者，说明药物对症，病邪已被清泻出去，即可停服脉泻剂而继续用药清涤数次，肃清余邪。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;9．反压：反压包括止吐，无效时，要找出原因、导滞留、镇痛、通阻塞、施治过头要抑制等六种内容。 呕吐的抑制法与泻下法相同。服用脉泻剂无效时，应找其原因，如药物剂量不够，斑蝥加工不当，或寒热药引投放有误等，总要找出原因，补救施治。对药物不消化积于胃部，背部涂擦黄油进行按摩，腹部热熨，令饮开水。若小腹和生殖器刺痛者，可热罨施治。如用此法无效，是为黄水滞留之故，可用硇砂、螃蟹、荜拨等量，共研细末，以酒送服。如药后仍无效，则是斑蝥炮制不善所致，用蝗虫头三个、水甲虫、查干榜嘎各5g，共研细末，以白酒调和为丸内服。仍然不能治愈者，乃是赫依之故，须饮羊陈骨汤，当能收效止痛。梗阻多因尿道为精液粘连、绒毛、瘀血、结石等阻塞所致，可用手揉搓阴茎，然后如挤牛奶似地向外挤压。如上法不生效，用硇砂、广木香、黑菖蒲、胡椒共研煎汤，用导管注入膀胱即效。如施治过头而患者身体强壮者，可以毋须施治；如体弱而需要施治者，药用紫草茸、枇杷叶煎汤加熊胆0.5g内服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、善后调理<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;服药后寒性大者。可逐步地进食新鲜糊粥、肥羊肉汤、热粥等；如热性大者，进食瘦肉汤、凉开水、淡味面粥、大米或小米粥等，均勿受寒冷，半个月内需要注意饮食起居。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (667, 1, 7, '泻下疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;泻下法利用药物刺激消化道，通利大便，荡涤胃肠，排除积滞，以促进消化功能的一种疗法。也是祛除希拉病的疗法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本疗法适应范围很广，奏效迅速而明显。且不留后遗症。凡骚热、成熟热、热症扩散、毒热症、六腑热、食积不消、痞病、浮肿、水臌、巴达干包如病、黄水病、麻风病、痛风、痹病、虫病、血、希拉性视力模糊、陈旧性疮疡等。不论发病部位属内属外，均宜用泻下法治疗。尤其对希拉病，疗效更为显著。冬季不适宜使用泻下法的，体质衰弱、年老者、孕妇、赫依性病、胃火衰退、痔疮、下清赫依上逆者、月经过多、呕吐、外伤由于子弹等异物未取出者，均禁用本法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一、服药方法：服药前的准备又有远近之分。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(一) 远的准备：非突发性疾病，宜用汤、散剂药物收敛播散之病邪；聚积的病邪，应先予攻而破之；深窜者诱发；隐伏者揭盖；未成熟者促使成熟，以创造施治之条件。常用药有马先蒿、野玫瑰、黄柏、猪血、蔓菁膏等，可收敛播散之毒邪；姜黄、黄柏、大黄汤，收敛散布于胸部之包如病；紫草茸、茜草、枇杷叶汤加硼砂，可收敛散布于下身之包如病；猪血、土木香、芫荽子、沙棘、冰糖散共服，可收敛散布于全身之包如病；野玫瑰、文冠木、黄柏汤，可收敛扩散性黄水病；服用大剂汤，可收敛扩散之毒热、陈旧热、巴达干、包如等病邪。珊瑚刺皮煎汤内服，可收敛希拉扩散而食物不消者。以上诸症，经用上列药物收敛后。如症状表面为眼睑及面容发白，尿色褐红而浑浊，神志昏沉，身体沉重，饮食不进及口渴等。具体病症收敛特征是：巴达干包如病则身体沉重、口干、胃呆、胃部疼痛，尿脉均呈热象；陈热则身体轻松，胃痛，尿脉呈明显热象；黄水病则胃、肝或发病部位疼痛。出现上述征象，说明病已收敛，此时即可应用泻下法。血痞者宜在初期先放血泄其血热；陈旧性血痞则用炒盐、火硝、秃鹫粪、沙棘子、朴硝、毛茛、芒硝等量，共研细末，连续服用数日，使其破散，然后投以泻剂。希拉性痞块，用黑冰片、石榴、木鳖子加冰糖等量，共研细末，服用数日，待攻破后投以泻剂。剑突痞块用盐(煅烧)、猛制寒水石、光明盐、秃鹫粪等量，共研细末，内服攻破，然后投以泻剂。石痞块用火硝、秃鹫粪、盐(煅烧)、硇砂、硼砂等量，共研细末，内服攻破，再投泻剂。虫痞块用秃鹫粪、盐(煅烧)、硇砂、信筒子、阿魏、麝香、可瓜子等量，共研细末，内服攻破，再投以泻剂。脉痞用甘草、拳参、杉叶藻、秃鹫粪、硇砂等量，共研细末，内服攻破，后投以泻剂。陈旧者常用五味润僵汤冷服，诱发后再投以泻剂。隐伏热则用石膏顺气散或石榴四味散，揭开寒罩，待诱发后投以泻剂。未成熟之疫热则用土木香四味汤，促使其成熟，用当药三味汤使希拉热成熟，用三籽汤使血热成熟，然后投以泻剂。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(二)、近的准备：赫依妨碍下泻，服用泻药前三天，应驱除之。取小白蒿、酒糟渣，浸泡于温水内，洗净身体，如血、希拉热盛者用新鲜黄油，寒性赫依偏盛者用植物油，除腹部以外周身擦涂按摩。赫依盛又不易下泻者，进食新鲜黄油，口含长嘴诃子，赫依成分过大者，再从肛门使用温和导泻，以驱赫依，诱发巴达干与希拉。服用泻药前24小时内，令食不适宜之食物，以诱发潜伏之疾病。如麦、豆类、蔬菜类、猪、鱼肉、酸腐食<br>\r\n      物、酒、牛乳、油饼等，可诱发巴达干、希拉；豆面、豆浆诱发巴达干；陈酒、酸酒、诱发巴达干包如；生冷牛乳可诱发虫病。服药前晚适当进食荨麻菜汤、面、肉、骨汤、温黄油等，这样，既能诱发疾病，又能使腹部柔软，以抑制赫依之势头。如食物或药物中毒，急性未消化病，腹部柔软者，秋季可不作先行准备，而直接服用泻剂。如便秘可在服泻药前1―2日，服用大黄三味汤，以利下清赫依之运行。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、泻药的使用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(一) 先导药物的配方：诃子五味汤(长嘴诃子、亚大黄、塔黄、光明盐、荜拨等量。服上药后，能泻者为软腹，肠鸣者为中腹，无任何反应者为硬腹。根据腹之软硬程度，以决定服用泻药之剂量，如为硬腹而剂量过小，不仅不能达到泻下之目的，反致造成不良后果。如软腹而剂量过大，则泻泄不止，损伤正精。因此，为了诊察病情，必须以先导汤探察之。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(二)主药：包括峻泻剂与缓泻剂两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;峻泻剂为舵手猛烈四味丸，酌情内服。本方药性锐利猛烈，适用于体壮邪实之一切疾病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;缓泻剂为十味舵手膏，本方药性温和，药力不疾不徐，服后无恶心、腹胀等反应，亦不致引动赫依，无伤正精元气，冬季、年老、体弱患者，即使在旅行中，皆可应用本方施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(三)、加味：以上峻泻和缓泻两方为基本方药，然后按疾病之情况，辅以下列药物。药量为泻剂的三分之一，有三十种。如治心热症加白檀香、沉香、广枣；治肺病，加硼砂、甘草、葡萄、茵陈；治肝热症加红花、巴沙嘎、五灵脂；治脾病加芒硝、灰盐、荜拨；治肾病加冬葵果、豆蔻、芹叶铁线莲；治胃病加光明盐、土木香、荜拨；治希拉病加当药、木鳖子、地梢瓜或止泻子；治大、小肠病加叉分蓼、茜草、紫硇砂；治脉病加斑蝥、螃蟹、白云香；治头部疾病、脉病加芹叶铁线莲、石龙芮、斑蝥；治上半身疾病加拳参、熊胆、冬葵果；治下半身疾病加硇砂、螃蟹、豆蔻；治皮肤病加野玫瑰、芹叶铁线莲、苘麻子；治头部疾病加紫菀花、鼠麴草、芒果核；治眼病加通经草、诃子、铁屑；治热病加冰片、白檀香、牛黄；治寒病加荜拨、光明盐、胡椒；治寒热并发症加诃子、栀子、川楝子；治黄水病加黄水三药；治毒症加钩藤、查干榜嘎、绵马贯众；治巴达干包如病加柿子或酸梨干、芫荽子、川木香；治痞病加芹叶铁线莲、石龙芮、石菖蒲；治水肿加黎豆、水柏枝、斑蝥；治呕吐加山豆根、硇砂、肉豆蔻；治肠胃胀满加胡麻、青木香、桶酥；治赫依上行返及肠鸣加肉豆蔻、荜拨、胡椒；治虫病加蔓荆子、花椒、可瓜子；治痛风加红花、芹叶铁线莲、苘麻子；治肌、脉、骨痈加木香、硫磺、硇砂；治子宫病加茉莉花或瓜蒌根、赤瓞子、芒硝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;此外，还有治疗泻药副作用的七种秘诀配方：治食物不消加芒硝；需保胎加茉莉花；治呕吐加塔黄；治催泻过量加黄连；治恶心并使之迅速泻下加海螺灰、硇砂、水银；治口渴加冬葵果；防止水肿加芥菜子。根据三十种加味配方，无疑能医治对治的疾病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、洗 肠：服用泻药后，要洗胃肠。如泻下2～3次后，患者自觉胸满消失时，应少量多次饮用开水或光明盐水，以助泻下。但不可过早过迟，过早饮用可引起呕吐，过迟则病邪不能排出；赫依偏盛患者服用骨汤；体弱患者服用红糖水；热邪成熟而亢盛者可饮冷水；黄水病及陈热诸病，宜服用文冠木煎汤；泻泄缓慢时宜服用尖嘴诃子煎汤。如在催泻中，患者体力减弱，神志不清，频频空呕者，是赫依骚动之，可在赫依穴处涂擦黄油进行按摩，并用黄油红糖燃火烟熏，内服骨汤抑制赫依势头。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;四,泻下程度：下泻时注意观察泻次、泻出物的数量、颜色。下泻次数，以三十次为最宜，二十次为中等，十次次之。泻物的数达三升者最好，二升为中等。颜色清稀如水者为最佳，象痰液为中，黄色者为下等。以上只是一般标准，至于应否予以继续催泻，还根据患者的病势和体力而定。如体力强壮而病邪未尽者，可以继续洗肠催之。病虽未除而正精已受损耗者，应即停止催泻。泻物如清水者亦应止泻。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;五、辅助疗法：辅助疗法包括镇呕逆、收敛、导迟泻、止泻利过度、维护生命等五种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[镇呕逆]：服泻药后欲呕吐者，用冰凉石冷敷颈下天突穴，并鼻吸香味及揪拉头顶之发，冷水喷激面部，按压两肩，不使患者摇晃。引起呕吐之原因是，由于药性粗糙，剂量过大，未抑制赫依所致；或因患者厌弃服药，胃中不和，饮食不进；或说话过多，或服药后进行活动等引起。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[收敛法]：服泻药后不吐不泻，无任何反应时，其原因为药分散和病分散两种，应予收敛。药分散是服药后无任何感觉，原因为药力不足及病分散时出现肠绞痛，宜用酸性酒温服。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[导迟泻]：服药后胃部胀满坚硬，不作泻泄，应温熨胃脘，然后使患者散步，服用光明盐汤，如不见效，可用猛烈导泻施治，同时内服金腰草、水柏枝等分煎汤内服，服后可能发生吐泻，但可将胃中病邪驱除无遗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[止泻利过度]：服泻药后泻泄不止者，有因寒、因热、药力滞留、病已被引出等四种原因所致。热性者物色红黄，作恶臭，尿、脉均显热象。可进食冬葵果煎汤、酸牛奶、茶、炒大米等；并可在小腿部冷敷、饮紫堇汤止泻。寒性者，泻泄不止，泻物无味，呈水样。宜用肥肉汤或黄油中加五味子、桂皮煎汤止泻。药力滞留时，宜服光明盐煎汤。病已引出时，不需用药止泻，只宜培补正精。但对胆脉开放所致者，宜用止泻子或地梢瓜四味汤加熊胆0.3g止泻。以上四种泻泄过度之饮食调护，以加红糖、黄油的大米粥或鲜肉汤为宜，并在肥肉汤调入兽骨或兽皮之胶、五味子、胡芦、橡实、茯苓、车前子各等分，少量频服。同时应禁忌茶水及酒等各种饮料。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[维护生命]：服药后发生生命危及者，有五种原因，即对零散赫依未行压制，药不对症而发生刺痛；服镇吐药太过，而使泻药逸入肺脉；药物激起虫动，转化为痧症；生命寄于病，病泻出而命随之而去。以上五种原因，均可危及生命，应提早预防。零散赫依未被抑制时，出现全身不适、空呕，体力虚惫，神志昏迷等症状。可用黄油、肉类等燃烟熏之，并用黄油涂擦按摩，服用骨汤、牛奶、黄油，火灸赫依穴施治。药不对症是由诊断与治疗错误，未掌握应泻与不应泻之时机，药物与病相错而驰之刺痛者。此时须分析其寒热属性，治以温凉药剂。对热刺痛宜用土木香、广木香等分，煎汤凉服，并施以冷罨。寒刺痛宜服光明盐四味汤，芝麻渣热罨。服镇吐药太过，致使泻药逸入肺脉时，出现剧烈咳嗽，胸部刺痛，宜用沙棘五味散，肺脉放血，然后服石灰华十三味散或能吸干肺脓之药物。虫动转化成痧症，由于服泻药而致体内寄生虫蠢动而引起腹部剧痛者，宜用信筒子四味方以杀虫。生命寄于病：体弱之患者，由于不能承受猛烈之泻药，病虽去而生命亦将随之而逝。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[善后调理]：催泻施治后，应进食大米粥或面糊，补益身体。以后逐渐增加肉汤、热面。要分清寒热两种情况。热余邪又分新旧两种。骚热、疫热属新的余邪，进食面汤、米粥、掺水乳酪等凉性食物断除余邪。陈旧余邪，进食凉性鲜肉、无脂肉汤、茶水、凉开水、乳酪等凉性食物断除余邪。寒性疾病进食新旧羊肉汤、淡酒、黄油、热面等温性饮食断除余邪。饮食必须逐渐增加，热性疾病如果用热性饮食断除时，会产生余热；寒性疾病如果以凉性饮食断除时，会产生不消化病、水肿、痞病等。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (668, 1, 7, '催吐疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;催吐为利用引吐作用的药物来刺激消化道，通过呕吐祛除毒物的一种疗法。凡属食积不化，未消症、食痞、剑突痞块、铁垢巴达干、中毒症、痧症、血、希拉降于胃，赫依、希拉性头部疾病、灰巴达干、饮食不进、巴达干包如降于胃中等症，均可用催吐法治疗。对于正精衰弱，赫依型体质者、年老、幼儿、孕妇、眼病障翳、尿闭、虫病、痔疮、肉毒扩散、白脉病、心脉病，皆不可使用催吐药剂。催吐之药物配方分急与缓两种。催吐法之先导与催吐大致与泻下法相同。但急性消化病、由饮食不当所致的巴达干、希拉偏盛而郁结于胃内、巴达干包如、巴达干希拉亢盛、或常食油脂而腹中有油脂者，可不用先导药物施治。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、 配方<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;有主药与加味药方两种。在春季发芽时采集的肾叶橐吾、刺参、藜芦为主药，与菖蒲、光明盐、荜拨各等量配伍。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;加味：治不消化症，主药加山奈，合并赫依病加娑罗子；合并希拉加丝瓜子；痞病加芹叶铁线莲；中毒症加草乌；胸部化脓症加地锦草；肺病加甘草；巴达干病加寒水石、大黄等。加味药每服加2～5g。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、用法：根据病情及患者的体质强弱，恰当地使用急缓两种催吐药，缓催用汤剂，急催用丸剂。服药时间以黎明为宜，服后漱口，可讲话，切勿着凉，须蹲坐。服药后欲吐时，不可即行吐出，宜抑制回吞数次，然后方可吐出，使病邪得以全部清除。但回吞一般以三次为宜，不可过甚，否则将有病邪下陷之弊。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、洗胃：洗胃取刺参、肾叶橐吾、藜芦、光明盐、菖蒲、荜拨煎汤温服，用翎毛或手指搔喉头催吐。催吐的程度，要取决于洗胃的需要。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;四、催吐量：一般催吐次数为四次、六次、八次；呕吐物质数量为半升、一升、两升；颜色以水样液为上等，胆色为中等，痰样液为下等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;五、辅助治疗：服药后出现赫依扩散、胆汁逸失，包如渗漏、肺脉决破以及药物精华窜入于目等五种反应症候，则需进行辅助治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;赫依扩散时，头痛、恶心、神志模糊、眼花、耳鸣、身体沉重、空呕等，治宜临睡前取新鲜黄油加肉豆蔻散涂于赫依穴，并进行按摩、药物熏疗、饮踝骨汤等，抑制赫依之势头。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;胆汁逸失时，吐物胆黄色，治宜当药五味汤(当药、木鳖子、止泻子、熊胆、山苦荬等分)或红花三味汤或熊胆三味汤内服，同时短翅脉微量放血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;包如渗漏时，吐出血液、胆汁及烟色状液。治宜以亚大黄叶、水底石．、铧犁进行冷敷，内服芫荽子、红花、石灰华、熊胆、射干，共研细末，冷开水送下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;肺脉决破时，咳血，治宜急取熊胆、银朱、豌豆花、大黄、冰糖，共研细末，用紫草茸煎汁冷却后送服，或用三红汤送服。药物精华窜入目时，出现头疼、目赤，治宜在鼻尖或金柱穴、银柱穴放血并在头部喷激冷水。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;六、止吐及善后处理：为了使余药吐出，用黑云香、香附与新鲜黄油配伍熏疗施治，同时服用光明盐水。<br>\r\n      以上催吐治疗，如行之恰当，术后五功能明显恢复，身体轻快，精神振奋，食欲增进，口味香郁，鼻涕及痰液等排泄物清洁不浊。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (669, 1, 7, '鼻药疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;鼻药疗法将特制的药物，注入鼻腔，通过鼻粘膜吸收，达到治疗目的之一种疗法。鼻为脑之门，滴鼻施治，对锁骨以上的头部疾病，效果明显。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;鼻药疗法分为平息与清泻两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[平息药]：红花5g、融黄油50ml，冰糖10g配伍制成。适用于头部赫依血相搏、头刺痛；用甘草、栀子、冰糖、融黄油配伍制成。适用于耳病、眼病、腮部疾病及脑病；萝卜汁滴鼻，可治耳病、头痛；蜀葵花根、侧柏、桂皮、豆蔻、鼠麴草、天竺黄、光明盐，共研细末，水煎取汁滴鼻腔，适用于热性音哑症；融黄油滴鼻，可治咽喉以上诸疾病；红花用黄油调和放入鼻腔，主治鼻干发热；红花、栀子、贝齿灰，共研细末，吸入鼻腔，治鼻腔化脓、堵塞；蟒缎灰、贝齿灰、红花、熊胆，共研细开，水调后放入鼻腔深部，堵住鼻孔，可止鼻出血。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;用法：一般在早晚滴鼻。用药期间禁忌烟熏、风吹。瘟疫初期、突发性外伤、酒醉、食油脂后，均禁用本法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;[清泻药]：适应于感冒历时较久而渗入喉，白喉、炭疽热邪道于脑者，目赤、麻风、面部黄水病、头部外伤热邪而致的黄水下降等诸症。配方分峻泻剂与缓泻剂两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;峻泻剂为查干榜嘎5g、贝齿5g、山蓼2.5g、山奈2.5g，共烧成灰，病势大则另加斑蝥三只，硇砂 \r\n      1g，用童尿调和滴鼻。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;缓泻剂为地锦草五味方(地锦草、硇砂、菖蒲、木香、甘草各10g，共研细开，水调)；又方：姜黄、三颗针根、光明盐、斑蝥、腊肠果、甘草、藜芦、桔梗、白云香各5g，用童尿或山羊奶调和。用法：清晨滴鼻，令患者擤净鼻涕，清洁鼻腔，并在额部用炒热之面粉进行温敷。滴九滴，不易滴入者可用烟或桔梗粉吹入鼻腔少许，然后擦净鼻腔，如饮用开水或芸香、鲜黄油焚烟熏之。凡属赫依病所致之头晕、眼朦胧症、倒睫、牙病流脓血者均禁用本法。施治后流血者，在头、胸部用凉水冷敷，内服止血药及选用火灸和放血疗法。如药效未达到则可见目赤及头额刺痛，应取鼻脉、额脉放血，然后再在头部喷冷水。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (670, 1, 7, '油脂疗法', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;油脂疗法系采用油脂类制剂治疗疾病的方法。本疗法具有抑赫依、补阳、润肠，有能使感觉器官清明、强壮身体、延年益寿的作用。临床用于年老体弱，身体消瘦无力，忧虑过度，营养不良，失血过多，精液枯竭，赫依偏盛，视物模糊不清等疾病。对胃火衰弱、不消化病、腹泻、痛风、痹病、珍宝药物中毒、恶心呕吐、烦渴等巴达干、希拉偏盛性患者，都禁忌用此疗法施治。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;油脂疗法使用的油脂有黄油、芥子油、芝麻油等动植物油和脂肪、骨髓等。黄油延年益寿，增强记忆，健胃强身；植物油有滋补、润肠，抑赫依的功能，对腺病、巴达干病、虫病、肥胖症等有治疗作用；脂肪对关节疼痛、烧伤、腹痛、子宫病、脑病、耳病等有治疗作用；骨髓抑赫依，补精，增生巴达干，对产后劳累及房事过度，体质耗竭，脉道阻塞有疗效。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;具体用法是根据疾病的性质、部位及时令，选择油类品种，用于涂擦、内服、点滴、罨疗温和导泻等疗法。有总疗法与具体疗法两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(一)总疗法：对皮肤肌肉及头部赫依病，可在发病部位涂擦，小、大肠赫依症可作温和导剂施治，耳化脓可滴耳，口腔、咽喉、舌、齿等疾患可将黄油温热的滴于患处，前额部、眼睑疾病，可将油温热后滴于鼻腔；营养不良者可内服，剂量应视其消化吸收能力而定。经常进食脂肪性食物的人，须先进食荞麦面、糜子米等粗粝的食物，然后再使用油制剂，饮食粗粝，营养不良者，可在油内先加少量食盐开水冲服。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;(二)具体疗法：空腹时用药油，能延年益寿，增强胃火，增强记忆力。若为助胃火，温肠腑，使精神焕发，应服用黄油。赫依偏盛，腹部硬者，可服植物油；产后体衰，劳累过度，应服骨髓；创伤、烧伤、骨节疼痛、子宫病、耳病、脑病，应服陈旧脂肪。此外，冬季宜用黄油，春季宜用骨髓，夏季宜用植物油。用油疗导泻时，应在黎明时用融黄油；儿童、老年人、胃火衰弱、体弱、烦渴及习惯进食油腻者，不能单独使用脂肪性食物，应配合肉汤、大米、蜂蜜、红糖、黄油等共同服用。疾病在身体上部者在饭前服，在下部者饭后服，在中部者与饭共同进食。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在施行油脂治疗法期间，禁忌进食酸腐、生冷饮食。避免过劳、忧虑、骑乘，着凉、烟熏、房事等。应食用大米粥、面汤等性轻、温而容易消化的食物。油治过量，会出现呕吐、下泻、食欲减退等情况。如春季夏初服用植物油时，容易诱发巴达干与希拉性疾病；冬季服用脂肪过量时，由于身体寒冷而不易消化。抑制以上副作用的方法是：使病者饥渴、热敷、催吐而后进食豌豆、大麦、陈酸奶、酒、开水、浓茶等，并可选用六味安消散、益胃诃子十味散、光明盐四味汤等方。</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (671, 2, 53, '蒙药学专著《白晶鉴》简述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;蒙药学专著《白晶鉴》简述 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医学院蒙医系 赵百岁 宝音图 玉杰<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;成书于18世纪的《白晶鉴》是由著名蒙医药学家伊喜巴拉珠尔用藏文撰写，是现存最早的一部蒙药学专著。<br>\r\n      伊喜巴拉珠尔是一位著名的医药学家、文学家、佛学家、天文学家和历史学家，他一生中著书甚多，共撰写八卷71部、289万9千字的著作。其中医药学方面的书籍有《甘露之泉》、《甘露医法从新》、《甘露点滴》、《甘露汇集》和《白晶鉴》5部。《白晶鉴》是其中唯一一部蒙药学著作。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《白晶鉴》(又称为《药名种类及认药水晶鉴》)的藏名叫《西勒嘎尔米龙》，全书共100页。该书于 1786年在青海郭隆寺首次用藏文木刻版印行，之后在内蒙古呼和浩特乌苏图召寺也曾藏文木刻版印行。到了19世纪，该书首次被译成蒙古文，并以蒙文体手抄书形式流传于民间。在20世纪60年代，内蒙古锡盟蒙医研究所进一步系统译成蒙古文，并以《蒙药常识》之名印行于内部。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《白晶鉴》是根据《甘露之泉》中出现的药物内容分为药名种类、药物的形态特征及性味、功能的说明以及秘诀精略三部分，做了进一步解释和详细阐述。其主要特点归纳如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 药名种类<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药名种类中包括了药物的简称及各药名称，这是该书的一大特点之一。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 药物简称：蒙医经典著作中以往多数用简称来简略几种药物或十几种药物的名称。这种方法虽然对掌握药名及药性具有简便易记等优点，但对初学者或自学者而言，这种简称就成了学习和掌握蒙药学的难越的障碍。因为古时医学传授非常保守，无论从师徒间的知识和经验传授，还是经典著作中文字叙述，在关键、重要内容上都用秘密术语来概括。药物简称就是带有这种神秘色彩的名称。为了揭开药物名称上的神秘面纱，伊喜巴拉珠尔在《白晶鉴》中系统详细解释了药物简称多达175个，涵盖了475味药物。这对后人正确掌握和应用蒙药起到了非常重要的作用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 各药名称：由于历史的原因，蒙药名称繁多，容易导致药物名称的混淆，尤其在药物的梵名和藏名上更易产生混淆。伊喜巴拉珠尔在严格考证的基础上，在《白晶鉴》中详细列出了475种药物的梵名和藏名。《白晶鉴》按传统分类方法把全部药物分为8大类，并逐一做了梵名和藏名的对照。其中：①珍宝类药物37种，包括可熔性22种、不熔性15种；②石类药物21种，包括可熔性6种、不熔性15种；⑧土类药物8种，包括天然土类药物和人工土类药物各 \r\n      4种；④汁液精华类药物10种；⑤树类药物145种，包括果实类药物45种、花类4种、叶类3种、杆类6种、枝类9种、树皮类9种、汁液类12种、全树类(即根、叶、花、果，单用或两用或三用类)66种；⑥草类药物111种，包括根类12种、叶类15种、花类20种、果实类15种、地上部分类(即叶、花、杆、果实同用类)26种、全草类(即根、叶、杆、果实、花同用类) \r\n      23种；⑦盐类药物10种；⑧动物类药物(包括食用部分)133种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 饮食名称：《白晶鉴》中载人食物类20种；饮料类18种。蒙医饮食类的梵名、藏名如此详细对照列出，在历代书籍中尚属首次。这对蒙医饮食营养学的名词术语的规范和学术研究具有重要参考价值。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 秘诀精略<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《白晶鉴》中对药量和药引的名称、放血和艾灸部位以及脉结的有些名称做了对照解释。其中：①根据不同地区量衡制进行了梵名、藏名对照解释的同时，还对不同地区量的概念及药量换算方法也做了说明；②药引是蒙医治疗疾病的特殊药物。《白晶鉴》中列出红糖、白糖及冰糖3种药引的梵名和藏名的同时，还指出了散剂的别名；③在《白晶鉴》中补载了在《甘露医法从新》中未记载的54种放血和艾灸部位以及脉结的别名。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 药物的形态特征及性昧、功能<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药物的形态特征及性味、功能的阐述是《白晶鉴》的重要内容，占全书的一半。《白晶鉴》把药物分为珍宝类药物、石类药物、土类药物、树木汁液精华类药物、真树类药物、草类药物、动物类药物等7大类，把该书各药名称中所分的盐类药物归人土类药物，而《四部医典》中的单味药物类归属于树木汁液精华类药物。故《白晶鉴》的药物分类方法也自成体系了。<br>\r\n      《白晶鉴》共载371种554味药物，从药物的形态特征、药物来源、生境、药物真伪鉴别、入药部位、性味功能等诸多方面进行了严格考释和详细阐述。其中：①珍宝类药物，共9种29味药物；②石类药物，共40种57味药物；③土类药物，共11种19味药物；④树木汁液精华类药物，共51种82味药物。该部分内容中还简述了树木汁液精华类药物的分类方法，如树木类药物分根、杆、枝、枝梢、树心、树皮、树汁、叶、花、果实等10种，汁液类药物分根、茎、叶、花、果实等5种，精华类药物分为草、树、动物等3种；⑤真树类药物，共42种70味药物；⑥草类药物，共123种214味药物；⑦动物类药物，共13类83种，包括角类9种、骨类15种、肉类10种、血类6种、胆类多种、脂肪类4种、全身类10种、脑类4种、皮类3种、趾甲蹄子类4种、羽和毛类6种、尿类2种、粪类9种。这种药物分类方法具有一定的科学性，迄今在天然药物分类、植物分类学的研究中仍有很重要的参考价值。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;此外，该章节中还重新介绍了《论学医典》、《秘诀医典》、《后续医典》中出现的药物106种；其他典籍中选择叙述了12种药物；特殊选释了5种矿泉；补充阐述了其他食物和药物128种318味。故该书实际介绍了622种998味药物的性味、功能，这对丰富和发展蒙药学内容起了极其重要的作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《白晶鉴》是目前所发现的最早的一部蒙药学专著。该书被认为是蒙、藏药学经典著作的《晶珠本草》(即《无垢晶串》)和《无误蒙药鉴》还早100多年。著名蒙医药学家博尔之斤氏・占布拉道尔吉在撰写《无误蒙药鉴》时曾大量参考了该籍，说明《白晶鉴》是历代蒙药学家必读、传承的经典之一。纵观全书内容，我们不难发现《白晶鉴》在药物的简称、各种药物及饮食的众多名称(别名)，以及药量在不同地区的命名和换算方法等都做了详细的阐述和说明，尤其在药物的形态特征及性味、功能的阐释对今后学习和研究蒙药学都具有重要的参考和借鉴价值。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (672, 2, 53, '《甘露四部》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;《甘露四部》<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《甘露四部》是18世纪蒙古族医学家伊希巴拉吉尔（1704-1788）在50余年医疗、医学研究生涯中陆续创编的《甘露之泉》、《甘露医法从新》、《甘露点滴》及《甘露略要》等四部医药学著作的简称。原著为藏文木刻版，书名音译为《都德泽西》（意为甘露四部）。后由干朱・巴拉登等译成蒙古文（年代不明），早年以手抄本流传，到60年代，由内蒙古锡林郭勒盟蒙医研究所重新整理，与原文核对后铅版印刷，内部发行，书名为《蒙医简易治疗手册》。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;这是至今所发现的最早的具有蒙医特色的民族医药学系列专著。伊氏将《四部医典》的理论创造性地同多发寒症之蒙古地区的特点及蒙古传统医学治疗“寒症”经验加以结合，把“寒症”、“热症”两章列于“十要症”之首，并写了“寒热相搏”、“寒热相互转化”等数章，丰富和发展了“寒热症理论”。另外，本书对人体生哩、病理、诊断、治疗原则和治疗方法等，从理论上作了精辟阐述。并提出“在‘赫依’病、‘协日’病、‘巴达干’”病之上，加血病、“黄水”病和虫病三者则为六基证。还附加了腹泻剂等七种疗法和温泉浴等五种疗法。因此，《甘露四部》可以说是最早的蒙医药学经典著作之一。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (673, 2, 53, '《兰塔布》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;《兰塔布》<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;又名《门阿兰塔布》，是一部具有巨大影响的临床学专著，是《四部医典》之后，在藏医历史上出观的另一部伟大作品。1691年由著名藏医学家第司・桑吉嘉措编著。它基于《四部医典》，高出《四部医典》，使藏医临床学发展到新水平。因此300多年来一直被蒙藏医学界视为经典著作而努力学习，深入研究。18世纪中叶与《四部医典》一起也被译成蒙古文，广泛流传于蒙古地区，至今影响着蒙医学术界。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《兰塔布》在许多方面均有创造性发挥，记载了许多常见病、多发证，使理论紧密联系实际，更加实用。依据作者本人及他人的临床经验，从病因病机、辨证分类、症状、治疗方法等几方面都作了较大的改进。据统计《兰塔布》所载新病种约达35种之多，例如：索龙，察龙多桑，伯格，粘头痛，粘胸痛，粘腹痛，肠疚，腰疝等。方剂学方面的发明创造也是引人注目的，记载的新方很多。如25味犀角散、28味犀角散、25味铜灰散、 \r\n      43味铜灰散等至今流传很广。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;它提出的粘证和伯格病理论，对瘟热病学科来说是个突破性贡献，对整个藏蒙医学是个巨大的推动。<br>\r\n      《兰塔布》认为粘症就其本质而言，可分为黑粘，毒粘，平粘3种。就其所中部位而言，可分为头痛粘，胸痛粘，腹痛粘…等 18种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;它是由于饮食起居的不当或气候的突变等原因，侵入人体的致病小虫与体内固有的虫体合并后吸营养、乱体素而成疾，治疗时需专方专药，冲阿主治，此方具有杀粘解毒效果等。并将18种粘病逐一独立成章，进行了详细论述。在藏蒙医学史上正式确立了粘证的一整套理论，极大地丰富了藏蒙医学。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;它认为伯格病是皮肤粘膜出现丘疹为主的瘟疫，相当于现代医学的麻疹、猩红热、风疹之类疾病。是血黄水相搏之际粘热兼夹所致。据此进一步分类为黑伯格、白伯格、花伯格 \r\n      3种。一般情况下伯格病出现发冷发烧、头痛、骨关节酸楚、咳嗽、眼红、全身皮肤发红出疹、脉数尿黄等症状。主张按其分类性质进行治疗。因而黑伯格用邦吉朱尼；白伯格用马奴西汤和巴布敦朱；花伯格用18味水银丸等；<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;内科是《兰塔布》的主要内容，所占比例很大，因而《兰塔布》的大量、有效的工作都体现在内科疾病方面。<br>\r\n      &nbsp;&nbsp;&nbsp;《兰塔布》认为感冒之类疾病初期表现的寒热往来、头痛、全身骨关节酸楚、失眠、喜暖怕冷等症状是气与血相互交搏所致，并称为“气血交搏之明证”。气(指龙)为三素之首，具有善行而数变的特点，血为七亢之一，有炎热而尖锐之性能。二者易因气候突变，受风受寒而互相交搏。因服加味熬日布敦汤治疗，即热盛者加细辛，疼痛者加马钱子。此外还可用15味沉香散，35味沉香散等方剂。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;如果气血交搏而引起喘息证时主要表现胸部刺痛，内气壅滞而躬背发难、气急胸憋、烦燥等症状。这包括了现代医学心绞痛、心肌炎、心肌病之类疾病。对这个气血性喘证《兰塔布》主张用朗8味沉香散治疗。现证明此方临床效果颇佳。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;遗精是《兰塔布》创造性总结整理的病种之一。《兰塔布》认为此病属于肾病范畴，其寒证与肾寒有关，其热证与肾热有关。因而单独将其标题醒目的立为“隶属肾病的遗精”病。治疗时据此理论主张采取标本兼治方法。一方面用6味蜀季汤，熬日布旺吉拉等固涩止遗类方剂治标，另一方面还要用补肾壮腰类方剂治本，如果与肾寒有关的配以苏格木朱瓦；与肾热有关的配以汗马阿如朱瓦等。由此可见《兰塔布》认为肾与遗精病密不可分．肾功能正常则生殖系统功能正常，如若生殖系统功能异常，患有遗精、阳萎、不育或月经不调等类疾病，证明肾功能有了病变，应予补肾壮腰的治本疗法，以达到恢复肾功能，治愈生殖系统疾患的目的。这就是《兰塔布》注入藏蒙医学的肾病理论，为蒙医脏象学说的建立提供了有力的依据。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (674, 2, 53, '《蒙医金匮》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;《蒙医金匮》<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《蒙医金匮》 于1829年，蒙古族医学家民若勒・占布拉却吉丹增璞仁来用藏文编著。藏文书名音译为《满阿嘎仁钦纵乃》（汉意为“医诀之海”），曾汉译为《方海》、《医法海鉴》等。1978年由内蒙古人民出版社组织多方专家蒙译，并出版。汉译名为《蒙医金匮》。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是一部较为完整的蒙药方选集。收载有内、外、妇、儿、五官科及传染病、热病等临床各科医疗的200种药方，涉及1700余种药物。作者还结合蒙古地区的实际情况，刨造性地发展了《四部医典》的理论。例如，把“赫依”病的病变分成三型，论述了治疗各型的大、中、小三种滋补剂的应用及功能；并分别阐述成熟热和炽盛热，以及94种眼病及其疗法、配剂等。提出了应用酸马奶治疗瘿病等新观点和方法。为后来蒙药方剂学的研究提供了依据。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (675, 2, 53, '《蒙药正典》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;《蒙药正典》<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;这是19世纪内蒙古奈曼著名蒙古族医药学家占布拉道尔吉所著的一部蒙药本草。原文为藏文木刻版本，音译名《柴测尔・米格赞》（意为秀丽的景色），汉译名《蒙医本草图鉴》、《本草图鉴》、《蒙药图鉴》等等，但最常用《蒙药正典》作为译名。1965年，由老蒙医根敦扎拉森译成蒙古文，于1987年由内蒙古人民出版社出版。1971年被译成英文，1985年被译成俄文。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;这是一部比较完整而惟一附有图解的蒙药本草经典。该书纠正了药物的误认和错名的混乱现象。全书共收集了879种药物，附有599幅插图，按药物来源分八部、二十四类。对每一种药物尽可能注明了蒙、汉、印、藏、满等文药名，指出了生长环境，叙述了形态、入药部分、采集时季、炮制方法和性味、功能、主治、用法等；还介绍了真伪优劣品种及其区别，药物禁忌和某些药物的鉴定方法等等。在原版中附有墨线图，并用蒙、汉、藏、满四种文字对照注名。该书中引用了大量古今中外参考文献内容，是研究和学习蒙药学不可缺少的历史范本。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (676, 2, 53, '《认药白晶药鉴》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;《认药白晶药鉴》<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;著名蒙医学家伊希巴拉吉尔在18世纪中叶用藏文所著的一部蒙药学专著，即蒙药本草。藏文名《西勒嘎日・莫隆》（意为白晶鉴）。汉译《识药晶鉴》，或《认药白晶药鉴》。60年代，由内蒙古锡林郭勒盟蒙医研究所重新整理，命名为《蒙药常识》。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;本书是最早的完整蒙药本草。它收录了在《四部医典》和《甘露四部》中出现的药物共801种，根据药物来源进行分类，对每种药物的药名、别名、简名用藏、梵两种文语对照，集中介绍了药用动、植、矿物及药材性状、生境、性味、功能及药用部分，也指出了部分药物的混乱品种及其鉴别方法。书的后半部还附了药引子、用药剂量、饮食种类等有关药剂知识和部分蒙医放血点及针灸穴位。它为蒙药学的分科和发展奠定了基础。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (677, 2, 53, '《四部医典》', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;藏医经典文献《四部医典》述要<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;陕西省中医药研究所 \r\n      苏礼 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;成书于公元八世纪下半叶的《四部医典》，是我国藏医学的经典著作。作者宇妥・贡布（即宇妥・宁玛元丹贡布，708―833年)是古代西藏地区著名医学家。他青年时被选拔跟随从大唐定居西藏的名医东松嗄瓦学医，后被吐蕃王朝第五代藏王赤松德赞任命为首席侍医。他善于学习，曾多次到祖国内地以及临近的印度等国家学习医药知识，同时勤奋实践，积累了奉富的医疗经验。在广泛汲取《月王药诊》、《紫色王室保健经函》等藏医典籍精华的基础上，经过二十多年的艰苦努力，终于著成了这部藏医学的巨著――《四部医典》(一译《医方四续》，藏名《据悉》。到公元十一世纪，经老字妥的后代新宇妥重新编纂增修，广泛流通，流传至今。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《四部医典》由四部组成。第一部为“根本续”(藏名“扎据”)，为总论部分；第二部为“论说续”(藏名“协据”)，讲述人体生理解剖、病因病理，发病途径、治疗原则以及外科医疗器械等；第三部为“秘诀续”(藏名“门阿据”)，论述各种疾病的分类和治疗；第四部为“后绪续”(藏名“亲玛据”)，论述脉诊、尿诊等诊断知识，药物功能，针灸以及艾灸、放血、外敷等治疗方法和技术。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《四部医典》总结了藏医学的基本理论和具体临床实践，其中包括生理解剖、病因病理、临床各科、诊断治疗、药学知识等，充分体现了藏医学的独特内容和民族特色。在生理方面，《四部医典》认为，人体包括三大因素，七种物质，三种排泄物，“三大因素”即“龙”主呼吸，“赤巴”主身体的热能，“培根”主体液；七种物质即血、肉、脂肪、骨、髓、精、饮食精微；三种排泄物即大便、小便和汗液。《四部医典》中还指出，妇女怀孕的孕期为三十八周，其胚胎发育则要经历鱼期(水生动物)、龟期(爬行动物)及猪期(哺乳动物)。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在解剖学方面，《四部医典》把人体脏腑分成心、肝、脾、肺、肾五脏；胃、肠、十二指肠、胆、膀胱、三姆体(类似中医所说的“三焦”)六腑，全身有骨360块，牙齿32枚。在脉络方面，把连接人体内外的“白脉”、“黑脉”通称为“联接脉”。认为脑为白脉之海，白脉象树根一样，分布全身，主持感觉和运动，大致相当于神经系统；黑脉属于医生放血治疗的某些部位，实际上是指静脉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;问诊、望诊和触诊是《四部医典》中所介绍的藏医三种主要诊断方法。问诊主要询问患者的疾病起因、发病时间、患病部位、症状、用药情况、饮食起居等；望诊则包括观察患者的神色、体型、痰、大小便、舌质、舌体情况等。一般来说，舌质红属于“龙”型，舌苔黄垢属于“赤巴”型；舌苔白滑属于“培根”型。藏医用观察尿的色泽、泡沫、沉淀、臭味等做为诊断依据，也颇具特色，称之为“尿诊”。触诊主要是切脉。切脉所用的部位在腕后高骨下约一寸左右或稍后一些，称之为“寸、甘、恰。”其脉象有浮、沉、迟、数、洪、弦、滑、细等说法，和中医之脉诊颇为接近。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《四部医典》把藏医的治疗方法分为饮食、起居、药物、外治四大方面，一般小病轻病，都可用调整饮食、起居的方法予以解决。而对久病、重病则需采取药治或外治法予以治疗。《四部医典》收载了465味药物，443个藏医方剂，藏医所用的药物以西藏当地所产的动、植物、矿物药为主，剂型以散剂和糊丸剂为主，汤剂使用较少。从《四部医典》有关记载来看，藏医的外治法丰富多采。诸如以药水擦身、按摩、拔牛角罐、艾灸、火灸、热敷、冷敷、药物薰洗、腹腔穿刺、静脉放血等等，从而适应了多种疾病的治疗需要。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《四部医典》为藏医学体系的形成奠定了基础，又是历代学习藏医学的必修课程，在国内外有相当大的影响。宇妥.宁码元丹贡布也因之被誉为藏医学的“医圣”。 \r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (678, 2, 53, '《四部医典》中的医德观', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;《四部医典》中的医德观<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学 包龙 满都拉 \r\n<br>    \r\n<br>&nbsp;&nbsp;&nbsp;&nbsp;藏医学经典著作《四部医典》约成书于8世纪。这部集基础理论与临床各种疾病的诊断治疗为一体的巨著，在传授医学知识的同时重视医生医德观的树立，就其这方面内容而言，从医生应具备的素质到职业活动中应遵循的道德规范都有精辟的论述，其中不乏真知灼见，至今闪耀着思想的光芒，对于我们今天医风医德建设仍有重要的借鉴意义。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;《四部医典・论述本》第31章中有一节专门“论医生”，全面系统地论述了医生的职业道德规范，其内容涉及医生与患者的关系、医生与医生的关系和医生本身品德修养等各个方面。有学者指出：与世界上任何一种医学系统具有影响的医学道德规范的内容比较起来，《四部医典》中所提到的内容已经相当全面，其中包含的规范与其他医疗体系的道德规范相比，毫不逊色，有的甚至远远超过包括著名的希腊的希波克拉底的《誓言》，阿拉伯医学的迈蒙尼提斯的《祷文》，汉族中医孙思邈的《千金要方・大医精诚论》等书中医德内容。这样的评价是符合实际的。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;由于职业的性质和特点，医生所扮演的角色，要么是“病人的保护神，是生命垂危者与阎罗之间的调节人”，要么是“夺取生命的刽子手”。因而“技术高明、精通理论、经验丰富、工作细致、责任心强”的医生是疾病能否容易治疗的首要条件(《四部医典》中根据病情和外部治疗条件把疾病分为容易治疗的疾病；不易治疗的疾病；可以用药物试治的疾病；不能治疗的疾病等四类)。正是基于这种认识，《四部医典》中对医生的专业水平和道德品质修养提出很高的要求，认为刻苦学习、博采众长、精通医理、勤于实践是作为一个医生的基本素质和美德。首先，它要求只有对“严格遵守誓言、尊敬上师、不贪财、喜施舍、言行一致、有恻隐之心，肯扶植他人和诚信佛法”的人，才能传授医学。其次，要求学医者“要有宽广的胸怀，坚定的信心，周密的思考，精通医学理论，在实践过程中战胜一切阻碍和困难，能详察、能明辨、能断言。对工作要勤奋，就是对医业本身的学习勤奋，要勤奋学习基础知识，依靠条件与同道共同钻研，达到熟练程度。学习的基础是问和读，要勤问、勤思考、勤验证，不可偷懒，偷懒会使思想停止不前而僵化。”作者还用借喻的手法，辛辣地讽刺和鞭笞了不学无术，碌碌无为的医生，如“没有严师传授的医生，好像狐狸坐在王位，没有人尊敬它；不懂理论的医生，犹如让生来就是瞎子的人看东西，根本分辨不了真伪，……不懂治疗方法的医生，好像在黑暗里瞄靶子，所用的药物根本击中不了要害。”等等，这些生动的比喻对今之学医者仍有警示作用。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;医生在处理医患关系时应遵循的道德原则，是医德学的核心内容，主要表现在医生诊断和治疗疾病的一系列职业活动中道德行为的选择上，是医生道德品质修养的外在表现。《四部医典・论述本》第25章医生诊断疾病的方法中提出：①对疾病的特点应有充分的了解和掌握；②从侧面了解患者的情况；⑧病人面前要详细观察；④不轻易下结论；⑤言语要谨慎，少开口；⑥了解以往的用药情况；⑦树立威信；⑧不要装腔作势。这八点诊断疾病的方法包含着如下四个方面的道德要求。一是医生要用自己适度的言行和渊博的知识树立威信，在病人心目中留下良好的印象，“用温和的话语使他喜悦”，从而消除病人的疑虑，取得病人的信任。二是要耐心倾听病人的陈述。“三诊中间诊最为重要”，通过问诊，充分了解和掌握疾病的特点和以往治疗的情况。在病人陈述的过程中，医生尽量少说话，不要随意打断，也不随便否定病人提供的资料，如遇到病人难以启齿的隐私时，“可从侍从那里侧面了解情况”。三是在询问病情的基础上，要认真细致，有条不紊地进行体格检查，做到不漏诊内容、不丢疑点，在检查的过程中，要体贴病人，“怜悯病人的痛苦，不要把病人的脓血视为污秽而嫌脏”。四是经诊断，“如有把握治疗痊愈的疾病，则要明确告诉患者”，如果一时诊断有困难，则不要轻易下结论，而要耐心等待、进一步观察，从中找到线索。特别是“死亡、危险、痊愈尚没有一个定局时，急忙做出肯定的诊断，是不合适宜的”。同时，如果确实经过努力，仍感到不能诊断或治疗的疾病，要及时另请高明，“绝不能顾及自己的声誉而拖延时间，贻误病情。”这是极不道德的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;治疗疾病的道德要求：<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一、“对病人的治疗要竭尽全力，要专心致志地医治，不可拖延。犹如在高墙上端着一碗溶化的酥油行走，若泼出一点酥油，就要死亡的威胁一样”，专心致志、一丝不苟。任何疏忽大意，粗枝大叶都是不允许的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;二、“在辨证、用药及外治方面、要严防走上低的极端、超的极端、反的极端、要执行正确的中道，”即避免低劣的、多余的和不正确的治疗。原则上对于严重的疾病，要依次用药物，外治、饮食、起居四个方面进行治疗，病情不严重的疾病，须从饮食、起居、药物、外治四个方面逐步治疗；对单一型的疾病，治疗时注意避免诱发其他疾病；对混合型的疾病，哪种疾病严重，便先医治那种病；全面考虑各种因素，正确掌握药物剂量，一切从实际出发。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;三、“要把自己的全部本领献给众生，对待众生象对待自己的亲兄弟一样，考虑别人的心情及有节制地适应别人的行为，从精通人情事故出发，进行治疗；医生贡献自己的力量不骄傲、不贪婪，必要时要以恩报怨。”四、医生要有坚持真理的科学态度。“经医生诊断，如与病人的怀疑相符时，可明确告诉所患的疾病；如与病人的怀疑不相符时，口头上伪称所患的疾病与病人所说的相同，但在实际治疗中要按实际所患的疾病治疗。”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;s除此之外，《四部医典》中还提出：“视师长犹如佛陀，师长的教诲如同仙人指示的准则；爱护同道犹如同胞兄弟；与同行共同钻研理论；师长务必精通医学理论，博采众长，不吝惜、不草率、不虚诞，对病人、学生一视同仁，不鄙视而能慈爱”等等有关处理医医关系，师徒关系的道德规范。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;s道德属于上层建筑，它随经济基础的改变而改变，不同的社会有着不同的道德规范和标准。《四部医典》成书的年代正是西藏地区佛教盛行的时期，研习医学者大多是寺院僧侣，在这样一个特定的社会环境中，对医生的道德要求不可避免地带有浓厚的宗教色彩。有因果报应的迷信思想，更有其时代的局限性。但从总体而言《四部医典》中所提倡的医德规范，有很多任何时代任何医生都适用的合理内核。有的方面还超出了佛教教义，如用动物药一例，与佛教教义中“不杀生”相悖的做法，说明了治病救人，发扬人道主义精神毕竟要比佛教教义有更高的精神境界。<br>\r\n      <br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (679, 2, 54, '丰富多采的蒙医眼科', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;丰富多采的蒙医眼科<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 \r\n      乌仁图亚<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医眼科是蒙医药学的重要组成部分，早在元朝时代，蒙古地区就已制作外用眼膏和使用内服眼药。及至 16世纪藏医《四部医典》传入蒙古地区，为蒙医眼科的发展起了重要作用。《四部医典》“眼病治法”中对眼科“病因、病缘、疾病分析、症状及治疗”等方面进行了较为详细的论述。18世纪著名蒙医学家伊希巴拉珠尔所著的《甘露点滴》说眼球结构为“眼外膜与光明轮间盛满眼水”，外界万物“通过眼睛镜子搬结构反射而眼内成像”。它对眼病定位，掌握病情变化等方面有一定的意义。《甘露点滴》还指出了眼与脏腑的关系：“眼接旺布道尔吉脉(白脉)与肝相连，肝血代谢正常则视力敏捷，否则致目疾”。伊氏所著《白露医法从新》(都德泽楚俊)中还最早提出“眼疫”(传染性眼病)，并吸收“汉医炉甘石制剂”，丰富和发展了蒙医眼科学内容。世纪初蒙医学家敏若・敖民罕・占布拉著的《蒙医金匮》(满阿嘎仁钦纵乃)中对蒙医眼科更进行了全面系统的叙述。书中将眼病分为94种，并突出蒙医重视“整体观”，指出：“视瞻昏渺、眼障、眼水污浊等神瞳病多因心肾所致；黑睛病多因肝胆所致；白睛病多因肝脾所致；眼睑病多因脾肠所致；眼疫、查嘎症，胬肉障等多因粘虫、血热、头部病症所致”。这些理论在临床上具有一定的指导意义。在眼病治疗方面仅成方就载入45种，单味药和加减法甚多。方剂剂型有汤剂、散剂，丸剂、油剂、水剂、膏剂、栓剂、冲洗剂……等多种。并对中障 \r\n      (相当于白内障)的手术详细描述其适应症、禁忌症，体质特性检查、术前准备、术中意外处理、术后护理、治愈标准等内容。19世纪洛桑确泊勒所著的《蒙医药选编》(哲兑宁诺尔)对某些曾经模糊的眼病概念进行澄清。19世纪末伊喜丹僧旺扎拉撰写的《珊瑚验方》(珠如道沙拉)中以蒙医理论的核心“寒热论”为依据指出：“眼病多数为肝血热或肾寒所致”。并创造性地提出：“达顺一6味汤，扎高德一7味汤，尼顺一17味散”等服科有效方剂。到本世纪蒙医眼科已有专科医生。如40年代兴安总省阿鲁科尔沁旗的朝格苏荣是当时一位远近闻名的蒙医眼科医生，他不仅善用蒙药治疗各种眼病，而且对“翼状胬肉”等外眼病能进行手术治疗。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;新中国成立后，蒙医眼科得到显著发展。1958年内蒙古医学院蒙医专业编写了五官科讲义，使蒙医眼科进入正规教学轨道。80年代《中国医学百科全书一蒙医分卷》中眼科列有57个条目。1990年高等医药院校《蒙医眼科学》教材问世。内蒙古中蒙医医院和内蒙古蒙医学院附属医院分别成立蒙医眼科门诊、病房。近年来有关杂志上发表蒙医眼科理论，临床研究论文。蒙医眼科的医、教、研工作正日臻完善。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (680, 2, 54, '古代蒙医的食疗应用意义', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;古代蒙医的食疗应用意义<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医医院 \r\n      伊光瑞<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医食疗不仅有悠久的历史，而且还具丰富的实践经验和本民族应用方面的特色。顾名思义，食疗必然既需食物，又佐药品。古代蒙古民族长时期居住草原地区，多以游牧、狩猎为业，又很少种植谷物。《礼记，王制》即说过“北狄不粒谷”， \r\n      《汉书》有“北地谷稼不熟”。处在这样环境的条件下，食疗也只有以乳肉为食料佐以药物入剂了。此为蒙医古代食疗的突出特点之一。在这里仅就对食疗理论的认识及其临床应用，谈点个人的理解。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;由于北方地区气候变化异常，寒多热少，高风气燥，加上食用以乳肉为主的特点，久而久之，人们认识到寒与热、饥与饱的处理当否，对于健康与疾病有着极为密切的关系。在《饮膳正要》中有冬季避寒就温，夏日防暑纳凉，为适应寒暑之道：先饥而食，食勿过饱，先渴而饮，饮勿令过，乃调剂饥饱之理。并且一再强调“保养之法，莫若守中，守中则无过与不及之病。调顺四时，节慎饮食，起居不妄，使以五味调和五脏。五脏和平则血气资荣，精神健爽，心志安定，诸邪自不能入，寒暑不能袭，人乃怡安”。由上可见掌握“过之与不及”，适应四时气候变化，协调五脏安和是食疗运用的基本理论依据。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 食疗在保健中应用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;古代北方各民族都长期缺医少药，所以食疗已广泛的被应用。在保健方面有句俗语：“病之始始于食不消，药之源源于百煎水”。是说不吃过量之食，不饮不洁生水，就可不生疾病。在《蒙古秘史》中早讲过，要“喝羊汤”，吃“烧羊肉”。从《饮膳正要・聚珍异馔》收载预防保健的94方用料看，可以知道在元朝时期仍以乳肉为主食的配方。例如书中有55方突出了羊肉的用量，还有的用羊的心、肝、肺、肚、肠、髓、脑、头、尾、胸乳、酪以及鹿、狼、鸡、鱼等等，佐的药品种类并不多。有不少汤剂只用了草果、回回豆子等。这些足以说明当时保健的着眼点在于增加营养，调和五脏，滋补心志，扶正强身为目的。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 食疗在治疗中应用<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;食疗，古代医家主张“先食后药”的原则。如孙思邈说：“夫为医者当领先洞晓病源，知其所犯，以食治之，食疗不愈，然后命药”。这只是一般原则。其实，食疗也要辨证施治。在蒙医学，首先是“寒者热之”和“热者寒之”。《千金要方》的匈奴露宿丸，以食用的姜桂加附子、誉石等大热药，治疗寒食不消病。《饮膳正要》炒狼汤，以狼肉佐砂仁、胡椒等温中焦，暖五脏，治疗虚弱症。与此相反《辽史拾遣》记载耶律德光征途患热病，“苦热聚冰于胸、腹、手、足，且啖之”。啖冰退热符合物理降温的精神。食疗也有一病一药的用法，《饮膳正要》的瓠子汤，用瓠子6个，羊肉一脚子专治消渴，饮一溲一。马奶酒自古以来即是补虚健身的营养品，并可治疗卒中。蒙医习用的沙棘果，近世研制成新剂型，用于慢性气管炎取得理想的疗效，至于广枣、诃子新用法，无不是从古人留下来经验的基础上继承发掘而使之重放光辉。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;总之，以上可见食疗并不是简单的以药佐食，而是有理论知识，有临证经验，可防可治，有较强的针对性和很高的科学性的一门学问。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (681, 2, 54, '蒙医脉诊的历代研究与现状', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医脉诊的历代研究与现状<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    内蒙古中蒙医医院 那顺达来 格日乐 　　　　\r\n <br>     &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    内蒙古医院 阿拉坦<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医脉诊是蒙医学的重要诊断方法之一。长期以来，蒙医脉诊一直有效地指导着临床实践，测病因、识病位、辨病性、预转归，为诊治疾病起着极其重要的作用。历代医家都十分重视脉诊，将其视为“诊法之首”。因而，阐明蒙医脉诊历代研究概况及其现状，对于深入理解脉诊要旨、探索脉诊发展规律、确定脉诊研究方向及对于发掘整理、继承发扬蒙医脉学遗产，都具有十分重要的历史意义和现实意义。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 古代传统蒙医药对藏医脉诊的有机吸收<br>\r\n      古代传统蒙医药在漫长的实践过程中，形成了以寒热理论、滋补理论、发震治震学说为指导，以善长正骨、灸疗、刺血、饮食疗法、外伤治疗、急救疗法等独特理论和临床经验。古代传统蒙医药发展至13世纪，随着蒙古帝国的强大，蒙古民族与欧亚各国民族之间的文化与经济往来空前繁荣，古代传统蒙医药亦由此进入一个新的发展时期。尤其是藏传佛教在蒙古地区广泛流传，藏医学文献大量被翻译成蒙古文，从而传统蒙医药有条件得以参考、借鉴和有机吸收藏医理论精华。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 《月王药诊》中的脉诊：《月王药诊》是现存最早的集古印度医学、中医学以及传统藏医药理论与经验的一部综合性医学经典著作。8世纪上、中叶之间著成，全书共113章，内容首尾完整、总结了基础理论与临床各科的医学经验。<br>\r\n      《月王药诊》虽是一部涉及医学各方面知识的综合性医著，但迄今它是现存文献中最早记载和论述藏医脉诊的著作。该书从第十五章至二十四章以《寒热脉与胸腰》、《心脏脉》、《肝脏脉》、《肾脏脉》、《肺脏脉》、《脾脏脉》、《死兆》、《脉应时变》、《鬼邪脉》、《脉轮推算》等10个章节的内容，专门论述脉学，从理论到临床应用，作了较详细的阐述。《月王药诊》以如此大量篇幅(约占全书1／10章)论述脉诊，可见在当时，脉诊已引起医家们的重视。书中论脉已涉及到了有关诊脉的各方面内容，对诊脉的条件、时间、部位、寸关恰分属五脏六腑、正常脉象、寒热脉象、五脏病变脉象、死兆脉、脉象与四季和五行的关系、鬼邪脉等都有比较具体的论述。<br>\r\n      《月王药诊》中已明确提出了诊脉的时间和条件。认为诊脉应在“朝阳初露、由阴转阳之时”进行，且医生和患者均须平心静神。对诊脉的部位和方法，具体指出：“在腕部最长横纹上一寸处用三指均匀压取”，并主张诊脉男女有别：“诊女子的脉时医生先用左手诊女子的右手脉，诊男子脉时医生先用右手诊左手脉”。在脉象的认识上，《月王药诊》不仅对正常脉象做出了描述，而且将病脉分类为寒热两大类，并以五个章节分别论述了五脏脉。主要通过五脏各对应的寸、关、恰的脉象变化和总脉象来判断各自的多种疾病。如：“左寸脉候心脏疾患，寸脉弦则心存风热，寸关恰三部皆浮则心风扩散”。据初步统计，五脏脉中共论述了60种五脏病症脉。《月王药诊》亦初步认识到了脉象随季节更替而发生变化的规律，将脉象与四季、五行相联起来综合判断疾病的演变。书中对死兆脉、鬼邪脉及脉轮推算论述颇多，这些内容多是通过脉象与五行、四季、天宇日数之间的相互关联来预示疾病的转归和吉凶。值得注意的是，《月王药诊》对于寸关恰三部分属五脏六腑具有独特的认识和观点。指出：“患者左手寸部上角候心脏、下角候小肠；关部候肝胆；恰部候右肾、膀胱；患者右手寸部候肺、大肠；关部候脾胃；恰部候左肾、生殖”。此方法与我们今天所用的分属方法是截然不同的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;总之，《月王药诊》论脉，其内容已几乎涉及到脉诊的各方面问题，无论是对诊脉的时间、部位、条件，还是对正常脉象、病脉、死兆脉都有了一定的认识。不难看出《月王药诊》时期已将脉法作为主要的诊断疾病的方法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 《四部医典》中的脉诊：《四部医典》是继《月王药诊》之后的一部重要的藏医学著作。由宇妥・云丹贡布等8世纪末著成。《四部医典》第四部《后序本》将脉诊作为重要内容，首先以一个章予以专门论述。《四部医典》论脉体例非常清楚，编排有序，主要从以下13个方面进行了逐一、全面地论述。即：(1)诊脉的准备条件，(2)脉诊时间，(3)诊脉部位，(4)诊脉手法的轻重，(5)寸关恰分属五脏六腑，(6)脉性，(7)季节脉与五行，(8)七怪脉，(9)以脉搏次数判断有无疾患，(10)以总脉象与具体脉象辨病情，(11)三种死兆脉判断吉凶， \r\n      (12)鬼邪脉，(13)命脉断寿数。<br>\r\n      《四部医典》论述诊脉的条件、时间在《月王药诊》的基础上有了进一步的补充，从饮食、起居、心神因素等三个方面提出更详细、严格的要求。对诊脉部位、正常脉象、死兆脉、脉象与四季五行关系以及鬼邪脉的认识，《四部医典》在继承《月王药诊》的基础上做了进一步的发挥。总结出三种正常脉象与三种死兆脉。寸关恰分属五脏六腑，《四部医典》提出不同于《月王药诊》的分属方法。《四部医典》认为：“左手寸部候心、小肠；关部候脾胃；恰部候左肾、生殖；右手寸部候肺、大肠；关部候肝、胆；恰部候右肾、膀胱；并指出女子的寸部所候脏腑与男子的左右恰相反”。这种分属方法与《月王药诊》是截然不同的。后世医家多宗《四部医典》的分属方法，并一直沿用至今。而《月王药诊》的分属法自此被弃用，没有得到临床应用和进一步发展，这值得思考和研究。《四部医典》首次提出七怪脉和命脉，主张用总脉象与具体脉象辨病情。提出12种寒热脉象，总结列举了35种病症的脉象，极大地丰富了脉象与主病的内容。《四部医典》已认识到了不同的病症可以出现相类似的脉象，列举了6种容易混淆的病脉，并对其作了详细鉴别。这对于临床诊断疾病具有很重要的意义。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;综观《四部医典》的脉诊内容，它在吸收《月王药诊》的某些内容的基础上，作了许多补充和总结，提出较多新的见解，使脉诊的内容更趋丰富和充实。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 《祖先言教》中的脉诊：16世纪著名的藏医学家舒卡・洛珠杰布著《祖先言教》。该书论脉主要宗《四部医典》脉法，对其逐条注释，并从理论方面给予了精辟的解释。如对《四部医典》中的“女子寸脉所属驻腑与男子左右恰恰相反”解释为：“人固有阴阳两脉且男女相同，阴脉位于身体左侧，阳脉位于身体右侧。心类部有‘感思’出入之孔，男属阳，故此孔朝向阴脉，女属阴，故此孔朝向阳脉，因而男女寸脉所主脏腑恰恰相反”。这种解释较《四部医典》的解释更为贴切、合理。此外舒氏还对各种病脉的产生机理作了阐述。如：“新热症时，由于巴达根、赫依较盛，因而脉象较浮；陈旧性热时，赫依恢复平缓，而巴达根与热相结合而引起脉象沉、紧、洪”等。这无疑使人们对病脉的产生有了更深的认识。舒氏还对当时一些医家和著述论脉的错误，进行了尖锐地批评和讽刺。 \r\n      总之，舒氏论脉甚有发挥，对《四部医典》脉法做了精湛的注释和提高，使脉诊有了进一步深入的发展。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.4 《蓝琉璃》中的脉诊：《祖先言教》之后，17世纪杰出的藏医学家第司・桑吉嘉措著《蓝琉璃》，对脉诊的论述，大多与《祖先言教》中的脉诊内容相同，所不同的是，在此基础补充了当时临床诊脉的经验，新增了鼠疫、喉蛾、疗毒等病症的脉象。对死兆脉也有进一步的认识，增添了巴达根合并希日症的死兆脉象犹如蛙跃，合并证的死兆脉犹如老牛唾液等内容。更值的一提的是，桑吉嘉措主持绘制了79幅藏医彩色挂图，其中 \r\n      9幅是关于诊脉的象图。此脉图不仅表达了不同的脉象，而且还把脉位与脏腑、五行、五官、四季、物候等联系起来，非常形象地予以表述。这在当时世界上是绝无仅有的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.5 《亲・却吉扎拉申三著》中的脉诊：17世纪著名藏医学家达日茂仁巴・罗布桑朝日格著《亲・却吉扎拉申三著》对脉学进行了整理和总结。此书的论脉内容，大多与《祖先言教》相同，几乎是《祖先言教》脉学内容的转录，所不同的是还吸收了《蓝琉璃》中的内容，即吸收了鼠疫、喉蛾、疗毒三种疾病的脉象，在最后的附记中还收录整理了《月王药诊》的有关脉学内容。所以《亲・却吉扎拉申三著》可以说是集17世纪前脉学之大成。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 历代蒙医学家对脉诊的研究<br>\r\n      上述藏医著作，随佛教文化传人蒙古地区后对古代传统蒙医药学产生了一定影响。诸多蒙古族医学家结合蒙古地区的实际情况，在古代传统蒙医药学的理论与临床实践的基础上，灵活、有机地吸收藏医脉诊法的同时，进一步对脉诊进行创造性研究，提出许多不同于藏医脉诊的新见解和临床诊脉新经验，增加了新的内容，从而使蒙医脉诊不断深化提高，具有了丰富的内涵和鲜明的民族特点。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 《四部甘露》中的脉诊：《四部甘露》是一部在古代传统蒙医药学理论与临床实践基础上，吸收古印度医学、藏医学以及中医学的理论精粹，与古代传统蒙医药有机融合而形成近代蒙医学理论体系的经典学著作。18世纪由蒙古族著名医学家伊喜巴拉珠尔著成。《四部甘露》中的《甘露之泉》第三章的第一节和《甘露医法从新》的第一部分专论脉学，其它两部《甘露点滴》和《甘露汇集》中亦记载有脉诊内容。伊氏论脉主要根据自己的临床诊脉体会和心得，提出新的见解和观点。<br>\r\n      《四部甘露》中共列举了44种疾病的脉象，较《四部医典》多2种病脉，新增了狂犬病和各类中等疾病的脉象，并在此基础上总结出一些病症的特征性脉象。如：“热症脉象中，数脉多主疫热，洪脉多主紊乱热，弦脉多主陈旧热等；迟脉多主风痰合病，微脉多主寒性痞瘤”。此论述对临床诊脉治病具有较高参考价值。与此同时，伊氏提出不同于《四部医典》的见解。《四部医典》指出：“诊脉时，六指的上角且脉管向外搏动之时诊察五脏脉；六指的下角且脉管回缩时诊察六腑脉”。而《四部甘露》则认为：“六指的阳侧为五脏之脉，在患者呼气时进行诊察；六指的侧面为六腑之脉，当患者吸气时诊察”。此外伊氏还主张，诊脉主要用于诊察脏器之疾。伊氏提出的这些不同于《四部医典》的观点和理论，是蒙医脉诊有别于藏医脉诊的显著特点之一。<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 《脉诊概要》中的脉诊：《脉诊概要》是一部蒙医学脉诊专著。18世纪末叶，由著名蒙医学家罗布桑苏勒和木所著。全书详细论述了脉诊的各方面内容，对脉诊进行了一次全面的整理与总结，并在此基础上进行了大量的补充和提高。尤其对12种寒热脉象进行了深入分析，总结出6种热症脉的区别是：“实、滑、数、弦脉则是脉形之别；洪、紧脉乃为脉位之异”。从而使人们对6种热症脉象易于辨认和掌握。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 《蒙医药选编》中的脉诊：《蒙医药选编》是19世纪著名蒙医学家罗布桑朝佩勒所撰的一部以临证各科为主，包括基础理论、诊断、药物疗术等多方面内容的著作。该书第66章专论脉诊，在继承历代脉诊内容的同时，进一步增添了临床诊脉经验。新总结出麻风病脉象，为“涩、颤而不显”。并在此基础上对历来无脉形说解的一些脉象进行了指感描述。如：“双管脉犹如二条扭绳，两边高而中央底；交替脉则强弱不均交替反复”。从而使这两种脉象的含义清楚易懂，对脉名、脉形的规范工作起到了一定作用。<br>\r\n      除了上述医著论脉以外，有关脉诊的论述还见于19世纪末著名蒙医学家伊喜丹增旺扎位所著的《蒙医药简编》，20世纪初叶吉格木德丹增扎木苏所著的《蒙医传统验方》等各种书中，皆有精辟的见解和独到之处。<br>\r\n      总之，在这一时期历代蒙医学家在有机吸收藏医诊脉的基础上，在蒙古地区具体医疗实践中，结合蒙古地区的社会经济、文化习俗、气候、地理环境等特点和各自的临床诊脉体会和心得，对脉诊进行了大量的补充和创新，提出许多新的见解和观点，增加了前所未有的新的内容，极大地丰富了脉诊内容，从而使蒙医脉诊日臻完善，形成了系统的蒙医脉诊法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 蒙医脉诊的现状(20世纪50年代以后)<br>\r\n      50年代以来，在继承和发扬蒙医学的新形势下，对蒙医学进行了全面的挖掘和整理，并编著出版了大量的著作。蒙医脉诊随之得以进一步的发展。1977年于庆祥编著了《脉诀》一书，该书全面细致地论述了脉诊的各方面内容，同时增补了自己临床诊脉经验，共列举了54种疾病的脉象，丰富了脉诊的临床应用。同年，由内蒙古医学院蒙医临床教研组，锡林郭勒盟蒙医研究所合编《蒙医内科学》，对脉诊的各项内容进行了总结性阐述，编排新颖、简明易了，收集了当时临床诊疾所出现的脉象，共列举了42种病脉。1986年白清云等主编的《中国医学百科学・蒙医学》，在继承和收集脉诊内容的基础上，对脉象进行了深入探讨，如从脉象宽度、长度、部位等方面进行了综合论述，共讲述44种病症脉象。白纲主编的《蒙医诊断学》一书，亦在此基础上作了较详尽的论述。这些论著根据蒙医学的理论，总结历代脉诊的成就，系统、全面地论述脉理、脉法、脉象与主病的同时增补了新的临床诊脉经验。<br>\r\n      近年来，有学者对脉诊的理论进行了探讨，认为蒙医的诊脉部位渊源于中医诊脉，提出蒙医诊脉部位应与中医诊脉部位相同的观点；亦有将蒙中医脉诊进行对比研究，认为两者之间有同有异具有一定联系。还有人进行了将死兆脉与西医某些疾病相联系的尝试。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;总之，这一时期，随着蒙医学的发展，脉诊在文献整理和理论研究取得了一定的进展。最近，巴・吉格木德教授等已开始对《蒙医脉象客观化、标准化》进行研究，这标志着蒙医脉诊的研究，已由过去的以整理和诠释为主的传统研究方法向运用新科技以探索脉象实质为主的现代化研究方法迈进。随着时代的发展，蒙医脉诊研究正在开创着新的发展领域，蒙医脉诊必将会有新的突破。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (682, 2, 54, '曼巴扎仓促进蒙医学的发展', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;          &nbsp;&nbsp;曼巴扎仓促进蒙医学的发展<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   中国中医研究院医史文献所 苏 诺<br>\r\n <br>     &nbsp;&nbsp;&nbsp;&nbsp;蒙古医学是蒙古人民长期与大自然及各种疾病作斗争过程中，逐步总结和积累起来的，具有丰富的医疗经验和理论基础。是我国医学宝库中的重要组成部分，随着兄弟民族文化交流的日益增多，医学交流的日益频繁，在蒙医形成和发展过程中，不断吸收藏族医学和其他民族医学的精华，使蒙古医学得到了丰富的发展。其中，藏医的医药学对蒙古族医药学的发展影响深远。《四部医典》等藏医学著作随着藏传佛教，主要以在寺庙中设置的曼巴扎仓，培养喇嘛医的形式广泛流传于蒙古地区。在蒙古地区1000多座寺庙中，有曼巴扎仓的寺庙大约有30多座，蒙古医学起初主要以祖父辈传授于子孙或者以带学徒的方式培养自己的继承人。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;明代，蒙古地区的部分寺庙开始设置曼巴扎仓，开始了有组织、有系的传援藏医藏药，很多蒙古僧人学到了藏医学的知识，为藏医学全面传入蒙古地区创造了极好的条件，《四部医典》是在这个时期做为曼巴扎仓的重要教学内容之一。系统而全面传入蒙古地区。《四部医典》堪称是古代藏医学的一部基础理论和临床实践俱全的医学百科全书，其在蒙古地区的传播，大大促进了蒙古传统医学的发展。使蒙医学理论化，系统化，完整化，在理论和实践方面产生了极大的影响，对其进一步发展起了至关重要的作用。蒙古医僧们研究《四部医典》，除翻译、注释和整理外，还结合各自的临床实践经验，撰写了多部有关的著作，为藏医学的传播和发展也做出了贡献。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;清代，由于藏传佛教的全面深入，寺庙数量的增多，培养出了很多的喇嘛医生，一些蒙古僧人对藏语文有了比较深的造诣，很多藏文的经典著开始翻译成蒙文，如藏文大藏经《丹珠尔》在乾隆年间以译成蒙文，其中包括多部医学证著，同时一些蒙古高僧还用藏文或蒙文撰写了早期蒙医药学著作。如：17世纪蒙古医学家罗布桑丹金扎拉仓著的《疾病类型详解经全》、《二十五味方剂集》、《泻法祛寒法及接骨法大全》；18世纪蒙古族医学家伊希巴拉朱尔用藏文撰写的《四部甘露》、《认药白晶药鉴》；敏如尔占布拉用藏文撰写成《方海》；19世纪蒙古医学家伊希丹金旺吉拉用藏文撰写成《珊瑚验方》，《医学整诀一百三十四首》，占布拉道尔吉用藏文撰写《蒙药正典》等。这些医学著作标志蒙医药学日臻完善，奠定了现代蒙医学的理论体系。由此可见，清代是蒙古族医学发展的突飞猛进时期。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;近代，蒙古各地寺院中曼巴扎仓仍是培养蒙古医学人材的重要基地，部分地区开始出现在曼巴扎仓的基础上创办蒙医学校专门学蒙医。这些学员来自有一定文化知识的年轻僧人，教员也是从曼巴扎仓聘请的僧侣医生。当时的蒙医学校尽管还带有曼巴扎仓色彩，但还是从纯寺院教育性质的模式中逐渐脱离开来。为蒙医学真正成为一，门独立的现代医药学体系打下了基础。据《库伦旗文史资料》记载，库伦旗的蒙医学校，于20年代初库伦旗第二十三任扎萨克喇嘛罗布桑林沁，在下仓(旗办公务地方福缘寺 \r\n      )开办了一所蒙医学校，从科左中旗莫林庙曼巴扎仓聘用一位名叫博彦特古斯的蒙医来任教，学员从年轻的喇嘛众徒中挑选出聪慧好学者。另有曾经在土尔扈特喇嘛门下学医的喇嘛也自愿转入这所蒙医学校来学习，“蒙医学校的主要教材是《四部医典》，教学上采取教师授课、学徒背诵课文和集体讨论的方式。在教授基础理论课的任务已告完成之后，又从土默特左翼聘请蒙医毕力格图来任教，这位名医就学于瑞应寺曼巴扎仓。他除了讲解《四部医典》之外，又教授方剂学，诊断学及放血、针灸、罨疗等五疗术”。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;综上所述，自17世纪蒙古地区设置曼巴扎仓始至20世纪初蒙医学校的成立，曼巴扎仓为蒙古地区培养了大批的蒙医人才，大大促进了蒙医学的发表。这主要表现在从原来的自然发展阶段跨跃到自觉发展阶段。蒙医学在原来较落后的基础上经过几百年的积累和吸收，成为一个独立的科学体系--蒙古医药学。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (683, 2, 54, '“蒙古灸”考', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“蒙古灸”考 \r\n<br>\r\n   <br>    &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医药学院 宝龙 龚金山<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古灸是蒙医疗术学中的一种外治法。由于它被载入藏医学经典著作《四部医典》中，而成为蒙医学悠久历史的见证，但我们在研究医学史，参考古代文献时，必须尊重历史事实，实事求是地分析问题。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．问题的提出<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;“蒙古灸”一词最早出现在18世纪著名学者敏珠尔绰尔吉所译蒙文木刻版《四部医典》第二部《总则本》中。在其第5章治疗方法中写道：“外治法分为 \r\n      (治疗赫依病的)涂油按摩和蒙古灸，(治希拉病的)发汗、放血和冷水浴，(治巴达干病的)温熨和灸法”说明蒙古灸是一种治疗赫依病的外治法。而这蒙古灸在藏文版《四部医典》中则记为“hor―gyi― \r\n      me―btsa・a(霍尔灸)”。问题的关键是这个“霍尔”能不能与“蒙古”等同，我们拟对此作一分析。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．“霍尔”不能与“蒙古”等同。对于“霍尔”一词大致有如下三种解释。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 “霍尔，不同的时期所指的民族不同，唐宋时指回讫，元代指蒙古人，元明之间指吐谷浑人，现代指藏北牧民和青海土族。”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2“霍尔，①蒙古；②游牧人；3土耳其。”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 “霍尔，这是自吐蕃时期起古代藏族对北方游牧民族中统称”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;笔者认为“霍尔”很有可能是“胡”的音变。胡是中国古代北方和西方各族的泛称。匈奴人自称为“胡”，汉通西域之后，西域各族也被称为“胡”，于是把匈奴称为“北胡”，把乌桓、鲜卑称为“东胡”，而匈奴以西，葱岭以东各族称为“西胡”。可见“胡”是从匈奴人的自称转变为中原汉人对北方和西方各族的泛称。唐朝时，吐蕃与中原地区关系密切，中原地区先进的文化对吐蕃王朝政治、经济和社会生活等各方面都产生了很大的影响。把北方各民族称为“霍尔”也是这种影响的直接反映。“霍尔”被赋于“蒙古”的含义，是从13世纪，更确切地说是从1206年成吉思汗统一北方各民族，建立蒙古大帝国以后开始的。各种突厥部落，如扎刺亦尔、塔塔儿、斡亦剌惕汪古惕克列亦惕、乃蛮、唐兀惕等，按当时的历史条件，都自称为蒙古人”。元朝建立后，忽必烈封八思巴为国师，西藏正式纳入祖国的版图，蒙藏民族的关系进入了一个新的历史时期。“霍尔”为“蒙古”之含义被固定下来。由此可知，在此之前，“霍尔”并不是“蒙古”，而是北方各民族的泛称，尽管包括蒙古族的先民，但我们不应将局部和整体混同。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．“霍尔灸”何时载入《四部医典》<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏文《四部医典》是公元8世纪由著名藏医学家宇妥.元丹贡布(708～833)所著。后经世代许多藏医学家的修改、增补、注释、整理而愈趋详细完整，直到公元1173年小宇妥・元丹贡布(1138～1213)对此进行了全面修定，后来舒卡・洛追杰波刻印了“扎当《据悉》”。在5世达赖和第司・桑杰嘉措执政时期，重新对此进行了校订，刊刻了现行的《四部医典》。由于“霍尔灸”只是出现在总则本中，而论述本、密诀本及后续本中均无记述，所以对“霍尔灸”的具体操作方法，以及何时被载入《四部医典》等问题，我们只能从《四部医典》和注释本中找到答案。<br>\r\n      舒卡・洛追杰布(1509―1573)是藏医南方学派最杰出的代表。他为恢复《四部医典》的本来面目，不辞辛劳。四处奔波，极力搜寻《四部医典》的原本，终于找到宇妥・萨玛元丹贡布亲注的《四部医典》，并于1546年，以这部医典为准，刻印出版了“扎当《据悉》”。这是已知最早的《四部医典》木刻印刷版本，《祖先口述》是舒卡・洛追杰布讲解《四部医典》总则本和论述本的名著，一向被奉为藏医南方学派的经典。在本书的《总则本集》中谈及“霍尔灸”时写到：“有人误认为霍尔灸是把白脂石烧烤后热熨，这是灰药灸法，不是霍尔灸。在《四部医典》中把霍尔灸列入罨敷章中，正确的方法是将拌有小茴香的油煮熟，涂在身上，铺上毛毡，再用烧烤热的虫蚀白脂石热熨这才是自古以来的霍尔灸”。如此看来，《四部医典》后续本罨敷章中“虚热及隆(赫依)性病痛用酒沾虫蚀白脂石或陈骨热熨之”很有可能是“霍尔灸”的雏形。因为这是罨敷章中唯一用以治疗隆性病的外治法，而且酒、虫蚀白脂石和陈骨都是北方最常见之物。那么，何以把明明是热熨之方法却冠之以“霍尔灸”呢?我们不妨设想：先是载入了这种方法，后来在修订过程中才予以“霍尔灸”之名。我们知道，灸法是有固定穴位的。但“霍尔灸”有没有特定的穴位呢?这已经引起了有关学者的注意，但仍需进一步研究。<br>\r\n      不管怎么说，“霍尔灸”作为北方民族一种外治方法的特殊概念而载入《四部医典》中，最晚也是在13世纪之前。很显然，敏珠尔绰尔吉将《四部医典》蒙译时，并没有深究历史，才把“霍尔灸”译成“蒙古灸”，这是不符合历史真实情况的。据报道，在此之前，即1648年前，卫拉特・咱雅班弟达首次将《四部医典》译成蒙文，但我们无从考证这位大翻译家是怎么对待这个问题的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4．“霍尔灸”的发展<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;霍尔灸是不断发展的具有生命力的外治方法。从简单到复杂说明一种治疗方法只有不断地改进，才不致被时代所淘汰而世代相传。<br>\r\n      如果我们把《四部医典》中“用酒沾虫蚀白脂石或陈骨热熨”看成是“霍尔灸”，那么，到了舒卡・洛追杰布时代，已把“酒”换成了“油”，因为“油”对“隆性病”来说，比酒更有针对性，更能提高其疗效。到了第司・桑杰嘉措时代，其操作方法更进了一步。被认为是《四部医典》标准注释本的《蓝琉璃》(第司・桑杰嘉措著)中，说“霍尔灸”是“油拌小茴香涂在毛毡上加热裹敷的方法，”很明显，“裹”比“铺”更能提高疗效。到了现代，“蒙古灸”又增加了新的内容，“蒙古灸”包括霍尔蒙古灸和西河柳条灸，西河柳灸是现代“蒙古灸”的新发展。”<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5．结 语<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古灸作为中国古代北方民族的一个古老的外治法，最晚也是在13世纪之前被载入藏医学经典《四部医典》中。值得一提的是，把蒙古灸改称“霍尔灸”或“霍尔蒙古灸”似乎更能准确反映历史的真实面貌，尽管“蒙古灸”作为一个概念被写入了很多书籍中，但笔者还是认为，研究古代文献，必须尊重历史事实，这应是每一个文献研究者坚持的原则。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (684, 2, 54, '蒙医外用药物用法概述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医外用药物用法概述 <br>\r\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学 满都拉 包龙<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;外用是与内服相对而言，就是用药物制成不同剂型，施用于病人体表或病变部位，使药物直接作用于皮肤和粘膜，使之吸收而起到治疗作用。历代蒙医学家在长期的医疗实践中，不断总结经验，积累了丰富的、具有民族特色的外治药物疗法，以其灵活多变的剂型和用法，使药物充分发挥疗效，取得满意的治疗效果。现将蒙医临床中常用的几种外用方法概述如下： \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 涂法：即①把药物研成粉末，用醋、油、乳汁或鸡子清调和成糊状，或者②把药物制作成膏剂，或者3把药物溶于水中直接涂搽于患处，以外引毒邪的治疗方法，是蒙医外科最常用的方法。适用于血热、黄水及“粘”虫引起的痈疽、肿疡，各种皮肤病的治疗。如治疗小儿腮肿时，可用利比巴拉珠尔(八味瑞香狼毒散)，以醋或蛋清调和外涂，有良好的拔毒作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 浴法：有水浴和缚浴两种。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 水浴：将药物放入水中煎煮，使之有效成份溶入水中，让病人浸浴治疗的方法。蒙医临床上常用的五味甘露汤浴就是将侧柏叶、照白杜鹃、水柏枝、麻<br>\r\n      黄、小白蒿等5味药按一定比例配制而成。主治伤热、四肢僵直、拘挛、胃火衰败、脾血不足、肾寒及外伤疡疮、各种皮肤病等。在应用时可视病情加入相应特效药或控制水温、入浴时间等，以期提高疗效。此外，蒙医尚有骨汤浴、驴骨浴等疗法。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 缚浴：是用药物或药布罨敷包扎患部从而起到治疗作用的一种疗法。有清热缚浴和祛寒缚浴两种。适用于胸部及关节，局部病症，有时也可用于全身性<br>\r\n      痛病，如小儿高热不退时，用在苦参一7味汤中浸泡的纱布缚浴胸部，使之发汗即可退烧。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 熏法 将药物放在特制的燃具内点燃，取其烟熏鼻治疗的方法。此法常用于头部“亚玛”病，鼻“亚玛”病，中风，昏厥等症的治疗。如中风昏厥时用单味黑云香熏鼻等。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 塞法： 将药物用棉花或沙布包裹扎紧或用栓剂，塞入鼻腔、阴道、肛门等处的一种治疗方法。如将肉桂、瑞香狼毒细研，用棉花包裹扎紧塞入鼻腔治疗鼻衄。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 敷法：有热敷和冷敷两种。热敷又称熨法，即用药物炒热、布包，热熨痛处或相关穴位。利用其热和药物作用，使气血畅通、散寒止痛，适用于因寒症引起的疼痛、腹胀等，如用炒热的食盐热敷腹部治疗不消化症。冷敷是将药物浸在井水、雪水或冰水中使之变凉或药物煎汤放凉后敷于病处或相关部位，主要有止痛、消肿等功能，适用于希拉、血不和引起的肿、痛、热等症的治疗。如查干榜嘎、枇把叶研细，调入井水冷敷治疗痢疾。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;6 吹药法；用药物研成细末，借助器械吹入口腔、咽喉的一种治疗方法。如治疗口腔溃疡时把嘎木珠尔均匀吹敷于溃疡部位等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;7 漱法，用药液含漱口腔，清洁口腔、咽喉的治疗方法。如治疗咽喉肿痛可用玛尔布一3汤煎液反复漱口等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;8 浸渍法：用药物煎汤在病变局部进行淋洗、浸泡、温敷的方法，它能使伤口洁净、祛除毒邪等，凡痈疽疮疡、溃后脓水淋漓或腐肉不脱，以及皮肤病瘙痒、脱屑、内外痔的肿胀疼痛均可使用本法。如苦参、胡椒煎汤有祛风除湿、杀虫止痒的功能用以洗涤治疗疥疮病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;9 插药法：把药物制作成细条状，插入痈破溃深处的一种治疗方法。具有生肌、祛腐、解毒、排脓的作用。适用于痈破溃后腔大口小，流脓不畅时，如斑蝥一10制成药条，插入肉痈破溃处治疗等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;10 吸入法：让病人吸入某些药物的烟雾蒸气、粉末或气味，达到治疗目的的方法。如吸入芬芳一12味散的粉末治疗头部“亚玛”病等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;11 药线引流：用吸水性较强的纸，搓成纸捻，外粘或内裹药、插入窦道或漏管中，引流祛腐，促进疮口愈合的治疗方法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;12 涂粉剂法：用药物研细的干粉直接涂在伤口表面的一种方法。具有止血敛脓、消肿、止痛、生肌等功效。如颈部串珠疮破溃后久不收口时可外涂轻粉七味散，则有拔毒、收口生肌的功效。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;13 滴法：即把药液滴入眼、鼻、耳等器官、治疗头面部病变的方法。如将阿魏、麝香和肉蔻细研溶入蒜汁滴入耳内治疗耳刺痛。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;14 温和导泻法和猛烈导泻法(直肠给荐法)：以特定药物粉末加入油脂，乳汁或肉汤制成胶状液，注入肛门内进行灌肠的治法。温和导泻法和猛烈导泻法各有其特定的药物配方、适应症、禁忌症及操作方法。并且根据病情可灵活加减药物。温和导泻法的适用范围：赫依性疾病、体虚、精损、肾寒及腹部虫病等。猛烈导泻法主治大便干燥、腹胀、痧症，下半身热症、陈旧性温病及痞块初期等症。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;随着现代科学的突飞猛进，蒙医外治药物的用法及剂型也不断地发展，如今已研制出膏贴、注射液等，如嘎日迪注射液。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (685, 2, 54, '蒙医“扎萨勒”及其起源', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医“扎萨勒”及其起源 \r\n      <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 陶.苏和 敖银梅 <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药是祖国医药学的重要组成部分，是蒙古族人民在长期与自然和疾病作斗争中积累起来的医疗经验总结，其历史悠久，内容丰富，具有民族特点和完整的理论体系。“扎萨勒”是蒙医疗法中的一部分，是一种较古老而又最基本的医疗手段，它有操作方便、器具简单、费用少、对疑难症作用迅捷、治愈率高等优点，其起源可追溯到史前文化时期，它的产生、发展与蒙古民族及其先民生活方式和自然环境有着密切的联系。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙语“扎萨勒”是“疗术”的意思，更确切地说是“非给药疗术”，有人习惯汉译为“五种疗法”，实则不仅是五种，而是包括许多非给药传统疗法，如：灸、针灸、放血、涂擦、罨敷、拔罐、浸敷、缚浴、沙疗、石疗、披皮、烙灼、色搏斯勒格、正脑、正骨、正内脏、脉泻、催吐、水浴、骨汤浴、油脂疗、饮食疗等，都包括在“扎萨勒”之内，因此译为“五种疗法”不能确切地表达其真正含义，故采用蒙文名词音译“扎萨勒”，更贴其义。<br>\r\n      隋唐时期蒙古部落已从原始社会分化出来，以游牧生活为主，有少量家庭手工业，传统医疗也有了发展：如“蒙古灸”是在原始灸疗的基础上，创造发明的适合于游牧生活方式、生产条件及北方寒冷气候的特点的一种疗法，即“奶油拌小茴香涂在毡上加热裹敷”以达到治病的目的。传统刺血疗法也是蒙医外治术之一，据有关记载这种疗法早在8世纪或更早时候已被传人西藏。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;根据考古学家，近年来在内蒙古地区出土的石针、骨针、青铜砭针等足以证明北方民族在石器时代就开始创造和使用针灸疗术。高希言先生在《火针的起源与发展》一文中指出：一般认为火针系由《灵枢・九针十二原》中之火针发展而来，故可谓火针是与灸相结合的一种方法；《素问・异法方宜论》讲灸的起源：“北方者，天地所闭藏之地也，风寒冰冽，其民乐野处而乳食，藏寒生满病，其治宜灸，故灸者，亦从北方来”；自王冰注《素问》，直到唐代依然是“北人正行其法”，而现在火针亦盛行北方，故可谓其来源于北方”。历史学家、考古学家们认为从内蒙古多伦淖尔出土的石器时代的石针、伊克昭盟出现的青铜砭针、包头阿善遗址发发掘的骨针为医疗用具，北方民族的针灸、放血疗法等医疗手段在唐朝时期早已传人吐蕃。在《大宇妥・元丹贡布(公元790～915年)传》中记载“蒙古地区引病善于用放血疗术”，在8世纪成书的藏医《四部医典》中也有“治赫依病则用蒙古灸”的记载。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古畜牧业的发展有着悠久的历史，《魏书》称：“失韦……养牛马”，《北史》谓：“室韦……无羊，少马”，《新唐书》云：“室韦……其畜无羊，少马，有牛”，这些记述表明蒙兀室韦早有畜牧业。到了10世纪初蒙古诸部在这一基础上进一步发展了畜牧业，这时畜牧业在蒙古族经济生活中的作用越来越大，占据了主导地位，成为生产的主要部门，生活的根本来源，“全部财产皆在于是”，“家畜且供给其一切需要”。传统的“红白食”是古代蒙古人的饮食疗法之一，“红食”主要指肉类，“白食”则指乳制品，如：奶酪、奶皮、奶油、奶酒以及鲜奶、酸奶等，因此以“红白食”滋补身体、治疗疾病的丰富经验是古代北方民族的传统疗法特点，蒙古人抢救失血过多昏迷不醒的病人时常用马奶来治疗，如在一次战争中成吉思汗受了箭伤，在大量失血的情况下有人给他找马奶喝，当时的战场上找不到马奶，而给他喝了对水的塔日克(发酵的牛奶)，成吉思汗马上就苏醒过来了。“红白食”疗法后来成为蒙古人的一种独特医疗方法，14世纪出现了这方面的专著，元太医饮食营养学家忽思慧(蒙古族)于1330年撰写了《饮膳正要》一书，此书目前是我国第一部最完整的饮食营养学专著，书中大量收录了蒙古族饮食疗法，其中酸马奶疗术在蒙饮食疗法中占有独特而重要的地位，早在成吉思汗八世祖母莫拿伦时代就有利用马奶来治病的记载。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;正骨、治伤外科是传统蒙医学中的一门临床学科，独具特色。蒙古人在骑马、驯马、狩猎以及长期的战争中经常遇到跌伤、射箭、创伤等内外伤，这成为传统蒙医正骨、正脑、正脏器外科产生和发展的条件，所以元朝时期的正骨、治伤外科知识在原有的基础上得到迅速发展，成为一门独立的学科。《中国医史讲义》中讲“汉唐时期已有伤科论著，宋元(特别是元代)更有显著发展，这与当时的治疗大量骑兵在征战中受外伤、脱臼、骨折的需要有直接关系，元代官方医学增设了正内兼金镞科，反映了当时伤科的发展有较高水平”。在牛、驼腹里浸泡重伤员施行解救的医疗方法也很有特点。《元史》记载“出征回回、斡罗思等国，每临阵，而智而奋身力战，身中数矢，太祖亲视之，令人拔其矢，流血满体，闷仆几绝。太祖命取一牛，剖其腹，浸热血中，移时遂苏”；“从征西京，睦欢力战先登，连中三矢，仆城下，太宗见而怜之，命军校拔其矢，缚牛，刳其肠，裸而纳诸牛腹中，良久及苏”。其他文献中也能见到在驼腹中浸泡重伤员的记载。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古人善用烙灼来治疗断脉出血之伤。在《蒙古秘史》中记载“孛罗忽勒口上带着血，因斡阔台项上中箭，孛罗忽勒将凝住的血咂去。成吉思汗见了，眼泪流着，心里艰难了，便用火将斡阔台箭疮烙了，与些止渴的物教吃”。<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;银碗按摩疗术在古代蒙古人中盛行，这方面的记载在蒙古史籍中多见，如《蒙古源流》中记载：巴图孟克达延罕患痞症，名曰赛海的妇人以银碗按摩之，穿漏三口，病愈。《水晶珠》、《成吉思汗传》等史书中也有类似的记载。涂奶酒、白酒、奶油按摩都是古老的医疗手法，这些疗术对消化不良、腹痛、腰腿痛、风湿性关节炎、子宫移位症、创伤及骨伤均有一定的疗效。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;还有一种叫“结节带的疗术，它是在绸缎腰带上按人体穴位打数个节，系在身上进行自然按摩，这种疗术适合于四肢、头颈部、躯体等多种疾患。《新元史》里提到斡阔台罕患腰痛病，系宝石带病情即愈。这个宝石带就是蒙医常用的“结节带”按摩术。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;此外“扎萨勒”中还包括：巴力亚疗术、道莫疗术、尼如哈，气功疗术、井・百日胡疗术、酒糟疗术、滋补壮阳疗术等诸多疗法，因篇幅的关系恕不一一介绍。<br>\r\n      下面简要介绍部分“扎萨勒”具体操作方法和适应症。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 托努日(灸疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在体表指定穴位上用灸草棒或灸草条烧灼、熏熨，则有温通经脉、调和气血、增强肌体抵抗力等作用，对消化不良、胃三火衰败、浮肿、水肿、痞症、关节希拉乌苏病、疖痈、炭疽等病都有疗效。古代蒙古灸是将焙制的小茴香包裹在干净的羊毛毡中，在一定的穴位或患处进行烧灼、温熨或将一小块毡片浸泡在奶油中熬煎后取出，待温热时敷于患处。白山蓟灸――白山蓟是一种植物，它有易燃、火力温柔而持久、灰烬不散等特点。西河柳灸――将柳条剥皮削光，放人芝麻油或豆油锅中煎沸，捞出后隔两层沙布置于穴位热熨，主要施于胃、食道癌等疾病。火把灸――将揉成球状的棉花，蘸上芝麻油点燃，直接置于患处。温针灸――它是针刺与白山蓟灸相结合使用的一种疗术，是使温热通过针体传授到人体内部达到治疗目的一种疗法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 嘎拉珠，也叫哈塔古日(穿刺疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;用金、银针和特制器械，穿刺人体特定穴位，接上热源，并通过针具调节，传导温度以增强刺激的一种外治法，也称火针。穿刺器械有：用于穿刺心、肺、肾脏病的大麦头针，用于穿刺心脏希拉乌苏的蛙头针，用于穿刺关节病的剃刀针，用于穿刺排出希拉乌苏和脓液的矛头针，用于穿刺胸腔希拉乌苏和脓液的空心扦子，用于穿刺头颅的扁嘴扦子，用于穿刺水肿和排气的笔头式空心针，用于刮除眼翳的三角针等。穿刺穴位分总穴位和具体穴位，总穴位又分宽、很宽、狭窄、很狭窄穴。宽穴位可斜刺或横刺，很宽穴位可交叉穿刺，狭窄和很狭窄穴位属于要害部位，易发生危险，穿刺时要特别小心谨慎。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 哈努日(放血疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在一定的部位将浅静脉切开进行放血，以达到防治疾病的目的，多用于治疗瘀血和希拉引起的热性疾病。放血包括行术前的结扎、选穴、具体操作及观察血色、血量和辅助治疗等内容，对体弱、孕妇、产后、浮肿、胃阳衰降等寒性病症患者忌施放血术。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 萨莫・扫如勒(拔罐穿刺)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;是一种拔罐与放血相结合的疗术，用以达到改善气血运行来治疗疾病的目的。此法有奏效快、疗程短、简便易行的特点，一般先拔罐后在隆起的部位用三棱针浅刺，然后再继续拔罐。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 井・百日胡(罨敷)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;利用药物或物品敷于人体表面某一部位或穴位，以期达到治疗疾病目的的一种疗术。它具有增强胃阳、强身健体、改善感觉器官功能和促进气血运行、止痛消肿等作用。罨敷疗法分冷、热两种、冷敷是喷激凉水、冰水、冰块或灌于器皿中进行罨敷；热敷是根据病症不同而行术方法也各有差异，如：消化道疾病和痧症则将青盐炒热装入布袋置于腹部热敷，肾寒及小便不利可将干酒糟烘热装入布袋进行局部热敷等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;6 乌嘎拉嘎，也叫阿日善(水浴疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;通过水对人体的浸渍与浴洗作用，以达到防病治病的目的，其中分天然温浴、五味甘露汤浴。天然温泉中所含矿物元素的不同，其治疗作用也有所不同，天然温泉对皮肤病、关节僵直和外伤有显著疗效；五味甘露汤浴具有祛巴达干、除希拉乌苏、清热解毒、活血化瘀、益肾壮腰等功效，也可用于治疗四肢僵直、脾亏损、疡疮、皮肤病等疾患。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;7 色搏协勒格<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;将无病的绵羊宰杀后，在瘤胃中加入药物，在皮面也涂一层药物，趁热令患者骑坐在瘤胃口上，进行浸浴治疗。此法可增强肾阳，养脾血，滋肾壮阳。主治妇女赫依症、寒性赫依病、白脉病及希拉乌苏病、肢体强直等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;8 牙森西冷・德布特勒格(骨汤浴)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;一种利用驴、羊等动物的全骨(包括头、蹄)熬煎的汤进行浸浴治疗疾病的疗术。驴骨汤浴适合于妇女“赫依”证、头晕、肌肉萎缩、皮肤干裂、关节酸痛、胃痹、“赫依”性瘫痪病。羊骨汤浴适用于“赫依”症、全身关节酸痛、肌肉萎缩、肢体麻木等“赫依”性疾病。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;9 都日和勒格(涂擦疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在相应的部位涂油，进行按摩，以达到治病目的的一种外治术。适用于精血不足，忧思过度，年老体弱，皮肤粗糙，失眠及多种“赫依”性病症，如炭疽、痈疖、热性肿胀、痛风、丹毒、白癜风、牛皮癣、疥疮等。操作法：在会门穴、命脉穴、心脉穴、手足心等处，涂擦奶油，充分按摩后，用面粉将油擦净。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;10 陶森・扎萨勒(油脂疗)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;通过涂擦、内服、点滴油脂类制剂来治疗消瘦、失血、营养不良、视力模糊等疾病的一种传统疗术。植物油具有滋补、润肠、抑制“赫依”等功能，对腺体病、虫性病、巴达干病与肥胖症有明显的疗效；动物油则用于关节疼痛、烧伤、腹痛、子宫疾患、脑及耳病、骨髓病、产后劳累、房事过度与静脉栓塞，有较好的治疗作用；奶油则能增强记忆，健胃强身，延年益寿。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;11 图拉布日(导泻)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;采用药物灌肠治疗下腹部疾病的一种疗术。根据病情，导泻法分为峻烈与温和导泻两种。<br>\r\n      峻烈法主治便秘、痧症、尿闭、腹胀、虫病及陈旧性温病；温和法适用于年老体弱者及“赫依”性疾病的治疗。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;12 搏勒朱勒格(催吐)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;利用具有催吐作用的药物刺激消化道，通过涌吐而祛病和清除毒物的一种疗术，适用于食积不化、食痞、剑突痞块、饮食不进等病症。催吐法包括配方、用法、洗胃、催吐量测定及辅助治疗等内容。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;13 苏德令吐拉布日(脉泻) <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;用专门药物刺激消化道，开通脉管，使疾病从脉道或尿道排泻出来。适用于治疗子宫痞、妇女血症、扩散等疾病和血病、希拉性疾病、中毒症、陈旧性热病、痛风、脾病、疖痈、皮肤瘙痒症、水肿、麻风、不孕症等诸症，其治疗过程中分先导、正式用药和善后调理等几部分。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;14 吐卢勒格(泻下术)<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;利用药物刺激消化道，通利大便，荡涤胃肠，排除积滞，以促进消化和新陈代谢功能。主治成熟热，骚热，毒热证，希拉乌苏病，痹症，虫病，血病，希拉性视力模糊等。治疗程序分施治前的准备，泻药的使用及善后调理等三部分。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (686, 2, 54, '蒙医对传染病的认识简述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医对传染病的认识简述 \r\n      <br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 巴雅尔<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古族医家在长期的医疗实践中对传染病有了较全面的认识，而且积累了丰富的预防治疗经验。 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医称急性传染病为瘟疫，关于本病之种类，据《四部医典・秘决本》称：有时疫、痘疫、肠刺痛、白喉、流感等5种。蒙医把各种传染病概括为“粘”证，即由肉眼看不到的粘虫所引起的。蒙医还认为四时气候反常，劳累过度，忧愁悲伤，恶臭、忿怒、恐惧、饮食不节或食不洁之物等因素，均可使人体之功能减弱或失调，为粘虫之侵入和活动给予可乘之机。粘虫是通过毛孔或皮肤微小损伤以及呼吸道、食道进入体内，随血行窜遍全身。由于粘虫所侵和发病部位不同，而发生各种不同的瘟疫。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医古籍《兰塔布》一书中记载了许多粘性剧毒证，诸如：“病势似雷轰一般迅速而来不及治疗，可造成暴发流行导致大批患者死亡的黑粘症。该病可引起剧痛，使机体功能紊乱，热疾出现寒证，寒疾出现热证，脉象和小便出现各种不同的证候，所以，医者则无法进行诊断。恶臭、灾难性传染而导致扩散，而且很快会危及生命”。书中全面阐述了传染病的内容，并明确地指出了传染病的危害性。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;17世纪蒙医占布拉所著《方海》一书中在论述传染病粘疫时提到：“粘疫证亦称蒙蔽险峻证，由粘热、风、虫、黄水等五种因素合并而成，以气味进行传染”。特别是书中对传染病的分类、症状、预防治疗等方面进行了详尽的阐述。值得提出的是书中记载了五种预防传染病的方法，即佩戴、熏蒸、口服、隔离、 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;清净等五法。其佩戴法是将麝香、阿魏、雄黄、黑菖蒲等药物共研细为丸，用于净布子包好后佩戴在脖子上以预防热性瘟疫的传染；熏蒸法是将猿猴肉、黑云香共研细末，再用猪油搅拌后涂在患者的身上或燃烧用烟气熏蒸身体，有预防和治疗瘟疫的作用。18世纪蒙医学家伊希巴拉珠尔所著《甘露四部》中记载：“如食用蛰伏之病塔尔巴干肉，即得塔尔巴干瘟”。“塔尔巴干”汉语称旱獭、塔尔巴干瘟是一种烈性传染病，系由粘虫引起，现代医学称鼠疫。古籍又称：“塔尔巴干瘟，其势猛如霹雳，实属无暇施治之黑瘟病”。临床表现起病急剧，如病邪散布于血中，烧损正精，使全身中毒，随而病势迅速加重。其治疗方法在《甘露四部》中记载：“拂晓时饮用雪水，在湖中洗澡一日数次会使患者身上的恶臭洗净，同时内服解毒泻剂，病趋好转”。此外，本书还记载当时普遍流行的腾布病即梅毒和草原上常见的狂犬病。认为这两种疾病是接触毒证，对其发病原因、传染途径、诊断等方面的认识与现代医学基本相同，而且在预防治疗方面的效果十分明显。特别是蒙医古籍中指出狂犬的唾液有传染源“粘”，与当今医学所谓狂犬的唾液腺中存在着病毒相一致，是非常新奇的。除此之外，蒙医对病毒性肝炎、结核病等传染病有其自己独特见解，治疗效果显著也是当今医学界所公认的。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (687, 2, 54, '蒙医骨伤学史简述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医骨伤学史简述<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 和尔伦巴特尔<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;传统蒙医正骨术是蒙医学的重要组成部分，它是蒙古族人民在特有的生产生活习俗及地理环境中，同自然，尤其是同伤病长期斗争的经验结晶。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古民族自古以来就以肉酪为食，以狩猎游牧为主要生产生活方式。故每日都要剖解猎物或宰杀牲畜，日久天长积累了较全面系统的家畜及常见动物的生理解剖知识。所以男女老幼皆能宰解牲畜，是蒙古民族的一大突出特点。随着生产力的发展、对畜牧业的经营和畜产品的利用要求也随之不断提高。此时牧民自然而然的依靠所掌握的家畜生理解剖知识来治疗家畜的伤病。从而渐渐摸索出一套简便实用的治疗方法，而且流传至今、如今在边远地区牧民中有些方法仍在沿用。如内蒙伊克昭盟鄂尔多斯地区牧民对羊腰部受击而致拖髋不能站立行走者，牧民认为是因肾受震荡所致。故把羊四脚朝天放于地上，一人抓羊两前肢，术者抓住两后肢，用木板酌情击震双后肢即可。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古高原地域辽阔，秦汉前后，部落众多，各部落之间争战时有发生，常有人员伤亡。在救治伤病员时，与牲畜和野兽的生理解剖结构对比观察中，使人们对人体结构有了初步认识。加之，蒙古民族特有的社会生产生活方式，即马背文化决定了其因坠马、牛顶、马踢和野兽咬伤等事故中所见伤筋动骨较多，客观要求他们不断摸索克服这些伤病的方法。如：人们摔伤时见到自己肢体变歪变形后，情急之下，下意识地突然自我扭正。脑震荡及内脏震伤者为自救被迫乘马、驼或用勒勒车拉着返归或急投医的颠簸途中有时患者伤病症状自然缓解或痊愈而使人受到启示，联想到人体结构及创伤的治疗与家畜相近，逐渐发明创造了一套治疗骨伤的方法。据史书记载，蠕蠕民族有阿纳环。“阿纳环则是治疗者之义也”。今蒙古语谓“医疗方法”。又提到匈奴有医，苏武在匈奴引刀自杀后，匈奴医凿地为穴，下置温火，把苏武放在上面并按压后背……“半日后仍复苏醒”。这些诊治技巧方法在民间中广泛应用，形成一些以家族为主的骨伤治疗正骨世家，世代相传。遇疑难之症时，以解剖尸体或死囚来寻找治疗良策。如：“公元1263年，元宋交战时，元将匣刺，矢镞中左肩不得出，钦察惜其骁勇，取死囚二人，封其肩，视骨节浅深，知可出，即为其凿其创，拔镞出之，匣刺神色不动。”自然更充实了对人体生理解剖知识和创伤治疗的经验，也逐渐形成了适合自己民族生活习俗、生产方式，地区地理特色的多种诊治方法和技巧。<br>\r\n      13世纪初，成吉思汗统―蒙古各部，建立统一的蒙古帝国到明朝，随着蒙古社会经济文化的发展，传统蒙古正骨术也在内容方面，有了进一步的发展。这与当时治疗大量骑兵征战中外伤脱臼骨折的需要有直接关系。元代官方医学增设了正骨兼金镞科，就反映了当时伤科不仅自成体系，而且已发展到了较高的水平。12―14世纪在多伦淖尔问世的《回回药方》一书第34卷中收载的流传于蒙古地区的部分骨外伤治疗方法中就充分反映了传统蒙医正骨术的成绩和特点。元朝危亦林所著《世医得效方》一书称“……在骨伤科方面，尤其是有关正骨手法，麻醉药的应用等记述都是十分宝贵的，为我国古代骨伤科的代表作品之一”。这很可能是在与蒙古人入关的同时，蒙古正骨术传入关内，作者受其影响所致。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;明清时代，蒙医情况，远胜于前。明代有扎失列在京行医的史实。他的医术很好，颇得人们的信仰。他还通过心传口授，教其徒弟，把自己的学术传授下来。据《青史稿》记载：蒙医正骨医师取骨折碎骨时，便使用了冰块进行低温麻醉，其手术技术十分高超，故清朝政府在上驷院设“……蒙古医士凡禁廷执事人有跌损者咸命其医治限以日期报愈、逾期惩治焉，齐息园侍郎坠马伤首、脑涔涔，然蒙古医士尝以牛脬蒙其首以治之其创立愈……”。当时规定从3个蒙旗中挑选了30名精通骨科的蒙医担任正骨医士临证限期治愈。是见当时的蒙医正骨术是多么发达。清朝初期骨科名医淖尔济墨尔根“……有正白旗先锋鄂硕与蒙古战中流矢命在须臾，济为拔镞传以药遂愈，又都统吴拜交战时，身被30余矢已昏绝，济令剖橐驼腹置拜其中，遂苏，又黄冠苗君稷之徒臂屈不信、济先以热蕊熏蒸、次用斧椎其骨，手捏有声对好即愈，其余起死回生不可枚举，时比之神医华陀云。”还有“……最著名有觉罗伊桑阿者，以正骨起家，至于巨富。授其徒法、先将笔管戕削数段，令其包摩挲、皆使其节，合接如未破者，然后如法接骨，皆奏效焉。……雍正六年，俄罗斯遣子弟入学……其学蒙古接骨大夫者……”。由此可见，蒙医骨伤科经验比较丰富，而且还影响到了国外。16世纪后蒙古骨伤科的理论与实践进一步发展完善，已形成为以顺其人体内在动态规律促进自我修复本能为原则，治疗手段与病人心理相结合的以动治动、动静结合的正骨术，以震治震的内脏损伤治疗同饮食起居相结合的疗养法为基本方式的蒙古骨伤科学。但由于蒙古族居住分散及其特有的游牧生产生活方式和长期征战及其文字形成较晚等之故，医史资料散失，现存古籍中骨伤学方面的记载甚少。18世纪蒙古族医学家伊希巴拉吉尔初次把蒙古正骨术、创伤医治术等传统医疗经验结合理论编写成文。如在《甘露四部》中，他首次论述了“创伤医疗术”、“骨伤疗法”、“脱臼复位术”和“脑震荡疗法”等理论与实际操作的内容。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;16世纪末17世纪初随着佛教传入，部分蒙古骨伤科医家有机地吸收了藏医关于外伤的血期、黄水期，脓期的三期变化及用药理论，将其与自己的实践相结合，使理论与医疗实践进一步充实完善。但大部分蒙古骨伤科医家未受藏医理论的影响，仍以自己独特风格，通过家传口授流传了下来。今边远农牧区蒙古正骨师们的乍惊法、震疗术、畸形愈合骨折的拆解法、瑟必素浸渗法……等正骨技巧及特点即可略见一斑。对此如不及时抢救继承，大有失传的可能。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;新中国成立后，蒙医骨伤学有了很大发展，1958年蒙医正式进入了高等学府殿堂。如今已有了比较完整的蒙医骨伤学教材。还有了一些蒙古正骨医院。随着各种先进医疗设备的出现，使蒙古骨伤学与现代医疗诊治手段得以相得益彰。相信通过抢救、整理、继承，定能使蒙古正骨术重放异彩，为人类作出新的贡献。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (688, 2, 55, '处于低级开发状态的蒙药蕴藏巨大商机', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;处于低级开发状态的蒙药蕴藏巨大商机<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;具有鲜明民族特色和神奇功效的蒙药，长期以来一直处于低级开发状态。一些专家指出，处于低级开发状态的蒙药蕴藏着巨大的商机。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;形成于13世纪的蒙医药学，是用纯天然药物和一些器械来防病治病的蒙古族传统医学。在其漫长的发展过程中，还相继吸收了古印度医、藏医、中医和西医等医药学，使其日臻成熟和完善。根据长期的临床实践，蒙医药不仅对骨科疾病有独特疗效，而且对心脑血管疾病和血液病等疑难病症有明显疗效。据内蒙古卫生部门提供的资料显示，蒙医药治疗冠心病、高血压、老年缺血性中风和心血管神经官能症等心脑血管疾病，总有效率均超过90%。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;但是由于缺乏资金和人才，蒙药产业发展十分缓慢。目前，内蒙古只有4家蒙药制药厂，2000年总产值仅为3000万元。对外开放程度很低，内蒙古蒙医药企业中，招商引资建立的企业仅有一家――内蒙古复旦蒙耀生物技术有限责任公司。该公司成立于2000年9月，是由内蒙古创业集团和复旦大学共同出资组建的，并在上海成立了蒙药研发中心。经过近一年的努力，复旦蒙耀公司自主研制的一个蒙药四类新药已进入初审阶段，一个蒙药三类新药也取得了阶段性成果。复旦蒙耀公司的目标是在短时间内建成一个具有鲜明民族特色、具有创新和竞争优势的蒙药产业化集团。　 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;值得欣喜的是，内蒙古的亿利、伊利、仕奇和盘古集团等实力雄厚的企业，均已着手筹划投资蒙药开发。来自香港的新世界中国实业项目有限公司总经理纪文凤认为，目前蒙药的产业化开发程度比较落后，发展空间巨大。纪文凤最近对内蒙古的蒙医药事业进行了考察，以寻找投资点。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;这一蕴含巨大商机的产业，已经开始吸引来自海外的关注。不久前，在内蒙古自治区首府呼和浩特市召开的中国首届蒙医药国际学术会议上，来自中国、蒙古、日本、韩国和俄罗斯等十多个国家的蒙医药学专家和企业界人士呼吁：蒙医药亟待进一步发掘，使其更系统、更科学、更产业化，为全人类的健康带来福音。来自日本五十岚针灸治疗院的五十岚秀夫说：“我久仰蒙医药的神奇和疗效，很想尝试用蒙药为病人治病，但苦于日本市场上无蒙药，我希望中国政府能加大蒙药的研制开发，并进入国际市场。” \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古卫生厅副厅长郝富说，蒙药产业商机日渐显露。他认为，这主要得益于以下几个方面的原因： \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其一，近来，蒙医药的发展已引起内蒙古自治区政府的高度重视。在西部大开发战略中，内蒙古已把蒙药作为重点产业给予扶持。近日，内蒙古自治区政府成立了生物技术与中蒙药科技开发领导小组，对内蒙古生物技术、中蒙药开发和产业化进行统一协调和领导。此外，内蒙古还准备先期投入１００万元成立“内蒙古中蒙药和生物药研究工程中心”和“内蒙古中蒙药研究开发发展基金”，并争取在今后的５年内研制开发出四五种具备进入国际市场条件的新蒙药。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;前不久，内蒙古人大常委会还颁布了《内蒙古自治区蒙医中医条例》，以地方法规的形式确定，要全方位发展蒙医中医事业。其中特别强调政府要鼓励和吸引国内外团体和个人投资、捐资蒙医药事业。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;据新上任的内蒙古生物技术与中蒙药科技开发领导小组办公室主任马强介绍，自治区政府正准备出台有关蒙药产业招商引资的特别优惠政策。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其二，蒙医药日渐被越来越多的患者所青睐，蒙医药市场在不断扩大。全国有蒙医院39所，分布在内蒙古、新疆、辽宁、青海等省区。据内蒙古中蒙医院统计，该医院年门诊量由几年前的不足20万人次达到现在的近30万人次，其中１／３的患者是从自治区外慕名而来的，包括从俄罗斯、德国、日本和美国等国家来的患者。内蒙古自治区还有旗县级以上中蒙医疗机构80多所。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其三，丰富的蒙药材资源，为蒙药产业的做大做强提供了基础。据统计，内蒙古有蒙药材2000多种。近年来，为了防止野生药材品种退化和人为滥挖，内蒙古还利用现代生物技术进行大面积种植，建立了五大中蒙药材种植基地，这五大基地分别是阿拉善盟梭梭肉苁蓉繁殖基地、鄂尔多斯市甘草种植基地、包头市黄芪种植基地、大兴安岭鹿茸生产加工基地和呼和浩特市周边地区沙棘种植基地。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;其四，加强人才培养，构筑蒙医药产业起飞的平台。内蒙古呼伦贝尔蒙医学校、内蒙古民族大学蒙医药学院和内蒙古医学院中蒙医系，是专门培养蒙医药中高级人才的场所。内蒙古加强这些院校、系的建设，计划5年内选拔培养5名在国内及国际具有一定知名度的中青年蒙药研究专家、20名学科带头人，形成合理的蒙医药人才梯队。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (689, 2, 55, '略论传统蒙药方剂剂型改革', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;略论传统蒙药方剂剂型改革<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 \r\n      特木尔<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药方剂学历史悠久，有独特而完整的传统理论和确实的疗效，是蒙古族人民长期同各种疾病作斗争的经验总结和智慧结晶，是祖国药学宝库中之瑰宝。蒙药方剂和其他药学制剂一样，也是通过一定的剂型理论指导下服务于人类健康的。为了各族人民卫生保健事业的需要、应不断运用医药基础理论的新成就和现代科学的新技术、新工艺研究和改进蒙药传统剂型，是蒙药事业科学技术现代化的重要步骤。现蒙药文献已整理出3000余种方，其中常用的有400种左右，但用现代科学技术方法系统研究传统蒙成药的剂型改革及研制新剂型、新产品等工作开展较晚，近几年来才陆续有所报导，为使这项工作顺利健康的发展，提出粗浅的看法供探讨。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1、蒙药方剂的理论的来源<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;方剂理论是根据蒙医药的基础理论产生的。如五源生机的人体内存在三大因素：“赫依”、“协日”、“巴达干”；“七大物质基础”；“三种排泄物”。三大因素支配着七大物质基础及三种排泄物的运动变化。在一定条件下，赫依、协日、巴达干三者保持相互协调、维持人体正常的生理功能活动。若其中之一偏盛或偏衰而失调，平衡破坏，就会产生疾病。而且蒙药学理论同样认为药物与五生有关；其性、味、效、力亦原于五行。并由此而产生蒙药的六味、三化味、八性、十七效、二力等独特理论。但蒙药在临床上应用往往是复方，单味药甚少，而味、性、效、力是指单味药讲的，在复方中应如何应用?蒙药配伍时在上述理论指导下以药味作用配伍法，以消化后的三化味作用配伍法，以药物功能配伍法等三种基本的配伍方法，而且都有各自的临床效应。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．蒙药方剂的配伍原理<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1依据药味作用配伍法：这是根据蒙药的六味作用理论配伍，并能够用药味效能理论所指导的一种配伍原理，从方剂文献现已发掘74种配方，在临床上多用于赫依、协日、巴达干的偏盛或偏衰所引进的内环境失调的疾病，临床上多见于汤剂的配方。例：一种味配方――肋助花――三味汤(肋助花、麻花秦艽、船形乌头)都是一种苦味的药，按药味理论可医治病理状态的协日。这一独特的依药味作用配方原理，以蒙药的六味学说为理论基础。蒙药的“味”很可能有共同的物质基础，即药味相同者可能含有某类基本母核相似的化合物，或含有共同的功能团，也许是空间构型相似，甚至是有一定的电子云密度分布等等。如果通过成分分析，药理实验，并结合仪器测定能穹其究竟，可给传统蒙药找到科学依据或赋予新的理论，对此为蒙药基础理论，维持人体内环境的平衡和预防保健品的研究具有重要的理论价值。对研制新产品、开发新药有一定的指导意义。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 依据消化后的三化味作用配伍法：这是指药物的六味经消化后变化的情况，药物服后，与胃火相遇，这时巴达干、协日、被赫依(指机体三大因素参于消化功能的成分)依次消化。甘味和咸味被消化后变为甘味，酸味处于中间阶段，消化后仍为酸味，苦、辛、涩味消化后成为苦味。消化后的每一种药味，能医治两种疾病(指上述三大因素，病理条件下合并形成的疾病)。蒙药方剂理论认为这种配方原理适用于治疗三大因素在病理情况下合并症及综合时可以避免对其药物的利弊效应。对此应用现代药理和生化技术探索其原理，搞清其药理作用发现和发明新的用药理论和方法，研制新剂型是有意的尝试。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 依据药物的功能配伍法：这是除上述药味，三化味作用外只依着药物的功能配伍的方法。是针对五脏六府组织器官的功能性或特别是器质性病变，现在蒙药临床上最多见的一种制剂配伍方法。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．蒙药的剂型理论和新剂型的研制<br>\r\n      蒙药剂型是蒙医理法方药的重要组成部分，是通过一定的剂型理论来服务于人类健康的。这主要是蒙药―一药性、药味、药效、药力和上述三种配伍原理等传统理论指导下结合临床实际需要而产生的一门科学。在进行蒙药剂型整理改进时，要根据蒙医蒙药的基本理论及蒙药的药性特点，运用现代科学知识和方法，通过临床疗效客观指标的观察，新旧剂型对比积极进行蒙药型剂的研究和改进工作。对传统的平息与清泻两类剂型，如平息药剂有：汤、散、丸、搅合剂、药油剂、灰制剂、膏剂、药酒、珍宝剂、纯草药剂等十种。清泻剂有：油疗、清泻药剂、吐泻药剂、鼻药剂、温和导剂、猛烈导剂，脉泻药剂等七种。以上17种传统剂型都有自己独特的剂型理论和特定剂型来发挥药效，现已形成医药密切结合，配合临床需要而发展成蒙药制剂学。在试制新剂型，新制剂型一般经复方筛选要重视蒙药复方的特点，因为蒙医多用复方，其方剂配伍有一定主从关系，有起主要治疗作用的君药，有起辅佐作用的间接药(辅佐药)也有保护脏腑，调正机体、抑制毒性的保健药 \r\n      (解毒药)，还有向病变部位的引入药。正因为蒙医在组方时广重视药物的相生相克，相辅相成的内在规律，所以它具有高效低毒发挥复合药理作用，显示出单味药所不具有的独特疗效。对蒙药复方有必要通过成分分析药理作用的研究，探索其独特作用的物质基础和作用机理，搞清方剂组合，应用的奥秘。复方的研究改进传统剂型的研究，要克服“化学结构唯一决定药效论”的片面看法，必须在蒙药理论指导下密切配合临床，以期达到既保持蒙药复方和剂型的固有特点，又能提高临床疗效工艺先进的目的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 传统蒙成药剂型改革的现状和启迪<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.1 蒙药传统剂型改革前先行复方筛选：蒙药多用复方，因为复方疗效高，用量小，毒副作用小，发挥复合药理作用，显示出单味药所不具有的独特疗效，原因是有它自己独特理论，组方合理。对其独特理论和合理性通过对复方的成分分析及药理作用研究，搞清方中的有效组分、主从关系、协同作用以及药理作用的机制等，从而加以验证，为剂改和研制新产品打好理论基础。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.2 蒙医药理论指导下剂改和研制新剂型：复方的研究，传统剂型的改革和研制应在蒙药的―性、味、效、力等理论，三种配伍原理，组方规则，17种剂型等理论指导下进行，否则不能反映蒙药复方和传统剂型的特性及内涵。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3对复方和剂改研究工作的几点建议：蒙药传统复方及剂型改革的研究工作虽然开展较晚，但取得了可喜苗头，出现的问题会在发展过程中得到克服和纠正。如(1)复方的筛选、提取有效组分、研制新产品等要遵循蒙药传统理论，并用蒙药理论能够作出解释为标准。(2)传统汤散剂改成片剂等，要保持传统剂型特点和疗效，并用蒙药理论能够作出解释。(3)丸剂改成针剂，首先研究和处理好非水溶性药物及物质，要保持成方完整性和合理性，其次蒙药剂型85％是口服给药，应该对新旧二种剂型经不同的给药途径系统观察实验动物的生物学指标和常数，否则无法证明其新旧剂型的优越性，并对研制品用蒙药理论作出解释。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;复方和剂改研究工作应该用蒙西医药药学理论和方法认真探讨和处理，要避免失去传统方剂和剂型的特性及内涵。否则研制品不能成为蒙药，事实上也难于达到现代药的制剂水平。总之，要力争达到既保持蒙药传统复方和剂型固有特点，又能提高临床疗效、工艺先进的蒙药制剂，为保障各族人民的健康，为继承和发扬祖国医药学做出积极的贡献。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (690, 2, 55, '蒙成药额日敦・乌日乐的研究概况', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙成药额日敦・乌日乐的研究概况<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古医学院中蒙医系 特木尔<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;额日敦・乌日乐，由石膏、藏红花、丁香、白豆蔻、牛黄等 29味药物组成，组方严谨，攻补兼施、寒热并用、邪正兼顾，共达清热、安神、舒筋活络、除“协日乌素”之功，是蒙医药学中的传统有效古方。本文综述了近年来的研究概况及展望。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1．历史沿革<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;额日敦・乌日乐首见于蒙医经典《千万舍利》(14世纪末)。《医决补遗)(1691年)所载额日敦是在此基础上，药味稍加增减，调整药量而成，较《千万舍利》去甘草、冬葵子、犀角，加珊瑚、白云香、草决明、苘麻子、川木香、冰糖等药，增强了治“协日乌素”病的力量。《内蒙古蒙成药标准》收载的额日敦・乌日乐的处方及配制方法是参考内蒙古中蒙医院“验方”，并参阅《蒙医金匮》 \r\n        (1700年)，从上述方去冰糖、珊瑚，加甘草、犀角，调整药量而成。目前，全国各地约有8个省市生产，但处方、规格不尽相同，除水丸外，尚有内蒙古中蒙医研究所的额日敦注射液。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2．制剂学研究<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;《内蒙古蒙成药标准》将牛黄、麝香、犀角、藏红花、珍珠研细，与余药细粉配研，混匀，凉开水泛丸，朱砂包衣，打光，干燥即得。传统制备工艺简便易行，无需特殊设备，对药物活性几乎无破坏。通辽蒙药厂用现代科技方法灭菌的额日敦均符合卫生学标准。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在剂型研究方面石山等报导，内蒙中蒙医研究所药物室将其研制出额日敦注射液，批号860716，以下是用注射液作的现代药理研究报导。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．现代药理研究<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3．1对微循环的影响： 李氏等证明静注额日敦1.10g／kg30分后大鼠肠系膜细动脉的血流速度明显加快，较药前增加31％(p<0.01)，血流速度呈现极快速的线流，细动脉口径明显增大，较药前增加 \r\n        25％(p<0.01)，细静脉的血流速度增快，较药前增加 21％(p<0.01)，血流状态亦明显改善，多呈现线粒流，细静脉的口径较药前增加13％(p<0.05)。对毛细血管数量的影响：静注额日敦后同一观察区域内的毛细血管数量明显增加，较药前增加85％(p<0.01 \r\n        )。微循环障碍是缺血性脑血管病发生与发展的主要病理变化，逆转微循环障碍的病理过程，改善微循环功能在缺血性脑血管病的防治中具有重要意义。额日敦通过改变血液流变性，扩张微循环血管，加快血流速度，增加微循环的血液灌注量，从而达到改善微循环功能，防止血栓形成，有效地防止脑血管病。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2 抑制血小板聚集和抗血栓作用：李氏等：实验证明额日敦对阈剂量胶原，adp、aa诱导的家兔及大鼠体内外血小板聚集性，大鼠实验性动脉血栓形成，均具有显著的抑制作用。表现为聚集抑制率增高，坡度降低和血栓形成时间延长，并呈现明显的时效和量效反应关系。并观察到额日敦与另一种脑血管药“嘎日迪一13"合用的作用强度，明显优于两药单用，此结果与临床合并用药相符。实验表明，额日敦具有抗血栓和活血化瘀的功效，具有改善病变部位血液循环和肢体营养的作用，在应用它治疗缺血性偏瘫的同时，还可以起到预防脑血栓形成和脑栓塞的作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.3 对血液流变性的影响：石山等观察了额日敦具有明显降低家免bv pv、hct，加快红细胞在血浆和ns中的eet，减慢esr和降低血浆纤维蛋白原等作用，并呈现明显量效反应关系。因此额日敦降低血液粘度，改善血液流变性是其治疗脑血管病的作用机理之一。实验结果提示蒙药额日敦由于降低红细胞的聚集性，而降低bv，同时又使红细胞的变形能力明显增强，加速红细胞在微血管中的流动，有利于脑血管病患者微循环障碍时脑血流状态的改善。 \r\n        bv尚受pv与血小板聚集性的影响，而血浆纤维蛋白原含量又是影响pv与血小板聚集性的重要成份。额日敦1.10和0.55g／kg，可使家兔血浆纤维蛋白原含量明显降低。李氏等研究表明对多种诱导剂所诱导的体内外血小板聚集和动脉血栓形成均具有明显抑制作用。上述结果表明，额日敦可能通过其降低血浆纤维蛋白原作用，抑制血小板聚集和降低bv，改善血流变性，从而抑制脑血栓的形成。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.4 对心脏功能的影响：额日敦注射液具有毒性低，能提高小鼠耐缺氧能力，降低耗氧量和延长存活时间等作用。李氏等实验表明腹腔注射额日敦2.19g／kg，明显延长小鼠存活时间，减慢耗氧速度，30<br>\r\n        分钟时的耗氧量和死亡时的余氧量均低于对照组。额日敦在明显改善大鼠肠系膜微循环，血流速度加快的同时并不因增加心肌耗氧量而加重心脏负担这对中风患者十分有利。<br>\r\n        对大鼠因垂体后叶素所致急性心肌缺血心电图的st―t变化的影响：李氏等：实验表明静注额日敦2.19g／kg，对垂体后叶素所致的st―t变化具有明显对抗作用，st―t变化的发生率降低为60％心律的变化：给药前心律失常的发生率为70％，给药后心律失常的发生率为40％，表明额日敦对大鼠因急性心肌缺血所致的心律失常具有明显的对抗作用。心率的变化：额日敦2．19g／kg对垂体后叶素所致心率减慢无对抗作用。实验表明额日敦组单纯给垂体后叶素15秒和5分时的心率分别较药前减慢10.6％和 \r\n        32.7％，给额日敦后心率较药前减慢18.4％和36.2％。有减慢心率作用。<br>\r\n        对离体大鼠心脏的心率、心肌收缩力和冠脉流量的影响：心率的变化：李氏等用额日敦22.5mg/l，可使离体大鼠心脏的心率明显减慢。药后1、3、5、10和15分时的心率分别较药前减慢56、59、6l、64和 \r\n        66％。可使心肌收缩力增强，药后3、5、10和15分时的心肌收缩力分别较药前增加21、27、36和36％。有较弱的减少冠脉流量的作用，药后10和15分分别较药前降低14％、34％。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;对正常家兔血压、心率、心肌收缩力和呼吸的影响：额日敦1.95g／kg和0.98g／kg，以家兔呼吸和血压无明显影响。额日敦1.95g／kg，药后1分钟的心率较药前降低17％，而0.98g／kg则无减慢心率作用。额日敦1.95g／kg与0.98g／kg，药后1、5和20分钟对家兔心肌收缩力均无明显影响。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.5对骨骼肌的作用：李氏等实验结果表明额日敦具有活血化瘀的功效，有改善神经、血管肌肉等病变部位血液循环和肢体营养的作用，这与蒙医认为它能治疗“躯肢僵踌”、“神经肌肉疾病”是相符的。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.6毒性研究：李氏等急性毒性试验表明，按序贯法求得小鼠静脉注射额日敦的ld50，为l0.95+_l. \r\n        26g／kg。在临床中按蒙成药标准所规定的原剂型常用量，1次13～17粒(2.5g～3.5g)，1日1～2次，从未发现和闻见有毒副反应。对此有待进一步观察研究。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4 临床应用<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;临床上常用额日敦治疗白脉病，中风瘫痪、半身不遂、神经麻痹，肌筋萎缩、关节疼痛，西医诊断为脑溢血、脑血栓形成、面神经麻痹、肋间神经或坐骨神经痛、风湿类风湿性关节炎、神经血管损伤、肌肉萎缩等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.1脑血管疾病：内蒙古中蒙医院蒙药治疗脑血管偏瘫临床疗效观察组用额日敦原剂型为主方治疗脑血管偏瘫患者240例，其中脑血栓形成187例、脑溢血42例、脑栓塞11例，经治疗后治愈43例，好转或明显好转186例，总有效率为95.4％。辽宁省阜新蒙医药研究所脑血栓临床观察组以额日敦为主方治疗脑血栓患者100例，治疗结果，根据 \r\n        &nbsp;&nbsp;&nbsp;&nbsp;1988年辽宁省综合医院疗效判定标准，治愈47例，好转49例，总有效率为96％。并认为该药对体温偏高、血压上升、脉象洪大数实，舌苔黄厚、尿色发黄气味大者疗效显著。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.2 风湿症：白氏采用额日敦原剂型为主方并加蒙医辨证施治治疗风湿性关节炎40例，30d为 \r\n        1个疗程，结果痊愈者18例，好转21例，总有效率为97.5％。朝氏等采用额日敦为主方并加蒙医辨证施治治疗风湿、类风湿症患者有很好的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4.3创伤性损伤：包清等介绍曾经术后2年和4年未见任何进展，经服用额日敦有显著疗效的 2名典型病例，并认为对外伤性损伤，引起神经麻痹、肌筋萎缩、肢体瘫痪等症有一定的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (691, 2, 55, '传统蒙药剂型改革研究概况', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;传统蒙药剂型改革研究概况<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  内蒙古中蒙医医院 赛西娅 顾维彰 <br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙药传统配方、验方、据有人不完全统计约为 1190个均为复方制剂，现在常用者约200种左右。传统蒙成药有十大剂型，即散剂、汤剂、丸剂、膏剂、酒剂，油剂、灰剂，搅合剂，草药剂，金石合剂。后又研制成薰药剂，搐鼻剂及导药剂等。蒙药传统剂型虽有着制法简便，服用量小(一般2～5g)，疗效明显，使用方便等特点。然而用现代剂型概念来要求，尚有许多值得改进和提高之处，应蒙医用药理论指导下，用现代科学方法和先进制剂技术，研究传统蒙药的化学成分，研制蒙药新剂型，使之高效低毒，质量恒稳，服用方便，是发展蒙医药的重要措施。本文就内蒙古地区对传统蒙药的剂型改革情况简介如下。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药那如・苏木珠尔，是由草乌、荜菝及诃子组成的丸剂，用於除湿祛风已有悠久历史，效果显著，顾维彰等通过对组方中各单味药的镇痛，抗炎作用；两味药配伍时的镇痛抗炎作用；以及诃子解草乌毒性机制分析等实验确证，那如・苏木珠尔以草乌为主药，起消炎镇痛作用，荜菝是辅助药，它能轻微地增强草乌的镇痛药效，又能明显的延长其消炎效应，同时发现荜菝的活性成分具有一定的降低乌头碱类的毒性作用。诃子在原方中用以抑制草乌的毒性，原方主从分明，配伍合理。但是由于原料药材来源不同，草乌中乌头总生物碱的含量及诃子中鞣质成分常有很大差别，从而使制成的丸剂，毒性大小不一。为了提高疗效降低毒性，顾维彰等进行其剂改研究，即提取草乌总生物碱和荜菝挥发油，去掉诃子，采用既能显著发挥消炎镇痛效果，又无毒副作用的草乌总生物碱剂量，与一定量的荜菝挥发油配伍，以吐温――80为乳化剂，用氯化钠调节成等渗的注射液，肌肉注射给药收到良好效果。经田德真等动物实验证明，那如注射液有显著镇痛和抗炎作用，且无不良反应。1982年春将那如注射液试用於临床，经过五年由4个单位对328例的疗效总结，对坐骨神经痛的治愈率为59.7％，总有效率达94.4％，风湿性关节炎的临床治愈率是35.5％，总有效 \r\n        率为 95.4％，类风湿性关节炎临床治愈率为24.2％，总有效率为86.3％，那如注射液高效低毒，受到国家卫生部的重视。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药“嘎日迪一5”系由诃子、麝香、草乌、水菖蒲及木香五味药组成，内蒙医学院和包头二医院蒙医科，分别在原方的基础上制成“嘎日迪注射液”、“嘎日迪片”。经郭质端做动物实验证明两药均有镇痛，抗炎作用后试用临床。内蒙医院蒙医科通过 \r\n        48例风湿痛、颈椎病及腰椎病人的临床疗效观察。总有效率为85.4％，包头二医院蒙医科对30例类风湿性关节炎患者的临床观察与优布芬对比，并通过类风湿因子测定及免疫球蛋白检测，总有效率 \r\n        83.3％，且可与优布芬比美。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“赞旦.古日班”由白檀香，肉豆蔻，广枣3味药组成，碎成粗粉，沏泡饮服，用于心悸心热、烦燥不安。顾维彰等通过析方再组方的实验证明，方中广枣为主药，肉豆蔻为广枣配伍可增强其对心血管的药理作用。提取广枣总黄酮，白檀香和肉豆蔻的挥发油、制成肌肉注射液。命名为复方广枣注射液，经李增唏等经动物实验证明它具有显著的提高小鼠耐缺氧能力、对垂体后叶素引起的心肌缺血有非常明显的保护作用。并使因心肌缺血而发生的心律失常和心律减慢得以迅速纠正。经静文英等临床观察 \r\n        62例结果，确证本品缓解心绞痛及抗心律不齐效果明显。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药三普勒・淖日布，由麝香、牛黄、犀角、珍珠、藏红花等二十九味组成的水丸；扎冲一13是由麝香、珍珠，石菖蒲及草乌等13味制成的水丸。两药均为治疗半身不遂，左瘫右痪，口眼歪邪，四肢麻木，腰腿不利及言语不清等脑血管疾患的蒙医常用药。顾维彰等鉴於组方中药味较繁，贵重药材偏多，故进行修方改型研究。用正交试验法确定新的组方，减少药味，以人工牛黄代替牛黄，用珍珠母代替珍珠，以水牛角代替犀角，用红花代替藏红花。将三普勒・淖日布制成口服液，定名偏瘫康口服液，把扎冲一13丸制成肌肉注射液，命名偏瘫康，二号注射液。经动物实验证明两药对脑血管疾患有着多方面的生理活性。1989年试用于临床对94例缺血性脑血管病的临床治愈率为25.5％，显效率高达56.4%，总有效率为96.8%，正在进一步开发研究。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药勒必巴拉珠尔，是由大戟、大黄、翻白草，水菖蒲等九味药组成的外敷散剂，惯用醋或鸡蛋清调敷，治疗急性腮腺炎及乳腺炎显效。田近代制剂技术将其制成涂膜剂，由于加入了皮肤透入剂．不但用起来方便卫生，而且提高了疗效。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;国家蒙药制剂中心近年来用新概念改革蒙药传统剂型。例如蒙药散剂现利用分散度的原理，即用微晶技术和固态分散法改革蒙药散剂。尤其是含珍珠，寒水石，磁石等难溶于水的药材，经微粉化处理，或制成固体分散体，使药物的表面面积增大，溶解度增加，可以达到吸收迅速完全，生物利用度高，体内效显著的目的。药物微粉化后，药量相对减少，包装采用硬胶囊，因此，蒙药的胶囊制剂就诞生了。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙药汤剂相当于中药的煮散，现采用制粒方法，将十种汤剂改制成冲剂。使用复合袋包装。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙药药浴是蒙医传统疗法之，药浴剂对神经痛，风湿痛，皮肤疾患疗效显著，试制成速溶颗粒剂，分装成适当量，可进入家庭。制剂中心在研制，并受到患者欢迎。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;从以上研究情况分析，在蒙医药理论指导下，利用现代制剂新理论，新技术和新工艺，引进新设备，改进包装，改革蒙药古老的传统剂型，越来越显出其必要性和可行性。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (692, 2, 55, '蒙古族药用矿物与传统分类法的研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙古族药用矿物与传统分类法的研究<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古阿拉善盟蒙医研究所 乌苏日乐特 　　　 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古阿拉善盟医院 斯琴高娃<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族医药学是蒙古族人民长期同疾病做斗争的经验总结，是我国民族医药学中的瑰宝。蒙医药学在几千年的发展过程中不断完善自身的理论体系，现已发展成为具有民族特色的名副其实的民族医药学。蒙古族医药学，为振兴中华，提高人民健康水平付出了巨大贡献，是祖国医药学宝库中的重要组成部分。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族选用矿物治疗疾病历史悠久。据有关史料载，公元前889年问世的《甘露要义八支秘诀续》中就有蒙古族用白石脂治疗病的记载。据此，蒙古族利用矿物药治疗疾病的历史最少已有两千多年的历史。蒙古族药用矿物的数目虽较植物药、动物药为少，但是它的医疗价值在临床上同样是十分重要。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;一、蒙古族药用矿物研究沿革 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在蒙古族医学发展史上，现存最早药学古典著作是《白晶鉴》。《白晶鉴》是著名蒙医药学家伊希巴拉珠尔(1704―1788)，依据大量的梵藏文古典文献，实地考察药用植物、动物、矿物产地、产质，在对品名进行考证的基础上而著成的蒙药学巨著。它的学术价值在蒙医药学上可谓不决于《本草纲目》。《白晶鉴》将蒙古族药用矿物首类，共记载药用矿物计188种。其中宝类67种、矿石类72种、土类38种、盐类11种。伊氏《白晶鉴》所载药用矿物品种远远超过明代李时珍巨著《本草纲目》(刊于1590年)，记载的金石部161种。清代杰出藏医药学家帝玛尔・丹增彭描名著《晶珠本草》(于1840年成书)所记载药用矿物161种。而且它具有浓厚的蒙古族用药特色。在十七世纪八十年代问世的著名蒙医药学家罗布桑措拉尔玛(1740―1810年)《认药鉴》中记载药用矿物87种，其中宝类7种、矿石类49种、土类10种、盐类17种。在十九世纪四十年代成书的著名蒙医药学家占布拉道尔吉《无误蒙药鉴》中记载药用矿物112种，其中宝类38种、矿石类43种、土类11种、盐类20种，并附有图鉴。在 \r\n        &nbsp;&nbsp;&nbsp;&nbsp;1901年问世的蒙古族名医吉格米德丹增加木苏《观者之喜》中记载蒙古族常用矿物药 76种，其中宝类10种、矿石类39种、土类10种、盐类17种。我国国家药典于1977年首次收入少数民族药品时，蒙古族药用矿物禹粮土，以烧伤特效药已被牵先编入。《内蒙古蒙药材标准》第一版，编入蒙古族现代最常用矿物药44种。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;二、蒙古族药用矿物传统分类法<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族药用矿物传统分类法有三级分类法。一级分类是属于宝类、矿物类、土类、盐类四大类，在大类下再设类，类下分种。这是蒙古族广泛采用的传统的矿物分类法。该分类体系各级序具有拉特的标志。<br>\r\n        大类，以矿物性质为标志，把当时社会经济条件下的价值昂贵的矿石，归并为宝类，如红宝石、金刚石、松石、水晶石等。将价格低廉的矿石，则归并为矿石类。如雄黄、白石脂，铁矿等。土质物归并为土类，如禹粮土、白垩等。味碱盐性矿物，归并为盐类。如食盐、白矾等。<br>\r\n        类：在前两大类中，以矿石易熔性与非易溶性；后两大类中，以天然型与非天然型为标志，把易熔性矿石，归并为一类；非易熔性矿石，归为另一类。如金刚石、松石为宝类，属非易熔性矿石；铁矿、铜矿属于矿石类，是易熔性矿石。白垩，禹粮土等属于土类，系天然型矿物；大青盐、光明盐等属盐类，系天然型矿物；角盐、灰盐属于盐类非天然型矿物，墨等属土类，系非天然型矿物。<br>\r\n        在蒙古族药用矿物方面值致得研究的另一个普遍问题是，蒙古族历代本草把动物药材珍珠、珍珠母、贝齿、海螺、低等植物石花、制品角盐、灰盐、砖、瓦等列入药用矿物，并归类。诸在正文中明确指出本品基原乃属动物，反而列入矿物某类中，这是著者明知故犯吗?不是，是历代蒙医药学者，把药用动物珍珠、珍珠母、海螺、贝齿等编入宝类非易熔性矿物的目的，在于一是当时以上动物药材在蒙古地区短缺，价格也较高等历史原因而编入宝类。其二是，以上动物药材因质硬，常需借助火力经过炮制才可配入方剂服用。所以在炮制中它们都具很强的非易熔性。以此为后人暗示以上动物药材的价值及硬度的一种方法。把低等植物石花归入矿石类做为非易熔性药材的情况，如同李时珍《本草纲目》把石花列入金石部一样，是由于人类知识的局限性所造成。砖、瓦归入土类非天然型药材，角盐、灰盐等归入盐类非天然型药材的目的是，由于历代蒙药古典文献中从来不划分制品类别，所以只能采取以制做原料为依据的归类方法。这种传统分类法，在某种意义上也还是比较科学的，这为蒙古族医药学的发展创造了良好的基础。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;综上所述，蒙古族古代药用矿物种类最多，已达202种。我们从古代药用矿物中，除去非矿物8种，古代正式药用矿物还达194种。其中基原确属矿物者147种，古生物4种；制品43种。基原属矿物者中目前对原矿已搞清的有77种，占52.38％，没有搞清原矿物的还有70种，占47.62％；蒙古族现代实用矿物药比较少，只占古代药用矿物的10.84%。所以，加强对蒙古族药用矿物的研究工作是历史赋予我们的重任，我们应该努力完成。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (693, 2, 55, '蒙药药理学研究的回顾与展望', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药药理学研究的回顾与展望<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医药学院 金玉 李宝山 巴根那 孙福祥<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1．过去的回顾<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我国是一个多民族的国家。自古以来，各少数民族在与疾病斗争中，积累了丰富的医疗经验。由于地理环境，风俗习惯的不同，形成了各具特色的民族医学，为各民族人民的繁衍昌盛曾发挥并继续发挥着重要作用。蒙医的“蒙古灸”早在两千年前就已使用并传入内地，载入中医古籍。蒙医的“七十二脉放血”、皮疗法、震脑术、马奶酒疗法是最具有民族特色的疗法。蒙医吸收藏医、中医的医学知识形成了据有民族特色的、独立的蒙医理论体系，是当今世界上独具理论体系的几个传统医学之一。中医学一方面以自己博大的知识影响着蒙医，另一方面也吸收民族医学中的精华，充实自己。从《神农本草经》、《千金方》、《唐本草》到《本草纲目》、《本草纲目拾遗》都记载有蒙医、蒙药的经验和方剂，极大的充实了中医药学的内容。同样清代蒙医药学家章巴拉道尔吉的专著《蒙药本草丛新》是蒙、中医药结合的佳作。蒙、中医药的相互学习、相互渗透、相互充实是祖国医学发展的历史经验。古人为我们做出了榜样，我们有责任继承和发扬，加强蒙、中医药的合作。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;解放后党和政府十分重视蒙医药的发展，我国宪法规定“发展我国传统医学”，这就为蒙医药的发展有法可依，为振兴蒙医药学奠定了基础。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;从50年代后期起始，对蒙药发展史，古代蒙药本草整理，动、植物蒙药的考证和品种鉴定进行了大量的卓有成效的工作。出版了《方海》、《无误蒙药鉴》、《观者之喜》、《蒙医验方集》、《蒙医方剂集》、《蒙医传统方药》等十几部蒙医药著作。吸收植物分类学、中药生药学、天然药物化学等知识，编著了《蒙药志》、《蒙药学》、《蒙药化学》、《蒙药制剂学》等教材，促进了蒙医药教学和科研工作的发展。为了适应蒙药工业的发展，还制定了《内蒙古蒙药材标准》和《内蒙古蒙成药标准》。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;60年代后，蒙药的现代药理学研究得到了发展。很多蒙、西医药工作者对古方“赞丹一3汤”、“阿如拉一3味”、“三普勒一淖日布”、“扎冲一―13”等进行了大量现代药理学研究，筛选出药理作用较强的单体化合物，如荜茇、广枣总黄酮等。特别是内蒙古中蒙医院通过对“三普勒一淖日布”和“扎冲一13”的研究，研制出对脑血管有多种药理作用的“偏瘫康口服液”和“偏瘫康二号”，已通过国家“七・五”攻关成果鉴定。为深入研究蒙药，为蒙药的开发和利用开了一个好头。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;综上所述；40余年，蒙、中、西医药工作者，为蒙药的继承和发展做了大量工作，取得了很大成绩。但是在改革开放的今天，在生命科学飞速发展的今天，加强蒙药药理学研究是社会的需求，也是蒙药走出区外、迈向世界的必经之路。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．今后研究的方向与方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2．1多学科协作是蒙药药理学研究的基本途径：据记载，蒙药有上千种。由于历史和现实的原因，蒙药的品种鉴定、规范化、标准化工作亦在局部进行。《内蒙古蒙药材标准》仅记载322种蒙药。至于蒙药药理学研究只有少数单位，少数人在进行。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;总结过去的经验和教训，加强蒙药药理学研究是继承和发展蒙医药必经的途径。首先要明确蒙药药理学研究的方针。一是要在蒙医药理论指导下，对蒙药的药性、单味药、方剂和治则进行研究，即所谓的临床药理学，二是运用现代药理学、现代中药药理学和其它学科的理论和手段，研究蒙药的药理作用，即所谓的实验药理学。两者有机结合才是蒙药药理学研究的全部内容。要把蒙药药理学研究从单纯经验总结、临床验证转变为临床与实验密切结合的形式。研究手段上要从传统的主观观察，转变为依靠高灵敏度、高精确度的仪器分析上。在组织形式上要把过去孤军作战转变为蒙、中、西医药人员参与的联合协作形式。还要取得有关单位的财力资助，只有这样才能将蒙药的药理研究推上一个新的水平。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;生命科学是新兴科学中的带头科学，而医学(包括蒙医)是生命科学的重要组成部分。人的生物性和社会性决定今后医学科技发展总趋势是在宏观上向群体医学进展，在微观上向分子水平进展。这是不以人们意志为转移的客观现实。在这种趋势下蒙药药理学研究要站的高，看的远些，研究的结果应能说明蒙药的药性，单味药的药理作用，方剂的药理作用和配伍、以及单味蒙药和方剂在机体内的运输和转归等。为了达到上述目的，蒙药药理学研究应与化学、生物化学等学科合作，通过实验确定单味蒙药和方剂有作用的活性物质及其作用机制，从而阐明蒙药及其方剂药理作用的物质基础。万事开头难，蒙药药理学研究首先选择那些临床上有肯定疗效的蒙药进行。经过实验，分析归纳，删繁就简，在蒙医药理论的指导下重新组合，在保持原有功效的前提下，进行剂型改进，做到传统方药新剂型才有竞争力。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2蒙药药理学研究要和剂型改进相结合：目前蒙药剂型汤剂占16％，散剂占27％，水丸剂占 36％，冲剂、片剂、糖浆剂等仅占5％以下，也就是说传统剂型占80％以上。这和中药相比差距很大，需要加快步伐进行蒙药的剂型改进，否则很难走出区外，迈向世界。蒙药剂型改进涉及面广，是复杂的系统工程。其基本内容包括传统蒙药剂型的提高和新剂型的研制两方面，解放后对传统蒙药剂型整理方面做了大量的工作，取得了可喜的成绩。相比之下新剂型的研制和开发显得不足。因此蒙药药理学研究必须和蒙药剂型改进相结合。传统方剂新剂型是蒙药剂型改进第一步，如将四味土木香汤改为颗粒剂、查干扫日劳一4汤口服液等。使之体积变小，服用方便、有效，易于携带和保管，以适应现代化社会快节奏生活和对蒙药需求的不断增长。如内蒙古中蒙医研究所将传统蒙成药三普勒一淖日布和扎冲一13进行药理研究剂型改进，研制出偏瘫康口服液和偏瘫康二号注射掖，通过对赞丹――3汤的药理实验，研究出复方广枣注射液，又如，锡盟蒙医研究所通过对古方阿如拉一3味研究，研制出具有扩张冠状动脉、改善心肌功能的注射液。这是蒙药传统方剂新剂型的典范，既保留了传统方剂的功能，又适应现代人的需求。但是蒙药剂型改进要在蒙医药理论指导下进行，如六味安消散(中国药典)、三味大黄汤均含有大黄、面碱(主成份是碳酸钠na2co3)、诃子，在方剂中起主要的药理作用。在中药制剂中这3种药是不允许在一起的。将三味大黄汤改为片剂，按蒙医药理论3味药一起提取制成片剂，蒽醌类成份就高，如将大黄、诃子分别提取，然后再加碳酸钠制成片剂其蒽醌类成份含量就低。说明在剂改进中要遵循蒙医药理论。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3．我院研究现状<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我院是蒙医药的高等院校，设有蒙医系和蒙药系。对蒙医药的继承和发展具有不可推卸的责任。然而我院又是新建院校，底子薄，设备差，财力紧张，人才少而分散，要求“家家户户”搞研究，实际上是一句空话。10多年的实践证实，在蒙医药研究和开发上资金分散，人才分散，设备分散，严重阻碍我校蒙医药科研的进展。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;为了克服上述困难，集中优势兵力，我院于 1994年组建了中心实验室。在全校三系一部中抽调一批事业心强，业务素质好，有一定科研能力的中、青年教师。其中高级职称占16％，中级职称占66％，初级职称占18％。在完成教学任务的前提下，集中精力搞蒙药药理学研究。将全校大型仪器全部调入中心实验室，建立了细胞培养室、仪器分析室、实验操作室、准备室等。为了克服资金上的困难我们采取 \r\n      2个办法，一个是学校在实验资金分配上优先关照中心实验室的需求，另一方面积极申请科研基金，1年多来申请6个课题，中标4项，与其它系合作2项，缓解了资金困难。中标的课题全部是蒙药药理学的研究。研究对象既有传统的方药珍珠丸、扎冲等，又有单味药广枣等。从蒙药抗氧化、抗自由基损伤起步做了大量的实验室检测，获得了大量参数和信息。已完成学术论文5篇。在将近一年的时间内做出如此成绩，这在过去是不可能的。这充分表明我校领导集中优势兵力、设备，主攻蒙药药理学的决策是正确的。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (694, 2, 55, '蒙蒙医治疗高血脂症新药保利尔', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医治疗高血脂症新药保利尔<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;北京民族医药研究所 \r\n      黄福开 <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古蒙药股份有限公司 \r\n      白乙拉<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;当今世界，特别是在工业发达国家，威胁人类健康和生命最大的疾病是冠心病。1996年世界卫生组织发出警告：如果不控制住冠心病的流行趋势，冠心病将成为“人类最大的流行病”。一个世纪以来，从大量动物实验、遗传、流行病学调查以及临床治疗等研究，已有充分的证据说明，血胆固醇水平增高与冠心病发病有密切的因果关系，降低高胆固醇血症可以减少冠心病危险，以是无可置疑的事实。因此，研究高脂血症(高脂蛋白血症)与防治冠心病，已是两者不可分割的课题。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自古以来，蒙古族以肉食、乳食为主，少五谷蔬菜，居住风寒冰冽之处，逐水草游牧，过车帐鞍马生活，多饮酒御寒解乏。长期受这种生活习俗和生产方式的影响，易藏寒好生饱满不消化和脂质增多症，即西医所称高脂血症。这样的环境，为蒙医创造了接触这些疾病较多的客观条件。所以经过长期的医疗实践，蒙医不仅对这些疾病的病因和发病机理有了深刻的认识，也积累了丰富的防治经验，防治的方法和方药也较多，其中不乏也有卓著疗效者。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医认为：西医所称高脂血症属蒙医经典《四部甘露》等经典所载：“沃赫阿力必达乎额布钦”范畴。“沃赫阿力必达乎额布钦”的内因外缘，即病因和发病机理是过极的寒、凉、重、腻性饮食起居和不当的治疗，使机体赫依、希拉、巴达干三根和胃肠的消化希拉，腐熟巴达干，调理赫依等三项消化之火失调，使分布在全身各个组织器官的分热能减弱，在机体“塔米日”(水谷精微、血、肉、脂、骨、髓、精液)的运化过程中“沃赫”(脂)的生成、消化、排泄受到障碍，增高的脂质在体内沉积，久而成浊，混入血液，所导致的一些病症。治疗的原则是调理运化、清浊化脂，解滞通脉。即：通过治疗调整机体三根，胃肠三火和全身各个组织器官的分热能，进―步增强消化功能，达到气血通畅，排除“沃赫”(脂质)的生成、消化、代谢的障碍，变机体不正常的病理状态为正常的生理状态。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;保利尔，即是基于上述病因和发病机理在蒙医《珊瑚验方》清血热，分离病血与好血之方――“希日汤”和《蒙医成方剂》治疗心悸、心神不守、胸闷、气喘、心刺痛之方――“五味豆蔻散”，《至高方》治消化，生清降浊之方――“通拉嘎―5”的基础上根据临床实践以蒙医阴阳、三根、五元、六基、七素和蒙药两力、六味、八性、十七效能的传统理论为指导，由保利尔课题组先行筛选拟方，然后聘请著名蒙医专家反复论证确定处方，运用现代科学知识和制药工艺研制的三类新药。主要成分为广枣、丹参、肉豆蔻、土木香、红花、栀子、檀香、牛黄、麝香等21味组成。具有行气活血，升清降浊，化瘀解滞，开窍通脉的功能。主治心闷、气短、心胸刺痛、眩晕、头痛、胸胁胀痛、肢麻沉重等症。临床用于高脂血症，对调理脏腑机体运化功能，调解血脂代谢，抑制胆固醇吸收，促进胆固醇排泄，降低血液粘稠度，调解血压，改善心脑供血作用显著。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 保利尔胶囊的急性毒性实验<br>\r\n      保利尔以最大浓度2.088g生药／ml，最大体积0.4ml／10g体重，给小鼠24h内二次灌胃，观察7日，未见毒性反应和胃肠道反应，其最大耐受量>167.04g生药／kg，相当于临床用药量的640倍，结果表明一次大量服用保利尔胶囊无急性毒性反应。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 保利尔胶囊的长期毒性实验<br>\r\n      大鼠按31.32、15.66和7.83g生药／kg剂量灌胃，每日1次，连续90日，结果表明动物一般行为、体重、血常规、尿常规及生化检测的各项指标均正常，未见毒性反应，病理组织学检查结果与对照组比较未发现药物引起的异常改变。用药量为临床用药量的120、60、30倍，结合急性毒性实验结果，说明保利尔胶囊无毒<br>\r\n      性，久服安全。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 保利尔胶囊的药效学实验<br>\r\n      通过采用大鼠、小鼠、家兔三种动物分别形成几种高脂血症模型对保利尔进行了实验研究，并与已知中药制剂脂可清进行了比较。实验结果表明，保利尔不但能明显降低血清总胆固醇的含量，而且能显著的降低甘油三酯的含量，提高血清中高密度脂蛋白胆固醇的含量。其作用与已知药脂可清作用类似。同时，保利尔还能使高脂血症大鼠血液粘度降低，凝血时间延长，常压耐缺氧时间延长，抑制血小板聚集，使血栓湿重减轻，提示本品除具有很强的降脂作用外，对动脉硬化及冠心病的防治也有很好的作用，同时还具有镇静、镇痛及降压作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 保利尔胶囊的临床研究结果<br>\r\n      保利尔胶囊自2000年4月开始由陕西省中医药研究院附属医院国家药品临床基地牵头，内蒙古四家医疗单位协助进行二、三期临床研究工作，截止目前临床研究工作基本结束，正在进行资料汇总与统计学处理。根据已揭盲的临床资料来看：中医证侯疗效为90％以上，降血脂疗效为80％左右，在试验过程中未发现明显的不良反应，总体上看临床疗效优于对照药物“降脂灵”。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 保利尔胶囊的研制工艺<br>\r\n      在保利尔胶囊的研制过程中，课题组遵照蒙医配伍用药习惯，以蒙医药力、药味、药性、药效、三化味等基础理论为指导，注重其科学性的同时，运用现代科学知识，从原料选配，工艺路线设计到产品质量监控的各个环节，采用现代工艺新技术，不仅达到了低成本、小剂量、高疗效、无毒、安全的革新目的，保证了产品质量，提高了临床疗效，也为传统蒙药注入了新机，使蒙药行业在应用新技术方面有了突破性进展，起到了推动和示范作用。所以，其开发研制已被通辽市政府列为地方支柱产业重点扶持项目，并被内蒙古自治区发展计划委员会列为蒙药新药产业化示范工程高新技术项目上报国家计委。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.1 正交试验设计工艺：通过正交试验方法确定了药材最佳颗粒度，溶媒数量和提取时间，缩小了服药体积。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.2 超临界co2萃取技术：采用超临界co2萃取技术，缩短了药物成分在生产过程中的留存时间，降低了损耗，避免了热能性物质遇水、遇热发生降解，提高了有效成分在产品中的含量。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.3 一步动态连续提取技术：对处方中的主要含水溶性成分，采用一步动态连续提取装量，将其连续提取、浓缩、干燥，得到干品。缩短了工时，减少了有效成分的损失，减少了污染，提高了成品质量。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.4 微波灭菌技术：采用微波灭菌技术，即保证了有效成分不被破坏，又杀灭了药材中所含有的微生物，确保了产品质量。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5.5 高效液相色谱定量技术：对主要原料及产品采用高效液相色谱法控制主要成分的含量，使产品从投入到产出都在量的控制下进行，从而进一步保证了产品的疗效。<br>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (695, 2, 56, '蒙古族医学起源及特点之概说', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙古族医学起源及特点之概说<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医学院(028041) \r\n      策・苏荣扎布<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙古族医学是以居住在蒙古高原为中心地带的北方少数民族，在长期的生活、生产历史过程中，同自然灾害和疾病的斗争中所积累起来的医疗经验之结晶。它在发展过程中，吸收了藏医及古代印度医学的部分理论和汉医学的知识而形成了独特的民族医学，以下简称为《蒙医学》。兹将其起源及发展的历史过程和现代蒙医学的特点概述如下。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 任何一个民族的所有科学的产生与发展与其所处的社会、经济、文化和宗教的发展以及生活习惯密切相关。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;自古以来，蒙古族人民在北方蒙古高原过着逐水草而游牧、狩猎的生活，在同寒冷、潮湿、风雪等自然灾害和各种疾病的斗争中，不断地积累了丰富的医疗知识和经验。古代蒙古民族信仰萨满教，当罹患疾病或遭受自然灾害时，即请奉命巫师禳除厉殃，驱邪治病。因而当时之巫师掌握了应用偏方、土方治病等一定的医疗技术。在《蒙医偏方疗法》中记载：“…秦玉之母生病，请巫师治之……当遭祸害、饥馑、疫疠盛行时，亦请巫祭祀祷之”。当时，蒙古人多饮用酸马奶、羊肉汤以补养身体；对由寒冷所致的疾病用热敷、热针、艾灸及色布苏疗法医治；对发病部位则施以拔毒之法及皮疗；由暑热引起的疾病，则施以冷敷、放血、煎服大黄根等医治；由于坠马、跌倒等所致的脑震荡则用“以震治震”之法治之；对外伤出血，则用驼毛、狗毛或人的头发等，施以烧灼疗法止血。可以说，所有这些，是蒙医学产生的萌芽时期。有关蒙医学最早有记载的医书算是中医名著《黄帝内经・素问》第四卷《异法方宜论》第80页第十二章记载：“北方者，天地所闭藏之域也。其地高陵居，风寒冰冽，其民乐野处而乳食，藏寒生满病，其治宜灸 \r\n      ，故灸者亦从北方来”并在《蒙医学简史》引里记载：“在《前宇妥・元丹贡布传略》中提到：‘蒙古地区驱病邪外达宜用放血之法”’。如上所述，古代蒙古民族虽有丰富的传统民间疗法和医疗经验，但因没有自己的文字，所以也没有专著流传下来，同时也未能使之系统化，理论化。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1206年，元太祖成吉思汗统一全蒙古各部落之后，同国内各民族，特别是同汉族之间的往来更加密切，并与印度、阿拉伯和欧洲等国家和地区的通商及文化交流也随之开始，于是蒙古民族的经济、文化也得以发展，到成吉思汗后代元世祖忽必列时代，创造了蒙古文字。从15世纪后期，蒙古族开始信仰黄教(即喇嘛教)，继而从西藏和印度引进佛经，如《甘珠尔》、《丹珠尔》等巨著及《八支心要集》(即古代印度医学――阿优吠陀)、《月王药诊》、《四部医典》等许多医学专著。当时之蒙古帝政、后来的明清两朝政府，实行鼓励发展佛教的政策，因而佛教在蒙古地区广为传布，各地修建了很多寺庙，在较大的寺庙里还设立了教授佛经、哲理和医学的三种学堂，以培养各方面的人才。医学堂称为“满巴拉仓”，从此，开始了有组织有系统的医学教育；同时用蒙文翻译了大量的古代印度、西藏和汉族的文学艺术、哲学、宗教、医学等著作，并以木版本和手抄本发行，随而涌现出许多蒙古族医学专家，编写了不少医药学专著，其中代表性的有大汗贝经王罗布桑丹增扎拉森所著的《泻剂提要及抑制寒症方例》等五部著作，宋布汉贝伊希巴拉珠尔的《甘露四部》、明如勒・占而拉的《蒙医金匮》、罗布桑殊勒和木的《认药学》、占巴拉道尔吉的《蒙药正典》、罗布桑却因丕勒的《蒙医药选编》、吉格木德丹金扎木苏的《观者之喜》(通瓦戈吉德)等，这些著作是在不同的历史时期所写的具有民族医药特点的代表性的书籍。对蒙医学的形成和发展起了极为重要的作用。蒙医学在传统医疗经验的基础上，吸收了藏医、古代印度医学的部分基础理论和汉医学知识，逐步形成了具有独特的理论体系和临床特点的民族医学。18世纪蒙古族著名医学家伊希巴拉珠尔，将多发寒症的蒙古地区的特点和蒙古传统医学的“寒症”理论加以结合，在他所著的《甘露四部》中，把“寒症”、“热症”两章列于“十要症”之首位，并写了“寒热相传”、“寒热相互转化”等数章，丰富发展了“寒热症”理论。伊氏在《甘露之泉》中，对生理、病理、诊断、治疗原则和治疗方法等，从理论上作了精辟阐述，提出了：“在赫依病、希拉病、巴达干病三者之上，加上血病、黄水病和虫病三者，则为六种基本病症，简称“六基症”。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;19世纪40年代，在张家口市由官方成立了西部蒙古蒙医药院，全蒙诊所等医药机构，对察哈尔、锡林郭勒地区的蒙医提供珍贵药材和药品加工工具等。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;特别是在1947年，内蒙古自治政府成立后，在党和政府的大力支持和民族政策的光辉照耀下，蒙医学获得再生并走上了发展的道路。首先，在50年代，自治区政府召开了蒙医代表会议，深入开展党的民族政策和中蒙医政策的宣传教育工作，把分散的各地蒙医们组织起来，建立旗县蒙医联合诊所，把较有名望、技术水平较高的蒙医安排在自治区、盟市和旗县医院工作；同时举办进修班和学习班，提高他们的业务技术水平及服务质量。从此，蒙医在医学教育、科研、临床医疗方面步人正规化的发展阶段。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 现代蒙医学的概况<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;所谓的蒙医学理论体系的特点，是与西医学及其它医学体系相对而言，任何一种医学均以人体及疾病为其研究对象，这是所有医学所具有的共性。然而，他们对人体的观察和研究方法则互不相同，如在研究人体、疾病的诊断和临床治疗上，有着各自的特点。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学内容包括基础理论部分、蒙药部分、传统疗法、临床各科等四个部分。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 蒙医学的最基本特点，可概括为整体观理论、辨证诊断理论和重病因辨证论治理论等三大特点。概括如下：(1)整体观理论指人体与自然界是一个对立统一的整体，而人体则为其缩象。蒙医学认为自然界是由土、水、火、气、空五种物质之精华转化而构成，此五种物质统称五元。人体则以五元之变形物质，即三根赫依、希拉、巴达干和七素――食物之精华、血液、肌肉、脂肪、骨骼、骨髓、精液所构成。另外，精液聚积于精腑，其精华演变成至精华，亦称活力素。三根之间、三根与七素之间都有着共同进行的复杂的内在生理功能活动和对立统一的协调关系，即它们之间既相辅相成，又相互妨害和对抗，以保持平衡状态；并与饮食、气候及外界环境条件相适应有规律地、不断地进行着精华与糟粕的分解，吸收以及排泄等正常的生理功能活动。当研究它们时，以前者为前提，后者为基础，它们是不可分割的一个整体，蒙医在临床实践中，始终坚持这一整体观理论。对三根七素相辅相成的内在活动规律的研究，是掌握人体生理活动内容的基础；反之，对三根七素相互妨害和对抗关系的研究，是掌握人体病理现象的基础。(2)辨证诊断是指对发病的内因、外缘、地点、季节等主客观两方面的资料，结合当时遇到的情况，进行综合分析，作出总的和具体诊断而言。如在诊察疾病时，首先从整体观理论加以考虑，运用问、视、触“三诊法”进行严谨的讯问和检查，然后将所得的资料，按“诊断+要点”及“疾病本质辨证分析”的要求加以分析和鉴别。(3)以重病因为前提与具体病情相结合进行治疗的原则。致病原因虽然很多，但从蒙医学整体观理论出发，归纳为由功能失调之三根所致；由七素之内在运动失调而发生的疾病，其因可归纳为血和黄水二者；从外界侵袭人体致病因很多，可归纳为虫和粘(细菌、病毒之类)，上述之致病原因，称之为“致病总六因”，所有疾病之本质均属于此六因，因此，由它们所引起的疾病，称为“六基症”。对所有疾病都从“六基症”的角度去加以分析研究，针对其发生、合并、蓄积等不同的变化情况进行辨证施治。所有产生均受季节、气候、居住环境、饮食、生活习惯及年龄、体质特性等的影响，其本质和病势发生不同的变化，所以，应视其变化的规律，判明主要是因哪一要素发生何种病变而起主导作用，而后针对具体变化情况按治疗原则进行治疗。治法种类很多，可归纳为滋养疗法和削弱法两种。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 蒙药：蒙药主要分植物性、动物性和矿物性三大类，其中草药用量最多。著名蒙药学家占布拉道尔吉所著《蒙药正典》是一部比较完整的蒙药学经典著作，收载876味药，蒙医常用方剂有300余种，各地蒙医用药习惯亦有所不同。蒙药剂型有汤、散、丸、搅合、灰、浸膏、药油等，随着蒙药剂型的不断改进亦有了片剂、液剂、注射溶液等多种剂型。用药量成人每次不超过2～3克。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 蒙医的传统疗术主要有针刺、艾灸、放血、按摩、正骨、药浴、皮疗、色布苏疗法、偏方、土方治疗等，这些疗法是从体外施治，以调整和恢复失调之三根七素的功能之平衡关系，改善血液循环和精华与糟粕的分解吸收及排泄等正常生理活动，以达扶正祛邪的目的。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.4 蒙医之分科：现代蒙医有内科、热病科、传染病科、外科、眼科、口腔耳鼻科、妇科、儿科、骨科等十余科。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 临床治疗的基本特点<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学是一种传统医药学。同蒙古民族历来居住生活过的地理环境、气候、饮食、生活习惯相关的多发病、疑难病至今仍有发生，相对而言蒙药对这些疾病的治疗，积累了较丰富的经验，如对胃肠等消化系病、心脏病、萨病(中风)等脉系病、痛风、风湿痹病、关节黄水病、骨折脱臼、脑震荡等都有独特的疗法。<br>\r\n      蒙药的来源丰富，用药量少，药价低廉，加工炮制简便，特别是针疗、灸疗、正骨、对脑震荡的“以震治震”等疗法，与其它医学相比，具有相对便利的条件，非常适合于在广阔草原上高度分散居住的牧民的就医。长期进行流动医疗。因此，自古以来蒙医在广大人民群众中有较高的威望。 \r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (696, 2, 56, '蒙医学史概述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医学史概述 <br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 巴・吉格木德 </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;蒙医学史是一个新的学科，前人对此未进行系统研究，为了解蒙医学的产生与发展，本人从70年代开始进行立题研究，发表了数十篇论文，出版了一部专著(蒙、汉、日三种版本)。根据这一初步探讨，这里简要阐述蒙医学的产生与发展概况，请指正。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药学是蒙古民族丰富文化遗产的重要组成部分，也是东方传统医学宝库中的一颗明珠。它是蒙古民族长期以来在同疾病斗争的经验中总结的结晶。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;早在12世纪以前，蒙古族劳动人民就发明和运用了许多适合当时社会环境、生产方式、生活习惯以及地理气候特点的医疗方法。这是蒙医药学的萌芽和积累经验时期。13世纪初成吉思汗统一蒙古各部落后到明朝初期，随着蒙古社会经济、文化的发展，总结提高原有蒙古传统医疗经验，丰富其内容，逐步形成了具有初步医疗理论和独特临床经验的古代蒙医药。16世纪开始，在原有的古代传统蒙医药的基础上，吸收了古印度医学和藏医学，在实践中不断的整理提高，融会贯通，形成了以“三元素(赫依、希拉、巴达干)”学说、“六基症”学说、“寒热”学说为主要内容的系统理论体系，又保持蒙医药独特临床经验的近代蒙医药学。20世纪50年代以后，蒙医药学在医疗、科研和教学等方面都进入了一个新的历史阶段。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;根据蒙古族社会、经济、文化的发展和蒙医药学本身的发展特点，对蒙医学发展史概括为如下四个发展阶段。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1 古代蒙医药萌芽和积累经验时期(公元13世纪以前)<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族同其他民族一样，也经历了漫长的原始社会的历史岁月，但究竟何时进入奴隶社会，现在还没有定论。那时蒙古民族以游牧生活为主、狩猎为副，还有了些相应的简单的家庭手工业。他们生息劳动在北方辽阔的蒙古草原和茂密的森林之中，信仰萨满教。\r\nbr>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药是游牧经济基础上产生和发展的。古代蒙古族及其祖先，在同自然界和疾病做斗争的实践中逐步积累了原始的医疗知识，发明了最早的医疗保健方法。由于当时缺乏规范化的民族文字，以致蒙医学未能得到系统的整理。根据散见于有关著作中的记载，对传统的蒙医临床经验和流传于民间治疗方法的分析，可以肯定已经出现了适应当时社会生活条件的、具有独特内容和方法的蒙古医药知识，例如：灸疗、正骨、外伤、马奶疗法、饮食疗法、药物知识等。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古灸疗是在热敷疗法的基础上发展起来的。蒙古族使用灸疗法比其它民族为早。一千多年前藏医《四部医典》中就有“蒙古灸”的记载。蒙古灸是一种“油(奶油)拦小茴香涂在毛毡上加热裹敷的疗法”。公元前的中医经典《内经・异法方宜论》中云：“北方者，天地所闭藏之地也。其地高陵居，风寒冰冽。共民乐野处而乳食。藏寒生满病，其治宜。故灸者，亦从北方来”。“灸”即指灸法。所谓“北方”虽然没有直接指明是蒙古地区，但当时所说的“北方”和“其民乐野处而乳食”来看，显然是把蒙古地区和蒙古民族的祖先包括在内的。从上述记载里可以看出，火灸疗法起源于蒙古地区，并很早就传入了内地和西藏。灸疗是蒙医的传统疗法，操作简便，用具简单，适合于游牧民族的生产方式、生活条件以及北方寒冷的气候特点。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;刺血疗法是蒙医传统的外治术之一。根据西藏著名医学家《宇妥传》有关“蒙古传统刺血疗法”传到西藏的记载，早在8世纪或更早的时期蒙医刺血疗法已经传到西藏。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医饮食疗法是在用饮食治疗某些疾病的方法中发展而来的。古人最初由于生食，常发生消化不良和食物中毒的现象。人类会用火以后，逐渐改为熟食，消化系统的疾患也就大大减少。蒙古族人民中流传着这样一句民间谚语：“病之始，始于食不消，药之源，源于百煎水”。这是前人对原始时代用饮食治疗消化道疾患经验和医药起源的概括。奶食、肉食、肉汤之类，只要饮用适当，都可能起到滋补、防病治病的作用。这是古代蒙古人民从长期的生活实践中总结出来的饮食疗法的前身。这种治疗方法是适合以游牧为主、狩猎为副的蒙古族人民的劳动和生活特点的。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在蒙医饮食疗法中，酸马奶疗法是值得研究的。蒙古人的祖先早在远古代时代从事畜牧业和挤奶劳动时就已掌握发酵技术，酸马奶、酪酥等作为药物，早已传人内地汉族地区。如《北史》曾说突厥人“饮马酪取醉”；《新唐书》也载有北狄“资重酪以食”。“马酪”、“重酪”，都是指的酸马奶。《蒙古秘史》中就记载了蒙古人普遍酿制酸马奶和喝酸马奶的史实。如：成吉思汉的十代祖先孛端察尔“每日必至，索求酸马奶喝”。当时称酸马奶为“额酥克”。酸马奶疗法，从现有史料来看，早在13世纪以前，在《蒙古秘史》中就记载有元朝以前蒙古人用酸马奶救治受伤大出血昏厥病人的记载。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古族的骨伤科形成较早。由于蒙古人民从事畜牧业和狩猎、飞马射箭等日常生活以及长期征战中经常发生跌伤、骨折、脱臼、脑震荡等创伤。因此，自古以来就积累了不少的正骨治伤和战伤外科的宝贵经验。如在《蒙古秘史》等历史文献中就有用烧红的铁烙烙治流血伤口，用蒸气热罨的活血方法治疗内伤，用牛羊瘤胃内的反刍物做热罨疗法，用热血浸疗法治好箭伤等。在《元史》中记载，成吉思汗的名将布智儿，在战场上奋身力战，有一次“身中数矢，太祖视之，令人拔其矢，血流满体，闷仆几绝。太祖命取一牛，剖其腹，纳布智儿于牛腹，浸热血中，移时遂苏”等治疗箭伤的记载。后来这些治疗经验在实践中发展成为独特的各种蒙医外伤治疗术。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙古草原上和森林中生长着丰富的药用植物，蒙古人用这些药用植物来治疗疾病和骨折外伤，积累了不少的药物学方面的知识。在有关中药古典著作中记载了蒙古地区的特产药物肉苁蓉。在六百多年前拉施达丁所著的《史集》中曾经记载成吉思汗以前居住在鄂毕河上游森林里的“兀刺速惕、贴良古惕和客思的迷这些部落熟悉蒙古药剂，以用蒙古方法很好地治病闻名于世”等史实。两千年前匈奴人用蜜丸，用四味药组成的丸剂“匈奴露宿丸’’被收入唐代《千金要方》，这说明他们已有了一定的方剂学知识。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;这些史实告诉我们，早在原始社会和奴隶制社会，蒙古族人民就发明和使用了许多适合于当时蒙古社会、经济、文化、气候、地理条件的独特治疗方法和经验。虽然这些治疗方法和经验没有形成独立的医学体系，但它是蒙古医学形成和发展的起源。所以我们把它称为古代蒙医药萌芽和积累经验时期。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2 古代蒙医药的形成时期(13世纪～16世纪上半叶)<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;到公元10世纪末，蒙古部落逐渐强大，13世纪初铁木真(成吉思汗)统一了大漠南北的近百个部落，于1206年在斡难河畔建立了蒙古帝国。蒙古社会进入了新的历史发展阶段―封建社会。随着蒙古帝国的兴起和强大，各兄弟民族经济文化的广泛交流，欧、亚两洲各国之间的频繁交往，已有了统一的蒙古文字。蒙医药也进入到一个新的发展时期。蒙医临床医疗经验有了进一步的发展和丰富，并不断的总结和提高，从而产生了初步的医疗理论，形成了古代蒙医药。例如：原始时期产生的蒙医骨科、蒙古灸、刺血疗法、外伤科、饮食疗法、酸马奶疗法以及药物学知识均有了新的发展。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;值得引起我们注意的是这个时期开始人体解剖方面的研究。古代蒙古族劳动人民起初在狩猎宰杀牲畜的实践中积累了丰富的动物解剖知识，并以此来解释人体结构。但是后来由于骨伤科的发展，这种牵强附会的解释，不能满足当时人们对骑马摔伤、骨折、脱位、脑震荡等外伤治疗以及战伤外科要求。因此，13世纪时，蒙古族人民便结合治疗外伤，开始进行人体解剖方面的研究。如“公元1262年蒙古军将军匝刺在战场上“矢镞中左肩不得出，钦察惜其骁勇。取死囚二人、封其肩、视骨节浅深，知可出，即为凿其创，拔镞也之。匣刺神色不为动”。这种结合治疗战伤对人体解剖的研究，不仅丰富了对人体构造的认识，而且大大促进了战伤外科的发展。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;骨伤外科的经验。到了这时期已经发展成为一个有丰富临床经验的临症学科，以后这种正骨术传入内地，促进了元代骨伤科的发展。到明末时蒙古地区出现了不少的正骨学家。据史书记载，在13世纪时蒙古人就已使用了皮疗术。皮疗就是杀死牲畜，立即剥取其皮，披在患者身上或者裹患病部位，以进行治疗。有趣的是还有用鱼皮治“脚瞳”的记载。1267年忽必烈汗曾遣使者赴高丽取鱼皮，鱼名阿吉尔合蒙合(akirhomunho)鱼，形状似牛，“称患脚瞳者，以其皮作靴则立愈，盖帝有足疾故求之”，于是高丽国王献鱼皮十七领。我区牧区和半农半牧区至今仍用羊皮、艾虎皮来作皮疗的<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;饮食疗法：在原始时期蒙古族劳动人民就发明了用饮食治疗某些疾病的简单方法，并在这个时期发展成为古代蒙医学的饮食疗法，并有饮食营养学专著问世。公元1330年元太医忽思慧用汉文编写了《饮膳正要》，这是我国最早的一部较完整的营养学著作。在这部著作中广泛地整理了历代名医有关秘方和验方，同时也编入了蒙古地区的大量的饮食营养学方面的内容，如：马奶、各种奶食、牛羊肉、牛骨髓等。书中有很多药物和饮食名称，如“八儿不汤”(羊腿肉汤)、“阿八儿忽鱼”(鱼名)、“赤赤哈纳”(酸刺)等都是用蒙语译音写的。在这时期酸马奶疗法，有了进一步的发展。《饮膳正要》一书中云：“马乳性冷、味甘、止渴、治热”。《卢不鲁克行记》中写道：“忽迷思(即酸马奶)为蒙古人民亚洲游牧人习用之饮料…忽迷思又可以久存，相传其性滋补，且谓其能治瘵疾”这些史料充分说明，在这个时期对酸马奶的性能已有了比较全面正确的认识，为酸马奶疗法奠定了一定的基础，药物方剂方面的知识进一步得到丰富和发展。据史书记载1226年蒙古军里瘟病流行，曾用大黄治愈的史实。13世纪开始随着蒙古族同国内各民族和阿拉伯、欧洲各国的通商和文化交流，内地和国外一些药物进入蒙古。蒙古草原的特产肉苁蓉等药物也输送内地和国外。如在元代大量进入波斯、阿拉伯和中亚药材。1292年在现在的内蒙锡林郭勒盟多伦建立了“回回药物院”(回回一―指阿拉伯)。内地和外国的药材进入蒙古地区的同时，他们的药物方剂学知识也传人蒙古。后来锡林郭勒盟蒙古学者关布扎布所著的《药方》一书中就收入了印度、西藏、回、汉医使用的药物和验方。这就说明当时不但各地药物有了广泛的交换，而且药物交流的同时也丰富了蒙医药学方剂学的内容。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;长期的医疗实践中随着临床经验的积累，产生了古代蒙医初步的医疗理论。这种理论不仅在古典著作中有所记载，而且重要的是保存在蒙医传统的临床医疗经验里。一千多年前在医学经典中所记载的我国北方的“灸疗法”和“蒙古灸”是“以热治寒”的理论为指导的；忽思慧的著作中所阐述的马奶疗法是“滋补”和“以寒治热”的理论为指导的。他们把疾病分为寒热两大类，同时把饮食、药物、疗术也分寒热两大类进行辩证治疗。可以说这种以寒治热、以热治寒的寒热对立统一学说是古代蒙医医疗理论的核心，是临床治疗的理论指导。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;古代蒙医独特的传统脑荡疗法，其理论原理是值得研究的课题。在18世纪古典医著中虽有“蒙古正脑术”的记载，但没有看到有关脑震荡疗法的理论原理的记载。沿袭至今的民间脑震荡疗法是研究其理论原理的活资料。为了探讨蒙医脑震荡疗法的理论原理，本人访问了治疗脑震荡有经验的各地老蒙医，另一方面还亲自对一些脑震荡病例进行过临床观察。经过反复研究，提出了：古人治疗脑震荡的理论原理是“以震治震、震静结合、先震后静”的辩证学说。发表在1977年《内蒙古大学学报》。14世纪初，希拉布僧格曾把印度经典《金光经》，从维、藏两文译本译成蒙文。其中有古印度哲学“四大元素学说”和阿输吠陀医学“三元素”学说的内容。此书以手抄本或木刻版印行后，蒙医学曾受其影响；这对蒙医理论的发展和系统化起到促进作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;现在我们虽然不能系统地阐明古代蒙医的基本理论，但根据上述史料，可以肯定这个时期的蒙医药不但有了丰富的临床经验而且已经有了初步的医疗理论。另外，这个时期随着蒙古族同其它兄弟民族之间的经济、文化交流日益扩大，医学方面的交流也日益增多。在元代，中药本草书曾译成蒙文。元政府设专管医药的广惠司，由阿拉伯医生主持。有四名意大利名医元朝首都行医。汉医、阿拉伯医、意大利的医疗经验开始传人蒙古地区。蒙医从中吸收了适合本地区和本民族特点的医疗经验，丰富了古代蒙医药的内容。12世纪时蒙古族借用维吾尔文字时期，就受到了维吾尔医学的影响(维医和阿拉伯医有相同之处)。蒙医吸收兄弟民族医疗经验的同时，古代蒙医骨伤科、蒙古灸等某些医疗经验也传人到中原地区和西藏、新疆等其它兄弟民族地区。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;这个时期，蒙医的社会地位也有了明显的变化。如在元朝以前，成吉思汗规定的“扎撒黑”(大意是治理法典)中明确规定“免除医生的税赋”的条目，鼓励发展医学。在1206年前后，蒙古军里设有军医，专门负责战伤外科。由此可见，当时的蒙医队伍，不但在数量方面增多了，而且社会地位也提高了。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，这个时期，由于蒙古族当时社会、经济、文化发展的客观原因，留下来的系统的蒙医史料较少，但从现有的史料对传统蒙医医疗方法中分析，可以看出这个时期已经形成了具体初步医疗理论，并有丰富临床经验的古代传统蒙医药。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3 16世纪以后蒙医药学的发展时期(16世纪下半叶～20世纪中叶)<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在这个历史时期，蒙古民族经历了明末清朝的封建社会和1840年以后的半殖民地半封建社会。经济方面逐渐形成了以牧为主，部分半农半牧，并有适当手工业，成为经营多种经济的地区。文化方面，同汉、藏、满、回等兄弟民族之间的文化交流日益扩大，蒙古民族的文化有了迅速的发展，在科学、历史、文学等方面取得了光辉的成就，涌现了不少著名的科学家和学者。随着经济文化的发展，传统的古代蒙医药也有了进一步发展和提高。在原有古代蒙医药的基础上，吸收了古印度医学和藏医学的理论与经验，同时也受到了汉族医学影响。在蒙医界内部出现了不同的学派，各学派著书立说，进行学术争鸣，出现了不少著名的蒙古族医学家，他们编写了数十部蒙医药著作。在不断的实践中把蒙医药学提高到一个新的发展阶段。这个历史时期蒙医药学发展成为有系统理论体系的医学。在这一阶段古代蒙医传统的骨伤科也发展到一新的水平。例如：在清朝初期正骨医师接股骨骨折手术时使用了低温麻醉术。蒙古族正骨医师觉罗・伊桑阿和外科医师淖尔济・墨尔根就是这个时期的名医。他们的正骨术不仅传入内地，而且在1728年传授给在北京的俄罗斯学员，蒙古族的正骨术传到了俄罗斯。清朝政府在上驷院设“蒙古医士”时，仅仅三个蒙旗(县一级)中就挑选了三十名精通骨科的蒙医担任正骨医士，可见当时的蒙医正骨术是多么发达，正骨队伍的发展是多么迅速。此外，如正脑术、酸马奶疗法、饮食疗法、矿泉疗法(蒙古草原近百个矿泉利用)、瑟必素疗法(用牛羊瘤胃内反刍物热敷的热罨疗法)等蒙古传统的治疗术和临床经验都有了进一步提高。这些传统医疗经验在蒙医古典著作中均有所整理。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;这一时期，随着兄弟民族之间经济文化交流的日益发展，医药学方面的交流也更加频繁起来。近代蒙医药学不仅吸取了兄弟民族医药学的精华，丰富了自己的内容，而且，蒙医对兄弟民族医药学的发展也给予了一定的影响。近代蒙医药学，在蒙医史上达到极盛时期，这与吸收兄弟民族医学经验，特别是与印度医学和藏医学的传人是分不开的。<br>\r\n        藏医是在藏族传统医药的基础上吸收古印度医学和汉族医学的内容发展起来的民族医学。关于古印度医学和藏医学传人蒙古地区的时间有不同的说法，有人认为是元代，也有人认为是清代。印、藏医是随着西藏的喇嘛教传人蒙古的。因此，首先弄清喇嘛教何时传人蒙古是极为必要的。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;公元前6世纪在印度产生的佛教，到了公元7世纪传人我国西藏。13世纪，忽必烈汗扶植西藏的喇嘛教，并试图在蒙古地区推行，但没成功。喇嘛教真正传人蒙古地区是在16世纪末。1576年土默特的俺答汗从西藏请来第三世达赖喇嘛，并自己信奉了喇嘛教，接着林丹汗等蒙古封建贵族也相继皈依喇嘛教。这样佛教以黄帽派喇嘛教的形式在蒙古地区广泛传播，逐渐取代了蒙古族原来信仰的萨满教。随着喇嘛教的传播，许多印、藏佛教经典也传人蒙古地区。同时《医经八支》(astangahardaya \r\n        samhita)等古印度医学主要经典和《四部医典》等藏医学主要经典藏文木刻版本也相继传播到蒙古地区。约在18世纪时这些印、藏医著作被翻译成蒙文，并在北京刻版印行。从上述史实可知，印、藏医传人蒙古地区是在16世纪末的明朝末期。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;印、藏医学传人蒙古地区以后，大大丰富了蒙医的基础理论和临床实践，推动了蒙医的发展。随着印、藏医的传人，在蒙医内部出现了三个不同的学术派别，即：传统的古代蒙医学派、藏医学派和近代蒙医学派，通过各学派之间的长期交流，蒙古医学进入了一个新的发展阶段。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;古代蒙医学派：这个学派是13世纪以后形成的古代蒙医药的继承者。他们有丰富的蒙古传统医疗经验，尤其是有独特内容的骨伤科和饮食疗法等方面的丰富经验。著名正骨医师觉罗・伊桑阿、外科医生和著名正骨医师淖尔济・墨尔根等人是这一学派的代表人物。当时这个学派的某些疗术带有蒙古传统宗教――萨满教的色彩。如：“安代舞”是蒙古人民中流传下几百年的集体舞蹈，也是治病的一种精神疗法和体育疗法。因为这带有一定的萨满教色彩，所以信仰喇嘛教的藏医学派医师一般拒绝用这个疗法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;藏医学派：印、藏医学经典在蒙古地区广泛传播以后，蒙古族中学习印、藏医学，尤其学习《四部医典》并支持其理论的人日益增多，并成为一个学派。当时人们称这一派医师为“雄根额木其”(经典医生之意)，他们有较高的理论水平，其中的很多人精通《四部医典》，编写了许多医学著作，对藏医理论和医疗经验的传播做了大量的工作。但他们忽视古代蒙医学的传统疗术和临床经验。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;近代蒙医学派：这是大约17世纪末、18世纪中叶，在上述两派争鸣的基础上产生的另一学派。他们主张古代蒙医同印度医学和藏医学相结合的观点。这些人既熟悉古代蒙医的传统医疗经验，又精通藏医《四部医典》的理论。他们从17世纪开始整理编写了许多蒙医著作，在他们的著作中灵活地吸收了以“三元素”学说(赫依、希拉、巴达干)为主要内容的医学理论和经验，开始整理了古代蒙医学的丰富而具有独特内容的临床经验。如18世纪蒙医学家青海蒙古人伊希巴拉珠尔在他的著作《甘露点滴》一书中，曾以“蒙古正脑术”为题整理了古代蒙医传统正脑术。在他们的著作中也吸收了汉医和阿拉伯医的某些经验。在这几个学派的长期并存中近代蒙医学派逐渐强大，到了19世纪以后成为蒙医学的主流。18世纪著名医学家伊希巴拉珠尔、19世纪蒙古族著名蒙药学家占巴拉道尔吉、察哈尔蒙古族著名蒙医学家伊希旦金旺吉拉等人是这一学派的代表人物。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;这个时期是蒙医发展史上的极盛时期。在这四百年的历史中涌现了很多著名的蒙医药学家，如17世纪的著名外科正骨医师墨尔根，外蒙的罗桑丹金扎拉桑，18世纪的蒙医学家伊希巴拉珠尔，19世纪蒙药学家占布拉道尔吉，青海蒙古族方剂学家占布拉，外蒙古著名蒙医学家龙日格丹达尔等人。他们给后人留下了《方海》、《四部甘露》、《蒙药正典》等数十部丰硕的古典医学巨著。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在医学教育方面举办了几十所蒙医学校。例如18世纪成立的鄂托克旗的阿尔巴斯山的满布日仓、外蒙古扎亚库沦满布日仓、十九世纪成立的追新蒙古勒金格根高的满布日仓等学校。这些学校培养了不少的蒙医师和蒙药师。<br>\r\n        在理论方面，原有古代蒙医初步医疗理论的基础上吸收了古印度医理论和藏医理论，在长期的医疗实践中不断整理提高，形成了以“三元素”学说、六基症学说、寒热学说为主要内容的理论体系，其主要内容有：阴阳学说、五大元素学说、寒热对立统一学说、三元素学说、七元三秽学说、六因辨证学说、脏腑脉络学说等。其中所谓六因辨证(六基症)学说是按病因进行辨证的理论。脏腑脉络学说是按病位辨证的理论，而寒热对立统一学说则是辨证总纲。这些学说不仅是辨证论治的理论基础，也是解释人体生理功能的理论基础。17世纪开始出现的《甘露之泉》、《脉诊概要》等蒙医基础理论专著和《白露医法从新》、《蒙医蒙药选编》等蒙医古典医籍均有详细论述。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;随着近代蒙医学理论体系的形成，对过去临床经验的总结和提高，临床分科更为详细。近代蒙医的临床科分为骨伤科、内科、温病、传染病、妇科、儿科、皮肤科、五官科。并有《白露医法从新》、《甘露点滴》、《蒙医药选编》、《观者之喜》、《珊瑚验方》等十几部临床医学专著问世。古代蒙医的传统疗术如酸马奶疗法、针灸疗法、刺血疗法、温泉疗法、瑟必素疗法等各种疗术，在这一时期有了显著的发展。在许多经典中有专章记述。<br>\r\n        药物学和方剂学的整理工作取得了巨大成就，出现了专门巨著，如18世纪蒙药学巨著《认药学》(满乌西吉得)、《认药白晶药签》、19世纪方剂学巨著《方海》、蒙药学巨著《蒙药正典》等相继问世。其中《蒙药正典》一书就载人了879种药物，附有药物插图576张。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，16世纪以后的近400年的历史时期，在原有古代蒙医药学的基础上，吸收了古印度医学和藏医学等各兄弟民族医学，在实践中不断地整理提高，融会贯通，发展成为有系统的理论体系，又保持原有古代蒙医药丰富医疗经验特点的近代蒙医药学。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4 现代蒙医药学的发展(20世纪中叶以后)<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;20世纪50年代开始，国内蒙医学进入一个新的发展阶段。但是，在国外，蒙古国和前苏联的布利亚特共和国，一时停顿状态的蒙医药，80年代开始重新重视，正在积极地恢复和继承发扬<br>\r\n        在国内继承和发扬蒙医药学、教学、医疗、科研各方面都有新的发展。50年代开始，把分散行医的蒙医组织起来，为他们建立门诊部、联合诊所等民间医疗机构，并把部分人员安排在自治区和盟、市级医院工作。在自治区首府，分别在1956年和1958年相继成立了中蒙医研究所和中蒙医院，部分盟市、旗和蒙古族聚居的其它省的旗县，亦先后成立了蒙医研究所和蒙医医院，调配具有丰富临床经验和有名望的老蒙医，开展了医疗和研究工作。另一方面，以举办蒙医学习班、进修班、研究班或带徒弟等形式培养了大批蒙医药人才。同时，大量搜集分散在各地的蒙医古籍，还组织有较高理论水平和临床经验的老蒙医，对古籍(因为藏传佛教的影响，16世纪以后的蒙医学家的著作大多是藏文)进行翻译和整理，已翻译的出版的有《四部甘露》、《蒙药正典》、《方海》等三部经典和《蒙医药选编》、《珊瑚验方》、《观者之喜》、《金光注释集》、《月王药诊》等多种古籍著作。40多年来，蒙医界编著出版《蒙医学基础理论》、《蒙医学简史》、《蒙药学》、《蒙汉藏文药名对照》、《肝病治疗经验》、《蒙药理研究》、《蒙医验方集》、《蒙医学基础知识》、《蒙医医疗手册》、《蒙医内科学》、《蒙西医结合心脏病学》、《酸马奶疗法》、《蒙医正骨学》、《蒙药材标准》、《蒙成药标准》等各学科专著。<br>\r\n        广大蒙医同中西医并肩奋斗，对防治常见病、多发病、地方病和疑难病症以及保护各族人民的健康，做出了很大贡献。现在，蒙医广泛利用自然科学成果和现代诊断法，不断地丰富和革新自己的传统诊断法。如在自治区蒙西医合作，对蒙医学从基础理论到临床实践，进行系统地研究和整理，已获50余项成果。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医学教育方面，1958年在内蒙古医学院设立蒙医本科专业，开始培养蒙医高级人才，1992年设立了蒙医医史文献硕士学位点。1979年成立了以蒙医为主的民族医学院，后改为蒙医学院，有的盟成立了中级蒙医学校，有的盟、市卫生学校也开办了蒙医班。蒙医队伍也在不断壮大，现在全自治区有4000余名蒙医，32％在旗县以上医疗、科研、教学和卫生防疫机构工作，大部分则在农村牧区医疗机构工作。80年代出版了《中国医学百科全书蒙医学》(分卷)，高等医药院校蒙医药(本科)统编教材(25门学科)。近年来，对蒙医药人员也授予相应的技术职称。总之，蒙医药学正在利用现代科学技术，在各方面有较大发展。</p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (697, 2, 56, '蒙藏医学体系比较研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙藏医学体系比较研究<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青海省藏医院 昂青才旦<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;大部分学习或从事藏医学专业的人士，一谈起蒙医学就认为藏医学与蒙医学基本上是一回事，甚至有人认为蒙医学就是藏医学，她的理论体系和临床实践完全和藏医学相同。其实不然，只要我们稍加研究，就不难发现两派医学的不同之处，笔者想从以下几个方面就藏医学与蒙医学之间的区别进行比较论述。<br>\r\n      历史渊源藏族和蒙古族都是有着悠久历史和辉煌成就的优秀民族，早在旧石器时代，藏蒙民族的祖先就生活在青藏高原和大漠南北，自那时起藏蒙民族的医学文化开始萌芽，并在各自不同环境、不同生活方式、不同社会背景之下产生了独具特色的医学体系，正如医学史界有个公认的观点，即人类的历史有多久，医药的历史就有多长。公元前5世纪，古代藏族象雄文化在冈底斯山周围兴起，著名藏医学家协吾赤西初撰藏医学著作《忒本夸翁》；公元6世纪，藏族社会开始进入奴隶制，629年松赞干布统一吐蕃王朝，规范文字，引进了佛教。佛教的传人促进了藏医学理论体系的形成，当然，它是以藏族先民在长期生活和生产的过程中积累起来的经验为主体的，这是藏医学理论体系的基础。公元8世纪，《月王药诊》、《四部医典》的问世，使藏医学理论体系更加趋于完善，在藏医学的发史上产生了举足轻重的影响。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;距今3000多年前，蒙古民族的祖先匈奴、东胡等游牧民族与秦相争；隋唐五代契丹、突厥、回纥等北方民族与唐征战，积累了丰富的外科医学知识；公元13世纪成吉思汗建立蒙古国，结束了北方民族聚散无常的局面，并在中央设立太医院、御药院、广惠司等医学机构，广泛吸收阿拉伯等其他民族的医学精华，产生了蒙古正脑术、正骨、瑟必素疗法、皮疗法、脏疗法、外伤急救疗法、蒙古灸等创伤医学为代表，以阴阳寒热理论为总纲的蒙医学理论体系；14世纪喇嘛教开始传人蒙古地区，古印度医学、古代传统藏医学的部分经典译成蒙古文，这些佛教经典和古代异族医籍的传人丰富了蒙医学的内容，对后来蒙医药学理论体系的形成起到了积极的作用，但从民族自身文化发展的角度讲，也影响了蒙医药学自我发展的进程。公元17世纪开始，蒙古医学大量吸收《医经八支》、《四部医典》的理论和经验，并与蒙古地区地理环境、气候条件、蒙古族人体质、生活习俗相结合，逐渐形成了近代蒙医学理论体系。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;另外，在这里还有必要简要说明蒙古文字的渊源问题。在笔者接触过的很多藏族学者或从事藏医学工作的人士都认为现行的蒙古文字是萨迦八思巴创制的，在这以前蒙古没有自己的文字。其实，蒙古最原始的文字是粟忒文字母，公元6到7世纪时，蒙古祖先和畏兀儿一起使用sogda字母；根据《元史》卷一二四《塔塔统阿传》记载，13世纪初，采用畏兀儿文字书写蒙古语，创造了畏兀儿字蒙古文，虽然1269年忽必烈曾命令萨迦八思巴根据藏文字母创制了蒙古新字“八思巴字”，但使用不广，到元朝灭亡，八思巴字不再使用。原来的粟忒蒙文逐渐演变成了现行的蒙古文字。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 理论体系<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医学理论体系受古印度医学及佛教文化的影响，以土、水、火、风“四大元素”及隆、赤巴、培根三因学说，七津、三秽理论为核心内容，并吸收汉族医学阴阳五行及其他民族医学理论，在此基础上根据藏族传统文化及生活方式创立了独具特色的藏医学理论体系。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医学认为，隆、赤巴、培根三因是构成人体的物质基础，也是进行生命活动所不可缺少的能量和基础。而“四大”元素又是构成隆、赤巴、培根三因的物质保证，风为隆，火为赤巴，土水为培根。在正常生理状态下，“四大”之间或“三因”之间的体内保持着协调和平衡的关系，隆、赤巴、培根虽各有自己的特性，各有自己不同的功能，但它们之间并非各自独立，互不相干，而是互相依存、互相制约的。当三者中的任何一个因素或几个因素由于某种原因而出现过盛或过衰的情况时，则变成了病理性的，人体就产生了疾病。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药学理论体系也是在受古印度医学及佛教文化、藏医药学的影响，主要以土、水、火、风、空“五元”及赫依、希拉、巴达干三根学说，七要素、三秽内容为依据，结合蒙医学核心思想阴阳学说及六基症理论，并吸收汉族医学阴阳五行及其他民族医学理论，在此基础上根据蒙古族传统文化及生活方式创立了独具特色的蒙医学理论体系。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学最具特点的理论体系是阴阳学说和六基症的辨证施治。蒙医学阴阳思想并非与我国古代哲学或中医学阴阳思想相同。据蒙古古代史料及文学艺术著作记载，古代蒙古人称苍穹为“天父”，称地球为“母地”。天、地在蒙古人心中是万物之本，是构成自然界一切事物的基础，这是蒙古族对阴阳概念的最初认识。蒙医药学阴阳理论正是在对自然界有了这种认识的基础上发展起来的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为，宇宙间的任何事物都可以概括为阴或阳两类，任何一种事物的内部又可分为阴阳两个方面。阴阳之间既是对立的，又是相互依存的，任何一方都不能脱离另一方而单独存在，阴阳之间相互对立、依存、消长、转化几个方面的关系，是阴阳学说的基本内容，这些内容不是孤立的，而是相互联系、互相影响、互为因果的。蒙医学的最初理论就是运用阴阳变化的相互关系来说明人体的组织结构、生理功能、病理变化、药物性能、诊断依据、治疗原则等的。<br>\r\n      六基症理论把引起疾病的主要原因归纳为“赫依、希拉、巴达干、血液、黄水、粘虫”六种，这与藏药学《四部医典》将六症归纳在寒热二症中有很大的区别，伊氏在《甘露点滴》中写道：将病症概括起来就是赫依、希拉、巴达干单独三个；合二为一的三个；合三为一的一个；共七种症。“三邪”单、合、聚七症是基本病症，而“三邪”以外的血症、希拉乌素症、虫菌症也是具有普遍性的典型病症，所以应把这些病症也纳入到基本病症里。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 临床实践<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医临床学主要以《四部医典》秘诀部十五至九十二章的疾病分类及诊治方法为主，结合历代藏医学的实践经验，在治疗高原多发病、心脑血管系统疾病、肝胆胃肠系统疾病、关节疾病等方面积累了非常丰富的临床经验，并在临床学方面产生了许多有影响的著作，如第司・桑杰嘉措的《秘诀补遗》、贡珠云旦嘉措的《藏医临床札记》、达日莫・曼然巴的《临床实践概论》等。蒙医学在临床方面将各科疾病分为六基症、十要症、五官科、五脏疾病、六腑疾病等，形成了以“六基症”为病因辨证，以“阴阳”理论为病性辨证，以“脏腑”理论为病位辩证的临床学体系。在治疗各种外伤性疾病方面有独到之处，如脑震荡、关节脱臼、骨伤疾病等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;诊断技术藏蒙医学无特殊区别，都采用望、切、问三种诊断方法，但各有侧重，藏医学以诊脉、验尿誉饮杏林；而蒙医则侧重问、望二法。<br>\r\n      治疗方面藏医以饮食、起居、药物、外治治疗为基本原则，运用药味较多的复方制剂及火灸疗法、药浴疗法、放血疗法等方法，擅于治疗各类“内科”疾病，特别注重珍宝药品的临床应用。蒙医则为药物、外治、饮食为基本治疗方法，多采用药味较少的复方制剂及正脑、正骨、瑟必素疗法、脏疗法等治疗方法，善于治疗外擅于治疗外科”疾病，特别注重操作技术在临床中的应用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 药理药材<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药学是藏蒙医学最具有差异的方面，藏蒙医学的理论虽受同一医学的影响，但药物却生长在各自不同的生态环境之中，无法用某一特定地方生长的药物作为唯一的标准，藏蒙民族世代都以游牧方式生活，逐水草起居，对生长在大自然中的树、木、花、草有着与众不同的认识和了解，因地制宜，就地取材是藏蒙药产生的基础，从而形成了各具特点的药学文化。<br>\r\n      藏药学以六味、八性、十七效、三化味为理论指导，多采用青藏高原特产的植物、矿物、动物药材，如船形乌头、唐古特乌头、金腰草、乌奴龙胆、胡黄连、喜马拉雅紫茉莉、獐牙菜等入药，经过精密的炮制加工，配制成汤、散、丸、膏、丹等剂型用于临床，其中“水银洗炼”是藏药学独一无二的制药工艺。各类珍宝药品的配制和广泛的临床应用也是藏药学的显著特点；1735年著名藏药学家帝玛尔・丹增彭措所撰藏药学名著《晶珠本草》的诞生奠定了藏药学的基础，成为现行藏药学的蓝本。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药学亦以六味、八性、十七效、三化味为理论指导，但多采用草原及沙漠中的植物及蒙古大森林里的植物、动物、矿物药材入药，如肉苁蓉、锁阳、蒙古黄芪、酸马奶、驼奶、沙棘等等，这些药材在生态特征、形态特征、药理特性等方面均与藏药材有很大的区别，内蒙古高原与青藏高原在地理环境、气候条件等方面有着显著的差别，内蒙古高原属大陆性气候，地貌以草原、戈壁为主，平均海拔1000米以上，而藏药大多生长在海拔 \r\n      3800米以上的高寒草甸及高山流石间，属典型的高原植物；传统蒙药制剂多为散剂和丸剂，较少选用矿物类药材，很少应用珍宝类药品，如藏药中的珍珠七十味、仁青璋觉、仁青芒觉等，而蒙医中的民间草药则占很大比重；19世纪，蒙药学家占布拉道儿吉所著《蒙药正典》将蒙药学进行了一次全面系统的总结，成为现行蒙药的经典著作。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (698, 2, 56, '蒙藏医学体系比较研究', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙藏医学体系比较研究<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青海省藏医院 昂青才旦<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;大部分学习或从事藏医学专业的人士，一谈起蒙医学就认为藏医学与蒙医学基本上是一回事，甚至有人认为蒙医学就是藏医学，她的理论体系和临床实践完全和藏医学相同。其实不然，只要我们稍加研究，就不难发现两派医学的不同之处，笔者想从以下几个方面就藏医学与蒙医学之间的区别进行比较论述。<br>\r\n      历史渊源藏族和蒙古族都是有着悠久历史和辉煌成就的优秀民族，早在旧石器时代，藏蒙民族的祖先就生活在青藏高原和大漠南北，自那时起藏蒙民族的医学文化开始萌芽，并在各自不同环境、不同生活方式、不同社会背景之下产生了独具特色的医学体系，正如医学史界有个公认的观点，即人类的历史有多久，医药的历史就有多长。公元前5世纪，古代藏族象雄文化在冈底斯山周围兴起，著名藏医学家协吾赤西初撰藏医学著作《忒本夸翁》；公元6世纪，藏族社会开始进入奴隶制，629年松赞干布统一吐蕃王朝，规范文字，引进了佛教。佛教的传人促进了藏医学理论体系的形成，当然，它是以藏族先民在长期生活和生产的过程中积累起来的经验为主体的，这是藏医学理论体系的基础。公元8世纪，《月王药诊》、《四部医典》的问世，使藏医学理论体系更加趋于完善，在藏医学的发史上产生了举足轻重的影响。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;距今3000多年前，蒙古民族的祖先匈奴、东胡等游牧民族与秦相争；隋唐五代契丹、突厥、回纥等北方民族与唐征战，积累了丰富的外科医学知识；公元13世纪成吉思汗建立蒙古国，结束了北方民族聚散无常的局面，并在中央设立太医院、御药院、广惠司等医学机构，广泛吸收阿拉伯等其他民族的医学精华，产生了蒙古正脑术、正骨、瑟必素疗法、皮疗法、脏疗法、外伤急救疗法、蒙古灸等创伤医学为代表，以阴阳寒热理论为总纲的蒙医学理论体系；14世纪喇嘛教开始传人蒙古地区，古印度医学、古代传统藏医学的部分经典译成蒙古文，这些佛教经典和古代异族医籍的传人丰富了蒙医学的内容，对后来蒙医药学理论体系的形成起到了积极的作用，但从民族自身文化发展的角度讲，也影响了蒙医药学自我发展的进程。公元17世纪开始，蒙古医学大量吸收《医经八支》、《四部医典》的理论和经验，并与蒙古地区地理环境、气候条件、蒙古族人体质、生活习俗相结合，逐渐形成了近代蒙医学理论体系。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;另外，在这里还有必要简要说明蒙古文字的渊源问题。在笔者接触过的很多藏族学者或从事藏医学工作的人士都认为现行的蒙古文字是萨迦八思巴创制的，在这以前蒙古没有自己的文字。其实，蒙古最原始的文字是粟忒文字母，公元6到7世纪时，蒙古祖先和畏兀儿一起使用sogda字母；根据《元史》卷一二四《塔塔统阿传》记载，13世纪初，采用畏兀儿文字书写蒙古语，创造了畏兀儿字蒙古文，虽然1269年忽必烈曾命令萨迦八思巴根据藏文字母创制了蒙古新字“八思巴字”，但使用不广，到元朝灭亡，八思巴字不再使用。原来的粟忒蒙文逐渐演变成了现行的蒙古文字。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 理论体系<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医学理论体系受古印度医学及佛教文化的影响，以土、水、火、风“四大元素”及隆、赤巴、培根三因学说，七津、三秽理论为核心内容，并吸收汉族医学阴阳五行及其他民族医学理论，在此基础上根据藏族传统文化及生活方式创立了独具特色的藏医学理论体系。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医学认为，隆、赤巴、培根三因是构成人体的物质基础，也是进行生命活动所不可缺少的能量和基础。而“四大”元素又是构成隆、赤巴、培根三因的物质保证，风为隆，火为赤巴，土水为培根。在正常生理状态下，“四大”之间或“三因”之间的体内保持着协调和平衡的关系，隆、赤巴、培根虽各有自己的特性，各有自己不同的功能，但它们之间并非各自独立，互不相干，而是互相依存、互相制约的。当三者中的任何一个因素或几个因素由于某种原因而出现过盛或过衰的情况时，则变成了病理性的，人体就产生了疾病。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药学理论体系也是在受古印度医学及佛教文化、藏医药学的影响，主要以土、水、火、风、空“五元”及赫依、希拉、巴达干三根学说，七要素、三秽内容为依据，结合蒙医学核心思想阴阳学说及六基症理论，并吸收汉族医学阴阳五行及其他民族医学理论，在此基础上根据蒙古族传统文化及生活方式创立了独具特色的蒙医学理论体系。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学最具特点的理论体系是阴阳学说和六基症的辨证施治。蒙医学阴阳思想并非与我国古代哲学或中医学阴阳思想相同。据蒙古古代史料及文学艺术著作记载，古代蒙古人称苍穹为“天父”，称地球为“母地”。天、地在蒙古人心中是万物之本，是构成自然界一切事物的基础，这是蒙古族对阴阳概念的最初认识。蒙医药学阴阳理论正是在对自然界有了这种认识的基础上发展起来的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为，宇宙间的任何事物都可以概括为阴或阳两类，任何一种事物的内部又可分为阴阳两个方面。阴阳之间既是对立的，又是相互依存的，任何一方都不能脱离另一方而单独存在，阴阳之间相互对立、依存、消长、转化几个方面的关系，是阴阳学说的基本内容，这些内容不是孤立的，而是相互联系、互相影响、互为因果的。蒙医学的最初理论就是运用阴阳变化的相互关系来说明人体的组织结构、生理功能、病理变化、药物性能、诊断依据、治疗原则等的。<br>\r\n      六基症理论把引起疾病的主要原因归纳为“赫依、希拉、巴达干、血液、黄水、粘虫”六种，这与藏药学《四部医典》将六症归纳在寒热二症中有很大的区别，伊氏在《甘露点滴》中写道：将病症概括起来就是赫依、希拉、巴达干单独三个；合二为一的三个；合三为一的一个；共七种症。“三邪”单、合、聚七症是基本病症，而“三邪”以外的血症、希拉乌素症、虫菌症也是具有普遍性的典型病症，所以应把这些病症也纳入到基本病症里。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 临床实践<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;藏医临床学主要以《四部医典》秘诀部十五至九十二章的疾病分类及诊治方法为主，结合历代藏医学的实践经验，在治疗高原多发病、心脑血管系统疾病、肝胆胃肠系统疾病、关节疾病等方面积累了非常丰富的临床经验，并在临床学方面产生了许多有影响的著作，如第司・桑杰嘉措的《秘诀补遗》、贡珠云旦嘉措的《藏医临床札记》、达日莫・曼然巴的《临床实践概论》等。蒙医学在临床方面将各科疾病分为六基症、十要症、五官科、五脏疾病、六腑疾病等，形成了以“六基症”为病因辨证，以“阴阳”理论为病性辨证，以“脏腑”理论为病位辩证的临床学体系。在治疗各种外伤性疾病方面有独到之处，如脑震荡、关节脱臼、骨伤疾病等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;诊断技术藏蒙医学无特殊区别，都采用望、切、问三种诊断方法，但各有侧重，藏医学以诊脉、验尿誉饮杏林；而蒙医则侧重问、望二法。<br>\r\n      治疗方面藏医以饮食、起居、药物、外治治疗为基本原则，运用药味较多的复方制剂及火灸疗法、药浴疗法、放血疗法等方法，擅于治疗各类“内科”疾病，特别注重珍宝药品的临床应用。蒙医则为药物、外治、饮食为基本治疗方法，多采用药味较少的复方制剂及正脑、正骨、瑟必素疗法、脏疗法等治疗方法，善于治疗外擅于治疗外科”疾病，特别注重操作技术在临床中的应用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 药理药材<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;药学是藏蒙医学最具有差异的方面，藏蒙医学的理论虽受同一医学的影响，但药物却生长在各自不同的生态环境之中，无法用某一特定地方生长的药物作为唯一的标准，藏蒙民族世代都以游牧方式生活，逐水草起居，对生长在大自然中的树、木、花、草有着与众不同的认识和了解，因地制宜，就地取材是藏蒙药产生的基础，从而形成了各具特点的药学文化。<br>\r\n      藏药学以六味、八性、十七效、三化味为理论指导，多采用青藏高原特产的植物、矿物、动物药材，如船形乌头、唐古特乌头、金腰草、乌奴龙胆、胡黄连、喜马拉雅紫茉莉、獐牙菜等入药，经过精密的炮制加工，配制成汤、散、丸、膏、丹等剂型用于临床，其中“水银洗炼”是藏药学独一无二的制药工艺。各类珍宝药品的配制和广泛的临床应用也是藏药学的显著特点；1735年著名藏药学家帝玛尔・丹增彭措所撰藏药学名著《晶珠本草》的诞生奠定了藏药学的基础，成为现行藏药学的蓝本。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药学亦以六味、八性、十七效、三化味为理论指导，但多采用草原及沙漠中的植物及蒙古大森林里的植物、动物、矿物药材入药，如肉苁蓉、锁阳、蒙古黄芪、酸马奶、驼奶、沙棘等等，这些药材在生态特征、形态特征、药理特性等方面均与藏药材有很大的区别，内蒙古高原与青藏高原在地理环境、气候条件等方面有着显著的差别，内蒙古高原属大陆性气候，地貌以草原、戈壁为主，平均海拔1000米以上，而藏药大多生长在海拔 \r\n      3800米以上的高寒草甸及高山流石间，属典型的高原植物；传统蒙药制剂多为散剂和丸剂，较少选用矿物类药材，很少应用珍宝类药品，如藏药中的珍珠七十味、仁青璋觉、仁青芒觉等，而蒙医中的民间草药则占很大比重；19世纪，蒙药学家占布拉道儿吉所著《蒙药正典》将蒙药学进行了一次全面系统的总结，成为现行蒙药的经典著作。<br>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (699, 2, 56, '蒙医学在新世纪继往开来', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医学在新世纪继往开来 \r\n     \r\n      <br>\r\n      \r\n\r\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;诸国本<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学是蒙古族人民在历史上创造的传统医学。它既是蒙古族人民长期与疾病作斗争的经验积累，又是蒙古文化与周边文化交融生发的医学结晶。世界上任何一种传统医学都有与生俱来的漫长历史，都经历过充满神话与巫术的童年时代，但真正的医学史是以医学体系的形成作为标志的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医学的形成，首先是蒙古族人民自身医疗，实践的总结。蒙古族人民长期生活在中国北方，游牧，狩猎，逐水草而居，常年与寒冷、潮湿、风雪作斗争。人们的生活与疾病和当地的地理环境、气候条件、食物种类、生活习俗息息相关，在“天人相应”中体现着人与自然的矛盾统一。同时，蒙古人民长期以来是一个马背民族，游牧，狩猎和战争交替构成交宕起伏的历史画卷，摔伤，战伤、骨折、脑震荡都成了常见病和多发病，客观的生存需要时时刻刻都在呼唤医学和创造医学。当成吉思汗的儿子窝阔台受了箭伤，他的同伴用嘴吸吮伤口的瘀血，用烧红的铁块烙治新鲜的创面的时候，他们动用了全部的经验和智慧，利用身边一切自以为有效的药物和工具，以达到救死扶伤的目的。\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;公元13世纪初，横跨欧亚的蒙古帝国出现在地平线上，在客观上提供了中西方文化大交流的舞台。印度医学、希腊医学、阿拉伯医学进一步传入东方。元朝在太医院下设广惠司，最早由一位叙利亚人掌修回回药物及其制剂。1292年，在今内蒙古锡林郭勒盟正兰旗的避暑行宫还设立了“回回药物院”。加上当时，辽、金、南宋地区中医学的发展，在医学领域出现了开放的、泛滥的、供人择善而从的热闹景象。16世纪以后即明代中、末期，随着藏传佛教传入蒙古地区，在全面接受和弘扬佛教文化的背景下，印度医学和藏医学深深地切入人们的日常生活，全面影响蒙医学的发展。伊希巴拉珠尔等医学家结合蒙医的实践，对藏医学大胆吸收，全面继承，积极创新，形成了相对独立的蒙医学学术体系。这一切都表明，传统医学的大树总是深深地根植于民族文化的本土之中。纵然它借用外来的枝干形成骨架，撑起那千千万万智慧的绿叶，但仍然吸吮着地下的养分，覆萌着自己的人民，并―脉相承，流传千秋。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;因此，从宏观的角度审视，蒙医学不仅是一种传统的医学科学，而且是蒙古族人民观察人体生命现象的特有的思路和方法，是那一个社会的文化沉淀的一部分。蒙医学的发展必将在继承学术精华的基础上，融入医疗实践和理论研究的新知，并用时代精神和现代科学促进发展，以适应现代社会医疗保健的需要。<br>\r\n      我们清醒地看到，当代传统医学的发展面临着许多新的情况和问题。一方面，时代在飞速前进，传统医学已经永远失去它原创时期的宁静生活和宽松环境，现代的卫生法规和医药管理已经不适合“神农尝百草”了；另一方面传统医学正面临现代医学的激烈竞争。在理论上说，这种竟争应该是中西医并重，取长补短；相互促进，但实际上某些“游戏规则”可能只利于现代医学而不利于传统医学。于是就需要政府当局采取某些调整的措施，于是就产生了对传统医学的这样那样的政策，于是就产生因政策的落实与否所引发的这样那样的呼吁。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;这个时候，蒙医学像其他传统医学一样，只有保持和发扬自己的特色与优势，循着自身的基本规律向上攀升，才能自立于世界医学之林。这期间并不排斥、相反地因积极利用先进的科学技术和现代化手段促进蒙医学的发展，但决不是迎合西医的认可，皈依西方医学的模式，走“西医化”的道路。18世纪，蒙医学家伊希巴拉朱尔把藏医《四部医典》的理论和蒙医的医疗实践相结合，在他所著的《甘露四部》一书中，重点阐述了“寒症”理论，把寒、热两章列在“十要症”的首位，从而弥补了前人在寒症方面的理论不足，使之更适合于蒙古地区的医疗实践。在另一部医疗著作《甘露之泉》中，他又在赫依病、希拉病、巴达干病之外，加上血病、黄水病和虫病，全称为“六基症”，从而把蒙医学的理论建设大大地推进了一步。这是前人理论创新的光辉范例。今天的蒙医学者也因该在本身的领地内纵横驰骋，而不是轻易地荒芜自己的田园，去耕耘别人的土地。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在上个世纪，中国的传统医学包括蒙医学在内遭遇过不公证的对待，有些人想方设法要把传统医学驱逐出现代文明。“西医在朝，中医在野”的状况，“改变从属地位，争取自主发展”的呼声，屡见与史，不绝于耳。今天，人们在回归自然的中途反思传统医学的价值，在重开学术民主的大门中再一次评估传统医学的作用，在总结“文化革命”的历史教训中检讨以“革命”和“科学”的名义对传统文化的扫荡，这一切都成了振兴传统医学的深刻教训和思想前提。<br>\r\n      当前，医学尚未至善，更非万能。医学仅仅是一列载者人类健康的目标奔驰前进的列车。当年成吉思汗在西征的途中，七十余岁高龄的长春真人丘处机万里赴约，两人在撒马尔干附近会面。成吉思汗问道：“真人远来，有何长生之药，以资朕乎？” \r\n      丘处机回答道：“有卫生之道，而无长生之药”。据说成吉思汗当场表彰了他的坦诚。我们今天发展蒙医药，其真谛也在这里。我们并不想从中寻找长生不老之药，而是从传统医学的底蕴中探求其卫生之道，使它更好地为新世纪的人民健康服务。 \r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-08');
INSERT INTO myy_text VALUES (700, 2, 56, '蒙药在内蒙古的现状与展望', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药在内蒙古的现状与展望<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古自治区科技厅 马强 \r\n<br>    \r\n<br>&nbsp;&nbsp;&nbsp;&nbsp;蒙古医学历史悠久，理论严谨。蒙药制剂疗效显著。内蒙古蒙药具有浓厚的民族特色。千百年来，蒙医药对蒙汉各民族的繁衍昌盛，做出了重大贡献。在祖国医学中独树一帜。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1 蒙医药在内蒙古的发展<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;内蒙古自治区地处中国北部，是一个高度多样化的地区，有森林、沙漠、山脉、平坦的高原和肥沃的平原，气候多样。中蒙药材非常丰富，可满足医药工业需要。<br>\r\n      蒙医药学的发展经历了漫长的历史过程。在2000年前的中医经典著作《黄帝内经》中就有蒙医药学的记载。至公元13世纪，随着蒙古帝国的兴起和强大，国内各民族之间的文化广泛交流以及欧亚各国之间相互交往的增多，蒙医学得到了迅速发展。17世纪以来，涌现出大批著名蒙医药学家，编写了大量的专著，使蒙医药学日臻完善，奠定了现代蒙医学理论体系。蒙医药学宝贵的实践经验和多种防病治病手段，不仅擅治多种慢性病，而且对某些急性病也有较高的疗效，特别是对各种肝病、心脑血管疾病、消化系统疾病、妇科病疗效更佳。<br>\r\n      建国50年来，蒙药产业也有长足的发展。在蒙药采集、鉴别、加工、制剂、栽培各方面，积累了丰富的经验，通过普查，对内蒙古野生药源有了全面了解，现已发现有2000余种植物类蒙药，并开展了变野生为家种的植物研究。目前，各种蒙药制剂达5000余种，其中144种载人国家标准，多数制剂制定了蒙医常用药的配方、制剂、炮制方法，为建立规范奠定了基础。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 蒙药资源概况<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;内蒙古自治区地域辽阔，总面积118万平方公里，地跨北纬37度30分～53度20分，东径96度58分～126度02分，东西长2000公里，分为湿润、半湿润、半干旱及干旱地区，地理复杂而多样，气候、土壤、日照时间差异大，是中国乃至欧亚地区生物多样性和独特性最显著的地区之一。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.1 植物药资源<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;植物类药是蒙药材中最重要的部分，总数在2000种以上，几乎全部维管束植物共131科660属。其中 \r\n      1300余种为中药、蒙药共用药材，有400余种为蒙药方剂、成药、验方常用药而中药较少用或少用，\r\n<br>近年来，内蒙古自治区各地区为了防止野生药材品种退化和人为滥挖，利用现代生物技术进行大面积种植，取得了生态效益和经济效益双赢的可喜成果。其中列为自治区科技示范种植基地的已取得明显成效。<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.2 动物药和脏器资源<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;内蒙古的药用动物有100种以上，其中蒙药应用的有66种。家畜和动物脏器是生物药物(biopharmaceutics)最主要的原料。内蒙古每年约有1000吨以上的各类动物脏器，过去，这些宝贵的资源尚未得到有效利用。现在，自治区的骨干企业、高校已和国内外著名企业、院校合作，开始进行了蛋白质工程产品的研发和生化药物的开发，如亿利集团与北京大学等8所高校合作开展蛋白组的研究，内蒙古医学院利用羊脾脏成功地提取了高活性抗癌活性肽。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2.3 矿物药<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;矿物药是蒙药常用药，内蒙古资源相当丰富。按照国家有关法规和新药审批办法，我们正在进行和支持高校有关研究单位进行实质性评价和质量认证的制定，为更好地开发利用资源奠定了基础。<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 内蒙古蒙药研究的发展战略<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;为了使中蒙药更好地服务于人类健康，必须依靠科技进步和创新，而蒙药现代化是实现蒙药走向世界的前提。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 坚持蒙药的优势与特色，加强传统蒙药和现代药学的结合与交流，建立蒙药现代系统工程。<br>\r\n      蒙药的独特理论体系构成了蒙药的基础。对蒙成药的开发，要用现代药学和其它学科的一切先进手段，把蒙药开发做为系统工程来抓。①建立规范的种植基地，保证高质量的、稳定的蒙药材来源。②建立蒙药材和成药科学的质量保证体系。③阐明单一蒙药和复方中有效部位的药理作用。④采用现代剂型和先进生产工艺。⑤促进中蒙药基础理论研究，阐明构效关系，为蒙药在国际上的传播与推广奠定基础。⑥建立现代营销网络和理念，为蒙药的国际化和产业化创造条件。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 在蒙医理论指导下，根据国家技术法规、利用现代医药学研究方法，创造性地挖掘蒙药，加快新药开发步伐，研制符合国际市场需求的蒙药新药。 \r\n      <br>\r\n      分析蒙药的现状，目前还没有好的有竞争优势的名牌品种。因此，按照世界上主要国家地区的医药规范标准要求，研究蒙药材、制备工艺、质量标准、药效学和安全性评价、临床疗效。在安全有效的前提下，今后若干年重点攻关主要治疗领域10个左右三类新药。对现有品种，重视二次开发。每个企业集团或研究单位，都要树立国际化大市场观念，增强以创新为主线的新蒙药的国际竞争力。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.3 蒙药生物多样性的保护与可持续利用研究<br>\r\n      内蒙古自治区是我国生物多样性最丰富的地区之一。但由于近年来生态环境的变化，生物多样性遭到了破坏。内蒙古自治区把生态建设列为一项长期战略任务，已取得了一定成绩。对蒙中药材，主要是：①挽救珍稀濒危动、植物物种，用生物技术快速繁殖。如中国科学院与我区阿盟合作，通过筛选肉苁蓉新品种，研究肉苁蓉愈伤组织体胚根苗诱导技术，分化出根苗，建立再生体系。内蒙古盘古集团引入澳大利亚技术，进行黄芪、甘草人工良种快速育苗。②合理利用药材资源，防止对蒙中药材过度采伐，现已实施大面积、大区域的封山育林、禁牧还草工程。<br>\r\n      在可持续利用方面，积极开展：①绿色药材栽培，用生物防治手段，建设无污染药材基地。②科学规划种植区域，根据药材最适宜的自然环境，培育地道药材，推广优良品种。③寻求代用品，一些蒙药方剂中有重金属，动物粪便，也有濒危动物药，如羚羊角、虎骨、麝香。应采取生物技术或组方优化，寻找代用品，使蒙药发挥安全、高效的特色。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.4 保健食品的研究与开发<br>\r\n      蒙药有“药食同源”的渊源。如沙棘、广枣、枸杞。自治区已开展重要蒙药的综合利用研究。内蒙古科迪高科技公司已在开发沙棘系列产品方面实现了产业化。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.5 中蒙药和脏器生物技术研究<br>\r\n      ①对植物蒙药，利用植物培养技术，将植物的分生组织进行离体培养，建立无性繁殖系并诱导分化为植株。此外，代谢产物的发酵生产，转化器管的扩增研究，基因工程方面的研究，都是采用工程的方法增加中蒙药材的数量和提高其品质。②对动物类或脏器生化制药，一是利用现有脏器生产药物，如羊胎素和脾细胞抗癌活性肽的研发，二是有关转基因动、植物反应器构建研究，这类可表达外源基因产品的生产系统也叫做“动物生物反应器”，其中从转基因动物乳腺中生产药物的技术又称为“动物乳房反应器”。对于畜牧业大区的内蒙古开发、生产蛋白质类药，前景十分广阔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.6 中蒙药信息系统的完善及人工智能研究<br>\r\n      ①蒙药方剂约有上万种。用信息技术的手段，将名医、名方、验方、借助人工智能总结出带有规律性的东西。如用模糊聚类分析方法，帮助分析蒙药方剂药味替代、组方优化、构效关系，使蒙药研究的方法和思路有质的飞跃。②建立有特色并实用的蒙药信息库，如药理活性分析、资源、疗效、方剂、理化研究数据库。③建立信息网。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4评价与展望<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在数千年延展的文化史上，蒙药经历了时代的考验，特别是近50年来西方医学引入我国以来，两个医学体系相互交融，相得益彰。它对整个医学的发生、发展提供了丰富的内容。我们可以证明，蒙医药学与现代医学通过相互交流已使医学宝库更加丰富多彩。<br>\r\n      内蒙古自治区党委和人民政府十分重视蒙药的发展，自治区科技厅已将蒙药开发与产业化列为发展高新技术的重要战略举措。现代蒙医药学已为人类健康做出重要贡献，做为历史悠久的蒙医药学，在现代科技推动下，日益为广大人民所接受。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙药不但有临床上的治疗作用，而且有滋补保健功效，这是古老文明文化的又一重要佐证。在过去50年间，这种作用已经得到药理学和临床应用的证明，许多蒙药今日仍在沿用，蒙药的滋补作用为我国人民所周知并在东南亚广泛使用，研究成果已使蒙药的作用和应用为人们所接受。因此，研究开发前景广阔。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;我们热切希望科研院所、中外专家学者，致力于中蒙医药学的发掘、整理和研究，使内蒙古的中蒙医事业发扬光大，使蒙药产品享誉国际市场，使内蒙古蒙药走向全国，走向世界，让民族瑰宝誉满全球。 \r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-08');
INSERT INTO myy_text VALUES (701, 2, 56, '我国蒙医药事业发展的主要成就', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我国蒙医药事业发展的主要成就<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国家中医药管理局 金二澄<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医药是蒙古族人民在长期的医疗实践中逐渐形成与发展起来的传统医学，具有独特的理论体系和诊断经验。<br>\r\n      1984年以来，在党和国家的民族政策和卫生工作方针的指引下，蒙医药事业的发展得到了有关地区各级领导的高度重视与大力支持，使蒙医药无论机构建设还是学术发展都有了进一步的发展，特别是内蒙古、青海、辽宁等蒙古族聚集较多的地区或州、县，蒙医药为保障各民族人民的身体健康起到了积极的作用。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;截至1999年底，全国以有蒙医医院39所(其中，内蒙古32所，新疆3所，辽宁、黑龙江、甘肃、青海各1所)，而实际上，有很多蒙古族聚集地区，还设有不少蒙医门诊部、诊所、专科医院等其他类型的蒙医医疗机构，在这些地区的多数综合医院设有蒙医科。因此，全国蒙医医疗机构、床位、人员的实际总数要比单纯蒙医医院的合计数大的多，他们共同为满足蒙医聚集地区各族人民对蒙医医疗保健服务的需求发挥了积极的作用。例如：在内蒙古自治区，旗以上蒙医医院、研究所共35所，蒙医、中医合署中蒙医院24所，共有病床2351张。全区实有蒙医药人员4777人，其中蒙医人员4019人，蒙药人员758余人。有高级职称者近200人，中级职称者近900人。蒙医药人员占全区卫生技术人员总数的3.88%。这些机构和人员承担了所在地区约15%――30%的诊疗任务。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;全区蒙医药教育事业得到了较快发展。1986年和1987年经自治区政府批准先后建立了呼伦贝尔盟蒙医学校和内蒙古蒙医学院，为全区及全国蒙医机构输送了一批合格蒙医药人才。蒙医在职培训及学徒培训也按计划、有步骤地进行，举办了各级各类学习班、培训班、研究班、约30期。1995年经自治区卫生厅批准同意将1所民办蒙医职业高中学校改建为蒙医职工中等专业技术学校，开设蒙医士、蒙药士、骨伤等专业作为向基层定向培养蒙医药人才的基地，以培养蒙医师30余人，蒙医(药)士1200余人。1988年成立了兴安盟残疾人蒙医职业学校，学校定向向农牧区蒙古族残疾人招生，共培养学生近300余人，全部在农村、牧区就业，成为残疾青年成才的苗圃。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;全区蒙医科研工作也取得一定成绩。建有盟(市)及蒙医研究所5所，自治区级中蒙医研究所 1所，1949年以后，蒙医科研工作者首先从蒙医药古籍的翻译、整理入手，从1956年到1986年搜集整理蒙、藏、汉、日、俄文献达600余部，还有挂图、图谱、针灸铜人、传统医疗器械等文献资料，编译出版了《四部医典》、《本草图鉴》、《蒙医药学概要》。1984年哲里木盟蒙医研究所被国家民委、卫生部和自治区列为国家民族医药重点建设单位。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;从1982年以来加强了具有蒙医特长的专科专药研究工作。蒙药“那如注射液”治疗风湿性关节炎、坐骨神经痛，“扎冲”治疗“萨病”，“通拉嘎601”治疗原发性血小板减少性紫癜及再障，《蒙药新药保利尔的研制》，“桑肝丸”、“萎胃舒”、《沙棘茎提取物的心血管药理与毒理研究》等87项有代表性的专科专药著作、临床科研课题分别通过了卫生部、国家中医药管理局、自治区科研成果鉴定。截止1995年，在蒙医药科研以获自治区科学技术进步各等奖38项。在青海，蒙医药人员总数200多人，其中具有高级专业技术职务者5人。全省现有1所蒙医医院(青海省海西蒙古族藏族自治州乌兰县蒙医院)和3所蒙藏医院(海西蒙古族藏族自治州蒙藏医院、海西蒙古族藏族自治州都兰县蒙藏医院、黄南藏族自治州河南蒙古族自治县蒙藏医院)。四所医院共有病床 \r\n      105张，职工170人。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;新疆维吾尔自治区巴音郭楞蒙古族自治州蒙医院(和静县)、博尔塔拉蒙古族自治州蒙医院(博乐县)、伊犁州塔城县蒙医院、巴州博湖县蒙医院、巴州轮台镇蒙医院，即共有5各蒙医机构，实际使用床位160张，民族医药人员186人，其中卫生技术人员135人，属于医师以上人员101人。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;在辽宁省，阜新蒙古族自治县是该省蒙医药事业建设发展的主要基地。该县于1958年 8月5日成立蒙医学校，并附设蒙医研究所。1978年，阜新蒙古族自治县蒙医研究所兴建了面积达 \r\n      10000平方米的科研大楼，并于1987年与蒙药厂合并，扩建成为及蒙医科研、医疗、药厂三位一体的机构，从而有力地促进了阜新地区蒙医药事业的健康发展。1989年，该所更名为辽宁省阜新蒙医药研究所，1994年5月，又经省教委批准，在原有蒙医中专班的基础上开设了蒙医大专班。该所现有职工205人，专业技术人员145人，其中有高级职称的9人，中级职称的22人，初级职称的104人。所内设有12个门诊科室、100张床位。近十多年来，该所先后开设了33项科研课题，其中1项已通过省级鉴定，2项通过市级鉴定，4项已纳入国家中医药管理局课题。该所医疗业务上充分依靠名老专家，积极培养后继人才，先后树立了“八大金刚”、“十大新秀”，他们对各种疑难杂症各有所长，患者遍及全国29个省、市、自治区和12个国家或地区。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;隶属于该所的阜新蒙药厂，始建于1970年，1983年投资90万元移地改建，1986年3月落成投产，占地面积10000平方米，建筑面积2600平方米，现有职工80人。注册生产蒙药品种有42种。有国内较先进的制药工业设备和检测设备及生产线。蒙药年产量50吨，产值300万元，是辽宁省蒙药生产专业重点厂家。该厂现有199个销售网点，产品远销东北三省、内蒙古、青海、甘肃、新疆、宁夏、等地。1986年该厂被市、县分别授予“卫生红旗单位”和“文明单位”称号。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;当前，在新时期卫生工作方针的指导下，以内蒙古自治区为代表的几个省区，又为今后一个时期内蒙医药事业的进一步发展提出了更高的目标与任务，并为之努力奋斗。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (702, 2, 56, '蒙医配方理论与方剂组成分析', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医配方理论与方剂组成分析<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院 那森桑<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;现代蒙药开发研究中，传统处方的方解较为重要。配方理论在新药研制过程中起指导性作用，对处方组成的分析，质量标准的制定，以及用药方法的选定，关系十分密切。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;l 配方理论<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.1 方剂组成：蒙医药方多为相对固定的成方。其组成大致与中医方剂近似，由君、臣、佐、使四个成员组成，而―且多数原方中这些组成剂齐全，数量恒定。各组成在方中的作用较明确，即：1)君药：针对主病或病所起主要治疗作用，是每一首方中必有的成员。例如驴血一3中的驴血，针对主病；扫日劳一4汤中的沙参，针对主病和病所兼有。 \r\n      &nbsp;&nbsp;&nbsp;&nbsp;(2)臣药：辅助君药加强治疗主病，或同时治疗兼证(合并症)。例如治疗感冒药方查干・汤中的苦参，加强君药发汗；乌珠目一 7中的天竹黄，治兼证止咳，3)佐药：协助君、臣药治伴随症和起预防作用。例如查干汤中的珍珠杆。清热的同时有镇咳作用；敖西根一l8里的沉香、肉豆蔻、苦参和蒜炭等，是预防肺热清散后的空虚热(热退去之末，起风吹余热而出现口渴、烦躁等症状的证候)。4)使药：方中起引导或调和作用。引导作用也称桥梁作用，能引方中诸药至病所；所谓调和系指降低君、臣药的毒、锐(烈)性，或调节药方之寒热性等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.2 方剂各组成味数的比例：蒙医药方各组成需由多少味药主要根据病情而定。一般大致规定为：治疗轻病，病情轻的药方，由君、臣药各1味，佐药2味，使药3味组成；治疗中病，病情中等的药方由君、臣药各l味。佐药3味，使药5味组成；治疗重病，病情重的药方由君、臣药各2味，佐药5味，使药9味组成。所以蒙药方中味数7、10、18者居多。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;1.3 方剂各味药剂量比例：蒙医药方味药剂量比例一般是恒定的，而且已经确定再不能随意变动。但剂量未顶或整理验方，需要确定其味药剂量时，要遵循下列原则之一。1) \r\n      一般的方，按君药5份，臣药4份，佐药3份，使药各1份的比例确定。2)来源于藏医经典的药方。则君药方，则君药剂量比其它组成略多或2倍，其它诸组成各l份。例如沏其日甘一5味中，沙棘6份，木香5份，葡萄干4份。甘草3份，栀子2份是按前一原则确定的；赞丹一3汤中，檀香5份，广枣、肉豆蔻各3份，是按后一原则制定的。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;2 传统药方方名来源探讨<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;有关方名由来文献记载甚少。经分析可归纳如下：①以方中的君药名命名。这是蒙医最常用的命方名方法。例如那鲁一4味(草乌一3)，如达一6(木香一6)，七味葡萄散等等。②以药方功能、主治命名。例如阿木尔一6味散(六味安消散)，嘎日迪一5味丸 \r\n      (五凤丸)，六味止痛散等均以功能命名而得；浩道敦一10(胃一10味)，通拉嘎一5，乌力吉一18等以主治命名而得。③以药剂颜色、气味特征命名。例如查干・汤(白色汤)。乌兰一10(红色一10)，伊赫・哈日-12(大黑一12)，芬芳一12等等。4)其他命名法：传统药方尚有以人名命者，例如萨丽冲；以方源命名者，例如雄・阿嘎日一8(“雄”为《四部医典》缩名)；以组方形式命名者，例如三份丸；以代名命名者，如桑漫一9等特殊命名方法，但为数不多。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3 传统药方构成的分类<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;因传统方的形成方法不同，将其构成可分为4类。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.1 原方：即符合前述配方理论及原则，未经过加(减)改变的药方。此类方的药味数多为集中在3、4、7、10或 18、25，其君、臣、佐、使各组成齐备而数量、比例规律恒定。如查干・汤，七味红花散，乌力吉一18味等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.2 加味方：在临床实践中，据治疗疾病不同变化的需要以1个(或有时几个)一定的原方为基础，再加 (减)一至若干味药而形成，并固定了的成方。例如石榴一5味由石榴一4味加良姜而成；那仁一满都拉一ll是由通拉嘎一5加五根药及冬葵果而成的成方。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.3 合方：根据病证规律，将某2个(或几个)原方合并成一个固定成方。例如土木香一10味汤由土木香一4味汤、天灵盖一3味汤和波棱瓜一3味三者合并而成；十五味云风散由十味白云香散与五凤丸合并而成。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;3.4 验方：系指个人经验方和民间经验方：一般没有明确的文献来源记载：其处方组成也不十分符合配方理论。但多数方中君药仍很明确：例如广枣一7<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;味，嘎木朱尔等。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4 确定方剂的君、臣、佐、使方法<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;早年的蒙医药经典著作，对方剂的组成及方解记载很少，现代教课书虽有些解释，但最多仅指出君药外对其他组成―概而论。因此，对一个具体药方来说，确定其各组成仍有困难，需要较高层次的专业知识与经验。笔者认为，采取下列方法有助于解决这一问题。 \r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.1 依据方名：如同前述，蒙医药方常以君药名为方名，故从方名可直接确定君药，再以配方理论知识加以分类其他组成及数量。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.2 依据方剂功能、主治：绝大多数药方的功能，直接来源于君药，或君、臣二药，然后根据君药特性和药方主治，可以逐个分出佐、使。多数情况下佐、使二者间并没有明显的界线可分。所以有时将二者合并称为配药。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.3 依据药方药剂量比例：据君药在方中比例一般最大，或君、臣、佐、使依次递减的规律，可不难确定部分药方的组成。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;4.4 依据药方的构成：上述3种方法除依据方名外一般仅符合原方的方解，对于加味方、合方及验方来说不那么实用。所以。此类方要遵循下列原则。加味 方：以其中的基础方的组成来分解；合方：以其中的主要方(从方名或主治可确定)的组成为主，加其他方相应组成。但君药一般只选l味。验方：君药一般明确。其他组成要看具体药方。<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;5 结 语<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;蒙医配方理论是蒙医配药组方必须遵循的原则，是蒙医药方在临床治病的基础。但也应该指出成方在临床实践过程中，跟随辨证论治规律，发生了或多或少的变化。这对药方的组成分析与功效解释，带来了一定的难度，也造成了蒙医方剂本身的许多不规范现象。这也是在现代研究中加以注意的一点。另外，蒙医历史上，也有过多个医家药家相互辨论，提出各自的配方学说，这里不再再述。<br>\r\n      <br>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (703, 2, 56, '蒙药研究开发刍议', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙药研究开发刍议 <br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古中蒙医研究所 顾铠 顾维彰 </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;蒙药现代化研究工作开展较晚。近二三十年来用科学方法和先进技术系统研究蒙药取得一定成绩，推动了蒙药现代化，也保存着蒙药一些特色。在研究中吸取了中药研究的经验教训，一开始就注意到蒙药的性味、宏观整体、相互制约和调节的理论基础，采取多学科协作，理论与实践相结合，走上蒙药现代化的系统工程，为蒙药开发研究打下一定基础，也有了一些经验和体会。单味药化学与复方药化学的研究密切结合蒙成药(下称蒙药)均为复方制剂，少则由二三味，多则由几十味药材组方。即把原料药材加工炮制，制成丸、散、汤、膏等供用。这与传统中药相似，药材的有效成份与无效成份同时存在於制剂中，随着社会进步，人类生活水平提高及时代的要求，需用科学方法提取有效成份类，以先进技术制成剂型新颖、疗效显著、服用方便、别具特色的新蒙药。提取蒙药的有效成份类，必须从研究单味药材的成份开始，唯有弄清单味药的有效成份，才可能知道复方的有效成份类。但是复方的成份并不等於单味药材成份的简单相加。因为多味药材配伍在一起，经过加工制造成蒙药的过程中，可能成份与成份间发生某些物理或化学变化，产生新成份。为此复方化学的研究，必须注意复方的特征性及特效性。结合单味药的成份来研究复方的有效成份类。判定某类成份有效与否，必须与药理实验相结合。只有这样才能弄清楚蒙药治病的物质基础，从而研制出新蒙药。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;单味蒙药中含多种成份，复方制剂的成份就更复杂，其有效成份的分离提取难度很大。而提取一类或几类成份，难度就小多了。主张提取有效分类，并非避难就易，而是不走纯化学的路子，使研制的新蒙药更符合蒙医理论。例如那如苏木朱尔原由3味药组成，以水丸供用，治疗风湿及类风湿痛显效。但由于草乌与诃子的药材质量不恒定，疗效也不稳定，毒性忽大忽小。经过拆方和机理研究，确认复方中草乌为主药，其生物碱类有显著镇痛消炎作用；荜茇为辅佐药，所含挥发油可延长草乌生物碱类的镇痛消炎作用，尚能轻微降低其毒性；原方用诃子解草乌毒，实际上是诃子中的鞣质与草乌中的生物碱类作用，产生难溶性的盐类而降低生理活性。我们提取草乌总生物碱，取其定量与荜茇挥发油一定量配伍，制成肌肉注射液，经动实验后试用放临床，经328例疗效观察，对坐骨神经痛的治愈率高达59.7％，总有效率为94.4％；对风湿痛的治愈率35.5％，总有效率95.4％。研制成的那如注射液高效低毒，曾获1986年国家卫生部重大科技成果乙级奖。证明通过分析传统蒙药的主从关系，提取组方中的有效成分类来研制新药是可行的。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;复方化学成份与药理实验的研究密切结合<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;复方制剂化学成份的研究比较复杂，而且没有成为一门独立学科，隶属於西药学的植物化学或天然药物化学，这易使研究者走上纯化学的岐路，失去了有效成份类研究的本意。我们采取蒙药化学成份与药理作用研究同步进行，而且重点放在复方成份类与药理作用关系的探讨及两者密切结合上收到成效。例如赞旦一3汤由广枣、白檀香、肉蔻三药等量组方，以汤剂(相当中药煮散)供用，治疗心热心悸等。通过拆方、有效成份类筛选、重新组方及药理作用研究确认广枣总黄酮对心血管有多方面的生理活性，白檀香及肉豆蔻挥发油具有显著芳香开窍作用，故提取广枣总黄酮与白檀香及肉豆蔻挥发油配伍，制成复方广枣片注射液用於临床，获得显著疗效，又制成广枣黄酮片内服，收到所用药材比原方少、临床疗效明显、服用方便、患者喜用等良好效果。再如三普勒・淖日布原方由29味药组成，通过拆方、简化方剂，经有效成分类分析与药理作用研究，最后用16味药组方，制成偏瘫康口服液，动物实验证明口服液具有改善血液流变性，抑制血小板聚集、改善微循环，增加脑血流量，抗血栓形成、促进血肿消散、修复脊髓损伤后的神经元、保护心肌缺血和增强机体免疫功能等功效，尚可清除氧自由基等多方面的药理作用后，试用於临床，经94例疗效观察，显效率86.9％，由此证明将蒙药有效成份类的分析与药理作用密切结合，探索药物作用机理，改革传统剂型，开发研制新蒙药，是行之有效的捷径。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;实验药理与临床药理的研究密切结合<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在动物身上做实验，药理作用显著的某些成份类，用到患者身上时却不明显甚至无效，这是常见的事。尤其是目前建立使用的动物模型都是西医的“病”，关于蒙医“证”的模型尚在摸索阶段。这给实验药理的研究带来一定困难。因为动物实验对某种“病”有药理作用的成份类，对“证”不一定有生理活性。研制的新蒙药对某“证”是否有效，必须靠临床药理与临床药效结果来证实。例如上述的复方广枣注射液及广枣黄酮片，经动物实验证明，两药对心血管系统有多方面的药理作用后，试用於临床。给药一疗程(14天)后，对用药者的血液流变性、血小板聚集功能与心电图的改善，以及对心绞痛自觉症状的主诉，做了一系列实验和调查，证明两药均有改善患者各项血液流变性指标，明显降低血小板聚集，调整心律，降低血压，改善缺血心电图，缓解心绞痛，对心律不齐有显著疗效。经血药浓度测试，复方广枣注射液给药30分钟后即达到最高血药浓度，可维持120分以上。广枣黄酮片起效时间约需1小时，但药效持久，每间隔4～5小时服药1次，即可达到保护心肌缺血和抗心律失常作用。通过68例临床疗效对比观察，初步确认广枣黄酮片保护心肌缺血作用，可与复方丹参片比美，而其抗心律失常作用却明显优於复方丹参片。即经临床药理与药效研究肯定了两药的有效性。对新药的临床应用也起到监督作用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药的剂型改革与新药开发研究密切结合<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;传统蒙药剂型改革，内蒙古中蒙医院中心制剂室做了许多工作，改革后的剂型新颖、科学，受到广大患者欢迎。关于新蒙药的研制，我们先后研制成治疗心、脑血管疾患、坐骨神经痛、风湿痛及医治呼吸道疾病的新蒙药8种。试用临床均收到明显疗效。我们体会，无论是传统蒙药剂型改革，还是研制新蒙药，都应想到开发。因为只有报请新药审批，拿到新药批准文号，才能将研究成果投入市场，在立项时就应注意到新药审批问题，要按国家卫生部颁布的新药审批办法《关于中药部分的修订和补充规定》设计蒙药剂型改革或新药研制的计划或施实方案。力求实验项目与内容与“补充规定”一致，以避免研究工作与新药开发脱节。通辽药厂开发出的两种新蒙药，就是这样做的，可供借鉴。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;另外要重视质量控制的研究，一定要对新剂型或新蒙药，用化学方法，薄层层析方法，高效液相或气相色谱法等的任何一二种方法，进行定性及定量研究，制订出确实可行的质量控制方法，这是保证新蒙药质量的需要，更是报请新药审批的需要。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，蒙药开发研究工作是多学科协作的医药系统工程。需要蒙药、药理药化、制剂以及临床等专家们的密切协作，才能使蒙药投入全国、走向世界。 ', 1, '2003-06-09');
INSERT INTO myy_text VALUES (704, 2, 56, '蒙医药学名词术语注释', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蒙医药学名词术语注释<br>\r\n      <br>\r\n      　　&nbsp;&nbsp;三根：“赫依”、“协日”、“巴达干”三者为寒，是存在于人体内进行生命活动的三种能量，同时也是产生一切疾病的根本内因。对于人体的生理功能和病理机制，都以此三者的生成变化为理论根据和说明工具。<br>\r\n      　　“赫依”：为蒙古语音译。译成汉语是风或气，其含义大致相当于中医的风和气，但是作用则比中医的风和气更为广泛。它的功能是主呼吸、血液循环、全身活动及机能反射、五官感觉、大小便排泄、分解食物、输送饮食精微与糟粕，是维持人体生理活动的动力。由于“赫依”所在部位和功能的不同，又可分为五种，即司命“赫依”、上行“赫依”、普行“赫依”、调火“赫依”、下清“赫依”。<br>\r\n      　　“协日”：为蒙古语音译。译成汉语是胆或火，大致相当于中医的“火”，但其作用比中医的胆或火更为广泛，它除了包括胆的本腑以外，广泛地被作为代表生理、病理学上热与火的概念之词。如在生理学中，“协日”可以理解为进行人体生命活动的热能，具有产生热量和补充体温等各种功能。根据“协日”之功能和所在部位，分为五种，即消化“协日”、变色“协日”、能成“协日”、明色“协日”。<br>\r\n      　　“巴达干”：为藏语的音译。译成汉语是涎或水和土。相当于中医的津、涎，但其作用则比中医的水和土更为广泛。“巴达干”性寒，喜温燥而恶寒湿，它的功能是在胃腑内腐化食物、运输体液、调节水分等。根据其所在部位和功能的不同，又分五种“巴达干”，即主靠“巴达干”、腐熟“巴达干”、司味“巴达干”、供养“巴达干”、连接“巴达干”。<br>\r\n      　　七元素：饮食物的精微、血液、肌肉、脂肪、骨骼、骨髓、精液（女子卵胞）七种物质为七元素。<br>\r\n      饮食精微：饮食物在消化道内消化分解为营养液和糟粕，其营养液为饮食精微。饮食精微通过输精微管到肝脏进一步清浊分泌。<br>\r\n      　　三秽：通过新陈代谢排出体外的排泄物，一般指大便、尿和汗三者。<br>\r\n      　　白脉：指大脑、小脑、延脑、脊髓以及各种外周神经。<br>\r\n      　　“黄水”：是机体的组成物质之一，其形成过程是血液糟粕归入胆腑成为胆汁，胆汁再清浊分泌，其清则“黄水”。“黄水”存在于全身各处，肌肉及关节尤多，略相当于中医的湿。<br>\r\n      　　“黄水”病：蒙医“黄水”病之原意不完全指一般皮肤湿疹、疥疮、疔等病的渗出物。“黄水”为病，皮肤可导致荨麻疹，在关节可引起关节炎，在内脏可引起内脏积水、内脏脓疡等。<br>\r\n      　　“粘”：为肉眼看不见的致病小虫，相当于现代医学之细菌、病毒之类病原微生物。<br>\r\n      　　六基症：亦称六种基本疾病。即“赫依”病、“协日”病、“巴达干”病、血病、“黄水”病和虫病六种疾病，是一切疾病的发生原因。<br>\r\n      　　三邪：三素发生病变，出现病理现象为三邪，即病态三素。<br>\r\n      　　身、语、意活动：指身体的体力活动、语言活动和思维活动。<br>\r\n      　　温和导泻剂：用温性药物制成药锭塞入肛门，使疾病下泻。<br>\r\n      　　罨敷：有热罨和冷罨，相当于理疗中的热疗和冷冻疗法。<br>\r\n      　　铁垢“巴达干”：在食道和胃未消化性“巴达干”粘液物储留聚积形成垢物引起食道、胃狭窄，吞咽困难的一种“巴达干”病。<br>\r\n      　　“宝日”病：宝日为蒙古语音译，“宝日”病系“巴达干”，血、“协日”和“赫依”四者混合发病，并与“黄水”相凝而成的综合症。<br>\r\n      　　刺痛：在身体的某部位针刺样剧烈疼痛，常伴有气急、气喘、大汗。<br>\r\n      　　聚合性：一般指“赫依”、“协日”、“巴达干”病并发性合并症为聚合性疾病。<br>\r\n      　　“三舍”：指男女内生殖器官的总称。<br>\r\n      　　正津：元气之精华，存在于心脏，随血循环布于全身，使身体健康，神色艳丽，精神焕发。<br>\r\n      　　生化：七元清浊分离的同时由一元素变为另一种元素的过程称为生化。例如血元之清化为肌肉。<br>\r\n      　　循经：是“赫依”、“协日”、“巴达干”等循环周身的特定经络。<br>\r\n      　　宿位：是“赫依”、“协日”、“巴达干”等主要依存的部位。<br>\r\n      　　“宝日・巴达干”：指以“巴达干”为主的“巴达干”病之一。症状为胃痛、饮食生冷或非适应饮食则疼痛加剧，有时还吐血或便血。类似消化性溃疡。<br>\r\n      　　“巴达干”罩：伏盖于热病之表的寒象。<br>\r\n      　　五境：五元学说中的色、声、嗅、味、触为五境。<br>\r\n      　　人体十三种欲望：饥、渴、呵欠、喷嚏、呼吸、睡眠、呕吐、痰、唾液、排气、大便、小便、精液。<br>\r\n      　　四施：饮食治疗、起居治疗、药物治疗、外治四种治疗方法简称四施。<br>\r\n      　　开欲：改善情绪，增加欲望和欲念。<br>\r\n      　　三缺：指三素缺乏。<br>\r\n      　　发症：类似现代医学之炭疽病。<br>\r\n      　　金伤：金属物所致伤。<br>\r\n      　　童尿：健康八周岁儿童尿。<br>\r\n      　　“粘”奇哈：一般指寒性x肿为“奇哈”病，如合并“粘”虫感染则为“粘奇哈”。<br>\r\n      　　“吾雅曼”病：为藏语音译词。热性“黄水”病为“吾雅曼”病。<br>\r\n      　　锁脉：有使脉管收缩之意，如止血、闭尿药物具有销脲之功能。<br>\r\n      　　固精：有控制遗精之意。<br>\r\n      　　男女标脉：男性和女性外生殖标点。</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (705, 2, 56, '传统蒙医学“绿色”特点辨析', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="623">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;传统蒙医学“绿色”特点辨析<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族大学蒙医药学院 \r\n        &nbsp;图门吉日嘎拉 &nbsp;熬姝芳 &nbsp;乌力吉巴特尔<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1“绿色医学”概念的内涵探讨<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;随着现代工业发展对生态环境造成的污染日益增加，人们崇尚自然、贴近自然的愿望日趋强烈，而以“无污染、安全、优质、营养”为基本内涵的绿色食品越来越迎合了现代人的需求。因此，“绿色”新概念就是“无公害”、“无污染’、“自然”或“安全”的代名词。我们有理由把绿色之概念引入传统医学范畴，将绿色的延伸意义定义为“科学性、优越性、安全性较高的，具有治疗意义的食品、药物，以及顺从自然法则的疗法”。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;中国社会科学院哲学研究所邱仁宗先生认为，医学这门学科有三个值得注意的特点：医学的目的是为了诊断、治疗、预防、控制疾病，恢复、维护和增强人们的健康；医学的对象是人，单个人或人群；医学的方法是综合的，它利用其他任何科学获得的成就，不论是数学的、自然科学的、还是社会科学的、人文科学的、工程技术的。美国华盛顿乔治城大学医学和医学人文科学教授e \r\n        d pellegrino等指出：“医学是一门治愈技艺”，“作为一门知识的医学是一门尊重生命体完善的科学，通过把身体的症状和医疗联系起来的技能而使这种完善具体化。” \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;笔者认为，蒙医学是一门传统的、无损伤的自然疗法，是综合运用自然科学、人文社会科学、数学的方法达到诊断、治疗、预防疾病、保健康复目的，尊重生命体完善的科学和治愈技艺。它尊重生命法则与生态规律，采用绿色食品、天然药物、心理行为、传统物理疗法(手法)等四大治法医治疾病。这种把“无创伤、无公害、天然(自然)的传统诊察方法应用于临床医疗活动，并以绿色食品、天然药物、传统的物理疗法与技艺(手法)以及心理调适技能等完善人的体质与心理状态防治疾病、恢复健康的医学暂且称为绿色医学”。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2 蒙医学理论体系的基本特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医学理论具有蒙古民族文化特点、思维特点和习俗特点，现代蒙医学理论体系主要包括整体观理论、辨证诊病理论和辨因治本理论等三个部分。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.1 以东方哲学――整体思维为基础的蒙医整体观理论的核心是“自然与人合一”、“社会与人合一”、“脏腑、器官一体”、“身、语、意(心、身)一体”为思维特征的，以系统论、控制论(黑箱方法)、信息论为思想方法的，揭示人体生、老、病、死等各种生命运动规律的理论。同时运用这一理论研究疾病的发生、发展和预防、治疗、康复。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.1.1 自然与人合一――自然现象包括天文、地理、气象、物候等的变化无一不在影响着人体；组成人体的三元即赫依、希拉、巴达干是由自然界土、水、火、气、空五源精华缩而形成的。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.1.2 社会与人合一――社会政治、经济、宗教、文化、民族习俗、个人生活习惯、体质、年龄等与人的健康密切相关。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.1.3 脏腑、器官一体――脏腑不仅是具有各自独立的生理解剖功能结构的实体概念，更重要的是一个生理和病理学方面的功能概念。脏腑之间存在着多种联系，脏与脏、脏与腑、腑与腑联系密切，脏腑与机体各种组织、各个器官之间也存在着多种单一的或复杂的机能联系，而由此组成人体七大基本物质生成、代谢的物质基础。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.1.4 身、语、意(心身)一体――人是一个统一的整体，精神和躯体(心和身)是同一生命过程的两个方面，二者共同作用于生命活动。人的思维、情感、欲望，直接影响着躯体，蒙医认为人的“贪、嗔、痴”等精神、心理活动的过度是身心病的“终极根源”。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.2 以内在发病原因、致病外缘因素，起居环境、起病时间等主观与客观两个方面的全部资料为信息以控制论的黑箱方法为手段，综合分析、归纳、判断、推理，得出诊断。“三诊法”和“十要诊”是辨证诊病时收集诊据的基本途径。辨证诊病是蒙医学在诊断中广泛应用的最重要的科学方法。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;2.3 蒙医学将复杂的病因归纳分类为基本因素、专门因素、特殊因素三大类。以发病基本因素赫依、希拉、巴达干及以致病专门因素血、希拉乌素(血之代谢浊液)为内源性疾病的根源，以致病特殊因素粘(致病微生物)为外源性疾病的根源导致的病症，统称为“六基证”或“六类自源症”。辨明六基证，并随证结合病者当时所受饮食、起居行为、气候、季节、环境、生活习惯、年龄、个性、体质、发病部位影响程度的强弱、量的多少，辨因治本。蒙医学在研究人体生理现象时，主要研究三元与七素之间的相互依存、相互补充、相互协调、相对平衡的内部运动规律。在研究人体病理机制时，是以观察三元与七素的内部运动的失衡状态为基础。一般而言，三元与七素的对立矛盾是绝对的，而二者协调平衡关系是相对的。从机体内部运动的整体而论，三元属阳，发挥着人体第一禀性的作用。从三元内部运动关系看，三者之间存在着相对稳定的动态平衡状态。三元虽属于阳之范畴，但由于三元各自属性决定了希拉具热性属阳，巴达干具寒性属阴，而赫依则因具有轻浮、易动之属性呈偏寒性，且与热并合加剧之，与寒并合亦加剧之。因而赫依(神经系统功能)控制一切生命运动之始末，调节机体功能，开窍行血，舒通经脉以及调控精微代谢，完成一系列重要生命功能。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;从人体生命运动整体讲，七素属阴，发挥着人体第二禀性的作用，且为三元运动的物质基础。七素的精微、血、骨属阳，肉、脂、髓、精属阴，七素间的生成、代谢、运化也保持了相对的阴阳平衡。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;人体内部的两种禀性的相对平衡作用是相互依存、相互补充、相互调节的双向调控的作用。这种双向调控作用保证了体内物质的代谢的正常运化，从而维持了各项生命运动的正常进行。如果这种相对平衡与双向调控状态受到破坏，人体则处于亚健康态甚或产生疾病。此三元七素学说是蒙医理论体系的核心。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3 蒙医学诊疗基本特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1 蒙医诊断基本特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.1 综合运用五官感觉，通过视觉、触觉、味觉、听觉、嗅觉的总动员，从病人身心活动的各个方面观察收集诊断信息，应用控制论的黑箱方法，从外测内，见证推病，以常衡变进行分析、归纳、推理、判断疾病是蒙医诊断疾病的主要方法特点。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;人体外表与脏腑的关系极为密切。蒙医诊断方法中充分应用了生物学的生物全息律，如面部、舌部和耳廓、脉象、体表穴位等的变化，可以反映出人体脏腑各部变化。“内病必外现”、“脏腑虽居内，其窍却开于外”、“整体之病，证形于局部；局部之病，牵涉整体”，“人体内之精华特别是精液之至精聚于心脏，由心脏输送于全身各部，以起补益正精，丰泽颜色之效。生命活动总的外在表现即为精神状态，通过观察病人的精神状态、意识活动、动作的协调与否、反应的灵敏程度来判断脏腑气血盛衰和疾病所在及预后”。再如脉象、尿相可反映出脏腑、器官等所患之病属性、发病部位和病势轻重，从而作为辨证诊病的依据。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.1.2 将病人视作“自然――社会――人”大系统中的子系统，加以观察诊断。蒙医学整体观理论认为：人体与自然界是个对立统一的整体，即大系统。然而，人体本身又是这一对立统一体的缩影(即子系统)。人有生存于自然界，改造自然，调节自身适应自然，依靠自然的能力。自然界季节与气候变化，食物、阳光、水、空气等的存在，都是人体生存所必需的基本物质条件。自然界的运动规律失调，会直接影响人的整个生命活动，引起生理的病理的变化，导致身心异常状态或疾病。大气污染引发的温室效应，使地球变暖，季节发生异常，冬天不冷、夏天炎热，直接导致人类疾病谱发生改变，癌症肆虐、流感爆发、心血管疾病发病率增加、辐射病的出现、热浪致人死亡的事实，证明了人与自然是对立统一整体。他(它)们之间存在着相互作用与反作用的内在联系。观察自然生态环境与人是否平衡、协调一致，是蒙医诊断的基本方法特点之一，从人的生物属性、自然属性、社会属性以及文化属性等多层面、多角度进行多维立体观察是蒙医诊断的最基本思维特点。<br>\r\n        3.2 蒙医临证治疗基本特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2.1 饮食疗法特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“绿色食品热的兴起是人们返朴归真、回归自然的必然选择”。绿色食品在蒙医悠久的医疗实践中早已应用。元代蒙古族营养学家忽思慧所著饮食疗法专著《饮膳正要》便可以说是绿色食品疗法的经典之作。蒙医饮食传统上分为饮品和食物两大类共计约200种。饮品包括水、奶类及奶制饮料类、茶类、酒类等四类；食物包括农作物类、肉类、油脂类、乳食类、蛋类、水果类、蔬菜类和调味品类等共九类，并全部属于绿色食品。<br>\r\n        蒙古人的饮食疗法是蒙医临床医疗四施(饮食、起居、药物、外治)之一。如赫依病是在饮食方面过量进食或长期食用浓茶、猪肉、花椒、荞面、山羊肉以及味苦、性轻而粗粝的食物，少食或长期缺乏营养等促使赫依偏盛所致，治疗上宜用三骨滋养汤、绵羊瘦肉浓缩汤、黄油、红糖、酒等。<br>\r\n        酸马奶疗法是蒙古族典型的绿色食品疗法，蒙古人以此法健身、治病，历史悠久。它是用马奶经过发酵制成的一种健身饮料，也是治病良药(已载人药典)，适用于肝中毒、心脏病、高血压、肺痨、神经衰弱等多种疾病的治疗。现代科学试验证明，酸马奶富含糖、蛋白类不饱和脂肪酸和脂肪酸及低分子脂肪酸、维生素类，还有氨基酸、乳酸、酶、矿物质(钙、铁、镁、磷、钾、碘、铜、锰、锌等多种元素)以及芳香物质、微量酒精等对人体有益的有效成分。<br>\r\n        蒙医食疗内容非常丰富，临床疗效也确切，理论体系完整，是蒙古人对绿色食品认识的智慧结晶。所以，习归自然、崇尚绿色食品，首选蒙医饮食疗法之中所列食品完全符合现代人的需求。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2.2 行为疗法特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;行为(起居)疗法，是蒙医临床医疗四施之一。《蒙医治疗法则》中指出：“医病时要根据病势之轻重，注意依行为、饮食、药物、外治疗法之顺序予以选择。换言谓之，临床要首选行为疗法，不成则施饮食疗法，再不成则施药物疗法，仍不成则施外治疗法”，并认为“以愉悦之心情，参加适当的体力运动有益于健康诸行为之首要”。实践证明，蒙医行为疗法是强心健体，增强抗病力，无毒副作用的自然疗法，并具有不可替代的、重要的医疗作用。譬如，赫依病的行为疗法中，要求病人避免空腹劳动、房事过度、睡眠缺失、寒风吹袭、强忍或努责二便，言语过多。在情志方面，避免思虑过度、悲伤、哭泣、愁苦、抑郁，过分恐惧等。故病人的居室环境应温暖而幽静，光线不宜过强，衣着须温暖，要有充分的休息和睡眠，要由知心亲友陪护，消除精神刺激，消除忧愁，保持平静的心境，参加适量的体育活动。这一疗法符合“生物――社会――心理”医学模式的要求。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2.3 药物疗法特点<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;药物疗法是蒙医临床医疗四施之重要内容。据文献统计，目前蒙药品种多达2200余种，较常用者1300余种。这些药材主要来源于自然界的植物、动物和矿物，而且以植物药为主，占60％以上，这些蒙药的绝大多数以天然野生物的生药部分入药，不经过药品工业深加工和化学合成，入药后基本保持了其自然成分。蒙医药物治疗是以绿色植物为主治疗疾病的医疗方法。仅举几例植药物如下：蒙医用广枣治心悸、心绞痛等心脏病；沙棘用于止咳祛痰、活血化瘀；蓝盆花清肺热、治肝热病；文冠木清热燥湿，治疗风湿、痹症。现代科学研究证实，这些植物药的疗效确切。众多蒙药是古代蒙古人常年在辽阔的草原上祖祖辈辈，逐水草而游牧狩猎和战时医疗求治实践中，与自然界的生态环境广泛接触，利用天然物质与疾病的斗争而逐步发现和积累的，它已形成完整的学科体系――蒙药学。蒙药是绿色药物，其疗效的可信性是漫长的医疗实践所赋予的。所以蒙药是现代人的绿色消费中应当被重新发现和认识的绿色新“大陆”。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;3.2.4 传统物理疗法特点 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;理疗为蒙医临床医疗四施之外治法，古来俗称“五疗”，实则包括放血、火灸、穿刺、罨敷、涂擦、浸浴六种疗法。现代医学中，对理疗定义为“研究和应用天然的、人工的物理因素作用于人体，并通过神经――体液的调节以达到治疗和预防疾病的方法。”传统的蒙医理疗主要应用自然界的天然存在的物理因素，如阳光、水、火、空气、矿泉、沙石土盐、动物脏器、各种蒙药药用植物等。①日光――日光浴、日光沙埋疗法等，主治巴达干病与寒病、风湿性关节炎、消化不良、手脚僵直(拘挛)、水肿、抑郁症等。②空气――空气浴，各类型心、肺、肝脏病、克山病、肺结核，各类型癫狂症等要辅以空气浴治疗。③水――蒙医称水疗法为浴疗，包括天然温泉疗法、五味甘露汤浴、sebusen疗法(动物瘤胃热罨法)、骨汤浴、驴骨汤浴、缚浴，适应伤热、毒热、结核、脉病、技体或脊椎僵直或挛急、肌肉萎缩、风湿、类风湿等病的治疗。④火――灸疗，包含蒙古灸、白山蓟灸、山川柳灸、火把灸、温针灸以及火针疗法，适应消化不良、水肿、关节病、癫狂、健忘症等病的治疗。除此，还有按摩及手技之法，手法技巧复位器官移位，肢体挫伤或断裂(诸如脑震荡、脏腑震动移位、骨折、关节脱位、胎儿胎位<br>\r\n        不正或移位等)；手法技巧正骨法，即对筋伤骨折、脱臼应用生物力学原理整复、医治的自然方法。 <br>\r\n        蒙医传统的物理疗法种类很多，它们具有收效迅速、无损伤、无副作用、少痛苦、疗效持久、经济方便等优点，易被大众所采纳。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;4 蒙医学的生态医学本质<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;“传统医学的本质是生态医学”。纵观蒙医学发展史及其经典，它博大精深的内容涵盖了天文、地理、气象、生物、生态、数学、哲学、心理学等众多学科，但归结而言，研究的是“人与自然”的关系对人的生、老、病，死的影响。事实上，蒙医学的本质是生态医学。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;生态学是研究有机体与其周围环境(包括非生物环境和生物环境)相互关系的一门学科。而生态医学则是研究生物内外环境与人类卫生保健、医疗实践间相互关系的“广义科学”。蒙医学研究生物内环境(生物圈)里的数千种野生植物的植物学特性，研究水、陆、空中生存的百余种飞禽走兽、海洋动物与微生物、农养动物、爬虫、昆虫的生物学特性，及其与医学的联系；发现了各类繁多的野生植物的药物功能，发现了众多动物的血、肉、骨、内脏、角、结石、粪便等成分的药用价值，也发现了部分爬虫、昆虫可以入药。蒙医学研究生物外环境(生态环境)，发现了人与自然的自然联系，创立了“天人合一”的著名整体观医学理论，并以此理论为“奠基石”，实践了伟大的医疗活动。蒙医学还研究生物内外环境与人体健康及疾病的相互联系。古代蒙医发现并总结了什么病在什么季节、时辰聚集、发病、平息，什么时候吃什么可以治什么病，或可以诱发什么病，什么气象、什么气候、什么季节、什么时间可以诱发或平息什么病。在上千年的医疗实践，蒙医家们成功地运用了现代科学所称的“人体生物钟”。譬如，以昼夜论，人体赫依聚集时间为每日11时至14时及23时至2时；赫依运动时间为3时至6时至22时。以季节论，人体赫依平息时间为7时至10时及19时至22时。以季节论，人体赫依在4月16日至6月15日平息。在赫依的运动时间里过分生气、悲愤、饥饿、疲劳、饮茶、言语及房事过度，进食辛、苦味、性轻或无营养食物及药物，极易使赫依偏盛，从而引致赫依病(神经、精神系统疾患)。“21世纪将是生态医学、分子生物学、生命科学的主宰年代”。蒙医学要实现现代化，要认清21世纪医学――生物――社会――心理医学模式以及宇宙观这一综合发展趋势，发挥自身生态医学的优势，成为21世纪医学领域的前沿学科。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;综上所述，蒙医学的理论特点，诊疗特点及其生态医学本质无一不在证明，蒙医学是一门历史悠久的绿色医学，它必将在21世纪人类保健事业中再创辉煌。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (706, 2, 56, '从医学史角度看蒙藏医学', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="623">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从医学史角度看蒙藏医学<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;    中国中医研究院民族医史研究室 甄艳 蔡景峰<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;同属于中国传统医学之重要组成部分的蒙医学和藏医学，是蒙古族和藏族人民在总结各自的医疗实践经验的基础上逐渐形成和发展起来的。由于两者的自然环境、风俗习惯和原始宗教信仰的差异，使它们有有各自的独特之处；而两者之间又由于文化上的交流和人文条件的某些相似，它们又有着十分密切的关系。15世纪以前，蒙古族先民信奉萨满教，其医疗实践的经验十分丰富，具有象蒙古灸、酸马奶疗法、瑟必素疗法、正骨术、正脑术、安代舞等极具民族特色的内容；而藏族先民在信奉苯教时期也有一些有民族特点的疗法，如对抗“毒药”的配制和治疗作用、热酥油、青稞渣等疗法。两个系统都处在经验的阶段。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;藏医在8世纪已经有较系统的理论。13世纪，藏传佛教萨迦派首领八思巴被元朝忽必烈奉为“国师”、“帝师”。八思巴到元大都弘扬佛法。15世纪，宗喀巴改革减传佛教，创建了格鲁派，即黄教。明万历期间，格鲁派首领根敦嘉措的转世索南嘉措与蒙古土默特部首领俺答汗会面，索南嘉措被尊为“圣识一切瓦齐尔达喇达赖喇嘛”，达赖喇嘛活佛的转世系统也自此形成。格鲁派从此传人蒙古地区，为藏医学大规模传人蒙古地区提供了基础。《四部医典》也在这一阶段随黄教的传播而输入，并大约在17世纪上半叶被译成蒙文。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;可以看出，蒙医和藏医有着各自独立的起源，但同时藏医的传人对蒙医学的发展产生了较大的影响，所以在两者的医学理论体系中有较多的共同点，同时，又存在一定的差异，可谓各具特色。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医吸收了藏医学一些基本理论内容，提出了人体的三根学说，即赫依、希拉、巴达干，认为它们是构成人体的物质基础，而这三种物质又依其位置和功能各分为五种，即赫依分为司命、上行、普行、调火和下清五种；希拉分为消化、变色、能成、能视、明色五种；巴达干分为主靠、腐熟、司味、供养、连接五种。而在藏医理论中，隆、赤巴、培根也是人体进行生命活动的物质能量基础，根据其存在部位和机能的不同，可将隆分为维命隆、上行隆、遍行隆、伴心隆和下泄隆五种；将赤巴分为能消赤巴、变色赤巴、能作赤巴、能视赤巴和明色赤巴五种；将培根分为能依培根、能化培根、能味培根、能足培根和靠合培根五种。不难看出，蒙医与藏医的这种论述是同出一源的。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医认为人体有七津和三秽，也就是精华、血、肉、脂、骨、髓、精七种物质，以及粪、尿、汗三种排泄物。其他如脉道、五脏六腑、病因、病理等基础理论知识，也都与藏医很相似。在诊断方法问、理、切中，又以切脉与尿诊最为重要。在蒙医学的脉学理论中，有许多内容与藏医也是极为相似的，如对诊脉的时机，部位和方法的论述等。同时蒙医也将脉分为阴脉，即雌脉；阳脉，即雄脉和中性脉三种正常脉象，两个系统基本一致。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;在蒙药学的理论方面，蒙医也有六味、五元和十七能效的概念。六味指的是甘、酸、咸、苦、辛、涩六种味道；五元即是土、水、火、风、空五种元素；十七能就是指轻、重、固、动、温、凉、积、燥、腻、淡、寒、热、钝、锐、柔、稀、涩。这种理论上的叙述与藏医药的理论是十分相近的。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;尽管蒙医在理论上多来自《四部医典》等古代医典，但蒙古族人民也并非一成不变地套用，而是结合本民族的自然和人文条件而有所侧重和发挥，其中比较明显的主要包括：<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;阴阳学说：蒙医较藏医更多地吸收了汉地中医的阴阳学说，并加以应用。到了18世纪，这一学说已经被广泛地应用于基础理论、临床等方面，如把边脉、三毒归入阳，白脉、上津归入阴；而且在人体胚胎的形成及其发展方面也应用了阴阳学说。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;寒热学说：寒热学说在《四部医典》中已有涉及，但在临证实践中，藏医着重于热的方面。在《四部医典》中，有许多专篇论述热症，而唯独缺少对寒症的深入论述。蒙医对此进行了补充和发挥，如伊希巴拉珠尔在《甘露四部》中，著有专篇以补叙“寒症”，并有“寒热相搏”、“寒热转化”等进一步的发挥和治疗，同时还补充了寒病死证，充分结合蒙古族地区的具体特点加以发展。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;六基症：18世纪中由伊希巴拉珠尔提出，包括赫依、希拉、巴达干、血、黄水、虫等六种最基本的病证。并结合寒热理论将它们进行了分类，即希拉、血病为热；赫依、巴达干病为寒；而黄水、(粘)虫病则可寒可热。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙药学：如前所述，尽管蒙药的用药理论与藏医药有许多相似之处，但由于蒙古族居住地区多为草原，与藏族所处的雪山高原的条件迥然不同，因而所用的药物品种也有较大的差异，在对药物的具体应用上两者也存在许多不同点，至于蒙医的正骨、正脑、治伤、酸马奶疗法、矿泉疗法等各种民族特点突出的疗法，则是蒙古族人民的独特创造。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;由于藏医学的明显影响，使蒙医出现了学术争鸣，并形成不同的学术流派，主要的有古传流医学派、藏医学派和综合传流蒙医与藏医于一体的近代蒙医学派。后来，近代蒙医学派逐渐发展成为具有完整理论体系的流派，并成为近现代蒙医学的主流。这一学派的著作，基本上都是用藏文写成的，其代表医家基本上都谙熟藏文，其中如伊希巴拉珠尔，对传统蒙古医药学、藏医和古印度医学等均有很深的造诣，著有《甘露之泉》、《百露医法从新》、《甘露点滴》、《甘露汇集》和《认药白晶药鉴》等著作，均为藏文写成。他将外来的医学知识根据蒙古族的实际情况与传统蒙古医药学习相结合，进行了许多创造性的研究；又如罗布桑苏勒和木也精通蒙、藏、梵文。他不但是一位蒙古药物学家，也是著名的翻译家，并著有多部著作，如《认药学》、《油剂制法》、《脉诊概要》等；官布查布更是清政府中专司翻译的蒙古著名学者，官至“大清内阁掌译番蒙诸文西番学总管仪宾”，对蒙、藏、梵文均有极高的造诣，著有《藏、蒙、汉合壁药方》等著作，充分体现了蒙古族学者对蒙藏医学的贡献。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;有趣的是，同属于蒙古族的俄国布里亚特人，他们使用的语言也是阿尔泰语系的蒙古语族，其人民在宗教信仰、生活习惯等方面，与蒙古族无大差异。布里亚特共和国境内喇嘛庙林立，曼巴札仓很多，这里也是蒙医学流行的地区，由寺庙培养的医生很多，其国家历史博物馆保存的一套近乎完整的“曼汤”，是除西藏以外保存最完整的一套。俄国的布里亚特人用的传统医学，正是蒙藏医学的内容，布里亚特已经成为向世界人民传播蒙藏医学的重要窗口，蒙文版《四部医典》也成为最早的除藏文以外其他文字的全译本，它是外文版即俄文版《四部医典》的母本，由布里亚特医生巴德玛耶夫俄译，他还将蒙藏医学传播到俄国圣彼得堡，引起国外的关注，成为传播蒙藏医学的媒介和滥觞。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;总之，蒙医学是在蒙古族人民总结本民族的医疗实践的基础上，借鉴了藏医学等多种医学理论，经过历代医家的努力和发展而逐渐形成的一门医学体系，充分地显示出蒙古族人民的聪明才智。<br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (707, 2, 56, '再论世界传统医学的结合发展历程及必然规律、关键问题', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="623">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;         &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再论世界传统医学的结合发展历程及必然规律、关键问题<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;     &nbsp;&nbsp;&nbsp;&nbsp;鄂尔多斯市鄂托克前旗蒙医院 金・巴图<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;一、人类医药发展的第1、2阶段，由于没有完全形成独局异格的理论化医学体系，故规模和形式等相近无别，在第3阶段――即理论化医学兴起之后出现传统医学模式。因为中、印、希腊地带的文化技术，社会生产力发展较快而早，所以时先在古老哲学、宗教、基础文化知识下创造世界三大传统医学。但就在那时，蒙古祖先也不断创造和充实自己的医术及其分散支离的理论并与其它医术相沟通。是当代民族医学的前驱；世界传统医学大系的前身。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;二、自现代科学和文化知识、哲学思想的基础上形成西方医学以来，人类医药科学自然地分成传统和现代两大系。这两大系的相互影响，相互渗透，共同为人类服务的过程中，又自然地出现了近代汇通和当代结合发展局面及其服务队伍。在此发展规程中，有的落后而淘汰了，有的壮大而普及了，有些民族和民间医术与进步的科学等结合成强大生命力及服务市场的先进成果和医学了。为何呢？<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;三、在医药科学发展规程中，必然规律和先进性、科学性、实用性、结合性、普及性等无情地确定其命运。据纵横研究，吾认为必然规律是无情的；先进的必然占领主局；大合共振乃是正统大道。与结合发展规律一致化则是共振永旺的最基本前提；开发潜在能力，寻找突破口，解决供求不应，创造新而独特东西并把它返规利用是最终目的。其中，最关键的是顺其自然，大合共振。世上一切都有其必然规律，医学发展也不例外，蒙古称其为“要顺”，印度称其为“梵”(瑜珈)，汉方称“道”它们都函有一致、齐全、结合、融合、合一、合气等义。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;人类医学是广泛实践和结合为基而发生，发展过来的一门最接近与哲学，社会科学，边统学科的自然科学。人类原古医药发展的第一阶段是自人类生存于该世为始。以劳动生活中与疾苦斗争，寻求安宁舒适等多因素为起源。特点为无巫有医。自原始人群中分出思维活跃，掌握一定火水气关系及原式文化和知识，势力较大的一群起，就盛开统治阶层和巫师。shaman类不单控制原始部落及其医政，而且利用原哲古医之故，人类医药进入医巫相混为特点的第二阶段。当时的“医”是shaman类医者的综称而已。《山海经》所提的121中药物的部分以被神化；口传经中：从烹调中引出药后配制“伊尹制沟液”有所神奇，但巫上山采神药是当时医生的活动实记。技术遗迹。从医巫混合体中逐步分化出“医”，人类医药发展进入第三阶段。特点为医和巫的分道立家发展。龙山酿酒技术(皇帝氏、约4千年前)等等与古医结合发展，给医学带来兴旺之机。由于分道立家开端期、医者主源为巫徒，所以呈现“医源于巫”的表现。古希腊“希波克拉底”，古印度“医经八支”，中国“周礼”，和“皇帝内经”把人类医药的精练部分与当时当地技术结合起来了。在皇帝与岐伯、医圣(导师)与大仙等以问答形式完成标志性的经典时，吸取和利用了相关大小民族和部落的医术及医者。所以，蒙古祖先所创的部分绝技也与北方民族医术形式溶《内经》、《医经八支》、《药王月帝》等中。此继续汇通和结合发展中出现《四部医典》、《四部甘露》等经典。至今，没有出现一个纯种的医学经典。也没有出现不受当时哲学、信仰、文化、技术的医学。蒙古族传统医术曾受到印度古哲、文化知识、宗教统治和ayorveda系医学的大冲击。但是近代进步派典范者掌握结合发展规模的同时，抓住震整术等关键，寻找突破口，把蒙医向独特和进步方向推了一大步。伊希丹赞旺扎和贡布扎布等近代改革思想者把俄、回、汉等国家和民族医术中吸取点点精华。后来把《个体全录》译蒙发行，并想望汇通和发展。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;外来医术和西洋医学的传华早在张骞、班超出使醒西域，带回香料药为始。《唐书》曰“大秦(罗马)医眼及痢，能开脑出囊”。苏敬(659年)在《新修本草》中记载“府野边” \r\n        (theraica,盖仓丸，万病丸)。至尽汉医眼科所有的“金针技内障术”最先是由印度传来得的技术。唐诗家刘禹锡《赠眼医婆罗门僧》诗云：“三秋伤望远，终日泣途穷；两目先暗，中年已老翁。看朱渐成碧，羞日不尽凤；师有金蓖术，如何为发朦。”文城，金城公主带医书数十种进藏(641年、710年)和汉医东松噶瓦两次进藏传医(8世纪)乘和尚修编翻译《医王月帝》，元丹据汉医编《肪学师承记》对组织编写《四部医典》打下基础。元朝大统一和大交流，不但给各民族医学的互相影响和渗透开了绿灯，而且给阿拉伯医学传入方面也起积极作用。回回医院、太医院在大都(北京)、上都(多伦)被设立。此后出现了《饮膳正要》等含有多民族医食精华的蒙古医著。尤其蒙古骨伤科特技丰富了中华的整个骨伤科。元代危亦林1343年著的《世医得效方》中记载着金族对骨伤处理有绝技。aycvreda \r\n        &nbsp;&nbsp;&nbsp;&nbsp;系医学理论和古印哲学在宗教文化或以宗教强制的基础上相应地影响了蒙古医术，并且经过三大流派的争议，逐渐向主流――结合方向发展。总归言此，西洋医学传入之前，在传统医学和医术之间就有汇通和结合发展历程。古希腊医学的原哲古医也相应地影响了汉医、蒙医等。但是都属于传统医学，所以没有结下优良果子。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;西洋医学在明末清初期，第一次传入时，开明的传统医学家就选善而用了。著名哲学家汉医师方以智(1611―1671)道德提出“会通”(汇通)思想。西医的脑主记忆说在汪昂《本草备要》中出现。邓王函编写《人体说概》(1700年左右)就逆此必然规律，力图将阴阳五行学说和中医理论与古西方四体液说加以汇通。但是由于近亲配偶之故，不但没有成功结下改良而进化之果子，反而导致先天缺弱，后天退化之局。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;第二次西洋医学传入时，西医找到永久落足之地(缺口)。1805年皮尔逊在华推行牛痘接种法。但原有人痘接种法的地方(“蒙医接痘法与防治天花史――兼谈接种法之源”一文中以详述)不为奇此。故应用免费服务来打开市场。由于原来接痘法费用过高而不安全的缺点。所以人民最终依靠了先进。从而把原接痘发推进了淘汰之坑。1829年陈定泰为母治病，专访于羊城，引起荒荡的解剖找经络之事，诱发探索风。约1846年，伯驾等醚麻方法，小刀手术，眼科诊疗传华后，中国医药界受到理论、技术、市场的第一次冲击。从而伯氏也被称为“当大炮举不起中国的一根横木时，以手术小刀打开中国大门的第一个侵略者”了。从本质上讲，这绝非侵略性为之果，而是必然的必然所确定的命运。小刀手术由此在中国被称“神刀”。西洋医学由此在中国找到新田地。接着《全体新论》、《西洋图论》影响话医界。以后唐容川著《中西汇通医书》，书中并提到“西医也有所长从而兴起中西医汇通热流。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1916年余云山由日本留学回国后，鼓吹“医学革命”，1917年著《医学革命论》并提出“废医存药”逆论。1929年3月17日“全国医药团体联合会”成立并派代表赴南京请愿，抵抗国民党第一中央卫生委员会议所定的“废止旧医以扫除医事卫生障碍案”1919――1937年间，张锡纯著《医学衰中参西录》共30卷并打开了汇通大局，打下了结合的基础。1950年第一界全国卫生工作会议决定“团结中西医”，1981年中国中西医结合研究会成立，从而结合发展进入健康、正规的新阶段。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;四、 掌握突破口和关键的同时，发扬优越、增加独特是健康发展，保证永振的最起码前提条件。硬着头皮向刀刃针尖上创，没有好结果可得，世界传统医学的三大体系及其属下的大大小小医学(医术)，都以原哲及气(龙)学说来立其理论和武器的。原哲带给医学的遗产和武器则为“天人合一”观“阴阳”辨证、贪痴嗔与火水气、五星机械唯物论.....这些在理论萌芽期，推动了医学发展，但原哲作为社会和自然科学的总指导思想及认识论只能占其相应的位置，只能通过医学本身的气 \r\n        学说等来被用于医学。在传统医学的结合发展过程中，哲学和方法论的升级，现代化则成为一个关键。 <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;五、现行的传统医学与现代医学的模式在哲学和方法论上有一个关键的交叉点。前者所用的是原哲、笼统整体观，朴素唯物论，自发性辩证法、分散型“假说”，后者所用的是现代哲学、系统控制论、过细整体观，唯物辩证法，精密型“学说”。虽然传统医学孕有一定水平上 \r\n        的方法论原则和哲学 观念，但远远没达到现代化科学的各学科统用的方法论及其原则水平。实验研究，数据统计、最佳处理，有序排列原则上有缺口。只有相互联系，动态平衡。所以应用进步方法论的同时，把原哲和逻辑概念升级并统一化，现代化是传统医学及其结合发展焕然一新的关键。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;传统医学体系的构成层次中普遍存在的规律是第一层次理论的概括、核心、指导性和第四层次理论和实践的专科、专病、专方、专技、实用、客观性有密切联系。而第二、三向层次中的抽象、机械、思维推理性内涵颇多。故，把第二、三向第一概括归纳或向第四具体实和方面发展的同时，把第一、四理论及实践升位乃是突破的关链之一。这也是宏微结合，双向发展，重点突破的基本前提之一。天人合一，阴阳（火、水）气，精气神，正雅，整体调理（调气为主）等是第一层次理论之例。五行、经络、辨证施治等是第二、三层次理论之例。"麻风病"、"震荡症候"、"痔疮"、"胃下垂"……等是第四层次之例。第二、三层次中运用既定的原哲，宗教文化上的抽象概念和名称来硬套的内容和形式较多。故在类此"病"、"证"关系或辩证施治上再下百年功夫也许无法顺利统一标准、客观化水平。传统医学不从原则古医医脱壳升级；不往现实实践深入的话，很难跳出笼统整体观，模糊辩证的泥坑。辩来辩去，挖来挖去，保守自满只能带来思维套思维的繁杂理论和已经淘汰的药方和其它也硬加之局。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;六、阴阳、气、命脉（蒙）、三焦、脉诊（汉）、舌诊和心理、社会方面重点下功夫来探索是传统医学的结合发展中的一个重点。气则成阴阳（火水、胆涎……）五行说的关键;三焦、命脉等则成脏肝脉络说的关键，舌诊则成四诊的客观化之要。舌诊发展与元朝骨伤科一样。十三世纪有一位姓敖的医生对舌诊作系统研究，并编著《金镜录》。此后，中西汇通和结合热潮中更深化了一大步。我们在《金代舌诊特技》、《蒙医史概略》、《蒙古族民间疗法》、《蒙医新论与特技》等中对类此关键总是已作详实明述了。此外，对"气学说"、"命脉学说"等方面我们另文专论。在各民族文化和民间疗法之中包含着极其重要和预防、保健、诊疗、康复、长寿等多方面的"道"（要顺）方法。这些又是当今或未来传统医学的结合发展中关键所在。古人首先了解到气（龙、凤）与呼吸生亡有密切关系。人类医药也就开始为气论道了。接着产生天地人相应观和火水观、气血观。再发展中产生精气神、阴阳五行说、四液说。其中，火水气和阴阳、精气神等，已通用至今。历代研究关键也围绕气这一"中介"和"阴阳"这一对立统一。今后仍然以此解决结合中的一系列关键问题。所以，我们曾完成"世界传统医学的结合发展与民族康复医术的开发服务论坛"（获奖）、"论气的质"（获奖）、"再论气-兼谈民间疗法、民族医学归宿"（获奖）、"co2激光与蒙医药结合治疗"（获奖）等后，详述此"再论世界传统医学的结合发展历程及必然规律，关键问题"本文"。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;最后我们认为：现在不仅急需发展传统医学（含民族、民间医术）和现代医学，而且更需要结合发展。创造新而独特医学体系及其服务工程模式。这不单是传统医学大系和现代医学本身的发展规律及需求，而且也是全人类社会所需。当今基础知识的统一；医学模式的转移；医学结构的完善；服务半径和项目的扩大；人民需求量的提高；边缘学科中的渗透；世界性大热潮的兴起；第四医学的发展；信息市场的联通，已给传统医学的结合发展带来空前的振兴和普及的机会。世界医药新发展去势也给传统医学大系带来巨大冲击，现就看跨世纪的天使们究竟如何深悟史况，掌握规律，明知关键，寻找突破口，了解归宿，面向大市场，解决供求不应，大胆创新立业，为全人类安康大局服务。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (708, 2, 56, '内蒙古蒙医药事业的回顾与展望', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px" height="623">\r\n  <tr>\r\n    <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古蒙医药事业的回顾与展望<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古自治区卫生厅 </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;蒙医药学历史悠久，具有完整理论体系和丰富临床实践经验，是我国传统医药学的重要组成部分。千百年来，它不仅为蒙古民族的繁衍昌盛和社会进步做出了重要贡献，而且以严谨的思维方式，独特的临床疗效丰富了世界医药宝库。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;早在12世纪以前，蒙古族劳动人民就发明和运用了许多适合当时社会环境、生产方式、生活习惯以及地理气候特点的医疗方法，这是蒙医药学的萌芽和积累时期。从13世纪初成吉思汗统一蒙古各部落到明朝初期，随着蒙古社会、经济、文化的发展，逐步形成了具有初步医疗理论和独特临床经验的古代蒙医药。16世纪开始，在原有古代传统蒙医药的基础上，吸收了中医学、古印度医学和藏医学理论，形成了近代蒙医药学。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药基础理论以阴阳五行、五元学说包括生理、病理、诊断、治疗原则和物的六味、八性、两力、十七功能为理论指导。治疗方法非常丰富，包括放血、灸疗、针刺、震脑、按摩、药浴、擦涂、罨敷等；临床分内科、外科、儿科、五官科、热病科、皮肤科、骨伤科、护理科等。蒙医药对常见病、多发病及疑难病如心脑血管病、糖尿病、血液病、肝病等有明显疗效，并具有简便验廉的特点。同时在预防、保健、康复、抗衰老等方面具有许多优势。它与中西医并存，形成了内蒙古自治区卫生事业中最具代表性的特色与优势。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;建国以来，特别改革开放以来，在各级党政领导的重视和支持下，蒙医药事业发生了历史性变化，取得了令人瞩目的成就。主要是：一、以蒙医医院为主体的蒙医医疗体系基本形成，蒙医医疗的总体诊疗水平和综合能力明显提高，担负了大量的医疗保健工作。二、蒙医药教育实现了由传统师承方式向现代化教育方式的转变，开创了以院校教育为主体，教育层次和布局合理的办学格局，为自治区内外培养了大批蒙医药人才。三、蒙医药学术水平不断提高，科技成果大量涌现，蒙医药古典医籍的挖掘和整理取得了显著成绩。蒙医药学术活动空前活跃。四、蒙医药的法制化、规范化、标准化建设化不断提高。尤其是2001年2月，全区正式颁布施行了《内蒙古自治区蒙医中医条例》，通过地方性法规，将蒙医药工作的地位和作用、蒙医药事业发展的方向、蒙医药工作的方针政策等以法律的形式确定下来，这是我国民族医药的第一部地方性法规，标志着蒙医药事业走上了依法发展的新阶段。五、蒙药开发利用前景广阔。内蒙古自治区是野生动植物生息繁衍的天然宝库。近年来，出现了一批具有较高科技含量的蒙医制剂及新药，既满足了蒙医药临床的需要，又推动了蒙医药的科技进步与创新，而且潜力很大。六、随着对外开放的不断扩大，蒙医药对外交流与合作日益增多，层次和水平不断提高，蒙医药国际上的影响越来越大，正在进一步走向世界。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;当前，蒙医药事业发展进入了新的历史时期。21世纪将是生命科学的时代，随着全球生态环境和改革和疾病谱的变化，现代疑难病、药源性疾病和化学药品的毒副作用，困扰着人们。《回归自然》已成为人类追求健康的潮流，用天然药物代替一些化学药品已经成为国际医药发展的重要趋势，蒙医药发展正面临良好的国际环境和发展机遇。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;为此，内蒙古自治区卫生厅确定了蒙医药事业“十五”计划，其主要目标是：到2005年，在巩固和完善现有蒙医药事业基础上，建成一批疗效突出、特色明显、优势显著、设施配套、功能齐全、管理科学的蒙医医疗机构；形成一定数量的临床疗效显著、学术水平较高、人才结构合理的蒙医药专科（专病）基地；加强现有蒙医药科研机构配套建设，使全区蒙医药人员整体素质、科研开发能力有明显提高，民族医药资源进一步得到有效的作用，基本满足自治区各族人民对蒙医药服务的多层次要求。同时，我们要进一步加强蒙医药的对外合作与交流，扩大视野，博采众长，学习和吸收对发展蒙医药有利的现代科学技术及先进成果，学习和借鉴其他国家在发展传统医药方面的有益的经验，逐步实现蒙医药现代化，为丰富世界医学宝库，为全人类的健康事业做出更大贡献！</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (709, 2, 56, '内蒙古民族医药学的发展及其影响', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古民族医药学的发展及其影响<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 内蒙古中蒙医医院 伊光瑞 乌力吉特古斯 </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;蒙医药学是蒙古民族传统文化之一，是东方传统医学的重要组成部分。蒙医学的发展经历了漫长的历史过程。早在两千年前成书的中医典籍《黄帝内经》就有中国北方少数民族医术的记载。公元13世纪，随着蒙古帝国的兴起和强大，国内各民族之间的文化广乏交流，以及欧亚各国之间相互交往的增多，蒙医也得到了迅速的发展。16世纪以来，随着古印度医学巨著《医经八支》和藏医《四部医典》等经典著作传入蒙古地区，为蒙医学基础理论的形成与系统化创造了条件，也进一步丰富了蒙医学诊断、治疗等内容。17世纪到19世纪涌现出一批著名的蒙医药学家和大量的蒙医药学著作，使蒙医药学日趋完善，奠定了现代蒙医药学的理论基础。如伊希巴拉珠尔所著《四部甘露》，罗布僧苏勒和木的《脉诊概要》，占布拉道尔吉的《蒙药正典》，占布拉著的《方海》等都是这个时期的代表人物和著作。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药学同中医学和其他少数民族医学一样，也具有自身的理论特点和医疗特色。它在学习、吸收古印度医学四大元素学说及藏医学“三元”学说的同时，也受到了中医“阴阳五行”学说的影响。这样，总结寒热对立统一规律及土、水、火、风、空的“五元”学说，进而发展成为蒙医对人体构造及其生理、病理、发展机理等方面的整体观。公元18世纪著名蒙古族医学家伊希巴拉珠尔首次提出“赫依”(气)、“协日”（火）巴达干”(湿痰)、“其苏”(血)、“协日乌素”(黄水或体病）、“粘耗日害”(寄生虫类及致病微生物)六大症的“六基”辨证学说，伊氏的基本理论是近代蒙医遵循的重要理论依据。近代蒙医认为人体是一个对立统一的整体，它以三根(即赫依、希拉、巴达干)与七素（塔米日”)之间的关系来解释人体的各种生理病理现象。这样，蒙医学已形成的自己的独特的学说有“三元”学说、“六基症”学说、“七素三秽”学说、“五元”学说、“寒热对立统一”学说、“脏腑经络”学说。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医诊断是通过望、问、切三诊，结合按、闻、嗅以及尿液诊察等进行综合分析的方法。这与中医及其他民族医学在认识人体生理功能和疾病施治方面有许多相似之处，但其所用药物、组方、功效、炮制等方面远不同于其它医学，只是有部分药物交叉使用。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医历来使用动、植、矿物药可达2000多种，常用有400余种。传统验方、成方约1200多种，常用者约400-500种。剂型有汤、丸、散、酒等，不下数十种。近些年剂型改良，又出现了片剂、胶囊、注射液、口服液等。蒙医用药具有是制作精细、用量小、剂型多样，服用与携带方便等特点。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医常用的疗术有灸疗、放血，正脑疗术、火针、温泉疗法、药浴疗法、针刺疗法、皮疗法等。蒙医正骨术是蒙医外治法的一大优势，具有方法独到、疗效好等特点。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药对许多疾病，诸如萎缩性胃炎、慢性结肠炎、妇科病、风湿病及类风湿关节炎、心血管疾病、乙肝和某些疑难病，如血小板减少性紫癜、再生障碍性贫血、系统性红斑狼疮、牛皮癣、高血压、中风等都有相当满意的疗效。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;近年来蒙医药在内蒙古有了较大发展。内蒙古自治区现有内蒙古医学院中蒙医系、内蒙古蒙医学院两所蒙医药高等学府和数个蒙医药中等专科学校。近年来已为全国各地及蒙古国培养了一批蒙医药人才。蒙医药工作者在继承、整理和挖掘蒙医药古典著作和蒙医医疗与科研方面做了很大的成绩，现已收集到的蒙医药古籍500余种，编写出版的论著有50多部，参加国内外医学交流学术论文1000多篇，各地建起了一些蒙医医院与研究所。内蒙古自治区中蒙医医院进行的科研课题“蒙药治疗脑血管意外引起的偏瘫”荣获国家卫生部重大科技成果乙级奖。蒙药治疗血小板减少性紫癜和再生障碍性贫血获内蒙古科委二等奖。现正在进行的蒙药治疗乙型肝炎、遗精、阳萎、类风湿性关节炎等课题也很受患者的好评，有很大的挖掘潜力。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;随着蒙医药事业在中国的发展与对外交流，蒙医药学在国际上也有了一定的影响，一些东欧国家纷纷派人前来内蒙古参观学习，蒙古国、波兰、匈牙利等国还相继建起了蒙医药研究机构。蒙医药作为国际传统医学的组成部分，也引起了部分发达国家专家学者的高度兴趣。如我区科研专著《蒙医学史》一书在日本出版，并获日本国翻译文化奖。日本、韩国、美国和台湾、香港等国家和地区曾多次派专家学者来内蒙古访问考察，试图从蒙医药中找到当今世界疑难病症的解决方法。总之，今天的蒙医蒙药正走向世界，为世界各族人民的健康事业做出更大的贡献。</p>\r\n    </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (710, 2, 56, '浅谈三根学说在蒙医学中的重要意义', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;浅谈三根学说在蒙医学中的重要意义<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;内蒙古医学院中蒙医系 崔东祥 阿迪雅 </p>\r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;蒙医学的三根学说是蒙医学理论基础的核心，贯穿于蒙医理论体系的各个方面，指导着历代医家的理论思维和诊疗实践。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医学认为人体之所以能维持正常的生理活动，主要在于体内具有三种能量和基本物质，即赫依、希拉、巴达干三大要素，因其为人体生命的本源、根本，故将之称为“三根”。在正常情况下，三根之间相互依存、相互制约的处于动态平衡的状态下行使着各自的职能。如赫依在三者中起着调节统帅的作用，具有推动血液运行，司理呼吸，分解食物，输布精华，传化糟粕及主生殖发育等生理功能。由于赫依具有阴阳的双重性，在人体中无孔不入，无处不到，运行于周身，发挥着犹如气化功能的作用。机体通过这一气的功能，利用摄入的水谷精华物质，修复组织细胞、化生能量、贮存营养、促进新陈代谢、促使机体发育生长，支配肢体活动功能的反射和维持人体的健康。故从广义上说，机体的物质代谢全过程以及所有的机能活动，都可以视作为赫依的运动所产生的变化，都是赫依参与其间的结果。而希拉则为人体正常生理活动的热能，属于阳、火之性，具有产生热量和调节体温，促进消化，可增强食欲，开胃进食，促使营养精华的成熟等生理功能。希拉以巴达干为自己存在的前提，与其保持相对平衡的状态，并参与胆汁的贮藏与排泄，为促进饮食的消化吸收起着直接的作用。三根中的巴达干则具有阴、寒、水、土之性，在人体正常生理活动中，它有滋生和调节体液，使身体、舌、思维活动稳定，辅助消化，滋养正精，增强意志，促进睡眠，濡润皮肤，坚固骨节等生理功能。巴达干以希拉为自己存在的依托，与其保持着相对平衡的状态，并在人体水液代谢过程中起着主导作用。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;事实上，人体所显现出的各种生命活动，都是在三根相互作用的基础上产生的。三者的有机配合和协调，使整个生命过程处于一个更为有序的代谢状态。而这种协调的机能活动，主要在于三根在体内所处的相对平衡的动态环境，即当它们的部位、容量处于相对平衡的状态时，生命体才能始终处于运动的新陈代谢过程。如三根中希拉与巴达干既是相互对立，又是相互依存的两个方面，任何一方都不能脱离对方而单独存在。而赫依则是希拉、巴达干二根运动的保证，对任何一方的盛衰均起着调节保持平衡的作用。希拉属阳属火，是温热疾病的主要起因；巴达干则属阴属水、土，是寒性疾病的主要原因；赫依则介于阴阳之间属于气，当其功能偏盛时，与希拉相合则如火上浇油，与巴达干相合则如雪上加霜，均会使病情加重。但在正常情况下，当赫依之凉性增盛而将失去相对平衡状态时，希拉之腻性(温性、油性)就会通过自我调控的形式来压抑赫依之凉性使其恢复正常；当希拉之锐性偏盛而将失去相对平衡状态时，巴达干之钝性就发挥作用，压抑希拉之锐性，使其恢复常态；当巴达干之寒性偏盛时，则希拉之热性就会压抑其寒性，使其达到平衡的状态。正是这种相互制约、相互依存的关系，人体的正常生理活动才能够得以维持。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;三根即是维持人体正常生命活动的基本物质，又是导致罹病患疾的内在因素。蒙医学认为，在各种致病因素的影响下，都会引起三根的偏盛偏衰、太过或不及等病理变化，进而即会发生病变。由于三根这一反常之态会给人体带来病变灾害，故又将之称为“三弊”。三弊是一切疾病产生的根本因素，诚如《四部医典・论说本》中所云：“但所有疾病无不归属于三弊”。这是指除了三弊本身固有的三根失调所导致的赫依病、希拉病、巴达干病外，血液受损时易引起热性病变，并助长希拉之热，抑制巴达干之寒；黄水受损则与血、希拉相合而引起的热性黄水病变，及与巴达干或赫依相合所引起的寒性黄水病变；还有肉眼看得见和看不见的致病虫类亦可与体内之三弊相搏而引起复杂的病理变化。故近代蒙医家将其发病之根源归纳为赫依、希拉、巴达干、血、黄水、虫6种，并将此而引起的疾病称之为“六基证”。此外，三根不但决定人体体质差异对不同致病因子的易感性，而影响疾病的发展趋向。同时，在病理变化方面，三根之间又各自显现出不同的证候反应，从而表现为不同类型的病变。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;由此可见，三根学说作为对于认识人体的生理功能和病理机制的理论根据，必然也指导着蒙医辨证与治疗等各个方面。辨别三根，是蒙医学其他各种研究方法的共性所在和基本点。三根学说的对立统一观重点地反映了两层关系，一是无形和有形物体与生命活动三者间的错综联系；二是赫依与希拉和巴达干三者间的辨证关系。并由这两层关系开拓出蒙医生理、病理、发病、诊法、辨证、治法方药等各个方面与各个层次，它与五元学说一起，起到了构筑基本框架的重要作用，是蒙医学理论体系结构的核心。 \r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (711, 2, 55, '抓住西部大开发机遇实施蒙药发展新战略', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;抓住西部大开发机遇实施蒙药发展新战略<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;抓住国家实施西部大开发的良好机遇，充分利用通辽市资源条件优势，实施蒙药发展新战略，力争用10年时间将蒙药产业发展成为通辽市的支柱产业。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;蒙医药是我国民族医药重要组成部分，具有较系统的理论体系、独特的诊疗方法、显著的临床疗效，正被越来越多的人伞所认识。通辽地区是我国蒙古 \r\n        族最为密集的聚居地区，蒙医药发展历史源远流长，发展蒙医药具有得天独厚 的优势和有利条件。该地区目前拥有内蒙古蒙医学院及其附属医院、通辽市蒙 \r\n        医研究所、内蒙古蒙药制药厂、库伦旗蒙药厂。其中内蒙古蒙医学院及其附属医院是国内实力较强的蒙医药教学、临床及科研基地；内蒙古药制药厂是全国最大、设施最完善的蒙药制药企业。同时该地区拥有丰富的药材资源，每年有大量中蒙药材源源不断地运往国内外药材市场，是我国重要的中草药材生产基地。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;面临前所未有的良好发展机遇，通辽市政府对蒙药产业的发展十分重视，计划在产业规划布局、政策制定、组织保障及资金倾斜等方面采取一系列措施予以扶持。根据蒙药产业化发展战略，计划在2000年-2002年完成企业集团的组建，即组建以内蒙古蒙药制药厂为龙头的紧密型蒙药集团，实现通辽地区传统蒙药产业向现代化蒙药产业的历史性转变。预计通辽市蒙药产业2010年销售收入可超过10亿元，实现利税2亿-3亿元，从而成为通辽市的支柱产业和新的经济增长点。</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (712, 2, 56, '我国民族医药科技工作现状概述', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我国民族医药科技工作现状概述<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国家中医药管理局中西医结合与民族医药处 张奇<br>\r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;教育事业全面发展，教育体系初步形成<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1984年在呼和浩特召开的第一届全国民族医药工作会议上提出了“加速发展民族医学教育事业”的任务，促进了民族医教育事业的发展。各地先后建立了一批中等民族医药专科学校，民族医药正规教育开始进入一个新的发展时期。“七五”期间，经国家教委批准，西藏、内蒙、新疆分别成立了高等藏医、蒙医、维医院校，民族医药正规教育开始步入正轨。建校以来，这三所院校培养出近3000名毕业生。此外，正在筹建中的青海省藏医学院已招收了本科学生。另有部分医学院校开设了民族医系或民族医专业。各高、中等院校成立后，注重了教材建设，完成了比较系统、完整的第一版教材，其中藏医教材包括31门课程，蒙医25门，维医14门。为了缓解人才教育的不足，除大、中专院校的正规学制教育外，各地在培养人才上进行了多方面的探索：举办在职人员短期培训班，举办年度继续教育考试，培训基层医务人员；采取师承方式，跟师学习；选拔骨干到内地或国外进修；组织考试考核，录用民间的民族医人员，充实医院的技术力量等等。民族医教育以高骂等院校为龙头基地，采取不同形式、不同渠道培养人才的体系已经形成。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;科研工作得到重视，学术水平逐步提高<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;1984年内蒙会议提出了“加强民族医药科研工作，保持和发扬民族医药特色，提高民族医药科研水平”的方针，科研工作开始在各地得到重视，学术水平逐步提高，科研机构得到发展。<br>\r\n        1984年以来，各民族地区不同程度地开展了一些民族医药科研工作，其中既有文献整理、实验研究，也有开发研究。据统计，全国已获各级科研成果 283项，其中省部级以上成果32项。藏、蒙、维、傣、壮、朝、彝、瑶、苗、回、侗、土家等民族均出版了一批有影响的专著，翻译、收集和整理了一大批古典医学文献和民间验方等。在实验研究方面，主要是围绕着临床常见病、多发病，一些地区已开始进行协作研究。在开发研究方面，充分利用民族地区药物资源的优势，进行新药、新产品的开发研究，既有临床医疗用药，又有营养保健品，并积极开展野生药用植物人工栽培的研究工作。各地还注意了科研成果的推广转化和学术交流活动的开展，并与一些国家或地区开展了交流与合作。 \r\n        <br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;民族医药科研机构也有了一定的发展。许多民族地区相继建立了一批民族医科研机构，其中15所为具有独立编制的县级以上科研机构。这些机构近十年来列入各级科研课题228项，其中省部级以上课题92项，为促进民族医药的学术发展做出了重要贡献。<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;民族药的开发利用走上规范化道路<br>\r\n        &nbsp;&nbsp;&nbsp;&nbsp;民族药物是民族医药特色的重要内容，也是关系到民族医药机构社会效益、经济效益的重要方面。各地在民族药物的生产与管理方面做了大量的工作。1984年以来，各民族地区普遍开展了当地药物自然资源的专题考察，初步探明了各地民族药物资源的分布、生长和贮藏情况，如西藏已查明药用动植物4000余种，其中藏药2000多种。在民族药标准化管理方面，各地开展了一系列的工作，先后制订实行藏药、蒙药、维药的地方标准。在此基础上，制订全国统一部颁标准的工作也已起步，藏药的部颁标准在西藏颁布，为促进民族药生产与管理向制度化、规范化方向发展迈出了可喜的一步。在民族药生产方面，国家中医药管理局在1992年确定了4个藏药、蒙药、维药制剂中心建设单位，它们在民族药产、剂型改革、管理和效益上都发挥了重要作用。民族药的生产逐步得到改进，能生产十几种剂型，藏药350个品种，蒙药300个品种，维药192个品种。多数民族地区都拥有民族药制药工业或民族医医院制剂室，生产各种民族成药。由江苏援建的西藏自治区藏医院藏药厂改扩建工程已经竣工，这必将促进民族药生产与管理的现代化进程。此外，一些濒危的动植物药用资源的保护、引种与人工替代研究也引起了有关方面的重视，并已起步实施。</p>\r\n      </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (716, 3, 7, '无标题', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>1977年第四期<br>\r\n        <br>\r\n        1 胃、十二指肠溃疡几例总结 <br>\r\n        2 老蒙医大夫白清云治愈结核性腹膜炎穿孔一例整理<br>\r\n        3 关于蒙医几种合并症及其治疗药方<br>\r\n        4 常见的几种心脏病(资料续登) <br>\r\n        5 几种皮肤病的治疗几例整理 <br>\r\n        6 治疗肝脏疾患体会 <br>\r\n        7 治疗妇女白带小方<br>\r\n        8 治疗耳部疾患经验<br>\r\n        9 烟酒对冠心病人的危害<br>\r\n        10 人造关节 <br>\r\n        11 蒙医针灸铜人简介<br>\r\n        12 关于《其其格日那》(沙棘)一词的读音，写法的商确<br>\r\n        13 关于玛努-西汤的分析 <br>\r\n        14 藏医常用药的藏、蒙，汉名对照</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (713, 3, 7, '1974年第一期', '轻武器', 1, '2003-06-09');
INSERT INTO myy_text VALUES (714, 3, 7, '1975年第二期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>1975年总第二期<br>\r\n        <br>\r\n        1 蒙药沉香八味散治疗冠心病的临床观察<br>\r\n        2 常见的几种心脏病<br>\r\n        3 蒙药沙棘五味散治疗慢性气管炎疗效分析<br>\r\n        4 蒙医对几例西拉乌苏风湿病(风湿性关节炎)的疗效总结<br>\r\n        5 用几种民间理疗法治疗胡盈(类似半身不遂)及风湿病的初步体会<br>\r\n        6 查干胡盈与神经性疾患的区别及其治疗的初步认识<br>\r\n        7 对几种词语的看法<br>\r\n        8 藏医常用药的藏、蒙、汉名对照 </p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (715, 3, 7, '1976年第三期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>1976年第三期 \r\n        <br>\r\n        <br>\r\n        1 老蒙医恩和巴雅尔(敖连合)临床经验汇编<br>\r\n        2 用蒙药阿嘎茹--松瓦治疗失眠症若干例体会 <br>\r\n        3 对蒙药&quot;道日贵荟荣&quot;的探讨 <br>\r\n        4 藏医常用药物的藏、蒙，汉名对照</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (717, 3, 7, '1980年第五期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>1980年第五期<br>\r\n        <br>\r\n        1 蒙医治愈肺气肿二例初步体会<br>\r\n        2 关于小儿指纹诊断及其在西医分析中的简要体会<br>\r\n        3 关于细菌性痢疾的防治<br>\r\n        4 传染性肝炎的治疗<br>\r\n        5 关于流行性感冒的鼻烟药疗法的简介<br>\r\n        6 蒙医治愈卵巢瘤一例的整理体会<br>\r\n        7 公召葛根喇嘛(活佛)关于《四部医典》诊断，治疗及其调节生理功能诵诗百二十四节<br>\r\n        8 百日咳及其用鸡苦胆治疗的小结<br>\r\n        9 简述肝包虫病的传染、预防及其危害 <br>\r\n        10 过量饮酒的危害<br>\r\n        11 藏医常用药的藏、蒙，汉名对照</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (718, 3, 7, '1981年第六期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td> \r\n      <p>1981年第六期<br>\r\n        <br>\r\n        1 蒙医对布鲁氏菌病的认识与治疗<br>\r\n        2 小儿疳积及其简易疗法<br>\r\n        3 蒙西医结合治疗肺部慢性疾患十五例的汇综分析<br>\r\n        4 再生障碍性贫血的治疗及运用蒙医理论对本病致病机理的分析<br>\r\n        5 蒙医对治疗巴达干型（抑郁性)精神病与赫依型(躁狂性)精神病的初探<br>\r\n        6 蒙医治疗风湿病及对制作并使用蒙药嘎茹迪注射剂的体会<br>\r\n        8 蒙医治疗骨质增生的经验总结<br>\r\n        9 蒙医治疗胃、十二指肠溃疡的初步疗效及经验<br>\r\n        10 对蒙医&quot;赫依&quot;(即中医所称之广义的&quot;气&quot;)的肤浅认识<br>\r\n        11 蒙医关于胃的保护及胃病的预防<br>\r\n        12 介绍若干西医名词术浯的蒙文译名</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
