INSERT INTO myy_text VALUES (724, 3, 7, '1987年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1987年第1期<br>\r\n      <br>\r\n      1 治疗囊虫病的体会<br>\r\n      2 对妇科病诊治的认识<br>\r\n      3 蒙药治疗急性黄疸型肝炎的体会<br>\r\n      4 大肠寒胀症的治疗<br>\r\n      5 嘎木朱尔治疗宫颈糜烂的总结<br>\r\n      6 蒙药治疗脑震荡<br>\r\n      7 略谈力毕巴拉珠尔<br>\r\n      8 桑吉德的功效<br>\r\n      9 乳白石疗法<br>\r\n      10 关于《四部医典》<br>\r\n      11《医药月帝》考略<br>\r\n      12 浅谈蒙药学<br>\r\n      13 蒙医教授--苏荣扎布</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (725, 3, 7, '1987年第2期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1987年第2期<br>\r\n      <br>\r\n      1 肝与肝病<br>\r\n      2 试述肾病 <br>\r\n      3 简述传染性疾病源流<br>\r\n      4 在党的民族政策光辉照耀下内蒙古自治区蒙医药事业发展迅速<br>\r\n      5 对新褐日乎病的认识<br>\r\n      6 简述胰腺<br>\r\n      7 黑箱理论与蒙医诊断、治疗<br>\r\n      8 食道狭窄巴达干的起因与证候<br>\r\n      9 治疗常见病之方药<br>\r\n      10 荡黑泌得治疗头痛<br>\r\n      11 泻心方治疗精神病四则<br>\r\n      12 肝热病治验<br>\r\n      13 有关药检标准的几点意见<br>\r\n      14 火罐放血法<br>\r\n      15 针灸在蒙古的发展<br>\r\n      16 内蒙阿尔山(矿泉)简介<br>\r\n      17 外疗专家-----罗布桑西德日布</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (726, 3, 7, '1987年第3期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1987年第3期<br>\r\n      <br>\r\n      1 临床救误二例<br>\r\n      2 治疗常见病的方药<br>\r\n      3 试述赫依、希日，巴达干<br>\r\n      4 六基证对骨伤科的指导意义<br>\r\n      5 略谈癃闭<br>\r\n      6 简述传染性疾病源流(续)<br>\r\n      7 &quot;赫依&quot;在临床中的重要性<br>\r\n      8 希拉乌素性心脏病治疗体会<br>\r\n      9 高原病治验<br>\r\n      10 传统方治疗肺结核<br>\r\n      11 巴达干、赫依性心刺痛<br>\r\n      12 哈敦阿尔山散治疗宫颈糜烂<br>\r\n      13 淋巴结结核的治疗<br>\r\n      14 氟骨症治验1例 <br>\r\n      15 蒙药标准与药检<br>\r\n      16 印度传统医研究状况<br>\r\n      17 熏法<br>\r\n      18 烙法<br>\r\n      19著名蒙医鲁宝加木苏及其学术思想 </td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (727, 3, 7, '1987年第4期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1987年第4期<br>\r\n      <br>\r\n      1 蒙药内服配合熏、灸法治疗精神病<br>\r\n      2 治疗肾病综合症之方药<br>\r\n      3 试论赫依、希拉、巴达干个（续)<br>\r\n      4 嗔、痴、贪浅说<br>\r\n      5 关于白脉理论的初探<br>\r\n      6 阑尾炎治验三例 <br>\r\n      7 蒙医药的治疗肝热病的体会<br>\r\n      8 蒙西医对照治疗宫颈糜烂125例疗效观察<br>\r\n      9 流行性脑膜炎的治验<br>\r\n      10 浅述肺结核的治疗<br>\r\n      11 外敷剂治疗疥疮105例<br>\r\n      12 肝热病验案二例<br>\r\n      13 蒲公英煎剂治疗结膜炎<br>\r\n      14 关于苘麻子与火麻仁基源的考证<br>\r\n      15 如何应用汤剂<br>\r\n      16 十七味安宁散的应用<br>\r\n      17 关于蒙药&quot;达格沙&quot;的考证研究<br>\r\n      18 关于制作丸药的工艺<br>\r\n      19 关于火针的妙用<br>\r\n      20 应用放血法治疗顽固性头痛四例<br>\r\n      21 蒙西医结合护理脑血管意外的体会<br>\r\n      22 试论如何发展蒙医学<br>\r\n      23 印度医学<br>\r\n      24 关于〈柴测尔米格赞〉的源流<br>\r\n      25 著名蒙医--吴井昌</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (728, 3, 7, '1988年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1988年第1期<a name="19881"></a><br>\r\n      <br>\r\n      1 赫依病的认识及治疗<br>\r\n      2 西吉德六位散的应用<br>\r\n      3 阴阳和人体<br>\r\n      4 阴阳平衡学说是正确的吗? <br>\r\n      5 关于精、糟、火及饮食 <br>\r\n      6 论类风湿 <br>\r\n      7 治疗遗精10例 <br>\r\n      8 核桃仁治疗疝气 <br>\r\n      9 乳房恶血症 <br>\r\n      10 试谈骨结核的治疗 <br>\r\n      11 治疗肝炎的经验<br>\r\n      12 治疗肝黄疸性粘疫 <br>\r\n      13 治疗小儿肺门淋巴结核 <br>\r\n      14 几种肉苁蓉的生物学研究 <br>\r\n      15 阿明尔登成方中应用胎盘的效果 <br>\r\n      16 巴豆简方 <br>\r\n      17 药浴的配伍法 <br>\r\n      18 芝麻油的医疗作用 <br>\r\n      19 丸药分解限时过程的调查报告 <br>\r\n      20 马奶浅谈 <br>\r\n      21 署伏沙疗 <br>\r\n      22 治脑震荡方法 <br>\r\n      23 关于《柴测尔米格赞》的源流(续) <br>\r\n      24 蒙药专家一-于庆祥 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (729, 3, 7, '1988年第2期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1988年第2期<a name="19882"></a><br>\r\n      <br>\r\n      1 试述&quot;粘&quot;症 <br>\r\n      2 也述&quot;粘&quot;症 <br>\r\n      3 固护&quot;热能&quot;的意义 <br>\r\n      4 简述&quot;胃内三火&quot; <br>\r\n      5 胆结石形成的机理 <br>\r\n      6 通脉利尿法治疗狂犬病 <br>\r\n      7 阳萎治验 <br>\r\n      8 儿科病症诊治要点 <br>\r\n      9 小儿消化不良治验 <br>\r\n      10&quot;鼠疮&quot;治验 <br>\r\n      11 治疗胆热症的体会 <br>\r\n      12 急性关节炎的治疗经验 <br>\r\n      13 治疗急性病毒性肝炎的体会 <br>\r\n      14 对蒙成药品种应用的调查 <br>\r\n      15 商陆中毒的解救 <br>\r\n      16 妊娠伤胎的治疗 <br>\r\n      17 清代名医罗布桑丹曾扎拉胜 <br>\r\n      18 契丹族及其医学 <br>\r\n      19 古印度医学--维达医学 <br>\r\n      20 临床大师--苏德宝</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (730, 3, 7, '1988年第3期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1988年第3期<a name="19883"></a><br>\r\n      <br>\r\n      1 简述《黄水》的作用 <br>\r\n      2 高血压病的病因学说 3<br>\r\n      3 肾聚合症的治疗分析 <br>\r\n      4 治疗尿闭的体会 <br>\r\n      5 蒙医治疗《坏血病》验案 <br>\r\n      6 《黄水》病治验 <br>\r\n      7 颈淋巴结核的治法 <br>\r\n      8 《自脉病》治验 <br>\r\n      9 骨科治疗中《动》的作用 <br>\r\n      10 灸法治愈精神分裂症的分析 11<br>\r\n      11 用《嘎日纳嘎》--10味散治疗小儿腹泻的经验 <br>\r\n      12 妇幼保健系列内容<br>\r\n      13 关于蒙药学授课方法的初探<br>\r\n      14 1002种手法和366种手法的计算<br>\r\n      15 医经甘露略要<br>\r\n      16 古代印度医学<br>\r\n      17 蒙医主任医师--杜格尔扎布 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (731, 3, 7, '1988年第4期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1988年第4期<a name="19884"></a><br>\r\n      <br>\r\n      1 伊西巴拉珠尔及其学术思想<br>\r\n      2 关于伊西巴拉珠尔考略 <br>\r\n      3 头面部胡盈(神经疾患)14例治验 <br>\r\n      4 简易临症疗法 <br>\r\n      5 试述乳类的临床应用 <br>\r\n      6 小儿肺热的治疗经验 <br>\r\n      7 治疗小儿黄疸33例分析 <br>\r\n      8 治疗产褥热的体会 <br>\r\n      9 通拉嘎--601治疗原发性血小板减少性紫癜 1<br>\r\n      10 治疗雅玛病(鼻窦炎)30例 1<br>\r\n      11 对心刺痛(冠心病)的治疗体会 <br>\r\n      12 萨病(脑意外)的治疗体会 <br>\r\n      13 药浴治疗牛皮癣验案 <br>\r\n      14 蒙药引子与协同汤剂 <br>\r\n      15 诃子的解毒功用 <br>\r\n      16 诃子四味散治疗老鼠疮 <br>\r\n      17 医经甘露略要 <br>\r\n      18 古代印度医学 <br>\r\n      19 藏，蒙医学者--龙德格</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (732, 3, 7, '1989年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1989年第1期<br>\r\n      <br>\r\n      1 老蒙医巴彦斯楞外治手法整理 <br>\r\n      2 以阴阳理论解释胚脉及其它体质因素与疾病发生的关系 <br>\r\n      3 蒙药对胰腺的作用初探 <br>\r\n      4 试述皮肤病的病因病机 <br>\r\n      5 浅说心理作用是如何影响胃病的 <br>\r\n      6 传统方与自拟方治疗24例慢性布病的比较分析 <br>\r\n      7 应用药浴治疗慢性风湿病74例 <br>\r\n      8 传染性肝炎治验 <br>\r\n      9 精神病治验 <br>\r\n      10 慢性肾热病治验 <br>\r\n      11 治愈白血病一例分析 <br>\r\n      12 试述诃子 <br>\r\n      13 传统密方--人参三味汤 <br>\r\n      14 识别牛黄的经验 <br>\r\n      15 应用儿茶治疗口腔溃疡 <br>\r\n      16 &quot;五味黑&quot;驱黄的分析 <br>\r\n      17 医经甘露略要 <br>\r\n      18 著名蒙医--陈・希日部</td>\r\n  </tr>\r\n</table>', 1, '2003-06-09');
INSERT INTO myy_text VALUES (733, 3, 7, '1989年第2期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1989年第2期<br>\r\n      <br>\r\n      1 论阴阳五行 <br>\r\n      2 述正常赫依、希拉，巴达干三要素相互关系 <br>\r\n      3 论蒙医三要素在骨骼中所显之作用特点 <br>\r\n      4 三要素疾病症状问诊二十九大要点 <br>\r\n      5 简要介绍&quot;dme&quot;学说及其在蒙医研究中的可行性<br>\r\n      6 老蒙医巴彦斯愣外治手法整理(继篇) <br>\r\n      7 消化不良，胃脘部&quot;巴达干&quot;及胃阳衰弱之鉴别诊断与治疗 <br>\r\n      8 慢性肺病治验 <br>\r\n      9 应用加味石药九味散治愈肺孔洞症的报告 <br>\r\n      10 常用那茹九味丸的治疗经验 <br>\r\n      11 应用乌力吉十八味丸和巴特尔七味丸治疗乳痈的经验 <br>\r\n      12 应用灸法治疗椎柱骨质增生等几种&quot;赫依、巴达干&quot;合并症的体验 <br>\r\n      13 蒙西医结合对外伤性瘫痪的治疗观察体验 <br>\r\n      14 试述蒙药方剂学基础知识<br>\r\n      15 匈奴的宗教与巫医 <br>\r\n      16 放血疗法的沿革<br>\r\n      17 蒙药学教授--罗布僧</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (734, 3, 7, '1989年第3期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1989年第3期<br>\r\n      <br>\r\n      1 胃下垂治验 <br>\r\n      2 萨病(偏瘫)防治经验 <br>\r\n      3 简述《未消症是内科诸病之本因》 <br>\r\n      4 关于治疗妇女更年期综合症 <br>\r\n      5 简论用&quot;狗肺滋补&quot;法治寒性肺痨 <br>\r\n      6 结肠&quot;宝日&quot;病的治验 <br>\r\n      7 痔疮治验 <br>\r\n      8 石榴七味散治疗膀胱炎热症 <br>\r\n      9 四味解毒散治疗醇中毒的体会 <br>\r\n      10 蒙药治疗沥清中毒的体会 <br>\r\n      11 试述蒙药方剂学基础知识 <br>\r\n      12 论述&quot;巴沙嘎&quot;之来源 <br>\r\n      13 试论广枣三味汤 <br>\r\n      14 木鳖子炮制经验 <br>\r\n      15 蒙医学心理学初探 <br>\r\n      16 蒙药浴治疗风湿性关节炎100例 <br>\r\n      17 对电热针治疗腱鞘囊肿的体会 <br>\r\n      18 简论传统医药 <br>\r\n      19 蒙医学者--嘎拉僧桑杰</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (735, 3, 7, '1989年第4期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1989年第4期<a name="19894"></a><br>\r\n      <br>\r\n      1 《哈敦散》治疗宫颈糜烂的成果报导 <br>\r\n      2 蒙古人民共和国医药发展概况 <br>\r\n      3 老蒙医吴景昌的医疗经验 <br>\r\n      4 治疗牛皮癣的体会 <br>\r\n      5 论蒙医学和气象学的关系 <br>\r\n      6 草果四味汤治疗心绞痛二例 <br>\r\n      7 关于老年病的防治 <br>\r\n      8 玉簪花十五味散治疗小儿咳嗽 <br>\r\n      9 主用苏日各金十一味散治疗精神分裂症 <br>\r\n      10 常见精神病的治疗 <br>\r\n      11 应用辛辣性药物降脂减肥 <br>\r\n      12 三子汤的多种用用法 <br>\r\n      13 关于鉴定草乌 <br>\r\n      14 传统蒙医整骨术 <br>\r\n      15 试述矿泉疗法结合蒙药治疗的疗效 <br>\r\n      16 热罨疗术 <br>\r\n      17 尿的妙用 <br>\r\n      18 《丙寅》杂志与健康教育 <br>\r\n      19 元代医学研究者沙图穆苏考略 <br>\r\n      20 著名老蒙医--白・扎木苏荣</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (736, 3, 7, '1990年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1990年第1期<a name="19901"></a><br>\r\n      <br>\r\n      1 牧区原发性肝癌的早期诊断问题 <br>\r\n      2 谈谈蒙医急诊 <br>\r\n      3 色布格扎布老蒙医的临床经验点滴 <br>\r\n      4 胃溃疡的治疗经验 <br>\r\n      5 疥疮的诊断与方药 <br>\r\n      6 &quot;巴木&quot;病的治疗经验 <br>\r\n      7 应用&quot;苏热，曼珠尔&quot;方治疗睾丸结核 <br>\r\n      8 以不消化所致&quot;巴达干&quot;病与隐性&quot;宝日&quot;症的治疗经验 <br>\r\n      9 婴儿不消化症的治疗体会 <br>\r\n      10 应用泻法治疗肝疫的益处 <br>\r\n      11 大肠陈旧热症的治疗 <br>\r\n      12 新方一--腹泻散 <br>\r\n      13 槟榔--七味散的临床应用 <br>\r\n      14 应用&quot;包兹&quot;炮制寒水石的方法 <br>\r\n      15 蒙成药制作工艺 <br>\r\n      16 副主任医--丹金扎木苏 <br>\r\n      17 记为蒙医做出杰出贡献的--苏德那木达尔扎</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (737, 3, 7, '1990年第2期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1990年第2期<a name="19902"></a><br>\r\n      <br>\r\n      1 治疗男性不育症两例 <br>\r\n      2 神经血管性头痛的治疗体会 <br>\r\n      3 人工药浴制剂的剂改探讨 <br>\r\n      4 谈谈温泉的疗效 <br>\r\n      5 治疗&quot;宝日&quot;症的体会 <br>\r\n      6 玛拉哈扎布治疗疮疡的经验 <br>\r\n      7 试述赫依、希拉、巴达干与七恒三糟的依存关系 <br>\r\n      8 赫依、希拉、巴达干是抽象名词 <br>\r\n      9 应用通嘎拉嘎一601治疗再障32例、原发性血小板减少性紫癜80例的临总<br>\r\n      10 治疗毒肿的经验 <br>\r\n      11 黄疸性肝疫的治疗观察 <br>\r\n      12 应用紫草油剂治疗烫伤86例 <br>\r\n      13 简易疗法治疗胆道回虫症 <br>\r\n      14 乌力吉-一18味丸的临床应用 <br>\r\n      15 妇女气滞症的治疗经验 <br>\r\n      16 月经与情绪 <br>\r\n      17 沙棘的药性 <br>\r\n      18 含水银制剂的药性探讨 <br>\r\n      19 放血穴位的定位 <br>\r\n      20 藏医历史学者一--弟司・桑杰加错 <br>\r\n      21 蒙医副主任医师--一色仁如勒玛</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (738, 3, 7, '1990年第3期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1990年第3期<a name="19903"></a><br>\r\n      <br>\r\n      1 治疗膝关节半月瓣损伤30例体会 <br>\r\n      2 治疗坐骨神痛的印象 <br>\r\n      3 蒙医结合耳针、耳压治疗胆结石500例 <br>\r\n      4 治疗骨质增生治验 <br>\r\n      5 治疗疥疮的经验 <br>\r\n      6 结肠溃疡治验 <br>\r\n      7 论&quot;粘&quot;证 <br>\r\n      8 应用通嘎拉嘎--一601治疗再障32例，原发性血小板减少紫癜80例的临床总结 <br>\r\n      9 应用珍珠一9味治疗宫颈糜烂261例 <br>\r\n      10 &quot;打鼾&quot;的产生原因及治疗措施 <br>\r\n      11 胆病的治疗 <br>\r\n      12 治疗癫痫病2例 <br>\r\n      13 应用红花--13味治疗偏头痛10例 <br>\r\n      14 腹部术后应用蒙药敖勒盖一13味的体会 <br>\r\n      15 面神经麻痹的治验 <br>\r\n      16 发展蒙医护理教学工作的意义 <br>\r\n      17 蒙药&quot;玛努拉格扎&quot;的考证 <br>\r\n      18 简法治疗&quot;胃震&quot;症 <br>\r\n      19 熏药的功用 <br>\r\n      20 老蒙医金巴的金炙治疗经验 <br>\r\n      21 著名蒙药学家--一占布勒道尔吉 <br>\r\n      22 《蒙古传统医学》一书的序 <br>\r\n      23 蒙医主任医师一白宝玉 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (739, 3, 7, '1990年第4期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1990年第4期<a name="19904"></a><br>\r\n      <br>\r\n      1 民族卫校的教学要发挥民族语言的优势<br>\r\n      2 蒙医临床要结合现代医学检验手段 <br>\r\n      3 胃溃疡治验 <br>\r\n      4 节气变化与蒙医的关系 <br>\r\n      5 黄水(津液)与精华，糟粕 <br>\r\n      6 论&quot;粘&quot;证(续) <br>\r\n      7 桑吉扎木苏的医学学术思想 <br>\r\n      8 精神分裂症治验二则 <br>\r\n      9 心剌痛的治疗 <br>\r\n      10 应用针灸治疗面神经麻痹41例的体会 <br>\r\n      11 肾结石治验 <br>\r\n      12 应用郎庆膏治疗痔疮124例 <br>\r\n      13 治疗小儿消化不良的简易疗法 <br>\r\n      14 鼻窦炎的治验 <br>\r\n      15 介绍几种蒙药的特效作用 <br>\r\n      16 对珍珠一27味散的解析 <br>\r\n      17 放血疗法特点及重要作用 <br>\r\n      18 手法治疗风湿病 <br>\r\n      19 外治术治疗扁桃腺炎54例 <br>\r\n      20 蒙古民间医研究院博士--策・海德布<br>\r\n      21 护理中的心理学 <br>\r\n      22 风湿性关节炎的护理体会 23 主任蒙药师--包景荣</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (740, 3, 7, '1991年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1991年第1期<br>\r\n      <br>\r\n      1 论六腑病变之机理 <br>\r\n      2 总述肝热 <br>\r\n      3 心刺痛的病理与诊治 <br>\r\n      4 羊肉淡酒及其临床作用 <br>\r\n      5 周・扎拉桑的临症治验 <br>\r\n      6 蒙医治疗《视网膜脱离》 <br>\r\n      7 《布日和呼》眼症的解析 <br>\r\n      8 小儿肾热治验 <br>\r\n      9 试论小儿鼻衄 <br>\r\n      10 胃溃疡治验 <br>\r\n      11 感冒的辨证治疗 <br>\r\n      12 治疗脱肛的有效疗法 <br>\r\n      13 《途来，希拉乌素》病的治疗体会 <br>\r\n      14 用沉香一35味治疗心刺痛11例 <br>\r\n      15 治疗风湿关节病20例 <br>\r\n      16 简述蒙医护理 <br>\r\n      17 萨病(脑溢血)病人的护理 <br>\r\n      18 荨麻根临症应用一得 <br>\r\n      19 灸疗应用四则 <br>\r\n      20 放血疗法特点及重要作用(继篇) <br>\r\n      21 为现代蒙医奠定基础的杰出领导--呼尔钦毕力格 <br>\r\n      22 蒙医事业的开拓者--杨・拉哈巴</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (741, 3, 7, '1991年第2期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1991年第2期<a name="19912"></a><br>\r\n      <br>\r\n      1 关于&quot;赫依&quot;的史观与研究 <br>\r\n      2 试论更年期综合症 <br>\r\n      3 青光眼治验 <br>\r\n      4 《嘎玛病》 (白塞氏病)治验 <br>\r\n      5 治疗肝《宝日》 (肝硬化)2例 <br>\r\n      6 胆结石治验 <br>\r\n      7 遗尿症的病机与治验 <br>\r\n      8 《心刺痛》 (冠心病)的治疗 <br>\r\n      9 治疗乳腺炎2例 <br>\r\n      10 《巴木病》 (红班狼疮)治验 <br>\r\n      11 &quot;贫血&quot;的治疗 <br>\r\n      12 《那如一3味丸》的理论分析 <br>\r\n      13 应用《巴特日一7》治疗小儿腹泻20例 <br>\r\n      14 应用《黑矾一3味汤》治疗口腔溃疡 <br>\r\n      15 试述骨折的修复 <br>\r\n      16 脑震荡治验 <br>\r\n      17 按摩、火罐治疗《主动脉赫依病》 (气鼓) <br>\r\n      18 五味阿尔善(药浴)的功效与应用 <br>\r\n      19 纪念廿世纪蒙医学家--古那巴达拉诞辰100周年 <br>\r\n      20 蒙医副主任医师--苏德那木</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (742, 3, 7, '1991年第3期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1991年第3期<a name="19913"></a><br>\r\n      <br>\r\n      1 应用煎骨液治疗小儿白脉性麻痹 <br>\r\n      2 沙棘九味散治疗慢性支气管炎的分析 <br>\r\n      3 咽症验方一胡木日乐 <br>\r\n      4 哮喘症治验 <br>\r\n      5 临床科研设计 <br>\r\n      6 关于&quot;赫依&quot;的史观与研究 <br>\r\n      7 风显性关节炎治验 <br>\r\n      8 酸马奶的作用 <br>\r\n      9 胃、十二指肠溃疡治验 <br>\r\n      10 炙疗治疗精神病11例 <br>\r\n      11 蒙医应用药引子的原由 <br>\r\n      12 黄芩的鉴定 <br>\r\n      13 按蒙药的味、功能、消化后变化配制蒙药 <br>\r\n      14 &quot;巴斯玛&quot;油制剂的制作和作用 <br>\r\n      15 特制红花13味散治疗肝病的观察 <br>\r\n      16 针法治疗胃下垂 <br>\r\n      17 《红柳灸》的临床应用<br>\r\n      18 对放血疗法的体验 <br>\r\n      19 著名老蒙医--萨拉布朋素格 <br>\r\n      20 慢性支气管炎的护理 <br>\r\n      21 蒙医史学、理论专家--吉格木德教授 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (743, 3, 7, '1991年第4期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1991年第4期<a name="19914"></a><br>\r\n      <br>\r\n      1 再论现代蒙医理论体系的基本特点 <br>\r\n      2 对《四部医典》中古代医学心理学的研究 <br>\r\n      3 脊髓灰白质炎的病机与预防 <br>\r\n      4 新旧方对照治疗慢性布病24冽 <br>\r\n      5 肝&quot;粘&quot;热治验 <br>\r\n      6 传统方法炮制《铁屑》的研究 <br>\r\n      7 蒙医应用药引子的原由(续) <br>\r\n      8 对脑部&quot;黑脉病&quot;的治疗观察 <br>\r\n      9 风湿性关节炎治验 <br>\r\n      10 应用按摩、针疗术治疗肩周炎45例 <br>\r\n      11 对骷髅的初步分析 <br>\r\n      12 应用蒙药离子导入治疗骨质增生22例 <br>\r\n      13 长寿法 <br>\r\n      14 胃病的护理 <br>\r\n      15 无名的贡献者 -阿格旺都格米德 <br>\r\n      16 珊瑚珠医经 <br>\r\n      17 杰出的现代蒙医学，脑血管病专家--阿古拉</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (744, 3, 7, '1991年汉译版', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1991年汉译版<a name="19915"></a><br>\r\n      <br>\r\n      1 蒙医学史概述<br>\r\n      2 蒙医学概说 <br>\r\n      3 蒙药治疗萨病临床疗效观察 <br>\r\n      4 九味沙棘片治疗慢性气管炎临床疗效分析 <br>\r\n      5 用蒙药利尿方医治狂犬毒之经验<br>\r\n      6 癫狂病蒙医治疗 <br>\r\n      7 蒙医刮术治疗沙眼的报告<br>\r\n      8肺痨的诊治<br>\r\n      9 蒙医治疗急性病毒性肝炎疗效观察 <br>\r\n      10 酸马奶治疗冠心病临床总结<br>\r\n      11 蒙医治疗青盲症疗效观察报告 <br>\r\n      12 蒙医治疗坐骨神经痛临床体会<br>\r\n      13蒙药&quot;玛努拉格扎&quot;的考证<br>\r\n      14治疗巴木病经验<br>\r\n      15 嘎尔迪注射液治疗腰腿痛病的近期疗效观察<br>\r\n      17 蒙药通嘎拉601治疗再生性障碍性贫血临床观察 <br>\r\n      18 骨折的白酒按摩疗法 <br>\r\n      19 放血疗法 <br>\r\n      20 震脑术 <br>\r\n      21 内蒙古矿泉简介 <br>\r\n      22 伊希巴拉吉尔及其著作考略<br>\r\n      23 有关汉译名词的注释 <br>\r\n      24 内蒙古蒙医学会简介 </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
INSERT INTO myy_text VALUES (745, 3, 7, '1993年第1期', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1993年第1期<br>\r\n      <br>\r\n      1 对寒热十二脉的探讨 <br>\r\n      2 脏腑病的辨证治疗 <br>\r\n      3 元丹扎木苏治疗呃逆的经验 <br>\r\n      4 敖斯尔治疗肺结核的经验 <br>\r\n      5 对子宫癌瘤的治验 <br>\r\n      6 溃疡性结肠炎治验 <br>\r\n      7 治疗牛皮癣二则 <br>\r\n      8 肺脓疡的治疗 <br>\r\n      9 血热性月经不调治验 <br>\r\n      10 神经性头痛治验 <br>\r\n      11 蒙西医结合治疗&quot;心刺痛病&quot;的体会 <br>\r\n      12 对高血压的治疗体会 <br>\r\n      13 咽喉炎治验 <br>\r\n      14 杜鹃一16味散的鉴定 <br>\r\n      15 对芦漏花的鉴别和应用 <br>\r\n      16 应用五味阿尔善(药浴)治疗黄水症632例 <br>\r\n      17 试述灸法的源流及应用--&lt;续) <br>\r\n      18 应用&quot;震脑术&quot;治疗脑震荡 <br>\r\n      19 火罐及拔罐放血法治疗血热性头痛 <br>\r\n      20 应用蒙医三诊鉴别诊断急症 <br>\r\n      21 对慢性肠炎的护理 <br>\r\n      22 罗布桑丹曾扎拉生和他的蒙医学术思想 <br>\r\n      23 蒙医副主任医师--娜仁高娃</td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
