INSERT INTO myy_text VALUES (747, 3, 7, '1993年汉文版', '<table width="100%" border="0" cellspacing="0" cellpadding="0"style="font-size:12px">\r\n  <tr>\r\n    <td>1993年汉文版<a name="1993h"></a><br>\r\n      <br>\r\n      1 蒙医药学的形成和发展概况<br>\r\n      2 百灵庙&quot;曼巴拉桑&quot;及蒙医史梗概<br>\r\n      3 试论蒙医学对琪素(血)和琪素病的认识<br>\r\n      4 关于蒙医&quot;三根&quot;之一&quot;赫依&quot;的探讨 <br>\r\n      5 蒙医整骨疗法的起源与发展 <br>\r\n      6 内外结合治疗&quot;乳房肿痛症&quot; <br>\r\n      7 蒙医治疗胆襄炎40例临床体会<br>\r\n      8 蒙药&quot;哈斯哈图古日--15味散&quot;的临床应用及疗效观察 <br>\r\n      9 蒙医药浴治疗慢性布病疗效观察 <br>\r\n      10 蒙药治疗慢性胃炎100例临床疗效观察<br>\r\n      11 蒙医药治疗类风湿性关节炎的体会 <br>\r\n      12 蒙医肝《宝日》病临床体会 <br>\r\n      13 &quot;通拉嘎一5味散&quot;敷脐治疗婴幼儿腹泻<br>\r\n      14 蒙药&quot;壮西一7味散&quot;治疗&quot;宝日&quot;病68例疗效观察 <br>\r\n      15 蒙医药对&quot;萨病&quot;的研治概述<br>\r\n      16 蒙医药治疗视网膜色素变性100例观察 <br>\r\n      17 蒙医治疗&quot;套列病&quot;的体会 <br>\r\n      18 蒙医药治疗脑血栓100例临床观察<br>\r\n      19 蒙医治疗心悸症二则 <br>\r\n      20 蒙药&quot;尼拉哈森一阿日山&quot;治疗婴幼儿腹泻100例 <br>\r\n      21 蒙医&quot;五种疗法&quot;<br>\r\n      22 蒙医整骨治疗小儿肱骨髁上骨析100例报告<br>\r\n      23 吉如很・阿嘎如一8味散鉴别研究<br>\r\n      24 蒙古族药用矿物沿革与传统分类法的研究 <br>\r\n      25 蒙药&quot;巴特尔一7味&quot;的鉴别和薄层层析法研究<br>\r\n      26 蒙药&quot;伊赫一汤&quot;改为冲剂的剂型改革研究<br>\r\n      27 简述蒙药之王一阿如拉<br>\r\n      28 应用&quot;蒙药酒挫法&quot;护理偏瘫病人的体会 \r\n      <p></p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n', 1, '2003-06-09');
